INSERT INTO `menu` VALUES (1, '巧克力脆皮雪糕【初味日记】', 1, '{\"1\": \"巧克力隔热水融化，装入裱花袋。\", \"2\": \"把适量的巧克力挤入硅胶模具。\", \"3\": \"晃动模具使巧克力均匀铺满模具壁，放冰箱冷藏10分钟，直到凝固。\", \"4\": \"雪糕提前取出室温软化，分出香草味的雪糕。\", \"5\": \"一份加入抹茶粉。\", \"6\": \"一份加入奥利奥碎。\", \"7\": \"取出凝固好的巧克力壳，填入调好口味的雪糕，放入提前切好的水果粒。\", \"8\": \"放回冰箱冷冻3小时左右，直到雪糕凝固。\", \"9\": \"取出冻好的雪糕，在顶部填上巧克力封口。\", \"10\": \"冷冻半小时，完全凝固后脱模即可。\"}', '{\"芒果\": \"适量\", \"巧克力雪糕\": \"适量\", \"香草味雪糕\": \"适量\", \"白巧克力\": \"120g\", \"黑巧克力\": \"120g\", \"奥利奥碎\": \"适量\", \"抹茶粉\": \"适量\", \"奇异果\": \"适量\"}', 1, '2020-02-27 22:19:53', 0, '2020-03-12 19:35:41', '421764');
INSERT INTO `menu` VALUES (2, '蓝莓冰棍', 2, '{\"1\": \"蓝莓酱与酸奶。\", \"2\": \"蓝莓酱倒入酸奶中，搅拌均匀。\", \"3\": \"倒入模具，9分满。\", \"4\": \"冷冻四小时，脱模即可。\", \"5\": \"顺带介绍下蓝莓酱的做法，原料是蓝莓与糖水。\", \"6\": \"将蓝莓倒入糖水中，煮开。\", \"7\": \"搅打至顺滑。\", \"8\": \"蓝莓酱来了，简单吧！\"}', '{\"蓝莓酱\": \"90克\", \"酸奶\": \"400克\", \"蓝莓\": \"90克\", \"糖水\": \"50毫升\"}', 1, '2020-02-27 22:19:54', 0, '2020-03-12 19:36:05', '414608');
INSERT INTO `menu` VALUES (3, '猕猴桃椰汁冰棍', 3, '{\"1\": \"猕猴桃一个，椰汁300克。\", \"2\": \"猕猴桃去皮，切片。\", \"3\": \"将猕猴桃片放入到模具中，插入冰棍棒。\", \"4\": \"椰汁倒入模具，9分满。\", \"5\": \"冷冻四小时，脱模即可。\"}', '{\"猕猴桃\": \"1个\", \"椰汁\": \"300克\"}', 1, '2020-02-27 22:19:55', 0, '2020-03-12 19:36:05', '414600');
INSERT INTO `menu` VALUES (4, '杏子雪糕', 5, '{\"1\": \"杏子清洗干净，杏的重量是连核在内的重量哦，其实多一点少一点也没有什么太大的关系啦。\", \"2\": \"将杏子去核加入牛奶一起放入料理机里，打成泥。\", \"3\": \"淡奶油事现是在冰箱里冷藏的，取出用手动打蛋器将其搅打至浓稠。\", \"4\": \"再加入棉花糖搅打均匀，至棉花糖完全融化，这一步用的是液体草莓味棉花糖，如果没有，也可以加白糖，白糖比棉花糖要甜些，所以用量可以适当减少。\", \"5\": \"再把杏泥倒入淡奶油中，搅拌均匀即可。\", \"6\": \"准备好模具，将拌好的雪糕糊倒入模具中，放入冰箱里冷冻至凝固。\"}', '{\"淡奶油\": \"100克\", \"棉花糖\": \"50克\", \"杏子\": \"210克\", \"牛奶\": \"30克\"}', 1, '2020-02-27 22:19:58', 0, '2020-03-12 19:36:05', '414985');
INSERT INTO `menu` VALUES (5, '自制巧克力夹心雪糕', 6, '{\"1\": \"准备材料如图。\", \"2\": \"100克淡奶油里放入40克巧克力隔水融化。（水温在50度左右）。\", \"3\": \"融化好的巧克力用裱花袋装起来扎好口放入冰箱冷冻20分钟。\", \"4\": \"将火龙果切块放入料理机里打成汁。\", \"5\": \"500克淡奶油加入40克糖打至九分发。\", \"6\": \"倒入火龙果翻拌均匀，并装入裱花袋中。\", \"7\": \"将调好的奶油水果糊挤入模具中，先挤5分满，然后放入冰箱冷冻30分钟。\", \"8\": \"将猕猴桃片放在表面，盖上盖子放入冰箱冷冻24小时即可。\", \"9\": \"吃起来口感较软，不像棒冰那么硬。\", \"10\": \"黑巧克力夹心也很提味。\"}', '{\"淡奶油\": \"600克\", \"火龙果\": \"200克\", \"猕猴桃\": \"1个\", \"细砂糖\": \"40克\", \"黑巧克力\": \"50克\", \"模具\": \"若干\", \"雪糕棒\": \"若干\", \"裱花袋\": \"若干\"}', 1, '2020-02-27 22:20:00', 0, '2020-03-12 19:36:05', '414091');
INSERT INTO `menu` VALUES (6, '五味水果雪糕', 7, '{\"1\": \"准备材料如图。\", \"2\": \"将五种水果剁碎分装在碗中备用。\", \"3\": \"1000克淡奶油加入80克细砂糖打至九分发。\", \"4\": \"将打发的淡奶油分成五等份，每一份分别加入一种水果碎翻拌均匀。\", \"5\": \"将五种水果奶油糊分别装在裱花袋中。\", \"6\": \"按喜好将五种口味的奶油糊装入模具中。\", \"7\": \"插入雪糕棒，盖上盖子放入冰箱冷冻24小时。\", \"8\": \"黑巧克力隔热水融化（水温50度左右，不可太烫）。\", \"9\": \"融化好的巧克力装入裱花袋中。\", \"10\": \"将雪糕脱模，然后随意的挤上黑巧克力，稍等片刻即可食用。也可继续放冰箱冷冻保存。\", \"11\": \"香甜、可口、解暑。\"}', '{\"西瓜\": \"100克\", \"火龙果\": \"100克\", \"猕猴桃\": \"100克\", \"芒果\": \"100克\", \"蓝莓\": \"100克\", \"淡奶油\": \"1000克\", \"黑巧克力\": \"50克\", \"细砂糖\": \"80克\"}', 1, '2020-02-27 22:20:02', 0, '2020-03-12 19:36:05', '413986');
INSERT INTO `menu` VALUES (7, '火龙果沙冰', 8, '{\"1\": \"准备好一个红心火龙果，红心火龙果比较甜，我家常备，因为女儿超级喜欢吃红心火龙果，如果用白心的火龙果做，再准备一点蜂蜜或者糖粉，因为冷冻后甜度会降低，打沙冰的时候可以加一下酸奶或者纯净水。\", \"2\": \"火龙果去皮切块，装入保鲜袋冰箱冷冻5个小时以上，我是头天晚上冻在冰箱里，第二天白天随时拿出来打沙冰。\", \"3\": \"冷冻室取出的火龙果，稍微化冻掰开放入破壁机杯中，不要一大坨放进去，那样不好搅打成沙冰的，此时如果火龙果不太甜，可以添加一些蜂蜜或者糖粉，也可以添加一些纯净水或者酸奶，这些我都没放，只想吃到纯正的水果沙冰。\", \"4\": \"盖好盖子把杯体安装好，长按电动清洗键，搅打几秒停止一下，用破壁机配套的搅拌棒往下压搅打的沙冰，如此反复几次沙冰就搅打好了，是不是超级简单。\", \"5\": \"看看搅打好的沙冰超级细腻，忍不住先挖一勺吃。\", \"6\": \"把搅打好的沙冰用勺子挖入杯中就可以享受清凉解暑的火龙果沙冰了，也可以叫做水果冰淇淋，口感超级细腻，真正的无添加的水果冰淇淋。\", \"7\": \"成品图。\", \"8\": \"成品图。\", \"9\": \"成品图。\"}', '{\"红心火龙果\": \"1个\"}', 1, '2020-02-27 22:20:04', 0, '2020-03-12 19:36:05', '413122');
INSERT INTO `menu` VALUES (8, '奇异果冰', 9, '{\"1\": \"雪糕代替冰块。\", \"2\": \"奇异果➕雪糕➕酸奶，打成奶昔。\", \"3\": \"给冰淇淋打个广告。\"}', '{\"奇异果\": \"适量\", \"雪糕\": \"适量\", \"酸奶\": \"适量\"}', 1, '2020-02-27 22:20:06', 0, '2020-03-12 19:36:05', '412100');
INSERT INTO `menu` VALUES (9, '百香果柠檬蜂蜜杂果冰棒', 10, '{\"1\": \"食材一：自制百香果柠檬蜂蜜。\", \"2\": \"食材二：各种水果和葡萄干。\", \"3\": \"水果切小粒备用。\", \"4\": \"将水果和葡萄干依自己喜好，一层一层摆入冰棒模具中。\", \"5\": \"最后加入百香果柠檬蜂蜜。\", \"6\": \"加上盖子，入冰箱冷冻三小时以上。\", \"7\": \"酸酸甜甜，冰冰凉！\"}', '{\"百香果\": \"适量\", \"柠檬\": \"适量\", \"蜂蜜\": \"适量\", \"小西红柿\": \"适量\", \"黄桃\": \"适量\", \"葡萄\": \"适量\", \"猕猴桃\": \"适量\", \"葡萄干\": \"适量\"}', 1, '2020-02-27 22:20:07', 0, '2020-03-12 19:36:05', '409880');
INSERT INTO `menu` VALUES (10, '枇杷果酱奶油冰棍儿', 1, '{\"1\": \"准备好所用的材料。\", \"2\": \"全部倒入破壁机里。\", \"3\": \"打成细腻的果酱奶油糊。\", \"4\": \"倒入冰棒模具中，刮平表面。\", \"5\": \"盖好盖子放入冰箱冷冻过夜。\", \"6\": \"成品图\", \"7\": \"成品图\", \"8\": \"成品图\", \"9\": \"成品图\", \"10\": \"成品图\", \"11\": \"成品图\", \"12\": \"成品图\"}', '{\"枇杷果酱\": \"400g\", \"淡奶油\": \"200g\", \"果味酸奶\": \"320g\"}', 1, '2020-02-27 22:20:08', 0, '2020-03-12 19:36:05', '408906');
INSERT INTO `menu` VALUES (11, '芒椰果昔', 2, '{\"1\": \"将芒果对半切块，划落碗中。\", \"2\": \"准备好椰汁、冰块、芒果。\", \"3\": \"将椰子汁、冰块、芒果、冰块一起放入搅拌杯中。\", \"4\": \"选择破壁机自动程序-果汁功能-按下巡航键即可。\", \"5\": \"成品。\"}', '{\"芒果\": \"400G\", \"椰汁\": \"200ML\", \"冰块\": \"200G\"}', 1, '2020-02-27 22:20:09', 0, '2020-03-12 19:36:05', '408239');
INSERT INTO `menu` VALUES (12, '轻松自制炒芝士酸奶', 3, '{\"1\": \"准备材料，把水果和干果切碎。\", \"2\": \"准备个大点的平底盘子，把酸奶倒进去。\", \"3\": \"先铺上一层干果。\", \"4\": \"再铺上一层水果.然后包上保鲜膜，冰箱冷冻四个小时。（其实我觉得两个小时应该也可以）\", \"5\": \"取出后静置5分钟左右，拿勺子从边缘开始刮。\", \"6\": \"用力刮。\", \"7\": \"盛到碗里大功告成。\", \"8\": \"可以加点自制芋圆，开吃吧！\"}', '{\"芝士酸奶\": \"180g\", \"火龙果\": \"适量\", \"芒果\": \"适量\", \"蓝莓\": \"适量\", \"杏仁\": \"适量\", \"腰果\": \"适量\", \"葡萄干\": \"适量\"}', 1, '2020-02-27 22:20:11', 0, '2020-03-12 19:36:05', '407474');
INSERT INTO `menu` VALUES (13, '青柠冰红茶', 4, '{\"1\": \"一个红茶包和适量冰糖放入容器中。\", \"2\": \"倒入热水冲泡放凉备用。\", \"3\": \"杯子里放入冰块。\", \"4\": \"挤入青柠汁。\", \"5\": \"放入青檬片。\", \"6\": \"倒入红茶水。\", \"7\": \"放入青柠片。\", \"8\": \"成品图。\", \"9\": \"成品图。\"}', '{\"红茶包\": \"1个\", \"青柠\": \"适量\", \"冰块\": \"适量\", \"冰糖\": \"适量\"}', 1, '2020-02-27 22:20:12', 0, '2020-03-12 19:36:05', '406339');
INSERT INTO `menu` VALUES (14, '西瓜牛奶草莓冰沙', 5, '{\"1\": \"可能会有些沫。\", \"2\": \"先把果肉全部掏出，再用榨汁机把瓜肉炸成西瓜汁。\", \"3\": \"准备牛奶和碎冰冰。\", \"4\": \"把牛奶倒入碗里，千万不要倒完。\", \"5\": \"把一半的牛奶倒入碗里，再把碎冰冰挤入碗内，碎冰冰最好冷藏过，再放入一勺白砂糖。\", \"6\": \"用保鲜膜把牛奶碎冰冰密封，再把西瓜汁用盖子密封！放冰箱里冷冻。\", \"7\": \"放置两分钟。\", \"8\": \"冷冻鱼一至两小时后，拿出来，把它们捣碎，然后再混合在一起，再放一勺糖，然后搅拌。\", \"9\": \"然后我们的冰沙就大功告成了。\"}', '{\"西瓜\": \"半个\", \"纯牛奶\": \"165ml\", \"旺旺碎冰冰\": \"1个（草莓味）\", \"白砂糖\": \"2勺\"}', 1, '2020-02-27 22:20:13', 0, '2020-03-12 19:36:05', '406232');
INSERT INTO `menu` VALUES (15, '西瓜冰棒', 6, '{\"1\": \"准备好西瓜。\", \"2\": \"把西瓜切小块。\", \"3\": \"放入榨汁机里。\", \"4\": \"榨出好喝的西瓜汁。\", \"5\": \"模具提前清洗干净。\", \"6\": \"挤入一半酸奶。\", \"7\": \"轻震一下模具后倒入西瓜汁。\", \"8\": \"盖好后放冰箱冰冻。\", \"9\": \"西瓜棒冰。\", \"10\": \"酸奶西瓜的。\", \"11\": \"美美哒！\", \"12\": \"缺点就是模具太小了。\"}', '{\"西瓜\": \"1/4个\", \"酸奶\": \"半盒\"}', 1, '2020-02-27 22:20:14', 0, '2020-03-12 19:36:05', '405654');
INSERT INTO `menu` VALUES (16, '西瓜芒果西米露', 7, '{\"1\": \"煮好过凉的西米。\", \"2\": \"芒果半个。\", \"3\": \"用小勺挖进搅拌机，适量牛奶，椰浆，糖粉。\", \"4\": \"打成细腻奶昔。\", \"5\": \"倒入杯中。\", \"6\": \"舀入适量西米。\", \"7\": \"西瓜球放入。中间再放入适量西米。\", \"8\": \"樱桃点缀。\", \"9\": \"成品。\"}', '{\"西瓜\": \"适量\", \"芒果\": \"适量\", \"椰浆\": \"适量\", \"牛奶\": \"适量\", \"糖粉\": \"适量\"}', 1, '2020-02-27 22:20:16', 0, '2020-03-12 19:36:05', '404763');
INSERT INTO `menu` VALUES (17, '芒果椰浆西米露', 8, '{\"1\": \"砂锅里适量水烧开，适量西米放入，中小火煮二十分钟左右。\", \"2\": \"煮至西米中间剩下小白点，关火焖至白点无。\", \"3\": \"取适量，过凉纯净水。\", \"4\": \"搅拌机内适量牛奶，椰浆，糖粉，半个芒果。\", \"5\": \"打成细腻奶昔。\", \"6\": \"倒至杯中，舀入适量西米。\", \"7\": \"芒果丁放上。\", \"8\": \"樱桃一切二，去核，点缀。成品。\", \"9\": \"成品。\"}', '{\"芒果\": \"适量\", \"西米\": \"适量\", \"椰浆\": \"适量\", \"牛奶\": \"适量\", \"糖粉\": \"适量\"}', 1, '2020-02-27 22:20:17', 0, '2020-03-12 19:36:05', '404757');
INSERT INTO `menu` VALUES (18, '西瓜椰奶西米露', 9, '{\"1\": \"砂锅里适量水烧开，小西米放入，中小火煮二十分钟左右。\", \"2\": \"煮至西米中间仅剩小白点，关火焖一会儿。\", \"3\": \"西瓜挖球。\", \"4\": \"过滤好的西瓜汁适量。\", \"5\": \"适量椰浆，西瓜汁，牛奶，糖粉。\", \"6\": \"适量西米放入。\", \"7\": \"西瓜球放入，再舀上一些西米，即成。\", \"8\": \"成品。\", \"9\": \"成品。\"}', '{\"西米\": \"适量\", \"西瓜球\": \"适量\", \"椰浆\": \"适量\", \"牛奶\": \"适量\", \"糖粉\": \"适量\"}', 1, '2020-02-27 22:20:18', 0, '2020-03-12 19:36:05', '404755');
INSERT INTO `menu` VALUES (19, '沙冰～～蓝天白云', 10, '{\"1\": \"准备RIO鸡尾酒一瓶，酸奶一杯和一个食品封口袋。\", \"2\": \"打开RIO，倒入食品袋里。\", \"3\": \"食品袋封口放入冰箱冷冻6小时以上。\", \"4\": \"冻好的鸡尾酒拿出，捏成碎状（如果冻成冰块就把它敲成冰沙状）。\", \"5\": \"酸奶开盖准备好。\", \"6\": \"沙冰用勺子挖入杯子里，一层沙冰，里面倒入适量的酸奶。（根据自己的爱好可以倒成自己想象的蓝天白云样即可）。\"}', '{\"鸡尾酒\": \"一瓶\", \"酸奶\": \"一杯\"}', 1, '2020-02-27 22:20:19', 0, '2020-03-12 19:36:05', '404722');
INSERT INTO `menu` VALUES (20, '抹茶牛奶', 1, '{\"1\": \"准备材料。\", \"2\": \"把抹茶粉过滤加入热水搅拌。\", \"3\": \"加入练乳搅拌、加入冰块。\", \"4\": \"加入牛奶。\", \"5\": \"成品图。\"}', '{\"抹茶粉\": \"2勺\", \"冰块\": \"适量\", \"炼乳\": \"2勺\", \"牛奶\": \"适量\"}', 1, '2020-02-27 22:20:22', 0, '2020-03-12 19:36:05', '404089');
INSERT INTO `menu` VALUES (21, '香蕉火龙果牛奶棒冰', 2, '{\"1\": \"备好食材。\", \"2\": \"香蕉去皮切片，放入料理杯中。\", \"3\": \"红心火龙果，用刀沿四周划一圈，再从中间切小块，接着用刀尖轻挑出来，放入料理杯中。\", \"4\": \"倒入适量牛奶，量盖过果粒即可，将料理机带刀头的底座装上拎紧。\", \"5\": \"装入料理机，按下开关，每次持续工作不要超过1分钟。\", \"6\": \"其实只需要20、30秒，就已经打的均匀细腻了。\", \"7\": \"打好的牛奶果汁，亲不想做棒冰，正好准备外出，可将带刀头的底座拎下改换成带提手的杯盖。\", \"8\": \"弹开小盖直接饮用，干净又方便；因为我要想棒冰，所以这里只是演示给大家看下哈。\", \"9\": \"事先将棒冰模具洗净，再倒入开水消毒下。\", \"10\": \"接着将打好的牛奶果汁，倒入棒冰模具中，依次插入棒冰棍，送至冰箱冷冻凝固。\", \"11\": \"吃前取出模具，用水冲下圆柱体。\", \"12\": \"旋转几下，就可以取出。\", \"13\": \"自制棒冰，不仅颜值高，营养好，还安全放心，多好。\"}', '{\"香蕉\": \"1根\", \"红心火龙果\": \"半个\", \"纯牛奶\": \"适量\"}', 1, '2020-02-27 22:20:23', 0, '2020-03-12 19:36:05', '401217');
INSERT INTO `menu` VALUES (22, '蜜桃西瓜沙冰', 3, '{\"1\": \"西瓜、桃准备好。\", \"2\": \"西瓜去籽切小块。桃去皮去核切小块。\", \"3\": \"把西瓜块、桃块放料理杯中，加白糖、奶粉。\", \"4\": \"搅打成泥状。\", \"5\": \"倒入模具中。\", \"6\": \"放冰箱冷冻成块，我冻了一天。\", \"7\": \"把冻好的冰块取出，再放入料理机中，搅打成沙冰。\", \"8\": \"成品。细腻冰凉，像冰糕的口感。\"}', '{\"去皮西瓜\": \"300g\", \"水蜜桃\": \"2个\", \"奶粉\": \"2匙\", \"白糖\": \"2匙\"}', 1, '2020-02-27 22:20:25', 0, '2020-03-12 19:36:05', '403426');
INSERT INTO `menu` VALUES (23, '草莓冰饮', 4, '{\"1\": \"倒入草莓汁。\", \"2\": \"放冰块。\", \"3\": \"冰块放满。\", \"4\": \"倒入雪碧。\", \"5\": \"完成了。\"}', '{\"草莓汁\": \"适量\", \"雪碧\": \"适量\", \"冰块\": \"适量\"}', 1, '2020-02-27 22:20:27', 0, '2020-03-12 19:36:05', '404114');
INSERT INTO `menu` VALUES (24, '草莓蓝莓冰沙', 5, '{\"1\": \"料理机中加入冰块。\", \"2\": \"放入一勺蓝莓果酱。\", \"3\": \"在放入一勺蓝莓果酱。\", \"4\": \"在放入两勺草莓果酱。\", \"5\": \"启动料理机。\", \"6\": \"杯子壁抹上炼乳。\", \"7\": \"打好的冰沙倒入杯子里完成。\"}', '{\"草莓果酱\": \"2勺\", \"蓝莓果酱\": \"2勺\", \"冰块\": \"600g\", \"炼乳\": \"适量\"}', 1, '2020-02-27 22:20:28', 0, '2020-03-12 19:36:05', '403880');
INSERT INTO `menu` VALUES (25, '巧克力脆皮雪糕', 6, '{\"1\": \"巧克力放入碗中，隔热水融化。\", \"2\": \"融化好的巧克力。\", \"3\": \"巧克力液淋在冰糕上。\", \"4\": \"冰糕均匀的裹上巧克力液。\", \"5\": \"撒上坚果放冰箱冷冻二十分钟。\", \"6\": \"成品图。\"}', '{\"巧克力\": \"适量\", \"冰糕\": \"适量\", \"坚果\": \"适量\"}', 1, '2020-02-27 22:20:30', 0, '2020-03-12 19:36:05', '403730');
INSERT INTO `menu` VALUES (26, '水蜜桃红豆冰', 7, '{\"1\": \"冷藏过的水蜜桃两个，洗净去皮切大块。\", \"2\": \"放搅拌机内。\", \"3\": \"打成细腻桃泥。\", \"4\": \"冷藏熟红豆适量。\", \"5\": \"适量白糖腌渍一会。\", \"6\": \"打好的桃泥倒入杯中，舀入腌渍红豆。撒上剪碎的玫瑰花瓣点缀即可。\", \"7\": \"成品。\", \"8\": \"成品。\"}', '{\"水蜜桃\": \"2个\", \"熟红豆\": \"适量\", \"白糖\": \"适量\"}', 1, '2020-02-27 22:20:32', 0, '2020-03-12 19:36:05', '402721');
INSERT INTO `menu` VALUES (27, '红豆酸奶冰棒', 8, '{\"1\": \"两勺淀粉。\", \"2\": \"加水调成面糊。\", \"3\": \"加入一个蛋黄。\", \"4\": \"加适量白糖，调均匀的面糊。\", \"5\": \"隔水炖，要不停的搅动碗中的蛋黄液。\", \"6\": \"炖的蛋黄液很浓的时候就停火。\", \"7\": \"煮好的蜜红豆。\", \"8\": \"蜜红豆加入蛋黄糊中。\", \"9\": \"搅拌均匀的蛋黄糊舀入冰棒模具中。\", \"10\": \"老酸奶一瓶。\", \"11\": \"把老酸奶舀在模具里，在蛋黄糊的上面。最后再放些蜜红豆。\", \"12\": \"用模具的把杆搅动一下和老酸奶搅拌均匀。\", \"13\": \"盖上模具盖子，放冰箱冷冻三小时左右。\", \"14\": \"做好的冰棒，酸酸甜甜，清凉可口。从冰箱拿出模具用凉水冲一下，顺利脱模。\"}', '{\"酸奶\": \"一个\", \"蜜红豆\": \"适量\", \"蛋黄\": \"一个\", \"淀粉\": \"2匙\", \"白糖\": \"适量\", \"柠檬水\": \"适量\"}', 1, '2020-02-27 22:20:33', 0, '2020-03-12 19:36:05', '402664');
INSERT INTO `menu` VALUES (28, '咸柠七-6种雪碧的高颜值喝法，点燃你的世界杯！', 9, '{\"1\": \"青柠切片。\", \"2\": \"两面抹上喜马拉雅火山盐（岩盐）腌渍至少2小时。\", \"3\": \"准备一个好看的杯子，放入2片咸柠檬。\", \"4\": \"倒入1/2杯冰块，放入3片咸柠檬，倒入剩下1/2杯冰块。\", \"5\": \"倒入雪碧，在杯侧装饰一片柠檬片即可。\"}', '{\"雪碧\": \"300ml\", \"青柠\": \"一个\", \"岩盐\": \"适量\", \"冰块\": \"1杯\"}', 1, '2020-02-27 22:20:35', 0, '2020-03-12 19:36:05', '402000');
INSERT INTO `menu` VALUES (29, '红色风暴-6种雪碧的高颜值喝法，点燃你的世界杯！', 10, '{\"1\": \"樱桃洗净，用吸管去籽。\", \"2\": \"将樱桃切成小丁备用。\", \"3\": \"在樱桃丁种加入糖，放入微波炉中火2分钟，即成樱桃酱。\", \"4\": \"准备一个好看的杯子，放入樱桃酱，加入冰块。\", \"5\": \"倒入雪碧，在杯侧放一片柠檬片装饰即可。\"}', '{\"雪碧\": \"200ml\", \"樱桃\": \"3颗\", \"糖\": \"5g\", \"冰块\": \"1/2杯\", \"柠檬片\": \"1片（装饰）\"}', 1, '2020-02-27 22:20:37', 0, '2020-03-12 19:36:05', '401995');
INSERT INTO `menu` VALUES (30, 'Mojito雪碧-6种雪碧的高颜值喝法', 1, '{\"1\": \"青柠对切，一半切成青柠块，另一半备用。\", \"2\": \"准备一个漂亮的杯子，将步骤1留下的半个青柠汁挤入杯子。\", \"3\": \"倒入1/2杯冰块，放入青柠块，再倒入1/2杯冰块。\", \"4\": \"薄荷叶洗净，稍微揉搓一下放入。\", \"5\": \"倒入雪碧，插入吸管，在杯侧点缀一片柠檬片即可。\"}', '{\"雪碧\": \"300ml\", \"青柠\": \"1个\", \"薄荷\": \"10片\", \"冰块\": \"1杯\"}', 1, '2020-02-27 22:20:39', 0, '2020-03-12 19:36:05', '401967');
INSERT INTO `menu` VALUES (31, '冰棍儿，雪糕', 2, '{\"1\": \"酸奶里加入蜂蜜搅拌均匀，火龙果剁成泥，加一勺拌过蜂蜜的酸奶。\", \"2\": \"搅拌好的酸奶火龙果蜂蜜。\", \"3\": \"模具里平均放入火龙果泥。\", \"4\": \"加入剩余酸奶。\", \"5\": \"用牙签在每个模具里随便划拉几下成型之后就会出渐变效果。\", \"6\": \"放入冰柜冷冻几个小时冻实即可。\"}', '{\"火龙果\": \"80g\", \"原味酸奶\": \"200g\", \"蜂蜜\": \"2个\"}', 1, '2020-02-27 22:20:41', 0, '2020-03-12 19:36:05', '401982');
INSERT INTO `menu` VALUES (32, '水果冰块雪碧', 4, '{\"1\": \"杯子里放入提前冷冻好的水果冰块。\", \"2\": \"继续加入冰块。\", \"3\": \"加满冰块。\", \"4\": \"倒入雪碧。\", \"5\": \"漂亮吗？\", \"6\": \"把雪碧倒满杯子。\", \"7\": \"成品图。\"}', '{\"水果\": \"适量\", \"冰块\": \"适量\", \"雪碧\": \"适量\"}', 1, '2020-02-27 22:20:45', 0, '2020-03-12 19:36:05', '401896');
INSERT INTO `menu` VALUES (33, '家庭版冰粥', 5, '{\"1\": \"提前冻好的冰块。\", \"2\": \"把冰块加入榨汁杯中。\", \"3\": \"启动榨汁杯把冰块打碎。\", \"4\": \"放上蓝莓果酱。\", \"5\": \"放上坚果。\", \"6\": \"成品图。\"}', '{\"冰块\": \"适量\", \"蓝莓果酱\": \"适量\", \"坚果\": \"适量\"}', 1, '2020-02-27 22:20:46', 0, '2020-03-12 19:36:05', '401787');
INSERT INTO `menu` VALUES (34, '凉爽西瓜饮', 6, '{\"1\": \"西瓜肉放入榨汁杯，榨成西瓜汁。\", \"2\": \"杯子里放入冰块。\", \"3\": \"倒入榨好的西瓜汁。\", \"4\": \"倒入一饼养乐多。\", \"5\": \"放上吸管完成。\"}', '{\"西瓜\": \"适量\", \"冰块\": \"适量\", \"养乐多\": \"适量\"}', 1, '2020-02-27 22:20:48', 0, '2020-03-12 19:36:05', '400411');
INSERT INTO `menu` VALUES (35, '火龙果酸奶冰糕', 7, '{\"1\": \"火龙果肉放入榨汁杯中，加入酸奶，加入适量蜂蜜。\", \"2\": \"启动榨汁杯。\", \"3\": \"打成果泥。\", \"4\": \"把果泥倒入冰糕模具中。\", \"5\": \"放上冰糕棍。\", \"6\": \"放冰箱冷冻5个小时以上。\", \"7\": \"成品图。\"}', '{\"火龙果\": \"适量\", \"酸奶\": \"适量\", \"蜂蜜\": \"适量\"}', 1, '2020-02-27 22:20:50', 0, '2020-03-12 19:36:05', '400383');
INSERT INTO `menu` VALUES (36, '脏脏草莓奶', 8, '{\"1\": \"草莓清洗干净。\", \"2\": \"草莓切丁备用。\", \"3\": \"杯子里放入两勺草莓酱。\", \"4\": \"放入两勺草莓丁。\", \"5\": \"杯子内壁涂上草莓酱。\", \"6\": \"放入冰块。\", \"7\": \"倒入牛奶。\", \"8\": \"完成了。\"}', '{\"草莓\": \"适量\", \"牛奶\": \"适量\", \"草莓酱\": \"适量\", \"冰块\": \"适量\"}', 1, '2020-02-27 22:20:52', 0, '2020-03-12 19:36:05', '399858');
INSERT INTO `menu` VALUES (37, '青瓜柠檬特饮', 9, '{\"1\": \"准备食材，黄瓜和柠檬用盐搓洗干净备用。\", \"2\": \"先将冰块装入杯子一半。\", \"3\": \"小黄瓜切薄片。\", \"4\": \"柠檬切小角。\", \"5\": \"黄瓜柠檬放入杯子里。\", \"6\": \"倒入满杯的雪碧。\", \"7\": \"完成。\"}', '{\"小黄瓜\": \"半根\", \"柠檬\": \"半个\", \"雪碧\": \"200毫升\", \"冰块\": \"半杯\"}', 1, '2020-02-27 22:20:53', 0, '2020-03-12 19:36:05', '399667');
INSERT INTO `menu` VALUES (38, '炒酸奶', 10, '{\"1\": \"主材料酸奶，取350克就可以了。\", \"2\": \"准备好各种水果。（水果搭配个人随意）\", \"3\": \"将水果洗净切好。\", \"4\": \"取个稍微深一点的盘子垫上油纸，打入350克的酸奶抹平。\", \"5\": \"将各种水果粒洒入酸奶表面，放入冰箱冷冻3小时以上变硬。\", \"6\": \"取出冷冻硬的酸奶，切成块食用。\"}', '{\"自制酸奶\": \"350克\", \"猕猴桃\": \"1个\", \"圣女果\": \"3个\", \"黄桃\": \"1块\", \"提子干\": \"15克\"}', 1, '2020-02-27 22:20:55', 0, '2020-03-12 19:36:05', '399125');
INSERT INTO `menu` VALUES (39, '藤椒肥牛', 1, '{\"1\": \"准备原料，金针菇去根撕散开，蒜剁细末。\", \"2\": \"锅中倒入清水烧开，下入藤椒酱。\", \"3\": \"下入金针菇煮熟，捞出放入盛器中。\", \"4\": \"下入肥牛片煮制，去除浮沫，肥牛片捞出倒在金针菇上面。\", \"5\": \"盛器中倒入底汤，汤量没过肥牛片一半。\", \"6\": \"撒上蒜末，浇少许热油炝香即可。\"}', '{\"渔人杰藤椒酱\": \"100克\", \"肥牛片\": \"500克\", \"金针菇\": \"500克\", \"清水\": \"750克\", \"蒜末\": \"10克\"}', 6, '2020-02-27 22:22:56', 0, '2020-03-12 19:36:05', '517085');
INSERT INTO `menu` VALUES (40, '藤椒水煮肉', 1, '{\"1\": \"猪瘦肉切3mm薄片，用水反复清洗5次，沥干水分，加入浆肉料抓揉至肉片发粘，腌制10分钟。\", \"2\": \"准备原料，娃娃菜去根一分为四散开，腌好的猪肉再次搅打均匀。\", \"3\": \"锅中热油，下入娃娃菜炒至成熟软化，倒入盛器中垫底。\", \"4\": \"锅中倒入清水500g烧开，下入藤椒酱。\", \"5\": \"将猪肉均匀打散下入锅中煮制，持续沸腾约1分钟即可成熟，捞出放在垫菜上。\", \"6\": \"将藤椒汤底倒入盛器中，汤量没过猪肉一半即可。\", \"7\": \"在肉片上撒蒜末，舀取热油，放入花椒、干辣椒炝香后浇在蒜末上即可。\"}', '{\"渔人杰藤椒酱\": \"100克\", \"猪瘦肉\": \"250克\", \"娃娃菜\": \"1棵\", \"清水\": \"500克\", \"热油\": \"20克\", \"蒜末\": \"10克\", \"花椒\": \"5克\", \"干辣椒段\": \"5粒\", \"盐\": \"2克\", \"料酒\": \"10克\", \"水\": \"10克\", \"淀粉\": \"10克\", \"鸡蛋清\": \"半个\"}', 6, '2020-02-27 22:22:57', 0, '2020-03-12 19:36:05', '517096');
INSERT INTO `menu` VALUES (41, '酸菜肥牛', 1, '{\"1\": \"准备原料，粉丝用开水泡软，金针菇去根撕散开。\", \"2\": \"锅中烧水，烫熟金针菇，捞出和粉丝一起放入盛器中垫底。\", \"3\": \"锅中倒入500g清水烧开，下入酸菜酱、25g炒酸菜、鸡精、盐、糖。\", \"4\": \"将肥牛下入酸菜汤底中烫制，期间去除浮沫，烫熟后捞出放入盛器中。\", \"5\": \"将剩余25g炒酸菜放在肥牛上，往盛器中倒入酸菜汤底，汤量可以没过一半肥牛即可。\", \"6\": \"在肥牛上撒蒜末，舀取热油，放入花椒、干辣椒炝香后浇在蒜末上即可。\"}', '{\"渔人杰酸菜酱\": \"25克\", \"炒酸菜\": \"50克\", \"肥牛片\": \"300克\", \"金针菇\": \"300克\", \"水发粉丝\": \"150克\", \"清水\": \"500克\", \"鸡精\": \"5克\", \"盐\": \"3克\", \"热油\": \"20克\", \"蒜末\": \"10克\", \"花椒\": \"5克\", \"干辣椒\": \"5粒\"}', 6, '2020-02-27 22:22:59', 0, '2020-03-12 19:36:05', '517092');
INSERT INTO `menu` VALUES (42, '鱼香茄子', 1, '{\"1\": \"圆茄子切块，葱姜蒜切碎。\", \"2\": \"调制鱼香汁。\", \"3\": \"油烧开炸茄子块。\", \"4\": \"炸制略焦黄捞出。\", \"5\": \"锅中留底油烧至六成热下入葱姜蒜。\", \"6\": \"煸出香味后下入郫县豆瓣酱炒出红油。\", \"7\": \"下入炸好的茄子块，翻炒匀。\", \"8\": \"下入青椒。\", \"9\": \"炒匀后倒入鱼香汁。\", \"10\": \"烧开后炖五分钟，水淀粉勾芡。\", \"11\": \"炒亮后关火加入味精出锅。\"}', '{\"圆茄子\": \"适量\", \"青椒\": \"适量\", \"葱\": \"适量\", \"姜\": \"适量\", \"蒜\": \"适量\", \"盐\": \"适量\", \"生抽\": \"适量\", \"老抽\": \"适量\", \"白砂糖\": \"适量\", \"醋\": \"适量\", \"味精\": \"适量\", \"郫县豆瓣酱\": \"适量\"}', 6, '2020-02-27 22:23:01', 0, '2020-03-12 19:36:05', '516890');
INSERT INTO `menu` VALUES (43, '大厨四宝鱼香肉丝', 1, '{\"1\": \"瘦肉切丝备用。\", \"2\": \"冬笋切丝备用。\", \"3\": \"青红椒切丝备用。\", \"4\": \"加入适量料酒，拌均匀。\", \"5\": \"加入淀粉搅拌均匀。\", \"6\": \"热锅下油，葱姜蒜爆香。\", \"7\": \"倒入鱼香肉丝酱汁，翻炒均匀。\", \"8\": \"放入肉丝，木耳，冬笋，青红尖椒丝。\", \"9\": \"翻炒均匀。\", \"10\": \"出锅。\"}', '{\"瘦肉\": \"200g\", \"冬笋\": \"25g\", \"青尖椒\": \"50g\", \"木耳丝\": \"25g\", \"红尖椒\": \"适量\", \"大厨四宝鱼香肉丝调料\": \"1罐\", \"葱\": \"少许\", \"蒜\": \"适量\", \"姜\": \"适量\"}', 6, '2020-02-27 22:23:02', 0, '2020-03-12 19:36:05', '516771');
INSERT INTO `menu` VALUES (44, '酸辣粉', 1, '{\"1\": \"把粉条用80度水泡软。\", \"2\": \"调汁，花生酱，蒜压成泥，辣椒油。\", \"3\": \"生抽，香醋，麻辣鲜调料一滴拌匀，这调料就很香了。\", \"4\": \"泡软的宽粉，水开煮3分钟左右。\", \"5\": \"青菜烫一下，放入大碗。加入调好的料汁。\", \"6\": \"拌匀开吃。\", \"7\": \"比外面的卫生。\"}', '{\"红薯宽粉\": \"200克\", \"青菜\": \"适量\", \"蒜泥\": \"适量\", \"辣椒油\": \"一勺\", \"花生酱\": \"一勺\", \"生抽\": \"一小勺\", \"香醋\": \"一勺\", \"麻辣鲜调料\": \"1滴\"}', 6, '2020-02-27 22:23:04', 0, '2020-03-12 19:36:05', '516266');
INSERT INTO `menu` VALUES (45, '麻婆豆腐', 1, '{\"1\": \"准备两块豆腐。\", \"2\": \"将豆腐切成小四方块。\", \"3\": \"将豆腐焯水3分钟，然后捞出备用。\", \"4\": \"准备辅料。\", \"5\": \"将辅料炒香，接着放豆腐翻炒均匀后加适量水，焖煮3分钟后调味、调色，出锅前勾芡、撒上葱花即可。\"}', '{\"豆腐\": \"2块\", \"辣椒粉\": \"50g\", \"花椒粉\": \"50g\", \"肉末\": \"50g\", \"蒜末\": \"50g\", \"豆瓣酱\": \"50g\", \"葱花\": \"10g\"}', 6, '2020-02-27 22:23:05', 0, '2020-03-12 19:36:05', '516335');
INSERT INTO `menu` VALUES (46, '美味的香辣口水鸡', 1, '{\"1\": \"用Harthsun珐琅锅烧一锅水 。\", \"2\": \"水中放入葱结，姜片和花椒粒，准备两个大鸡腿洗净放入锅中 。\", \"3\": \"大火烧开后转中火，煮10分钟 。\", \"4\": \"捞出一小碗鸡汁备用 。\", \"5\": \"关火后把鸡腿焖8分钟，鸡腿煮熟后捞出。\", \"6\": \"捞出后迅速把鸡腿放入冰水浸泡 。\", \"7\": \"用手把鸡腿肉撕成自己喜欢的大小，装盘备用 。\", \"8\": \"下面开始调料汁，适量的干辣椒面，花椒面和白芝麻。\", \"9\": \"淋上适量的油做油辣子 。\", \"10\": \"油辣子搅拌均匀后放入蒜泥，葱花，生抽，醋，花椒油，白糖，鸡精，蚝油。\", \"11\": \"加上之前的鸡汁，料汁搅拌均匀后淋在鸡肉上 。\", \"12\": \"撒上一点香菜和白芝麻 。\", \"13\": \"香辣口水鸡完成啦！ \"}', '{\"琵琶腿\": \"2个\", \"姜\": \"5片\", \"生抽\": \"适量\", \"醋\": \"2勺\", \"蚝油\": \"适量\", \"大葱\": \"2根\", \"蒜\": \"半头\", \"鸡精\": \"少许\", \"花椒粒\": \"1小把\", \"干辣椒面\": \"适量\", \"花椒面\": \"适量\", \"花椒油\": \"1勺\", \"白糖\": \"少许\", \"白芝麻\": \"适量\", \"香菜\": \"1把\"}', 6, '2020-02-27 22:23:07', 0, '2020-03-12 19:36:05', '516281');
INSERT INTO `menu` VALUES (47, '农家小炒肉', 1, '{\"1\": \"青椒切圈，蒜、姜切小片。\", \"2\": \"取一点黑豆豉放入碗中加一点水化开。\", \"3\": \"猪瘦肉切片，倒入一点老抽、生抽、白酒、花椒粉抓匀后加入一点淀粉再次抓匀腌制10分钟。\", \"4\": \"油烧开。\", \"5\": \"下入肉片快速翻炒滑散。\", \"6\": \"肉色略微变白下入葱丝，翻炒至肉色彻底变色后盛出备用。\", \"7\": \"另起油锅油烧开。\", \"8\": \"下入青椒圈、蒜片、姜片，翻炒一分钟。\", \"9\": \"加入盐、老抽翻炒均匀。\", \"10\": \"下入炒好的肉片。\", \"11\": \"将青椒、肉片炒匀后倒入化水的黑豆豉，翻炒一分钟翻炒均匀。\", \"12\": \"出锅装盘。\"}', '{\"青椒\": \"适量\", \"猪瘦肉\": \"适量\", \"葱\": \"适量\", \"姜\": \"适量\", \"蒜\": \"适量\", \"老抽\": \"适量\", \"生抽\": \"适量\", \"花椒粉\": \"适量\", \"淀粉\": \"适量\", \"盐\": \"适量\", \"白酒\": \"适量\", \"黑豆豉\": \"适量\"}', 6, '2020-02-27 22:23:09', 0, '2020-03-12 19:36:05', '516263');
INSERT INTO `menu` VALUES (48, '宫保鸡丁', 1, '{\"1\": \"黄瓜切丁，干辣椒花椒少许备用，糖和醋1.5:1的比例和适量淀粉加清水加入小碗中。\", \"2\": \"鸡胸肉切丁加料酒生姜丝盐淀粉腌制。\", \"3\": \"冷油中倒入花生米小火炒香起锅备用。\", \"4\": \"锅中剩适量油烧热加入鸡肉翻炒，变色捞起备用。\", \"5\": \"倒入黄瓜和干辣椒花椒翻炒一会儿。\", \"6\": \"倒入鸡肉并加入酱油豆瓣酱翻炒均匀。\", \"7\": \"再加入糖醋汁翻炒均匀，再加入适量的花生米就可以起锅了。\"}', '{\"鸡胸肉\": \"一块\", \"黄瓜\": \"一根\", \"花生米\": \"适量\", \"生姜\": \"适量\", \"淀粉\": \"适量\", \"糖\": \"少许\", \"料酒\": \"适量\", \"酱油\": \"适量\", \"醋\": \"适量\", \"盐\": \"适量\", \"豆瓣酱\": \"适量\"}', 6, '2020-02-27 22:23:10', 0, '2020-03-12 19:36:05', '516122');
INSERT INTO `menu` VALUES (49, '麻辣香锅', 1, '{\"1\": \"准备好鸡肉丁。\", \"2\": \"虾洗净挑去虾线。\", \"3\": \"山药切片，芹菜、洋葱、彩椒切块。\", \"4\": \"起油锅放入葱姜煸炒。\", \"5\": \"放入鸡丁煸炒变色。\", \"6\": \"放入虾。\", \"7\": \"煸炒变色。\", \"8\": \"放入料酒和少许盐。\", \"9\": \"放洋葱和山药煸炒。\", \"10\": \"放彩椒和芹菜煸炒。\", \"11\": \"放一勺郫县豆瓣、花椒粉、生抽和少许糖。\", \"12\": \"成品图。\"}', '{\"鸡肉丁\": \"半斤\", \"虾\": \"4-5个\", \"芹菜\": \"少许\", \"彩椒\": \"少许\", \"洋葱\": \"少许\", \"山药\": \"少许\", \"郫县豆瓣\": \"适量\", \"盐\": \"少许\", \"花椒粉\": \"适量\", \"胡椒粉\": \"适量\"}', 6, '2020-02-27 22:23:12', 0, '2020-03-12 19:36:05', '515872');
INSERT INTO `menu` VALUES (50, '口水鸡', 1, '{\"1\": \"鸡腿洗净。\", \"2\": \"冷水下锅，放卤肉料。\", \"3\": \"放食盐。\", \"4\": \"盖锅盖，中小火煮30分钟。散养的鸡要煮久一点，肉食鸡不用煮30分钟。\", \"5\": \"花生放微波炉。\", \"6\": \"烤1分50秒。\", \"7\": \"烤熟的花生米凉透。\", \"8\": \"大蒜处理干净切末。\", \"9\": \"放容器内加食盐。\", \"10\": \"放鸡精。\", \"11\": \"放白糖。\", \"12\": \"放蒸鱼豉油。\", \"13\": \"放陈醋。\", \"14\": \"取辣椒油。\", \"15\": \"放辣椒油。\", \"16\": \"凉透的花生米捣碎，放调料里。\", \"17\": \"放芝麻油。\", \"18\": \"拌匀，放葱叶末。\", \"19\": \"煮好的鸡腿，取出来放冰水浸泡一会儿。\", \"20\": \"取出来切块。\", \"21\": \"摆盘。\", \"22\": \"倒上料汁。\", \"23\": \"成品。\", \"24\": \"成品。\"}', '{\"鸡腿\": \"1个\", \"食盐\": \"3克\", \"卤肉料\": \"小许\", \"清水\": \"适量\", \"大蒜\": \"几瓣\", \"花生米\": \"1把\", \"鸡精\": \"小许\", \"白糖\": \"小许\", \"陈醋蒸鱼豉油\": \"各10克\", \"香油\": \"小许\", \"辣酱油\": \"3汤匙\", \"葱叶\": \"小许\"}', 6, '2020-02-27 22:23:13', 0, '2020-03-12 19:36:05', '515764');
INSERT INTO `menu` VALUES (51, '蚂蚁上树', 1, '{\"1\": \"将一把粉丝用冷水浸泡一小时，然后用剪刀把粉丝剪短些。\", \"2\": \"准备1两瘦肉末、适量蒜泥、红椒末、葱花。\", \"3\": \"准备1两瘦肉末、适量蒜泥、红椒末、葱花。\", \"4\": \"起锅烧油，将瘦肉末、蒜末、红椒末放进锅内一起炒香，然后放粉丝调味、调色，出锅前撒上葱花即可装盘。\"}', '{\"粉丝\": \"100g\", \"红椒末\": \"50g\", \"蒜末\": \"50g\", \"葱花\": \"适量\"}', 6, '2020-02-27 22:23:15', 0, '2020-03-12 19:36:05', '515483');
INSERT INTO `menu` VALUES (52, '麻辣拌', 1, '{\"1\": \"主要食材；各种火锅丸子（虾丸，蟹丸、牛肉丸等）、莲藕60克、荷兰豆8个、魔芋丝120克等。\", \"2\": \"先来准备酱料；花生酱、芝麻酱各1茶匙放入碗中，加入1茶匙香油、少许食盐、凉白开搅拌至顺滑浓稠备用（芝麻酱、花生酱根据自家口味加，可以都用芝麻酱，也可以都用花生酱，自家喜欢就好）。\", \"3\": \"准备完酱料咱在准备料汁：香葱切葱花，大蒜切末、线椒切小圈（如果有小米椒也可以放一个，红色点缀成品会更好看）。\", \"4\": \"、切好的葱末、线椒、大蒜末和白芝麻、辣椒粉一起放入碗中。锅中烧热油，趁热浇在辣椒粉上激出香味。\", \"5\": \"依次加入3茶匙生抽、2茶匙香醋。\", \"6\": \"加入1茶匙白糖拌匀即成料汁备用。（味精鸡精着些放不放随自己口味）\", \"7\": \"现在来处理食材，荷兰豆大的一切二小的不用切，莲藕洗净去皮切成薄片。（食材不拘于这几种，喜欢什么蔬菜都可以加）\", \"8\": \"切好的莲藕和荷兰豆、魔芋丝一起放入沸水锅中焯水，捞出后过凉备用。\", \"9\": \"丸子类也一起入沸水锅中炒水捞出，这个不用过凉哈。\", \"10\": \"焯过水的火锅丸子、魔芋丝、荷兰豆、莲藕一起放入大一些的容器中。\", \"11\": \"淋入调好的料汁，和调好的麻酱汁拌匀即可。\", \"12\": \"成品。\"}', '{\"火锅丸子\": \"200克\", \"莲藕\": \"1节\", \"荷兰豆\": \"1把\", \"魔芋丝\": \"120克\", \"芝麻酱\": \"2茶匙\", \"辣椒粉\": \"1茶匙\", \"白芝麻\": \"1茶匙\", \"香葱\": \"2棵\", \"大蒜\": \"3瓣\", \"生抽\": \"3茶匙\", \"香醋\": \"2茶匙\", \"白糖\": \"1茶匙\", \"香油\": \"1茶匙\", \"线椒\": \"1个\", \"食用油\": \"适量\"}', 6, '2020-02-27 22:23:16', 0, '2020-03-12 19:36:05', '515225');
INSERT INTO `menu` VALUES (53, '鱼香鸡丝', 1, '{\"1\": \"食材。\", \"2\": \"鸡肉切丝，加入少许白胡椒粉、少许食盐、1茶匙淀粉、1茶匙料酒抓匀腌渍几分钟。\", \"3\": \"腌渍鸡肉的时候来处理辅料，豆瓣酱细细的切成末、红萝卜、泡发好的木耳，玉兰片分别切成丝。\", \"4\": \"准备一碗料汁，防止一会炒至的时候手忙脚乱。1茶匙料酒、2茶匙生抽、2茶匙白糖、3茶匙香醋、1茶匙玉米淀粉再加入少许清水搅拌均匀。\", \"5\": \"腌渍好的鸡丝中加入1茶匙食用油，再次拌匀。\", \"6\": \"锅中放油，油温五六成热下入鸡丝炒至变色即盛出。\", \"7\": \"另起锅，放入蒜末、剁椒、豆瓣酱、葱末小火炒出红油。\", \"8\": \"炒出红油后，放入笋丝、木耳丝、胡萝卜丝翻炒。\", \"9\": \"炒至三丝变软，放入滑炒好的肉丝。\", \"10\": \"快速翻炒均匀，倒入备好的料汁。\", \"11\": \"快速炒至料汁浓稠，基本裹在食材上即可光火。\", \"12\": \"成品。\"}', '{\"鸡胸肉\": \"200克\", \"玉兰片\": \"60克\", \"水发木耳\": \"5多\", \"胡萝卜\": \"半根\", \"剁椒\": \"1茶匙\", \"红油豆瓣酱\": \"1茶匙\", \"大蒜\": \"3瓣\", \"生抽\": \"2茶匙\", \"香醋\": \"3茶匙\", \"白糖\": \"2茶匙\", \"料酒\": \"1茶匙\", \"玉米淀粉\": \"1茶匙\", \"葱末\": \"1茶匙\"}', 6, '2020-02-27 22:23:18', 0, '2020-03-12 19:36:05', '504895');
INSERT INTO `menu` VALUES (54, '麻辣香锅', 1, '{\"1\": \"娃娃菜焯一下。\", \"2\": \"提前泡好腐竹木耳。\", \"3\": \"各种丸子。\", \"4\": \"香菜备用。\", \"5\": \"芹菜切好焯水。\", \"6\": \"豆皮切好焯水。\", \"7\": \"午餐肉。\", \"8\": \"顺便夸爆这款午餐肉，朋友送的，今天吃一下，跟平时买的完全不一样，这个肉切开都是大块肉粒，不像火锅店的，都是淀粉。\", \"9\": \"看看这肉，就是切的时候容易散。\", \"10\": \"很不错。\", \"11\": \"回题，菜花，荷兰豆焯水。\", \"12\": \"金针菇焯水。\", \"13\": \"炸点儿果仁，火大了。\", \"14\": \"葱姜蒜切好。\", \"15\": \"热油放入葱姜蒜，放入虾，边炒边按压虾头，挤出虾油。\", \"16\": \"干辣椒。\", \"17\": \"炒虾的油放入花椒，干辣椒，大料，香叶，豆瓣酱和火锅底料，小火炒开后放入所有食材翻炒即可。\", \"18\": \"成品满满一盆。\"}', '{\"白菜\": \"适量\", \"菜花\": \"适量\", \"芹菜\": \"适量\", \"土豆\": \"适量\", \"金针菇\": \"适量\", \"午餐肉\": \"适量\", \"各种丸子\": \"适量\", \"腊肠\": \"适量\", \"虾\": \"适量\", \"花椒\": \"适量\", \"红尖椒\": \"适量\", \"香叶\": \"适量\", \"郫县豆瓣酱\": \"适量\", \"火锅底料\": \"适量\", \"葱\": \"适量\", \"麻椒\": \"适量\", \"姜\": \"适量\", \"蒜\": \"适量\"}', 6, '2020-02-27 22:23:19', 0, '2020-03-12 19:36:05', '513271');
INSERT INTO `menu` VALUES (55, '鱼香肉丝', 1, '{\"1\": \"同脊肉切丝，放料酒、酱油、白胡椒粉腌制。\", \"2\": \"准备郫县豆瓣和泡椒。\", \"3\": \"辅料切丝，笋、黑木耳、胡萝卜切丝，姜切丝，小葱切丝。\", \"4\": \"准备调料汁：一勺料酒、一勺糖、两勺醋、一勺酱油、一勺蒜蓉，一勺葱段、一勺淀粉。\", \"5\": \"锅烧热放油烧热。\", \"6\": \"放入郫县豆瓣和泡椒炒出红油。\", \"7\": \"放入姜丝翻炒。\", \"8\": \"放入肉丝炒八成熟。\", \"9\": \"放入胡萝卜丝、笋丝、黑木耳丝翻炒。\", \"10\": \"放入调料汁，出锅前淋少许红油。\", \"11\": \"盛盘，表面撒些葱花。\"}', '{\"通脊\": \"适量\", \"笋\": \"适量\", \"胡萝卜\": \"适量\", \"黑木耳\": \"适量\", \"姜\": \"适量\", \"葱\": \"适量\", \"郫县豆瓣\": \"适量\", \"泡椒\": \"适量\", \"酱油\": \"适量\", \"料酒\": \"适量\", \"糖\": \"适量\", \"淀粉\": \"适量\", \"蒜蓉\": \"适量\"}', 6, '2020-02-27 22:23:20', 0, '2020-03-12 19:36:05', '514364');
INSERT INTO `menu` VALUES (56, '零失败水煮肉片', 1, '{\"1\": \"先准备一包水煮肉片的调料包。我很喜欢蜀味源的，特别正宗特别香。\", \"2\": \"准备包川粉，约200克，也可以用红薯粉条。\", \"3\": \"黑木耳切小块。\", \"4\": \"莴苣杆切薄片。\", \"5\": \"金针菇剪去菇脚，洗净备用。\", \"6\": \"瘦猪肉片成薄片。\", \"7\": \"将腌料包倒入瘦肉中，抓匀腌制备用。\", \"8\": \"烧锅开水，放些盐，滴几滴油，放入黑木耳煮3分钟。\", \"9\": \"煮好的黑木耳过下凉开水，沥干水份，铺在砂锅底部。\", \"10\": \"另烧一锅新开水，放点盐，滴几滴油，下入莴苣片抄水30秒。\", \"11\": \"抄好水的莴苣片，过下凉开水，沥干水份铺在黑木耳上。\", \"12\": \"下金针菇煮开1分钟。\", \"13\": \"捞起金针菇，过凉开水后铺在莴苣片上。\", \"14\": \"洗净的锅中，加适量清水，放入调料包，中火煮开。\", \"15\": \"下川粉煮2分钟。\", \"16\": \"调大火，下腌制好的肉片，煮开。\", \"17\": \"将煮好的肉片及汤汁倒入砂锅中。\", \"18\": \"将干料包集中的倒在砂锅的中间，方便等下浇热油。\", \"19\": \"撒点葱花在干料上。\", \"20\": \"锅中烧滚油炝在干料上，油温一定要高。我用大火烧油，差不多1分钟，其间会微微晃动下锅，让油受热更均匀。\", \"21\": \"嫩嫩的肉片，配上白米饭，可香了！\", \"22\": \"Q弹爽滑的川粉，嚼劲十足！\", \"23\": \"零失败的水煮肉片，快学起来吧！\"}', '{\"瘦肉\": \"300g\", \"水煮肉片调料包\": \"1包\", \"黑木耳\": \"适量\", \"莴苣\": \"0.5根\", \"金针菇\": \"适量\", \"香葱\": \"适量\", \"食盐\": \"适量\", \"食用油\": \"少许\", \"川粉\": \"1包\"}', 6, '2020-02-27 22:23:22', 0, '2020-03-12 19:36:05', '514329');
INSERT INTO `menu` VALUES (57, '水煮肉片', 1, '{\"1\": \"鸡胸肉切成薄片，加入料酒盐淀粉拌匀，再加入花椒面，辣椒面，五香粉少许拌匀，其实大家也可以用猪肉牛肉。\", \"2\": \"把其他材料也备好。\", \"3\": \"锅中放少许油烧热放入火锅底料，大葱，生姜翻香。\", \"4\": \"加入适量清水煮沸，先放土豆煮至八成熟，再加入豆皮，根据咸淡加入酱油和盐调味。\", \"5\": \"豆皮土豆煮好之后捞出，开大火放入鸡胸肉，要一片一片的放，而且动作快点。\", \"6\": \"放完之后，鸡胸肉煮两分钟左右就可以起锅享用，撒入香菜。\"}', '{\"鸡胸肉\": \"一块\", \"土豆\": \"适量\", \"豆皮\": \"适量\", \"料酒\": \"少许\", \"火锅底料\": \"适量\", \"香料\": \"少许\", \"大葱\": \"适量\", \"生姜\": \"适量\", \"盐\": \"适量\"}', 6, '2020-02-27 22:23:23', 0, '2020-03-12 19:36:05', '514289');
INSERT INTO `menu` VALUES (58, '口水鸡', 1, '{\"1\": \"准备一只鸡腿，用清水洗去鸡腿的血水。\", \"2\": \"锅中加水，放入鸡腿、姜块、干红椒、桂皮、花椒粉、料酒、盐、胡椒粉，小火煮15-20分钟。\", \"3\": \"准备辅料小米椒、花椒、姜蒜末。\", \"4\": \"将煮好的鸡腿切块摆盘，将辅料调味、炒香铺在鸡腿表面，撒上葱花即可上桌。\"}', '{\"鸡腿\": \"1个\", \"蒜\": \"50g\", \"小米椒\": \"5个\", \"花椒\": \"20g\", \"姜块\": \"1个\"}', 6, '2020-02-27 22:23:25', 0, '2020-03-12 19:36:05', '514010');
INSERT INTO `menu` VALUES (59, '水煮肉片', 1, '{\"1\": \"准备食材:粉条、莴笋丝、姜葱蒜、干辣椒、肉片(肉片用油、盐、鸡蛋清、淀粉、料酒、胡椒粉拌匀)。\", \"2\": \"将热水泡开的粉条放入砂锅备用。\", \"3\": \"起锅烧油，爆香姜蒜。\", \"4\": \"放入一勺豆瓣酱，炒出红油。\", \"5\": \"加入适量的水。\", \"6\": \"煮开后放入酱油、陈醋和糖。\", \"7\": \"放入莴笋丝焯熟。\", \"8\": \"捞起放入砂锅的粉条上。\", \"9\": \"留下的汤汁，下肉片煮。\", \"10\": \"汆熟马上捞起放入砂锅。\", \"11\": \"红油汤汁倒入砂锅内，开火煮开。\", \"12\": \"炖开后放入干辣椒和葱花。\", \"13\": \"浇上热油完成。\", \"14\": \"成品。香辣开胃。\"}', '{\"瘦肉\": \"180g\", \"干粉条\": \"100g\", \"莴笋丝\": \"100g\", \"蒜\": \"适量\", \"姜\": \"适量\", \"干辣椒\": \"适量\", \"葱花\": \"适量\", \"淀粉\": \"适量\", \"鸡蛋清\": \"适量\", \"盐\": \"适量\", \"料酒\": \"适量\", \"胡椒粉\": \"适量\", \"豆瓣酱\": \"1勺\", \"酱油\": \"2勺\", \"陈醋\": \"1勺\", \"糖\": \"2匙\", \"鸡精\": \"少许\", \"油\": \"适量\", \"水\": \"适量\"}', 6, '2020-02-27 22:23:26', 0, '2020-03-12 19:36:05', '513895');
INSERT INTO `menu` VALUES (60, '麻辣水煮鱼', 1, '{\"1\": \"准备一个鱼尾，将鱼骨肉分离，肉切片并腌制10分钟、骨切段。\", \"2\": \"准备一个鱼尾，将鱼骨肉分离，肉切片并腌制10分钟、骨切段。\", \"3\": \"准备一个鱼尾，将鱼骨肉分离，肉切片并腌制10分钟、骨切段。\", \"4\": \"准备一些莴笋皮和青红椒、姜蒜末、花椒。\", \"5\": \"准备一些莴笋皮和青红椒、姜蒜末、花椒。\", \"6\": \"起锅烧油，将半块麻辣火锅底料炒香后加水、调味，放鱼骨，大火煮出浓汤。\", \"7\": \"将莴笋皮在锅内煮断生与鱼骨一起捞出垫底。\", \"8\": \"将鱼片在锅内煮熟后捞出装入盆内，将调味料铺在鱼片表面，淋上热油即可上桌。\", \"9\": \"成品。\"}', '{\"鱼尾\": \"1个\", \"莴笋杆\": \"半个\", \"青椒\": \"适量\", \"花椒\": \"10g\", \"姜末\": \"适量\", \"麻辣火锅底料\": \"半块\", \"红椒\": \"适量\", \"蒜末\": \"适量\"}', 6, '2020-02-27 22:23:28', 0, '2020-03-12 19:36:05', '513740');
INSERT INTO `menu` VALUES (61, '鱼香肉丝', 1, '{\"1\": \"肉切肉丝，加水淀粉、料酒抓匀备。\", \"2\": \"木耳、胡萝卜分别切丝。姜、蒜剁蓉（忘拍照了，各位脑补）。\", \"3\": \"生抽、陈醋、白糖、淀粉加少许清水调成汁备用。\", \"4\": \"锅烧热放油，倒入肉丝稍炒至变色盛出。\", \"5\": \"锅里留底油，放剁好的姜、蒜及豆瓣酱炒香。\", \"6\": \"放入木耳丝、胡萝卜丝翻炒。\", \"7\": \"放入之前炒过的丝肉和调好的碗汁快速翻烧炒均匀盛出。\", \"8\": \"一道美味的下饭菜就做好了，开饭啦。\"}', '{\"瘦肉\": \"3两\", \"木耳\": \"6朵\", \"胡萝卜\": \"1个\", \"姜\": \"1小块\", \"蒜\": \"2瓣\", \"陈醋\": \"2勺\", \"生抽\": \"3勺\", \"淀粉\": \"适量\", \"白糖\": \"少许\", \"豆瓣酱\": \"1勺\"}', 6, '2020-02-27 22:23:29', 0, '2020-03-12 19:36:05', '513655');
INSERT INTO `menu` VALUES (62, '麻婆豆腐', 1, '{\"1\": \"姜、蒜剁蓉，豆瓣酱剁越碎越出味。\", \"2\": \"豆腐切小块入淡盐水稍煮捞起。\", \"3\": \"锅里加油，小火。入牛肉末炒香，加入剁好的姜、蒜、豆瓣酱炒出红油。\", \"4\": \"加入清水或高汤煮出味道，再放入豆腐稍煮，放少许生抽。豆瓣酱和生抽有盐味，不用再放盐。\", \"5\": \"淋入水淀粉翻炒，撒少许麻椒面翻炒均匀。\", \"6\": \"起锅，一碗鲜香麻辣的麻婆豆腐就可上桌了。\"}', '{\"豆腐\": \"半斤\", \"牛肉末\": \"1两\", \"姜\": \"1小块\", \"蒜\": \"3瓣\", \"郫县豆瓣酱\": \"1勺\", \"生抽\": \"1匙\", \"水淀粉\": \"少许\"}', 6, '2020-02-27 22:23:30', 0, '2020-03-12 19:36:05', '460946');
INSERT INTO `menu` VALUES (63, '蒜泥白肉', 1, '{\"1\": \"五花肉。\", \"2\": \"冷水下锅，加少许米酒，盐煮熟(米酒去腥，盐令肉更紧实)。\", \"3\": \"五花肉煮至用筷子很轻松穿入即可。\", \"4\": \"取出，放凉或过冰水，或放冰箱冰一会。\", \"5\": \"三粒蒜头去皮。\", \"6\": \"用压蒜器压成泥。\", \"7\": \"如图，很方便的。\", \"8\": \"加入蚝油，酱油，糖，胡椒粉调匀，酱汁就好了。\", \"9\": \"五花肉放在熟食菜板上切薄片。\", \"10\": \"把五花肉摆入碟子中，随各人喜爱摆放。\", \"11\": \"把酱汁淋在五花肉表面，加点小米辣点缀下，就完成了。\", \"12\": \"成品图。\", \"13\": \"成品图。\"}', '{\"五花肉\": \"适量\", \"蒜头\": \"3粒\", \"蚝油\": \"少许\", \"酱油\": \"少许\", \"胡椒粉\": \"少许\", \"糖\": \"少许\", \"米酒\": \"少许\", \"盐\": \"少许\", \"小米辣\": \"少许\"}', 6, '2020-02-27 22:23:32', 0, '2020-03-12 19:36:05', '512792');
INSERT INTO `menu` VALUES (64, '酸萝卜老鸭汤', 1, '{\"1\": \"鸭边腿清洗干净。\", \"2\": \"白萝卜，鸭腿切块。\", \"3\": \"鸭腿放清水中煮开。\", \"4\": \"去除浮沫。\", \"5\": \"转入砂锅中，放酸萝卜和调料包煮1小时，然后放白萝卜再炖20分钟。\"}', '{\"酸萝卜老鸭汤调料\": \"1袋\", \"鸭边腿\": \"2个\", \"白萝卜\": \"半个\"}', 6, '2020-02-27 22:23:33', 0, '2020-03-12 19:36:05', '512207');
INSERT INTO `menu` VALUES (65, '四川 蒜苗回锅肉', 1, '{\"1\": \"豆瓣酱准备。\", \"2\": \"肉切片，姜蒜切好，蒜苗切好。\", \"3\": \"起锅放油，下入猪肉爆出油出来。\", \"4\": \"有点金黄色就可以了。\", \"5\": \"打出来装好。\", \"6\": \"下姜蒜爆炒。\", \"7\": \"下入豆瓣酱翻炒出红油。\", \"8\": \"然后加入蒜苗头。再加入肉。加老抽加盐翻炒。\", \"9\": \"一直翻炒。\", \"10\": \"加入叶子。翻炒。\", \"11\": \"断生就可以。\", \"12\": \"成品图。\"}', '{\"肉\": \"适量\", \"蒜苗\": \"适量\", \"姜\": \"适量\", \"豆瓣酱\": \"适量\", \"盐\": \"适量\", \"老抽\": \"适量\", \"蒜\": \"适量\"}', 6, '2020-02-27 22:23:35', 0, '2020-03-12 19:36:05', '512063');
INSERT INTO `menu` VALUES (66, '自制麻辣火锅', 1, '{\"1\": \"各种配菜洗净备用。\", \"2\": \"火锅底料放入锅中化开。\", \"3\": \"加入半锅水烧开后，先加入不容易熟的火锅粉和土豆片，然后依次放入各种配料煮5分钟左右。\", \"4\": \"摆放完毕，开始煮5分钟。\", \"5\": \"出锅，配上饮料和牛奶，慢慢享受火锅的美味吧！\"}', '{\"火锅底料\": \"250g\", \"金针菇\": \"200g\", \"火锅肠\": \"50g\", \"火锅粉\": \"100g\", \"火锅肉丸子\": \"500g\", \"虾饺\": \"100g\", \"火锅笋\": \"100g\", \"香油\": \"20g\", \"土豆\": \"1个\"}', 6, '2020-02-27 22:23:36', 0, '2020-03-12 19:36:05', '511672');
INSERT INTO `menu` VALUES (67, '鱼香茄子', 1, '{\"1\": \"茄子切成段。\", \"2\": \"姜葱切末。\", \"3\": \"锅中放入适量油烧热后放入姜葱蒜爆香。\", \"4\": \"放入肉末煸炒。\", \"5\": \"放入茄子翻炒至断生变色。\", \"6\": \"调料加入适量水调匀。\", \"7\": \"放入煸炒出香味的茄子中加入适量水大火烧开即可。\", \"8\": \"成品装盘。\"}', '{\"茄子\": \"适量\", \"肉末\": \"适量\", \"鱼香茄子调料\": \"适量\", \"油\": \"适量\", \"姜葱\": \"适量\", \"蒜茸\": \"适量\", \"盐\": \"适量\"}', 6, '2020-02-27 22:23:37', 0, '2020-03-12 19:36:05', '511468');
INSERT INTO `menu` VALUES (68, '鱼香鸡丝', 1, '{\"1\": \"准备好材料。\", \"2\": \"鸡腿去骨去皮切丝，加盐、黄酒、淀粉抓匀后腌制几分钟。\", \"3\": \"木耳切丝；山笋切丝。\", \"4\": \"青红椒切丝。\", \"5\": \"备好葱姜蒜末；将剩下的黄酒、醋、糖、老抽搅拌均匀，做成碗汁备用。\", \"6\": \"锅烧热后倒入油，倒入鸡丝煸炒至肉丝变色后盛出（也可以不用盛出，个人喜欢把炒出的汁水去掉）。\", \"7\": \"再热油，倒入木耳笋丝炒一两分钟盛出。\", \"8\": \"锅洗净，重新倒入一点儿油，放入郫县豆瓣酱和剁椒碎，翻炒出红油和香味后，加入葱姜蒜末。\", \"9\": \"炒出香味后倒入鸡肉和蔬菜丝，倒入碗汁炒匀。\", \"10\": \"再加入椒丝炒匀即可出锅。\"}', '{\"鸡腿\": \"3只\", \"山笋\": \"60克\", \"泡发木耳\": \"50克\", \"青椒\": \"半个\", \"红椒\": \"半个\", \"葱\": \"2根\", \"姜\": \"1片\", \"蒜\": \"1瓣\", \"糖\": \"半茶匙\", \"干淀粉\": \"1茶匙\", \"黄酒\": \"1汤匙\", \"醋\": \"1汤匙\", \"老抽\": \"半茶匙\", \"郫县豆瓣酱\": \"1汤匙\", \"剁椒碎\": \"1汤匙\", \"油\": \"适量\"}', 6, '2020-02-27 22:23:39', 0, '2020-03-12 19:36:05', '511081');
INSERT INTO `menu` VALUES (69, '回锅肉', 1, '{\"1\": \"五花肉冷水入锅，加入葱段，姜片，八角，花椒粒和黄酒，烧开转小火煮40分钟。\", \"2\": \"取出切片。\", \"3\": \"姜切片，干红辣椒切段，青椒去蒂去籽切片，青蒜切段。\", \"4\": \"锅中加入植物油，放入肉片翻炒出油后拨到一边。\", \"5\": \"放入剁碎的郫县豆瓣酱和甜面酱炒出红油，再同肉片炒匀。\", \"6\": \"加入白糖，鸡汁，放入青椒和青蒜茎翻炒断生后放入青蒜叶，炒匀即可。\"}', '{\"五花肉\": \"400g\", \"大葱\": \"1段\", \"姜\": \"1块\", \"八角\": \"1个\", \"花椒粒\": \"1匙\", \"黄酒\": \"1勺\", \"干红辣椒\": \"3根\", \"青椒\": \"1个\", \"青蒜\": \"3根\", \"植物油\": \"1勺\", \"郫县豆瓣酱\": \"1勺\", \"甜面酱\": \"1勺\", \"白糖\": \"1匙\", \"鸡汁\": \"1匙\"}', 6, '2020-02-27 22:23:40', 0, '2020-03-12 19:36:05', '511077');
INSERT INTO `menu` VALUES (70, '凤尾滑肉汤', 1, '{\"1\": \"瘦肉切片后，加入盐、胡椒粉、料酒和豆瓣酱搅拌均匀，码味半小时，让肉充分入味。\", \"2\": \"肉碗里加入两勺水，放入红薯粉搅拌均匀，一定要让红薯粉裹满肉片，成为一块包着肉片的粉团，然后一团一团的用筷子挑入烧开的水中。尽量不要让一团一团的肉粉团挨在一起哦！\", \"3\": \"盖上锅盖焖煮2分钟，等肉粉团浮上来以后，就加入凤尾在水里烫一会儿即可出锅。\", \"4\": \"出锅。\"}', '{\"瘦肉\": \"50g\", \"凤尾\": \"20g\", \"红薯粉\": \"50g\", \"黄瓜片\": \"5片\", \"姜粉\": \"2g\", \"豆瓣酱\": \"1勺\", \"胡椒粉\": \"2g\", \"料酒\": \"1勺\"}', 6, '2020-02-27 22:23:41', 0, '2020-03-12 19:36:05', '511073');
INSERT INTO `menu` VALUES (71, '豆豉回锅肉炒青椒', 1, '{\"1\": \"五花肉把肥的部分切下来，用水把肉煮熟。瘦的部分切成肉丝。\", \"2\": \"肥肉煮好以后，切成片在锅里干煎，熬出油来。\", \"3\": \"肥肉熬出油以后，放入瘦肉片翻炒，加入几粒花椒增香，放入一勺豆豉翻炒均匀。\", \"4\": \"倒入码好盐味的青椒丝翻炒均匀，青椒炒熟后，加入一勺酱油翻炒即可出锅。\", \"5\": \"出锅。\"}', '{\"五花肉\": \"200g\", \"大青椒\": \"4个\", \"豆豉\": \"1勺\", \"酱油\": \"1勺\"}', 6, '2020-02-27 22:23:43', 0, '2020-03-12 19:36:05', '511068');
INSERT INTO `menu` VALUES (72, '辣子鸡', 1, '{\"1\": \"将鸡腿和鸡胸上的肉剔下，切成大小适中的块，用水清洗一遍，加入料酒1匙、生抽2匙、少许胡椒粉和盐抓匀腌制2个小时以上。\", \"2\": \"放入淀粉再次抓匀。\", \"3\": \"锅内油烧至七成热，放入鸡块小火炸3分钟。\", \"4\": \"再用大火复炸至金黄，控油捞出。\", \"5\": \"辣椒剪断，姜切丝，蒜拍松切片，葱切碎备用。\", \"6\": \"锅底留少许食用油，放入葱姜蒜和花椒，小火爆香。\", \"7\": \"放入辣椒断煸炒一下。\", \"8\": \"倒入鸡块，加入生抽、白糖翻炒2分钟。\", \"9\": \"撒入白芝麻翻炒一下。\", \"10\": \"撒上葱花出锅。\"}', '{\"鸡腿\": \"2个\", \"鸡胸\": \"1个\", \"辣椒\": \"适量\", \"花椒\": \"1匙\", \"葱\": \"1段\", \"姜\": \"3片\", \"蒜\": \"2瓣\", \"白芝麻\": \"1匙\", \"白糖\": \"1匙\", \"淀粉\": \"2匙\", \"料酒\": \"1匙\", \"生抽\": \"3匙\", \"盐\": \"适量\", \"胡椒粉\": \"少许\", \"食用油\": \"适量\"}', 6, '2020-02-27 22:23:44', 0, '2020-03-12 19:36:05', '509426');
INSERT INTO `menu` VALUES (73, '怪味鸡', 1, '{\"1\": \"主要食材，三黄鸡，姜片、葱段、栀子。\", \"2\": \"黄鸡去除全部内脏，剪去指甲，切去鸡屁股冲洗干净。\", \"3\": \"凉水放入锅中，加入姜片、葱段、2个栀子烧沸后改最小火煮15分钟左右。\", \"4\": \"用筷子扎一下没有血水冒出即可。\", \"5\": \"煮好的三黄鸡取出稍稍晾凉。\", \"6\": \"调味料大集合。\", \"7\": \"芝麻酱中加入香油、红油充分调匀，搅拌到顺滑。\", \"8\": \"再依次加入剩余的调料。\", \"9\": \"大蒜、香葱、姜分别切末。\", \"10\": \"切好的葱姜蒜末也放入调料碗中拌匀。\", \"11\": \"稍凉的鸡斩块。\", \"12\": \"码入盘中上，淋调好的怪味汁，最后把熟芝麻仁和花生碎撒在上边即可。\"}', '{\"三黄鸡\": \"半只\", \"葱段\": \"10克\", \"姜片\": \"5片\", \"栀子\": \"2个\", \"料酒\": \"20克\", \"花生碎\": \"1茶匙\", \"熟芝麻\": \"1/2茶匙\", \"芝麻酱\": \"1茶匙\", \"辣椒油\": \"2茶匙\", \"香油\": \"1茶匙\", \"生抽\": \"2茶匙\", \"陈醋\": \"2茶匙\", \"白糖\": \"1茶匙\", \"花椒粉\": \"1/3茶匙\", \"香葱\": \"1克\", \"大蒜\": \"3瓣\", \"姜\": \"5克\"}', 6, '2020-02-27 22:23:45', 0, '2020-03-12 19:36:05', '507895');
INSERT INTO `menu` VALUES (74, '辣炒鸡丁', 1, '{\"1\": \"鸡腿肉切丁，加入生抽、料酒、盐各1匙，搅拌均匀，放入冰箱腌制2小时。\", \"2\": \"锅内放入食用油，油烧热至八成热，放入鸡丁炸至金黄。\", \"3\": \"捞出控油备用。\", \"4\": \"辣椒剪成小段去除辣椒籽，姜切丝，葱切碎。\", \"5\": \"锅内留少许底油，加入葱、姜、花椒、八角、辣椒爆香。\", \"6\": \"倒入鸡丁翻炒片刻，加入少许生抽、白糖继续翻炒几下出锅。\"}', '{\"鸡腿肉\": \"适量\", \"辣椒\": \"适量\", \"花椒\": \"适量\", \"八角\": \"适量\", \"葱、姜\": \"适量\", \"食用油\": \"适量\", \"生抽、料酒、盐\": \"适量\", \"白糖\": \"少许\"}', 6, '2020-02-27 22:23:47', 0, '2020-03-12 19:36:05', '508571');
INSERT INTO `menu` VALUES (75, '简单快手的辣子鸡', 1, '{\"1\": \"所有食材都准备好，买回来的鸡清洗干净用刀剁成2～3厘米的鸡块备用。\", \"2\": \"剁好的鸡块放入锅内倒入适量水水煮开在煮2～3分钟后捞出控干水分放入盘中备用。\", \"3\": \"蒜去皮葱姜洗净分别和蒜切成葱末和蒜片和姜片备用。\", \"4\": \"取2个小米辣洗净切成末备用。\", \"5\": \"锅内倒入适量油烧热分别倒入切好的葱姜蒜小米辣炒出香味后倒入适量的花椒再次炒出香味。\", \"6\": \"倒入焯好的鸡块翻炒均匀后倒入1匙生抽，半匙老抽，1匙蚝油翻炒均匀后在倒入1匙料酒翻炒均匀。\", \"7\": \"炒好的鸡倒入1瓶啤酒中小火盖上锅盖煮开在煮片刻后倒入2匙食用盐翻炒均匀盖上锅盖继续炖煮。\", \"8\": \"青红菜椒提前放到盆中倒入适量水和适量食用盐浸泡15~20分钟后清洗干净用刀切块备用。\", \"9\": \"等水煮的差不多的时候，转大火倒入切好的菜椒大火炒至菜椒断生关火盛出即可。\"}', '{\"三黄鸡\": \"1只\", \"青菜椒\": \"1个\", \"红彩椒\": \"1个\", \"啤酒\": \"1瓶\", \"花莲\": \"适量\", \"料酒\": \"1匙\", \"生抽\": \"1匙\", \"蚝油\": \"1匙\", \"小米辣\": \"2个\", \"盐\": \"2匙\", \"蒜瓣\": \"4瓣\"}', 6, '2020-02-27 22:23:49', 0, '2020-03-12 19:36:05', '508477');
INSERT INTO `menu` VALUES (76, '麻辣香锅', 1, '{}', '{\"豆腐\": \"1块\", \"虾\": \"5只\", \"土豆\": \"5颗\", \"鸡\": \"1只\", \"白菜\": \"1颗\", \"蘑菇\": \"1颗\", \"牛猪肉片\": \"2ml\", \"肉素鱼丸子\": \"15颗\", \"火锅料\": \"1ml\", \"鸡汤\": \"1ml\"}', 6, '2020-02-27 22:23:50', 0, '2020-03-12 19:36:05', '508433');
INSERT INTO `menu` VALUES (77, '鱼香肉丝', 1, '{\"1\": \"猪里脊切片。\", \"2\": \"切成均匀的肉丝。\", \"3\": \"木耳切丝，胡萝卜擦丝。\", \"4\": \"绿椒切斜丝。\", \"5\": \"肉丝内适量盐，料酒，生抽，生粉。\", \"6\": \"抓匀腌几分钟。\", \"7\": \"4勺陈醋，3勺白糖，2勺生抽，1勺生粉调成鱼香汁。\", \"8\": \"锅内适量油，开小火，肉丝放入。\", \"9\": \"用筷子快速滑至变色盛出。\", \"10\": \"锅内另放一点油，姜蒜放入炒香。\", \"11\": \"郫县豆瓣酱放入一勺炒出红油。\", \"12\": \"胡萝卜，木耳，青椒放入。\", \"13\": \"翻炒几下，一勺开水。\", \"14\": \"肉丝放入。\", \"15\": \"翻炒两下。\", \"16\": \"倒入料汁翻炒汤汁明亮关火盛盘。\", \"17\": \"成品。\", \"18\": \"成品。\"}', '{\"里脊肉\": \"适量\", \"胡萝卜丝\": \"适量\", \"木耳丝\": \"适量\", \"螺丝椒\": \"一个\", \"花生油\": \"适量\", \"陈醋\": \"4小勺\", \"白糖\": \"3小勺\", \"生抽\": \"2小勺\", \"生粉\": \"1小勺\", \"盐\": \"适量\", \"料酒\": \"适量\"}', 6, '2020-02-27 22:23:51', 0, '2020-03-12 19:36:05', '508244');
INSERT INTO `menu` VALUES (78, '剁椒鱼头', 1, '{\"1\": \"鱼头洗净，放料酒、酱油、胡椒粉、姜片腌制半小时。\", \"2\": \"剁椒加蒜末、1小勺蚝油搅拌均匀备用。\", \"3\": \"大盘葱姜铺底。\", \"4\": \"鱼放上，均匀浇上拌好的剁椒末，蒸17分钟（今天的鱼头比较大，多蒸了会儿）。\", \"5\": \"蒸鱼期间准备一下蒜末，切小葱叶备用，闹钟还剩一分钟时起锅烧油。\", \"6\": \"关火浇热油，浇在葱花上面，滋啦滋啦。\", \"7\": \"上桌了。\", \"8\": \"香香滴。\"}', '{\"花鲢鱼头\": \"适量\", \"红剁椒\": \"适量\", \"葱\": \"适量\", \"姜\": \"适量\", \"蒜\": \"适量\", \"小葱\": \"适量\", \"料酒\": \"适量\", \"酱油\": \"适量\", \"胡椒粉\": \"适量\", \"蚝油\": \"适量\"}', 6, '2020-02-27 22:23:53', 0, '2020-03-12 19:36:05', '500612');
INSERT INTO `menu` VALUES (79, '鲜榨石榴汁', 39, '{\"1\": \"石榴剥皮后取籽\", \"2\": \"准备好惠人原汁机，用小勺一点点的放入机器里(惠人是低速榨汁，所以必须少量放入)\", \"3\": \"全部榨好，打开出汁口，美味果汁哗啦啦的流下来\", \"4\": \"成品。\", \"5\": \"成品。\"}', '{\"石榴籽\": \"约1000克\"}', 2, '2020-02-27 22:24:40', 0, '2020-03-12 19:36:05', '487016');
INSERT INTO `menu` VALUES (80, '金奇艺火龙果汁', 35, '{\"1\": \"准备食材\", \"2\": \"剥皮\", \"3\": \"金奇艺切块\", \"4\": \"火龙果切块\", \"5\": \"放入破壁机或者豆浆机中，加入400克纯净水\", \"6\": \"果汁档\", \"7\": \"喜欢甜的可以放少许蜂蜜。\"}', '{\"火龙果\": \"一个\", \"金奇异果\": \"两个\", \"蜂蜜\": \"少许\", \"纯净水\": \"400克\"}', 2, '2020-02-27 22:24:41', 0, '2020-03-12 19:36:05', '484078');
INSERT INTO `menu` VALUES (81, '鲜榨西瓜汁', 33, '{\"1\": \"西瓜半个，足够一家人每人榨一杯\", \"2\": \"西瓜切1厘米大小见方的丁，可以更大一点，西瓜容易榨汁，注意去掉西瓜子。\", \"3\": \"榨汁杯清洗干净。\", \"4\": \"将西瓜丁放入搅拌杯，这季节的西瓜特别甜，什么都不要加，原汁原味，爽口开胃。\", \"5\": \"底部在手上轻震一下，这就出来很多汤汁了。\", \"6\": \"便携果汁机使用之前注意充好电，盖上主机杯盖，一定要拧紧，绿灯亮即可。\", \"7\": \"双击开关启动电源，注意将果汁机缓慢倒置手持，以45度角摇晃，单次工作时长是38秒，榨西瓜汁1次即可完成，口感特别爽。\", \"8\": \"榨好的西瓜汁倒入杯中即可饮用，也可以不出杯，直接饮用，随个人喜好吧。\"}', '{\"西瓜\": \"适量\", \"水\": \"适量\"}', 2, '2020-02-27 22:24:43', 0, '2020-03-12 19:36:05', '484015');
INSERT INTO `menu` VALUES (82, '猕猴桃梨汁', 69, '{\"1\": \"猕猴桃\", \"2\": \"梨\", \"3\": \"猕猴桃去皮切块\", \"4\": \"梨子去皮去核切块\", \"5\": \"放入原汁机中榨汁\", \"6\": \"放入猕猴桃榨汁\", \"7\": \"成品。\"}', '{\"猕猴桃\": \"适量\", \"梨子\": \"适量\"}', 2, '2020-02-27 22:24:44', 0, '2020-03-12 19:36:05', '481774');
INSERT INTO `menu` VALUES (83, '黄桃果汁', 37, '{\"1\": \"先将所需要的食材准备好，黄桃清洗干净，黄桃外表有毛，所以一定要把桃毛洗干净，不然弄到哪里哪里就是会痒痒的。\", \"2\": \"将黄桃去皮，再去核，将黄桃果肉切成小块。\", \"3\": \"切好的黄桃果肉放到长帝便携式果汁机里，注意不要装得太满。\", \"4\": \"盖上盖子，双击电源开关，然后把长帝便携式果汁机倒过来就可把黄桃搅打成汁了。\", \"5\": \"黄桃的汁不会太多，所以打好的汁也比较稠，所以时间上会需要多一些，有必要的时候需要摇晃一下果汁杯。\", \"6\": \"打好的黄桃汁倒入容器里。\", \"7\": \"无花果切成块，摆放在黄桃汁上，现在可以慢慢品尝了。\"}', '{\"黄桃\": \"2个\", \"无花果\": \"1个\"}', 2, '2020-02-27 22:24:46', 0, '2020-03-12 19:36:05', '477151');
INSERT INTO `menu` VALUES (84, '芒果汁', 36, '{\"1\": \"准备好材料。\", \"2\": \"芒果去皮切小块\", \"3\": \"将芒果放进果汁杯中\", \"4\": \"倒入矿泉水\", \"5\": \"盖上盖子扭紧，双击开关。\", \"6\": \"将杯体倒过来开始打汁，在打的过程中不断晃动杯体，快速完成打汁。\", \"7\": \"打好后扭开盖子，将入杯子即可饮用。\"}', '{\"芒果\": \"500g\", \"矿泉水\": \"200ml\"}', 2, '2020-02-27 22:24:47', 0, '2020-03-12 19:36:05', '477114');
INSERT INTO `menu` VALUES (85, '梨不仅可以当水果生吃还可以做成去暑饮料喝', 20, '{\"1\": \"所有材料准备好备用。\", \"2\": \"洗好的设成小块放入豆浆机/料理机/破壁机备用。\", \"3\": \"放入准备好的冰糖放入料理机/破壁机/豆浆机。\", \"4\": \"料理机/破壁机/豆浆机导入60ml的水。\", \"5\": \"摁一下水果键，打成细腻的果汁倒入杯内即可，差不多打3-4分钟。\"}', '{\"宝石梨\": \"1个\", \"纯净水\": \"60ml\", \"冰糖\": \"4-5颗\"}', 2, '2020-02-27 22:24:48', 0, '2020-03-12 19:36:05', '475514');
INSERT INTO `menu` VALUES (86, '自制奶茶，网红奶茶西瓜波波做法', 87, '{\"1\": \"加入脆脆波\", \"2\": \"加入无籽西瓜\", \"3\": \"捣碎无籽西瓜\", \"4\": \"加入冰块\", \"5\": \"倒入茉莉茶汤\", \"6\": \"倒入冰糖浆\", \"7\": \"雪克均匀\", \"8\": \"倒入出品杯\", \"9\": \"西瓜波波茶就做好了。\"}', '{\"茉莉绿茶汤\": \"250ml\", \"引爆点冰糖浆\": \"40ml\", \"无籽西瓜\": \"120g\", \"冰块\": \"260g\", \"脆波波\": \"50g\"}', 2, '2020-02-27 22:24:49', 0, '2020-03-12 19:36:05', '468981');
INSERT INTO `menu` VALUES (87, '甜瓜原汁', 45, '{\"1\": \"甜瓜洗净去瓤去皮。\", \"2\": \"切成小块，放果蔬杯中。\", \"3\": \"放破壁机上。\", \"4\": \"选果蔬，启动。\", \"5\": \"打好的果汁。\", \"6\": \"成品。\"}', '{\"甜瓜\": \"1个\", \"无\": \"适量\"}', 2, '2020-02-27 22:24:50', 0, '2020-03-12 19:36:05', '468880');
INSERT INTO `menu` VALUES (88, '雪梨西瓜汁', 25, '{\"1\": \"原料。\", \"2\": \"西瓜放入破壁机。\", \"3\": \"放入梨。\", \"4\": \"启动果汁功能。\", \"5\": \"程序停止，即可饮用。\", \"6\": \"清爽解暑。\"}', '{\"西瓜\": \"500克\", \"雪梨\": \"2个\"}', 2, '2020-02-27 22:24:52', 0, '2020-03-12 19:36:05', '467470');
INSERT INTO `menu` VALUES (89, '菠萝汁', 42, '{\"1\": \"菠萝好吃，但去皮有点麻烦，买回来新鲜的菠萝需要耐心去皮，还要抠了眼，盐水泡一泡，才能吃，我这去皮的技术也不怎么样，损失的要多一些呢。\", \"2\": \"去皮洗净的菠萝切成大片，撒些盐。\", \"3\": \"加入适量的水浸泡半小时左右，这样可以去酸去涩，如果不酸不涩可以减少浸泡的时间。\", \"4\": \"取出浸泡好的菠萝片，清水反复清洗几次，去掉咸味，切小块。\", \"5\": \"放入料理杯，菠萝汁水很多，我什么都没兑，喜欢的可以根据自己的口味兑些其他水果或者奶制品。\", \"6\": \"启动料理机，直接榨成菠萝汁就OK了！非常简单的菠萝汁，真可谓是分分秒秒就做好。\", \"7\": \"倒入杯中，喜欢的可以过滤一下，不过滤也很好，含有粗纤维更适合清肠刮油。\"}', '{\"菠萝\": \"1个\", \"盐\": \"适量\"}', 2, '2020-02-27 22:24:53', 0, '2020-03-12 19:36:05', '467440');
INSERT INTO `menu` VALUES (90, '蜂蜜黄瓜汁', 87, '{\"1\": \"黄瓜蜂蜜备用。\", \"2\": \"黄瓜切片放入料理机中。\", \"3\": \"加入蜂蜜。\", \"4\": \"放入适量水。\", \"5\": \"开启制作模式。\", \"6\": \"早餐来一杯很棒哦。\", \"7\": \"成品图。\", \"8\": \"成品图。\"}', '{\"黄瓜\": \"一根\", \"蜂蜜\": \"适量\", \"饮用水\": \"适量\"}', 2, '2020-02-27 22:24:54', 0, '2020-03-12 19:36:05', '464018');
INSERT INTO `menu` VALUES (91, '甜桃汁', 32, '{\"1\": \"提前准备好材料备用。\", \"2\": \"甜桃去皮。\", \"3\": \"将甜桃切块，与水混合榨汁。\", \"4\": \"倒入杯中，成品图。\", \"5\": \"成品图。\"}', '{\"甜桃\": \"2个\", \"纯净水\": \"一杯\"}', 2, '2020-02-27 22:24:55', 0, '2020-03-12 19:36:05', '463497');
INSERT INTO `menu` VALUES (92, '红心番石榴果汁', 68, '{\"1\": \"准备好食材和份量，奶粉之前做雪花酥剩下的。\", \"2\": \"奶粉加温水调成牛奶。\", \"3\": \"番石榴去皮切成小块。\", \"4\": \"把番石榴和牛奶和白砂糖放入东菱无线果汁杯里，不要超过最大标位线。\", \"5\": \"按2下开关，东菱果汁机就开始工作了。\", \"6\": \"打10秒左右，很细腻的番石榴果汁可以开喝了。\"}', '{\"红心番石榴\": \"1个\", \"奶粉\": \"35克\", \"水\": \"240克\", \"白砂糖\": \"10克\"}', 2, '2020-02-27 22:24:56', 0, '2020-03-12 19:36:05', '460719');
INSERT INTO `menu` VALUES (93, '胡萝卜梨汁', 8, '{\"1\": \"备好食材：梨子洗净去皮去芯切成块备用。\", \"2\": \"胡萝卜洗净去皮切成小块备用。\", \"3\": \"把胡萝卜块，梨块倒入破壁机杯内，加入适量凉开水。\", \"4\": \"盖好盖子，启用由源，按蔬果汁键操作。\", \"5\": \"时间到，蔬果汁已好。\", \"6\": \"盛入杯中，美美哒。\", \"7\": \"成品。\"}', '{\"梨子\": \"一个\", \"胡萝卜\": \"半根\", \"凉白开\": \"适量\"}', 2, '2020-02-27 22:24:58', 0, '2020-03-12 19:36:05', '458687');
INSERT INTO `menu` VALUES (94, '白心火龙果汁', 5, '{\"1\": \"备好所需要的材料。\", \"2\": \"两种材料放入榨汁机榨成汁。\", \"3\": \"倒入杯中，成品图。\", \"4\": \"成品图，完成开喝！\"}', '{\"火龙果\": \"半个\", \"纯净水\": \"适量\"}', 2, '2020-02-27 22:25:01', 0, '2020-03-12 19:36:05', '455333');
INSERT INTO `menu` VALUES (95, '蜜柚苹果汁', 8, '{\"1\": \"准备蜜柚和苹果一个。\", \"2\": \"用刀将蜜柚外皮切成“工”字，双手用力一点往两边扒就弄开了，非常容易。\", \"3\": \"将柚的白衣去掉，将瓤弄小块放入搅拌机里。放的时间长，蜜柚水分好像也流失了不少呢。\", \"4\": \"苹果去皮，去核，切小块，也放入搅拌机里。\", \"5\": \"加入适量的糖和水，放在主机上面，启动电源，十来秒就打成果汁了。\", \"6\": \"如果你想更细腻的口感，可以用筛网将果渣过滤掉。由于是高速运转，有少许泡沫是正常现象哟。\"}', '{\"苹果\": \"1个\", \"蜜柚\": \"2瓣\", \"细砂糖\": \"10克\", \"饮用水\": \"适量\"}', 2, '2020-02-27 22:25:02', 0, '2020-03-12 19:36:05', '454747');
INSERT INTO `menu` VALUES (96, '双莓果汁', 62, '{\"1\": \"备好食材： 将草莓，蓝莓清洗干净备用。\", \"2\": \"草莓切块，蓝莓放入杯内。\", \"3\": \"加入适量牛奶。\", \"4\": \"盖好盖子，启动电源开关操作一分钟。\", \"5\": \"美美的果汁已好。\", \"6\": \"成品。\"}', '{\"草莓\": \"适量\", \"蓝莓\": \"适量\", \"牛奶\": \"适量\"}', 2, '2020-02-27 22:25:04', 0, '2020-03-12 19:36:05', '454604');
INSERT INTO `menu` VALUES (97, '菠萝黄瓜汁', 59, '{\"1\": \"黄瓜去皮切块。\", \"2\": \"菠萝切碎。\", \"3\": \"放入榨汁机杯子里，到入适量，凉开水。\", \"4\": \"启动榨汁机，1分钟就可以。\", \"5\": \"加入一勺蜂蜜。\", \"6\": \"美味菠萝黄瓜汁。\", \"7\": \"成品。\"}', '{\"菠萝\": \"适量\", \"黄瓜\": \"半个\", \"蜂蜜\": \"一勺\", \"凉开水\": \"适量\"}', 2, '2020-02-27 22:25:05', 0, '2020-03-12 19:36:05', '453942');
INSERT INTO `menu` VALUES (98, '胡萝卜橙子汁', 74, '{\"1\": \"胡萝卜洗净去皮切成块。\", \"2\": \"橙子剥好切成块备用。\", \"3\": \"把橙子和胡萝卜放入破壁机杯内，加入适量凉开水，一小块冰糖。\", \"4\": \"盖好盖子，启动电源，按蔬果汁键操作。\", \"5\": \"时间到，果汁已好。\", \"6\": \"盛入杯中，美美哒，成品。\", \"7\": \"成品。\"}', '{\"胡萝卜\": \"一根\", \"橙子\": \"一个\", \"冰糖\": \"一小块\", \"凉开水\": \"适量\"}', 2, '2020-02-27 22:25:06', 0, '2020-03-12 19:36:05', '453860');
INSERT INTO `menu` VALUES (99, '胡萝卜苹果汁', 24, '{\"1\": \"一个苹果削皮和半根胡萝卜削皮待用。\", \"2\": \"苹果切小块。\", \"3\": \"胡萝卜切丁。\", \"4\": \"把苹果块和胡萝卜丁都放入破壁机里。\", \"5\": \"倒入500ml水。\", \"6\": \"开启果汁功能，打成细腻的糊糊。\", \"7\": \"破壁机功能强大，完全没有渣的感觉，口感很细腻。\", \"8\": \"可以加点蜂蜜，味道会更好。\"}', '{\"苹果\": \"1个\", \"胡萝卜\": \"半根\", \"纯净水\": \"500ml\"}', 2, '2020-02-27 22:25:07', 0, '2020-03-12 19:36:05', '453591');
INSERT INTO `menu` VALUES (100, '油桃风味乳', 69, '{\"1\": \"2袋风味发酵乳备用。\", \"2\": \"油桃三个洗净。\", \"3\": \"去皮。\", \"4\": \"切成大块。\", \"5\": \"发酵乳倒入。\", \"6\": \"搅拌两三次。\", \"7\": \"光滑细腻的油桃发酵乳完成。\", \"8\": \"成品。\", \"9\": \"成品。\"}', '{\"油桃\": \"3个\", \"发酵风味乳\": \"2袋\", \"白糖\": \"一勺\"}', 3, '2020-02-27 22:25:08', 0, '2020-03-12 19:36:05', '470813');
INSERT INTO `menu` VALUES (101, '自制酸奶', 94, '{\"1\": \"准备原料\", \"2\": \"美的bb煲内胆加少量水。\", \"3\": \"选择消毒功能。\", \"4\": \"消毒过的玻璃内胆。\", \"5\": \"倒入牛奶。\", \"6\": \"再倒入酸奶。\", \"7\": \"混合后放入美的bb煲内。\", \"8\": \"选择酸奶功能。\", \"9\": \"8小时后，酸奶做好。\", \"10\": \"舀一勺，沉甸甸，完美。\", \"11\": \"撒点儿果麦脆，更好吃。\"}', '{\"纯牛奶\": \"800ml\", \"酸奶\": \"80ml\", \"果麦脆\": \"少许\"}', 3, '2020-02-27 22:25:09', 0, '2020-03-12 19:36:05', '470051');
INSERT INTO `menu` VALUES (102, '皇帝蕉果汁', 66, '{\"1\": \"提前准备好所需要的材料。\", \"2\": \"两种材料放入榨汁机榨汁。\", \"3\": \"倒入杯中。\", \"4\": \"成品图\"}', '{\"皇帝蕉\": \"2个\", \"纯净水\": \"半杯\"}', 2, '2020-02-27 22:25:09', 0, '2020-03-12 19:36:05', '453358');
INSERT INTO `menu` VALUES (103, '纯胡萝卜汁', 88, '{\"1\": \"胡萝卜去皮切片备用，纯净水提前准备好半杯。\", \"2\": \"两种材料放入榨汁机榨成汁。\", \"3\": \"倒入杯中，成品图，完成。\", \"4\": \"成品图。\"}', '{\"胡萝卜\": \"适量\", \"纯净水\": \"适量\"}', 2, '2020-02-27 22:25:10', 0, '2020-03-12 19:36:05', '453327');
INSERT INTO `menu` VALUES (104, '水蜜桃发酵乳', 83, '{\"1\": \"水蜜桃一个洗净。\", \"2\": \"去皮切小块。\", \"3\": \"桃块放搅拌杯中。喜欢甜点的可放一小勺白糖。\", \"4\": \"发酵乳2袋。\", \"5\": \"发酵乳倒入搅拌杯。\", \"6\": \"搅拌机放座上，快速搅拌2次。\", \"7\": \"光滑细腻的鲜桃发酵乳完成。\", \"8\": \"成品。\", \"9\": \"成品。\"}', '{\"水蜜桃\": \"1个\", \"发酵乳\": \"2袋\", \"白糖\": \"一勺\"}', 3, '2020-02-27 22:25:10', 0, '2020-03-12 19:36:05', '468861');
INSERT INTO `menu` VALUES (105, '南瓜纯汁', 19, '{\"1\": \"南瓜去皮大火蒸熟备用。\", \"2\": \"加入纯净水与南瓜混合，榨汁备用。\", \"3\": \"倒入杯中，成品图。\", \"4\": \"成品图。\", \"5\": \"成品图。\"}', '{\"南瓜\": \"适量\", \"纯净水\": \"适量\"}', 2, '2020-02-27 22:25:12', 0, '2020-03-12 19:36:05', '453326');
INSERT INTO `menu` VALUES (106, '菠萝酸奶', 58, '{\"1\": \"自制原味酸奶。\", \"2\": \"菠萝。\", \"3\": \"用原汁机榨汁。\", \"4\": \"取适量酸奶放入杯中。\", \"5\": \"放入菠萝汁搅拌均匀。\", \"6\": \"鲜菠萝切碎后放入酸奶中。\", \"7\": \"成品。\"}', '{\"自制原味酸奶\": \"适量\", \"菠萝\": \"适量\"}', 3, '2020-02-27 22:25:13', 0, '2020-03-12 19:36:05', '468548');
INSERT INTO `menu` VALUES (107, '草莓苹果汁', 85, '{\"1\": \"清洗适量草莓，苹果切小块。\", \"2\": \"水果，冰糖，适量凉开水一起放入破壁机。\", \"3\": \"开始搅拌。\", \"4\": \"过滤一下(喜欢类似奶昔的可以带果肉，不过滤)。\", \"5\": \"装入容器，大功告成！\"}', '{\"草莓\": \"适量\", \"苹果\": \"半个\", \"冰糖\": \"适量\", \"凉开水\": \"适量\"}', 2, '2020-02-27 22:25:14', 0, '2020-03-12 19:36:05', '453299');
INSERT INTO `menu` VALUES (108, '自制原味酸奶', 12, '{\"1\": \"牛奶。\", \"2\": \"菌种。\", \"3\": \"将菌种放入无油无水的盆中。\", \"4\": \"先加入少量的牛奶将菌种融化。\", \"5\": \"放入剩余的牛奶。\", \"6\": \"盖上盖子放入酸奶机中发酵9-12小时。\", \"7\": \"成品。\", \"8\": \"成品。\"}', '{\"牛奶\": \"1000毫升\", \"菌种\": \"1包\"}', 3, '2020-02-27 22:25:14', 0, '2020-03-12 19:36:05', '468541');
INSERT INTO `menu` VALUES (109, '火龙果汁', 83, '{\"1\": \"红心火龙果1个。\", \"2\": \"切开。\", \"3\": \"挖出果肉。\", \"4\": \"加1勺椴树蜜，300ml凉开水。\", \"5\": \"榨成汁。\", \"6\": \"用笊篱把籽过出来，果汁更漂亮。\", \"7\": \"火龙果汁特别营养好喝。\"}', '{\"红心火龙果\": \"1个\", \"蜂蜜\": \"1勺\", \"凉白开\": \"300ml\"}', 2, '2020-02-27 22:25:15', 0, '2020-03-12 19:36:05', '452958');
INSERT INTO `menu` VALUES (110, '自制酸奶', 29, '{\"1\": \"准备一口小锅，和纯牛奶，酸奶，清水，白糖。\", \"2\": \"把布丁瓶洗干净，用开水烫，杀菌晾干备用。\", \"3\": \"清水、白糖加入锅中，小火溶解白糖。\", \"4\": \"水沸腾后关掉加热，冷却冷却。\", \"5\": \"60℃时慢慢加入牛奶，让温度下降至45℃以下。然后加入酸奶，搅拌均匀。\", \"6\": \"依次装瓶，盖上盖子。\", \"7\": \"放入电饭锅，加适量水，加热至45℃左右，保温6h即可。\", \"8\": \"可以做成芒果酸奶。\", \"9\": \"可以配上各种水果吃，味道不错。\"}', '{\"纯牛奶\": \"2瓶\", \"原味酸奶\": \"2瓶\", \"清水\": \"100g\", \"白糖\": \"40g\"}', 3, '2020-02-27 22:25:16', 0, '2020-03-12 19:36:05', '468092');
INSERT INTO `menu` VALUES (111, '苹果黄瓜酸奶汁', 86, '{\"1\": \"准备好食材；\", \"2\": \"将黄瓜、苹果洗净去皮再分别切成小丁；\", \"3\": \"各取一半的黄瓜和苹果装入榨汁机的搅拌杯里；\", \"4\": \"倒入原味酸奶；\", \"5\": \"安装好榨汁机（将榨汁机底座倒立装在搅拌杯上，拧紧至对位点）；\", \"6\": \"双击开始按钮；\", \"7\": \"榨汁机开始工作，左右摇晃几下，便于搅拌刀易搅打到苹果和黄瓜；\", \"8\": \"然后让榨汁机继续榨汁，运行35秒后会自动停止；\", \"9\": \"榨好后将机器倒置，拧开搅拌杯倒出果汁即可；\", \"10\": \"成品果味清香口感绵密，味道很赞哦。\"}', '{\"黄瓜\": \"100g\", \"苹果\": \"120g\", \"原味酸奶\": \"190g\"}', 2, '2020-02-27 22:25:16', 0, '2020-03-12 19:36:05', '452915');
INSERT INTO `menu` VALUES (112, '荔枝酸奶', 11, '{\"1\": \"将荔枝去壳与核。\", \"2\": \"荔枝肉放入破壁料理机中。\", \"3\": \"加酸奶，打成汁。\", \"4\": \"冷藏后饮用。\"}', '{\"荔枝\": \"8个\", \"酸奶\": \"200ml\"}', 3, '2020-02-27 22:25:17', 0, '2020-03-12 19:36:05', '466045');
INSERT INTO `menu` VALUES (113, '既能瘦身又好喝的果汁|黄瓜菠萝汁', 38, '{\"1\": \"超市买回削皮的菠萝切一半，倒入容器中加凉汤和盐搅拌几下使盐充分融合，浸泡一个小时。\", \"2\": \"黄瓜洗净用厨房纸擦干表面水分，用刀切小丁放入料理机备用。\", \"3\": \"浸泡好的菠萝捞出切丁放入料理机里。\", \"4\": \"盖上盖子摁一下键，打成细腻的水果汁，倒入杯子即可。\", \"5\": \"一道减肥又好喝的菠萝黄瓜汁就做好了，赶紧试一下吧。\"}', '{\"黄瓜\": \"一根\", \"菠萝\": \"1/2个（削皮的）\", \"盐\": \"适量\"}', 2, '2020-02-27 22:25:18', 0, '2020-03-12 19:36:05', '452827');
INSERT INTO `menu` VALUES (114, '电饭煲版儿童酸奶', 75, '{\"1\": \"准备好所有的材料，我用的是儿童菌粉，如果没有也可以用普通的菌粉\", \"2\": \"把美的BB电饭煲玻璃内胆洗净后加入适量的清水，这款电饭特别好的之处是采用高硼硅玻璃内胆，是奶瓶级玻璃内胆，无涂层、无杂质不含铅、镉等重金属，达到健康无菌的效果，做酸奶时所有接触到的工具是都必需是无菌的，所以用玻璃的器具来做是最好的，有些人担心有涂层给宝宝吃不健康这点倒是很好\", \"3\": \"加入清水后，再把功调至消毒程序，只需要消毒20分钟即可，这个功能很方便，不需要另外拿个大锅来煮水消毒很好，这个设计很人性化。\", \"4\": \"后面需要用到的所有工具都需要彻底的消毒，包括勺子和小碗\", \"5\": \"消毒好后趁锅还是热的，把细砂糖倒入锅中，再倒入少量的全脂牛奶，先搅拌至细砂糖完全的融化\", \"6\": \"再把剩下的牛奶倒入一起搅拌均匀，然后放在一边稍微的放凉到45度以下，这里需要注意下的是，加入菌粉是牛奶温度可以是常温的，也可以是45度下的，但切记不能是超过了45度，会把菌粉烫死而导致酸奶失败，没办法发酵成酸奶的\", \"7\": \"取一个消毒后的酸奶，加放少量的牛奶，加入一小包菌粉，正常买的是都是一小包菌粉1克的量，加入500-1000克的牛奶即可，如果是儿童要吃的酸奶建议用儿童菌粉，在口咸上没有普通的酸奶菌粉酸，还有其所含的成品有儿童所需的对营养成份，如果确实没有也是可以普通的酸奶菌粉来做的\", \"8\": \"先化开菌粉后，再重新倒回原来的牛奶中\", \"9\": \"同样的搅拌均匀后放入美的BB电饭煲中\", \"10\": \"盖上盖子，在盖子的面板上把功能调至酸奶功能，默认是8小时，这样就可以直接不管它了，等到8小时后再开吃就好了，非常的方便简捷\", \"11\": \"等做好后，用勺子挖起来就像是豆腐脑一样，如果再用勺子多搅拌几下，就非常的稠会拉丝的感谢。口感是非常的细腻\", \"12\": \"吃的时候，再加入一些麦片干果，非常的美味，小朋们都是非常的爱吃。要注意下的是酸奶是适合一周岁以上的小朋友才可以做，一周岁以下的宝宝还是不要吃，宝宝的肠胃还没发育成熟适应不了酸奶这个东西。\"}', '{\"全脂牛奶\": \"600克\", \"儿童菌粉\": \"1克\", \"细砂糖\": \"30克\"}', 3, '2020-02-27 22:25:18', 0, '2020-03-12 19:36:05', '466787');
INSERT INTO `menu` VALUES (115, '水果 橙子苹果汁', 12, '{\"1\": \"准备好的材料。\", \"2\": \"苹果橙子去皮切块。\", \"3\": \"放入破壁机里。按果汁键。\", \"4\": \"打好的果汁。\", \"5\": \"成品。\"}', '{\"苹果\": \"1个\", \"橙子\": \"3个\"}', 2, '2020-02-27 22:25:19', 0, '2020-03-12 19:36:05', '445965');
INSERT INTO `menu` VALUES (116, '自制酸奶', 69, '{\"1\": \"做好的酸奶如图～\", \"2\": \"准备好食材：纯牛奶450ml、冠益乳酸奶100ml、细砂糖40g。\", \"3\": \"纯牛奶倒入量杯内，加入细砂糖搅拌均匀。\", \"4\": \"纯牛奶内加入冠益乳酸奶，搅拌均匀。\", \"5\": \"过筛三次，不可省略。\", \"6\": \"玻璃容器清洗干净高温消毒可选择蒸、煮均可。将牛奶液倒入玻璃容器内，盖上盖子。\", \"7\": \"将装有奶的玻璃容器码放在韩国酷晨电饭煲内，加入纯净水如图的样子。\", \"8\": \"选择酸奶模式，开启即可。\", \"9\": \"发酵时长8小时。\", \"10\": \"好喝的酸奶就做好啦！打开盖子倾斜一下看不流动即可。\", \"11\": \"取出酸奶放入冰箱冷藏。食用前可搭配自己喜欢的水果小饼干，就是一份非常不错的下午茶。\"}', '{\"纯牛奶\": \"450ml\", \"冠益乳酸奶\": \"100ml\", \"细砂糖\": \"40g\"}', 3, '2020-02-27 22:25:19', 0, '2020-03-12 19:36:05', '464448');
INSERT INTO `menu` VALUES (117, '火龙果汁', 72, '{\"1\": \"火龙果去皮切块备用。\", \"2\": \"将火龙果与纯净水混合。\", \"3\": \"倒入榨汁机榨汁即可\", \"4\": \"倒入杯中，完成。\", \"5\": \"成品图。\"}', '{\"火龙果\": \"半个\", \"纯净水\": \"适量\"}', 2, '2020-02-27 22:25:20', 0, '2020-03-12 19:36:05', '450619');
INSERT INTO `menu` VALUES (118, '奇异果饮品制作之摇摇奇异乳酸', 80, '{\"1\": \"加入冰块\", \"2\": \"浓缩乳酸菌\", \"3\": \"奇异果肉\", \"4\": \"倒入冰糖浆\", \"5\": \"倒入冰水\", \"6\": \"多次搅拌\", \"7\": \"倒到出品杯\", \"8\": \"摇一摇，口感会更好呢\", \"9\": \"摇摇奇异果乳酸的成品图\", \"10\": \"摇摇奇异果乳酸的配方比例。\"}', '{\"饮爆点浓缩乳酸菌\": \"50ml\", \"饮爆点冰糖浆\": \"25ml\", \"奇异果肉\": \"60g\", \"冰水\": \"220ml\", \"冰块\": \"150g\"}', 3, '2020-02-27 22:25:20', 0, '2020-03-12 19:36:05', '462646');
INSERT INTO `menu` VALUES (119, '鲜榨果汁', 44, '{\"1\": \"把提前泡好的黄豆，倒入榨汁杯。\", \"2\": \"再把花生倒入榨汁杯，可不泡。\", \"3\": \"再把纯净水倒入榨汁杯，拧紧盖，启动。\", \"4\": \"把榨好的花生豆浆倒入榨汁杯中，记得过滤，再煮开噢，很简单的噢。\"}', '{\"猕猴桃\": \"1个\", \"苹果\": \"半个\", \"水\": \"300ml\"}', 2, '2020-02-27 22:25:21', 0, '2020-03-12 19:36:05', '450530');
INSERT INTO `menu` VALUES (120, '蜂蜜菠萝汁', 73, '{\"1\": \"买来的菠萝去皮。\", \"2\": \"菠萝切小块。\", \"3\": \"切好的菠萝肉放入破壁机加少量水和适量蜂蜜。\", \"4\": \"启动果蔬功能完成后自动停止。\", \"5\": \"蜂蜜菠萝汁完成了。\"}', '{\"菠萝\": \"适量\", \"蜂蜜\": \"适量\", \"水\": \"适量\"}', 2, '2020-02-27 22:25:22', 0, '2020-03-12 19:36:05', '450443');
INSERT INTO `menu` VALUES (121, '桑葚酸奶', 70, '{\"1\": \"准备自制酸奶一杯。\", \"2\": \"舀一勺自制桑葚酱拌均。\", \"3\": \"表面放一颗桑葚、薄荷叶装饰。粉嫩的颜值很是诱人。\"}', '{\"自制酸奶\": \"1杯\", \"桑葚酱\": \"1勺\", \"桑葚果\": \"2个\"}', 3, '2020-02-27 22:25:22', 0, '2020-03-12 19:36:05', '461168');
INSERT INTO `menu` VALUES (122, '胡萝卜圣女果汁', 54, '{\"1\": \"备好食材： 胡萝卜去皮洗净切成小块备用，圣女果洗净备用。\", \"2\": \"把胡萝卜及圣女果倒入破壁机杯内，加入适量凉开水。\", \"3\": \"启用电源，按破壁键操作。\", \"4\": \"时间到，果汁已好。\", \"5\": \"倒出，成品。\", \"6\": \"成品。\"}', '{\"胡萝卜\": \"半根\", \"圣女果\": \"六个\", \"凉开水\": \"适量\"}', 2, '2020-02-27 22:25:23', 0, '2020-03-12 19:36:05', '450154');
INSERT INTO `menu` VALUES (123, '榴莲酸奶', 40, '{\"1\": \"准备一块软糯的榴莲果肉，一瓶自制酸奶。\", \"2\": \"将榴莲果肉去核。\", \"3\": \"用勺子压成细腻果泥\", \"4\": \"加入自制酸奶搅拌均匀。\", \"5\": \"可以切些果肉放在酸奶中，增加咀嚼层次感。\", \"6\": \"味道美美的\"}', '{\"自制酸奶\": \"200g\", \"榴莲肉\": \"1小块\"}', 3, '2020-02-27 22:25:23', 0, '2020-03-12 19:36:05', '459326');
INSERT INTO `menu` VALUES (124, '酸奶西梅汁', 4, '{\"1\": \"西梅清洗干净备用。\", \"2\": \"西梅去壳备用，酸奶准备一盒备用。\", \"3\": \"将两种材料放入榨汁机，加入少许水，低速搅拌成果汁。\", \"4\": \"倒入杯中。完成，成品图。\", \"5\": \"成品图。\", \"6\": \"成品图。\", \"7\": \"成品图。\", \"8\": \"成品图。\", \"9\": \"成品图。\"}', '{\"西梅\": \"适量\", \"酸奶\": \"100克\", \"纯净水\": \"适量\"}', 2, '2020-02-27 22:25:24', 0, '2020-03-12 19:36:05', '449902');
INSERT INTO `menu` VALUES (125, '蜂蜜黄瓜汁', 18, '{\"1\": \"所需食材准备就绪。\", \"2\": \"黄瓜去皮口感更佳。\", \"3\": \"切碎放入杯中。\", \"4\": \"加适量饮用水。\", \"5\": \"放入一勺蜂蜜。\", \"6\": \"开启ACA小可爱。\", \"7\": \"美美的黄瓜汁就来咯。\", \"8\": \"家常饮品，请您品鉴。\", \"9\": \"成品图。\"}', '{\"黄瓜\": \"两根\", \"蜂蜜\": \"适量\", \"饮用水\": \"适量\"}', 2, '2020-02-27 22:25:25', 0, '2020-03-12 19:36:05', '449884');
INSERT INTO `menu` VALUES (126, '自制酸奶', 89, '{\"1\": \"成品图。\", \"2\": \"材料：天然博士有机蛋白粉10g（约1平勺），牛奶500ML，酸奶菌1包（1克），蓝莓酱适量，蜂蜜1小勺。\", \"3\": \"自己做酸奶，容器的卫生是很重要的，所以在开始制作时候，第一步就是要消毒容器，我准备了一些玻璃瓶，还有装牛奶的碗，都是要用刚烧开的开水烫过才可以的。\", \"4\": \"接着将牛奶倒入消毒过的大碗中，然后加入1克的酸奶菌，用消毒过的勺子搅拌均匀。\", \"5\": \"接着将牛奶再倒入之前消毒好的杯子中，盖上盖子，放在40度的环境进行发酵，时间大约8个小时。这个环境你可以用电饭煲、烤箱或者酸奶机都可以的，只要环境温度合适就成。\", \"6\": \"做好放冰箱冷藏，为了让营养更丰富，我特地加入了蓝莓酱。\", \"7\": \"顺便加点蜂蜜综合甜味。\", \"8\": \"同时为了让营养更全面，现在天气热了，很多人都开始健身减肥，也喜欢吃酸奶来促进消化，加入一勺的蛋白粉可以补充体力。\"}', '{\"牛奶\": \"500ML\", \"天然博士有机蛋白粉\": \"10克\", \"酸奶菌粉\": \"1克\", \"蓝莓酱\": \"适量\", \"蜂蜜\": \"适量\"}', 3, '2020-02-27 22:25:25', 0, '2020-03-12 19:36:05', '458565');
INSERT INTO `menu` VALUES (127, '雪梨胡萝卜汁', 73, '{\"1\": \"雪梨，胡萝卜洗净。\", \"2\": \"去皮，切块。\", \"3\": \"放入榨汁机中。\", \"4\": \"启动机器，榨汁。\", \"5\": \"成品。\", \"6\": \"倒入杯中。\"}', '{\"胡萝卜\": \"1根\", \"雪梨\": \"1个\", \"白开水\": \"300ml\"}', 2, '2020-02-27 22:25:26', 0, '2020-03-12 19:36:05', '449820');
INSERT INTO `menu` VALUES (128, '原味酸奶', 23, '{\"1\": \"纯牛奶和酸奶菌准备好；牛奶根据自家的情况可选择全脂或者脱脂牛奶，酸奶菌有很多品种，可以在网上搜一下，有五菌、七菌、双歧杆菌多种；因为做原味的，所以我不放糖；\", \"2\": \"我用面包机来做酸奶，先用清洁剂将面包桶及搅拌用的筷子彻底清洗干净，主要是为了去除掉油脂；再用沸水烫一下，千万不要用厨房纸巾或者布再去擦，画蛇添足会让烫得很干净的容器重新粘上杂菌，不利于酸奶的形成；先将一半的牛奶倒入桶中，再将酸奶菌倒入其中；\", \"3\": \"将剩下的牛奶全部倒入桶中；分两次倒牛奶是因为酸奶菌入牛奶中后会成团不易散开，再次倒入牛奶时，冲击力会将结块打散溶化；但还是要用沸水烫过的筷子搅拌均匀；表面起泡沫没关系，用筷子将其戳破就能使牛奶表面平整光洁了；\", \"4\": \"将面包桶放入面包机内，再盖上内盖；如果面包机没有内盖可不盖，内盖上面有小洞，可以将制作酸奶过程中产生的蒸汽释放出来，这样就不会有水珠滴在酸奶表面了；\", \"5\": \"合上外盖，我用的东菱JD08面包机，有专门的酸奶程序，按指示选择好，默认6小时；这个可以在晚上做，也可以一早做；晚上做，一早起来就是凝固好的酸奶；一早做，下班回来后也能喝上原味酸奶；\", \"6\": \"看，这是做好的酸奶，细腻、洁白、结实；此时的酸奶已经降温了，可以直接食用；如果还是温热的，建议入冰箱冷藏后再食用，完成继续发酵后的酸奶风味更佳；可以将桶蒙保鲜膜冷藏，也可以将沸水烫过的净瓶分装酸奶冷藏。\", \"7\": \"食用之前用净勺将酸奶搅拌均匀成稠糊状，淋蜂蜜、果酱、干果、放水果或者做成酸奶昔都美味得不要不要的。更多的创意酸奶吃法你就自由发挥吧！\", \"8\": \"火龙果酸奶杯。\", \"9\": \"芒果酸奶昔。\", \"10\": \"早餐来一杯果酱酸奶，开胃补钙，元气满满一整天。\"}', '{\"纯牛奶\": \"1000毫升\", \"酸奶菌\": \"1克\"}', 3, '2020-02-27 22:25:27', 0, '2020-03-12 19:36:05', '457739');
INSERT INTO `menu` VALUES (129, '火龙果草莓汁', 13, '{\"1\": \"所需食材准备就绪。\", \"2\": \"草莓用盐水浸泡，冲洗干净。\", \"3\": \"火龙果去皮改刀。\", \"4\": \"小番茄改刀。\", \"5\": \"草莓去蒂放入器皿中。\", \"6\": \"水果一同放入捣成泥状。\", \"7\": \"用料理机打成泥状即可。\", \"8\": \"自制饮品完美呈现。\", \"9\": \"成品图。\"}', '{\"火龙果\": \"适量\", \"小番茄\": \"适量\", \"草莓\": \"适量\"}', 2, '2020-02-27 22:25:27', 0, '2020-03-12 19:36:05', '449057');
INSERT INTO `menu` VALUES (130, '自制酸奶', 49, '{\"1\": \"成品图。\", \"2\": \"成品图。\", \"3\": \"把酸奶瓶子洗干净。\", \"4\": \"消毒控干水分备用。\", \"5\": \"做酸奶的材料。\", \"6\": \"把纯牛奶放入一个无油无水的容器中加入白糖搅拌均匀。\", \"7\": \"小火加热至白糖融化关火。\", \"8\": \"然后把酸奶菌粉放入搅拌均匀。\", \"9\": \"均匀地分配在酸奶瓶子里面。\", \"10\": \"放入面包机里面启动酸奶程序即可。\", \"11\": \"放冰箱冷藏过后口感更好。\", \"12\": \"成品图。\", \"13\": \"成品图。\"}', '{\"纯牛奶\": \"1000ml\", \"细砂糖\": \"30g\", \"酸奶菌粉\": \"1g\"}', 3, '2020-02-27 22:25:28', 0, '2020-03-12 19:36:05', '456213');
INSERT INTO `menu` VALUES (131, '草莓酸奶', 18, '{\"1\": \"自制酸奶200ml。\", \"2\": \"草莓5个(实际只用了两个半)，去掉蒂、清洗干净，用厨房纸吸干表面水分。\", \"3\": \"把草莓修成心形。\", \"4\": \"草莓切1.5到2mm的厚度，注意片不要切得太厚。\", \"5\": \"把草莓片贴到杯子内壁平滑处。\", \"6\": \"加入适量的酸奶，表面用剩下的草莓片装饰。\"}', '{\"自制酸奶\": \"200g\", \"草莓\": \"5个\"}', 3, '2020-02-27 22:25:29', 0, '2020-03-12 19:36:05', '454935');
INSERT INTO `menu` VALUES (132, '双皮奶', 69, '{\"1\": \"牛奶倒入奶锅里，加入白糖。\", \"2\": \"小火慢慢煮至快要冒泡为止，不要沸腾。\", \"3\": \"然后把牛奶倒入准备要蒸的容器里，自然放凉，表面会形成一层奶皮，此为第一层奶皮，等其放凉形成奶皮后，将奶皮留下，牛奶倒入另一个容器里。\", \"4\": \"取一个新鲜的生鸡蛋，将蛋白与蛋黄分离。\", \"5\": \"蛋白用手动打蛋器将其打散。\", \"6\": \"倒入牛奶中，再次搅拌均匀。\", \"7\": \"然后过筛，让其混合物更为细腻，且去掉浮沫。\", \"8\": \"将过筛好的牛奶沿着容器壁慢慢倒回去，之前的奶皮会慢慢浮起来。\", \"9\": \"盖上保鲜膜，这里的保鲜膜要是能耐高温的，用牙签在上面扎几个洞。\", \"10\": \"长帝蒸烤箱的水盒放入足够多的水，选择温度100度，时间30分钟，将容器放进去蒸，使其凝固。\", \"11\": \"待其自然冷却后，可以将芒果切块摆在双皮奶上，就可以品尝了，如果冷藏口味会不一样哦，期待你的品尝。\"}', '{\"牛奶\": \"150克\", \"蛋白\": \"1个（约40克左右）\", \"糖\": \"15克\", \"芒果\": \"适量\"}', 3, '2020-02-27 22:25:30', 0, '2020-03-12 19:36:05', '454428');
INSERT INTO `menu` VALUES (133, '干果酸奶芒果碗', 27, '{\"1\": \"芒果备用。\", \"2\": \"酸奶干果备用。\", \"3\": \"芒果去皮。\", \"4\": \"改刀放入碗中。\", \"5\": \"加入酸奶。\", \"6\": \"撒上干果即可。\", \"7\": \"家常美味，请您品鉴。\", \"8\": \"成品图。\"}', '{\"酸奶\": \"适量\", \"芒果\": \"适量\", \"干果\": \"适量\"}', 3, '2020-02-27 22:25:31', 0, '2020-03-12 19:36:05', '451558');
INSERT INTO `menu` VALUES (134, '自制简单减肥养颜食谱|绝对的无添加剂', 68, '{\"1\": \"准备好酸奶和一袋240ml全脂牛奶。\", \"2\": \"准备35-40°热水倒入酸奶机内胆备用。\", \"3\": \"倒入全脂牛奶和酸奶，用筷子搅拌牛奶和酸奶完全融合，差不多正反方向各50次。\", \"4\": \"盖上酸奶盖，插上电源，静置发酵7-8小时。\", \"5\": \"香蕉去皮切丁，草莓洗净切块放入碗中备用。\", \"6\": \"适量发酵好的酸奶倒入盛有香蕉和草莓碗中，用筷子搅拌均匀即可。\", \"7\": \"倒入准备好的杯子，一道自制无添加的酸奶水果捞就做好了。\"}', '{\"酸奶\": \"100g\", \"纯牛奶\": \"240ml\", \"350°热水\": \"酸奶机内胆1/3\", \"香蕉\": \"一根\", \"草莓\": \"10勺\"}', 3, '2020-02-27 22:25:33', 0, '2020-03-12 19:36:05', '450447');
INSERT INTO `menu` VALUES (135, '酸奶紫薯泥', 70, '{\"1\": \"紫薯提前蒸熟。\", \"2\": \"所需配料准备就绪。\", \"3\": \"紫薯去皮加牛奶捣成泥状。\", \"4\": \"碗抹油，紫薯泥盛入压实。\", \"5\": \"翻扣在盘子上。\", \"6\": \"淋入酸奶。\", \"7\": \"撒入干果。\", \"8\": \"快手美味，请您品鉴。\", \"9\": \"成品图。\", \"10\": \"成品图。\"}', '{\"熟紫薯\": \"三块\", \"酸奶\": \"适量\", \"牛奶\": \"适量\", \"干果\": \"一袋\"}', 3, '2020-02-27 22:25:34', 0, '2020-03-12 19:36:05', '446900');
INSERT INTO `menu` VALUES (136, '鲜果冰淇淋奶昔', 24, '{\"1\": \"备好食材，这种冰淇淋适合做各种消暑饮品，也可以用勺子直接挖着吃，但是，这样容易过量，尤其是没有自制力的孩子。家长们还是动起手来，脑洞大开玩花样，制成鲜果奶昔，既让家人消暑，也能吃到更多营养。\", \"2\": \"剥开火龙果皮，将果肉切粒、用搅拌机打成果泥。\", \"3\": \"芒果也取出果肉，将火腿肠冰淇淋从冰箱取出，切开，倒出碗里。\", \"4\": \"芒果肉搅成果泥。\", \"5\": \"分别将果泥和冰淇淋间隔地倒入杯中，形成漂亮的三色奶昔即可。\", \"6\": \"颜色亮丽，口感顺滑的鲜果冰淇淋奶昔，健康又营养，消暑神器！\"}', '{\"火腿肠冰淇淋\": \"100克\", \"芒果\": \"三个\", \"火龙果\": \"1个\"}', 4, '2020-02-27 22:25:35', 0, '2020-03-12 19:36:05', '409866');
INSERT INTO `menu` VALUES (137, '蓝莓麦片豆豆酸奶', 65, '{\"1\": \"提前一天做好酸奶。\", \"2\": \"舀出适量酸奶放入碗中。\", \"3\": \"撒入适量玉米麦片。\", \"4\": \"放入蓝莓。\", \"5\": \"撒入适量黑巧克力豆。\", \"6\": \"成品\"}', '{\"自制酸奶\": \"适量\", \"玉米麦片\": \"适量\", \"蓝莓\": \"适量\", \"黑巧克力豆\": \"适量\"}', 3, '2020-02-27 22:25:36', 0, '2020-03-12 19:36:05', '438235');
INSERT INTO `menu` VALUES (138, '甜点冰激凌', 67, '{\"1\": \"鸡蛋黄四个。\", \"2\": \"白砂糖100克。\", \"3\": \"将砂糖加入蛋黄中，搅拌成淡白色。\", \"4\": \"纯牛奶两袋。\", \"5\": \"将牛奶倒入没有油渍的煮锅中，烧至微开，晾凉至四十度左右。\", \"6\": \"将牛奶分几次倒入蛋黄液中，并不断搅拌均匀。\", \"7\": \"将搅拌好的蛋奶液倒入没有油渍的煮锅中，滴入两滴白醋烧开，晾凉，倒入冰激凌杯中，放入冰箱冷冻8小时以上。\", \"8\": \"从冰箱里取出冰激凌杯。\", \"9\": \"用小勺挖成小球，加入枸杞或葡萄干，尽情享用吧！\", \"10\": \"自制冰激凌容易化，要尽快吃进肚肚里哦！\"}', '{\"纯牛奶\": \"两袋\", \"鸡蛋黄\": \"四个\", \"白砂糖\": \"100g\", \"白醋\": \"两滴\"}', 4, '2020-02-27 22:25:36', 0, '2020-03-12 19:36:05', '408980');
INSERT INTO `menu` VALUES (139, '自制酸奶', 10, '{\"1\": \"容器用开水冲烫一分钟，晾干水分，倒入一包酸奶菌。\", \"2\": \"倒入牛奶。\", \"3\": \"搅拌均匀。\", \"4\": \"放入酸奶机启动酸奶功能九个小时完成。\", \"5\": \"成品图。\"}', '{\"牛奶\": \"1000ml\", \"酸奶菌\": \"1包\"}', 3, '2020-02-27 22:25:37', 0, '2020-03-12 19:36:05', '438141');
INSERT INTO `menu` VALUES (140, '奶香冰淇淋', 36, '{\"1\": \"蛋黄加糖打发到浓稠。\", \"2\": \"牛奶小火加热。\", \"3\": \"加入适量的炼乳。\", \"4\": \"牛奶冷确后加入到蛋黄液体中，小火加热，不要让它开了。最后加入用水化开的糯米粉，不断的搅拌，直到浓稠，关火。\", \"5\": \"出锅过滤一下。\", \"6\": \"从冰箱取出奶油。\", \"7\": \"打发5分钟，浓稠状即可。\", \"8\": \"分3次将蛋奶浆混合进打发好的淡奶油，盖上保鲜膜送入冷藏1一2小时后取出用打蛋器搅拌后再送冷藏保存，前后反复4次即可。\", \"9\": \"看看成品。\", \"10\": \"再来一张。\"}', '{\"牛奶\": \"200ml\", \"奶油\": \"200ml\", \"蛋黄\": \"2个\", \"糖\": \"100g\", \"糯米粉\": \"15g\", \"炼乳\": \"30g\"}', 4, '2020-02-27 22:25:38', 0, '2020-03-12 19:36:05', '400463');
INSERT INTO `menu` VALUES (141, '樱桃酸奶', 29, '{\"1\": \"酸奶准备2瓶，樱桃准备几粒。\", \"2\": \"将酸奶倒入杯中。\", \"3\": \"用樱桃点缀。\", \"4\": \"成品图。\"}', '{\"樱桃\": \"几粒\", \"酸奶\": \"2瓶\"}', 3, '2020-02-27 22:25:39', 0, '2020-03-12 19:36:05', '433819');
INSERT INTO `menu` VALUES (142, '桃子冰棒', 13, '{\"1\": \"3-4个桃子，一大部分切大块，只留一点切成小块。\", \"2\": \"准备淡奶油，炼乳和纯牛奶。\", \"3\": \"将切大块的桃子里加入纯牛奶，炼乳和淡奶油一起放入料理机里打碎。\", \"4\": \"将切成小丁的桃子果肉先放入冰棒模具里，然后倒入打碎的桃子果泥。放入冰箱冷冻2小时候后插入冰棒棍。等完全冻住的时候就可以脱模了！脱模的时候将冰棒模具放在水龙头下面，冷水冲十几秒钟，用力一拉，冰棒就出来了。\"}', '{\"桃子\": \"3-4个\", \"炼乳\": \"30克\", \"淡奶油\": \"40克\", \"纯牛奶\": \"100克\"}', 4, '2020-02-27 22:25:39', 0, '2020-03-12 19:36:05', '401807');
INSERT INTO `menu` VALUES (143, '#清凉一夏～～酸奶水果冰淇淋#', 7, '{\"1\": \"水果均切丁备用。\", \"2\": \"准备一个披萨盘（我用的是8寸的）。\", \"3\": \"披萨盘上面铺一层油纸。\", \"4\": \"准备酸奶牛奶糖搅拌均匀倒入披萨盘里，再放入切好的果粒。\", \"5\": \"放入冰箱冷冻5小时左右即可。\", \"6\": \"冰箱拿出，用水冲一下披萨盘底部！较好地脱模！切块装盘即可！\"}', '{\"火龙果\": \"半个\", \"酸奶\": \"半瓶\", \"芒果\": \"一个\", \"车厘子\": \"适量\", \"白糖\": \"适量\", \"披萨盘\": \"一个\"}', 4, '2020-02-27 22:25:41', 0, '2020-03-12 19:36:05', '401104');
INSERT INTO `menu` VALUES (144, '自制酸奶', 75, '{\"1\": \"准备好所有食材，喜欢吃甜的可以放一些白糖。\", \"2\": \"酸奶杯用开水烫一下消毒，或者放入消毒柜。\", \"3\": \"到入纯牛奶。\", \"4\": \"酸奶到入。\", \"5\": \"用勺子搅匀。\", \"6\": \"盖上盖子，放入酸奶机，插上电源，发酵8小时。\", \"7\": \"酸奶做好了，像豆腐脑的状态。\", \"8\": \"把做好的酸奶，盛入一个杯子。\", \"9\": \"放入任何水果.干果都好吃。\"}', '{\"纯牛奶\": \"2包\", \"原味酸奶\": \"一包\"}', 3, '2020-02-27 22:25:41', 0, '2020-03-12 19:36:05', '432019');
INSERT INTO `menu` VALUES (145, '自制酸奶', 56, '{\"1\": \"家里什么牌子的牛奶都可以。\", \"2\": \"白糖。\", \"3\": \"这是网上买的。（我用的佰生优的，觉得不错）\", \"4\": \"不锈钢碗和瓢羹用开水烫十分钟。\", \"5\": \"倒掉水，放入白糖。\", \"6\": \"倒入牛奶搅拌均匀。\", \"7\": \"放入菌粉搅拌均匀。\", \"8\": \"酸奶机里面放少许热水。（天气冷了需要放，大约40度左右）\", \"9\": \"把不锈钢碗放入酸奶机内。\", \"10\": \"盖上盖子，通电发酵6-8小时。\", \"11\": \"发酵好的酸奶晾凉后放入冰箱冷藏。（喜欢吃热的就可以直接吃，但网上说三到七天内的乳酸菌是最好的）\", \"12\": \"冷藏后的酸奶加点麦片或者水果，更加美味！\"}', '{\"纯牛奶\": \"500毫升\", \"菌粉\": \"1袋\", \"白糖\": \"2匙羹\"}', 3, '2020-02-27 22:25:42', 0, '2020-03-12 19:36:05', '430580');
INSERT INTO `menu` VALUES (146, '芒果冰激凌', 42, '{\"1\": \"芒果肉放入榨汁杯中打成泥。\", \"2\": \"淡奶油加白糖用打蛋器打发。\", \"3\": \"把打好的芒果肉倒入淡奶油中。\", \"4\": \"搅拌均匀。\", \"5\": \"倒入容器中放冰箱冷冻四个小时。\", \"6\": \"成品图。\"}', '{\"芒果\": \"1个\", \"淡奶油\": \"200g\", \"白糖\": \"20g\"}', 4, '2020-02-27 22:25:42', 0, '2020-03-12 19:36:05', '398839');
INSERT INTO `menu` VALUES (147, '自制原味酸奶', 21, '{\"1\": \"容器用开水烫一分钟控干水分放入酸奶菌。\", \"2\": \"倒入牛奶用勺子搅拌均匀。\", \"3\": \"启动酸奶机的酸奶程序，根据室温选择时间，室温高时间短一些室温底时间长些。\", \"4\": \"成品图。\"}', '{\"牛奶\": \"1000ml\", \"酸奶菌\": \"5g\"}', 3, '2020-02-27 22:25:44', 0, '2020-03-12 19:36:05', '429459');
INSERT INTO `menu` VALUES (148, '冰淇淋', 74, '{\"1\": \"准备好食材。\", \"2\": \"把白糖和蛋黄放在一起，用打蛋机打发，成这个模样。\", \"3\": \"把牛奶倒入锅中，煮至微开。\", \"4\": \"将煮好的牛奶用勺子慢慢舀入打好的蛋液中，边倒边搅拌。（注，不能把牛奶一下子都倒进去）。\", \"5\": \"将搅拌好的蛋奶液倒回锅中，煮至微沸关火晾凉。\", \"6\": \"把晾凉的蛋奶液放进冰箱冷冻，隔两小时就搅拌一下。成形后，放入自己喜欢的水果即可。\"}', '{\"鸡蛋黄\": \"4个\", \"牛奶\": \"2袋\", \"白糖\": \"二两\"}', 4, '2020-02-27 22:25:44', 0, '2020-03-12 19:36:05', '396043');
INSERT INTO `menu` VALUES (149, '自制可可酸奶', 47, '{\"1\": \"牛奶倒入面包机中。\", \"2\": \"放入酸奶。\", \"3\": \"可可粉过筛。\", \"4\": \"筛入可可粉。\", \"5\": \"搅拌均匀。\", \"6\": \"启动酸奶程序。\", \"7\": \"成品图。\"}', '{\"纯牛奶\": \"250ml\", \"可可粉\": \"10g\", \"酸奶\": \"100ml\"}', 3, '2020-02-27 22:25:45', 0, '2020-03-12 19:36:05', '427740');
INSERT INTO `menu` VALUES (150, '自制芒果冰淇淋', 41, '{\"1\": \"备好食材。\", \"2\": \"蛋黄加30g白糖。\", \"3\": \"搅打至乳白色。\", \"4\": \"加入牛奶搅匀。\", \"5\": \"蛋奶液坐入开水中小火加热并搅拌，至浓稠关火。\", \"6\": \"芒果打成泥。\", \"7\": \"淡奶油加30g糖打发成此状态。\", \"8\": \"芒果泥倒入蛋奶液中。\", \"9\": \"拌匀后分2次加入淡奶油，拌匀。\", \"10\": \"放入保鲜盒。\", \"11\": \"放入冷冻室，半小时取出用勺子搅拌，重复3次，至冷冻成型。\", \"12\": \"挖出。\", \"13\": \"成品。\", \"14\": \"成品。\"}', '{\"牛奶\": \"200ml\", \"蛋黄\": \"2个\", \"淡奶油\": \"100g\", \"白糖\": \"60g\", \"芒果肉\": \"200g\"}', 4, '2020-02-27 22:25:46', 0, '2020-03-12 19:36:05', '390847');
INSERT INTO `menu` VALUES (151, '自制老酸奶', 87, '{\"1\": \"酸奶杯开水消毒。\", \"2\": \"量杯先倒入一袋纯牛奶，糖粉倒入，搅匀。\", \"3\": \"老酸奶发酵菌粉倒入一袋，搅匀。\", \"4\": \"其余三袋倒入量杯中，搅拌均匀。\", \"5\": \"均匀倒入发酵杯中。\", \"6\": \"盖上盖儿。发酵箱内倒入40~60度温水。这样做出来的酸奶均匀细腻。(冬季适用)\", \"7\": \"接通电源，选择酸奶功能，自动显示10小时，开始制作。\", \"8\": \"10小时结束，取出酸奶杯，打开盖，把盖上水水蒸汽倒掉，再盖上，放冰箱里冷藏5小时或以上。\", \"9\": \"自制老酸奶完美呈现，不出水，凝结细腻光滑，口感酸甜适中。不加水果单吃口感也不错。\", \"10\": \"加上自己喜欢的水果，美味水果老酸奶即成。\", \"11\": \"成品。\", \"12\": \"成品。\"}', '{\"纯牛奶\": \"适量\", \"老酸奶菌粉\": \"一袋\"}', 3, '2020-02-27 22:25:47', 0, '2020-03-12 19:36:05', '427231');
INSERT INTO `menu` VALUES (152, '榴莲冰淇淋', 48, '{\"1\": \"取熟透的榴莲肉（去核）250克放入大口杯中。\", \"2\": \"加入淡奶油。\", \"3\": \"加入炼乳。\", \"4\": \"用ACA搅拌棒上下来回搅拌成均匀细腻的糊。\", \"5\": \"盛入硅胶模具中，放冰箱冷冻至硬。\", \"6\": \"取出冻好的榴莲冰淇淋，用融化的黑巧克力装饰即可。\", \"7\": \"成品图\", \"8\": \"成品图\", \"9\": \"成品图\", \"10\": \"成品图\"}', '{\"榴莲肉\": \"250克\", \"淡奶油\": \"220克\", \"炼乳\": \"50克\"}', 4, '2020-02-27 22:25:48', 0, '2020-03-12 19:36:05', '342833');
INSERT INTO `menu` VALUES (153, '自制甜酸奶', 82, '{\"1\": \"牛奶煮开锅。\", \"2\": \"放一边晾到温热。\", \"3\": \"把奶皮扔掉，不瘦身的亲可以保留。\", \"4\": \"酸奶容器放白糖。\", \"5\": \"倒入温热的牛奶搅拌均匀。\", \"6\": \"放乳酸菌。\", \"7\": \"搅拌均匀。\", \"8\": \"盖盖子。\", \"9\": \"放酸奶机里。\", \"10\": \"通电发酵6～8小时。\", \"11\": \"做好的酸奶。\", \"12\": \"取出来。\", \"13\": \"取石榴。\", \"14\": \"装盘。\", \"15\": \"开动。\"}', '{\"鲜牛奶\": \"750克\", \"乳酸菌\": \"1包\", \"白糖\": \"2汤匙\", \"石榴\": \"适量\"}', 3, '2020-02-27 22:25:49', 0, '2020-03-12 19:36:05', '427083');
INSERT INTO `menu` VALUES (154, '魅力冰激凌', 11, '{\"1\": \"挑出蛋黄\", \"2\": \"加入糖/牛奶\", \"3\": \"搅拌\", \"4\": \"热水杀菌\", \"5\": \"搅拌淡奶油\", \"6\": \"鲜榨火龙果汁\", \"7\": \"鲜榨芒果汁\", \"8\": \"蛋黄中加入奶油并搅拌后留备用\", \"9\": \"加芒果汁搅拌\", \"10\": \"或加火龙果汁\", \"11\": \"或加奥利奥碎\", \"12\": \"挤入器皿中冷冻\"}', '{\"蛋黄\": \"2个\", \"牛奶\": \"20g\", \"淡奶油\": \"240g\", \"白砂糖\": \"40g\", \"水果\": \"根据个人口味\"}', 4, '2020-02-27 22:25:50', 0, '2020-03-12 19:36:05', '367754');
INSERT INTO `menu` VALUES (155, '芒果蜂蜜拌酸奶', 47, '{\"1\": \"自己做的酸奶。\", \"2\": \"芒果肉。\", \"3\": \"酸奶里加适量蜂蜜。\", \"4\": \"搅拌均匀。\", \"5\": \"放入芒果肉。\", \"6\": \"吃的时候拌匀即可。\"}', '{\"酸奶\": \"适量\", \"芒果\": \"适量\", \"蜂蜜\": \"适量\"}', 3, '2020-02-27 22:25:51', 0, '2020-03-12 19:36:05', '426889');
INSERT INTO `menu` VALUES (156, '自制原味酸奶', 48, '{\"1\": \"伊莱特能量土锅电饭煲、纯牛奶、酸奶菌粉准备好；\", \"2\": \"将纯牛奶倒入能量土锅胆中，再将酸奶菌粉倒入其中，用干净无油的筷子搅拌均匀，菌粉不结块；\", \"3\": \"将能量土锅内胆安放在电饭煲中；\", \"4\": \"合上盖子，通电，选择“酸奶”功能，默认时间8小时，时间到会有提示音，就等着喝浓香原味酸奶吧！\"}', '{\"纯牛奶\": \"1000克\", \"酸奶菌粉\": \"1克\"}', 3, '2020-02-27 22:25:53', 0, '2020-03-12 19:36:05', '426852');
INSERT INTO `menu` VALUES (157, '抹茶冰棒', 81, '{}', '{\"冰淇淋粉\": \"适量\", \"抹茶粉\": \"适量\", \"牛奶\": \"适量\", \"白砂糖\": \"适量\"}', 4, '2020-02-27 22:25:53', 0, '2020-03-13 19:45:24', '365537');
INSERT INTO `menu` VALUES (158, '自制蜂蜜酸奶', 32, '{\"1\": \"鲜牛奶。\", \"2\": \"煮开锅。\", \"3\": \"倒入酸奶机的内容器。\", \"4\": \"放到温热，去掉奶油。\", \"5\": \"发酵菌，倒入牛奶中搅拌均匀。\", \"6\": \"盖盖子，放酸奶机里。\", \"7\": \"盖上外表的大盖子。\", \"8\": \"通电发酵6～8小时。\", \"9\": \"发酵好的老酸奶，不喜欢吃甜的，就可以直接食用了。\", \"10\": \"去掉奶油，放冰箱冷藏。不瘦身的亲，可以保留奶油哦。\", \"11\": \"蜂蜜。\", \"12\": \"倒入碗中。\", \"13\": \"蜂蜜可多可少，根据自己的口味加。\", \"14\": \"放酸奶。\", \"15\": \"搅拌均匀。\", \"16\": \"猕猴桃取一个。\", \"17\": \"去皮切片。\", \"18\": \"贴杯壁放好。\", \"19\": \"倒入酸奶。\", \"20\": \"上边在放两片猕猴桃。\", \"21\": \"成品的侧面。\", \"22\": \"成品。\", \"23\": \"成品。\"}', '{\"鲜牛奶\": \"750克\", \"酸奶发酵菌\": \"1克\", \"猕猴桃\": \"1个\", \"蜂蜜\": \"15克\"}', 3, '2020-02-27 22:25:54', 0, '2020-03-12 19:36:05', '423969');
INSERT INTO `menu` VALUES (159, '蔓越莓核桃冰淇淋', 13, '{\"1\": \"蔓越莓切碎，用朗姆酒浸泡（我用的是自酿的甜杏酒，可以用自己喜欢的果酒替代）\", \"2\": \"核桃烤箱烤熟，切碎，吹掉外皮，冷却待用\", \"3\": \"准备好做蛋奶糊原料牛奶、低筋面粉、玉米淀粉、糖、盐（我要做2种口味的冰淇淋，所以是按照配方2倍称量的）\", \"4\": \"将蛋奶糊的原料倒入煮锅（不锈钢加厚复底煮锅导热快速均匀，直径16cm即够用），牛奶、低筋面粉、玉米淀粉、糖、盐等\", \"5\": \"搅拌均匀，放入燃气灶（或者电磁炉都可以），中小火加热，边加热边不停搅拌\", \"6\": \"加热至底部刚出现沸腾小气泡，立刻关火，蛋奶糊成图中所示浓稠状，光滑细腻没有颗粒。冷却蛋奶糊（前十分钟需要边冷却边适时搅拌，至冷却不烫手，可以放入冰箱冷冻室冷冻几分钟，加快冷却）\", \"7\": \"冷却蛋奶糊的同时称量300g淡奶油，加入糖，打发至出现纹路\", \"8\": \"将打发好的淡奶油放入冷却的蛋奶糊盆中，用刮刀搅拌均匀（像搅拌戚风蛋糕一样）\", \"9\": \"浸泡好的蔓越莓干沥干水分倒入，并加入冷凉的核桃碎\", \"10\": \"搅拌均匀，放入容器，放入冰箱冷冻室冷冻即可\"}', '{\"牛奶\": \"250\", \"蛋黄\": \"4个\", \"低筋面粉\": \"15g\", \"玉米淀粉\": \"10g\", \"淡奶油\": \"300g\", \"蔓越莓\": \"50g\", \"核桃\": \"50g\", \"糖\": \"20g（淡奶油）\", \"盐\": \"3g\", \"朗姆酒\": \"适量\"}', 4, '2020-02-27 22:25:55', 0, '2020-03-12 19:36:05', '362561');
INSERT INTO `menu` VALUES (160, '坚果酸奶', 36, '{\"1\": \"自己做的酸奶。\", \"2\": \"取适量放入容器中。\", \"3\": \"放入适量的核桃仁。\", \"4\": \"放入适量的葡萄干完成。\"}', '{\"酸奶\": \"适量\", \"熟核桃仁\": \"适量\", \"葡萄干\": \"适量\"}', 3, '2020-02-27 22:25:56', 0, '2020-03-12 19:36:05', '423829');
INSERT INTO `menu` VALUES (161, '三色雪糕', 60, '{\"1\": \"1. 把所需要的材料，工具都准备好\", \"2\": \"2. 把红色部分的材料都放进料理杯\", \"3\": \"3. 打成泥状\", \"4\": \"4. 再用打蛋头打发\", \"5\": \"5. 打好的雪糕糊倒进盒子\", \"6\": \"6. 送进冰箱冷冻半小时\", \"7\": \"7. 把咖色部分的材料一起混在一起\", \"8\": \"8. 打发至8分发\", \"9\": \"9. 一样倒进盒子\", \"10\": \"10. 送进冰箱冷冻半小时\", \"11\": \"11. 芒果切花取芒果肉\", \"12\": \"12. 把材料混合一起打发\", \"13\": \"13. 顺滑的雪糕糊倒入盒子\", \"14\": \"14. 送进冰箱冷冻2小时\", \"15\": \" 成品图\", \"16\": \" 成品图\", \"17\": \"食诱惑\", \"18\": \"食诱惑\", \"19\": \"食诱惑\", \"20\": \"食诱惑\"}', '{\"淡奶油\": \"70g\", \"火龙果\": \"68g\", \"细砂糖\": \"10g\", \"可可酱\": \"20g\", \"可可粉\": \"20g\", \"芒果\": \"70g\"}', 4, '2020-02-27 22:25:56', 0, '2020-03-12 19:36:05', '347334');
INSERT INTO `menu` VALUES (162, '自制浓稠酸奶', 87, '{\"1\": \"准备好原料，牛奶用全脂的，脱脂牛奶做出来稀，不适合这个方子。奶油是前两天裱花剩下的，正好用上。可以买小包装的淡奶油，避免浪费。\", \"2\": \"把酸奶容器用开水烫一下，控干水分。\", \"3\": \"加入100ml常温牛奶。\", \"4\": \"加入酸奶粉，加入淡奶油，一起搅拌均匀。\", \"5\": \"倒入剩余的牛奶，如果想要很浓稠，就稍微调整一下牛奶和奶油的比例，少奶多奶油（不过热量较高）。\", \"6\": \"放入酸奶容器，加沸水，没过酸奶的高度。\", \"7\": \"盖好之后就完事啦，全程3分钟搞定。放置12小时后就可以饮用了，通常我会放15个小时左右，越久就会越酸。\", \"8\": \"这是做好的状态，放入冰箱冷藏过后，会更加粘稠。\", \"9\": \"能装这种小玻璃瓶7、8瓶，冷藏保存，每次喝之前自己调下口味，当然也可以原味。\", \"10\": \"搅拌后缓缓流下的感觉，这才是“酸奶”。那些加了凝胶，果胶或者琼脂的，在我看来可以称为果冻或者“奶冻”。\"}', '{\"牛奶\": \"700ml\", \"奶油\": \"70ml\", \"酸奶粉\": \"1包\"}', 3, '2020-02-27 22:25:57', 0, '2020-03-12 19:36:05', '422189');
INSERT INTO `menu` VALUES (163, '经典【香草冰淇淋】', 22, '{\"1\": \"先将冰淇淋搅拌机的制冷桶放入冰箱的冷冻库冷冻16小时以上\", \"2\": \"香草荚用刀子划开，将香草籽刮出\", \"3\": \"放进装有牛奶的小锅内，小火加热至牛奶快要沸腾即可\", \"4\": \"将蛋黄和砂糖放进盆内，用电动打蛋器将蛋黄打发至接近白色的粘稠状态\", \"5\": \"用勺子舀入一勺热的奶液，加入蛋黄糊，一边搅拌，搅拌均匀以后，再加入一大勺，如此反复几次，便可把剩下的奶液倒一半到蛋黄糊里，搅拌均匀，这样逐步提高蛋黄液的温度，可以避免温度过高的奶液把蛋黄糊给冲成蛋花汤\", \"6\": \"奶液和蛋黄糊完全混合后，重新倒回煮奶液的锅里，用小火慢慢的加热奶糊，一边轻轻的搅拌，注意不要让浆水沸腾，煮至浆水浓厚了，勺子背后能挂厚浆，手指划过后不会立即消失，就可以了\", \"7\": \"将牛奶蛋黄糊过筛\", \"8\": \"把装有牛奶蛋黄糊的盆隔着冰水冷却\", \"9\": \"将淡奶油打发至6-7分发\", \"10\": \"与牛奶蛋黄糊混合\", \"11\": \"将牛奶蛋黄糊倒入硅胶杯中\", \"12\": \"将制冷桶从冰箱里拿出\", \"13\": \"盖上盖子，插上电源插头，按下开关。将牛奶蛋黄糊从盖子注入口倒入冰淇淋桶内\", \"14\": \"冰淇淋机搅拌运行25分钟后，就完成了\", \"15\": \"用木勺将冰淇淋装出放带盖容器内，移入冰箱冷冻室冷冻保存\", \"16\": \"吃时用冰淇淋勺挖出来，稍加装饰。是不是很高大上滴感觉。。。\"}', '{\"蛋黄\": \"2个\", \"牛奶\": \"160ml\", \"砂糖\": \"70克\", \"淡奶油\": \"200ml\", \"香草荚\": \"半根\"}', 4, '2020-02-27 22:25:58', 0, '2020-03-12 19:36:05', '344642');
INSERT INTO `menu` VALUES (164, '有家鲜厨房：一个鸡蛋的冰淇淋', 53, '{\"1\": \"用鸡蛋切割器，小心切开鸡蛋壳，保留完整的鸡蛋外壳，分离蛋黄和蛋清。\", \"2\": \"奶锅煮开牛奶，加入鸡蛋黄，煮制过程中加入白砂糖\", \"3\": \"打发奶油，打发过程中分多次加入白砂糖，打发至黏稠的糊状\", \"4\": \"将制作好的蛋奶液倒入打发好的奶油中，搅拌均匀\", \"5\": \"倒入到鸡蛋壳中，放入冰箱冷冻\", \"6\": \"有家鲜厨房：一个鸡蛋的冰淇淋\"}', '{\"牛奶\": \"250毫升\", \"奶油\": \"250毫升\", \"鸡蛋\": \"6个\", \"白糖\": \"25克\"}', 4, '2020-02-27 22:25:59', 0, '2020-03-12 19:36:05', '344285');
INSERT INTO `menu` VALUES (165, '红茶酸奶', 5, '{\"1\": \"用料：红茶包2包，纯牛奶1000毫升，酸奶菌粉1包，蜂蜜适量，核桃碎少许。\", \"2\": \"将红茶包和一半牛奶倒入锅中。\", \"3\": \"煮至微沸，让牛奶保持小气泡的微沸状态2-3分钟。\", \"4\": \"熄火，焖至冷却后放入冰箱，冷藏一夜。\", \"5\": \"次日，再次煮沸后取出茶包，静置至冷却。\", \"6\": \"用剩下的牛奶将酸奶菌粉溶化。\", \"7\": \"倒入红茶奶，\", \"8\": \"搅拌均匀。\", \"9\": \"装入容器中，盖上盖子。\", \"10\": \"在电饭煲中倒入少量沸水。\", \"11\": \"放入包有屉布的平盘。\", \"12\": \"将装好奶液的容器放在平盘上，\", \"13\": \"盖上盖子，静置一夜。\", \"14\": \"次日，打开电饭煲，\", \"15\": \"揭盖，奶液凝固。\", \"16\": \"淋上蜂蜜，撒少许核桃碎，即可食用。\"}', '{\"红茶包\": \"2包\", \"纯牛奶\": \"1000毫升\", \"酸奶菌粉\": \"1包\", \"蜂蜜\": \"适量\", \"核桃碎\": \"少许\"}', 3, '2020-02-27 22:25:59', 0, '2020-03-12 19:36:05', '416023');
INSERT INTO `menu` VALUES (166, '哈密瓜牛奶冰淇淋', 8, '{\"1\": \"哈密瓜去皮去瓤洗干净\", \"2\": \"将哈密瓜切碎\", \"3\": \"哈密瓜放容器中放白糖\", \"4\": \"倒入牛奶搅拌均匀\", \"5\": \"添加少许连藕粉搅拌均匀，放入冰箱冷藏室，使其冷却到5-10度\", \"6\": \"从冰箱冷冻室取出冰淇淋桶，安装到变速箱上，再将搅拌叶安装在冰淇淋桶内的搅拌轴上\", \"7\": \"打开面包机上盖，将带有冰淇淋桶的变速箱安装在面包机的底座上，接通电源，选择面包机上“iMix”功能，时间设定为30分钟，启动该功能，搅拌叶转动\", \"8\": \"取出冰箱冷藏室冷藏好的原料，混合好后慢慢倒入冰淇淋桶中，盖上面包机上盖，面包机工作。听到蜂鸣器连续蜂鸣，机器自动停止工作\", \"9\": \"将冰淇淋桶从面包机中取出，再取出搅拌叶，即可享用\", \"10\": \"也可以将冰淇淋装入小杯中\", \"11\": \"放冰箱冷冻室冷冻后再享用\"}', '{\"哈密瓜\": \"100g\", \"纯牛奶\": \"200ml\", \"白糖\": \"适量\"}', 4, '2020-02-27 22:26:01', 0, '2020-03-12 19:36:05', '343844');
INSERT INTO `menu` VALUES (167, '自制酸奶', 6, '{\"1\": \"准备好食材。\", \"2\": \"容器提前用热水烫两分钟消毒，晾干水分，放入一袋酸奶菌。\", \"3\": \"倒入纯牛奶。\", \"4\": \"用便宜搅拌均匀。\", \"5\": \"放入酸奶机里面启动酸奶程序大约9个小时即可。\", \"6\": \"成品图。\"}', '{\"牛奶\": \"4袋\", \"酸奶菌\": \"1袋\"}', 3, '2020-02-27 22:26:01', 0, '2020-03-12 19:36:05', '415725');
INSERT INTO `menu` VALUES (168, '阳光沙滩鸡尾酒', 84, '{\"1\": \"准备食材\", \"2\": \"青柠檬\", \"3\": \"在杯子底部先倒入一些饮料\", \"4\": \"放入冰块\", \"5\": \"倒入锐澳鸡尾酒\", \"6\": \"充满夏日气息的鸡尾酒“阳光沙滩”\"}', '{\"鸡尾酒\": \"100ml\", \"饮料\": \"20ml\", \"青柠檬\": \"1片\", \"冰块\": \"适量\"}', 5, '2020-02-27 22:26:02', 0, '2020-03-12 19:36:05', '281230');
INSERT INTO `menu` VALUES (169, '芒果冰淇淋 免搅打懒人配方', 68, '{\"1\": \"芒果去皮切片，部分切大颗粒备用\", \"2\": \"奶油75g 白砂糖45g +170g芒果，因为冷冻甜味会下降虽然芒果很甜了还是需要再加一些糖，如果你很不爱甜也可以不加哦\", \"3\": \"所有材料加入料理机打均匀，有奶油加入可以增加奶香味而且口感更好不会有冰碴，料理机打了会让整个芒果泥变得很顺滑，吃起来更像雪糕，\", \"4\": \"打好的芒果泥先放一些芒果粒在碗里再倒上芒果泥，冷冻一夜即可\"}', '{\"芒果\": \"170g\", \"鲜奶油\": \"75g\", \"砂糖\": \"45g\"}', 4, '2020-02-27 22:26:03', 0, '2020-03-12 19:36:05', '343451');
INSERT INTO `menu` VALUES (170, '自制营养酸奶', 66, '{\"1\": \"准备四袋伊利纯牛奶（可以其它品牌但必须是纯原味牛奶）。\", \"2\": \"酸奶发酵剂10g。\", \"3\": \"将四袋牛奶和酸奶发酵剂用筷子充分搅拌均匀。\", \"4\": \"酸奶机选择8小时即可。\", \"5\": \"成品！类似安慕希的厚度，满满一钵，营养又实惠健康！因为是纯原味的会有点酸，喜欢吃甜的可以加入蜂蜜，红糖等调味。\"}', '{\"纯牛奶\": \"1000ml\", \"酸奶发酵剂\": \"10g\"}', 3, '2020-02-27 22:26:03', 0, '2020-03-12 19:36:05', '415514');
INSERT INTO `menu` VALUES (171, '饭碗里的酸奶冰淇淋', 93, '{}', '{\"酸奶\": \"1瓶\", \"奥利奥\": \"适量\", \"叶子\": \"一片\", \"水果\": \"适量\"}', 4, '2020-02-27 22:26:04', 0, '2020-03-12 19:36:05', '342980');
INSERT INTO `menu` VALUES (172, '蓝莓冰饮鸡尾酒', 7, '{\"1\": \" 称取蓝莓 50g，各种口味的RIO锐澳鸡尾酒，自制冰块适量,将蓝莓、锐澳RIO鸡尾酒放冰箱冷藏2小时再用。称取蓝莓50克，冷藏后的蓝莓用盐水浸泡5分钟后清洗干净。\", \"2\": \" 将浸泡后的蓝莓粒放入冰饮杯里，加入冰块。\", \"3\": \" 倒入RIO锐澳鸡尾酒后，一款冰爽清凉的蓝莓冰爽鸡尾酒制作就完成了，这样喝下去感觉倍爽！\"}', '{\"RIO锐澳鸡尾酒\": \"275ml\", \"蓝莓\": \"50克\", \"冰块\": \"适量\"}', 5, '2020-02-27 22:26:04', 0, '2020-03-12 19:36:05', '279575');
INSERT INTO `menu` VALUES (173, '冻水果拌酸奶', 19, '{\"1\": \"酸奶，冻草莓，冻芒果准备就绪\", \"2\": \"芒果去皮切成片\", \"3\": \"放入碗底\", \"4\": \"淋入酸奶，多少随性\", \"5\": \"放入草莓\", \"6\": \"剩余的芒果片卷起放入\", \"7\": \"成品图\", \"8\": \"成品图。\"}', '{\"冬草莓\": \"五个\", \"冬芒果\": \"一个\", \"酸奶\": \"适量\"}', 3, '2020-02-27 22:26:04', 0, '2020-03-12 19:36:05', '414440');
INSERT INTO `menu` VALUES (174, '姜瓜清饮鸡尾酒', 78, '{\"1\": \"姜切薄片，小黄瓜刮成长薄片，入杯中，RIO青柠朗姆酒和混合果味伏特加准备好\", \"2\": \"先倒入适量的RIO青柠朗姆酒\", \"3\": \"再倒入混合果味伏特加，一杯姜瓜清饮鸡尾酒做好啦\", \"4\": \"清清爽爽\", \"5\": \"黄瓜片在酒中很舒展，让人的心情也放松了\"}', '{\"青柠朗姆酒\": \"适量\", \"混合果味伏特加\": \"适量\", \"鲜姜\": \"4片\", \"小黄瓜\": \"6片\"}', 5, '2020-02-27 22:26:06', 0, '2020-03-12 19:36:05', '279471');
INSERT INTO `menu` VALUES (175, '原创|咖啡火龙果冰淇淋', 18, '{\"1\": \"准备好所有的材料；\", \"2\": \"将25克黄油隔水融化；\", \"3\": \"把3——4片玛利亚饼干装在食品塑料袋中用擀面杖压碎后用融化后的黄油与其搅拌均匀待用；\", \"4\": \"将3——5克咖啡豆研磨为咖啡粉待用；\", \"5\": \"用筛子将咖啡粉筛入黄油饼干粉后搅拌均匀；\", \"6\": \"将咖啡黄油饼干填充在磨具底部并压实，建议厚度不少于磨具的1/6；\", \"7\": \"把火龙果用榨汁机处理好；\", \"8\": \"在火龙果汁中加入鲜奶油并搅拌均匀；\", \"9\": \"把火龙果奶油液注入磨模具，9分满，然后将磨具放入冰箱冷藏3——4小时后取出脱模即可；\", \"10\": \"粉红少女心们的最爱~\"}', '{\"红心火龙果\": \"80g\", \"黄油\": \"25克\", \"玛利亚饼干\": \"3—4片\", \"咖啡豆\": \"3—5克\", \"糖\": \"适量\", \"奶油\": \"25g\"}', 4, '2020-02-27 22:26:06', 0, '2020-03-12 19:36:05', '342943');
INSERT INTO `menu` VALUES (176, '激情双莓鸡尾酒', 32, '{\"1\": \"RIO紫葡萄白兰地、混合水果伏特加、糖渍蔓越莓准备好\", \"2\": \"先倒入适量的RIO紫葡萄白兰地\", \"3\": \"再倒入适量的RIO混合水果伏特加\", \"4\": \"舀入几粒新鲜蔓越莓，激情双莓鸡尾酒做好啦\", \"5\": \"一起喝一杯吧\", \"6\": \"亮丽的颜色让人心情大好\"}', '{\"糖渍蔓越莓\": \"适量\", \"锐澳紫葡萄白兰地\": \"适量\", \"锐澳混合水果伏特加\": \"适量\", \"新鲜蓝莓\": \"数粒\"}', 5, '2020-02-27 22:26:07', 0, '2020-03-12 19:36:05', '279472');
INSERT INTO `menu` VALUES (177, '草莓味棉花糖冰棍', 1, '{\"1\": \"法罗夫草莓味棉花糖、鲜奶、做冰棍的模具、果汁机\", \"2\": \"牛奶注入果汁机的杯子中\", \"3\": \"挖出法罗夫草莓味棉花糖适量，根据自己的口味添加。\", \"4\": \"​法罗夫草莓味棉花糖放入搅拌杯中。\", \"5\": \"装上搅拌刀，并拧紧盖子。\", \"6\": \"​杯子放在主机上，点动按钮，让牛奶与法罗夫草莓味棉花糖完全融合\", \"7\": \"​模具插入小木棍。\", \"8\": \"将混合奶液注入模具中\", \"9\": \"盖上模具盖子，放入冰箱冷冻24小时；\", \"10\": \"食用时从冰箱里取出，脱模。\"}', '{\"牛奶\": \"100克\", \"棉花糖\": \"（草莓味）适量\"}', 4, '2020-02-27 22:26:08', 0, '2020-03-12 19:36:05', '342455');
INSERT INTO `menu` VALUES (178, '番茄红顶鸡尾酒', 15, '{\"1\": \"番茄用料理棒打成糊糊状倒入碗中，RIO果味酒准备好\", \"2\": \"将番茄糊平均分到两个酒杯中\", \"3\": \"将一个长柄勺子倒扣着贴在杯壁内侧，顺着勺柄将RIO果味酒倒入杯中。\", \"4\": \"混合果味伏特加番茄鸡尾酒\", \"5\": \"青柠朗姆酒番茄鸡尾酒\", \"6\": \"喝一杯\"}', '{\"锐澳混合果味伏特加\": \"适量\", \"锐澳青柠朗姆酒\": \"适量\", \"番茄\": \"两个\"}', 5, '2020-02-27 22:26:09', 0, '2020-03-12 19:36:05', '279470');
INSERT INTO `menu` VALUES (179, '可可冰淇淋', 47, '{\"1\": \"所有食材\", \"2\": \"蛋黄放入盆中和糖搅拌\", \"3\": \"搅拌成淡黄色\", \"4\": \"牛奶中放可可粉拌匀\", \"5\": \"倒入蛋黄液里搅拌均匀\", \"6\": \"然后加热煮开放凉\", \"7\": \"奶液过滤下更滑爽\", \"8\": \"淡奶油放糖打发\", \"9\": \"打发至竖起尖角\", \"10\": \"将冷却的奶液倒入淡奶油中拌匀\", \"11\": \"将冰淇淋桶装好后，倒入冰淇淋液，按冰淇淋键30分钟，然后倒入盒中冷冻即可\"}', '{\"牛奶\": \"200g\", \"蛋黄\": \"2只\", \"淡奶油\": \"400g\", \"可可粉\": \"5g\", \"糖\": \"50g\"}', 4, '2020-02-27 22:26:09', 0, '2020-03-12 19:36:05', '342257');
INSERT INTO `menu` VALUES (180, '蔓越莓苏打泡饮', 80, '{\"1\": \"全部材料准备好\", \"2\": \"先将柠檬切片备用\", \"3\": \"再将蔓越莓原汁倒入杯中\", \"4\": \"再将苏打汽水500毫升倒入杯中\", \"5\": \"加入几粒蔓越莓鲜果，可以果子捏开一个小口，让果汁更好地渗透到水中\", \"6\": \"再加入柠檬片\", \"7\": \"炎热夏日，来一杯吧\", \"8\": \"好清凉\"}', '{\"蔓越莓鲜果\": \"数枚\", \"蔓越莓原汁\": \"150毫升\", \"苏打汽水\": \"500毫升\", \"柠檬\": \"3片\"}', 5, '2020-02-27 22:26:10', 0, '2020-03-12 19:36:05', '272128');
INSERT INTO `menu` VALUES (181, '冰激凌面包的诱惑', 79, '{\"1\": \"面头切去顶部，用刀挖出一个圆型，慢慢挖，注意不要挖穿面包底，挖出中间部分的面包切小丁\", \"2\": \"面包放入烤盘撒上适量的烧烤料油拌匀，面包碗型内也撒上适量的烧烤料油\", \"3\": \"放入预热好的烤箱上下火200度烤10分钟，注意烤制过程要在旁边观察，看到面包外表焦黄色就可以，不要烤焦了烤糊了！\", \"4\": \"提前做好的香蕉冰激凌杯\", \"5\": \"提前冻好蛋黄牛奶冰激凌\", \"6\": \"隔水融化巧克力酱\", \"7\": \"把烤好的面包丁装入面包里，用勺子挖几个冰激凌放到面包顶部！滴几滴巧克力酱调色！\", \"8\": \"面包刚刚烤过还有热气，冰激凌很快融化掉，马上开吃！\", \"9\": \"面包烤过酥脆的口感，和冰激凌透心凉的滑感瞬间充激味蕾！好吃到尖叫！\"}', '{\"馒头\": \"一个\", \"烧烤料油\": \"适量\", \"奶香冰激凌\": \"2个\", \"香蕉冰激凌\": \"1个\", \"巧克力酱\": \"适量\", \"黄油\": \"1块\"}', 4, '2020-02-27 22:26:11', 0, '2020-03-12 19:36:05', '342075');
INSERT INTO `menu` VALUES (182, '彩虹鸡尾酒', 68, '{\"1\": \"顺着杯壁慢慢倒入红石榴糖浆\", \"2\": \"顺着杯壁慢慢倒入波士香蕉力娇酒\", \"3\": \"以同样的方式慢慢注入波士紫罗兰力娇酒，波士蓝橙力娇酒，波士蜜瓜力娇酒，波士白橙皮力娇酒\", \"4\": \"成品，倒酒的力度控制的不够好，分层不明显，下次努力\"}', '{\"红石榴糖浆\": \"10ml\", \"波士蜜瓜力娇酒\": \"10ml\", \"波士蓝橙力娇酒\": \"10ml\", \"波士香蕉力娇酒\": \"10ml\", \"波士紫罗兰力娇酒\": \"10ml\", \"波士白橙皮力娇酒\": \"10ml\"}', 5, '2020-02-27 22:26:12', 0, '2020-03-12 19:36:05', '269684');
INSERT INTO `menu` VALUES (183, '自制蛋黄牛奶冰激凌', 20, '{\"1\": \"蛋黄2个，纯牛奶150克，白糖50克\", \"2\": \"50克白糖倒入蛋黄中\", \"3\": \"用打蛋器低速打发\", \"4\": \"打发到蛋黄变白，白糖融化\", \"5\": \"打好的蛋黄糊放一边备用\", \"6\": \"牛奶倒入奶锅中小火煮到牛奶冒泡泡就好，\", \"7\": \"慢慢一点一点倒进蛋黄糊中，边倒边搅拌，以免牛奶烫熟蛋黄糊，全部倒完搅拌均匀\", \"8\": \"牛奶蛋黄液倒回奶锅\", \"9\": \"小火边煮边搅拌，以免糊锅\", \"10\": \"煮到奶液泡泡涨起来关火\", \"11\": \"放凉\", \"12\": \"倒入保鲜盒，放冰箱冷冻室冷冻。\", \"13\": \"一个小时拿出来搅拌一次，一共搅拌四次就可以了\", \"14\": \"成品\"}', '{\"蛋黄\": \"2个\", \"牛奶\": \"150克\", \"白糖\": \"50克\"}', 4, '2020-02-27 22:26:12', 0, '2020-03-12 19:36:05', '341988');
INSERT INTO `menu` VALUES (184, '海之蓝鸡尾酒', 62, '{\"1\": \"准备材料\", \"2\": \"朗姆酒倒入适量\", \"3\": \"锐澳鸡尾酒适量\", \"4\": \"倒入苏打水\", \"5\": \"挤几滴柠檬汁\", \"6\": \"放入一个柠檬片，最后缓缓注入蓝橙酒即可\", \"7\": \"可以看见清晰地分层\"}', '{\"朗姆酒\": \"适量\", \"锐澳鸡尾酒\": \"适量\", \"苏打水\": \"适量\", \"蓝橙力娇酒\": \"适量\", \"青柠\": \"一片\"}', 5, '2020-02-27 22:26:13', 0, '2020-03-12 19:36:05', '268746');
INSERT INTO `menu` VALUES (185, '冰激凌面包甜品杯', 25, '{\"1\": \"黄油隔水融化\", \"2\": \"馒头切片，两面刷上黄油\", \"3\": \"切成细丁\", \"4\": \"均匀地摆放在烤盘\", \"5\": \"预热烤箱上下火200度烤8分钟，\", \"6\": \"翻面一次再烤2分钟，出炉\", \"7\": \"提前做好的冰激凌\", \"8\": \"熟花生酥\", \"9\": \"隔水融化的巧克力酱\", \"10\": \"取一个杯子倒入一层香蕉冰激凌\", \"11\": \"放一层烤馒头丁\", \"12\": \"再放一层香蕉冰激凌\", \"13\": \"撒上适量花生酥\", \"14\": \"再放一层烤馒头丁\", \"15\": \"淋上巧克力酱撒上花生酥\", \"16\": \"另取一个杯子，倒入一层蛋黄牛奶冰激凌\", \"17\": \"加入适量烤馒头丁，再加入一层冰激凌，再放一层烤馒头丁，淋上巧克力酱！\", \"18\": \"这样就做好了\", \"19\": \"烤馒头丁酥脆的口感和冰激凌透心凉的顺滑，凉爽香脆！好吃停不下来！\", \"20\": \"成品\"}', '{\"馒头\": \"半个\", \"冰激凌\": \"适量\", \"熟花生碎\": \"适量\", \"黄油\": \"1块\"}', 4, '2020-02-27 22:26:14', 0, '2020-03-12 19:36:05', '342069');
INSERT INTO `menu` VALUES (186, '石榴龙舌兰鸡尾酒', 80, '{\"1\": \"主要材料备齐；\", \"2\": \"在冰镇鸡尾酒杯的边上用柠檬瓣擦一圈；在一个浅盘里撒上粗盐，然后把杯边放在盐里蘸一下；\", \"3\": \"把龙舌兰、柠檬汁、石榴糖浆混合搅拌均匀后倒入杯中；\", \"4\": \"加入浆果冰块；\", \"5\": \"用搅拌针搅散即可。\"}', '{\"银龙舌兰酒\": \"60毫升\", \"柠檬汁\": \"60毫升\", \"石榴糖浆\": \"40毫升\", \"浆果冰块\": \"适量\", \"粗盐\": \"少许\", \"柠檬\": \"1瓣\"}', 5, '2020-02-27 22:26:14', 0, '2020-03-12 19:36:05', '239147');
INSERT INTO `menu` VALUES (187, '榴莲冰激凌', 16, '{\"1\": \"熟透的榴莲果肉，把里面的核也去掉。榴莲前提在冰箱冷藏至比较凉，这样温度低，后期和奶油拌一起时不会把奶油融化\", \"2\": \"把果肉和纯牛奶一起放入料理机，牛奶也要用冷藏过的\", \"3\": \"搅拌成果泥装，因果肉多，牛奶少，搅拌出来会很浓稠\", \"4\": \"加入蜂蜜搅拌均匀\", \"5\": \"淡奶油放入无油无水的盆里，加入白糖\", \"6\": \"搅打到不流动的状态，即6分发的状态，不要搅拌果冻，否则影响口感\", \"7\": \"把搅拌好的榴莲泥倒入奶油中\", \"8\": \"用翻拌蛋糕的手法翻拌\", \"9\": \"翻拌均匀即可，不要翻拌过度\", \"10\": \"把拌好的糊倒入密封盒中，冰箱低温冷冻。我的低温冷冻一天后放到软冻室，负7度保存，这样吃的时候不用提前拿出来回温，就可以直接挖着吃，而且软硬非常合适。没有软冻的可以提前拿出来回温10分钟左右再吃，口感最佳。\", \"11\": \"做好的冰激凌，每次两个球我是完全不过瘾啊\", \"12\": \"这个是配了一块冻干的榴莲，给我儿子吃，他平时不爱吃榴莲，但喜欢吃榴莲冰激凌，榴莲干他也喜欢。\"}', '{\"榴莲肉\": \"250g\", \"纯牛奶\": \"100g\", \"淡奶油\": \"100g\", \"蜂蜜\": \"20g\", \"白糖\": \"8g\"}', 4, '2020-02-27 22:26:15', 0, '2020-03-12 19:36:05', '341894');
INSERT INTO `menu` VALUES (188, '鸡尾酒姑娘果果汁', 6, '{\"1\": \"去掉姑娘果的外皮，取出果实部分；\", \"2\": \"将姑娘果加入到料理杯中，并加入适量的水，开启料理机；\", \"3\": \"将姑娘果果汁、伏特加预调酒贮备好；\", \"4\": \"先将姑娘果汁倒入杯中，然后加入伏特加预调酒，一杯美味的鸡尾酒姑娘果果汁就摆在面前啦；\"}', '{\"姑娘果\": \"75g\", \"伏特加预调酒\": \"45ml\", \"水\": \"40g\", \"冰块\": \"2块\"}', 5, '2020-02-27 22:26:16', 0, '2020-03-12 19:36:05', '232555');
INSERT INTO `menu` VALUES (189, '香蕉牛奶冰淇淋', 83, '{\"1\": \"准备好香蕉牛奶和冰淇淋粉\", \"2\": \"将冰淇淋粉、香蕉牛奶倒入一个深容器中\", \"3\": \"用刮刀拌至融化无颗粒\", \"4\": \"用电动打蛋器打至蓬松状态，加盖冷冻至稍微凝固\", \"5\": \"取出用电动打蛋器打约5~8分钟左右体积逐渐膨大，再加盖冷冻稍凝固状态\", \"6\": \"这是第四次冷冻后的状态，体积已膨大至容器的一半，再搅拌后继续冷冻，时间需长点\", \"7\": \"至完全凝固时再取出，用电动打蛋器高速充分搅拌\", \"8\": \"搅拌至如图状态，体积比第一次已充分膨大，加盖冷冻一夜后即可食用\", \"9\": \"吃时用挖球器取出放入碗中\", \"10\": \"做好的冰淇淋非常爽滑绵软哟\"}', '{\"香蕉牛奶\": \"300克\", \"冰淇淋粉\": \"100克\"}', 4, '2020-02-27 22:26:16', 0, '2020-03-12 19:36:05', '327072');
INSERT INTO `menu` VALUES (190, '冰淇淋蛋糕', 73, '{\"1\": \"先做柠檬酱，将2勺白糖、柠檬汁、柠檬皮屑、蛋黄、和切小块的黄油放入奶锅中，小火边加热边搅拌，直至变浓稠，过筛至隔冰水的盆中冷却\", \"2\": \"将两张保鲜膜铺在模具中，四周要留出适当的宽度\", \"3\": \"将蓝莓和糖放入搅拌机\", \"4\": \"搅拌至成均匀的果酱液\", \"5\": \"过筛\", \"6\": \"淡奶油打至七发\", \"7\": \"加入柠檬酱，搅拌均匀\", \"8\": \"模具中加入一半的奶油柠檬酱\", \"9\": \"手指饼干表面沾满自制蓝莓酱，铺在模具中\", \"10\": \"在倒上剩下的奶油柠檬酱，用刮刀抹平\", \"11\": \"将模具外的保鲜膜反过来盖上，放入冰箱冷冻8小时\", \"12\": \"倒扣在盘上,揭开保鲜膜，用加热过的刀切开享用吧\"}', '{\"蛋黄\": \"4个\", \"黄油\": \"70g\", \"蓝莓\": \"3cup\", \"淡奶油\": \"2cup\", \"手指饼干\": \"若干\", \"砂糖\": \"2大匙\", \"柠檬汁\": \"3勺\", \"柠檬皮屑\": \"1大匙\"}', 4, '2020-02-27 22:26:18', 0, '2020-03-12 19:36:05', '339934');
INSERT INTO `menu` VALUES (191, '鸡尾酒奇异果果汁', 78, '{\"1\": \"将奇异果洗净，去皮，对切成小块；\", \"2\": \"将切块的奇异果，放入料理杯中，开启料理机；\", \"3\": \"将桂花苹果糖浆，奇异果果汁，伏特加预调酒（紫莓味）依次加入杯中；\", \"4\": \"搅拌均匀，即可饮用。\"}', '{\"奇异果\": \"400g\", \"桂花苹果糖浆\": \"30ml\", \"伏特加预调酒\": \"30ml\"}', 5, '2020-02-27 22:26:18', 0, '2020-03-12 19:36:05', '232219');
INSERT INTO `menu` VALUES (192, '清凉来袭—鸡尾酒饮品', 48, '{\"1\": \"葡萄与雪碧用冰格冰上\", \"2\": \"鸡尾酒两瓶\", \"3\": \"冻好的倒入杯中\", \"4\": \"加上鸡尾酒即可\"}', '{\"鸡尾酒\": \"2个\", \"雪碧\": \"1个\", \"葡萄\": \"8个\"}', 5, '2020-02-27 22:26:19', 0, '2020-03-12 19:36:05', '231726');
INSERT INTO `menu` VALUES (193, '自制冰淇淋', 70, '{\"1\": \"蛋黄加白糖搅拌均匀\", \"2\": \"将蛋液打发到乳白色就可以了……如图\", \"3\": \"将准备好的牛奶倒入锅中，煮至微开\", \"4\": \"将煮好的牛奶慢慢的倒入打发好的蛋液中，边倒边搅拌（千万要慢慢的倒，不然就成鸡蛋花了）\", \"5\": \"将牛奶和蛋液搅拌均匀\", \"6\": \"将做好的冰淇淋液再次倒入锅中，煮至微开。之后，将冰淇淋液倒入容器，等凉了后放入冰箱冷冻室即可\", \"7\": \"我吃的时候，还加了奥利奥䃐碎片，也可以去买果酱淋在上面，很好吃\"}', '{\"牛奶\": \"500g\", \"蛋黄\": \"4个\", \"白糖\": \"100g\"}', 4, '2020-02-27 22:26:19', 0, '2020-03-12 19:36:05', '339792');
INSERT INTO `menu` VALUES (194, '鸡尾酒葡萄果汁', 68, '{\"1\": \"将成串的黑晶葡萄用剪刀剪去大枝，留下带蒂的葡萄粒;\", \"2\": \"撒入少许面粉（这是小妙招噢，可以将葡萄洗得更干净）, 加入清水，用手进行揉搓。洗好的葡萄揪掉蒂;\", \"3\": \"将摘好洗净的葡萄对切去除葡萄籽，将葡萄果肉放入原汁机中，榨取葡萄汁；\", \"4\": \"饮用方式一：将葡萄汁盛入杯中；此时即可饮用；\", \"5\": \"饮用方式二：将杯中加入桂花苹果糖浆，然后再倒入葡萄汁，搅拌均匀即可饮用；\", \"6\": \"饮用方式三：将杯中加入桂花苹果糖浆，再倒入葡萄汁，最后倒入朗姆预调酒，搅拌均匀即可饮用；\"}', '{\"黑晶葡萄\": \"500g\", \"桂花苹果糖浆\": \"20ml\", \"朗姆预调酒\": \"30ml\"}', 5, '2020-02-27 22:26:21', 0, '2020-03-12 19:36:05', '231540');
INSERT INTO `menu` VALUES (195, '酸奶冰淇淋', 94, '{\"1\": \"蛋黄加糖用电动打蛋器打至奶白色\", \"2\": \"打发成这样状态就可以了！\", \"3\": \"淡奶油倒入锅中，用小火煮至锅边起泡(不可煮滚)\", \"4\": \"慢慢倒入打发的蛋黄，拌匀，倒回锅中，小火煮至浓稠，要不停搅拌，直到能将勺子用背面粘起雪糕浆\", \"5\": \"放凉后倒入酸奶拌匀，放冰箱冷冻40分钟后取出再用电动打蛋器打松，然后再次冷冻\"}', '{\"蛋黄\": \"3个\", \"淡奶油\": \"200ml\", \"细砂糖\": \"15克\", \"酸奶\": \"500ml\"}', 4, '2020-02-27 22:26:21', 0, '2020-03-12 19:36:05', '339759');
INSERT INTO `menu` VALUES (196, '青柠鸡尾酒', 96, '{\"1\": \"备好食材：锐澳鸡尾酒、青柠檬、冰块\", \"2\": \"青柠檬用盐搓揉片刻，再用清水冲洗干净\", \"3\": \"再将半个青柠檬切成薄片\", \"4\": \"将柠檬片放入杯中\", \"5\": \"倒入150g鸡尾酒\", \"6\": \"再到入80g雪碧\", \"7\": \"最后放入冰块便可畅饮啦，酸酸甜甜特别好喝！\"}', '{\"鸡尾酒\": \"150g\", \"青柠檬\": \"半个\", \"雪碧\": \"80g\", \"冰块\": \"6块\"}', 5, '2020-02-27 22:26:22', 0, '2020-03-12 19:36:05', '229654');
INSERT INTO `menu` VALUES (197, '小清新 | 草莓炼乳冰淇淋', 58, '{\"1\": \"将草莓酱和柠檬汁混合搅拌均匀\", \"2\": \"备用。\", \"3\": \"将白砂糖、牛奶、炼乳和鲜奶油倒入调理盆中，混合搅拌均匀后。\", \"4\": \"倒入草莓混合液\", \"5\": \"继续搅拌均匀。\", \"6\": \"取出冷冻好的冰桶\", \"7\": \"倒入草莓炼乳冰淇淋原液\", \"8\": \"开机充分搅拌15分钟。\", \"9\": \"开机充分搅拌15分钟。\", \"10\": \"效果图~\", \"11\": \"效果图~\", \"12\": \"蛋筒草莓冰淇淋\", \"13\": \"蛋筒草莓冰淇淋\", \"14\": \"成品~\"}', '{\"冰桶\": \"24小时（提前-18度冰冻24小时）\", \"牛奶\": \"40ml\", \"白砂糖\": \"10g\", \"柠檬汁\": \"5g\", \"淡奶油\": \"75ml\", \"炼乳\": \"40g\", \"草莓酱\": \"80g\"}', 4, '2020-02-27 22:26:23', 0, '2020-03-12 19:36:05', '339412');
INSERT INTO `menu` VALUES (198, '西瓜柠檬鸡尾酒果汁', 74, '{\"1\": \"原材料准备,西瓜、朗姆预调酒；\", \"2\": \"西瓜切成适合放入原汁机中大小的块；\", \"3\": \"西瓜放入原汁机中榨取果汁；\", \"4\": \"根据个人口味兑入适量的朗姆预调酒、蜂蜜搅匀；\", \"5\": \"爽口解暑的西瓜汁。\"}', '{\"西瓜\": \"500g\", \"朗姆预调酒\": \"适量\", \"蜂蜜\": \"适量\"}', 5, '2020-02-27 22:26:23', 0, '2020-03-12 19:36:05', '229588');
INSERT INTO `menu` VALUES (199, 'Mojito鸡尾酒', 9, '{\"1\": \"准备材料\", \"2\": \"薄荷洗净，放入清水中浸泡一会儿，取下嫩尖，余下的部分加入白砂糖混合，可以捣碎味道更出来，只是比较不好看，薄荷多些更好喝\", \"3\": \"薄荷和糖放入杯中，青柠切角，挤出果汁，并把青柠角也放入杯中\", \"4\": \"倒入朗姆酒，不胜酒力所以用了25ml，普通可用45ml\", \"5\": \"再倒入苏打水\", \"6\": \"放入适量冰块，可以在加入朗姆酒前放入碎冰，最后用薄荷的嫩尖装饰即可\"}', '{\"黑朗姆\": \"25ml\", \"苏打水\": \"150ml\", \"青柠\": \"1/2个\", \"薄荷\": \"1枝\", \"糖\": \"2小勺\", \"碎冰\": \"1/2杯\"}', 5, '2020-02-27 22:26:25', 0, '2020-03-12 19:36:05', '224714');
INSERT INTO `menu` VALUES (200, '酸奶芒果冰淇淋', 100, '{\"1\": \"自种的芒果,表皮粘粘的，芒果浸泡淡盐水中5分钟，冲洗干净\", \"2\": \"削皮，去核取下果肉\", \"3\": \"放入料理机打成果泥，装入保鲜盒里\", \"4\": \"准备食材\", \"5\": \"纯牛奶和糖倒入奶锅小火烧开，按热奶键，倒入奶粉，搅拌至没颗粒，再烧开1分钟，要不时搅拌下，放入冷水降温\", \"6\": \"倒入芒果泥里搅拌均匀\", \"7\": \"再加入酸奶搅拌均匀顺滑\", \"8\": \"盖上盖子，放冰箱靠里面速冻，1个小时后拿出来搅拌均匀，后面每隔半小时后拿出来搅拌均匀4次，最后数小时放着冻硬。要吃时拿出来放5六分钟软化些，勺子挖得动就可以了\"}', '{\"芒果肉\": \"300g\", \"无糖奶粉\": \"50\", \"纯牛奶\": \"250ml\", \"酸奶\": \"1瓶\", \"糖\": \"30g\"}', 4, '2020-02-27 22:26:25', 0, '2020-03-12 19:36:05', '338832');
INSERT INTO `menu` VALUES (201, 'Mojito鸡尾酒', 9, '{\"1\": \"材料准备好\", \"2\": \"薄荷叶子冲洗干净\", \"3\": \"青柠和黄柠用盐反复搓洗干净\", \"4\": \"青柠和黄柠一部分切片\", \"5\": \"将洗净的薄荷叶放入容器中，放入白糖\", \"6\": \"用木勺捣碎，出一点薄荷汁就行了\", \"7\": \"挤入柠檬汁\", \"8\": \"再摆入柠檬片\", \"9\": \"放入冰块\", \"10\": \"兑入朗姆酒\", \"11\": \"倒入苏打水\", \"12\": \"再摆上几片薄荷做装饰即可\"}', '{\"苏打水\": \"330ml\", \"朗姆酒\": \"60ml\", \"青柠\": \"1个\", \"黄柠檬\": \"1个\", \"薄荷叶\": \"适量\", \"白糖\": \"20g\"}', 5, '2020-02-27 22:26:26', 0, '2020-03-12 19:36:05', '223622');
INSERT INTO `menu` VALUES (202, '简单巧克力冰淇淋', 4, '{\"1\": \"准备材料：淡奶油500毫升，Nutella巧克力酱300克\", \"2\": \"巧克力酱和淡奶油混合\", \"3\": \"打蛋器打发\", \"4\": \"打到有不消失的纹路即可，最好打几秒就停下来观察一下\", \"5\": \"装到密封容器里，我用的是去年夏天为了做黑芝麻冰淇淋特地买的Tovolo冰淇淋容器\", \"6\": \"放冰箱冷冻三小时以上就完成啦！不需要冰淇淋机器，不需要隔段时间就拿出来搅拌，希望这道不能更简单的冰淇淋食谱能带给大家最冰凉浓郁的盛夏。\"}', '{\"淡奶油\": \"500ml\", \"巧克力酱\": \"300g\"}', 4, '2020-02-27 22:26:26', 0, '2020-03-12 19:36:05', '338408');
INSERT INTO `menu` VALUES (203, '樱桃鸡尾酒', 44, '{\"1\": \"材料备齐。\", \"2\": \"樱桃不像草莓比较软，不能用勺子捣碎。所以用搅拌机把去核的樱桃和一半的薄荷叶打成粗粒倒入杯中。\", \"3\": \"加入砂糖搅拌一下、倒入朗姆和伏特加。\", \"4\": \"苏打水加到满，加冰块点缀薄荷叶即可。\"}', '{\"樱桃\": \"5颗\", \"朗姆酒\": \"30ml\", \"伏特加\": \"30ml\", \"苏打水\": \"适量\", \"薄荷叶\": \"10片\", \"砂糖\": \"1勺\", \"冰块\": \"适量\"}', 5, '2020-02-27 22:26:28', 0, '2020-03-12 19:36:05', '221807');
INSERT INTO `menu` VALUES (204, '鲜芒果冰淇淋', 58, '{\"1\": \"鸡蛋黄两个加糖40g搅拌均匀，打一会，比较偏白了哦！加入两滴柠檬汁去味。\", \"2\": \"放到锅上煮，注意小火，一直搅拌避免糊锅，煮到粘稠了关火，我有过滤了一下，倒入碗中凉着。\", \"3\": \"奶油加糖40g打发，八成的时候，我喜欢稍微打泡一点。\", \"4\": \"加芒果打成泥，慢慢加入蛋液，搅拌均匀，最后再慢慢导入奶油里面，搅拌均匀，放入冰箱。\", \"5\": \"每过半个小时拿出搅拌一下，比较容易没有那么多沙冰，搅拌四五次，然后冻好后用勺舀出形状就好了哦。\"}', '{\"牛奶\": \"200ml\", \"奶油\": \"150ml\", \"糖\": \"80g\", \"鸡蛋\": \"2个\", \"柠檬汁\": \"两滴\"}', 4, '2020-02-27 22:26:28', 0, '2020-03-12 19:36:05', '337250');
INSERT INTO `menu` VALUES (205, '郎姆葡萄酱', 1, '{\"1\": \"将葡萄干放玻璃容器中，洗净。 \", \"2\": \"倒入朗姆酒，以没过葡萄干为宜。\", \"3\": \"浸泡5小时以上，葡萄干已涨发。\", \"4\": \"把葡萄干捞到料理机中，将浸泡的朗姆酒也一同倒入。 \", \"5\": \" 开动料理机，把葡萄干和酒搅打成浆。\", \"6\": \"把混合液体倒入盆中，中火烧开后转成小火，待酱汁出现小泡后，要不断用勺子将盆壁上的酱汁刮回盆中并搅拌，以防糊锅。 \", \"7\": \" 待酱汁浓稠，不断冒出大泡后，关火。把酱汁盛到干净无油无水的容器中，密封保存。\"}', '{\"葡萄干\": \"适量\", \"朗姆酒\": \"适量\"}', 5, '2020-02-27 22:26:29', 0, '2020-03-12 19:36:05', '193323');
INSERT INTO `menu` VALUES (206, '蓝莓果酱冰淇淋', 12, '{\"1\": \"备好食材\", \"2\": \"蛋黄、白砂糖和纯牛奶倒入奶锅中\", \"3\": \"用蛋抽搅拌至白糖融化\", \"4\": \"移至燃气灶上小火加热微滚关火待凉\", \"5\": \"淡奶油用电动打蛋器打于纹路状\", \"6\": \"将放凉的牛奶糊缓缓地倒入淡奶油中，边倒边搅匀\", \"7\": \"挤入蓝莓果酱\", \"8\": \"用电动打蛋器快速打至蓬松，加盖送至冰箱冷冻\", \"9\": \"约1小时后取出，再打至蓬松继续入冰箱冷冻\", \"10\": \"1小时后取出，按上述步骤操作\", \"11\": \"这是第4次取出搅打了\", \"12\": \"最后加盖送冰箱冷冻4小时以上\", \"13\": \"就可以享受美味了\"}', '{\"蛋黄\": \"2个\", \"白砂糖\": \"65克\", \"纯牛奶\": \"200克\", \"淡奶油\": \"200毫升\", \"蓝莓果酱\": \"170克\"}', 4, '2020-02-27 22:26:30', 0, '2020-03-12 19:36:05', '334718');
INSERT INTO `menu` VALUES (207, '水母鸡尾酒', 28, '{\"1\": \"观赏完就一饮而尽吧！\"}', '{\"伏特加\": \"30ml\", \"蓝橙力娇酒\": \"10ml\", \"百利甜酒\": \"适量\"}', 5, '2020-02-27 22:26:31', 0, '2020-03-12 19:36:05', '176643');
INSERT INTO `menu` VALUES (208, '香蕉芒果冰激凌', 56, '{\"1\": \"先把柠檬汁挤出来备用，用柠檬汁不仅可以增加风味，主要是可以防止香蕉氧化变黑\", \"2\": \"香蕉切小块后，加入柠檬汁拌均匀，防止氧化\", \"3\": \"芒果去皮切小块，也加一点柠檬汁拌一下\", \"4\": \"密封盒装好入冰箱冷冻一天\", \"5\": \"如果喜欢奶油的，不妨同时冷冻一杯淡奶油，按总量1/3加入即是有奶油的冰激凌，我是给儿子做了些加奶油的\", \"6\": \"把冷冻好的香蕉和芒果放入料理机\", \"7\": \"搅打成泥状\", \"8\": \"刚刚搅打好的冰激凌，只有香蕉和芒果，搅打好成这种软软的泥状，吃起来口感超赞。\", \"9\": \"如果吃不完就倒入模具中冷冻起来\", \"10\": \"随吃随取，冻硬的就是雪糕的口感哦味道依然是棒棒哒\"}', '{\"香蕉\": \"150g\", \"芒果\": \"200g\", \"柠檬\": \"一个\"}', 4, '2020-02-27 22:26:31', 0, '2020-03-12 19:36:05', '335770');
INSERT INTO `menu` VALUES (209, '蓝莓冰淇淋', 72, '{\"1\": \"新鲜蓝莓洗干净放入料理机打成泥。\", \"2\": \"倒入不沾小奶锅，加入20克砂糖中火熬制蓝莓酱。\", \"3\": \"边煮边快速搅拌，把水分煮干。无水份冰淇淋才会没有冰碴儿。\", \"4\": \"蓝莓酱放冰箱冷藏至完全冷却。\", \"5\": \"淡奶油加20克细砂糖打到酸奶状。不喜欢甜的糖可以不用放了。\", \"6\": \"淡奶油打到酸奶状，也就是稍微浓稠的状态加入完全冷却的蓝莓酱。\", \"7\": \"先用刮刀切拌，再用手抽搅顺滑。\", \"8\": \"倒入保鲜盒，盖上盖子放冰箱速冻6小时以上。\", \"9\": \"我冻了一夜，提前拿出来回温一下再来挖球球食用。一下一下把勺子挎满倒出即可。\", \"10\": \"味道超级赞\", \"11\": \"无需搅拌，完全没有冰碴儿。\"}', '{\"新鲜蓝莓\": \"200克\", \"砂糖\": \"20+20克\", \"淡奶油\": \"250克\", \"蛋卷托\": \"几个\"}', 4, '2020-02-27 22:26:33', 0, '2020-03-12 19:36:05', '335728');
INSERT INTO `menu` VALUES (210, '咸水湖鸡尾酒', 10, '{\"1\": \"把材料倒入雪克杯，摇匀即可倒到杯中。可加上水果装饰。\"}', '{\"伏特加\": \"1/3\", \"蓝色柑香酒\": \"1/3\", \"柠檬汁\": \"1/3\", \"水果装饰\": \"适量\"}', 5, '2020-02-27 22:26:33', 0, '2020-03-12 19:36:05', '132759');
INSERT INTO `menu` VALUES (211, '红粉雪碧', 48, '{\"1\": \"拿1个杯子，先倒入雪碧2/3杯的量\", \"2\": \"然后取1个小勺子，反向顶在杯壁偏上的位置\", \"3\": \"然后慢慢顺着勺子背面的地方倒入红葡萄酒\", \"4\": \"这样就能看到非常漂亮的分层了\"}', '{\"雪碧\": \"适量\", \"红葡萄酒\": \"适量\"}', 5, '2020-02-27 22:26:34', 0, '2020-03-12 19:36:05', '96371');
INSERT INTO `menu` VALUES (212, '可乐奶酪冰淇淋', 76, '{\"1\": \"可乐瓶撕去商标，洗干净备用。\", \"2\": \"从贴商标处分成二截，洗干净，沥干水。\", \"3\": \"用奶酪机做一块鲜奶酪。\", \"4\": \"依个人口味加入适量炼乳。\", \"5\": \"搅拌均匀。\", \"6\": \"加入适量奥利奥碎。\", \"7\": \"拌匀备用，冰淇淋馅就做好了。（如果没有奶酪机也可以换成自己喜欢的其它冰淇淋）\", \"8\": \"巧克币隔热水融化。\", \"9\": \"稍微放凉后倒入两截饮料瓶中，放冰箱冷藏至凝固。\", \"10\": \"取出盛入奶酪奥利奥冰淇淋馅放冰箱冷冻变硬。\", \"11\": \"脱模：用锋利的小刀划开一个小口，小心取下外面的饮料瓶。（上半部把盖子拧下来。）\", \"12\": \"取好的样子。\", \"13\": \"把两部分重新装在一起，再融化一小块巧克力涂抹在接口处，然后把干净的商标围上即可。\", \"14\": \"是不是和真的一样？\", \"15\": \"成品图\", \"16\": \"成品图\", \"17\": \"成品图\"}', '{\"可乐瓶\": \"1个\", \"炼乳\": \"适量\", \"奥利奥碎\": \"适量\", \"鲜奶酪\": \"1块\", \"巧克力币\": \"150克\"}', 4, '2020-02-27 22:26:35', 0, '2020-03-12 19:36:05', '335186');
INSERT INTO `menu` VALUES (213, '香格里拉鸡尾酒', 85, '{\"1\": \"橙半个、酸奶1盒、白糖水15ml。\", \"2\": \"法国波尔多红酒30ml。\", \"3\": \"把半个橙的橙肉取出加白糖水\", \"4\": \"橙肉用果汁机打碎倒入碗中\", \"5\": \"橙果汁里加入30ML波尔多红酒\", \"6\": \"杯中先倒入酸奶\", \"7\": \"再沿着杯壁，倒进红酒\", \"8\": \"橙切瓣，上面刻花\", \"9\": \"花朵成品的样子\", \"10\": \"橙子皮切成条状\", \"11\": \"花朵装饰杯口\", \"12\": \"橙子皮装饰杯口\"}', '{\"法国波尔多红酒\": \"30ml\", \"橙\": \"半个\", \"酸奶\": \"1盒\", \"白糖水\": \"15ml\"}', 5, '2020-02-27 22:26:36', 0, '2020-03-12 19:36:05', '3168');
INSERT INTO `menu` VALUES (214, '纯天然芒果冰淇淋', 56, '{\"1\": \"小台芒去皮。\", \"2\": \"去核，把果肉切小丁。\", \"3\": \"放冰箱冷冻室冻硬。\", \"4\": \"原汁机用上冰淇淋头，把芒果丁放入。\", \"5\": \"轻轻一摇，美味芒果冰淇淋就好了。\", \"6\": \"成品图\", \"7\": \"是不是看着就特别好吃啊\"}', '{\"小台芒\": \"适量\"}', 4, '2020-02-27 22:26:37', 0, '2020-03-12 19:36:05', '335182');
INSERT INTO `menu` VALUES (215, '有冰淇淋吃就开心 | 香滑醇厚的香草冰淇淋', 56, '{\"1\": \"将蛋黄倒入调理盆中，加入白砂糖\", \"2\": \"使用麦子厨房手持电动打蛋器打发至体积膨大，接近白色的状态，提起打蛋头呈冰链缓缓流下\", \"3\": \"锅中倒入牛奶和香草荚\", \"4\": \"加热至快要沸腾。\", \"5\": \"逐次加入打发的蛋黄\", \"6\": \"搅拌均匀，从底部翻起搅拌并慢慢加热至浓稠状。\", \"7\": \"将牛奶蛋黄混合液使用筛网过滤至调理盆中至冷却。\", \"8\": \"倒入淡奶油\", \"9\": \"搅拌均匀成冰淇淋原液\", \"10\": \"取出冷冻好的冰桶（-18度冷冻24小时）\", \"11\": \"倒入冰淇淋原液\", \"12\": \"装上冰淇淋主机。\", \"13\": \"开机搅拌15分钟即可。\", \"14\": \"开机搅拌15分钟即可。\", \"15\": \"取出冰淇淋装入容器中\", \"16\": \"冷冻1小时后，使用挖球勺舀入华夫碗中食用。\", \"17\": \"成品~\"}', '{\"白砂糖\": \"45g\", \"牛奶\": \"80ml\", \"蛋黄\": \"1个\", \"淡奶油\": \"100ml\", \"香草荚\": \"1/4根（剪小段）\"}', 4, '2020-02-27 22:26:38', 0, '2020-03-12 19:36:05', '333759');
INSERT INTO `menu` VALUES (216, '卡仕达冰淇淋', 58, '{\"1\": \"准备好所需要的材料，牛奶200克、鸡蛋2个、砂糖30克、鲜奶油100克。\", \"2\": \"鸡蛋敲开，取出蛋黄，加入细砂糖。(只要蛋黄蛋清是不用的。)\", \"3\": \"将牛奶倒入锅中开小火加热，加热至锅周围稍冒小泡泡即可停止加热，关火备用。\", \"4\": \"将蛋黄和砂糖充分搅拌均匀。\", \"5\": \"将加热好的热牛奶倒入大约三分之一到搅拌均匀的蛋黄里，一定要边倒边搅拌，不要一次倒太多，防止把蛋黄烫熟。\", \"6\": \"再逐步将剩下的牛奶全部倒入搅拌均匀。如果有微熟的蛋黄，可以用过滤网过滤掉。\", \"7\": \"将搅拌均匀的牛奶蛋黄，再次倒入锅中，并以小火边煮边搅拌，当快要被煮沸时可以关一下火搅拌一会儿，再开火搅拌，再快要被煮沸时可以关一下火搅拌一会儿，一直这样重复。\", \"8\": \"直到用手指可以在沾有牛奶蛋黄的勺子背面画出一道干净的细段，就表示已经制作完成。\", \"9\": \"立刻将锅子，放进装有冰水的锅子里，不停的搅拌降温（刚取出的热锅放入冰水中一定要不停的搅拌，防止形成结块。冰水：冬天自来水管里流出的水就可以了，夏天可以在水里加一些冰块）\", \"10\": \"将奶油倒入干净的容器里打发至可以划出纹路即可。\", \"11\": \"将冷却以后的蛋奶糊，倒入打发好的奶油中搅拌均匀，冰淇淋液就制作完成了。如果没有冰淇淋机，将冰淇液直接放冰箱冷冻，每隔2-3个小时拿出来搅拌一下，让冰淇液里进入更多的空气这样吃起来会更蓬松一些，不会有太多的冰渣。\", \"12\": \"取出提前冷冻好的冰淇淋桶。（冰淇淋桶最好放在冰箱冷冻室冷一夜）\", \"13\": \"将冰淇淋液倒入冰淇淋桶，迅速放入搅拌头，开始搅拌（到入冰淇淋液后一定是非常迅速的放入搅拌头开始搅拌，因为冰淇淋液倒入冰淇淋桶以后桶周围的会迅速凝固，会影响搅拌，如果倒入的冰淇淋液的桶的周边已经凝固，可以用软铲子刮一下周围，再放入搅拌头搅拌）\", \"14\": \"搅拌至冰淇淋液成浓稠状即可，大约需要10到15分钟。\", \"15\": \"搅拌好后取出搅拌杆。\", \"16\": \"挖出搅拌好的冰淇淋即可食用，也可以冷冻起来随吃随取。\"}', '{\"牛奶\": \"200克\", \"砂糖\": \"30克\", \"鸡蛋\": \"2个\", \"鲜奶油\": \"100克\"}', 4, '2020-02-27 22:26:40', 0, '2020-03-12 19:36:05', '333245');
INSERT INTO `menu` VALUES (217, '藤椒肥牛', 7, '{\"1\": \"准备原料，金针菇去根撕散开，蒜剁细末。\", \"2\": \"锅中倒入清水烧开，下入藤椒酱。\", \"3\": \"下入金针菇煮熟，捞出放入盛器中。\", \"4\": \"下入肥牛片煮制，去除浮沫，肥牛片捞出倒在金针菇上面。\", \"5\": \"盛器中倒入底汤，汤量没过肥牛片一半。\", \"6\": \"撒上蒜末，浇少许热油炝香即可。\"}', '{\"渔人杰藤椒酱\": \"100克\", \"肥牛片\": \"500克\", \"金针菇\": \"500克\", \"清水\": \"750克\", \"蒜末\": \"10克\"}', 6, '2020-02-27 22:27:01', 0, '2020-03-12 19:36:05', '517085');
INSERT INTO `menu` VALUES (218, '藤椒水煮肉', 85, '{\"1\": \"猪瘦肉切3mm薄片，用水反复清洗5次，沥干水分，加入浆肉料抓揉至肉片发粘，腌制10分钟。\", \"2\": \"准备原料，娃娃菜去根一分为四散开，腌好的猪肉再次搅打均匀。\", \"3\": \"锅中热油，下入娃娃菜炒至成熟软化，倒入盛器中垫底。\", \"4\": \"锅中倒入清水500g烧开，下入藤椒酱。\", \"5\": \"将猪肉均匀打散下入锅中煮制，持续沸腾约1分钟即可成熟，捞出放在垫菜上。\", \"6\": \"将藤椒汤底倒入盛器中，汤量没过猪肉一半即可。\", \"7\": \"在肉片上撒蒜末，舀取热油，放入花椒、干辣椒炝香后浇在蒜末上即可。\"}', '{\"渔人杰藤椒酱\": \"100克\", \"猪瘦肉\": \"250克\", \"娃娃菜\": \"1棵\", \"清水\": \"500克\", \"热油\": \"20克\", \"蒜末\": \"10克\", \"花椒\": \"5克\", \"干辣椒段\": \"5粒\", \"盐\": \"2克\", \"料酒\": \"10克\", \"水\": \"10克\", \"淀粉\": \"10克\", \"鸡蛋清\": \"半个\"}', 6, '2020-02-27 22:27:02', 0, '2020-03-12 19:36:05', '517096');
INSERT INTO `menu` VALUES (219, '酸菜肥牛', 62, '{\"1\": \"准备原料，粉丝用开水泡软，金针菇去根撕散开。\", \"2\": \"锅中烧水，烫熟金针菇，捞出和粉丝一起放入盛器中垫底。\", \"3\": \"锅中倒入500g清水烧开，下入酸菜酱、25g炒酸菜、鸡精、盐、糖。\", \"4\": \"将肥牛下入酸菜汤底中烫制，期间去除浮沫，烫熟后捞出放入盛器中。\", \"5\": \"将剩余25g炒酸菜放在肥牛上，往盛器中倒入酸菜汤底，汤量可以没过一半肥牛即可。\", \"6\": \"在肥牛上撒蒜末，舀取热油，放入花椒、干辣椒炝香后浇在蒜末上即可。\"}', '{\"渔人杰酸菜酱\": \"25克\", \"炒酸菜\": \"50克\", \"肥牛片\": \"300克\", \"金针菇\": \"300克\", \"水发粉丝\": \"150克\", \"清水\": \"500克\", \"鸡精\": \"5克\", \"盐\": \"3克\", \"热油\": \"20克\", \"蒜末\": \"10克\", \"花椒\": \"5克\", \"干辣椒\": \"5粒\"}', 6, '2020-02-27 22:27:04', 0, '2020-03-12 19:36:05', '517092');
INSERT INTO `menu` VALUES (220, '鱼香茄子', 94, '{\"1\": \"圆茄子切块，葱姜蒜切碎。\", \"2\": \"调制鱼香汁。\", \"3\": \"油烧开炸茄子块。\", \"4\": \"炸制略焦黄捞出。\", \"5\": \"锅中留底油烧至六成热下入葱姜蒜。\", \"6\": \"煸出香味后下入郫县豆瓣酱炒出红油。\", \"7\": \"下入炸好的茄子块，翻炒匀。\", \"8\": \"下入青椒。\", \"9\": \"炒匀后倒入鱼香汁。\", \"10\": \"烧开后炖五分钟，水淀粉勾芡。\", \"11\": \"炒亮后关火加入味精出锅。\"}', '{\"圆茄子\": \"适量\", \"青椒\": \"适量\", \"葱\": \"适量\", \"姜\": \"适量\", \"蒜\": \"适量\", \"盐\": \"适量\", \"生抽\": \"适量\", \"老抽\": \"适量\", \"白砂糖\": \"适量\", \"醋\": \"适量\", \"味精\": \"适量\", \"郫县豆瓣酱\": \"适量\"}', 6, '2020-02-27 22:27:04', 0, '2020-03-12 19:36:05', '516890');
INSERT INTO `menu` VALUES (221, '大厨四宝鱼香肉丝', 21, '{\"1\": \"瘦肉切丝备用。\", \"2\": \"冬笋切丝备用。\", \"3\": \"青红椒切丝备用。\", \"4\": \"加入适量料酒，拌均匀。\", \"5\": \"加入淀粉搅拌均匀。\", \"6\": \"热锅下油，葱姜蒜爆香。\", \"7\": \"倒入鱼香肉丝酱汁，翻炒均匀。\", \"8\": \"放入肉丝，木耳，冬笋，青红尖椒丝。\", \"9\": \"翻炒均匀。\", \"10\": \"出锅。\"}', '{\"瘦肉\": \"200g\", \"冬笋\": \"25g\", \"青尖椒\": \"50g\", \"木耳丝\": \"25g\", \"红尖椒\": \"适量\", \"大厨四宝鱼香肉丝调料\": \"1罐\", \"葱\": \"少许\", \"蒜\": \"适量\", \"姜\": \"适量\"}', 6, '2020-02-27 22:27:05', 0, '2020-03-12 19:36:05', '516771');
INSERT INTO `menu` VALUES (222, '酸辣粉', 78, '{\"1\": \"把粉条用80度水泡软。\", \"2\": \"调汁，花生酱，蒜压成泥，辣椒油。\", \"3\": \"生抽，香醋，麻辣鲜调料一滴拌匀，这调料就很香了。\", \"4\": \"泡软的宽粉，水开煮3分钟左右。\", \"5\": \"青菜烫一下，放入大碗。加入调好的料汁。\", \"6\": \"拌匀开吃。\", \"7\": \"比外面的卫生。\"}', '{\"红薯宽粉\": \"200克\", \"青菜\": \"适量\", \"蒜泥\": \"适量\", \"辣椒油\": \"一勺\", \"花生酱\": \"一勺\", \"生抽\": \"一小勺\", \"香醋\": \"一勺\", \"麻辣鲜调料\": \"1滴\"}', 6, '2020-02-27 22:27:06', 0, '2020-03-12 19:36:05', '516266');
INSERT INTO `menu` VALUES (223, '麻婆豆腐', 15, '{\"1\": \"准备两块豆腐。\", \"2\": \"将豆腐切成小四方块。\", \"3\": \"将豆腐焯水3分钟，然后捞出备用。\", \"4\": \"准备辅料。\", \"5\": \"将辅料炒香，接着放豆腐翻炒均匀后加适量水，焖煮3分钟后调味、调色，出锅前勾芡、撒上葱花即可。\"}', '{\"豆腐\": \"2块\", \"辣椒粉\": \"50g\", \"花椒粉\": \"50g\", \"肉末\": \"50g\", \"蒜末\": \"50g\", \"豆瓣酱\": \"50g\", \"葱花\": \"10g\"}', 6, '2020-02-27 22:27:08', 0, '2020-03-12 19:36:05', '516335');
INSERT INTO `menu` VALUES (224, '美味的香辣口水鸡', 66, '{\"1\": \"用Harthsun珐琅锅烧一锅水 。\", \"2\": \"水中放入葱结，姜片和花椒粒，准备两个大鸡腿洗净放入锅中 。\", \"3\": \"大火烧开后转中火，煮10分钟 。\", \"4\": \"捞出一小碗鸡汁备用 。\", \"5\": \"关火后把鸡腿焖8分钟，鸡腿煮熟后捞出。\", \"6\": \"捞出后迅速把鸡腿放入冰水浸泡 。\", \"7\": \"用手把鸡腿肉撕成自己喜欢的大小，装盘备用 。\", \"8\": \"下面开始调料汁，适量的干辣椒面，花椒面和白芝麻。\", \"9\": \"淋上适量的油做油辣子 。\", \"10\": \"油辣子搅拌均匀后放入蒜泥，葱花，生抽，醋，花椒油，白糖，鸡精，蚝油。\", \"11\": \"加上之前的鸡汁，料汁搅拌均匀后淋在鸡肉上 。\", \"12\": \"撒上一点香菜和白芝麻 。\", \"13\": \"香辣口水鸡完成啦！ \"}', '{\"琵琶腿\": \"2个\", \"姜\": \"5片\", \"生抽\": \"适量\", \"醋\": \"2勺\", \"蚝油\": \"适量\", \"大葱\": \"2根\", \"蒜\": \"半头\", \"鸡精\": \"少许\", \"花椒粒\": \"1小把\", \"干辣椒面\": \"适量\", \"花椒面\": \"适量\", \"花椒油\": \"1勺\", \"白糖\": \"少许\", \"白芝麻\": \"适量\", \"香菜\": \"1把\"}', 6, '2020-02-27 22:27:09', 0, '2020-03-12 19:36:05', '516281');
INSERT INTO `menu` VALUES (225, '农家小炒肉', 14, '{\"1\": \"青椒切圈，蒜、姜切小片。\", \"2\": \"取一点黑豆豉放入碗中加一点水化开。\", \"3\": \"猪瘦肉切片，倒入一点老抽、生抽、白酒、花椒粉抓匀后加入一点淀粉再次抓匀腌制10分钟。\", \"4\": \"油烧开。\", \"5\": \"下入肉片快速翻炒滑散。\", \"6\": \"肉色略微变白下入葱丝，翻炒至肉色彻底变色后盛出备用。\", \"7\": \"另起油锅油烧开。\", \"8\": \"下入青椒圈、蒜片、姜片，翻炒一分钟。\", \"9\": \"加入盐、老抽翻炒均匀。\", \"10\": \"下入炒好的肉片。\", \"11\": \"将青椒、肉片炒匀后倒入化水的黑豆豉，翻炒一分钟翻炒均匀。\", \"12\": \"出锅装盘。\"}', '{\"青椒\": \"适量\", \"猪瘦肉\": \"适量\", \"葱\": \"适量\", \"姜\": \"适量\", \"蒜\": \"适量\", \"老抽\": \"适量\", \"生抽\": \"适量\", \"花椒粉\": \"适量\", \"淀粉\": \"适量\", \"盐\": \"适量\", \"白酒\": \"适量\", \"黑豆豉\": \"适量\"}', 6, '2020-02-27 22:27:10', 0, '2020-03-12 19:36:05', '516263');
INSERT INTO `menu` VALUES (226, '宫保鸡丁', 82, '{\"1\": \"黄瓜切丁，干辣椒花椒少许备用，糖和醋1.5:1的比例和适量淀粉加清水加入小碗中。\", \"2\": \"鸡胸肉切丁加料酒生姜丝盐淀粉腌制。\", \"3\": \"冷油中倒入花生米小火炒香起锅备用。\", \"4\": \"锅中剩适量油烧热加入鸡肉翻炒，变色捞起备用。\", \"5\": \"倒入黄瓜和干辣椒花椒翻炒一会儿。\", \"6\": \"倒入鸡肉并加入酱油豆瓣酱翻炒均匀。\", \"7\": \"再加入糖醋汁翻炒均匀，再加入适量的花生米就可以起锅了。\"}', '{\"鸡胸肉\": \"一块\", \"黄瓜\": \"一根\", \"花生米\": \"适量\", \"生姜\": \"适量\", \"淀粉\": \"适量\", \"糖\": \"少许\", \"料酒\": \"适量\", \"酱油\": \"适量\", \"醋\": \"适量\", \"盐\": \"适量\", \"豆瓣酱\": \"适量\"}', 6, '2020-02-27 22:27:11', 0, '2020-03-12 19:36:05', '516122');
INSERT INTO `menu` VALUES (227, '麻辣香锅', 71, '{\"1\": \"准备好鸡肉丁。\", \"2\": \"虾洗净挑去虾线。\", \"3\": \"山药切片，芹菜、洋葱、彩椒切块。\", \"4\": \"起油锅放入葱姜煸炒。\", \"5\": \"放入鸡丁煸炒变色。\", \"6\": \"放入虾。\", \"7\": \"煸炒变色。\", \"8\": \"放入料酒和少许盐。\", \"9\": \"放洋葱和山药煸炒。\", \"10\": \"放彩椒和芹菜煸炒。\", \"11\": \"放一勺郫县豆瓣、花椒粉、生抽和少许糖。\", \"12\": \"成品图。\"}', '{\"鸡肉丁\": \"半斤\", \"虾\": \"4-5个\", \"芹菜\": \"少许\", \"彩椒\": \"少许\", \"洋葱\": \"少许\", \"山药\": \"少许\", \"郫县豆瓣\": \"适量\", \"盐\": \"少许\", \"花椒粉\": \"适量\", \"胡椒粉\": \"适量\"}', 6, '2020-02-27 22:27:12', 0, '2020-03-12 19:36:05', '515872');
INSERT INTO `menu` VALUES (228, '口水鸡', 69, '{\"1\": \"鸡腿洗净。\", \"2\": \"冷水下锅，放卤肉料。\", \"3\": \"放食盐。\", \"4\": \"盖锅盖，中小火煮30分钟。散养的鸡要煮久一点，肉食鸡不用煮30分钟。\", \"5\": \"花生放微波炉。\", \"6\": \"烤1分50秒。\", \"7\": \"烤熟的花生米凉透。\", \"8\": \"大蒜处理干净切末。\", \"9\": \"放容器内加食盐。\", \"10\": \"放鸡精。\", \"11\": \"放白糖。\", \"12\": \"放蒸鱼豉油。\", \"13\": \"放陈醋。\", \"14\": \"取辣椒油。\", \"15\": \"放辣椒油。\", \"16\": \"凉透的花生米捣碎，放调料里。\", \"17\": \"放芝麻油。\", \"18\": \"拌匀，放葱叶末。\", \"19\": \"煮好的鸡腿，取出来放冰水浸泡一会儿。\", \"20\": \"取出来切块。\", \"21\": \"摆盘。\", \"22\": \"倒上料汁。\", \"23\": \"成品。\", \"24\": \"成品。\"}', '{\"鸡腿\": \"1个\", \"食盐\": \"3克\", \"卤肉料\": \"小许\", \"清水\": \"适量\", \"大蒜\": \"几瓣\", \"花生米\": \"1把\", \"鸡精\": \"小许\", \"白糖\": \"小许\", \"陈醋蒸鱼豉油\": \"各10克\", \"香油\": \"小许\", \"辣酱油\": \"3汤匙\", \"葱叶\": \"小许\"}', 6, '2020-02-27 22:27:13', 0, '2020-03-12 19:36:05', '515764');
INSERT INTO `menu` VALUES (229, '蚂蚁上树', 38, '{\"1\": \"将一把粉丝用冷水浸泡一小时，然后用剪刀把粉丝剪短些。\", \"2\": \"准备1两瘦肉末、适量蒜泥、红椒末、葱花。\", \"3\": \"准备1两瘦肉末、适量蒜泥、红椒末、葱花。\", \"4\": \"起锅烧油，将瘦肉末、蒜末、红椒末放进锅内一起炒香，然后放粉丝调味、调色，出锅前撒上葱花即可装盘。\"}', '{\"粉丝\": \"100g\", \"红椒末\": \"50g\", \"蒜末\": \"50g\", \"葱花\": \"适量\"}', 6, '2020-02-27 22:27:14', 0, '2020-03-12 19:36:05', '515483');
INSERT INTO `menu` VALUES (230, '麻辣拌', 48, '{\"1\": \"主要食材；各种火锅丸子（虾丸，蟹丸、牛肉丸等）、莲藕60克、荷兰豆8个、魔芋丝120克等。\", \"2\": \"先来准备酱料；花生酱、芝麻酱各1茶匙放入碗中，加入1茶匙香油、少许食盐、凉白开搅拌至顺滑浓稠备用（芝麻酱、花生酱根据自家口味加，可以都用芝麻酱，也可以都用花生酱，自家喜欢就好）。\", \"3\": \"准备完酱料咱在准备料汁：香葱切葱花，大蒜切末、线椒切小圈（如果有小米椒也可以放一个，红色点缀成品会更好看）。\", \"4\": \"、切好的葱末、线椒、大蒜末和白芝麻、辣椒粉一起放入碗中。锅中烧热油，趁热浇在辣椒粉上激出香味。\", \"5\": \"依次加入3茶匙生抽、2茶匙香醋。\", \"6\": \"加入1茶匙白糖拌匀即成料汁备用。（味精鸡精着些放不放随自己口味）\", \"7\": \"现在来处理食材，荷兰豆大的一切二小的不用切，莲藕洗净去皮切成薄片。（食材不拘于这几种，喜欢什么蔬菜都可以加）\", \"8\": \"切好的莲藕和荷兰豆、魔芋丝一起放入沸水锅中焯水，捞出后过凉备用。\", \"9\": \"丸子类也一起入沸水锅中炒水捞出，这个不用过凉哈。\", \"10\": \"焯过水的火锅丸子、魔芋丝、荷兰豆、莲藕一起放入大一些的容器中。\", \"11\": \"淋入调好的料汁，和调好的麻酱汁拌匀即可。\", \"12\": \"成品。\"}', '{\"火锅丸子\": \"200克\", \"莲藕\": \"1节\", \"荷兰豆\": \"1把\", \"魔芋丝\": \"120克\", \"芝麻酱\": \"2茶匙\", \"辣椒粉\": \"1茶匙\", \"白芝麻\": \"1茶匙\", \"香葱\": \"2棵\", \"大蒜\": \"3瓣\", \"生抽\": \"3茶匙\", \"香醋\": \"2茶匙\", \"白糖\": \"1茶匙\", \"香油\": \"1茶匙\", \"线椒\": \"1个\", \"食用油\": \"适量\"}', 6, '2020-02-27 22:27:15', 0, '2020-03-12 19:36:05', '515225');
INSERT INTO `menu` VALUES (231, '鱼香鸡丝', 87, '{\"1\": \"食材。\", \"2\": \"鸡肉切丝，加入少许白胡椒粉、少许食盐、1茶匙淀粉、1茶匙料酒抓匀腌渍几分钟。\", \"3\": \"腌渍鸡肉的时候来处理辅料，豆瓣酱细细的切成末、红萝卜、泡发好的木耳，玉兰片分别切成丝。\", \"4\": \"准备一碗料汁，防止一会炒至的时候手忙脚乱。1茶匙料酒、2茶匙生抽、2茶匙白糖、3茶匙香醋、1茶匙玉米淀粉再加入少许清水搅拌均匀。\", \"5\": \"腌渍好的鸡丝中加入1茶匙食用油，再次拌匀。\", \"6\": \"锅中放油，油温五六成热下入鸡丝炒至变色即盛出。\", \"7\": \"另起锅，放入蒜末、剁椒、豆瓣酱、葱末小火炒出红油。\", \"8\": \"炒出红油后，放入笋丝、木耳丝、胡萝卜丝翻炒。\", \"9\": \"炒至三丝变软，放入滑炒好的肉丝。\", \"10\": \"快速翻炒均匀，倒入备好的料汁。\", \"11\": \"快速炒至料汁浓稠，基本裹在食材上即可光火。\", \"12\": \"成品。\"}', '{\"鸡胸肉\": \"200克\", \"玉兰片\": \"60克\", \"水发木耳\": \"5多\", \"胡萝卜\": \"半根\", \"剁椒\": \"1茶匙\", \"红油豆瓣酱\": \"1茶匙\", \"大蒜\": \"3瓣\", \"生抽\": \"2茶匙\", \"香醋\": \"3茶匙\", \"白糖\": \"2茶匙\", \"料酒\": \"1茶匙\", \"玉米淀粉\": \"1茶匙\", \"葱末\": \"1茶匙\"}', 6, '2020-02-27 22:27:16', 0, '2020-03-12 19:36:05', '504895');
INSERT INTO `menu` VALUES (232, '麻辣香锅', 34, '{\"1\": \"娃娃菜焯一下。\", \"2\": \"提前泡好腐竹木耳。\", \"3\": \"各种丸子。\", \"4\": \"香菜备用。\", \"5\": \"芹菜切好焯水。\", \"6\": \"豆皮切好焯水。\", \"7\": \"午餐肉。\", \"8\": \"顺便夸爆这款午餐肉，朋友送的，今天吃一下，跟平时买的完全不一样，这个肉切开都是大块肉粒，不像火锅店的，都是淀粉。\", \"9\": \"看看这肉，就是切的时候容易散。\", \"10\": \"很不错。\", \"11\": \"回题，菜花，荷兰豆焯水。\", \"12\": \"金针菇焯水。\", \"13\": \"炸点儿果仁，火大了。\", \"14\": \"葱姜蒜切好。\", \"15\": \"热油放入葱姜蒜，放入虾，边炒边按压虾头，挤出虾油。\", \"16\": \"干辣椒。\", \"17\": \"炒虾的油放入花椒，干辣椒，大料，香叶，豆瓣酱和火锅底料，小火炒开后放入所有食材翻炒即可。\", \"18\": \"成品满满一盆。\"}', '{\"白菜\": \"适量\", \"菜花\": \"适量\", \"芹菜\": \"适量\", \"土豆\": \"适量\", \"金针菇\": \"适量\", \"午餐肉\": \"适量\", \"各种丸子\": \"适量\", \"腊肠\": \"适量\", \"虾\": \"适量\", \"花椒\": \"适量\", \"红尖椒\": \"适量\", \"香叶\": \"适量\", \"郫县豆瓣酱\": \"适量\", \"火锅底料\": \"适量\", \"葱\": \"适量\", \"麻椒\": \"适量\", \"姜\": \"适量\", \"蒜\": \"适量\"}', 6, '2020-02-27 22:27:18', 0, '2020-03-12 19:36:05', '513271');
INSERT INTO `menu` VALUES (233, '鱼香肉丝', 45, '{\"1\": \"同脊肉切丝，放料酒、酱油、白胡椒粉腌制。\", \"2\": \"准备郫县豆瓣和泡椒。\", \"3\": \"辅料切丝，笋、黑木耳、胡萝卜切丝，姜切丝，小葱切丝。\", \"4\": \"准备调料汁：一勺料酒、一勺糖、两勺醋、一勺酱油、一勺蒜蓉，一勺葱段、一勺淀粉。\", \"5\": \"锅烧热放油烧热。\", \"6\": \"放入郫县豆瓣和泡椒炒出红油。\", \"7\": \"放入姜丝翻炒。\", \"8\": \"放入肉丝炒八成熟。\", \"9\": \"放入胡萝卜丝、笋丝、黑木耳丝翻炒。\", \"10\": \"放入调料汁，出锅前淋少许红油。\", \"11\": \"盛盘，表面撒些葱花。\"}', '{\"通脊\": \"适量\", \"笋\": \"适量\", \"胡萝卜\": \"适量\", \"黑木耳\": \"适量\", \"姜\": \"适量\", \"葱\": \"适量\", \"郫县豆瓣\": \"适量\", \"泡椒\": \"适量\", \"酱油\": \"适量\", \"料酒\": \"适量\", \"糖\": \"适量\", \"淀粉\": \"适量\", \"蒜蓉\": \"适量\"}', 6, '2020-02-27 22:27:19', 0, '2020-03-12 19:36:05', '514364');
INSERT INTO `menu` VALUES (234, '白切鸡（辣）', 11, '{\"1\": \"土鸡取一半。\", \"2\": \"冷水下锅，加姜片，煮15分钟。\", \"3\": \"刚好熟透即可。\", \"4\": \"捞起来干净水冷却。备好配料。\", \"5\": \"冷却后切块摆盘。\", \"6\": \"下油爆香辣椒、蒜、姜。\", \"7\": \"加入少量鸡汤，酱油，生粉勾芡。\", \"8\": \"做好的配料淋上鸡块即可。\", \"9\": \"成品。\"}', '{\"土鸡\": \"半只\", \"蒜\": \"适量\", \"辣椒\": \"2个\", \"姜\": \"适量\", \"酱油\": \"2匙\", \"香油\": \"1匙\"}', 7, '2020-02-27 22:27:21', 0, '2020-03-12 19:36:05', '516284');
INSERT INTO `menu` VALUES (235, '零失败水煮肉片', 16, '{\"1\": \"先准备一包水煮肉片的调料包。我很喜欢蜀味源的，特别正宗特别香。\", \"2\": \"准备包川粉，约200克，也可以用红薯粉条。\", \"3\": \"黑木耳切小块。\", \"4\": \"莴苣杆切薄片。\", \"5\": \"金针菇剪去菇脚，洗净备用。\", \"6\": \"瘦猪肉片成薄片。\", \"7\": \"将腌料包倒入瘦肉中，抓匀腌制备用。\", \"8\": \"烧锅开水，放些盐，滴几滴油，放入黑木耳煮3分钟。\", \"9\": \"煮好的黑木耳过下凉开水，沥干水份，铺在砂锅底部。\", \"10\": \"另烧一锅新开水，放点盐，滴几滴油，下入莴苣片抄水30秒。\", \"11\": \"抄好水的莴苣片，过下凉开水，沥干水份铺在黑木耳上。\", \"12\": \"下金针菇煮开1分钟。\", \"13\": \"捞起金针菇，过凉开水后铺在莴苣片上。\", \"14\": \"洗净的锅中，加适量清水，放入调料包，中火煮开。\", \"15\": \"下川粉煮2分钟。\", \"16\": \"调大火，下腌制好的肉片，煮开。\", \"17\": \"将煮好的肉片及汤汁倒入砂锅中。\", \"18\": \"将干料包集中的倒在砂锅的中间，方便等下浇热油。\", \"19\": \"撒点葱花在干料上。\", \"20\": \"锅中烧滚油炝在干料上，油温一定要高。我用大火烧油，差不多1分钟，其间会微微晃动下锅，让油受热更均匀。\", \"21\": \"嫩嫩的肉片，配上白米饭，可香了！\", \"22\": \"Q弹爽滑的川粉，嚼劲十足！\", \"23\": \"零失败的水煮肉片，快学起来吧！\"}', '{\"瘦肉\": \"300g\", \"水煮肉片调料包\": \"1包\", \"黑木耳\": \"适量\", \"莴苣\": \"0.5根\", \"金针菇\": \"适量\", \"香葱\": \"适量\", \"食盐\": \"适量\", \"食用油\": \"少许\", \"川粉\": \"1包\"}', 6, '2020-02-27 22:27:21', 0, '2020-03-12 19:36:05', '514329');
INSERT INTO `menu` VALUES (236, '水煮肉片', 35, '{\"1\": \"鸡胸肉切成薄片，加入料酒盐淀粉拌匀，再加入花椒面，辣椒面，五香粉少许拌匀，其实大家也可以用猪肉牛肉。\", \"2\": \"把其他材料也备好。\", \"3\": \"锅中放少许油烧热放入火锅底料，大葱，生姜翻香。\", \"4\": \"加入适量清水煮沸，先放土豆煮至八成熟，再加入豆皮，根据咸淡加入酱油和盐调味。\", \"5\": \"豆皮土豆煮好之后捞出，开大火放入鸡胸肉，要一片一片的放，而且动作快点。\", \"6\": \"放完之后，鸡胸肉煮两分钟左右就可以起锅享用，撒入香菜。\"}', '{\"鸡胸肉\": \"一块\", \"土豆\": \"适量\", \"豆皮\": \"适量\", \"料酒\": \"少许\", \"火锅底料\": \"适量\", \"香料\": \"少许\", \"大葱\": \"适量\", \"生姜\": \"适量\", \"盐\": \"适量\"}', 6, '2020-02-27 22:27:22', 0, '2020-03-12 19:36:05', '514289');
INSERT INTO `menu` VALUES (237, '荷香糯米鸡', 85, '{\"1\": \"三杯糯米洗净用凉水浸泡一夜，荷叶三片洗净用凉水浸泡一夜。\", \"2\": \"香菇洗净凉水浸泡过夜。\", \"3\": \"鸡胸脯肉切小块，加入洋葱丝，加东古酱油1匙和1匙料酒。\", \"4\": \"撒入适量十三香增香。\", \"5\": \"加0.5匙芝麻油。\", \"6\": \"加0.5匙蚝油拌匀，放冰箱保鲜腌制过夜。\", \"7\": \"往浸泡一夜的糯米中，加入浸泡香菇的香菇水，碗底沉淀的杂质丢弃不要。3杯米，我放的量就到电饭煲提示的三格处。\", \"8\": \"加入一块浓汤宝调味。用杂粮档煮。水开时一定要记打开盖搅拌下，否则浓汤宝只会集中在一小块地方，不均匀哦。\", \"9\": \"泡发好的香菇挤干水份切丁备用。\", \"10\": \"水开，放点盐，滴几滴油，下杂菜粒煮3分钟，捞起沥干水份备用。\", \"11\": \"锅中下油，下腌制好的鸡胸肉炒至变色。\", \"12\": \"下香菇丁翻炒出香味。\", \"13\": \"下杂菜粒炒匀，放适量的盐和鸡精调味。炒香备用。\", \"14\": \"焖好的糯米饭，尝了一口，味道很好！\", \"15\": \"洗净泡软的荷叶，我先对半剪开，再剪去头部的硬梗。勺一勺糯米饭，在荷叶的一角平铺出一薄层。\", \"16\": \"铺上一层馅料。\", \"17\": \"再盖上一薄层糯米。\", \"18\": \"如图整形包好。\", \"19\": \"包好的样子。\", \"20\": \"系上细棉线，防止散开。\", \"21\": \"依次做好，我一共做了五个，每个的量大约是一个成人的饭量。\", \"22\": \"锅中烧水，水开后，上锅隔水大火蒸15分钟。\", \"23\": \"搞定，一人一个，剩的可以放冰箱冷冻，想吃的时候拿出来大火蒸15分钟即可。\"}', '{\"糯米\": \"3杯\", \"鸡胸脯肉\": \"200g\", \"洋葱\": \"小半个\", \"东古酱油\": \"1匙\", \"料酒\": \"1匙\", \"芝麻油\": \"0.5匙\", \"蚝油\": \"0.5匙\", \"十三香\": \"适量\", \"干荷叶\": \"3张\", \"干香茹\": \"10朵\", \"浓汤宝\": \"1块\", \"杂菜粒\": \"适量\", \"食盐\": \"适量\", \"鸡精\": \"适量\", \"食用油\": \"适量\"}', 7, '2020-02-27 22:27:22', 0, '2020-03-12 19:36:05', '516451');
INSERT INTO `menu` VALUES (238, '口水鸡', 25, '{\"1\": \"准备一只鸡腿，用清水洗去鸡腿的血水。\", \"2\": \"锅中加水，放入鸡腿、姜块、干红椒、桂皮、花椒粉、料酒、盐、胡椒粉，小火煮15-20分钟。\", \"3\": \"准备辅料小米椒、花椒、姜蒜末。\", \"4\": \"将煮好的鸡腿切块摆盘，将辅料调味、炒香铺在鸡腿表面，撒上葱花即可上桌。\"}', '{\"鸡腿\": \"1个\", \"蒜\": \"50g\", \"小米椒\": \"5个\", \"花椒\": \"20g\", \"姜块\": \"1个\"}', 6, '2020-02-27 22:27:23', 0, '2020-03-12 19:36:05', '514010');
INSERT INTO `menu` VALUES (239, '蜜汁叉烧', 68, '{\"1\": \"1斤猪瘦肉（我们家不爱吃半肥瘦的，完全不喜欢肥肉的口味，烂烂的也不行）切成二指厚片，放上以上配料拌匀，放水会让它变得软嫩多汁，放入冰箱冷藏室腌制24小时，从冰箱里拿出来，少油小火慢煎致两面金黄即可出锅。\", \"2\": \"原谅我的刀工，味道真的好好，每次上桌都是秒完的。\", \"3\": \"怎么办才好，一定要放三个步骤，不想放生肉的图片，只能放成品图了。\"}', '{\"猪前腿肉\": \"1斤\", \"蚝油\": \"适量\", \"酱油\": \"适量\", \"蜂蜜\": \"适量\", \"盐\": \"适量\", \"十三香\": \"适量\", \"姜片\": \"5片\", \"蒜瓣\": \"3个\", \"水\": \"1汤匙\", \"油\": \"1汤匙\"}', 7, '2020-02-27 22:27:23', 0, '2020-03-12 19:36:05', '516249');
INSERT INTO `menu` VALUES (240, '水煮肉片', 94, '{\"1\": \"准备食材:粉条、莴笋丝、姜葱蒜、干辣椒、肉片(肉片用油、盐、鸡蛋清、淀粉、料酒、胡椒粉拌匀)。\", \"2\": \"将热水泡开的粉条放入砂锅备用。\", \"3\": \"起锅烧油，爆香姜蒜。\", \"4\": \"放入一勺豆瓣酱，炒出红油。\", \"5\": \"加入适量的水。\", \"6\": \"煮开后放入酱油、陈醋和糖。\", \"7\": \"放入莴笋丝焯熟。\", \"8\": \"捞起放入砂锅的粉条上。\", \"9\": \"留下的汤汁，下肉片煮。\", \"10\": \"汆熟马上捞起放入砂锅。\", \"11\": \"红油汤汁倒入砂锅内，开火煮开。\", \"12\": \"炖开后放入干辣椒和葱花。\", \"13\": \"浇上热油完成。\", \"14\": \"成品。香辣开胃。\"}', '{\"瘦肉\": \"180g\", \"干粉条\": \"100g\", \"莴笋丝\": \"100g\", \"蒜\": \"适量\", \"姜\": \"适量\", \"干辣椒\": \"适量\", \"葱花\": \"适量\", \"淀粉\": \"适量\", \"鸡蛋清\": \"适量\", \"盐\": \"适量\", \"料酒\": \"适量\", \"胡椒粉\": \"适量\", \"豆瓣酱\": \"1勺\", \"酱油\": \"2勺\", \"陈醋\": \"1勺\", \"糖\": \"2匙\", \"鸡精\": \"少许\", \"油\": \"适量\", \"水\": \"适量\"}', 6, '2020-02-27 22:27:24', 0, '2020-03-12 19:36:05', '513895');
INSERT INTO `menu` VALUES (241, '麻辣肠煲仔饭', 78, '{\"1\": \"长粒米洗净加水放入电饭煲中。\", \"2\": \"准备好麻辣肠。\", \"3\": \"麻辣肠切片，在米饭上摆满一层。\", \"4\": \"按煲仔饭程序煮饭。\", \"5\": \"烧开水，加盐加几滴油，把生菜放入锅中焯水捞出备用。\", \"6\": \"小碗里加2匙生抽，一匙蚝油，少许生粉加半碗水调匀，另起油锅下入蒜泥，倒入蚝油水勾芡。\", \"7\": \"勾芡淋在生菜上。\", \"8\": \"煎蛋。\", \"9\": \"电饭煲中米饭水快干时放入一匙生抽。\", \"10\": \"一匙食用油，顺边沿淋下去。\", \"11\": \"煮的是5人份的米饭，所以鸡蛋另煎的。\", \"12\": \"煮好的米饭。\", \"13\": \"摆盘。\", \"14\": \"香喷喷的。\"}', '{\"长粒米\": \"2杯（煮饭的量杯）\", \"麻辣肠\": \"250g\", \"生菜\": \"2颗\", \"鸡蛋\": \"5只\", \"生抽\": \"2匙\", \"蚝油\": \"1匙\", \"食用油\": \"4匙\", \"生粉\": \"少许\", \"蒜泥\": \"2颗\"}', 7, '2020-02-27 22:27:25', 0, '2020-03-12 19:36:05', '516141');
INSERT INTO `menu` VALUES (242, '萝卜糕', 20, '{\"1\": \"香姑泡好后洗净切小粒，腊肠一根切小粒，萝卜去皮擦成丝。\", \"2\": \"炒菜锅点火加热后，加适量的油先把香菇炒香，再加入腊肠炒一会，加上盐和糖翻炒一会，倒入萝卜丝一起翻炒至软，倒入蚝油和酱油调味。\", \"3\": \"直接倒入粘米粉在炒好的调料上，然后加适量的水搅拌均匀，不锈钢碟涂上一遍花生油，把搅拌好的萝卜粉盛入碟中，尽量用铲子抹平，这是原图。\", \"4\": \"蒸锅上气后放入萝卜糕粉大火蒸30分钟，这是成品。\"}', '{\"萝卜\": \"1个\", \"粘米粉\": \"半斤左右\", \"腊肠\": \"1根\", \"香菇\": \"适量\", \"水\": \"适量\", \"油\": \"适量\", \"糖\": \"一勺\", \"酱油\": \"适量\", \"蚝油\": \"适量\", \"盐\": \"适量\"}', 7, '2020-02-27 22:27:26', 0, '2020-03-12 19:36:05', '515969');
INSERT INTO `menu` VALUES (243, '麻辣水煮鱼', 79, '{\"1\": \"准备一个鱼尾，将鱼骨肉分离，肉切片并腌制10分钟、骨切段。\", \"2\": \"准备一个鱼尾，将鱼骨肉分离，肉切片并腌制10分钟、骨切段。\", \"3\": \"准备一个鱼尾，将鱼骨肉分离，肉切片并腌制10分钟、骨切段。\", \"4\": \"准备一些莴笋皮和青红椒、姜蒜末、花椒。\", \"5\": \"准备一些莴笋皮和青红椒、姜蒜末、花椒。\", \"6\": \"起锅烧油，将半块麻辣火锅底料炒香后加水、调味，放鱼骨，大火煮出浓汤。\", \"7\": \"将莴笋皮在锅内煮断生与鱼骨一起捞出垫底。\", \"8\": \"将鱼片在锅内煮熟后捞出装入盆内，将调味料铺在鱼片表面，淋上热油即可上桌。\", \"9\": \"成品。\"}', '{\"鱼尾\": \"1个\", \"莴笋杆\": \"半个\", \"青椒\": \"适量\", \"花椒\": \"10g\", \"姜末\": \"适量\", \"麻辣火锅底料\": \"半块\", \"红椒\": \"适量\", \"蒜末\": \"适量\"}', 6, '2020-02-27 22:27:26', 0, '2020-03-12 19:36:05', '513740');
INSERT INTO `menu` VALUES (244, '虾饺', 19, '{\"1\": \"澄粉和一半生粉混合，浇开水搅拌，揉成面团。\", \"2\": \"虾剥皮，去虾线。\", \"3\": \"一部分用刀压扁，一部分切成虾段。\", \"4\": \"放少许盐，鸡精、胡椒粉用手揉。\", \"5\": \"虾肉内放笋丁。\", \"6\": \"面揉成长条。\", \"7\": \"做圆机子。\", \"8\": \"用刀压成圆饼。\", \"9\": \"从一边起包成饺子。\", \"10\": \"蒸锅放水烧开，放入蒸屉，摆上虾饺蒸5分钟，捡到盘中。\"}', '{\"澄粉\": \"适量\", \"生粉\": \"澄粉的一半\", \"虾\": \"7-8个\", \"笋\": \"少许\", \"猪油\": \"少许\", \"白胡椒\": \"少许\", \"盐\": \"少许\", \"鸡精\": \"少许\"}', 7, '2020-02-27 22:27:26', 0, '2020-03-12 19:36:05', '515581');
INSERT INTO `menu` VALUES (245, '鱼香肉丝', 79, '{\"1\": \"肉切肉丝，加水淀粉、料酒抓匀备。\", \"2\": \"木耳、胡萝卜分别切丝。姜、蒜剁蓉（忘拍照了，各位脑补）。\", \"3\": \"生抽、陈醋、白糖、淀粉加少许清水调成汁备用。\", \"4\": \"锅烧热放油，倒入肉丝稍炒至变色盛出。\", \"5\": \"锅里留底油，放剁好的姜、蒜及豆瓣酱炒香。\", \"6\": \"放入木耳丝、胡萝卜丝翻炒。\", \"7\": \"放入之前炒过的丝肉和调好的碗汁快速翻烧炒均匀盛出。\", \"8\": \"一道美味的下饭菜就做好了，开饭啦。\"}', '{\"瘦肉\": \"3两\", \"木耳\": \"6朵\", \"胡萝卜\": \"1个\", \"姜\": \"1小块\", \"蒜\": \"2瓣\", \"陈醋\": \"2勺\", \"生抽\": \"3勺\", \"淀粉\": \"适量\", \"白糖\": \"少许\", \"豆瓣酱\": \"1勺\"}', 6, '2020-02-27 22:27:27', 0, '2020-03-12 19:36:05', '513655');
INSERT INTO `menu` VALUES (246, '麻婆豆腐', 33, '{\"1\": \"姜、蒜剁蓉，豆瓣酱剁越碎越出味。\", \"2\": \"豆腐切小块入淡盐水稍煮捞起。\", \"3\": \"锅里加油，小火。入牛肉末炒香，加入剁好的姜、蒜、豆瓣酱炒出红油。\", \"4\": \"加入清水或高汤煮出味道，再放入豆腐稍煮，放少许生抽。豆瓣酱和生抽有盐味，不用再放盐。\", \"5\": \"淋入水淀粉翻炒，撒少许麻椒面翻炒均匀。\", \"6\": \"起锅，一碗鲜香麻辣的麻婆豆腐就可上桌了。\"}', '{\"豆腐\": \"半斤\", \"牛肉末\": \"1两\", \"姜\": \"1小块\", \"蒜\": \"3瓣\", \"郫县豆瓣酱\": \"1勺\", \"生抽\": \"1匙\", \"水淀粉\": \"少许\"}', 6, '2020-02-27 22:27:28', 0, '2020-03-12 19:36:05', '460946');
INSERT INTO `menu` VALUES (247, '避风塘炒虾', 79, '{\"1\": \"准备食材。\", \"2\": \"将基围虾去头、开背、去除虾线。\", \"3\": \"将处理好的虾放盐、姜丝、胡椒粉、料酒、干淀粉腌制10分钟。\", \"4\": \"将腌制好的虾挑出姜丝后，五成油温下锅炸至变色捞出，待油温升至九成热时再下锅复炸一遍。\", \"5\": \"起锅烧油，用小火炒香姜蒜末，然后放面包糠炒至变色，接着放虾、盐、糖翻炒均匀，撒上蒜花即可出锅装盘。\", \"6\": \"成品。\"}', '{\"基围虾\": \"500g\", \"蒜末\": \"100g\", \"面包糠\": \"200g\", \"蒜花\": \"10g\", \"姜末\": \"适量\"}', 7, '2020-02-27 22:27:28', 0, '2020-03-12 19:36:05', '515755');
INSERT INTO `menu` VALUES (248, '蒜泥白肉', 53, '{\"1\": \"五花肉。\", \"2\": \"冷水下锅，加少许米酒，盐煮熟(米酒去腥，盐令肉更紧实)。\", \"3\": \"五花肉煮至用筷子很轻松穿入即可。\", \"4\": \"取出，放凉或过冰水，或放冰箱冰一会。\", \"5\": \"三粒蒜头去皮。\", \"6\": \"用压蒜器压成泥。\", \"7\": \"如图，很方便的。\", \"8\": \"加入蚝油，酱油，糖，胡椒粉调匀，酱汁就好了。\", \"9\": \"五花肉放在熟食菜板上切薄片。\", \"10\": \"把五花肉摆入碟子中，随各人喜爱摆放。\", \"11\": \"把酱汁淋在五花肉表面，加点小米辣点缀下，就完成了。\", \"12\": \"成品图。\", \"13\": \"成品图。\"}', '{\"五花肉\": \"适量\", \"蒜头\": \"3粒\", \"蚝油\": \"少许\", \"酱油\": \"少许\", \"胡椒粉\": \"少许\", \"糖\": \"少许\", \"米酒\": \"少许\", \"盐\": \"少许\", \"小米辣\": \"少许\"}', 6, '2020-02-27 22:27:29', 0, '2020-03-12 19:36:05', '512792');
INSERT INTO `menu` VALUES (249, '简易煲仔饭', 91, '{\"1\": \"剩米饭盛入小砂锅内，上蒸笼加热。\", \"2\": \"豆腐切丁，放开水中，加少许盐，煮透捞出沥干水分，待用。\", \"3\": \"青椒、葱切段，牛肉切小丁，豆豉备好。\", \"4\": \"豆瓣酱备用。\", \"5\": \"热锅凉油。\", \"6\": \"先放牛肉丁煸干水分后下豆豉炒出香味。\", \"7\": \"放一勺豆瓣酱，炒出红油。\", \"8\": \"再放青椒、葱花、豆腐，加少许开水、老抽、味精翻炒。\", \"9\": \"将炒好的菜放到砂锅内的米饭上。\", \"10\": \"小火加热，使菜、米饭充分融合。\", \"11\": \"成品。\"}', '{\"剩米饭\": \"300克\", \"豆腐\": \"150克\", \"青椒\": \"1个\", \"牛肉\": \"50克\", \"豆瓣酱\": \"1勺\", \"豆豉\": \"少许\", \"葱花\": \"少许\", \"老抽\": \"少许\", \"味精\": \"少许\"}', 7, '2020-02-27 22:27:29', 0, '2020-03-12 19:36:05', '514646');
INSERT INTO `menu` VALUES (250, '酸萝卜老鸭汤', 99, '{\"1\": \"鸭边腿清洗干净。\", \"2\": \"白萝卜，鸭腿切块。\", \"3\": \"鸭腿放清水中煮开。\", \"4\": \"去除浮沫。\", \"5\": \"转入砂锅中，放酸萝卜和调料包煮1小时，然后放白萝卜再炖20分钟。\"}', '{\"酸萝卜老鸭汤调料\": \"1袋\", \"鸭边腿\": \"2个\", \"白萝卜\": \"半个\"}', 6, '2020-02-27 22:27:30', 0, '2020-03-12 19:36:05', '512207');
INSERT INTO `menu` VALUES (251, '快手肠粉', 49, '{\"1\": \"准备1袋肠粉预拌粉。\", \"2\": \"按说明，取100g的预拌粉加250g的清水，调制均匀。\", \"3\": \"火腿肠切小丁，切好葱花备用。\", \"4\": \"1汤匙的东古酱油+0.5匙开水+少许白糖+少许芝麻油，调成料汁备用。\", \"5\": \"盘中刷上一层薄薄的芝麻油，方便脱模，做好摆放时也能因为有油不会互相黏连。\", \"6\": \"勺适合的预拌粉浆到摸过油的盘中，不停的摇晃，直至粉浆平铺整个盘中。\", \"7\": \"撒入香葱和火腿肠丁。直接放到已滚开的开水锅中，盖上盖，大火煮2分钟。\", \"8\": \"鼓起大泡就好了，取出后，用刮刀边铲边卷成筒状。\", \"9\": \"对半切后装盘，如此循环直至全部做完。\", \"10\": \"将调制好的酱油汁微波15秒后淋在肠粉上即可。\"}', '{\"肠粉预拌粉\": \"100g\", \"清水\": \"250g\", \"火腿肠\": \"1根\", \"香葱\": \"适量\", \"东古酱油\": \"1匙\", \"温开水\": \"0.5匙\", \"白糖\": \"少许\", \"芝麻油\": \"少许\"}', 7, '2020-02-27 22:27:30', 0, '2020-03-12 19:36:05', '514189');
INSERT INTO `menu` VALUES (252, '四川 蒜苗回锅肉', 53, '{\"1\": \"豆瓣酱准备。\", \"2\": \"肉切片，姜蒜切好，蒜苗切好。\", \"3\": \"起锅放油，下入猪肉爆出油出来。\", \"4\": \"有点金黄色就可以了。\", \"5\": \"打出来装好。\", \"6\": \"下姜蒜爆炒。\", \"7\": \"下入豆瓣酱翻炒出红油。\", \"8\": \"然后加入蒜苗头。再加入肉。加老抽加盐翻炒。\", \"9\": \"一直翻炒。\", \"10\": \"加入叶子。翻炒。\", \"11\": \"断生就可以。\", \"12\": \"成品图。\"}', '{\"肉\": \"适量\", \"蒜苗\": \"适量\", \"姜\": \"适量\", \"豆瓣酱\": \"适量\", \"盐\": \"适量\", \"老抽\": \"适量\", \"蒜\": \"适量\"}', 6, '2020-02-27 22:27:30', 0, '2020-03-12 19:36:05', '512063');
INSERT INTO `menu` VALUES (253, '油焖大虾', 43, '{\"1\": \"食材洗净，备好。\", \"2\": \"用牙签穿入大虾背部第二关节，挑出虾线。\", \"3\": \"用料酒腌制10分钟。\", \"4\": \"蒜切片。\", \"5\": \"葱切丝。\", \"6\": \"姜切丝。\", \"7\": \"制作调料汁：碗中加入白砂糖、生抽、香醋、淀粉、半碗水，调匀。\", \"8\": \"将油烧至6成热，加入大虾翻炒。用锅铲轻压大虾头部，炒出红色虾油。\", \"9\": \"大虾变色后盛出备用，留底油。\", \"10\": \"加入切好的葱姜蒜翻炒出香味\", \"11\": \"加入调好的料汁搅匀。\", \"12\": \"加入大虾，加入少量料酒，盖盖焖至收汁，加少许盐出锅。\", \"13\": \"出锅，盛盘！\"}', '{\"大虾\": \"300克\", \"生姜\": \"适量\", \"大葱\": \"适量\", \"大蒜\": \"4瓣\", \"料酒\": \"5克\", \"白砂糖\": \"2克\", \"生抽\": \"15克\", \"香醋\": \"5克\", \"淀粉\": \"5克\", \"食用油\": \"适量\"}', 8, '2020-02-27 22:27:31', 0, '2020-03-12 19:36:05', '434047');
INSERT INTO `menu` VALUES (254, '腊味萝卜糕', 35, '{\"1\": \"大白萝卜一个。\", \"2\": \"腊肉腊肠，香菇，虾米提前泡发好。\", \"3\": \"粘米粉，澄粉。\", \"4\": \"白萝卜削皮，洗净后擦成丝备用。\", \"5\": \"把腊肉腊肠，香菇，虾米全切小丁，切点姜末。\", \"6\": \"热锅放油，把配料下去小米煸炒出香味。\", \"7\": \"如图，焦香焦香的，先铲出备用。\", \"8\": \"锅中有底油，下姜末煸一会。\", \"9\": \"下萝卜丝翻炒至软。\", \"10\": \"萝卜丝煮会出水，所以加一点点水煮。\", \"11\": \"大火煮开，煮至萝卜丝变透明即是熟了。\", \"12\": \"把刚才炒好的配料放入萝卜丝中，加少许盐，胡椒粉，五香粉，拌均匀，关火。\", \"13\": \"然后把粘米粉，澄粉放入锅中拌匀。\", \"14\": \"如图，刚刚好，如水份多了就加粉，如太干了加少许开水，酌量添加。\", \"15\": \"取一玻璃盒刷油，防粘，把面糊装入八九分满，抹平即可。\", \"16\": \"放入蒸锅，冷水起蒸45-60分钟。\", \"17\": \"蒸好的萝卜糕用牙签插一下提起不粘即为熟了。\", \"18\": \"蒸好成品，可直接吃。\", \"19\": \"放凉后切块煎至两面金黄又另一番美味。\", \"20\": \"金黄香脆，口齿留香。\", \"21\": \"成品图。\", \"22\": \"成品图。\"}', '{\"白萝卜\": \"1个约900克\", \"粘米粉\": \"300克\", \"澄粉\": \"50克\", \"腊肉\": \"适量\", \"腊肠\": \"适量\", \"香菇\": \"适量\", \"虾米\": \"适量\", \"姜末\": \"少许\", \"油\": \"适量\", \"盐\": \"少许\", \"胡椒粉\": \"适量\", \"五香粉\": \"适量\"}', 7, '2020-02-27 22:27:31', 0, '2020-03-12 19:36:05', '514089');
INSERT INTO `menu` VALUES (255, '自制麻辣火锅', 13, '{\"1\": \"各种配菜洗净备用。\", \"2\": \"火锅底料放入锅中化开。\", \"3\": \"加入半锅水烧开后，先加入不容易熟的火锅粉和土豆片，然后依次放入各种配料煮5分钟左右。\", \"4\": \"摆放完毕，开始煮5分钟。\", \"5\": \"出锅，配上饮料和牛奶，慢慢享受火锅的美味吧！\"}', '{\"火锅底料\": \"250g\", \"金针菇\": \"200g\", \"火锅肠\": \"50g\", \"火锅粉\": \"100g\", \"火锅肉丸子\": \"500g\", \"虾饺\": \"100g\", \"火锅笋\": \"100g\", \"香油\": \"20g\", \"土豆\": \"1个\"}', 6, '2020-02-27 22:27:32', 0, '2020-03-12 19:36:05', '511672');
INSERT INTO `menu` VALUES (256, '溜肝尖', 52, '{\"1\": \"食材洗净准备。\", \"2\": \"黄瓜切片。\", \"3\": \"猪肝切片。\", \"4\": \"反复冲洗数次，浸泡水中，加白醋，泡三十分钟以上。\", \"5\": \"洗净的猪肝放入碗中，加盐、生粉，腌制。\", \"6\": \"制作调料汁：加生抽、老抽、料酒、糖、白胡椒、水淀粉、香油，调匀。\", \"7\": \"油热，放肝片，翻炒变色，盛出。\", \"8\": \"放入葱姜蒜。\", \"9\": \"放入黄瓜、木耳，翻炒。\", \"10\": \"放肝片，倒入料汁。\", \"11\": \"翻炒均匀。\", \"12\": \"出锅，盛盘。\"}', '{\"猪肝\": \"1块\", \"黄瓜\": \"1根\", \"木耳\": \"30克\", \"葱末\": \"30克\", \"姜末\": \"30克\", \"蒜末\": \"10克\", \"白醋\": \"3汤匙\", \"白胡椒粉\": \"3克\", \"水淀粉\": \"15克\", \"香油\": \"1克\", \"盐\": \"适量\", \"食用油\": \"适量\"}', 8, '2020-02-27 22:27:32', 0, '2020-03-12 19:36:05', '429008');
INSERT INTO `menu` VALUES (257, '腊味煲仔饭', 28, '{\"1\": \"五常米洗两遍，泡半小时-1小时。\", \"2\": \"腊肉腊肠放入水中焯下水。\", \"3\": \"捞出清洗下，切薄片备用(用不了这么多，实际用了不到一半)。\", \"4\": \"瓦煲洗净擦干水份，放少许油涂抹内壁。\", \"5\": \"把泡好的米放入。\", \"6\": \"加入开水，米和水比例1:1.5。\", \"7\": \"如图。\", \"8\": \"盖上盖子大火煮开，再转最小火，将米饭煮至表面没水份。\", \"9\": \"如图，这样的米饭八成熟。\", \"10\": \"把腊肉腊肠摆入饭表面马上盖回盖子继续小火焖煮，大约10分左右，关火，不要打开盖子，香味会保留在瓦煲里。\", \"11\": \"调少许酱汁:淡味生抽，花生油拌匀。\", \"12\": \"10分钟后打开盖子，哇，好香，饭米晶莹粒粒分明。\", \"13\": \"把酱汁用勺子沿锅也淋入(这样最后的锅巴香脆好美味)。\", \"14\": \"撒点葱花，完成了，美味腊味煲仔饭请你品尝。\", \"15\": \"成品图。\", \"16\": \"成品图。\"}', '{\"五常大米\": \"2杯\", \"腊肉\": \"适量\", \"腊肠\": \"适量\", \"油\": \"少许\", \"淡生抽\": \"少许\", \"葱花\": \"少许\"}', 7, '2020-02-27 22:27:32', 0, '2020-03-12 19:36:05', '513520');
INSERT INTO `menu` VALUES (258, '鱼香茄子', 55, '{\"1\": \"茄子切成段。\", \"2\": \"姜葱切末。\", \"3\": \"锅中放入适量油烧热后放入姜葱蒜爆香。\", \"4\": \"放入肉末煸炒。\", \"5\": \"放入茄子翻炒至断生变色。\", \"6\": \"调料加入适量水调匀。\", \"7\": \"放入煸炒出香味的茄子中加入适量水大火烧开即可。\", \"8\": \"成品装盘。\"}', '{\"茄子\": \"适量\", \"肉末\": \"适量\", \"鱼香茄子调料\": \"适量\", \"油\": \"适量\", \"姜葱\": \"适量\", \"蒜茸\": \"适量\", \"盐\": \"适量\"}', 6, '2020-02-27 22:27:33', 0, '2020-03-12 19:36:05', '511468');
INSERT INTO `menu` VALUES (259, '美味南煎丸子', 21, '{\"1\": \"备食材。\", \"2\": \" 莲菜剁碎。\", \"3\": \"肉馅中放葱、姜末、鸡蛋、莲菜、盐。\", \"4\": \"调入黄酒5克、酱油5克。\", \"5\": \"顺同一方向搅拌均匀，但不宜搅拌太久否则口感不够松散。\", \"6\": \"煎锅入少量油烧至六成热，挤出丸子。\", \"7\": \"用小勺舀入锅中。\", \"8\": \"丸子全部入锅，中小火煎制。\", \"9\": \"煎至焦黄色。\", \"10\": \"另起一锅，烧热，倒入适量水，水开后放入15克酱油、5克黄酒、1克醋。\", \"11\": \"2克盐、5克白糖、少许胡椒粉和香油烧开。\", \"12\": \"关火，放入葱段姜片、大料、干辣椒。\", \"13\": \"倒入煎好的丸子上（量以没过丸子为准）。\", \"14\": \"入开水锅加盖蒸50分钟（如果用高压锅20分钟即可。\", \"15\": \"蒸好的丸子。\", \"16\": \"挑出葱段姜片、大料、干辣椒，再将碗中的汤汁倒入炒锅烧开。\", \"17\": \"装盘。\"}', '{\"五花肉\": \"450克\", \"莲藕\": \"适量\", \"鸡蛋\": \"1个\", \"黄酒\": \"10克\", \"酱油\": \"29克\", \"大料\": \"2个\", \"醋\": \"1克\", \"白糖\": \"5克\", \"干辣椒\": \"一个\", \"香油\": \"适量\", \"盐\": \"适量\", \"胡椒粉\": \"适量\", \"葱末\": \"适量\", \"姜末\": \"适量\"}', 8, '2020-02-27 22:27:33', 0, '2020-03-12 19:36:05', '427612');
INSERT INTO `menu` VALUES (260, '鸡蛋肠粉', 83, '{\"1\": \"所需材料。\", \"2\": \"将葱洗净切碎。\", \"3\": \"将所有粉类混合，加入水。\", \"4\": \"搅拌成无颗粒粉浆。\", \"5\": \"取出蒸肠粉平盘。抹上一层食用油防粘。（没有这种蒸盘也可用披萨盘或较大的不锈钢盘代替）\", \"6\": \"磕入一个鸡蛋打散。\", \"7\": \"打入大半勺粉浆摊均匀至整个蒸盘。\", \"8\": \"撒上葱花。\", \"9\": \"上锅入水烧开，放入鸡蛋粉浆蒸盘。\", \"10\": \"盖上锅盖，大火蒸两分钟左右。（蒸熟的肠粉打开锅盖时，会冒出大气泡的）\", \"11\": \"取出蒸盘。\", \"12\": \"用刮板铲出盛盘。\", \"13\": \"成品～淋入适量的蒸鱼豉油和少许香油即可食用。也可随意加入自己喜欢的各种辣椒酱料。\"}', '{\"粘米粉\": \"80克\", \"小麦淀粉\": \"50克\", \"玉米淀粉\": \"20克\", \"清水\": \"400克\", \"香葱\": \"3颗\", \"鸡蛋\": \"3个\", \"蒸鱼豉油\": \"适量\", \"香油\": \"少许\"}', 7, '2020-02-27 22:27:33', 0, '2020-03-12 19:36:05', '512984');
INSERT INTO `menu` VALUES (261, '鱼香鸡丝', 64, '{\"1\": \"准备好材料。\", \"2\": \"鸡腿去骨去皮切丝，加盐、黄酒、淀粉抓匀后腌制几分钟。\", \"3\": \"木耳切丝；山笋切丝。\", \"4\": \"青红椒切丝。\", \"5\": \"备好葱姜蒜末；将剩下的黄酒、醋、糖、老抽搅拌均匀，做成碗汁备用。\", \"6\": \"锅烧热后倒入油，倒入鸡丝煸炒至肉丝变色后盛出（也可以不用盛出，个人喜欢把炒出的汁水去掉）。\", \"7\": \"再热油，倒入木耳笋丝炒一两分钟盛出。\", \"8\": \"锅洗净，重新倒入一点儿油，放入郫县豆瓣酱和剁椒碎，翻炒出红油和香味后，加入葱姜蒜末。\", \"9\": \"炒出香味后倒入鸡肉和蔬菜丝，倒入碗汁炒匀。\", \"10\": \"再加入椒丝炒匀即可出锅。\"}', '{\"鸡腿\": \"3只\", \"山笋\": \"60克\", \"泡发木耳\": \"50克\", \"青椒\": \"半个\", \"红椒\": \"半个\", \"葱\": \"2根\", \"姜\": \"1片\", \"蒜\": \"1瓣\", \"糖\": \"半茶匙\", \"干淀粉\": \"1茶匙\", \"黄酒\": \"1汤匙\", \"醋\": \"1汤匙\", \"老抽\": \"半茶匙\", \"郫县豆瓣酱\": \"1汤匙\", \"剁椒碎\": \"1汤匙\", \"油\": \"适量\"}', 6, '2020-02-27 22:27:33', 0, '2020-03-12 19:36:05', '511081');
INSERT INTO `menu` VALUES (262, '醋溜白菜', 59, '{\"1\": \"食材准备。\", \"2\": \"白菜中间切开，片片儿。\", \"3\": \"干辣椒剪段。\", \"4\": \"制作调料汁：放入生抽、白米醋、盐、白砂糖，搅匀。\", \"5\": \"油热放花椒，炒出香味，花椒捞出不用。\", \"6\": \"放入干辣椒段爆香。\", \"7\": \"放入白菜，翻炒至软。\", \"8\": \"倒入料汁，炒一分钟。再淋水淀粉，淋香油即可。\"}', '{\"白菜\": \"400克\", \"干辣椒\": \"4克\", \"花椒\": \"10粒\", \"香油\": \"适量\", \"淀粉\": \"10克\", \"白米醋\": \"20克\", \"生抽\": \"15克\", \"白砂糖\": \"15克\", \"盐\": \"适量\", \"食用油\": \"适量\"}', 8, '2020-02-27 22:27:34', 0, '2020-03-12 19:36:05', '425966');
INSERT INTO `menu` VALUES (263, '纯红豆钵仔糕', 50, '{\"1\": \"准备食材。\", \"2\": \"粉加入温水搅拌均匀。\", \"3\": \"把红豆馅拌入粉浆。\", \"4\": \"用油把模具刷一遍。\", \"5\": \"粉浆搅拌一下，装模。\", \"6\": \"上锅蒸熟即可。\"}', '{\"木薯粉\": \"25g\", \"澄面\": \"5g\", \"红豆馅\": \"120g\", \"温水\": \"60g\", \"植物油\": \"适量\"}', 7, '2020-02-27 22:27:34', 0, '2020-03-12 19:36:05', '512890');
INSERT INTO `menu` VALUES (264, '回锅肉', 69, '{\"1\": \"五花肉冷水入锅，加入葱段，姜片，八角，花椒粒和黄酒，烧开转小火煮40分钟。\", \"2\": \"取出切片。\", \"3\": \"姜切片，干红辣椒切段，青椒去蒂去籽切片，青蒜切段。\", \"4\": \"锅中加入植物油，放入肉片翻炒出油后拨到一边。\", \"5\": \"放入剁碎的郫县豆瓣酱和甜面酱炒出红油，再同肉片炒匀。\", \"6\": \"加入白糖，鸡汁，放入青椒和青蒜茎翻炒断生后放入青蒜叶，炒匀即可。\"}', '{\"五花肉\": \"400g\", \"大葱\": \"1段\", \"姜\": \"1块\", \"八角\": \"1个\", \"花椒粒\": \"1匙\", \"黄酒\": \"1勺\", \"干红辣椒\": \"3根\", \"青椒\": \"1个\", \"青蒜\": \"3根\", \"植物油\": \"1勺\", \"郫县豆瓣酱\": \"1勺\", \"甜面酱\": \"1勺\", \"白糖\": \"1匙\", \"鸡汁\": \"1匙\"}', 6, '2020-02-27 22:27:35', 0, '2020-03-12 19:36:05', '511077');
INSERT INTO `menu` VALUES (265, '宫保鸡丁', 80, '{\"1\": \"食材：鸡腿、花生等。\", \"2\": \"辣椒面放锅里，另起锅，烧油七成热，取一半一勺一勺浇在辣椒面上，边浇边搅拌。\", \"3\": \"留一半热油凉至五成热，浇在辣椒面上。加紫草，搅拌均匀，小火慢熬10分钟，关火，过夜即可。\", \"4\": \"花生倒入开水里，泡发后捞出，去皮。\", \"5\": \"起油锅，小火炒到微微黄，盛出。\", \"6\": \"鸡腿去骨、切丁，放入加饭酒、盐、蛋清拌匀，加淀粉，拌匀。\", \"7\": \"制作料汁：放入加饭酒、盐、糖、白醋、酱油、淀粉，拌匀即可。\", \"8\": \"取适量过夜红油，小火加热后，放入花椒和辣椒，炒到棕红色。\", \"9\": \"下鸡丁，转大火，略炒。立刻放入姜片、蒜，炒半分钟，淋入料汁，快速翻炒。\", \"10\": \"放葱段、花生，翻炒至酱汁完全包裹。\", \"11\": \"放少许花椒油，翻炒即可。\", \"12\": \"盛出，开饭！\"}', '{\"鸡腿\": \"400克\", \"花生\": \"50克\", \"花椒\": \"2克\", \"干辣椒\": \"10克\", \"粗辣椒面\": \"25克\", \"紫草\": \"2克\", \"生姜\": \"适量\", \"蒜\": \"适量\", \"大葱\": \"适量\", \"花椒油\": \"适量\", \"加饭酒\": \"10克\", \"盐\": \"3克\", \"蛋清\": \"半个\", \"淀粉\": \"适量\", \"白砂糖\": \"30克\", \"白米醋\": \"35克\", \"酱油\": \"20克\"}', 8, '2020-02-27 22:27:35', 0, '2020-03-12 19:36:05', '416568');
INSERT INTO `menu` VALUES (266, '凤尾滑肉汤', 100, '{\"1\": \"瘦肉切片后，加入盐、胡椒粉、料酒和豆瓣酱搅拌均匀，码味半小时，让肉充分入味。\", \"2\": \"肉碗里加入两勺水，放入红薯粉搅拌均匀，一定要让红薯粉裹满肉片，成为一块包着肉片的粉团，然后一团一团的用筷子挑入烧开的水中。尽量不要让一团一团的肉粉团挨在一起哦！\", \"3\": \"盖上锅盖焖煮2分钟，等肉粉团浮上来以后，就加入凤尾在水里烫一会儿即可出锅。\", \"4\": \"出锅。\"}', '{\"瘦肉\": \"50g\", \"凤尾\": \"20g\", \"红薯粉\": \"50g\", \"黄瓜片\": \"5片\", \"姜粉\": \"2g\", \"豆瓣酱\": \"1勺\", \"胡椒粉\": \"2g\", \"料酒\": \"1勺\"}', 6, '2020-02-27 22:27:36', 0, '2020-03-12 19:36:05', '511073');
INSERT INTO `menu` VALUES (267, '醋溜土豆丝', 79, '{\"1\": \"食材：土豆、大葱、干辣椒、蒜等。\", \"2\": \"土豆切丝，泡冷水中5分钟，反复冲洗至水清。\", \"3\": \"蒜切末，辣椒剪段，葱花切好，备用。\", \"4\": \"锅里水烧开，放入土豆丝，15秒后捞出控水。\", \"5\": \"热锅冷油，放花椒和干辣椒爆香。\", \"6\": \"放入土豆丝、葱花、蒜末，翻炒。\", \"7\": \"放盐、白醋，炒匀即可。\"}', '{\"土豆\": \"1个\", \"干辣椒\": \"5个\", \"大葱\": \"1段\", \"蒜\": \"3瓣\", \"花椒\": \"3克\", \"白醋\": \"15克\", \"盐\": \"适量\"}', 8, '2020-02-27 22:27:37', 0, '2020-03-12 19:36:05', '416305');
INSERT INTO `menu` VALUES (268, '豆豉回锅肉炒青椒', 3, '{\"1\": \"五花肉把肥的部分切下来，用水把肉煮熟。瘦的部分切成肉丝。\", \"2\": \"肥肉煮好以后，切成片在锅里干煎，熬出油来。\", \"3\": \"肥肉熬出油以后，放入瘦肉片翻炒，加入几粒花椒增香，放入一勺豆豉翻炒均匀。\", \"4\": \"倒入码好盐味的青椒丝翻炒均匀，青椒炒熟后，加入一勺酱油翻炒即可出锅。\", \"5\": \"出锅。\"}', '{\"五花肉\": \"200g\", \"大青椒\": \"4个\", \"豆豉\": \"1勺\", \"酱油\": \"1勺\"}', 6, '2020-02-27 22:27:37', 0, '2020-03-12 19:36:05', '511068');
INSERT INTO `menu` VALUES (269, '韭菜水焖蛋', 19, '{\"1\": \"韭菜切成小段。\", \"2\": \"韭菜放入大碗中，打入三个鸡蛋。\", \"3\": \"加入一勺盐，一勺鸡精，白胡椒粉，搅拌均匀。\", \"4\": \"起锅倒油，油温五成热下锅煎。\", \"5\": \"煎完翻身煎另外一面，煎至两面成型即可。\", \"6\": \"铲刀切成小块。\", \"7\": \"加入一大碗水，大火烧开。\", \"8\": \"加入少许盐,鸡精调味。\", \"9\": \"出锅，成品图。\"}', '{\"韭菜\": \"一小把\", \"鸡蛋\": \"3个\", \"色拉油\": \"适量\", \"盐\": \"少许\", \"鸡精\": \"少许\", \"白胡椒粉\": \"少许\"}', 8, '2020-02-27 22:27:38', 0, '2020-03-12 19:36:05', '415697');
INSERT INTO `menu` VALUES (270, '卷粉', 16, '{\"1\": \"打好的米浆。\", \"2\": \"上锅蒸3分钟。\", \"3\": \"卷好。\"}', '{\"大米\": \"两杯米\", \"鸡蛋\": \"2个\", \"肉末\": \"200g\", \"玉米粒\": \"适量\"}', 7, '2020-02-27 22:27:38', 0, '2020-03-12 19:36:05', '512722');
INSERT INTO `menu` VALUES (271, '辣子鸡', 98, '{\"1\": \"将鸡腿和鸡胸上的肉剔下，切成大小适中的块，用水清洗一遍，加入料酒1匙、生抽2匙、少许胡椒粉和盐抓匀腌制2个小时以上。\", \"2\": \"放入淀粉再次抓匀。\", \"3\": \"锅内油烧至七成热，放入鸡块小火炸3分钟。\", \"4\": \"再用大火复炸至金黄，控油捞出。\", \"5\": \"辣椒剪断，姜切丝，蒜拍松切片，葱切碎备用。\", \"6\": \"锅底留少许食用油，放入葱姜蒜和花椒，小火爆香。\", \"7\": \"放入辣椒断煸炒一下。\", \"8\": \"倒入鸡块，加入生抽、白糖翻炒2分钟。\", \"9\": \"撒入白芝麻翻炒一下。\", \"10\": \"撒上葱花出锅。\"}', '{\"鸡腿\": \"2个\", \"鸡胸\": \"1个\", \"辣椒\": \"适量\", \"花椒\": \"1匙\", \"葱\": \"1段\", \"姜\": \"3片\", \"蒜\": \"2瓣\", \"白芝麻\": \"1匙\", \"白糖\": \"1匙\", \"淀粉\": \"2匙\", \"料酒\": \"1匙\", \"生抽\": \"3匙\", \"盐\": \"适量\", \"胡椒粉\": \"少许\", \"食用油\": \"适量\"}', 6, '2020-02-27 22:27:38', 0, '2020-03-12 19:36:05', '509426');
INSERT INTO `menu` VALUES (272, '#我最喜爱的家常菜#～～油焖大虾', 21, '{\"1\": \"准备食材。香葱切段，姜蒜切碎。\", \"2\": \"用剪刀将虾腿儿、须剪掉，在虾的背部开口，挑去黑色虾线，头部与身连接处的脏东西也挤出去，将虾洗干净用厨房用纸擦干。\", \"3\": \"将虾清洗干净。\", \"4\": \"锅内放比平时多一点的油，油温六七成热时放入虾，稍大火将虾煎至二面酥壳呈金红色，边煎边用铲挤压虾头，把虾脑里的虾油挤出来。\", \"5\": \"将煎好的虾先盛出来控下油。\", \"6\": \"重新留少许底油，放入姜蒜末,小火煸出香味。\", \"7\": \"放入3小勺番茄酱炒出红油。\", \"8\": \"放入煎好的虾烧上色，顺着锅边烹入2勺黄酒、2勺蚝油、l勺海鲜酱油，3小勺白糖、少许胡椒粉，少许盐烧入味。\", \"9\": \"加半汤勺温水，盖上盖小火焖一分多钟。\", \"10\": \"打开盖，转大火收汁，汁收差不多关火，撒上香葱段即可。\", \"11\": \"盛盘。\"}', '{\"鲜虾\": \"10只\", \"盐\": \"少许\", \"香葱\": \"2根\", \"蒜\": \"10克\", \"姜\": \"5克\", \"蚝油\": \"2小勺\", \"海鲜酱油\": \"l勺\", \"白糖\": \"3小勺\", \"黄酒\": \"20克\", \"白胡椒粉\": \"3克\", \"番茄酱\": \"3小勺\"}', 8, '2020-02-27 22:27:39', 0, '2020-03-12 19:36:05', '414273');
INSERT INTO `menu` VALUES (273, '腊味萝卜糕', 14, '{\"1\": \"萝卜削皮，切丝。\", \"2\": \"虾米和香菇瑶柱泡10分钟左右，切丁。\", \"3\": \"配料下锅炒，加盐一勺，鸡精半勺。\", \"4\": \"萝卜下锅炒软，会出水。\", \"5\": \"粘米粉和澄粉混合均匀，加入适量水，调成这种流线状。\", \"6\": \"把配料倒进萝卜丝里，再放一勺胡椒粉，两勺盐，翻炒一会，倒入调好的粉浆搅拌。\", \"7\": \"装盘隔水蒸45分钟左右，用筷子插进去如果没有膏体粘着就是熟了。\", \"8\": \"出锅成品。\", \"9\": \"个人觉得香煎会更好吃，出锅把蒸的萝卜糕放冰箱两小时放凉，会比较好切开，然后放进锅里小火煎至两面金黄，不用放调味料。完工。\"}', '{\"萝卜\": \"1颗\", \"虾米\": \"适量\", \"腊肠\": \"3条\", \"瑶柱\": \"20颗\", \"香菇\": \"6个\", \"粘米粉\": \"300g\", \"澄粉\": \"50g\", \"清水\": \"一碗\"}', 7, '2020-02-27 22:27:39', 0, '2020-03-12 19:36:05', '512675');
INSERT INTO `menu` VALUES (274, '怪味鸡', 99, '{\"1\": \"主要食材，三黄鸡，姜片、葱段、栀子。\", \"2\": \"黄鸡去除全部内脏，剪去指甲，切去鸡屁股冲洗干净。\", \"3\": \"凉水放入锅中，加入姜片、葱段、2个栀子烧沸后改最小火煮15分钟左右。\", \"4\": \"用筷子扎一下没有血水冒出即可。\", \"5\": \"煮好的三黄鸡取出稍稍晾凉。\", \"6\": \"调味料大集合。\", \"7\": \"芝麻酱中加入香油、红油充分调匀，搅拌到顺滑。\", \"8\": \"再依次加入剩余的调料。\", \"9\": \"大蒜、香葱、姜分别切末。\", \"10\": \"切好的葱姜蒜末也放入调料碗中拌匀。\", \"11\": \"稍凉的鸡斩块。\", \"12\": \"码入盘中上，淋调好的怪味汁，最后把熟芝麻仁和花生碎撒在上边即可。\"}', '{\"三黄鸡\": \"半只\", \"葱段\": \"10克\", \"姜片\": \"5片\", \"栀子\": \"2个\", \"料酒\": \"20克\", \"花生碎\": \"1茶匙\", \"熟芝麻\": \"1/2茶匙\", \"芝麻酱\": \"1茶匙\", \"辣椒油\": \"2茶匙\", \"香油\": \"1茶匙\", \"生抽\": \"2茶匙\", \"陈醋\": \"2茶匙\", \"白糖\": \"1茶匙\", \"花椒粉\": \"1/3茶匙\", \"香葱\": \"1克\", \"大蒜\": \"3瓣\", \"姜\": \"5克\"}', 6, '2020-02-27 22:27:39', 0, '2020-03-12 19:36:05', '507895');
INSERT INTO `menu` VALUES (275, '香煎萝卜糕', 98, '{\"1\": \"准备食材。\", \"2\": \"热锅下油。\", \"3\": \"直接放入萝卜糕。\", \"4\": \"两面煎至金黄。\", \"5\": \"盖上锅盖焖一会即可。\"}', '{\"速冻萝卜糕\": \"6片\", \"油\": \"适量\"}', 7, '2020-02-27 22:27:40', 0, '2020-03-12 19:36:05', '512310');
INSERT INTO `menu` VALUES (276, '虾【油焖大虾】', 36, '{}', '{\"对虾\": \"适量\", \"葱\": \"适量\", \"姜\": \"适量\", \"生抽\": \"适量\", \"胡椒粉\": \"适量\", \"白糖\": \"适量\", \"料酒\": \"适量\"}', 8, '2020-02-27 22:27:40', 0, '2020-03-12 19:36:05', '411832');
INSERT INTO `menu` VALUES (277, '辣炒鸡丁', 69, '{\"1\": \"鸡腿肉切丁，加入生抽、料酒、盐各1匙，搅拌均匀，放入冰箱腌制2小时。\", \"2\": \"锅内放入食用油，油烧热至八成热，放入鸡丁炸至金黄。\", \"3\": \"捞出控油备用。\", \"4\": \"辣椒剪成小段去除辣椒籽，姜切丝，葱切碎。\", \"5\": \"锅内留少许底油，加入葱、姜、花椒、八角、辣椒爆香。\", \"6\": \"倒入鸡丁翻炒片刻，加入少许生抽、白糖继续翻炒几下出锅。\"}', '{\"鸡腿肉\": \"适量\", \"辣椒\": \"适量\", \"花椒\": \"适量\", \"八角\": \"适量\", \"葱、姜\": \"适量\", \"食用油\": \"适量\", \"生抽、料酒、盐\": \"适量\", \"白糖\": \"少许\"}', 6, '2020-02-27 22:27:41', 0, '2020-03-12 19:36:05', '508571');
INSERT INTO `menu` VALUES (278, '油焖大虾', 53, '{\"1\": \"花一点点心思一盘美食。\", \"2\": \"食材准备好。\", \"3\": \"买的鲜活大虾，将带壳大虾剪去虾须处理干净。\", \"4\": \"虾头往下数第二节用牙签挑出虾线。\", \"5\": \"用厨房用纸吸干水分。\", \"6\": \"植物油放入姜丝、葱段小火煸香，油稍微多放一点，虾子是很吃油的。\", \"7\": \"香味出来了倒入虾转大火翻炒。\", \"8\": \"两面都炸至金黄。\", \"9\": \"翻炒到虾变色，虾红油炒出来了，香气四溢，这时放入料酒。炒出虾红油是这道菜漂亮以及味美的关键。\", \"10\": \"加适量凉水，加入糖，辣椒粉，混合均匀，因为糖加的量合适，所以最后不需要勾芡，汤汁也会妥妥收得好，且红红亮亮。（我是湖南人喜欢吃辣，所以添加了辣椒粉，吃不了辣的可以不放哦）。\", \"11\": \"这时汤汁会烧滚，加盐调味，盖锅盖焖一分钟。\", \"12\": \"烹少许香醋，大火收汁即可（只须一点点去腥增香，多了就变成糖醋虾了）。\", \"13\": \"用绰水的小白菜点缀。\", \"14\": \"香喷喷。\", \"15\": \"成品。\"}', '{\"大虾\": \"200g\", \"辣椒粉\": \"2g\", \"小白菜\": \"3颗\", \"植物油\": \"20g\", \"盐\": \"3g\", \"葱\": \"3颗\", \"姜\": \"3块\", \"料酒\": \"2勺\"}', 8, '2020-02-27 22:27:42', 0, '2020-03-12 19:36:05', '401044');
INSERT INTO `menu` VALUES (279, '脆皮乳鸽（传统）', 48, '{\"1\": \"先爆香（蒜头，姜，干辣椒，花椒，草果，香叶，桂皮）注：爆炒香料前，先把鸽子过一下温水，去血腥。\", \"2\": \"爆香后加入适量的开水及卤水包，待水开后。\", \"3\": \"加入适量的蚝油，生抽，老抽，鸡精，味精，盐，冰糖，待煮开。\", \"4\": \"水开后，放入鸽子，大火卤8-10分钟，然后转小火卤20分钟，关火，待浸泡半小时至一小时（待用）。\", \"5\": \"鸽子用脆皮水浸泡半小时待用（脆皮水做法：麦牙糖，米醋，开水）做出来后皮会脆（也可以不用这一步）。\", \"6\": \"调制秘汁：南乳3-5块，生抽（适量）老抽（少许）蚝油少许。\", \"7\": \"涂抹在鸽子表面和肚子里，腌制半小时待用。\", \"8\": \"烧油：锅里倒入适量的油，放入葱，洋葱等，炸香后捞出不用。\", \"9\": \"待油温发出吱吱响后（100度以上）把鸽子先全方位炸几分钟后，捞起，待两三分钟后，又炸（这样的步骤反复两三次）。\", \"10\": \"反复几次，炸出来的乳鸽，颜色非常漂亮，金黄色。\", \"11\": \"炸成这样，再过一次油就非常漂亮了。\", \"12\": \"成品。\"}', '{\"乳鸽\": \"3只\", \"猪皮\": \"一小块\", \"南乳\": \"3-5块\", \"盐\": \"少许\", \"鸡精\": \"少许\", \"味精\": \"少许（可不用）\", \"卤水包\": \"1-2包\", \"干辣椒\": \"适量（一小把）\", \"草果\": \"1颗\", \"红花椒\": \"一小把\", \"蒜\": \"2根\", \"姜片\": \"适量\", \"蒜头\": \"6-8颗\", \"香叶\": \"2片\", \"冰糖\": \"适量\", \"白米酒\": \"适量\", \"蚝油\": \"适量\", \"生抽\": \"适量\", \"老抽\": \"少许\", \"桂皮\": \"一小块\"}', 7, '2020-02-27 22:27:42', 0, '2020-03-12 19:36:05', '511287');
INSERT INTO `menu` VALUES (280, '简单快手的辣子鸡', 12, '{\"1\": \"所有食材都准备好，买回来的鸡清洗干净用刀剁成2～3厘米的鸡块备用。\", \"2\": \"剁好的鸡块放入锅内倒入适量水水煮开在煮2～3分钟后捞出控干水分放入盘中备用。\", \"3\": \"蒜去皮葱姜洗净分别和蒜切成葱末和蒜片和姜片备用。\", \"4\": \"取2个小米辣洗净切成末备用。\", \"5\": \"锅内倒入适量油烧热分别倒入切好的葱姜蒜小米辣炒出香味后倒入适量的花椒再次炒出香味。\", \"6\": \"倒入焯好的鸡块翻炒均匀后倒入1匙生抽，半匙老抽，1匙蚝油翻炒均匀后在倒入1匙料酒翻炒均匀。\", \"7\": \"炒好的鸡倒入1瓶啤酒中小火盖上锅盖煮开在煮片刻后倒入2匙食用盐翻炒均匀盖上锅盖继续炖煮。\", \"8\": \"青红菜椒提前放到盆中倒入适量水和适量食用盐浸泡15~20分钟后清洗干净用刀切块备用。\", \"9\": \"等水煮的差不多的时候，转大火倒入切好的菜椒大火炒至菜椒断生关火盛出即可。\"}', '{\"三黄鸡\": \"1只\", \"青菜椒\": \"1个\", \"红彩椒\": \"1个\", \"啤酒\": \"1瓶\", \"花莲\": \"适量\", \"料酒\": \"1匙\", \"生抽\": \"1匙\", \"蚝油\": \"1匙\", \"小米辣\": \"2个\", \"盐\": \"2匙\", \"蒜瓣\": \"4瓣\"}', 6, '2020-02-27 22:27:42', 0, '2020-03-12 19:36:05', '508477');
INSERT INTO `menu` VALUES (281, '麻辣香锅', 63, '{}', '{\"豆腐\": \"1块\", \"虾\": \"5只\", \"土豆\": \"5颗\", \"鸡\": \"1只\", \"白菜\": \"1颗\", \"蘑菇\": \"1颗\", \"牛猪肉片\": \"2ml\", \"肉素鱼丸子\": \"15颗\", \"火锅料\": \"1ml\", \"鸡汤\": \"1ml\"}', 6, '2020-02-27 22:27:44', 0, '2020-03-12 19:36:05', '508433');
INSERT INTO `menu` VALUES (282, '酱爆小鲍鱼', 73, '{\"1\": \"新鲜的小鲍鱼用水反复冲洗干净杂质后控干水分备用。葱、姜、蒜剥去外皮清洗干净，分别切成葱花、姜片、蒜片备好。\", \"2\": \"香菜一颗择去根和老叶子清洗干净后切成香菜段。喜欢吃辣的朋友可以放两个干红辣椒。\", \"3\": \"热锅加入适量的花生油放入葱花、姜片、蒜片爆香，改小火放入一小勺豆瓣酱炒香。\", \"4\": \"放入清洗干净的小鲍鱼转中大火，烹入适量的料酒翻炒均匀。\", \"5\": \"炒至食材六成熟时加入少许白胡椒粉和白糖继续翻炒至熟透。切记糖不要放的太多。\", \"6\": \"关火后加入香菜段搅拌均匀，根据自己的喜好点几滴香油调味儿。\"}', '{\"小鲍鱼\": \"适量\", \"葱\": \"适量\", \"姜\": \"适量\", \"蒜\": \"适量\", \"香菜\": \"适量\", \"花生油\": \"适量\", \"料酒\": \"适量\", \"豆瓣酱\": \"适量\", \"白糖\": \"适量\", \"白胡椒粉\": \"适量\"}', 8, '2020-02-27 22:27:44', 0, '2020-03-12 19:36:05', '396242');
INSERT INTO `menu` VALUES (283, '鸿运当头', 19, '{\"1\": \"蒜蓉辣酱备好。\", \"2\": \"粉丝用开水泡软待用。\", \"3\": \"扇贝肉冲洗干净，放少许盐、生抽抓匀待用。\", \"4\": \"扇贝壳洗净，用开水烫一下，摆入盘中。\", \"5\": \"一切准备就绪。\", \"6\": \"把粉丝卷成小卷，整齐摆放在扇贝壳上。\", \"7\": \"每个粉丝上摆放一个扇贝肉。\", \"8\": \"每一个扇贝肉上放一小勺蒜蓉辣酱，保证每一勺蒜蓉辣酱都带一点油。\", \"9\": \"依次淋上蒜蓉辣酱。\", \"10\": \"水开后放在笼屉上。\", \"11\": \"大水蒸8分钟。\", \"12\": \"时间到后，掀开笼盖，每个扇贝肉上都放一点香葱，再带笼盖30秒，目的是激发香葱的味道，并保证香葱不失翠绿。\", \"13\": \"成品颜色黄亮，香葱翠绿，香味浓郁。\"}', '{\"蒜蓉辣酱\": \"适量\", \"粉丝\": \"1把\", \"扇贝\": \"适量\", \"扇贝壳\": \"6个\", \"香葱碎\": \"少许\"}', 7, '2020-02-27 22:27:44', 0, '2020-03-12 19:36:05', '509513');
INSERT INTO `menu` VALUES (284, '鱼香肉丝', 36, '{\"1\": \"猪里脊切片。\", \"2\": \"切成均匀的肉丝。\", \"3\": \"木耳切丝，胡萝卜擦丝。\", \"4\": \"绿椒切斜丝。\", \"5\": \"肉丝内适量盐，料酒，生抽，生粉。\", \"6\": \"抓匀腌几分钟。\", \"7\": \"4勺陈醋，3勺白糖，2勺生抽，1勺生粉调成鱼香汁。\", \"8\": \"锅内适量油，开小火，肉丝放入。\", \"9\": \"用筷子快速滑至变色盛出。\", \"10\": \"锅内另放一点油，姜蒜放入炒香。\", \"11\": \"郫县豆瓣酱放入一勺炒出红油。\", \"12\": \"胡萝卜，木耳，青椒放入。\", \"13\": \"翻炒几下，一勺开水。\", \"14\": \"肉丝放入。\", \"15\": \"翻炒两下。\", \"16\": \"倒入料汁翻炒汤汁明亮关火盛盘。\", \"17\": \"成品。\", \"18\": \"成品。\"}', '{\"里脊肉\": \"适量\", \"胡萝卜丝\": \"适量\", \"木耳丝\": \"适量\", \"螺丝椒\": \"一个\", \"花生油\": \"适量\", \"陈醋\": \"4小勺\", \"白糖\": \"3小勺\", \"生抽\": \"2小勺\", \"生粉\": \"1小勺\", \"盐\": \"适量\", \"料酒\": \"适量\"}', 6, '2020-02-27 22:27:45', 0, '2020-03-12 19:36:05', '508244');
INSERT INTO `menu` VALUES (285, '锅塌豆腐', 50, '{\"1\": \"取北豆腐切块，用盐腌制一会，随后平铺于撒了干淀粉的盘子中。\", \"2\": \"铺好之后在表面在撒一层干粉，抹匀。接着打一只鸡蛋准备蛋液。\", \"3\": \"油锅烧热，将豆腐沾满蛋液，入油锅炸至金黄。捞出。\", \"4\": \"另起油锅，放入姜葱爆香放入炸好的豆腐，并倒入用蚝油，生抽、黑胡椒、糖调好的酱汁，和一小碗水，没过豆腐。待汤汁稍微收干，淋香油。\", \"5\": \"装盘，撒葱花。\"}', '{\"北豆腐\": \"1块\", \"鸡蛋\": \"1只\", \"水\": \"一小碗\", \"淀粉\": \"适量\", \"葱\": \"适量\", \"盐\": \"适量\", \"黑胡椒\": \"适量\", \"蚝油\": \"少许\", \"生抽\": \"适量\", \"白糖\": \"少许\", \"姜\": \"少许\"}', 8, '2020-02-27 22:27:45', 0, '2020-03-12 19:36:05', '389060');
INSERT INTO `menu` VALUES (286, '猪脚姜', 96, '{\"1\": \"提前3-5天准备适量老姜，切厚片，拍松纤维，便于入味。\", \"2\": \"将老姜晾干。或者用炒锅炒至半干再彻底晾干。\", \"3\": \"取适量干姜本次使用，其余装袋。\", \"4\": \"准备新鲜干净的猪蹄，洗净后换清水浸泡四小时以上。\", \"5\": \"浸泡出血水后，洗净备用。\", \"6\": \"煮锅中倒入大量清水，放入猪蹄，开火煮沸。\", \"7\": \"煮沸后捞出猪蹄。\", \"8\": \"用温水洗净猪蹄。如果喜欢口感脆一点的话，用冷水冲洗。\", \"9\": \"猪蹄沥干水分备用。\", \"10\": \"沥干水分的猪蹄。\", \"11\": \"另取一锅煮鸡蛋，冷水入锅，煮八分钟即可。鸡蛋煮好捞出后放入冷水中浸泡几分钟再小心剥皮备用。\", \"12\": \"准备添丁甜醋和黑米醋。\", \"13\": \"大号砂锅中倒入甜醋、黑米醋、红糖和干老姜。\", \"14\": \"大火烧开后，再转中火继续熬煮。\", \"15\": \"熬煮至浓稠。\", \"16\": \"加入鸡蛋和猪蹄，醋汁没过猪蹄，煮至猪皮变软烂即可。\", \"17\": \"成品味道独特，营养丰富。\"}', '{\"猪蹄\": \"3个\", \"老姜\": \"800g\", \"添丁甜醋\": \"1850ml\", \"黑糯米醋\": \"150ml\", \"鸡蛋\": \"5个\", \"红糖\": \"100g\"}', 7, '2020-02-27 22:27:46', 0, '2020-03-12 19:36:05', '502578');
INSERT INTO `menu` VALUES (287, '剁椒鱼头', 12, '{\"1\": \"鱼头洗净，放料酒、酱油、胡椒粉、姜片腌制半小时。\", \"2\": \"剁椒加蒜末、1小勺蚝油搅拌均匀备用。\", \"3\": \"大盘葱姜铺底。\", \"4\": \"鱼放上，均匀浇上拌好的剁椒末，蒸17分钟（今天的鱼头比较大，多蒸了会儿）。\", \"5\": \"蒸鱼期间准备一下蒜末，切小葱叶备用，闹钟还剩一分钟时起锅烧油。\", \"6\": \"关火浇热油，浇在葱花上面，滋啦滋啦。\", \"7\": \"上桌了。\", \"8\": \"香香滴。\"}', '{\"花鲢鱼头\": \"适量\", \"红剁椒\": \"适量\", \"葱\": \"适量\", \"姜\": \"适量\", \"蒜\": \"适量\", \"小葱\": \"适量\", \"料酒\": \"适量\", \"酱油\": \"适量\", \"胡椒粉\": \"适量\", \"蚝油\": \"适量\"}', 6, '2020-02-27 22:27:47', 0, '2020-03-12 19:36:05', '500612');
INSERT INTO `menu` VALUES (288, '卷三丝', 55, '{\"1\": \"所用食材洗净备用，辣椒酱就绪。\", \"2\": \"黄瓜改刀成丝。\", \"3\": \"胡萝卜改刀成丝。\", \"4\": \"葱白改刀成丝。\", \"5\": \"豆皮过开水即可取出，去除豆腥味（这一步也可忽略），铺开放入三种丝。\", \"6\": \"卷紧成条状。\", \"7\": \"打斜刀如图。\", \"8\": \"排盘即可。\", \"9\": \"依次做完，小清新哦。\", \"10\": \"来份辣黄豆酱佐料碗即可享用。\", \"11\": \"成品图。\"}', '{\"东北豆皮\": \"三张\", \"黄瓜\": \"半根\", \"胡萝卜\": \"半根\", \"大葱白\": \"一节\", \"辣黄豆酱\": \"适量\"}', 8, '2020-02-27 22:27:47', 0, '2020-03-12 19:36:05', '388789');
INSERT INTO `menu` VALUES (289, '清蒸鲈鱼', 35, '{\"1\": \"鲈鱼洗净。\", \"2\": \"切去头。\", \"3\": \"切段，一边连着。\", \"4\": \"用盐，料酒，葱姜抓捏入味，腌制5分钟。\", \"5\": \"摆成孔雀状，上面摆上葱姜，放点肥肉丁。\", \"6\": \"蒸锅烧开。\", \"7\": \"放入鱼盘蒸5分钟即可。\", \"8\": \"蒸鱼的过程中调酱油汁，锅里放水，放生抽、鱼露、老抽煮开，倒进一个小碗中。。然后另起锅将油烧开。\", \"9\": \"将蒸好的鱼端出蒸锅，倒掉汤，捡去葱姜，重新撒上小红椒圈和葱花，将热油浇在鱼的表面，将酱油汁倒在鱼头侧，清蒸鲈鱼就做好了。\"}', '{\"鲈鱼\": \"一条\", \"葱\": \"适量\", \"小葱\": \"一个\", \"尖椒\": \"一个\", \"肥肉丁\": \"少许\", \"姜\": \"适量\", \"盐\": \"少许\", \"生抽\": \"一勺\", \"老抽\": \"一勺\", \"鱼露\": \"半勺\"}', 7, '2020-02-27 22:27:47', 0, '2020-03-12 19:36:05', '509405');
INSERT INTO `menu` VALUES (290, '油焖大虾', 96, '{\"1\": \"虾去掉虾须、虾脚、虾线，清洗干净\", \"2\": \"锅里多加点油，下入虾\", \"3\": \"炸至金黄酥脆，捞出备用\", \"4\": \"另起锅入油烧热，爆香葱姜\", \"5\": \"加入炸过的虾\", \"6\": \"加1勺糖、1勺生抽、小半勺老抽、1勺料酒、少许鸡粉、适量盐\", \"7\": \"加热水，水量为虾的一半即可，烧开后尝尝味道，我做的是咸甜味的\", \"8\": \"中火焖至汤汁收干即可\", \"9\": \"成品。\"}', '{\"青虾\": \"14只\", \"生抽\": \"1大匙\", \"老抽\": \"小半勺\", \"糖\": \"1大匙\", \"鸡粉\": \"适量\", \"盐\": \"适量\", \"葱\": \"适量\", \"料酒\": \"1勺\", \"姜\": \"适量\"}', 8, '2020-02-27 22:27:48', 0, '2020-03-12 19:36:05', '382409');
INSERT INTO `menu` VALUES (291, '紫气东来---紫薯发糕', 7, '{\"1\": \"紫薯去皮切薄片。\", \"2\": \"蒸熟。\", \"3\": \"过筛压成泥。\", \"4\": \"水和糖煮溶化。\", \"5\": \"拌入100g紫薯泥，放凉。\", \"6\": \"面粉和泡打粉拌匀。\", \"7\": \"倒入放凉的紫薯糖水，拌匀。\", \"8\": \"倒入玉米油。\", \"9\": \"拌匀。\", \"10\": \"装入蛋糕纸杯，至少9分满。\", \"11\": \"放入开水锅。\", \"12\": \"大火蒸25分钟。\", \"13\": \"出锅放凉。\", \"14\": \"非常漂亮的开口笑发糕。\"}', '{\"普通面粉\": \"250g\", \"紫薯泥\": \"100g\", \"水\": \"150g\", \"白糖\": \"100g\", \"玉米油\": \"60ml(4大匙)\", \"泡打粉\": \"4茶匙\"}', 7, '2020-02-27 22:27:49', 0, '2020-03-12 19:36:05', '508964');
INSERT INTO `menu` VALUES (292, '蓑衣黄瓜', 96, '{\"1\": \"洗净的黄瓜两头去掉，把到横着垂直切，注意千万不能切断，都是连刀片。下面剩余大约5毫米的样子\", \"2\": \"一面切好后，把黄瓜180度翻身，然后把刀倾斜为45度，依然是切连刀片。\", \"3\": \"切好的黄瓜可以拉开但是不能断\", \"4\": \"把切好的黄瓜用盐腌制20分钟左右\", \"5\": \"腌制黄瓜的时候调一个料汁：苹果醋、辣椒油、香油调均匀。也可以用白醋代替苹果醋，或者用其他果醋都可以\", \"6\": \"腌制好的黄瓜，倒上料汁即可\", \"7\": \"装盘享用吧。\"}', '{\"黄瓜\": \"2根\", \"盐\": \"适量\", \"苹果醋\": \"适量\", \"香油\": \"少许\", \"辣椒油\": \"适量\"}', 8, '2020-02-27 22:27:50', 0, '2020-03-12 19:36:05', '381030');
INSERT INTO `menu` VALUES (293, '叉烧肉', 92, '{\"1\": \"准备食材：梅子肉800克、叉烧酱100克、黄酒2茶匙、蚝油、2茶匙、蜂蜜10克、姜片10片、葱段15克。\", \"2\": \"梅子肉切大块，泡去血水，加入（1）中所有调料。\", \"3\": \"加入所有调料后充分抓匀，腌渍6个小时以上。（腌渍过夜更入味）\", \"4\": \"电饭锅底部铺上香葱段，姜片。\", \"5\": \"放入腌渍好的梅子肉，尽量每块都要接触到锅底，不要叠压。\", \"6\": \"按电饭煲煮饭键。\", \"7\": \"第一次跳保温后，给肉翻个面，再次按煮饭键。\", \"8\": \"第二次跳保温就可以取出大快朵颐啦！\", \"9\": \"成品！\"}', '{\"梅子肉\": \"800克\", \"叉烧酱\": \"100克\", \"黄酒\": \"2茶匙\", \"蚝油、\": \"2茶匙\", \"蜂蜜\": \"10克\", \"姜片\": \"10片\", \"香葱\": \"20\"}', 7, '2020-02-27 22:27:50', 0, '2020-03-12 19:36:05', '504053');
INSERT INTO `menu` VALUES (294, '醋溜白菜', 64, '{\"1\": \"备好材料\", \"2\": \"白菜洗净对半切开，再斜切片\", \"3\": \"蒜切片；姜切丝；辣椒切段；葱切段\", \"4\": \"酱油、蚝油、醋、白糖和盐加2汤匙水调汁备用\", \"5\": \"热油锅，放入葱姜蒜辣椒炒出香味\", \"6\": \"倒入白菜稍炒\", \"7\": \"然后倒入调好的酱油醋汁炒匀\", \"8\": \"淀粉加水调匀勾芡一下就可以了\"}', '{\"白菜帮\": \"1棵\", \"葱\": \"1根\", \"干辣椒\": \"5只\", \"蒜\": \"2瓣\", \"酱油\": \"1汤匙\", \"醋\": \"1汤匙\", \"白糖\": \"少许\", \"蚝油\": \"1茶匙\", \"淀粉\": \"少量\", \"姜丝\": \"少量\", \"盐\": \"适量\", \"油\": \"适量\"}', 8, '2020-02-27 22:27:52', 0, '2020-03-12 19:36:05', '380258');
INSERT INTO `menu` VALUES (295, '蚝油生菜', 4, '{\"1\": \"生菜摘成一块一块的洗净，蒜头切成蒜末备用。\", \"2\": \"锅里烧水，水开了，生菜下锅，放1勺盐，烫熟捞出。\", \"3\": \"烫熟的生菜捞出来，沥干水分，再装盘。\", \"4\": \"锅里放适量油，蒜末下油锅爆香，加蚝油，一勺生抽，一勺盐和少许白糖，炒出香味。\", \"5\": \"将热腾腾的酱汁淋上生菜，就可以美味的开吃啦。\"}', '{\"生菜\": \"1颗\", \"蚝油\": \"5g\", \"生抽\": \"1勺\", \"白糖\": \"少许\", \"蒜头\": \"半个\", \"盐\": \"1勺\"}', 7, '2020-02-27 22:27:52', 0, '2020-03-12 19:36:05', '507354');
INSERT INTO `menu` VALUES (296, '锅塌豆腐', 8, '{\"1\": \"豆腐一块500克左右切成厚薄、大小均匀的豆腐片，容器中加入适量的花椒和盐用开水浸泡出味儿待变温后再放入葱花、姜片、豆腐腌制1小时左右使豆腐更好的入味儿\", \"2\": \"葱、姜、方火腿适量分别切成葱丝、姜丝、火腿丝\", \"3\": \"香菜择洗干净切成香菜段\", \"4\": \"浸泡入味儿的豆腐捞出控干水分备用，鸡蛋两个打入碗中用筷子搅拌均匀成鸡蛋液，面粉适量放入容器中备好\", \"5\": \"热锅加入适量的花生油，把豆腐先均匀蘸上干面粉再裹上蛋液放入锅中煎至两面金黄即可，煎好的豆腐放入容器中根据自己的喜好把大块改刀切成小块\", \"6\": \"煎完豆腐的锅中加入少许花生油，把葱、姜丝爆香加少许蚝油翻炒均匀\", \"7\": \"倒入适量的清水放入煎好的豆腐，均匀地摆上火腿丝炖至汤汁浓稠时关火出锅\", \"8\": \"为了整体的美观把炖好的豆腐整体滑入盘中撒香菜段即可。\"}', '{\"豆腐\": \"适量\", \"鸡蛋\": \"适量\", \"面粉\": \"适量\", \"葱\": \"适量\", \"姜\": \"适量\", \"花椒\": \"适量\", \"盐\": \"适量\", \"水\": \"适量\", \"花生油\": \"适量\", \"蚝油\": \"适量\", \"方火腿\": \"适量\", \"香菜\": \"适量\"}', 8, '2020-02-27 22:27:53', 0, '2020-03-12 19:36:05', '374990');
INSERT INTO `menu` VALUES (297, '广式芋丝糕（简易版）', 1, '{\"1\": \"香芋去皮后刨成丝备用。\", \"2\": \"广式腊肠切小粒备用。\", \"3\": \"150克的粘米粉加入油，盐，糖，鸡粉以及胡椒粉，加温水200毫升搅拌成无颗粒的糊状备用。\", \"4\": \"锅里下油，油热加入腊肠粒，爆炒一下。\", \"5\": \"把芋丝倒入锅中，跟腊肠粒一起翻炒均匀。\", \"6\": \"把拌好的粘米粉糊倒进翻炒均匀的芋丝中，翻拌均匀。\", \"7\": \"蒸盘底部先刷上一层油，以防粘底。\", \"8\": \"把与粘米粉糊拌均匀的芋丝装到蒸盘中，用锅铲压平压实。\", \"9\": \"把装好盘的芋丝糕放到锅里，隔水大火蒸30分钟左右，就可以出锅了。\", \"10\": \"芋丝糕切块，放平底锅里煎一煎，香脆松化可口。\"}', '{\"香芋\": \"1个（去皮后约750克）\", \"粘米粉\": \"150克\", \"广式腊肠\": \"4根\", \"温水\": \"200毫升\", \"盐\": \"1/2茶匙\", \"糖\": \"1茶匙\", \"胡椒粉\": \"少量\", \"花生油\": \"适量\", \"鸡粉\": \"少量\"}', 7, '2020-02-27 22:27:53', 0, '2020-03-12 19:36:05', '504265');
INSERT INTO `menu` VALUES (298, '油焖大虾', 77, '{\"1\": \"将虾洗净，并挑出虾线，\", \"2\": \"挑好的虾放在盘子中\", \"3\": \"将葱姜蒜分别切好，备用\", \"4\": \"油预热，放入切好的葱姜蒜，翻炒，至有香味溢出\", \"5\": \"倒入基围虾，继续翻炒，\", \"6\": \"虾身全部变红，即可。\"}', '{\"基围虾\": \"500g\", \"色拉油\": \"适量\", \"盐\": \"适量\", \"鸡精\": \"适量\", \"葱\": \"适量\", \"姜\": \"3片\", \"蒜\": \"4块\"}', 8, '2020-02-27 22:27:54', 0, '2020-03-12 19:36:05', '372087');
INSERT INTO `menu` VALUES (299, '麻辣九转大肠', 54, '{\"1\": \"大肠用面粉和醋清洗干净\", \"2\": \"锅做水下入葱姜，花椒大料，料酒，大肠焯水\", \"3\": \"加入调料包和生抽适量老抽煮熟\", \"4\": \"大肠切段，粗的套在细的上，用剪刀修齐\", \"5\": \"锅烧热下入大肠炸至两面金黄色捞出控油\", \"6\": \"锅烧热入油烧热下入花椒大料煸香下入葱姜蒜煸香，加入大肠翻炒\", \"7\": \"加入盐，鸡粉，芝麻翻炒均匀即成\", \"8\": \"成品图\", \"9\": \"成品图\"}', '{\"大肠\": \"1个\", \"川椒\": \"适量\", \"花椒\": \"适量\", \"葱\": \"适量\", \"盐\": \"适量\", \"调料包\": \"适量\", \"白芷\": \"适量\", \"大料\": \"适量\", \"草寇\": \"适量\", \"姜\": \"适量\", \"香叶\": \"1片\", \"白扣\": \"适量\", \"胡椒\": \"适量\", \"桂皮\": \"适量\", \"料酒\": \"适量\", \"生抽\": \"适量\", \"老抽\": \"适量\", \"蒜\": \"适量\"}', 8, '2020-02-27 22:27:56', 0, '2020-03-12 19:36:05', '378844');
INSERT INTO `menu` VALUES (300, '家庭广式叉烧', 5, '{\"1\": \"置一大碗，放叉烧酱2大匙、蜂蜜1大匙、蚝油1大匙、生抽1大匙、黑椒粉1/2小匙、白兰地2大匙，搅拌均匀。\", \"2\": \"放五花肉入碗，用手搅拌均匀，每条都按摩一下，然后用小刀或利锥均匀的密插肉肉以容易入味，感觉用小刀插比较容易入味些，将插好的肉入密实袋或盒，也将多余的汁倒进去，入冰箱1天或以上。\", \"3\": \"到要烤时候，把肉放在锡纸上。\", \"4\": \"包好，放入空气炸锅，设置400F、35~40分钟。\", \"5\": \"有烤肉的空当，可以腾出手来做碗汁，置一小碗，放叉烧酱1大匙、蜂蜜1大匙、生抽1大匙、蚝油1大匙、麻油1小匙、清水1/3杯，搅拌均匀。\", \"6\": \"时间到后，把肉放入空气炸锅，在肉的全身涂上碗汁。\", \"7\": \"设置400F、4~6分钟直到烤至微焦，再翻转另一面，也涂上碗汁，再烤设置400F、4~6分钟，烤好后切薄片。\", \"8\": \"把剩下的碗汁加上生粉1小匙，搅拌，放在炉上加热，边煮加搅拌，烧到起泡。\", \"9\": \"倒在切了片的叉烧上面，可以享用了。Enjoy ！\"}', '{\"五花肉\": \"3条\", \"叉烧酱\": \"1大匙\", \"蜂蜜\": \"1大匙\", \"蚝油\": \"1大匙\", \"生抽\": \"1大匙\", \"黑椒粉\": \"1/2小匙\", \"白兰地\": \"2大匙\", \"麻油\": \"1小匙\", \"清水\": \"2大匙\", \"生粉\": \"1小匙\"}', 7, '2020-02-27 22:27:56', 0, '2020-03-12 19:36:05', '498714');
INSERT INTO `menu` VALUES (301, '河南变蛋', 93, '{\"1\": \"某宝有卖的皮蛋粉。\", \"2\": \"里面是这个样子。\", \"3\": \"冲绿茶水，尽量稠一些这样颜色重，出来效果好。我这里的绿茶冲的有些淡了。\", \"4\": \"绿茶水彻底放凉才可以使用哦，切记这一点！这一步忘记拍照，把彻底放凉的绿茶水缓慢加入皮蛋粉当中，少量多次哦，粘稠合适。放置一小时，达到用小筷子或者小勺子挑起，有缓慢滴落的情况出现，如下图这样皮蛋粉液就做好啦。\", \"5\": \"包裹上鸡蛋，尽可能均匀哦。外面套上三层塑料袋，切记使用塑料袋。\", \"6\": \"就是这样的密封三层啦。密封好放置在阴凉的地方，切记不要直接接触地面，不要见到阳光。室内20°左右的地方放五六天即成熟 天冷的要十天左右。到时候可以先取出来一个看看里面怎么样。\", \"7\": \"噔噔蹬蹬，启封成功啦。\"}', '{\"鸡蛋\": \"适量\", \"变蛋粉\": \"适量\", \"绿茶\": \"适量\"}', 8, '2020-02-27 22:27:57', 0, '2020-03-12 19:36:05', '377021');
INSERT INTO `menu` VALUES (302, '鸡蛋肠粉', 15, '{\"1\": \"鸡蛋打散，葱切粒，锅里装入水烧热。\", \"2\": \"粘米粉、澄面和玉米淀粉倒入盆里。\", \"3\": \"加入水，搅拌至没有颗粒的粉浆。\", \"4\": \"肠粉盘刷一层薄油（没有肠粉盘的话，如果用其他盘子，底部一定要平整的，肠粉盘在万能的淘宝网上有售）。\", \"5\": \"舀一勺半粉浆倒入肠粉盘里，加入一点打散的蛋液和葱花。\", \"6\": \"交叉摆放到早已烧开水的锅里，盖上锅盖。\", \"7\": \"大火蒸3分钟。\", \"8\": \"用肠粉铲把蒸好的肠粉刮到一边，再摆在盘子里。\"}', '{\"粘米粉\": \"97g\", \"澄面\": \"90g\", \"玉米淀粉\": \"12g\", \"清水\": \"500g\", \"鸡蛋\": \"2个\", \"葱花\": \"适量\"}', 7, '2020-02-27 22:27:58', 0, '2020-03-12 19:36:05', '494031');
INSERT INTO `menu` VALUES (303, '酱爆鸡丁', 61, '{\"1\": \"鸡腿一个\", \"2\": \"洗净后去掉骨头\", \"3\": \"切小块，加入生抽、胡椒粉、料酒、淀粉\", \"4\": \"抓匀腌制十多分钟\", \"5\": \"小碗里加入1勺豆瓣酱、1勺甜面酱，再加点水搅拌均匀\", \"6\": \"黄瓜切丁，葱姜切碎，辣椒剪成小圈\", \"7\": \"锅里注油烧热，油不用多，下腌好的鸡丁\", \"8\": \"炒至鸡丁变色\", \"9\": \"加入辣椒、葱姜，炒香\", \"10\": \"加入鸡粉\", \"11\": \"加黄瓜丁，翻炒一会儿\", \"12\": \"倒入酱汁\", \"13\": \"炒匀炒香，至酱汁浓稠即可\", \"14\": \"成品\"}', '{\"鸡腿\": \"1只\", \"黄瓜\": \"半根\", \"干辣椒\": \"6个\", \"甜面酱\": \"1勺\", \"豆瓣酱\": \"1勺\", \"生抽\": \"1勺\", \"胡椒粉\": \"少许\", \"料酒\": \"1勺\", \"淀粉\": \"半勺\", \"鸡粉\": \"适量\", \"十三香\": \"少许\", \"葱\": \"1小段\", \"姜\": \"2片\"}', 8, '2020-02-27 22:27:59', 0, '2020-03-12 19:36:05', '376465');
INSERT INTO `menu` VALUES (304, '姜撞奶', 16, '{\"1\": \"准备食材，黑糖是块糖，所以用之前要先打碎，姜要用老姜，牛奶尽量买全脂的，我是家里没有了，用了这个，不如全脂的好成型。\", \"2\": \"老姜去皮切片，要是清洗干净泥的也可以不去皮，我是用原汁机榨出姜汁，剩余的姜渣还可以用来煮姜茶，一点也不浪费哦。\", \"3\": \"姜片放入SKG原汁机内，启动正转键，将姜汁榨出。\", \"4\": \"准备好两个碗，每一个碗里倒入15ml，剩余的姜汁可以留着冲花茶，很有驱寒效果哦。\", \"5\": \"接下来开始煮牛奶，黑糖先倒入牛奶里融化，边煮边搅拌，直到看到锅变开始冒泡，立刻熄火，千万别煮沸了。\", \"6\": \"将黑糖牛奶迅速倒入姜汁内，高度大概要15cm，利用冲击力自然混匀，不用搅拌哦，大概3分钟左右即可开始凝结。，天气冷的话需要盖上一个盖子保温让其更好的凝结。\", \"7\": \"剩余的姜渣不要丢，直接用来煮姜枣茶或者晒干了泡水喝，对女性来说都是很有好处的哦\", \"8\": \"做好的姜撞奶能承受勺子的重量，口感十分的香滑，类似豆腐花，但是浓郁的黑糖混合着姜的香气，闻起来很是醒神，女人月经期，别错过这甜品哦。\"}', '{\"老姜\": \"1大块\", \"纯牛奶\": \"400ML\", \"黑糖\": \"40克\"}', 7, '2020-02-27 22:27:59', 0, '2020-03-12 19:36:05', '478485');
INSERT INTO `menu` VALUES (305, '扒牛肉条', 70, '{\"1\": \"将牛肉整块放入冷水内泡透，洗净血水，放入沸水锅中焯水后捞出洗干净\", \"2\": \"牛肉放入电压力锅内胆，加葱姜\", \"3\": \"再加清水，放入电压力锅\", \"4\": \"选择营养炖，适中档\", \"5\": \"压力锅解压后捞出牛肉晾凉，将牛肉切成大小、薄厚适中的片\", \"6\": \"净面朝下码入碗内，碗中加入酱油、 盐、料酒、葱段、姜片、大料\", \"7\": \"再加煮牛肉的原汤，将碗入蒸锅，旺火蒸20分钟\", \"8\": \"去掉大料、葱姜，滗出汤汁，将牛肉扣入平盘内 \", \"9\": \"蒸牛肉的汤倒入炒锅内，加入酱油烧沸，水淀粉勾芡，淋芝麻油成芡汁\", \"10\": \"将芡汁浇在牛肉上\", \"11\": \"即可上桌享用\"}', '{\"牛肉\": \"700克\", \"油\": \"适量\", \"盐\": \"适量\", \"酱油\": \"适量\", \"料酒\": \"适量\", \"大料\": \"适量\", \"淀粉\": \"适量\", \"葱\": \"适量\", \"芝麻油\": \"适量\", \"姜\": \"适量\"}', 8, '2020-02-27 22:28:00', 0, '2020-03-12 19:36:05', '376650');
INSERT INTO `menu` VALUES (306, '冰糖肘子', 81, '{\"1\": \"肘子清洗干净，冷水下锅一边煮一边将浮沫捞出，直到不再出浮沫为止。\", \"2\": \"将煮好的肘子捞出，用镊子摘去细小的毛。\", \"3\": \"调料：黄酒、葱姜、冰糖、老抽、生抽、腐乳汁、八角、香叶、白芷、白扣葱姜等调料（家里调料不全，又用了一个炖肉料包）\", \"4\": \"处理干净的肘子放入砂锅中，加入所有调料，加入温水稍稍莫过肘子。\", \"5\": \"砂锅置火上，大火烧沸后，改小火煨煮1.5个小时左右。\", \"6\": \"烧制的时候注意翻面。\", \"7\": \"最后把汤汁稍稍收浓即可。\"}', '{\"肘子\": \"1个\", \"冰糖\": \"40克\", \"黄酒\": \"50克\", \"老抽\": \"2茶匙\", \"生抽\": \"4茶匙\", \"腐乳汁\": \"适量\", \"八角\": \"2粒\", \"炖肉料包\": \"1个\", \"香叶\": \"3片\", \"白芷\": \"2片\", \"草果\": \"1个\", \"葱\": \"适量\", \"食盐\": \"适量\", \"姜\": \"适量\"}', 8, '2020-02-27 22:28:02', 0, '2020-03-12 19:36:05', '374416');
INSERT INTO `menu` VALUES (307, '姜撞奶', 93, '{\"1\": \"姜切碎，用料理机打成蓉。\", \"2\": \"用纱布挤出姜汁，107克姜去皮后挤了31克姜汁。\", \"3\": \"牛奶加白糖烧开后静置5分钟，这个过程没拍，冲入牛奶之前先把姜汁搅一下，因为姜汁里含有淀粉会沉底，然后从高处冲入放姜汁的碗里，此时不要移动碗，也不要搅动。\", \"4\": \"十分钟后，牛奶凝固，放上一个瓷勺子也不会沉，成功。\", \"5\": \"香甜滑嫩。\"}', '{\"姜\": \"107克\", \"牛奶\": \"250毫升\", \"糖\": \"适量\"}', 7, '2020-02-27 22:28:02', 0, '2020-03-12 19:36:05', '477239');
INSERT INTO `menu` VALUES (308, '诱人地锅鸡', 27, '{\"1\": \"鸡腿4个,约500克\", \"2\": \"鸡腿切块，放水中浸泡洗去血水\", \"3\": \"300克面粉加3克盐加165克水活成团\", \"4\": \"均等的分割成自己需要的小面团\", \"5\": \"青红辣椒切段，葱切段，姜蒜切片\", \"6\": \"锅内放油，爆香花椒大茴和浸泡过的干辣椒段\", \"7\": \"放入鸡块煸炒\", \"8\": \"放入姜片和葱段，烹入料酒，煸炒至水分将干\", \"9\": \"放入生抽，老抽，蚝油，适量水\", \"10\": \"放入浸泡好的木耳，盖上锅盖炖5分钟入味，放入适量盐\", \"11\": \"把小面团放入水中浸泡5到10分钟\", \"12\": \"取面团，用手扯成面片放入锅中，\", \"13\": \"放上青红椒，蒜片，剩下的葱，小火再焖5分钟即可\", \"14\": \"先将面片放入地锅中，再翻匀鸡块和辣椒，盛入锅中，放香菜装饰即可\"}', '{\"鸡腿\": \"4个\", \"干木耳\": \"15克\", \"面粉\": \"300克\", \"葱\": \"适量\", \"姜\": \"适量\", \"蒜\": \"适量\", \"生抽\": \"适量\", \"老抽\": \"适量\", \"蚝油\": \"适量\", \"青辣椒\": \"适量\", \"干辣椒段\": \"适量\", \"花椒\": \"适量\", \"大茴\": \"适量\", \"料酒\": \"适量\", \"红辣椒\": \"适量\"}', 8, '2020-02-27 22:28:03', 0, '2020-03-12 19:36:05', '375806');
INSERT INTO `menu` VALUES (309, '溜豆腐', 64, '{\"1\": \"将韧豆腐，切成厚度约1厘米的方块。\", \"2\": \"鸡蛋液里加点盐，打散。\", \"3\": \"热锅温油，将豆腐挂上蛋液，二面煎成金黄色。\", \"4\": \"锅内放油，油热爆香葱姜末，放入酱油，白糖，胡椒粉，蚝油，少半勺香醋，少许盐。\", \"5\": \"汤开放入豆腐，烧至入味后加入鸡粉，青红椒丁，蒜苗，少许淀粉。\", \"6\": \"出锅前放少许香油。\"}', '{\"豆腐\": \"200克\", \"鸡蛋\": \"2个\", \"葱末\": \"少许\", \"姜末\": \"少许\", \"蚝油\": \"1勺\", \"鲜味酱油\": \"1勺\", \"糖\": \"5克\", \"盐\": \"少许\", \"青椒丁\": \"少许\", \"蒜苗\": \"少许\", \"红椒丁\": \"适量\"}', 8, '2020-02-27 22:28:04', 0, '2020-03-12 19:36:05', '374249');
INSERT INTO `menu` VALUES (310, '鱼咬羊', 2, '{\"1\": \"用盐，鸡粉和料酒将羊肉腌制5-10分钟\", \"2\": \"锅中烧水，将腌制好的羊肉放入沸水中汆烫片刻\", \"3\": \"羊肉汆烫后取出待用\", \"4\": \"切姜片，并将姜片和八角放入油锅中炒香\", \"5\": \"锅中再放入羊肉翻炒\", \"6\": \"锅中放入料酒和少许老抽炒匀\", \"7\": \"加入水，生抽，料酒，盐，鸡粉和白糖将羊肉煮熟\", \"8\": \"把羊肉和汤底分别从锅中取出待用\", \"9\": \"鱼身上抹盐，腌制片刻，把羊肉塞入鲈鱼里\", \"10\": \"姜片放入油锅中炒香\", \"11\": \"将鱼腹部用竹签封口，防止鱼翻身时羊肉掉出来，下油锅将鱼炸至两面微黄\", \"12\": \"把煮羊肉的汤底倒入锅中，放入切好的大葱，枸杞，少许八角焖煮至鱼熟，取下封鱼腹部的竹签\", \"13\": \"把鱼取出装盘，把汤汁淋在鱼身上即可\"}', '{\"鲈鱼\": \"1条\", \"羊肉\": \"200克\", \"大葱\": \"适量\", \"八角\": \"少许\", \"枸杞\": \"少许\", \"生抽\": \"少许\", \"老抽\": \"少许\", \"姜\": \"适量\", \"食用油\": \"适量\", \"料酒\": \"少许\", \"盐\": \"少许\", \"鸡粉\": \"少许\"}', 9, '2020-02-27 22:28:06', 0, '2020-03-12 19:36:05', '345443');
INSERT INTO `menu` VALUES (311, '油焖虾', 2, '{\"1\": \"虾洗净、从背部剪开抽掉虾肠，用手挤压出头部沙袋。加入盐、料酒腌制10分钟\", \"2\": \"调料准备好\", \"3\": \"锅里放油烧热、下姜蒜辣椒炒出香味\", \"4\": \"加入虾翻炒至变色\", \"5\": \"加入料酒、生抽、老抽翻炒均匀后，淋入少许水焖2分钟，然后加入少许糖提鲜，撒入葱花即可出锅\", \"6\": \"成品图\", \"7\": \"成品图\"}', '{\"鲜虾\": \"300g\", \"盐\": \"适量\", \"糖\": \"适量\", \"姜\": \"适量\", \"干辣椒\": \"适量\", \"生抽\": \"适量\", \"老抽\": \"适量\", \"料酒\": \"适量\", \"蒜\": \"适量\", \"葱\": \"适量\"}', 8, '2020-02-27 22:28:06', 0, '2020-03-12 19:36:05', '340028');
INSERT INTO `menu` VALUES (312, '糖不甩', 39, '{\"1\": \"准备的原料。\", \"2\": \"糯米粉加清水揉成面条醒十分钟。\", \"3\": \"花生米烤箱150度20分钟左右烤熟。\", \"4\": \"醒好的面条分剂子再搓圆待用。\", \"5\": \"烤好的花生米去皮，用擀面杖压碎。\", \"6\": \"水烧开，下入糯米圆子烧开。\", \"7\": \"再将圆子下入煮好的红糖和冰糖的水中，大火烧到浓稠就可以。\", \"8\": \"最好撒上花生碎就可以。\"}', '{\"糯米粉\": \"250克\", \"红糖\": \"半碗\", \"冰糖\": \"适量\", \"花生米\": \"适量\"}', 7, '2020-02-27 22:28:06', 0, '2020-03-12 19:36:05', '473702');
INSERT INTO `menu` VALUES (313, '酱肘子', 84, '{\"1\": \"肘子凉水下锅加入葱姜和料酒，水开后去除血沫和杂质。\", \"2\": \"焯过水的肘子仔细拔去细毛，清洗干净。（焯过水再去毛更容易）\", \"3\": \"酱料：老汤、炖肉料包、黄酱、葱姜、八角、桂皮、香叶、草果、肉蔻、冰糖等调料\", \"4\": \"把处理干净的的肘子放入高压锅中加入所有调料\", \"5\": \"加入老抽\", \"6\": \"盖好盖子，上汽后改小火压30分钟\", \"7\": \"关火后不要急着捞出，肘子浸泡至还有温热时捞出。\", \"8\": \"剔去大骨\", \"9\": \"用保鲜膜包成卷，凉透后撕去保鲜膜切片\", \"10\": \"凉透后切片即可食用。\"}', '{\"猪肘子\": \"1个\", \"老汤\": \"适量\", \"香叶\": \"4片\", \"草果\": \"1个\", \"炖肉料包\": \"1个\", \"黄酱\": \"40克\", \"老抽\": \"适量\", \"肉蔻\": \"1个\", \"葱\": \"适量\", \"冰糖\": \"适量\", \"食盐\": \"适量\", \"八角\": \"适量\", \"姜\": \"适量\"}', 8, '2020-02-27 22:28:07', 0, '2020-03-12 19:36:05', '360607');
INSERT INTO `menu` VALUES (314, '原只椰子炖双皮奶', 7, '{\"1\": \"把牛奶倒入没有椰汁的带肉椰壳中；\", \"2\": \"椰子盖上保鲜膜，放入蒸烤箱蒸40分钟左右；\", \"3\": \"蒸好的椰子牛奶拿出放凉后，会形成一层奶皮，用小刀沿着椰子边划一圈，让奶皮和椰子壁慢慢分离；\", \"4\": \"放把椰子里的牛奶慢慢地倒入另一个无水干净的碗里；\", \"5\": \"椰子壳留点奶，不要倒净，确保奶皮不要粘在椰子底部，不然会浮不起来的。\", \"6\": \"分离蛋清后加入细砂糖搅拌均匀；\", \"7\": \"搅拌好的蛋清倒入碗里的牛奶中混合均匀；\", \"8\": \"把蛋清奶液慢慢沿着刚才划破的奶皮缺口倒回椰子壳里，让奶皮浮在上面；\", \"9\": \"盖上保鲜膜再次使用蒸烤箱的蒸功能 115℃，蒸 40 分钟。40分钟后停止加热，让椰子在里面闷5分钟再取出即可享用 。\", \"10\": \"炖好的双皮奶夹着淡淡的椰香，十分美味！\"}', '{\"原只椰子\": \"1个\", \"水牛奶或全脂牛奶\": \"120g\", \"蛋清\": \"2个\", \"细砂糖\": \"30g\", \"芒果\": \"1个\", \"蓝莓\": \"1盒\"}', 7, '2020-02-27 22:28:08', 0, '2020-03-12 19:36:05', '472021');
INSERT INTO `menu` VALUES (315, '梅干菜烧饼', 25, '{\"1\": \"食材图\", \"2\": \"五花肉切成小颗粒\", \"3\": \"用盐，生抽，料酒，胡椒粉，姜末腌制20分钟\", \"4\": \"面粉200克，先把100克面粉用开水烫拌均匀\", \"5\": \"用开水烫起搅拌好\", \"6\": \"加入剩余的100克面粉及酵母粉\", \"7\": \"加入冷水合成光滑的面团饧发10分钟\", \"8\": \"泡软的梅菜挤干水分切碎\", \"9\": \"切好的梅菜倒入肉里加点香油拌匀就可以啦\", \"10\": \"面团揉光\", \"11\": \"分成小份擀成中间后四周薄的面片，包上适量的梅菜肉馅\", \"12\": \"收口捏紧\", \"13\": \"收口朝下摆放，一共做了8个\", \"14\": \"刷一层稀释的麦芽糖水\", \"15\": \"放入预热好的烤箱里200度30分钟\", \"16\": \"烤好的\", \"17\": \"烤的脆脆的皮里面鲜嫩多汁的梅菜肉丁馅儿，真是好吃极了\"}', '{\"面粉\": \"200g\", \"五花肉\": \"120g\", \"梅干菜\": \"60g\", \"盐\": \"1勺\", \"生抽\": \"15g\", \"蚝油\": \"15g\", \"料酒\": \"15g\", \"胡椒粉\": \"少许\", \"香油\": \"10克\", \"麦芽糖水\": \"1匙\", \"芝麻\": \"1匙\", \"酵母粉\": \"2g\", \"姜末\": \"1g\"}', 9, '2020-02-27 22:28:09', 0, '2020-03-12 19:36:05', '337448');
INSERT INTO `menu` VALUES (316, '山东大煎饼', 95, '{\"1\": \"杂粮若干加水和成面糊\", \"2\": \"用刮板在电鏊子上烙成煎饼\", \"3\": \"上面可放鸡蛋，葱花，做成鸡蛋煎饼。也可放青菜，也可抹酱，随意搭配。\"}', '{\"全麦面\": \"适量\", \"玉米面\": \"适量\", \"水\": \"适量\"}', 8, '2020-02-27 22:28:09', 0, '2020-03-12 19:36:05', '371278');
INSERT INTO `menu` VALUES (317, '白灼虾', 98, '{\"1\": \"虾洗净。\", \"2\": \"锅中水烧开放姜下锅，再放虾下去。\", \"3\": \"虾一变色就熟了。\", \"4\": \"捞出上碟，蘸料:蒜蓉，酱油，醋，熟油。\", \"5\": \"成品，鲜美。\"}', '{\"鲜虾\": \"200克\", \"姜\": \"少许\", \"蒜蓉\": \"少许\", \"酱油\": \"少许\", \"醋\": \"少许\", \"熟油\": \"少许\"}', 7, '2020-02-27 22:28:09', 0, '2020-03-12 19:36:05', '463418');
INSERT INTO `menu` VALUES (318, '锅塌豆腐～冬季里的暖心菜', 32, '{\"1\": \"将豆腐顶刀切成约1厘米厚的片，放在盘中备用。\", \"2\": \"鸡蛋打散，搅拌均匀。葱姜蒜，蒜苗切末备用。\", \"3\": \"倒入油15克，油温五成热时，把豆腐片先沾满淀粉，再挂上蛋糊，小火煎成二面金黄色。\", \"4\": \"盛出控油。\", \"5\": \"锅内留底油，放入葱姜蒜末，中小火煸出香味。\", \"6\": \"加水约250克，放入盐，1勺海鲜酱油，半勺香醋，1勺白糖，味精，少许胡椒粉。\", \"7\": \"放入煎好的豆腐小火煨透。中途翻一次。\", \"8\": \"出锅前撒点蒜苗末，勾点薄芡。\", \"9\": \"收汁后盛盘。\"}', '{\"韧豆腐\": \"300克\", \"鸡蛋\": \"2个\", \"葱\": \"5克\", \"姜\": \"5克\", \"蒜\": \"5克\", \"蒜苗\": \"少许\", \"淀粉\": \"20克\", \"盐\": \"3克\", \"糖\": \"5克\", \"海鲜酱油\": \"1小勺\"}', 8, '2020-02-27 22:28:10', 0, '2020-03-12 19:36:05', '370085');
INSERT INTO `menu` VALUES (319, '腊肠煲仔饭(电饭煲版)', 51, '{\"1\": \"备好食材。\", \"2\": \"米淘好，放好水，加2勺植物油，浸泡1小时。\", \"3\": \"腊肠切片，叉烧肉切片，料汁(生抽、蚝油、白糖、香油、少许水配好)。\", \"4\": \"电饭煲按下煲子饭开始煮米饭。\", \"5\": \"米饭汤收进去后，放入腊肠，打入鸡蛋，继续焖。\", \"6\": \"菠菜焯下水。\", \"7\": \"饭好后，放入菠菜摆好。\", \"8\": \"倒入料汁拌匀。\", \"9\": \"盛出。\", \"10\": \"成品。\"}', '{\"大米\": \"2杯\", \"腊肠\": \"1个\", \"生抽\": \"2勺\", \"蚝油\": \"1勺\", \"糖\": \"半勺\", \"香油\": \"少许\", \"植物油\": \"2勺\", \"菠菜\": \"少许\", \"水\": \"适量\", \"叉烧肉\": \"少许\"}', 7, '2020-02-27 22:28:10', 0, '2020-03-12 19:36:05', '459606');
INSERT INTO `menu` VALUES (320, '麻辣小龙虾', 35, '{}', '{\"虾尾\": \"半斤\", \"青辣椒\": \"两个\", \"红辣椒\": \"适量\", \"香菜\": \"四五根\", \"味极鲜\": \"适量\", \"麻椒\": \"适量\", \"冰糖\": \"一块\", \"老干妈辣酱\": \"一勺\", \"葱\": \"一段\", \"姜\": \"适量\", \"蒜\": \"两瓣\", \"料酒\": \"适量\"}', 9, '2020-02-27 22:28:11', 0, '2020-03-12 19:36:05', '335061');
INSERT INTO `menu` VALUES (321, '秘制叉烧', 17, '{\"1\": \"梅花肉切成宽约2厘米的条状。\", \"2\": \"生姜切碎，小葱洗净打成结。\", \"3\": \"豆腐乳、叉烧酱A、耗油、生抽、高度酒、白糖、盐、姜末、小葱混合，倒在梅花肉上，涂抹均匀，装入容器，冷藏腌制一晚。\", \"4\": \"锅中放水，微微冒泡时，放入腌好的肉条，烫十秒，捞出。肉条摆在铺了锡纸的烤盘上。\", \"5\": \"放入预热好的烤箱，盖锡纸，上下火210度，中层烘烤25分钟后，取出。\", \"6\": \" 叉烧酱B与蜂蜜混合均匀，刷在叉烧上正反两面，放入烤箱，上火210度，下火160度，继续烘烤20分钟，中间取出翻面一次。\", \"7\": \"烘烤上色。\"}', '{\"梅花肉\": \"600克\", \"叉烧酱\": \"50克（B）\", \"豆腐乳\": \"1块\", \"蚝油\": \"10克\", \"生抽\": \"10克\", \"高度酒\": \"10克\", \"白糖\": \"10克\", \"盐\": \"3克\", \"生姜\": \"少许\", \"小葱\": \"少许\", \"蜂蜜\": \"30克\"}', 7, '2020-02-27 22:28:12', 0, '2020-03-12 19:36:05', '457882');
INSERT INTO `menu` VALUES (322, '锅包肉', 100, '{\"1\": \"很美味哦，分分钟光盘。\", \"2\": \"成品图\", \"3\": \" 将里脊肉洗净，去筋斜着切成两半，切厚片；葱姜段切好备用；黑乐砂锅用水冲洗干净，擦干水份，备用。\", \"4\": \"淀粉加入料酒，油，水，调成稀稠合适的糊（太稀的话，肉片挂不上面糊；太稠的话，面太厚，口感不好，加入一点油，可使炸出的肉外皮更酥脆，}搅拌均匀，倒入切好的肉片里。\", \"5\": \"将裹匀淀粉的肉片放十分钟。砂锅中倒入食用油，加热到七成熟。将肉片一片片下入锅中炸至起泡浮起。分次把肉炸好，然后油温继续加热至最高。\", \"6\": \"油面完全平静，有少许烟冒起，将肉分次下入复炸，炸至有斑驳的焦红色，捞出控油。 \", \"7\": \"将油倒出，切好青椒，调好糖醋汁，趁热锅倒入少许油，然后倒入葱，姜，蒜，辣皮，翻炒一会再加入调好的糖醋汁。\", \"8\": \" 中火熬至糖醋汁有些粘稠，随后倒入肉片，和青椒丝，大火快速挂汁出锅。\", \"9\": \"成菜色泽金黄，外酥里嫩，酸甜可口\", \"10\": \"成品图\", \"11\": \"成品图\"}', '{\"里脊肉\": \"500克\", \"葱\": \"半根\", \"姜\": \"3片\", \"青椒\": \"1个；\", \"蒜\": \"2瓣；\", \"干红辣皮\": \"5个；\", \"土豆淀粉\": \"2勺；\", \"料酒\": \"1勺\", \"油\": \"1勺；\", \"水\": \"2勺；\", \"醋\": \"2勺\", \"糖\": \"4勺；\", \"盐\": \"1小勺\"}', 8, '2020-02-27 22:28:12', 0, '2020-03-12 19:36:05', '369710');
INSERT INTO `menu` VALUES (323, '白灼菜芯', 39, '{\"1\": \"菜芯洗净备用。\", \"2\": \"去掉根部。\", \"3\": \"锅内加水烧开，放入少许盐，这样焯水以后的青菜颜色翠绿，口感更脆。\", \"4\": \"放入菜芯焯水半分钟以后捞出。\", \"5\": \"焯水以后摆在盘子里。\", \"6\": \"葱末，姜末 准备好。\", \"7\": \"耗油.酱油.白糖.盐.淀粉，适量清水放在一起，调成一个料汁。\", \"8\": \"搅拌均匀。\", \"9\": \"锅内放油烧热，放入蒜末，姜末炒香。\", \"10\": \"到入调好的料汁。\", \"11\": \"熬制这样颜色透亮，很多泡泡就做好了。\", \"12\": \"料汁撒在菜芯上面就可以。\", \"13\": \"清淡美味，非常好吃。\"}', '{\"菜芯\": \"500克\", \"蒜末\": \"少许\", \"姜末\": \"少许\", \"花生油\": \"2勺\", \"蚝油\": \"1勺\", \"酱油\": \"1勺\", \"盐\": \"少许\", \"白糖\": \"半勺\", \"淀粉\": \"半勺\"}', 7, '2020-02-27 22:28:13', 0, '2020-03-12 19:36:05', '455872');
INSERT INTO `menu` VALUES (324, '【安徽】凉拌猪心', 33, '{\"1\": \"卤煮好的猪心一个\", \"2\": \"切成薄片\", \"3\": \"黄瓜蒜黄跟香菜切好\", \"4\": \"放入盆中加入盐味精生抽醋和香油拌均匀\", \"5\": \"加入切成片的猪心\", \"6\": \"拌匀即可\"}', '{\"熟猪心\": \"适量\", \"黄瓜\": \"适量\", \"蒜黄\": \"适量\", \"香菜\": \"适量\", \"盐\": \"适量\", \"味精\": \"适量\", \"醋\": \"适量\", \"生抽\": \"适量\", \"香油\": \"适量\"}', 9, '2020-02-27 22:28:14', 0, '2020-03-12 19:36:05', '311300');
INSERT INTO `menu` VALUES (325, '烧卖', 33, '{\"1\": \"糯米洗净，放入适量水，像煮饭一样煮熟。\", \"2\": \"锅里放适量油，烧热，放入肉末和香肠丁，炒熟。\", \"3\": \"再放入胡萝卜丁和豌豆，翻炒。放入黄酒翻炒。\", \"4\": \"再放入切碎的香菇，小火翻炒2分钟。\", \"5\": \"放入老抽，少量白砂糖，香油。(小火)\", \"6\": \"小火，放入煮好的糯米饭。\", \"7\": \"拌匀。\", \"8\": \"饺子皮稍擀大一些。\", \"9\": \"放入炒好的糯米饭。\", \"10\": \"用大拇指和食指捏紧。(继续做下一个，做好可以放入冰箱冷冻保存)\", \"11\": \"放入蒸笼里。\", \"12\": \"大火蒸15分钟，焖5分钟即可。\"}', '{\"糯米\": \"500g\", \"饺子皮\": \"450g\", \"香肠\": \"一根\", \"肉末\": \"适量\", \"香菇\": \"适量\", \"胡萝卜丁\": \"适量\", \"豌豆\": \"适量\", \"老抽\": \"适量\", \"白砂糖\": \"少量\", \"香油\": \"适量\", \"黄酒\": \"适量\"}', 7, '2020-02-27 22:28:15', 0, '2020-03-12 19:36:05', '453964');
INSERT INTO `menu` VALUES (326, '什么菜能敢动你的鼻腔——地方风味憋辣菜', 37, '{\"1\": \"芥菜疙瘩洗净擦粗丝\", \"2\": \"起锅烧热油，加入芥菜丝翻炒。千万别炒熟，只要所有芥菜丝都翻炒勻，开始热乎就立刻停火。\", \"3\": \"趁热把芥菜丝装入密闭的容器，压实，婆婆习惯在最上面盖一层萝卜丝，也可以用干净的白菜叶来盖，免得热气外溢。盖好盖放一边憋两小时分钟。\", \"4\": \"憋好的辣菜，放一点味极鲜，一点醋，一点盐，拌好就可以吃了。（火腿丝可放可不放）\"}', '{\"鲜芥菜疙瘩\": \"一个\", \"油\": \"适量\", \"味极鲜\": \"适量\", \"米醋\": \"适量\", \"盐\": \"适量\"}', 8, '2020-02-27 22:28:16', 0, '2020-03-12 19:36:05', '366180');
INSERT INTO `menu` VALUES (327, '香煎藕饼', 41, '{\"1\": \"莲藕去皮洗净后切小碎丁。\", \"2\": \"猪肉要选择肥瘦的，五花肉最好。加料酒，生抽，黑胡椒粉，盐，鸡蛋，香油搅匀。\", \"3\": \"香葱切碎加进去。\", \"4\": \"筷子朝一个方向使劲搅出胶。\", \"5\": \"最后把藕放进去搅匀。\", \"6\": \"搅好的馅儿比较粘稠。\", \"7\": \" 带上一次性手套团成圆球。\", \"8\": \" 平底锅烧热油，用手轻压扁改小火煎制。\", \"9\": \" 一面金黄翻面煎另一面。\", \"10\": \"两面焦黄即可出锅。\"}', '{\"五花肉馅儿\": \"250克\", \"莲藕\": \"2节\", \"香葱\": \"50克\", \"料酒\": \"1勺\", \"生抽\": \"2勺\", \"鸡蛋\": \"1颗\", \"黑胡椒粉\": \"1茶匙\", \"香油\": \"1勺\", \"盐\": \"适量\"}', 9, '2020-02-27 22:28:16', 0, '2020-03-12 19:36:05', '320457');
INSERT INTO `menu` VALUES (328, '流沙包', 61, '{\"1\": \"咸鸭蛋黄压碎放进碗中，黄油50克加白砂糖50克放进碗里，蒸5分钟。\", \"2\": \"蒸好的咸蛋黄，黄油，白砂糖放一起，再加奶粉25克，牛奶30克，搅拌匀。\", \"3\": \"再加一点点胡萝卜汁，(用来提味)再次搅匀。放进冰箱冷藏。\", \"4\": \"200克温水加酵母5克，白砂糖20克，搅拌匀。\", \"5\": \"白面团:250克面粉加125克酵母水。揉光滑面团。红面团:250克面粉加剩下的酵母水，胡萝卜汁25克。(水不够可以适当再加胡萝卜汁)揉光滑面团。\", \"6\": \"发酵2倍大小。\", \"7\": \"发好的面团，再次分开揉匀，擀成长方形面片。\", \"8\": \"胡萝卜面团也擀成长方形面团，叠在一起。\", \"9\": \"卷起来，拉均匀。\", \"10\": \"平均分块。\", \"11\": \"用手按扁，用擀面扙擀大些。\", \"12\": \"放进冷藏的流沙馅。(太稀可以放些生粉搅匀)\", \"13\": \"每个都包好来，不要露馅。\", \"14\": \"发酵2倍大小。\", \"15\": \"大火蒸15分钟，焖5分钟即可。\", \"16\": \"趁热吃，就可看到流沙了。\"}', '{\"面粉\": \"500g\", \"酵母\": \"5g\", \"胡萝卜\": \"1根\", \"白砂糖\": \"50g(馅)\", \"咸鸭蛋黄\": \"100g\", \"黄油\": \"50g\", \"奶粉\": \"25g\", \"牛奶\": \"30g\"}', 7, '2020-02-27 22:28:17', 0, '2020-03-12 19:36:05', '447528');
INSERT INTO `menu` VALUES (329, '腌笃鲜', 6, '{\"1\": \"春笋切好块绰水备用\", \"2\": \"把猪蹄绰水\", \"3\": \"捞出用清水洗净\", \"4\": \"咸肉用温水洗净\", \"5\": \"把猪蹄、咸肉都放入电饭煲里\", \"6\": \"加入姜片、适量清水和料酒\", \"7\": \"用煲汤程序炖煮2小时\", \"8\": \"时间过半，放入春笋\", \"9\": \"继续炖煮至猪蹄酥烂，尝一下味道看是否需要加盐\", \"10\": \"成品图\"}', '{\"春笋\": \"4根\", \"咸肉\": \"150g\", \"猪蹄\": \"650g\", \"盐\": \"少许\", \"料酒\": \"3勺\", \"姜片\": \"3片\"}', 9, '2020-02-27 22:28:17', 0, '2020-03-12 19:36:05', '317718');
INSERT INTO `menu` VALUES (330, '徽派魔芋烧鱼', 31, '{}', '{\"魔芋豆腐\": \"适量\", \"草鱼\": \"适量\", \"香菇\": \"适量\", \"青葱\": \"适量\", \"盐\": \"适量\", \"鸡精\": \"适量\", \"白糖\": \"适量\", \"酱油\": \"适量\", \"醋\": \"适量\", \"料酒\": \"适量\", \"水淀粉\": \"适量\"}', 9, '2020-02-27 22:28:18', 0, '2020-03-12 19:36:05', '316904');
INSERT INTO `menu` VALUES (331, '腌笃鲜', 68, '{\"1\": \"咸肉一块，买不到的可以自己做，我就是自己做的，五花肉不用洗，表面擦干净，然后厚厚的裹上盐，放到外面去晾晒，每天去掉渗出的水分裹上盐，直到不出水了为止\", \"2\": \"然后切成不算太小的块儿（纯属比较过瘾）\", \"3\": \"冬笋洗洗干净切成滚刀块，怕涩的可以焯一下水\", \"4\": \"百叶结洗净泡开（北方只能买到这种看上去很像腐竹的东西……）\", \"5\": \"然后，提前炖上一锅老老的排骨汤头（没有的可以用浓汤宝之类）\", \"6\": \"下入冲洗干净表面盐分的咸肉\", \"7\": \"然后加入笋块儿\", \"8\": \"小火慢炖1小时\", \"9\": \"加入百叶结，然后继续小火慢炖1小时\", \"10\": \"最后根据喜好加点食盐或者完全不加都行~\"}', '{\"咸肉\": \"1块\", \"笋\": \"1个\", \"百叶结\": \"适量\", \"排骨汤\": \"适量\", \"食盐\": \"适量\"}', 9, '2020-02-27 22:28:20', 0, '2020-03-12 19:36:05', '310980');
INSERT INTO `menu` VALUES (332, '赛螃蟹试验版', 41, '{\"1\": \"姜切碎，多切几下，越碎越好。\", \"2\": \"醋，料酒，白糖，盐混合。\", \"3\": \"姜放入调好的汁中搅拌\", \"4\": \"鸡蛋白黄分开，分别打散。\", \"5\": \"成品，这次做的不是很理想，没有想象中受家人喜欢。\"}', '{\"鸡蛋\": \"4颗\", \"姜\": \"一块\", \"白醋\": \"2勺\", \"白糖\": \"1勺\", \"料酒\": \"1勺\", \"盐\": \"1勺\", \"食用油\": \"适量\"}', 8, '2020-02-27 22:28:21', 0, '2020-03-12 19:36:05', '365878');
INSERT INTO `menu` VALUES (333, '地三鲜', 38, '{\"1\": \"土豆切滚刀块裹上淀粉。\", \"2\": \"茄子切滚刀块裹上淀粉。\", \"3\": \"油锅分别炸定型备用。\", \"4\": \"青椒，蒜，葱，切好备用。\", \"5\": \"油热下葱蒜爆香。\", \"6\": \"再加入青椒翻炒。\", \"7\": \"下入土豆块翻炒。\", \"8\": \"加入生抽，盐，老抽调味，在加少许水。\", \"9\": \"加入茄子翻炒。\", \"10\": \"翻炒均匀，汤汁收好即可。\", \"11\": \"出锅。\"}', '{\"土豆\": \"1个\", \"茄子\": \"2个\", \"青椒\": \"1个\", \"盐\": \"适量\", \"淀粉\": \"适量\", \"生抽\": \"适量\", \"老抽\": \"适量\", \"葱\": \"1段\", \"蒜\": \"3个\"}', 10, '2020-02-27 22:28:22', 0, '2020-03-12 19:36:05', '513785');
INSERT INTO `menu` VALUES (334, '红烧肉', 9, '{\"1\": \"准备材料。\", \"2\": \"五花肉切块，放入冷水锅中，加一勺料酒去去腥，撇去浮沫。\", \"3\": \"焯过水的五花肉放入锅内，中小火煸出多余的油份。\", \"4\": \"煸出的多余的油。\", \"5\": \"炒锅中放入适量冰糖，小火融化。\", \"6\": \"倒入煸好的五花肉，翻炒上色。\", \"7\": \"倒入适量清水，差不多与五花肉齐平，放入香叶和八角，倒入适量红烧酱油，大火烧开后转小火慢焖40分钟。\", \"8\": \"时间差不多开盖观察一下上色情况，翻炒加盐调味收汁出锅。\", \"9\": \"成品图。\", \"10\": \"成品图。\", \"11\": \"成品图。\", \"12\": \"成品图。\"}', '{\"五花肉\": \"800克\", \"八角\": \"几粒\", \"冰糖\": \"40克\", \"料酒\": \"1勺\", \"香叶\": \"几片\", \"红烧酱油\": \"适量\", \"盐\": \"适量\", \"姜\": \"少许\", \"蒜块\": \"少许\"}', 9, '2020-02-27 22:28:22', 0, '2020-03-12 19:36:05', '307031');
INSERT INTO `menu` VALUES (335, '番茄锅塌豆腐', 3, '{\"1\": \"豆腐切片。\", \"2\": \"锅中加底油，中火。\", \"3\": \"豆腐煎两面金黄。\", \"4\": \"底油加葱 姜炒香。\", \"5\": \"放入番茄沙司。\", \"6\": \"将豆腐倒入锅中，加入酱油 盐 糖 调味 撒木耳丝加半小碗水炖一下，是豆腐入味，汁瘦收的差不多了，撒蒜末，装盘。\", \"7\": \"装盘可以吃了。\"}', '{\"豆腐\": \"1斤\", \"番茄沙司\": \"适量\", \"木耳\": \"适量\", \"盐\": \"适量\", \"糖\": \"适量\", \"油\": \"适量\", \"酱油\": \"适量\", \"蒜\": \"适量\", \"葱花\": \"适量\"}', 8, '2020-02-27 22:28:22', 0, '2020-03-12 19:36:05', '364665');
INSERT INTO `menu` VALUES (336, '烧茄子', 97, '{\"1\": \"茄子洗净，切成滚刀块裹上生粉。\", \"2\": \"油锅炸至定型出锅。\", \"3\": \"青椒切片。\", \"4\": \"准备适量生抽，老抽，蕃茄酱，少量盐备用。\", \"5\": \"热油下入葱和青椒，炒香。\", \"6\": \"倒入准备好的料汁。\", \"7\": \"下入炸好的茄子块。\", \"8\": \"翻炒均匀即可出锅。\"}', '{\"茄子\": \"2个\", \"青椒\": \"1个\", \"盐\": \"适量\", \"蕃茄酱\": \"适量\", \"生抽\": \"适量\", \"老抽\": \"少量\", \"生粉\": \"少量\"}', 10, '2020-02-27 22:28:23', 0, '2020-03-12 19:36:05', '512513');
INSERT INTO `menu` VALUES (337, '蒸香菇盒', 100, '{\"1\": \"把馅放入盆中拌匀，用筷子把馅放入香菇的中间即可如图。\"}', '{\"猪肉\": \"适量\", \"香菇\": \"适量\", \"酱油\": \"适量\", \"花生油\": \"适量\", \"葱\": \"适量\", \"姜\": \"适量\", \"胡椒面\": \"适量\", \"盐\": \"适量\", \"白糖\": \"适量\", \"鸡蛋\": \"适量\"}', 9, '2020-02-27 22:28:23', 0, '2020-03-12 19:36:05', '306716');
INSERT INTO `menu` VALUES (338, '黄焖鸡米饭', 81, '{\"1\": \"鸡半只洗干净。\", \"2\": \"把鸡剁成中等大小的块。\", \"3\": \"香菇切成条，青辣椒切成条，大蒜切粒，姜切丝。\", \"4\": \"另一边准备好紫菜，肉沫、鸡蛋打散。\", \"5\": \"原料都准备齐全。\", \"6\": \"锅里放油多谢，烧油六成热。\", \"7\": \"放入糖俩勺，用炒勺把它搅拌融化，烧成焦糖色，这一步叫炒糖色，现在鸡肉还不能到下去，否则鸡肉全是甜的无法吃。\", \"8\": \"炒成糖都冒泡可以倒入鸡肉了，糖烧过头也不行，鸡肉会发苦。\", \"9\": \"把鸡肉倒入锅了鸡肉成了焦糖色，非常好看，加盐、料酒、胡椒粉、酱油、姜、大蒜粒与香菇。\", \"10\": \"倒入开水慢慢地焖，焖至九分熟到入青椒，水焖干就可以了。\", \"11\": \"肉沫加入盐搅拌，加入生粉搅拌。\", \"12\": \"干紫菜水泡好，洗干净。\", \"13\": \"用小奶锅烧开水倒入紫菜加入油，把肉搓成丸子一个个放入锅里煮熟加盐与芝麻油即可。\", \"14\": \"黄焖鸡米饭搭配紫菜丸子汤，一顿简单的晚饭就可以开吃了。\"}', '{\"鸡\": \"半只\", \"青椒\": \"1个\", \"香菇\": \"5朵\", \"糖\": \"适量\", \"料酒\": \"少许\"}', 8, '2020-02-27 22:28:24', 0, '2020-03-12 19:36:05', '362159');
INSERT INTO `menu` VALUES (339, '香辣红烧肉', 13, '{\"1\": \"五花肉切小块，冷水焯肉。\", \"2\": \"锅烧热，加少量油，放入肉块煸炒出油，盛出备用。\", \"3\": \"锅中直接放入豆豉，八角，桂皮，葱，姜，干辣椒炒香。\", \"4\": \"放入肉，料酒，酱油，翻炒均匀。加水和冰糖，加盖小火焖一小时，开盖大火收汁即可。\"}', '{\"五花肉\": \"1条\", \"豆豉\": \"一小把\", \"八角\": \"3颗\", \"桂皮\": \"1段\", \"葱\": \"1根\", \"姜\": \"两片\", \"干辣椒\": \"5个\", \"酒\": \"2汤匙\", \"酱油\": \"5汤匙\", \"冰糖\": \"1小把\"}', 9, '2020-02-27 22:28:24', 0, '2020-03-12 19:36:05', '304436');
INSERT INTO `menu` VALUES (340, '红粘豆包', 83, '{\"1\": \"糯红高粱米面，加热水烫面，烫后用筷子搅拌成面块，撒入酵母粉。\", \"2\": \"活成软面团，盖保鲜膜保温、保湿发酵。\", \"3\": \"发酵好的糯红高粱米面，放保鲜袋里保湿。\", \"4\": \"红小豆豆沙。红小豆提前24小时浸泡，等红小豆涨大，上锅蒸熟，捣烂成泥，加糖、蜂蜜调味，放微波炉转几分钟，去除水份。\", \"5\": \"糯红高粱米面拍成园饼，包入红小豆豆沙馅。\", \"6\": \"包成团。\", \"7\": \"垫上玉米叶，蒸锅开后上锅，大火蒸20分钟，关火虚蒸5分钟。\", \"8\": \"成品，趁热吃。\"}', '{\"糯红高粱面\": \"250克\", \"红小豆豆沙\": \"适量\", \"玉米叶\": \"适量\", \"酵母粉\": \"3克\"}', 10, '2020-02-27 22:28:24', 0, '2020-03-12 19:36:05', '447560');
INSERT INTO `menu` VALUES (341, '小鸡炖蘑菇', 79, '{\"1\": \"红薯粉提前浸泡数小时，变软。\", \"2\": \"葱段，姜片，蒜备用。\", \"3\": \"整只鸡分解成快，冷水下锅，焯水。\", \"4\": \"沥水备用。\", \"5\": \"凉锅凉油，放白糖（有冰糖更好，绵白糖，白砂糖均可）。\", \"6\": \"小火煸糖，出褐色。\", \"7\": \"放入鸡肉，煸炒出虎皮色。\", \"8\": \"加生抽，老抽，上色。\", \"9\": \"加葱姜蒜，蘑菇，煸炒几下。\", \"10\": \"加温水，水要末过鸡肉，不可以用冷水，冷水容易让鸡肉变柴，不好吃，再放入炖肉料，盐，料酒，杏干。\", \"11\": \"鸡肉变软，(可以用筷子插进去)，加入红薯粉，不需要翻炒，就平铺在鸡肉上。\", \"12\": \"再炖十多分钟，可以夹断粉条就可以了，我不喜欢汤汁多，就大火收到没有汤汁，喜欢汤汁的，就不需要了。\", \"13\": \"出锅盛盘。\", \"14\": \"撒上香葱，就为了好看，哈哈。\"}', '{\"鸡肉\": \"适量\", \"蘑菇\": \"适量\", \"红薯粉条\": \"适量\", \"葱\": \"适量\", \"姜\": \"适量\", \"蒜\": \"适量\", \"盐\": \"适量\", \"味精\": \"适量\", \"生抽\": \"适量\", \"老抽\": \"适量\", \"炖肉料\": \"适量\", \"杏干\": \"适量\", \"料酒\": \"适量\"}', 10, '2020-02-27 22:28:26', 0, '2020-03-12 19:36:05', '512778');
INSERT INTO `menu` VALUES (342, '秘制红烧肉', 41, '{\"1\": \"准备肥瘦合适的五花肉。我这是从京东生鲜上买的，一盒450g，正好够做一顿的，5号晚上下单，六号一早就送来了\", \"2\": \"五花肉都切成2CM见方的块，太小了吃起来不过瘾，必须块稍微大一点才好。这里的肉块都给切好了，取出后把皮断开就可以了，方便\", \"3\": \"准备好姜片、葱段、大蒜、八角\", \"4\": \"锅里放上清水，把肉洗净后放冷水锅里，上火煮开\", \"5\": \"开锅后去除浮沫，这个肉很干净，浮沫很少，直接炖也没问题，但这样煮过后可以去些油脂\", \"6\": \"煮两分钟后捞出沥水\", \"7\": \"不沾锅上火烧热，改中小火，把肉块放进去，放姜片和八角，一起煸炒出油\", \"8\": \"五花肉里的油脂被煸出来了，肉块表面也被煎的有些发黄\", \"9\": \"用筷子夹出来备用\", \"10\": \"另起一锅。因为我要炖的久一些，所以我改用了铸铁锅，锅里放一小勺油和一大勺清水，把冰糖放进去小火融化\", \"11\": \"冰糖完全融化后再熬一小会儿，观察到颜色变成了焦糖色就可以了\", \"12\": \"把肉块放进去，翻炒均匀，使每个肉块都裹上糖色\", \"13\": \"加入热水，水量没入肉块，放进剩下的姜片、葱段，大火煮开，加一小勺酱油，适量盐，改中小火炖\", \"14\": \"盖上锅盖，中小火炖约45分钟\", \"15\": \"看水量减半的时候，加入大蒜瓣，我家孩子比较喜欢吃红烧肉里的蒜瓣，所以每次我都会加一些，在红烧肉里炖过的蒜瓣也特别好吃\", \"16\": \"待最后汤汁渐少后，要频繁的用铲子翻动\", \"17\": \"使每个肉块都裹住汤汁，收汁完毕即可\", \"18\": \"同时用汤锅烧水，水里加些盐和鲜鸡汁，烫熟小油菜，捞出沥水，铺在盘底\", \"19\": \"美味的红烧肉就可以上桌了。这样一盘，份量刚刚好\", \"20\": \"配碗大米饭，有肉有菜，一人食也很过瘾啊！（拍这张的时候上面的蒜瓣已经被我调皮的闺女夹走了）。肥而不腻，肉里的油脂被煸出来了，经过长时间的小火慢炖，入口即化，微甜，我家的小朋友特别爱吃这口感。每次都说妈妈做的红烧肉是最好吃的！\"}', '{\"猪五花肉\": \"450g\", \"大蒜\": \"适量\", \"葱段\": \"适量\", \"姜片\": \"适量\", \"八角\": \"适量\", \"盐\": \"适量\", \"食用油\": \"适量\", \"清水\": \"适量\", \"冰糖\": \"适量\", \"黄酒\": \"适量\", \"生抽\": \"适量\"}', 9, '2020-02-27 22:28:26', 0, '2020-03-12 19:36:05', '304363');
INSERT INTO `menu` VALUES (343, '宫保鸡丁', 59, '{\"1\": \"鸡脯肉切丁，加一点点酱油，少许料酒，盐，胡椒粉腌制10分钟\", \"2\": \"冷油小火炸花生米，注意油两成热把花生米炸进去就行，一直小火，直到微黄捞出备用\", \"3\": \"胡萝卜，莲菜，黄瓜切丁\", \"4\": \"葱姜蒜切片，干辣椒切段，一点点花椒，备用水淀粉：淀粉，酱油少许，醋，白糖，一点白胡椒粉，调成芡汁\", \"5\": \"倒入适量油，放入花椒一点炒香，然后放红油豆瓣酱炒出红油，我用的是郫县红油豆瓣酱，\", \"6\": \"然后放入干辣椒炒香，放入一半的葱姜蒜\", \"7\": \"然后放入鸡丁炒至然后变白\", \"8\": \"放入蔬菜丁和剩下的葱姜蒜，大火翻炒至熟\", \"9\": \"熟后倒入芡汁，翻炒半分钟，然后加入鸡精\", \"10\": \"出锅啦！酥脆爽口，鲜香扑鼻，辣中味甜，下饭！\"}', '{\"鸡脯肉\": \"300克\", \"胡萝卜\": \"一根\", \"黄瓜\": \"一根\", \"花生米\": \"适量\", \"莲菜\": \"小半根\", \"葱\": \"适量\", \"蒜\": \"适量\", \"姜\": \"适量\", \"辣椒\": \"适量\", \"花椒\": \"适量\", \"酱油\": \"适量\", \"料酒\": \"适量\", \"豆瓣酱\": \"适量\", \"糖\": \"适量\", \"醋\": \"适量\", \"水淀粉\": \"适量\"}', 8, '2020-02-27 22:28:26', 0, '2020-03-12 19:36:05', '358037');
INSERT INTO `menu` VALUES (344, '卤肉土豆丝卷饼', 17, '{\"1\": \"卤肉切片。\", \"2\": \"土豆擦成丝，用清水洗去淀粉沥干。\", \"3\": \"黄瓜切成丝。\", \"4\": \"小葱切段。\", \"5\": \"东北煎饼一张。\", \"6\": \"锅中放油，放入土豆丝翻炒。\", \"7\": \"土豆丝八成熟时放入黄瓜丝翻炒。\", \"8\": \"放入葱段翻炒。\", \"9\": \"加盐、少许生抽调味炒匀盛出。\", \"10\": \"土豆黄瓜丝放在煎饼一端，上面放上卤肉片，卷起。\", \"11\": \"裹着热热的土豆丝，煎饼也变得微软，蛮好吃滴。\"}', '{\"卤肉\": \"适量\", \"黄瓜\": \"适量\", \"土豆\": \"适量\", \"葱\": \"适量\", \"煎饼\": \"适量\", \"盐\": \"适量\", \"生抽\": \"适量\"}', 10, '2020-02-27 22:28:27', 0, '2020-03-12 19:36:05', '468682');
INSERT INTO `menu` VALUES (345, '徽州臭鳜鱼', 27, '{}', '{\"鳜鱼\": \"1条\", \"笋片\": \"适量\", \"白糖\": \"适量\", \"姜片\": \"适量\", \"肉片\": \"适量\", \"酱油\": \"适量\", \"绍酒\": \"适量\", \"青蒜\": \"适量\"}', 9, '2020-02-27 22:28:27', 0, '2020-03-12 19:36:05', '303661');
INSERT INTO `menu` VALUES (346, '蓑衣黄瓜', 62, '{\"1\": \"黄瓜一根洗净，两边各放一只筷子切薄片\", \"2\": \"把底部翻过来切45℃斜刀\", \"3\": \"这是切好的黄瓜\", \"4\": \"加入少许盐腌制五分钟倒出腌制出的多余水份，摆下一盘\", \"5\": \"锅里倒入少许底油下花椒爆香捞出弃用\", \"6\": \"加入蒜末和辣椒段炒香\", \"7\": \"倒入小碗，加入三茶匙生抽，一茶匙半香醋调匀\", \"8\": \"将调好的调味料浇在黄瓜上腌制十分钟即可食用\", \"9\": \"成品\", \"10\": \"成品\"}', '{\"黄瓜\": \"一根\", \"生抽\": \"三茶匙\", \"香醋\": \"一茶匙半\", \"盐\": \"少许\", \"蒜末\": \"适量\", \"辣椒段\": \"适量\", \"花椒\": \"适量\"}', 8, '2020-02-27 22:28:28', 0, '2020-03-12 19:36:05', '352728');
INSERT INTO `menu` VALUES (347, '家常烤冷面', 20, '{\"1\": \"油热后放入冷面一片，小火慢煎，鸡蛋打入后，轻轻地用筷子铺平在冷面上。此刻在锅的周围加一点水，这个是关键哦！\", \"2\": \"加入一勺冷面酱，放一片生菜，满满地把冷面由一边裹向另外一边，成一个卷即可。\", \"3\": \"可以在冷面卷上放上任何喜欢的酱汁哦！\"}', '{\"冷面\": \"1片\", \"鸡蛋\": \"1个\", \"生菜\": \"1片\", \"冷面酱\": \"1勺\"}', 10, '2020-02-27 22:28:29', 0, '2020-03-12 19:36:05', '512013');
INSERT INTO `menu` VALUES (348, '#信任之美#红烧肉', 10, '{\"1\": \"准备五花肉，最好半肥半瘦。\", \"2\": \"将五花肉切块，大小适中。\", \"3\": \"准备调料。\", \"4\": \"锅中倒入菜籽油，油开后倒入除了香料、姜、花椒。\", \"5\": \"倒入五花肉\", \"6\": \"倒入一勺盐。\", \"7\": \"翻炒，炒至五花肉变色微发黄，炒出部分的油。\", \"8\": \"倒入老抽，我用的东坡红，上色会比较好看。\", \"9\": \"翻炒入味。\", \"10\": \"倒入醋，继续翻炒。\", \"11\": \"锅中倒入水，刚好盖入五花肉。中小火煮一小时。\", \"12\": \"倒入冰糖，收干汁水即可。\", \"13\": \"味道真是棒棒哒，肥而不腻，不爱吃肥肉的我也很喜欢呢。\"}', '{\"五花肉\": \"1000克\", \"香料\": \"适量\", \"盐\": \"1勺\", \"冰糖\": \"几粒\", \"醋\": \"半勺\", \"老抽\": \"半勺\", \"花椒\": \"适量\", \"姜\": \"半个\", \"油\": \"适量\"}', 9, '2020-02-27 22:28:29', 0, '2020-03-12 19:36:05', '303110');
INSERT INTO `menu` VALUES (349, '煎粘豆包', 34, '{\"1\": \"红芸豆提前两天凉水浸泡，中间换两次水，红芸豆泡涨大，上蒸锅分两次蒸制，第一次蒸过后捂在热锅里，第二天再蒸一次，红芸豆蒸烂即可，红芸豆捣成豆沙，加入蜂蜜、玫瑰酱调味。\", \"2\": \"红芯红薯蒸熟，趁热放入大黄米面粉里，用筷子搅成软硬适中面块，晾凉后加入泡打粉，揉成面团，盖保鲜膜放温暖处发酵四个小时，取出揉成面团备用。\", \"3\": \"大黄米面取一块，拍成小园饼，包入红芸豆沙馅，收拢团成球，拍成小园饼。\", \"4\": \"热锅凉油，放入小园饼。\", \"5\": \"一面煎硬后翻面。\", \"6\": \"两面煎至微黄，小园饼明显涨大即可。\", \"7\": \"成品，要趁热吃哈。\"}', '{\"大黄米面\": \"适量\", \"红芸豆沙\": \"适量\", \"调和油\": \"适量\"}', 10, '2020-02-27 22:28:30', 0, '2020-03-12 19:36:05', '509137');
INSERT INTO `menu` VALUES (350, '家传红烧肉', 2, '{\"1\": \"五花肉洗净切块，用生抽，盐，油和胡椒粉腌制15-30分钟。\", \"2\": \"热锅，热一点油，放入五花肉，煸一会儿出油。\", \"3\": \"放入适量的白砂糖，煸炒上色。\", \"4\": \"加入适量的水，两滴老抽，适量的盐，一点鸡精，大火烧2-5分钟。\", \"5\": \"加入豆瓣酱和辣椒酱，再加一点点白砂糖，炒匀后改中小火，继续烧3-5分钟，至入汁入味。\", \"6\": \"加入适量的生粉水，使汁粘稠。\", \"7\": \"加入适量的葱花和芝麻，关火起锅。\", \"8\": \"微辣的口感适合下饭，一大口肉，一大口饭，再送一口酒。冬日里难道还有比吃肉更爽的事情吗？\"}', '{\"五花肉\": \"适量\", \"胡椒粉\": \"适量\", \"辣椒酱\": \"适量\", \"油\": \"适量\", \"白砂糖\": \"适量\", \"生抽\": \"适量\", \"芝麻\": \"适量\", \"生粉水\": \"适量\", \"豆瓣酱\": \"适量\", \"葱花\": \"适量\", \"盐\": \"适量\", \"老抽\": \"适量\"}', 9, '2020-02-27 22:28:31', 0, '2020-03-12 19:36:05', '302881');
INSERT INTO `menu` VALUES (351, '红薯黏豆包', 2, '{\"1\": \"红薯蒸熟，压成泥。\", \"2\": \"红薯泥里加糯米粉、小麦粉。\", \"3\": \"和成面团静置一会儿。\", \"4\": \"取大小适中的面团按扁，加豆沙馅（提前做好的豆沙馅）。\", \"5\": \"面皮将豆沙馅包裹严成黏豆包坯。\", \"6\": \"黏豆包入蒸锅蒸熟。\", \"7\": \"出锅即可享用。\"}', '{\"红薯\": \"200g\", \"糯米面\": \"200g\", \"红豆沙\": \"适量\", \"小麦粉\": \"适量\"}', 10, '2020-02-27 22:28:32', 0, '2020-03-12 19:36:05', '511659');
INSERT INTO `menu` VALUES (352, '#信任之美#红烧肉', 19, '{\"1\": \"准备材料\", \"2\": \"五花肉切块\", \"3\": \"锅中倒入适量清水、黄酒、姜片，再放入五花肉\", \"4\": \"冷水下锅沸5分钟捞出沥干备用\", \"5\": \"锅里倒入适量油将冰糖溶化\", \"6\": \"倒入五花肉翻炒\", \"7\": \"放入生姜和八角、香叶翻炒片刻成颜色微微变黄。\", \"8\": \"倒入生抽、老抽、料酒翻炒片刻\", \"9\": \"取一个砂锅把五花肉倒入锅内\", \"10\": \"放上香葱\", \"11\": \"倒入沸水，水量没过五花肉即可\", \"12\": \"大火煮开后用勺子舀出浮沫\", \"13\": \"盖上盖子转小火炖2个小时\", \"14\": \"两人小时后大火收汁，少许鸡精提鲜即可\", \"15\": \"成品\", \"16\": \"成品\"}', '{\"五花肉\": \"500g\", \"生抽\": \"2勺\", \"冰糖\": \"适量\", \"八角\": \"2g\", \"黄酒\": \"2勺\", \"老抽\": \"半勺\", \"香叶\": \"3片\", \"草果\": \"1g\", \"生姜\": \"适量\", \"鸡精\": \"适量\", \"香葱\": \"适量\"}', 9, '2020-02-27 22:28:33', 0, '2020-03-12 19:36:05', '301333');
INSERT INTO `menu` VALUES (353, '地三鲜', 90, '{\"1\": \"茄子，土豆，青椒，大葱，蒜，姜。\", \"2\": \"却成滚刀块，土豆却成滚刀片。\", \"3\": \"不沾锅到入油加热。\", \"4\": \"放入土豆，茄子煎。\", \"5\": \"煎到9分熟，放入青椒，大葱，蒜片，姜片。\", \"6\": \"炒到青椒熟了，不爱吃脆的就多炒一会。\", \"7\": \"翻炒出锅。\", \"8\": \"简单健康美味。\", \"9\": \"成品要比饭店的好，没有那么大的油。\"}', '{\"茄子\": \"2个\", \"土豆\": \"1个\", \"青椒\": \"2个\", \"葱段\": \"适量\", \"蒜瓣\": \"适量\", \"姜片\": \"3片\", \"生抽\": \"一勺\", \"盐\": \"少量\"}', 10, '2020-02-27 22:28:34', 0, '2020-03-12 19:36:05', '511088');
INSERT INTO `menu` VALUES (354, '尖椒鸡蛋闷子', 47, '{\"1\": \"碗内打入4只鸡蛋。\", \"2\": \"加入半勺辣椒粉（按个人口味，也可以省略此步骤）。\", \"3\": \"加入3勺酱、香油少许、色拉油少许，搅匀备用。\", \"4\": \"加入尖椒继续搅匀。\", \"5\": \"锅上气大火蒸18分钟取出完成。\"}', '{\"鸡蛋\": \"4只\", \"尖椒\": \"1只\", \"香油\": \"少许\", \"色拉油\": \"少许\", \"辣椒粉\": \"半勺\", \"许氏大酱\": \"3勺\"}', 10, '2020-02-27 22:28:35', 0, '2020-03-12 19:36:05', '510989');
INSERT INTO `menu` VALUES (355, '酸菜猪肉炖粉条', 60, '{\"1\": \"自家腌制的酸菜，取一个小一点的酸菜反复冲洗干净，切丝。\", \"2\": \"切丝后再冲洗一遍攥掉水分。\", \"3\": \"今天没用太多肉，而且这肉有点肥，因为刚刚煮了一块大骨头，可以直接用来炖酸菜。\", \"4\": \"炒锅加入适量的油，爆香五香粉和葱姜，下入肥肉片翻炒，炒出油来，这菜才香，而且香而不腻。\", \"5\": \"加入酸菜翻炒，酸菜要炒透。\", \"6\": \"买回来的大骨头，洗净提前加入适量的水，花椒粒和料酒入锅煮的差不多熟，然后直接把汤和骨头倒入酸菜锅里，不足在加点水，下入粉条，如果有时间，最好把粉条也提前泡上，更容易煮熟。\", \"7\": \"大火烧开，中火炖至粉条熟。大火收汁，加入盐、少许生抽调味。汤汁不要全部收掉，泡饭非常香。\", \"8\": \"出锅，这一大海碗的菜，全家人一顿吃得精光。\"}', '{\"酸菜\": \"1棵\", \"猪肉\": \"适量\", \"粉条\": \"1把\", \"油\": \"适量\", \"盐\": \"适量\", \"五香粉\": \"适量\", \"生抽\": \"适量\", \"葱\": \"适量\", \"姜\": \"适量\", \"大骨头\": \"1块\"}', 10, '2020-02-27 22:28:37', 0, '2020-03-12 19:36:05', '510286');
INSERT INTO `menu` VALUES (356, '#信任之美#私家红烧肉', 74, '{\"1\": \"猪肉洗净\", \"2\": \"肥瘦肉分别切成见方的块\", \"3\": \"生姜切片，大葱切段，蒜瓣\", \"4\": \"锅放入少许油，加入白糖小火烧至糖溶化，浅咖啡色鱼眼泡状\", \"5\": \"放入猪肉块翻炒\", \"6\": \"加入葱姜大料，加老抽，生抽翻炒至肉表面上色，烹入料酒\", \"7\": \"放入盐和适量水没过猪肉\", \"8\": \"其余的调料放入调料包放入，再次放葱，加入盐，中小火炖\", \"9\": \"继续中小火炖\", \"10\": \"熟后大火烧至锅内汤汁浓稠即可\", \"11\": \"装盘\", \"12\": \"装上一大碗，给老妈送去。。\"}', '{\"五花肉\": \"2000克\", \"白糖\": \"1汤勺\", \"花椒\": \"30粒\", \"小茴香\": \"少许\", \"葱\": \"2根\", \"蒜\": \"4瓣\", \"老抽\": \"1汤勺\", \"料酒\": \"2汤勺\", \"大料\": \"4朵\", \"香叶\": \"1片\", \"桂皮\": \"少许\", \"姜\": \"1块\", \"生抽\": \"适量\", \"盐\": \"适量\"}', 9, '2020-02-27 22:28:37', 0, '2020-03-12 19:36:05', '297752');
INSERT INTO `menu` VALUES (357, '小鸡炖粉条', 6, '{\"1\": \"小鸡择洗干净，去除不能吃的内脏和鸡屁股，鸡肉剁成块，冷水浸泡出血水，捞出控净水分，然后入锅炒，炒至没有水分出锅。\", \"2\": \"炒锅加入适量的油，爆香葱姜和所有的调味料，下入鸡块翻炒。\", \"3\": \"加点料酒翻炒去腥，加入少许生抽翻炒上色。\", \"4\": \"加入适量多一些水，大火烧开，中小火炖半小时，因为后边还要炖粉条，而且小笨鸡比较抗炖，需要多加一些水，炖的时间长。\", \"5\": \"加入粉条，继续小火炖10分钟左右，炖至粉条透明就OK了。\", \"6\": \"粉条炖至透明就可以关火了，加入盐、适量的生抽调味。\", \"7\": \"出锅装盘，香而不腻，老少皆宜，配上白米饭香得不要不要的。\"}', '{\"小鸡\": \"1只\", \"粉条\": \"适量\", \"油\": \"适量\", \"盐\": \"适量\", \"花椒粒\": \"数量\", \"八角\": \"1个\", \"香叶\": \"1片\", \"肉桂\": \"数量\", \"生抽\": \"适量\", \"葱\": \"适量\", \"姜\": \"适量\", \"料酒\": \"适量\"}', 10, '2020-02-27 22:28:38', 0, '2020-03-12 19:36:05', '509314');
INSERT INTO `menu` VALUES (358, '鸡膀根炖土豆', 36, '{\"1\": \"将土豆去皮洗净切块成滚刀块。\", \"2\": \"将鸡膀根洗净用热水略焯一下。\", \"3\": \"热锅凉油放入辣椒，花椒略炸后去除花椒。\", \"4\": \"用炸好的油略炒鸡膀根后加入葱，姜，蒜翻炒倒入适量料酒，酱油，糖翻炒后倒入适量热水加老抽略炖后加入土豆和适量盐盖盖炖到汤汁收尽为止。\", \"5\": \"待汤汁收尽后就可盛出享用了！☺\"}', '{\"鸡膀根\": \"6个\", \"土豆\": \"2个\", \"小辣椒\": \"4个\", \"花椒\": \"适量\", \"盐\": \"适量\", \"酱油\": \"适量\", \"老抽\": \"适量\", \"糖\": \"少量\", \"油\": \"适量\", \"葱\": \"一段\", \"蒜\": \"4瓣\", \"姜\": \"3片\", \"料酒\": \"适量\"}', 9, '2020-02-27 22:28:39', 0, '2020-03-12 19:36:05', '294535');
INSERT INTO `menu` VALUES (359, '铁锅炖大鹅', 12, '{\"1\": \"把鹅剁成小块，上面的是鹅肝。\", \"2\": \"准备调料:八角，桂皮，丁香，草果，花椒，香叶，葱姜切小块。\", \"3\": \"锅中放水，倒入剁好的鹅块，焯水。\", \"4\": \"这是焯过水的鹅。\", \"5\": \"锅中放油，倒入香料炒香。\", \"6\": \"放葱姜爆香。\", \"7\": \"倒入焯好水的鹅块进行煸炒。\", \"8\": \"倒入少许老抽生抽调色。\", \"9\": \"炒香后放入开水，没过鹅块，放入干辣椒，开锅后关小火，炖40分钟。\", \"10\": \"准备一个土豆，去皮切块。\", \"11\": \"鹅肉炖软后放去土豆块继续把土豆炖软就可以了。\", \"12\": \"鹅肉筋道，土豆香甜软糯。\"}', '{\"鹅\": \"一只\", \"葱姜\": \"适量\", \"土豆\": \"一个\", \"花椒\": \"一撮\", \"桂皮\": \"两小块\", \"香叶\": \"两片\", \"草果\": \"一个\", \"八角\": \"两个\", \"丁香\": \"两个\", \"干辣椒\": \"两个\", \"老抽\": \"少许\", \"生抽\": \"少许\"}', 10, '2020-02-27 22:28:40', 0, '2020-03-12 19:36:05', '508704');
INSERT INTO `menu` VALUES (360, '红烧猪蹄', 62, '{\"1\": \"诱惑我~~~诱惑我~~~诱惑我~~~\", \"2\": \"葱切切，姜切切，猪蹄切段后洗净（买的时候直接让人剁好），将猪蹄放入热开水中焯一下，煮出血末后捞出洗净。如果猪脚上有未除干净的毛，此时可以用镊子拔出；锅中放油，烧热后加姜蒜和花椒爆香；\", \"3\": \"然后将猪蹄放入翻炒，待翻炒片刻后，加入老抽、生抽、料酒、冰糖；\", \"4\": \"继续翻炒几下，加入没过猪蹄的热水；\", \"5\": \"将八角、香叶、桂皮调料放入，加适量的盐调味；\", \"6\": \"大火烧开后盖好锅盖转小火，一个小时左右，用筷子戳一下，能够轻松戳透就可以了。（我用的铸铁锅，比较省时，如果觉得不是很烂的话，就继续炖，越烂越好吃。）\", \"7\": \"喜欢吃卤鸡蛋的，可以中途煮好鸡蛋，剥了放进去一起卤，特别好吃~~~\", \"8\": \"一个小时可以炖好了，转大火收干汤汁就可以吃了。出锅撒点葱花。猪蹄在烹调过程中会转化成明胶，放凉后吃也超Q。\"}', '{\"猪蹄\": \"适量\", \"老抽\": \"适量\", \"生抽\": \"适量\", \"料酒\": \"适量\", \"冰糖\": \"适量\", \"香叶\": \"适量\", \"桂皮\": \"适量\", \"八角\": \"适量\", \"盐\": \"适量\", \"葱\": \"适量\", \"姜\": \"适量\", \"蒜\": \"适量\", \"花椒\": \"适量\"}', 9, '2020-02-27 22:28:40', 0, '2020-03-12 19:36:05', '291655');
INSERT INTO `menu` VALUES (361, '小鸡炖蘑菇', 37, '{\"1\": \"榛蘑温水泡30分钟，清水淘洗5遍去除杂质。\", \"2\": \"鸡肉剁成块，冷水下锅加入葱、姜焯水。\", \"3\": \"待水开去除浮沫。\", \"4\": \"焯好的鸡块捞出备用。\", \"5\": \"热锅倒入食用油，葱、姜、八角、花椒、干辣椒等待油热爆香。\", \"6\": \"下鸡块，爆炒后加入十三香、食盐、少量白糖。\", \"7\": \"加入老抽。\", \"8\": \"爆炒后加入泡好的榛蘑。\", \"9\": \"加水没过鸡肉，大火烧开。\", \"10\": \"开锅后转小火慢炖一小时。\", \"11\": \"出锅前10分钟加入粉条。\", \"12\": \"出锅装盘。\"}', '{\"公鸡\": \"1只\", \"榛蘑\": \"500克（干的）\", \"葱\": \"适量\", \"姜\": \"适量\", \"花椒\": \"适量\", \"香叶\": \"适量\", \"干辣椒\": \"适量\", \"老抽\": \"10克\", \"食盐\": \"10克\", \"十三香\": \"5克\", \"白糖\": \"10克\"}', 10, '2020-02-27 22:28:41', 0, '2020-03-12 19:36:05', '509030');
INSERT INTO `menu` VALUES (362, '红烧肉', 41, '{\"1\": \"准备好配料（姜切片，葱切段）\", \"2\": \"五花肉切成小块后焯下血水捞出沥水备用\", \"3\": \"锅里倒入少许食用油烧热，放入冰糖熬制糖色\", \"4\": \"这个程度就可以（小火慢慢熬别糊了）\", \"5\": \"放入五花肉\", \"6\": \"小火炒上色\", \"7\": \"放入姜片，料酒，生抽，八角，花椒翻炒均匀\", \"8\": \"放入少许葱段，加入适量的水没过肉，小火炖35-40分钟\", \"9\": \"炖好后放入少许盐和鸡精，大火收下汤汁既可\"}', '{\"五花肉\": \"500克\", \"葱\": \"少许\", \"姜\": \"少许\", \"冰糖\": \"少许\", \"食用油\": \"少许\", \"花椒\": \"1小把\", \"料酒\": \"2勺\", \"鸡精\": \"少许\", \"水\": \"适量\", \"八角\": \"3个\", \"生抽\": \"少许\", \"盐\": \"少许\"}', 9, '2020-02-27 22:28:41', 0, '2020-03-12 19:36:05', '291213');
INSERT INTO `menu` VALUES (363, '锅包肉', 62, '{\"1\": \"里脊肉切薄大片。\", \"2\": \"从中间切一刀。\", \"3\": \"放容器内。\", \"4\": \"一点点盐增加底味儿（我炸过一次不放盐的，外面酸甜口很好吃，肉也很脆，就是没有一点底味儿吃着别扭。这个根据自己口味选择），土豆淀粉，一点清水。\", \"5\": \"抓匀。\", \"6\": \"锅内油温烧五成热，挂好糊的肉片一片一片下锅炸。\", \"7\": \"三勺陈醋，三勺白糖（喜欢甜点，可稍微多加点），一小勺生抽，一勺清水调成糖醋汁。\", \"8\": \"炸至微黄捞出。\", \"9\": \"油温升至七成热，肉片倒入油中复炸。\", \"10\": \"炸至金黄捞出。\", \"11\": \"葱丝姜丝，蒜片，胡萝卜丝。\", \"12\": \"锅内一点底油烧热，调好的糖醋汁倒入炒至粘稠。\", \"13\": \"炸好的里脊肉片放入。\", \"14\": \"葱姜蒜，胡萝卜放入。\", \"15\": \"翻炒均匀挂糊，关火盛盘。\", \"16\": \"成品。\", \"17\": \"成品。\"}', '{\"里脊肉\": \"适量\", \"土豆淀粉\": \"适量\", \"清水\": \"一勺\", \"盐\": \"可选\", \"陈醋\": \"三勺\", \"白糖\": \"三勺\", \"生抽\": \"一小勺\"}', 10, '2020-02-27 22:28:42', 0, '2020-03-12 19:36:05', '508240');
INSERT INTO `menu` VALUES (364, '红烧肉', 46, '{\"1\": \"猪肉洗净\", \"2\": \"切块\", \"3\": \"备白塘 葱段 姜片 香料\", \"4\": \"肉块入开水焯过\", \"5\": \"捞出冲水\", \"6\": \"白糖入油锅小火炒糖色\", \"7\": \"炒好的糖色\", \"8\": \"肉入锅中小火炒\", \"9\": \"炒匀上色\", \"10\": \"倒入料酒\", \"11\": \"倒入老抽\", \"12\": \"放葱段 姜片\", \"13\": \"放香料煸炒均匀\", \"14\": \"倒开水或温水\", \"15\": \"放盐\", \"16\": \"连肉带汤倒入电压力锅，按排骨键或炖肉键加盖炖制\", \"17\": \"保温键灯亮时肉成熟 排气\"}', '{\"猪肉\": \"1000克\", \"葱段 姜片\": \"适量\", \"葱段姜片\": \"适量\", \"料酒 老抽\": \"适量\", \"草果\": \"半个\", \"八角\": \"2个\", \"茴香\": \"几粒\", \"香叶\": \"一片\", \"桂皮\": \"一块\", \"盐\": \"适量\"}', 9, '2020-02-27 22:28:43', 0, '2020-03-12 19:36:05', '290866');
INSERT INTO `menu` VALUES (365, '粘豆包', 80, '{\"1\": \"食材：糯米粉200克、细玉米面100克、豆沙馅。\", \"2\": \"玉米面与糯米粉混合均匀，加入沸水搅拌成絮状。\", \"3\": \"不烫手的时候揉成光滑的面团松弛20分钟。\", \"4\": \"松弛好的面团在案板上揉匀搓成长条。\", \"5\": \"分成大小均匀的剂子。\", \"6\": \"分好的剂子擀成稍厚一些的面皮，放上适量豆沙馅。\", \"7\": \"包紧后收口朝下稍稍整形。\", \"8\": \"放入蒸锅，上汽后蒸12分钟。（蒸制时间根据年糕的大小调整）\", \"9\": \"蒸好啦，豆沙馅香甜，外皮软糯Q弹，喜欢的可以试一试。\", \"10\": \"成品。\"}', '{\"糯米粉\": \"200克\", \"黄玉米面\": \"100克\", \"豆沙馅\": \"适量\"}', 10, '2020-02-27 22:28:43', 0, '2020-03-12 19:36:05', '508071');
INSERT INTO `menu` VALUES (366, '紫菜饭团', 22, '{\"1\": \"米饭，紫菜撕碎。\", \"2\": \"熟的黑白芝麻。\", \"3\": \"锅中热油，放入紫菜，小火慢炒，炒至酥脆。\", \"4\": \"放入白芝麻，再滴入适量的酱油，拌匀后铲起备用。\", \"5\": \"米饭内放入香油，胡椒粉，辣椒面。\", \"6\": \"拌匀。\", \"7\": \"将米饭按压成饼状，包入紫菜。\", \"8\": \"用手团成小三角的形状。\", \"9\": \"用蚝油，酱油，糖做成一个酱汁。\", \"10\": \"将做好的饭团放入不粘锅中煎，无需再放油。\", \"11\": \"在饭团的表面刷上酱汁。\", \"12\": \"翻面后也刷上料汁，撒上黑芝麻，多翻几次面，让米饭充分受热就完成了。\", \"13\": \"成品。非常香的小饭团，快把家里的紫菜翻出来，妥妥的解决了一顿。\"}', '{\"米饭\": \"适量\", \"无沙紫菜\": \"半张\", \"熟黑芝麻\": \"适量\", \"熟白芝麻\": \"适量\", \"酱油\": \"适量\", \"蚝油\": \"适量\", \"糖\": \"适量\", \"香油\": \"适量\", \"辣椒面\": \"适量\", \"胡椒粉\": \"适量\", \"油\": \"适量\"}', 11, '2020-02-27 22:28:43', 0, '2020-03-12 19:36:05', '514353');
INSERT INTO `menu` VALUES (367, '【家常红烧肉】', 90, '{\"1\": \"带皮五花肉切麻将大小的方块。\", \"2\": \"煮锅加入冷水下五花肉，加入姜片和料酒，大火烧开后煮一分钟。\", \"3\": \"3、捞出五花肉控干水分。\", \"4\": \"炒锅烧热加入植物油，放入冰糖小火慢慢融化，并用筷子不停搅拌，至冰糖变成红色开始起泡泡。\", \"5\": \"倒入五花肉进行煸炒，这时候一定注意五花肉的多余水分控干，要不然会溅油，煸炒至五花肉肥肉油脂析出一部分，并且均匀的包裹糖色。\", \"6\": \"这时候加入1大勺生抽、1大勺老抽再次煸炒至五花肉均匀上色。\", \"7\": \"加入足够的开水，然后加入姜蒜、桂皮、香叶、花椒、八角、干红辣椒、3大勺料酒大火开锅后，小火慢慢煮一个半小时。\", \"8\": \"加入适量盐大火收汁。\", \"9\": \"至汤汁粘稠均匀包裹在每一块肉上。\", \"10\": \"香甜软糯、入口即化的家常版红烧肉就做好了。\", \"11\": \"成品图\", \"12\": \"成品图\"}', '{\"带皮五花肉\": \"适量\", \"盐\": \"适量\", \"生抽\": \"适量\", \"老抽\": \"适量\", \"冰糖\": \"适量\", \"桂皮\": \"适量\", \"香叶\": \"适量\", \"花椒\": \"适量\", \"八角\": \"适量\", \"姜\": \"适量\", \"干红辣椒\": \"适量\", \"料酒\": \"适量\", \"蒜\": \"适量\"}', 9, '2020-02-27 22:28:44', 0, '2020-03-12 19:36:05', '290731');
INSERT INTO `menu` VALUES (368, '色泽金黄，口味酸甜，年夜饭中翘楚轰轰锅包肉', 42, '{\"1\": \"选一块新鲜的猪里脊肉，切成1cm左右厚的肉片。\", \"2\": \"把生姜、大葱、胡萝卜都切成丝备用。\", \"3\": \"用刀背敲击肉片，让肉片变得松散。\", \"4\": \"加入姜丝和白醋，把肉片腌制一下，大概10分钟。\", \"5\": \"200g淀粉加水搅匀。\", \"6\": \"把腌制好的肉片放进水淀粉里裹浆。\", \"7\": \"现在开始调料汁，姜丝、胡萝卜丝，葱丝、白醋、糖、生抽、料酒混合一起，搅拌均匀。\", \"8\": \"起锅烧油，油温6成热后把上好浆的肉片平铺下锅，中小火炸50秒定型。\", \"9\": \"捞出后转大火把油温烧至7成热，再下锅复炸30秒。\", \"10\": \"锅里留少许油，蒜爆香后加入调好的料汁。\", \"11\": \"加入炸好的肉片，把酱汁均匀的炒到肉片上面。\", \"12\": \"锅包肉完成了。\"}', '{\"猪里脊肉\": \"500g\", \"土豆淀粉\": \"200g\", \"胡萝卜\": \"1个\", \"大葱\": \"1个\", \"大蒜\": \"1个\", \"生姜\": \"1个\", \"白糖\": \"60g\", \"白醋\": \"40g\", \"生抽\": \"15g\", \"料酒\": \"30g\"}', 10, '2020-02-27 22:28:44', 0, '2020-03-12 19:36:05', '507742');
INSERT INTO `menu` VALUES (369, '紫甘蓝饭团', 48, '{\"1\": \"准备。\", \"2\": \"米饭中加入寿司醋、盐拌匀。\", \"3\": \"把紫甘蓝入瓦罐中冲细。（可以切细、越细越好）\", \"4\": \"然后放入米饭中拌匀。\", \"5\": \"铺在保鲜膜上。\", \"6\": \"铺上肉松。\", \"7\": \"包菜丝。\", \"8\": \"包裹起来、裹紧点。\", \"9\": \"美丽的色彩。\", \"10\": \"太喜欢了。\"}', '{\"米饭\": \"1碗\", \"紫甘蓝丝\": \"适量\", \"包菜丝\": \"适量\", \"肉松\": \"适量\", \"寿司醋\": \"适量\", \"盐\": \"少许\"}', 11, '2020-02-27 22:28:45', 0, '2020-03-12 19:36:05', '514056');
INSERT INTO `menu` VALUES (370, '肉丝酸菜炒秀珍菇', 78, '{\"1\": \"食材：肉丝、酸菜（已切）、秀珍菇（已清洗）\", \"2\": \"烧锅倒油烧热，下入肉丝翻炒翻炒。\", \"3\": \"接着，加适量的料酒。\", \"4\": \"加适量的生抽。\", \"5\": \"翻炒一下。\", \"6\": \"然后，合入切好的酸菜和已清洗好的秀珍菇翻炒翻炒。\", \"7\": \"最后，加适量的盐。\", \"8\": \"调味炒匀，即成。\"}', '{\"肉丝\": \"65克\", \"酸菜\": \"100克\", \"秀珍菇\": \"125克\", \"料酒\": \"适量\", \"生抽\": \"适量\", \"盐\": \"适量\"}', 9, '2020-02-27 22:28:46', 0, '2020-03-12 19:36:05', '288726');
INSERT INTO `menu` VALUES (371, '五花肉炖粉条', 57, '{\"1\": \"准备材料。\", \"2\": \"五花肉切片，切葱花、朝天椒、姜丝。\", \"3\": \"白菜斜刀切片。\", \"4\": \"热锅凉油，加入葱姜朝天椒爆出香味。\", \"5\": \"加入五花肉小火煸出油。\", \"6\": \"改大火加入白菜翻炒，待白菜微微出水变软。\", \"7\": \"加入生抽调色、加适量盐，加水、加红薯粉条，水要没过食材，大火烧开，中小火炖七八分钟，粉条软烂加鸡精调味即可出锅。\", \"8\": \"上桌了，好吃的五花肉炖粉条，鲜辣q弹。\"}', '{\"白菜\": \"200g\", \"五花肉\": \"80g\", \"红薯粉条\": \"1把\", \"朝天椒\": \"适量\", \"葱\": \"适量\", \"姜\": \"适量\", \"生抽\": \"适量\", \"盐\": \"适量\", \"鸡精\": \"少许\", \"花生油\": \"适量\"}', 10, '2020-02-27 22:28:46', 0, '2020-03-12 19:36:05', '504393');
INSERT INTO `menu` VALUES (372, '牛油果寿司', 62, '{\"1\": \"准备。\", \"2\": \"米饭中加适量盐、寿司醋拌匀。\", \"3\": \"牛油果、火腿肠切长条。\", \"4\": \"取一块海苔、上面铺上火腿肠、牛油果、肉松。\", \"5\": \"卷起来。\", \"6\": \"卷三个。\", \"7\": \"然后再取一块海苔铺上米饭。\", \"8\": \"三分之一处、摆放上二卷裹好的寿司、中间再放肉松。\", \"9\": \"摆放好最后一个裹好的寿司卷。\", \"10\": \"卷起来、紧一点、然后刀沾上水切成段。\", \"11\": \"美味又漂亮的牛油果寿司就做好了、赶紧享用吧。\"}', '{\"米饭\": \"1碗\", \"牛油果\": \"1个\", \"肉松\": \"适量\", \"火腿肠\": \"1个\", \"海苔\": \"4张\", \"寿司醋\": \"适量\", \"盐\": \"适量\"}', 11, '2020-02-27 22:28:46', 0, '2020-03-12 19:36:05', '513504');
INSERT INTO `menu` VALUES (373, '锅包肉', 63, '{\"1\": \"猪肉切块。\", \"2\": \"肉用肉锤，打松散。\", \"3\": \"碗中加一个鸡蛋、白胡椒粉、五香粉、少许盐、料酒、酱油。\", \"4\": \"搅拌均匀。冷藏2小时。\", \"5\": \"放入淀粉。\", \"6\": \"抓均匀。\", \"7\": \"锅中加油，放入肉片炸。金黄捞出。\", \"8\": \"炸好的肉片。\", \"9\": \"复炸一次。\", \"10\": \"准备好的材料番茄沙司葱姜蒜香菜。\", \"11\": \"锅中加油加姜丝。\", \"12\": \"放入番茄酱。\", \"13\": \"加入糖 料酒 酱油 耗油 半碗水。\", \"14\": \"汁熬开。\", \"15\": \"放入炸好的肉。\", \"16\": \"放入葱蒜。\", \"17\": \"装盘撒香菜。\"}', '{\"猪瘦肉\": \"250g\", \"鸡蛋\": \"1个\", \"葱\": \"适量\", \"姜\": \"适量\", \"蒜\": \"适量\", \"香菜\": \"适量\", \"番茄沙司\": \"适量\", \"料酒\": \"1勺\", \"酱油\": \"1勺\", \"蚝油\": \"0.5勺\", \"糖\": \"1勺\", \"淀粉\": \"适量\", \"白胡椒粉\": \"适量\", \"五香粉\": \"适量\", \"盐\": \"适量\"}', 10, '2020-02-27 22:28:47', 0, '2020-03-12 19:36:05', '444911');
INSERT INTO `menu` VALUES (374, '红烧肉', 21, '{\"1\": \"先把食材准备好，五花肉切成块，想吃鸡蛋的可以放两个，再就是一些基本的调味料，需要特别说明的是红曲，没有红曲的用老抽酱油做出来的也很好吃，红曲就是放了之后颜色比较红啦~红曲是红曲米做的，要比老抽更健康天然一点。也可以在炖煮的时候放一些山楂片和陈皮。这样会很好吃。\", \"2\": \"把鸡蛋煮熟，捞出来之后把皮剥了待用\", \"3\": \"五花肉用热水焯一下\", \"4\": \"锅里烧油，放入冰糖融化就可以，不用炒糖色，因为有好多小伙伴之前反应糖色炒不好，所以我们今天准备了红曲，就不用担心炒糖色会炒糊锅了。\", \"5\": \"冰糖融化后，加入肉不停翻炒，一直感觉油有点发粘了，记得用中火和小火炒，然后加葱、姜、蒜、香叶、干辣椒继续翻炒~~\", \"6\": \"炒出调料味儿后，倒入砂锅，要开始炖了~\", \"7\": \"加水没过肉，开锅后，放一点红曲，不用放太多，大概3颗花生豆大小吧。继续加些调味料，老抽放一丢丢，也可以不用、酱油、盐、鸡精，鸡蛋也一起放进去吧。盖上盖，小火炖煮40分钟左右，或者一直小火一直炖到没有多少汤汁，注意鸡蛋不要糊底，现在就等着出锅吧！\", \"8\": \"撒点小葱香菜点缀下，来一碗大米饭。快开吃吧~Q弹软烂，味道真心太销魂~\"}', '{\"五花肉\": \"800g\", \"鸡蛋\": \"2个\", \"红曲\": \"1小勺\", \"酱油\": \"适量\", \"盐\": \"适量\"}', 9, '2020-02-27 22:28:47', 0, '2020-03-12 19:36:05', '286074');
INSERT INTO `menu` VALUES (375, 'XO酱海苔饭卷', 6, '{\"1\": \"芦笋削去根部的老皮，开水焯烫十几秒捞出。\", \"2\": \"西红柿开水烫一下，去皮。\", \"3\": \"把去皮的西红柿切丁，芦笋也切小丁。\", \"4\": \"蒸熟的米饭一碗。\", \"5\": \"锅里放一点点油，放入米饭炒散。用不粘锅炒米饭，这样能用很少的油也不会粘锅。\", \"6\": \"米饭炒散后放入西红柿。我蒸的米饭比较硬，可以再吸收些西红柿的汁。\", \"7\": \"炒到西红柿汁完全被吸收。\", \"8\": \"放入芦笋丁翻炒均匀。\", \"9\": \"放入XO酱。\", \"10\": \"拌均匀即可。\", \"11\": \"把炒好的米饭铺在海苔上，卷起来定型。\", \"12\": \"定型后切段，再次在饭团上放一点XO酱就可以享用啦。\"}', '{\"米饭\": \"1碗\", \"芦笋\": \"50g\", \"西红柿\": \"1个\", \"XO酱\": \"2勺\", \"食用油\": \"少许\"}', 11, '2020-02-27 22:28:48', 0, '2020-03-12 19:36:05', '506670');
INSERT INTO `menu` VALUES (376, '东北饭包', 71, '{\"1\": \"所有的食材洗好备用。葱辣椒香菜\", \"2\": \"土豆煮好拌碎\", \"3\": \"鸡蛋炒八分熟加入黄豆酱少加点水炒好备用\", \"4\": \"葱香菜辣椒切好备用\", \"5\": \"所有的食材放入一起加入米饭拌开\", \"6\": \"看着不是很好看但是吃着非常香\", \"7\": \"包好装盘。\"}', '{\"生菜\": \"适量\", \"葱\": \"适量\", \"辣椒\": \"适量\", \"香菜\": \"适量\", \"米饭\": \"适量\", \"土豆\": \"适量\", \"鸡蛋\": \"适量\", \"黄豆酱\": \"适量\"}', 10, '2020-02-27 22:28:48', 0, '2020-03-12 19:36:05', '501888');
INSERT INTO `menu` VALUES (377, '米饭团', 31, '{\"1\": \"红薯蒸熟。\", \"2\": \"加入少量酸奶压成泥。\", \"3\": \"保鲜膜上放上海苔，海苔上面铺上米饭。\", \"4\": \"米饭中间放上红薯泥。\", \"5\": \"将米饭做成团子，将红薯泥包在里面。\", \"6\": \"将海苔覆盖在团子表面。\"}', '{\"米饭\": \"50g\", \"红薯\": \"10g\", \"海苔\": \"一张\", \"酸奶\": \"少许\"}', 11, '2020-02-27 22:28:49', 0, '2020-03-12 19:36:05', '513364');
INSERT INTO `menu` VALUES (378, '锅包肉', 37, '{\"1\": \"所有材料清洗干净，准备好，开始制作锅包肉。\", \"2\": \"将猪里脊肉切成1块钱硬币厚的肉片，将胡萝卜切、香葱、生姜，分别切成细长的丝状，再将大蒜切成蒜末，装盘备用。\", \"3\": \"准备好一碗清水，加入少许油。\", \"4\": \"再加入适量的玉米淀粉或土豆淀粉，和成稠稠的糊状。\", \"5\": \"将切好的猪里脊肉片，放入和好的淀粉糊中，要稠一点，然后用手或筷子抓拌均匀，使得每一片里脊肉片都均匀地裹上淀粉糊。\", \"6\": \"锅中倒入适量油，油温烧至五成热，将裹满淀粉糊的里脊肉片下入油锅炸一下，小火炸至定型后捞出。\", \"7\": \"转大火复炸一遍，将里脊肉炸透，捞出，控油。\", \"8\": \"锅中倒入少许水，加入半勺白糖，适量的白醋、生抽和蚝油，再加入适量的盐和少许油，小火炒至粘稠。\", \"9\": \"加入蒜末，煸炒出香味。\", \"10\": \"下入炸好的里脊肉片，转大火快速翻炒。\", \"11\": \"然后加入胡萝卜丝、葱丝和姜丝，翻炒均匀，好吃的锅包肉就可以出锅了，赶紧装盘吃起来吧。\", \"12\": \"这道菜就做好了，你学会了吗？赶紧学会它，年夜饭的时候在家人面前露两手，让家人也尝尝这口地道的东北味儿。\"}', '{\"猪里脊肉\": \"200克\", \"生抽\": \"适量\", \"白糖\": \"半勺\", \"胡萝卜\": \"1/4个\", \"大蒜\": \"4瓣\", \"生姜\": \"1小块\", \"香葱\": \"1根\", \"花生油\": \"适量\", \"清水\": \"适量\", \"玉米淀粉\": \"适量\", \"白醋\": \"适量\", \"蚝油\": \"适量\", \"盐\": \"适量\"}', 10, '2020-02-27 22:28:50', 0, '2020-03-12 19:36:05', '494608');
INSERT INTO `menu` VALUES (379, '香煎虾饼', 95, '{\"1\": \"鲜虾拨壳去筋，滴入几滴柠檬，少许盐，白葡萄酒或是料酒，搅拌，放入冰箱冷藏20分钟\", \"2\": \"将洋葱切碎，放锅里炒熟，或是微波炉里加热一两分钟！忘记拍照了，嘻嘻！\", \"3\": \"将腌制好的虾去掉水分，切成虾泥，放入洋葱，盐，黑胡椒，一点点糖，鸡蛋，淀粉，芝士，充分搅拌拌匀！\", \"4\": \"热锅倒入油！用勺子挖一勺虾泥，放入锅中！中小火煎至两面金黄即可！\", \"5\": \"出锅，单吃或配上甜辣酱都非常美味\"}', '{\"基围虾\": \"250g\", \"洋葱\": \"四分之一个\", \"鸡蛋\": \"一只\", \"黑胡椒\": \"少许\", \"盐\": \"2勺\", \"淀粉\": \"少许\", \"白葡萄酒\": \"少许\", \"碎芝士\": \"少许\", \"柠檬\": \"几滴\"}', 9, '2020-02-27 22:28:50', 0, '2020-03-12 19:36:05', '280425');
INSERT INTO `menu` VALUES (380, '薏米紫苏梅子饭团', 95, '{\"1\": \"大米薏米清洗干净。\", \"2\": \"放入电饭锅煮熟。\", \"3\": \"紫苏梅子切碎去核。\", \"4\": \"用保鲜袋或者手套，捏一团米饭，放入梅子。\", \"5\": \"捏成喜欢的形状。\", \"6\": \"包上紫菜即可。\"}', '{\"大米\": \"2杯\", \"薏米\": \"50g\", \"紫苏梅子\": \"2颗\", \"海苔\": \"1片\"}', 11, '2020-02-27 22:28:50', 0, '2020-03-12 19:36:05', '512794');
INSERT INTO `menu` VALUES (381, '地三鲜', 4, '{\"1\": \"备好图中材料。\", \"2\": \"材料如图备好。\", \"3\": \"锅内下油和土豆。\", \"4\": \"煎至金黄色至8分熟。\", \"5\": \"茄子如图拍去干淀粉。\", \"6\": \"放入锅中，煎至微软。\", \"7\": \"盛出备用。\", \"8\": \"锅内下油，放入姜蒜，炒出香味。\", \"9\": \"倒入茄子和土豆。\", \"10\": \"加入适量盐，生抽，半勺醋，适量水，焖3分钟。\", \"11\": \"倒入青椒，炒至断生即可。\", \"12\": \"最后勾薄芡即可。\", \"13\": \"成品图\", \"14\": \"成品图\", \"15\": \"成品图\", \"16\": \"成品图\", \"17\": \"成品图\"}', '{\"茄子\": \"半个\", \"土豆\": \"半个\", \"青椒\": \"1个\", \"蒜米\": \"2颗\", \"姜\": \"5g\"}', 10, '2020-02-27 22:28:51', 0, '2020-03-12 19:36:05', '490974');
INSERT INTO `menu` VALUES (382, '牛油果寿司', 84, '{\"1\": \"大米淘洗后加一杯水蒸熟。我蒸了两碗，一碗不够吃，配料表中是一碗的量。\", \"2\": \"取蒸好的米饭一碗，加寿司醋拌均匀调味。\", \"3\": \"玉米煮熟后放入几个荷兰豆焯烫一下捞出，玉米则剥下一点玉米粒即可。\", \"4\": \"把荷兰豆却小粒，和玉米粒一起用少许生抽调一下味。\", \"5\": \"拌好的米饭平铺在寿司海苔上。\", \"6\": \"下面铺好保鲜膜，然后把铺好米饭的海苔翻转，在海苔的一面，一端摆上拌好的蔬菜粒，两边摆上午餐肉。午餐肉是我自己做的，也是有味道的，不用调味。\", \"7\": \"借助寿司帘卷起来整形。\", \"8\": \"最上面铺上一层牛油果。\", \"9\": \"再用保鲜膜包着定型一下。\", \"10\": \"切段就可以了，我又配了一点自制的寿司姜片，即配色，也佐餐。\"}', '{\"大米\": \"1杯\", \"牛油果\": \"1个\", \"寿司海苔\": \"2张\", \"玉米粒\": \"50g\", \"荷兰豆\": \"50g\", \"寿司醋\": \"2勺\", \"生抽\": \"2g\", \"午餐肉\": \"2片\"}', 11, '2020-02-27 22:28:51', 0, '2020-03-12 19:36:05', '510136');
INSERT INTO `menu` VALUES (383, '圆素鸡烧龙骨 ', 45, '{\"1\": \"食材：龙骨（已清洗焯水）、圆素鸡、桂皮、大料\", \"2\": \"将圆素鸡放在案板上切开。\", \"3\": \"取平底锅置火上倒油烧热，下入切好的圆素鸡两面煎一下。\", \"4\": \"烧锅倒油烧热，下入焯好的龙骨翻炒翻炒。\", \"5\": \"接着，搁入桂皮和大料，加适量的料酒。\", \"6\": \"加适量的清水翻动一下煮开。\", \"7\": \"然后，合入煎好的圆素鸡翻动一下。\", \"8\": \"最后，加适量的生抽。\", \"9\": \"加适量的白糖。\", \"10\": \"加适量的盐。\", \"11\": \"调味煮开。\", \"12\": \"煮至汤汁差不多了，即成。\"}', '{\"龙骨\": \"555克\", \"圆素鸡\": \"190克\", \"桂皮\": \"适量\", \"大料\": \"适量\", \"料酒\": \"适量\", \"生抽\": \"适量\", \"白糖\": \"适量\", \"盐\": \"适量\"}', 9, '2020-02-27 22:28:52', 0, '2020-03-12 19:36:05', '277455');
INSERT INTO `menu` VALUES (384, '东北大炖菜', 94, '{\"1\": \"准备食材，豆角去掉两端洗净。土豆去皮洗净，嫩玉米最好用煮熟的嫩玉米，因为土豆和豆角都很好熟，生玉米需要煮的时间长。辣椒洗净，辣椒很辣，我只用半个。\", \"2\": \"猪肉切薄片，然后用酱油和料酒腌制10分钟，也可以放少许淀粉，腌制后肉质更鲜嫩。\", \"3\": \"嫩玉米切段，土豆且滚刀块，辣椒切块，葱去皮洗净切碎。\", \"4\": \"炒锅加入适量的油，爆香五香粉和葱花，下入腌制好的猪肉翻炒，至肥肉出油。\", \"5\": \"加入豆角、土豆和玉米段翻炒。\", \"6\": \"加入适量的水大火烧开，不要加多，豆角不吃水，中小火炖4~6分钟。\", \"7\": \"打开锅盖，加入辣椒，继续小火炖1~2分钟。\", \"8\": \"关火，加入盐、味极鲜酱油调味。\", \"9\": \"出锅装盘。\"}', '{\"熟嫩玉米\": \"1只\", \"土豆\": \"1个\", \"豆角\": \"200g\", \"猪肉\": \"适量\", \"油\": \"适量\", \"盐\": \"适量\", \"五香粉\": \"适量\", \"味极鲜酱油\": \"适量\", \"料酒\": \"适量\", \"辣椒\": \"半个\", \"葱\": \"适量\"}', 10, '2020-02-27 22:28:52', 0, '2020-03-12 19:36:05', '472372');
INSERT INTO `menu` VALUES (385, '安徽特色--葫芦膜膜', 98, '{\"1\": \"这种葫芦和单葫芦都行，忘记拍照了下载的\", \"2\": \"葫芦切粗条先放盐腌制几分钟，再放入面粉和鸡蛋调料搅拌均匀\", \"3\": \"下锅煎，火不要太大，慢火煎至熟透\", \"4\": \"成品好了，卖相不好味道很赞\"}', '{\"葫芦\": \"1个\", \"面粉\": \"适量\", \"盐\": \"少许\", \"鸡蛋\": \"2个\"}', 9, '2020-02-27 22:28:53', 0, '2020-03-12 19:36:05', '276841');
INSERT INTO `menu` VALUES (386, '东北小吃自制焖子', 22, '{\"1\": \"地瓜粉一碗。\", \"2\": \"2碗水搅匀，水和粉2:1。\", \"3\": \"倒入电饭锅慢慢搅动。\", \"4\": \"不停的搅。\", \"5\": \"直到搅拌成透明。\", \"6\": \"晾凉倒入容器里冰箱冷藏3-5小时。\", \"7\": \"可以切小块用油煎焦黄，加醋，生抽，麻酱，香菜，蒜末，辣椒油拌匀即可。\"}', '{\"地瓜粉\": \"1碗\", \"水\": \"2碗\"}', 10, '2020-02-27 22:28:54', 0, '2020-03-12 19:36:05', '468263');
INSERT INTO `menu` VALUES (387, '米饭寿司', 53, '{\"1\": \"香肠切条，备用。\", \"2\": \"鸡蛋黄加玉米淀粉混合成蛋糊，摊成蛋饼，切成条备用。\", \"3\": \"海苔打湿，打软。\", \"4\": \"米饭平摊在海苔上，用铲子压平、压结实，挤上沙拉酱摊平。\", \"5\": \"切好的火腿肠，蛋饼放到海苔的一头，寿司帘卷起来。\", \"6\": \"切好摆盘，挤上番茄酱装饰下。\"}', '{\"米饭\": \"适量\", \"海苔\": \"适量\", \"火腿肠\": \"适量\", \"沙拉酱\": \"适量\", \"番茄酱\": \"适量\", \"鸡蛋\": \"适量\"}', 11, '2020-02-27 22:28:54', 0, '2020-03-12 19:36:05', '511170');
INSERT INTO `menu` VALUES (388, '椒盐蘑菇', 25, '{\"1\": \"洗干净平菇，撕成小条，捏干水分，捏干，使劲捏干，懂吗？！我老公就是不懂，让他捏个蘑菇，我还要再复捏一遍，唉……捏干水分才能炸出来够脆，重点是炸的时候你才不会成为麻子脸。\", \"2\": \"我是个懒人，所以这样操作，打个蛋进去，然后边搅拌边撒面粉知道蘑菇上都裹上面浆，同时也撒盐进去，别问我加多少盐，不知道你有多少蘑菇，自己尝，淡了就继续放，不过原则上宁淡勿咸，淡了可以补救，不过盐放少了感觉炸出来香味会淡，自己看着办吧～\", \"3\": \"这样拍出来看起来浆不多，实际是够了\", \"4\": \"我觉得炸蔬菜必须大火，这样不兜油，蘑菇用筷子下锅，自己家炸东西，油用的少，只能少量多次来，用笊篱推推蘑菇，感觉有些干涩感，蘑菇也金黄色了，改中小火，就用筷子把蘑菇捡到笊篱里，这个过程油就控得差不多了。\", \"5\": \"蘑菇炸出来就好香了，这时趁热可以撒花椒粉或黑胡椒粉或孜然或辣椒粉或番茄沙司都是极好的，如果盐少了，也可以捏点盐撒上去。这菜在大学里算是肉菜，嘿嘿，我每见必打，就喜欢这样香香的素菜。\"}', '{\"平菇\": \"适量\", \"鸡蛋\": \"适量\", \"花椒粉\": \"适量\", \"盐\": \"适量\"}', 9, '2020-02-27 22:28:54', 0, '2020-03-12 19:36:05', '275427');
INSERT INTO `menu` VALUES (389, '东北大丰收', 93, '{\"1\": \"备齐食材，土豆去皮洗净，胡萝卜去皮洗净，玉米是冻灾冰箱里的熟玉米。\", \"2\": \"排骨冷水泡出血水，冷水入锅加葱姜焯水后捞出过冷水控净水分（过冷水后再炖肉质紧实）。\", \"3\": \"炒锅加入适量的油，爆香所有调料，下入排骨翻炒，喷点料酒，加入适量的辣椒酱翻炒。\", \"4\": \"加入适量多的水，有老汤更好，再撒点葱姜，大火烧开，中火炖10分钟左右。\", \"5\": \"土豆切滚刀块，胡萝卜切滚刀块，玉米切段儿备用。\", \"6\": \"排骨基本炖熟，下入玉米、土豆块、胡萝卜块和豆角，继续炖5-8分钟左右，炖熟即可。\", \"7\": \"按照自己喜欢的口味加入适量的盐、生抽调味，要少加，辣椒酱本身有咸度。\", \"8\": \"出锅装盘，因为加了辣椒酱，微辣的口感，特别下饭。\"}', '{\"猪排骨\": \"400g\", \"玉米\": \"1只\", \"胡萝卜\": \"1根\", \"土豆\": \"1个\", \"豆角\": \"300g\", \"油\": \"适量\", \"盐\": \"适量\", \"自制辣椒酱\": \"适量\", \"料酒\": \"适量\", \"花椒\": \"适量\", \"八角\": \"1个\", \"香叶\": \"1片\", \"葱\": \"适量\", \"姜\": \"适量\"}', 10, '2020-02-27 22:28:55', 0, '2020-03-12 19:36:05', '467576');
INSERT INTO `menu` VALUES (390, '梅干菜蒸五花肉', 100, '{\"1\": \"为了方便，买的超市切好的五花肉片\", \"2\": \"自家晒的新霉干菜，味道偏咸，故用量不多，也没再加盐调味\", \"3\": \"五花肉片先焯水\", \"4\": \"炒锅中加少量油，焯过水的五花肉入油锅中火煸一下，待肥肉颜色微微发黄，改小火，加入霉干菜和适量白糖，翻炒2下后马上加入1小碗清水。\", \"5\": \"炒锅中加少量油，焯过水的五花肉入油锅中火煸一下，待肥肉颜色微微发黄，改小火，加入霉干菜和适量白糖，翻炒2下后马上加入1小碗清水。\", \"6\": \"待水收干，装碗内，入高压锅内蒸半小时。\", \"7\": \"成品图，两个人份量，图方便没有另外装盘！\"}', '{\"五花肉\": \"360g\", \"梅干菜\": \"40g\", \"糖\": \"适量\"}', 9, '2020-02-27 22:28:55', 0, '2020-03-12 19:36:05', '271911');
INSERT INTO `menu` VALUES (391, '东北大冷面', 49, '{\"1\": \"成品图！\", \"2\": \"冷面提前浸泡2个小时！\", \"3\": \"调汁配料一人份:白糖5克，九度白醋2.5克，香油一滴，味极鲜酱油一勺，老抽一点点调色，味素 盐适量，雪碧少许！\", \"4\": \"冰镇的矿泉水250克，颜色是不是很漂亮！\", \"5\": \"适量香菜，葱花，蒜，黄瓜丝，火腿！\", \"6\": \"鸡蛋提前煮熟，冷面冷水下锅煮熟！\", \"7\": \"过冷水，多冲洗几次，面吃的时候才劲道！\", \"8\": \"面倒入碗中，倒入调好的汁，放入火腿 鸡蛋 葱 蒜 香菜 西红柿片，加入适量辣椒油，虾皮，芝麻，制作完成！\", \"9\": \"吃的时候就几瓣大蒜，味道鲜美！\", \"10\": \"世间万物，唯有美食与爱，不可辜负。携所爱之人，赴一场美食之约！\"}', '{\"冷面\": \"适量\", \"香肠\": \"一根\", \"黄瓜\": \"适量\", \"西红柿\": \"适量\", \"香菜\": \"适量\", \"蒜\": \"适量\", \"葱\": \"适量\", \"芝麻\": \"适量\", \"虾皮\": \"适量\", \"白糖\": \"5克\", \"9度的白醋\": \"2.5克\", \"香油\": \"一滴\", \"老抽\": \"一点\", \"味极鲜酱油\": \"一勺\", \"盐\": \"适量\", \"雪碧\": \"少许\", \"味素\": \"适量\", \"冰镇矿泉水\": \"250克\", \"辣椒油\": \"适量\"}', 10, '2020-02-27 22:28:56', 0, '2020-03-12 19:36:05', '467557');
INSERT INTO `menu` VALUES (392, '日式猪排家庭免炸版', 52, '{\"1\": \"选取Panko，日式面包屑。\", \"2\": \"加少许植物油（我用的橄榄油）将panko炒至变色。\", \"3\": \"小盘装两大勺面粉。\", \"4\": \"一个鸡蛋打散备用。\", \"5\": \"用肉锤或刀背在大里脊块两面敲打后撒盐和胡椒。\", \"6\": \"先把肉沾上面粉，裹上鸡蛋，再放在烤好的panko里沾满，放至烤箱400F/200C烤20分钟。\"}', '{\"猪排\": \"2块\", \"panko\": \"6勺\", \"面粉\": \"2勺\", \"盐\": \"1匙\", \"胡椒粉\": \"1匙\"}', 11, '2020-02-27 22:28:56', 0, '2020-03-12 19:36:05', '467570');
INSERT INTO `menu` VALUES (393, '椒盐鱼柳', 84, '{\"1\": \"首先把鱼尾片开\", \"2\": \"把鱼肉切成长条\", \"3\": \"用少许盐，料酒抓一下，这是为了去腥，盐不能放多哦，不然就成了腌咸鱼了\", \"4\": \"腌大概10分钟左右开始油炸，用中火炸\", \"5\": \"炸好出锅就撒上椒盐，非常酥、脆、香、很简单易行、不妨试试。\", \"6\": \"成品图\"}', '{\"草鱼\": \"1段\", \"盐\": \"1克\", \"料酒\": \"1勺\", \"色拉油\": \"300克\"}', 9, '2020-02-27 22:28:56', 0, '2020-03-12 19:36:05', '269925');
INSERT INTO `menu` VALUES (394, '贴饼子炖小黄鱼', 32, '{\"1\": \"新鲜的小黄鱼。\", \"2\": \"去头、去肠洗干净，沥干水。\", \"3\": \"取适量黄豆面、玉米面、豆腐渣。\", \"4\": \"加少许泡打粉，和成面团，静置10分钟。\", \"5\": \"用酱油、醋、料酒、盐、白糖勾兑成汁。\", \"6\": \"鱼裹上面粉。\", \"7\": \"入油锅煎至金黄后盛出。\", \"8\": \"起油锅，葱姜蒜、大料入锅煸香。\", \"9\": \"煎好的鱼入锅。\", \"10\": \"倒入调味汁，再加适量水。\", \"11\": \"开锅后，将面团做成大小适中的饼子贴在锅上。盖上锅盖炖。\", \"12\": \"饼子熟，鱼很入味，即可一起出锅享用。\"}', '{\"小黄鱼\": \"500g\", \"豆腐渣\": \"80g\", \"玉米面\": \"100g\", \"黄豆面\": \"100g\", \"油\": \"适量\", \"盐\": \"适量\", \"酱油\": \"适量\", \"醋\": \"适量\", \"料酒\": \"适量\", \"白糖\": \"适量\", \"葱姜蒜\": \"适量\", \"大料\": \"适量\"}', 10, '2020-02-27 22:28:57', 0, '2020-03-12 19:36:05', '463321');
INSERT INTO `menu` VALUES (395, '粢饭包油条', 66, '{\"1\": \"准备好食材（还有老干妈豉油，可用可不用）\", \"2\": \"把热的蒸好的糯米饭均匀的铺在保鲜膜上\", \"3\": \"刷上甜辣酱\", \"4\": \"放上半根油条，适量的肉松\", \"5\": \"再放上一个熟的咸鸭蛋，刷上少许老干妈豉油，然后用保鲜膜慢慢包起来，卷紧\", \"6\": \"这是卷好的粢饭团，趁热吃，糯，香...\", \"7\": \"成品图\", \"8\": \"成品图\"}', '{\"糯米饭\": \"1大碗\", \"甜辣酱\": \"适量\", \"熟咸鸭蛋\": \"2个\", \"油条\": \"1根\", \"肉松\": \"适量\", \"老干妈豉油\": \"少许\"}', 9, '2020-02-27 22:28:57', 0, '2020-03-12 19:36:05', '268306');
INSERT INTO `menu` VALUES (396, '三文鱼西蓝花', 98, '{\"1\": \"西蓝花折断洗干净。\", \"2\": \"新鲜三文鱼准备一点备用。\", \"3\": \"切成薄片即可。\", \"4\": \"西蓝花摆盘。\", \"5\": \"好看的摆盘。\", \"6\": \"摆入三文鱼。\", \"7\": \"成品图。\", \"8\": \"减肥菜谱，美味可口。\"}', '{\"西蓝花\": \"适量\", \"三文鱼\": \"适量\", \"盐\": \"少许\"}', 11, '2020-02-27 22:28:59', 0, '2020-03-12 19:36:05', '508370');
INSERT INTO `menu` VALUES (397, '农家一锅出', 31, '{\"1\": \"豆角洗净去筋掰断，五花肉切片，新鲜土豆刮去皮洗净备用。贝贝瓜去瓤切块，土豆切两半，玉米切段。\", \"2\": \"起锅烧油，我用了一半猪油，一半食用油。\", \"3\": \"放入豆角和五花肉一起翻炒。\", \"4\": \"放大料。\", \"5\": \"豆角变色放一勺甜面酱，一勺东古豆瓣酱。\", \"6\": \"炒均匀放水，水稍微多些，放一小勺盐，一小勺糖。\", \"7\": \"土豆南瓜玉米统统放进去。\", \"8\": \"水烧开调中火炖，土豆熟了就好了，成品。\", \"9\": \"看看这起沙的土豆。\"}', '{\"豆角\": \"700g\", \"五花肉\": \"适量\", \"嫩玉米\": \"一个\", \"贝贝瓜\": \"一个\", \"新鲜土豆\": \"两个\", \"甜面酱\": \"一勺\", \"大料\": \"一个\", \"豆瓣酱\": \"一勺\", \"糖\": \"一小勺\", \"盐\": \"一小勺\"}', 10, '2020-02-27 22:28:59', 0, '2020-03-12 19:36:05', '457240');
INSERT INTO `menu` VALUES (398, '咸蛋黄夹心饭团', 73, '{\"1\": \"咸蛋黄喷酒，烤箱150度烤5分钟去腥。我烤的多，其他的做蛋黄酥了。\", \"2\": \"用几个咸蛋黄打碎，用油炒香，加糖调味，做成蛋黄酱备用。\", \"3\": \"甜菜根洗净去皮切块，米加1.2倍的水，和甜菜根一起上笼水开后蒸25分钟。蒸熟后甜菜根里边会有部汤汁，就用这个汤汁染米饭。\", \"4\": \"把甜菜根的汤汁加入米饭里面。\", \"5\": \"拌均匀把米饭染成红色。\", \"6\": \"饭团模具底部先铺一层红米饭。\", \"7\": \"再加上一大勺咸蛋黄酱压实。\", \"8\": \"最上面再铺上米饭压实脱模。\", \"9\": \"为了方便拿取底部可以用海苔垫一下。\", \"10\": \"吃了一半儿的里边儿满满的咸蛋黄酱很好吃。\"}', '{\"大米\": \"1杯\", \"咸蛋黄酱\": \"1勺\", \"甜菜根\": \"适量\"}', 11, '2020-02-27 22:29:00', 0, '2020-03-12 19:36:05', '508167');
INSERT INTO `menu` VALUES (399, '香煎东北粘豆包', 94, '{\"1\": \"这是网购的粘豆包，是粘大黄米面做的，颜色金黄，黏糯而有弹性的哪一种。\", \"2\": \"锅中加入适量的油，下入粘豆包，煎至用铲子能推动为好，最好用平底锅来煎，我在外地没有平底锅。\", \"3\": \"一面煎好后，翻面煎。\", \"4\": \"翻面煎至用铲子能推动时，加入半碗水，盖上锅盖，焖至无水就好。\", \"5\": \"无水后再翻动煎至表面脆脆的就好了，如果喜欢还可以用勺子敲打成年糕饼，更好吃，就是麻烦一点点。\", \"6\": \"出锅装盘，蘸白糖吃香甜软糯，太好吃了。\"}', '{\"粘豆包\": \"10个\", \"油\": \"适量\", \"白糖\": \"适量\"}', 10, '2020-02-27 22:29:00', 0, '2020-03-12 19:36:05', '448900');
INSERT INTO `menu` VALUES (400, '香菇板栗烧子鸡', 48, '{\"1\": \"选用上好的走地童子鸡，切块焯水，去掉血沫。\", \"2\": \"焯鸡的同时 准备好配料。香菇打米字花刀，切姜片，香菇蒂切碎，小葱打结，准备桂皮、丁香、香叶\", \"3\": \"鸡焯好后取出备用，锅中放少许底油，加适量冰糖，一定要小火哦。\", \"4\": \"加入冰糖后铲子不停搅动，待冰糖完全融化后开始起大泡，马上下入焯好的鸡块\", \"5\": \"鸡块入锅立即翻炒，糖色开始显色后……\", \"6\": \"加入香叶、桂皮、丁香翻炒出香味……\", \"7\": \"加入香菇蒂碎、姜片、小葱翻炒，加料酒烹香（全程不加花椒、八角）\", \"8\": \"所有材料烹香后，加入没过鸡的水，加生抽一勺，老抽小半勺，蚝油半勺，鸡精适量（我没有加盐，咸度已经够了）开始炖煮\", \"9\": \"水开后下入板栗、香菇，转小火炖1小时\", \"10\": \"1小时候后所有食材已经成熟，转大火收汤汁，待汤汁粘稠后关火。\", \"11\": \"装盘上桌啦\", \"12\": \"满屋飘香的香菇板栗烧子鸡就做好啦，赶快叫家人一起吃饭啦！\"}', '{\"板栗\": \"300克\", \"香菇\": \"6朵\", \"走地童子鸡\": \"1只\", \"小葱\": \"两三根\", \"老抽\": \"适量\", \"桂皮\": \"一段\", \"鸡精\": \"少许\", \"冰糖\": \"少许\", \"姜片\": \"4-5片\", \"生抽\": \"适量\", \"香叶\": \"2片\", \"丁香\": \"少许\", \"蚝油\": \"适量\"}', 9, '2020-02-27 22:29:00', 0, '2020-03-12 19:36:05', '260922');
INSERT INTO `menu` VALUES (401, '大丰收东北菜', 49, '{\"1\": \"东北油豆角是东北的特产，冬季的口感要差一点，运输的途中有些冻了，豆角上可以看得出来了。\", \"2\": \"东北的土豆又粉又糯，特别适合做这道菜。土豆要先削皮洗干净，切成菱形小块备用，如果是炖土豆可以切成大块的。豆角我喜欢掰成小块的，按照个人的喜好吧。\", \"3\": \"葱花儿爆锅，爆出香味开水炒土豆块了，控制火候为中火。\", \"4\": \"不停的翻炒土豆块，别忘记火候一直保持中火，土豆块翻炒5分钟左右就会熟一半了。\", \"5\": \"继续翻炒，不要加水之类的，注意要调整下火候，别炒糊了，外焦里生的。经过十几分钟的翻炒土豆已经熟了。我们下一步炒豆角了。\", \"6\": \"热油下锅油豆角翻炒，还是火不要开得太大了，豆角的水分不是很大。\", \"7\": \"小火翻炒到油豆角变色。\", \"8\": \"加生抽酱油翻炒，油豆角颜色会变色。\", \"9\": \"翻炒豆角不时的加一点点水，注意豆角熟的快一些，要炒15分钟左右豆角就熟了，如果不知道熟了没有用锅铲铲一下就知道了。\", \"10\": \"炒好的豆角倒进炒好的土豆锅里加盐，翻炒均匀出锅了。\", \"11\": \"简单的美味。看着很诱人吧，软糯醇香的东北菜来了！喜欢吃的亲们做起来吧。\"}', '{\"油豆角\": \"300克\", \"土豆\": \"2个\", \"植物油\": \"二勺\", \"生抽\": \"一勺\", \"盐\": \"适量\", \"葱花\": \"适量\"}', 10, '2020-02-27 22:29:01', 0, '2020-03-12 19:36:05', '442575');
INSERT INTO `menu` VALUES (402, '肉松寿司饭团', 20, '{\"1\": \"米饭蒸熟，要蒸的稍干一点点。\", \"2\": \"取需要量的米饭，加入寿司醋拌均匀。\", \"3\": \"自制的海苔牛肉松备好。\", \"4\": \"取一团米饭，压扁，放上一小勺肉松。\", \"5\": \"为了口感更丰富，我放上量两段拌好的贡菜。\", \"6\": \"包严实，捏成三角形。\", \"7\": \"海苔纵向剪开成两片，贴在饭团表面即可。\"}', '{\"米饭\": \"1小碗\", \"肉松\": \"4勺\", \"海苔\": \"2包\", \"寿司醋\": \"2小勺\", \"贡菜\": \"适量\"}', 11, '2020-02-27 22:29:01', 0, '2020-03-12 19:36:05', '506979');
INSERT INTO `menu` VALUES (403, '笋干菜', 84, '{\"1\": \"新鲜的雪里蕻～\", \"2\": \"在阳光下晾晒一下去去水份～\", \"3\": \"晒好的菜堆在一起，上面盖层膜让它自然风干几天，直至叶子有点转黄～\", \"4\": \"风干几天后的雪里蕻，这样就可以清洗了～\", \"5\": \"冲洗干净～\", \"6\": \"洗好的菜晾干水份～\", \"7\": \"晾干的菜切成2.5厘米左右的小段～\", \"8\": \"20斤菜六两盐腌制起来，腌制可以用手搓也可以用脚踩，腌制好后上面用重物压置～\", \"9\": \"经过20天的腌制～\", \"10\": \"金黄色的雪里蕻诱人吧？\", \"11\": \"毛笋去皮待用～\", \"12\": \"切成小片～\", \"13\": \"一层毛笋一层菜～\", \"14\": \"最后放适量盐跟水～\", \"15\": \"上灶大火煮透～\", \"16\": \"煮好的笋干菜放在凉席上晾晒～\", \"17\": \"大太阳的话只要一天半时间就晾晒好了～\", \"18\": \"这个是笋干的晾晒～\", \"19\": \"晾晒好的笋干菜，鲜嫩～\", \"20\": \"有人说我们的笋干菜跟绍兴的梅干菜差不多，个人觉得还是有区别的～\", \"21\": \"笋干菜配上我们当地特有的炸面，那味道别提多美味了～\"}', '{\"雪里蕻\": \"20斤\", \"盐\": \"适量\", \"毛笋\": \"20斤\"}', 9, '2020-02-27 22:29:01', 0, '2020-03-12 19:36:05', '260765');
INSERT INTO `menu` VALUES (404, '春暖花开', 92, '{\"1\": \"准备食材，菠菜、黄瓜、胡萝卜、发芽葱洗净，干豆腐备好。\", \"2\": \"锅里坐水烧开，下菠菜焯烫一下。\", \"3\": \"焯好的菠菜过凉水备用\", \"4\": \"胡萝卜去皮，和黄瓜分别擦成丝，发芽葱切丝（一定要用发芽葱，够味）\", \"5\": \"干豆腐铺开，在一头放上胡萝卜丝、黄瓜丝、葱丝\", \"6\": \"卷起来，卷两三圈即可，一定要卷紧，否则切时容易散花。如果怕切段时散花，可以把干豆腐多卷几圈\", \"7\": \"如图，斜着下刀，切段\", \"8\": \"码放在盘子周围，形成花瓣\", \"9\": \"中间放上焯熟的菠菜\", \"10\": \"锅里注油烧热，油可以稍微多一点，直接打入鸡蛋，不用事先搅散（鸡蛋打成蛋液也行哈）\", \"11\": \"用铲子把鸡蛋快速绞碎\", \"12\": \"加1勺黄豆酱。做鸡蛋酱有诀窍:四个字“蛋多酱少”，酱只是替代盐调味的，千万不要多加酱，颜色不好看，口感也特别咸，一顿吃不了下顿没法看更不爱吃，就是个扔。\", \"13\": \"快速炒匀，加点葱花\", \"14\": \"炒匀即可\", \"15\": \"把鸡蛋酱放到菠菜上即可\", \"16\": \"上桌了，食材还是那些食材，吃法还是蘸酱，只不过摆下盘，立马颜值爆表，食欲大增。\"}', '{\"干豆腐\": \"2张\", \"胡萝卜\": \"1小根\", \"黄瓜\": \"1根\", \"菠菜\": \"1小把\", \"发芽葱\": \"1棵\", \"鸡蛋\": \"2个\", \"豆瓣酱\": \"1勺\"}', 10, '2020-02-27 22:29:02', 0, '2020-03-12 19:36:05', '441865');
INSERT INTO `menu` VALUES (405, '儿时的美味记忆【红烧石蛙】', 59, '{\"1\": \"石蛙去皮去内脏，斩去脚爪后剁成大块。大腿骨用刀背敲断，做出的石蛙腿就不会笔直一根。加入适量的盐用手抓均后，腌制10分钟（腌制时石蛙肉有轻微跳动是神经反应，无需多想）。\", \"2\": \" 五花肉切成肉丁备用；摘几片新鲜紫苏叶切成丝备用；姜粒、韭菜花、大蒜子、红椒圈各适量。\", \"3\": \"锅里去少量的油再倒入五花肉小火煸炒，炒出五花肉的油份。加适量的水转大火煸炒，进一步逼出油份（这样炒出的五花肉肥而不腻）。倒入姜粒、大蒜子、红椒圈，大火煸炒，要炒去辣椒的燥辣，需要加入适量的水一起煸炒。加适量的盐后再加适量的水，将辣椒炒香炒甜。\", \"4\": \"水气炒干后倒入石蛙，倒一勺二锅头酒在石蛙上面，盖上锅盖大火焖10秒，再将石蛙炒至变色，最后加入适量的老抽翻炒上色。加水平到石蛙，加适量的胡椒粉炒均后大火焖煮8分钟。\", \"5\": \"8分钟后加入适量的鸡精和蒸鱼豉油，倒入紫苏叶和韭菜花翻炒均匀，再淋入适量生粉水，让汤汁更浓郁。汤汁快收干时关火，美味即成。\", \"6\": \"成品图\"}', '{\"石蛙\": \"适量\", \"五花肉\": \"适量\", \"紫苏叶\": \"适量\", \"韭菜花\": \"适量\", \"红椒圈\": \"适量\", \"姜粒\": \"适量\", \"大蒜子\": \"适量\", \"二锅头酒\": \"适量\", \"胡椒粉\": \"适量\", \"盐\": \"适量\", \"老抽\": \"适量\", \"鸡精\": \"适量\", \"蒸鱼豉油\": \"适量\", \"生粉水\": \"适量\"}', 9, '2020-02-27 22:29:03', 0, '2020-03-12 19:36:05', '260145');
INSERT INTO `menu` VALUES (406, '龙虾刺身', 61, '{\"1\": \"龙虾剥壳。\", \"2\": \"取好肉，备用。\", \"3\": \"切片，摆盘。\", \"4\": \"成品。\"}', '{\"龙虾\": \"一只\", \"芥末\": \"适量\", \"酱油\": \"适量\"}', 11, '2020-02-27 22:29:03', 0, '2020-03-12 19:36:05', '507687');
INSERT INTO `menu` VALUES (407, '梅干菜饼', 28, '{\"1\": \"肉末选肥瘦相间的最好，梅干菜冷水泡软洗去泥沙。\", \"2\": \"将开水加入面粉中，用筷子搅拌成絮状。\", \"3\": \"揉成均匀光滑面团，包上保鲜膜放至30分钟左右。\", \"4\": \"期间准备馅料。在锅里倒入油，烧热后放入肉末翻炒至断生。\", \"5\": \"放入梅干菜和糖翻炒均匀，稍稍焖5至10分钟左右至入味，放凉备用。\", \"6\": \"在案板上撒上少许面粉，将面团分成等量的四份，揉成光滑均匀面团。\", \"7\": \"将面团擀成比手掌略大的面皮，包入馅料。\", \"8\": \"收口捏紧，稍稍压扁。\", \"9\": \"用擀面杖将面饼擀成薄饼，擀的时候适当翻面，使两面馅料分布均匀。\", \"10\": \"将平底锅烧热，将饼放入锅中小火煎至两面金黄即可。\"}', '{\"面粉\": \"150克\", \"开水\": \"110克\", \"梅干菜\": \"15克\", \"肉末\": \"150克\", \"糖\": \"1茶匙\", \"油\": \"适量\"}', 9, '2020-02-27 22:29:04', 0, '2020-03-12 19:36:05', '260085');
INSERT INTO `menu` VALUES (408, '蒜茄子', 12, '{\"1\": \"将茄子蒸熟。\", \"2\": \"准备好蒜，生菜，盐，味精。\", \"3\": \"把香菜铺到茄子里。\", \"4\": \"加入蒜和盐和味精。\", \"5\": \"茄子都包好后摆起来，上面撒一层盐，保鲜膜包好放入冰箱冷藏2天就可以吃了。\"}', '{\"茄子\": \"适量\", \"蒜\": \"适量\", \"香菜\": \"适量\", \"盐\": \"适量\", \"味精\": \"适量\"}', 10, '2020-02-27 22:29:04', 0, '2020-03-12 19:36:05', '432352');
INSERT INTO `menu` VALUES (409, '鳗鱼寿司', 16, '{\"1\": \"备好食材，鸡蛋打散煎成蛋饼切成条，胡萝卜切成条煮两分钟捞正，青瓜切条，鳗鱼放在饭面蒸热即可，米饭1碗。\", \"2\": \"用个小味碟放入少许苹果醋加一丢丢糖拌匀，自制寿司醋。\", \"3\": \"把寿司醋倒入温米饭中拌匀。\", \"4\": \"把寿司帘平放，剪一块保鲜膜放在上面。\", \"5\": \"把寿司专用海苔放在保鲜膜上面。\", \"6\": \"把米饭铺在紫菜上面周边留一点如图这样，把春瓜，胡萝卜，蛋饼，鳗鱼依次放在寿司的一端，如图。\", \"7\": \"借肋寿司帘从近至远的卷起，要稍微卷紧实一点。\", \"8\": \"如图，卷好了，依次做好另一卷。\", \"9\": \"刀子沾点水或油切自己喜欢的大小即可。\", \"10\": \"切好啦，美吧。\", \"11\": \"成品图。\", \"12\": \"成品图，挤点沙拉酱。\", \"13\": \"成品图。\"}', '{\"熟鳗鱼\": \"适量\", \"米饭\": \"适量\", \"青瓜\": \"少许\", \"胡萝卜\": \"少许\", \"鸡蛋\": \"1个\", \"海苔\": \"2张\", \"沙拉酱\": \"适量\", \"苹果醋\": \"少许\", \"糖\": \"少许\"}', 11, '2020-02-27 22:29:05', 0, '2020-03-12 19:36:05', '507351');
INSERT INTO `menu` VALUES (410, '肉末豆腐', 33, '{\"1\": \"大豆腐一块，大约500克。\", \"2\": \"切成三角片，控干水份备用。\", \"3\": \"平底锅或电饼铛，加热加点油中火开始煎豆腐。\", \"4\": \"两面都煎黄就可以了。\", \"5\": \"准备葱姜蒜，1个西红柿，2个青椒，60克里脊肉。\", \"6\": \"把食材清洗干净。\", \"7\": \"西红柿去皮切小块，其余的食材也都切好。\", \"8\": \"调个碗汁加糖，醋，酱油，蚝油，盐，五香粉和1勺淀粉。\", \"9\": \"平底锅加热加点油炒香葱姜蒜和肉末。\", \"10\": \"加番茄炒出汁液。\", \"11\": \"加入煎好的豆腐翻炒。\", \"12\": \"加青椒翻炒。\", \"13\": \"浇上碗汁翻炒均匀，大火收汁关火。\", \"14\": \"好吃的肉末豆腐就做好了。\"}', '{\"大豆腐\": \"1块\", \"里脊肉\": \"60克\", \"青椒\": \"2个\", \"西红柿\": \"1个\", \"葱\": \"1根\", \"姜\": \"1小块\", \"蒜\": \"适量\", \"油\": \"适量\", \"糖\": \"少许\", \"醋\": \"少许\", \"酱油\": \"少许\", \"蚝油\": \"少许\", \"五香粉\": \"少许\", \"盐\": \"少许\", \"淀粉\": \"1勺\"}', 10, '2020-02-27 22:29:05', 0, '2020-03-12 19:36:05', '432297');
INSERT INTO `menu` VALUES (411, '小鸡炖蘑菇', 5, '{\"1\": \"食材准备。整鸡斩件。\", \"2\": \"榛蘑泡发2小时，中途换3-5次水，将泥沙杂物冲洗干净，最后一次蘑菇水留用。\", \"3\": \"土豆粉条浸泡1小时。\", \"4\": \"葱姜蒜切好备用。\", \"5\": \"油热，放葱姜蒜、八角，炒香。\", \"6\": \"加入生鸡肉爆炒，炒到基本没有多余水份，鸡肉有焦香味为止。\", \"7\": \"倒入料酒、酱油和少量白糖翻炒。持续大火，炒到上色均匀。\", \"8\": \"加入泡发好的榛蘑和蘑菇水，再补充一些水。汤汁沸腾后调中小火炖鸡，炖半小时至鸡肉熟烂。\", \"9\": \"加盐、土豆粉条，继续炖10分钟左右。\", \"10\": \"开盖调大火再炖10分钟左右。\", \"11\": \"完成，出锅！\"}', '{\"整鸡\": \"一只，斩件\", \"榛蘑\": \"500克\", \"土豆粉条\": \"120克\", \"葱\": \"适量\", \"姜\": \"适量\", \"蒜\": \"8瓣\", \"八角\": \"2颗\", \"酱油\": \"15克\", \"料酒\": \"15克\", \"糖\": \"10克\", \"盐\": \"适量\", \"食用油\": \"适量\"}', 10, '2020-02-27 22:29:06', 0, '2020-03-12 19:36:05', '430607');
INSERT INTO `menu` VALUES (412, '可乐饼', 19, '{\"1\": \"土豆洗净了去皮，切块后煮熟。\", \"2\": \"火腿切成小丁。\", \"3\": \"小葱切成葱花。\", \"4\": \"芝士磨成碎末。\", \"5\": \"把三个鸡蛋打成蛋液。\", \"6\": \"煮好的土豆用料理机打成土豆泥。\", \"7\": \"打成泥的土豆加入火腿丁、葱花和芝士碎，搅拌均匀。\", \"8\": \"用你的小手把它捏成胖胖的小饼。\", \"9\": \"依次裹上鸡蛋液和面包糠。\", \"10\": \"油温7成热，把土豆饼放进去。\", \"11\": \"小火慢炸，等土豆饼飘起来就炸好了，捞出控油。\", \"12\": \"掰开看看，超漂亮的。\"}', '{\"土豆\": \"3个\", \"火腿\": \"1根\", \"小葱\": \"1根\", \"鸡蛋\": \"三个\", \"面包糠\": \"适量\", \"芝士碎\": \"适量\", \"玉米淀粉（生粉）\": \"适量\"}', 11, '2020-02-27 22:29:06', 0, '2020-03-12 19:36:05', '506981');
INSERT INTO `menu` VALUES (413, '松仁玉米', 25, '{\"1\": \"食材洗净备用。\", \"2\": \"玉米粒在开水中焯烫，捞出。\", \"3\": \"胡萝卜切丁，黄瓜切丁。\", \"4\": \"干锅放入松仁，小火慢慢炒香，稍上色盛出。\", \"5\": \"起油锅，油热，放葱花炒香，放入胡萝卜、玉米、黄瓜翻炒。\", \"6\": \"加盐调味，淋入水淀粉，快速翻炒均匀。\", \"7\": \"盛出。\", \"8\": \"撒上松仁即可。\"}', '{\"玉米粒\": \"300克\", \"松仁\": \"50克\", \"胡萝卜\": \"50克\", \"黄瓜\": \"1根\", \"水淀粉\": \"适量\", \"大葱\": \"适量\", \"盐\": \"适量\", \"食用油\": \"适量\"}', 10, '2020-02-27 22:29:07', 0, '2020-03-12 19:36:05', '426249');
INSERT INTO `menu` VALUES (414, '日式裙带味噌汤', 36, '{\"1\": \"裙带100克，汤底600ml，味噌2勺，豆腐150克，白玉菇150克，烘焙海米1捏，香菜2棵。\", \"2\": \"白玉菇去根洗净撕一撕，2棵裙带叶比较长断一断(新鲜裙带叶洗干净就行)，豆腐洗干净控干用手掰成小块，味噌用点温水化开。\", \"3\": \"准备好所有食材。\", \"4\": \"把汤底放入煮锅，烧开后放入白玉菇，再烧开放豆腐，会有些沫子用勺撇撇，然后放裙带叶，小火慢炖7，8分钟，这时可以加上烘焙海米，里面的菜软烂炖透了就可以加味噌了搅拌均匀就可以关火了，撒上香莱就可以享用了。\", \"5\": \"尝一口太鲜了。\", \"6\": \"日式裙带味噌汤好喝，日本人也太会吃了，这汤很好做的学起来吧。\"}', '{\"鲜裙带\": \"100克\", \"味噌\": \"2勺\", \"豆腐\": \"150克\", \"白玉菇\": \"150克\", \"烘焙海米\": \"1捏\", \"香菜\": \"2棵\", \"汤底\": \"600ml\"}', 11, '2020-02-27 22:29:07', 0, '2020-03-12 19:36:05', '506934');
INSERT INTO `menu` VALUES (415, '正宗地三鲜', 18, '{\"1\": \"食材洗净。\", \"2\": \"土豆去皮，切滚刀块。\", \"3\": \"茄子去皮，切滚刀块，沾一层薄薄的干淀粉。\", \"4\": \"青椒掰成小块，葱蒜切碎。\", \"5\": \"锅中放宽油，烧至八成热，放入土豆，中小火炸至表面金黄色，略显透明时捞出备用。\", \"6\": \"将茄子倒入油锅，炸至表面金黄色，捞出。\", \"7\": \"留底油，放入青椒翻炒，放入葱花、一半的蒜末炒香。\", \"8\": \"倒入酱油，加一小碗水烧开，放入盐、糖搅匀。\", \"9\": \"放入炸好的土豆、茄子大火烧一分钟。\", \"10\": \"淋入水淀粉，快速翻炒均匀，放入剩余蒜末，炒匀即可。\", \"11\": \"完成，装盘。\"}', '{\"土豆\": \"250克\", \"茄子\": \"250克\", \"青椒\": \"150克\", \"酱油\": \"15毫升\", \"白砂糖\": \"5克\", \"大葱\": \"1段\", \"大蒜\": \"4瓣\", \"盐\": \"适量\", \"水淀粉\": \"适量\", \"食用油\": \"适量\"}', 10, '2020-02-27 22:29:08', 0, '2020-03-12 19:36:05', '425053');
INSERT INTO `menu` VALUES (416, '日式芋头味噌汤', 87, '{\"1\": \"蟹腿棒1把，清水煮10分钟，加1点葱姜盐和五香粉，最后过出600mI汤底。\", \"2\": \"准备食材，味噌2勺，芋头2个，洋葱1个，香葱1绺。\", \"3\": \"所有食材洗干净切好，芋头去皮切厚片，芋头非常容易老化切完立即泡水里。\", \"4\": \"准备好所有食材。\", \"5\": \"把汤底倒入平底锅，烧开下入芋头片中小火煮7，8分钟，芋头软烂之后加洋葱，洋葱煮透明了加味噌，味噌不能直接放汤里化不开，要一点一点研磨都化开了汤也就好了，撒上香葱就可以了。\", \"6\": \"日式芋头味噌汤，太美味了。\"}', '{\"汤底\": \"600ml\", \"味噌\": \"2勺\", \"芋头\": \"2个\", \"洋葱\": \"4分之1\", \"香葱\": \"1绺\", \"盐\": \"少许\", \"五香粉\": \"少许\", \"姜\": \"少许\"}', 11, '2020-02-27 22:29:09', 0, '2020-03-12 19:36:05', '506374');
INSERT INTO `menu` VALUES (417, '东北三烀', 48, '{\"1\": \"备齐食材，食材可以随意添加，只要自己喜欢的都可以放进去。今天这些食材都来自姐姐家的小菜园，自家产的绿色食品，特意选了一些小土豆，而且是红土豆，特别绵糯，这些食材不用去皮，吃的时候一边扒皮一边吃，那也是一种情趣，一种吃不完的记忆。\", \"2\": \"土豆反复清洗干净，一定要清洗掉所有的泥土。\", \"3\": \"倭瓜清洗干净，切成条。东北的倭瓜和南方的南瓜差不多，感觉比南瓜面，没有南瓜甜。\", \"4\": \"茄子洗净，长茄子切断，所有食材放入蒸屉内。\", \"5\": \"砂锅加入适量的水，放上蒸屉，大火烧开，中火蒸10到15分钟左右。\", \"6\": \"10分钟左右打开锅，先取出茄子，茄子蒸过了就不好吃啦。此时如果土豆和倭瓜还没蒸熟，继续蒸5分钟左右。\", \"7\": \"出锅装盘。\"}', '{\"土豆\": \"750g\", \"倭瓜\": \"半个\", \"茄子\": \"2个\", \"辣椒酱\": \"适量\"}', 10, '2020-02-27 22:29:09', 0, '2020-03-12 19:36:05', '423438');
INSERT INTO `menu` VALUES (418, '东北大丰收', 23, '{\"1\": \"排骨剁成小块，用清水浸泡半小时后洗净沥干。\", \"2\": \"一颗葱挽结，一颗葱切花，老姜切片，蒜切碎。\", \"3\": \"排骨冷水下锅。\", \"4\": \"撇尽浮沫。\", \"5\": \"加入老姜片，葱结，花椒（花椒用容器盛装起来的）。\", \"6\": \"准备杂粮。\", \"7\": \"分别切块，白薯和土豆用清水漂起。\", \"8\": \"前两天烧鸡剩下的香菇，干脆也炖在里面算了，洗净切块。\", \"9\": \"排骨炖了约二十分钟后，先加香菇炖十余分钟，再一次性加入除白薯外的其它杂粮，加盐调味，炖十余分钟后最后加白薯，再炖十余分钟就可以关火了。\", \"10\": \"捡去姜葱不要，盛花椒的容器也捞出来。（这个容器已经用了快二十年了，某宝上有大些的卖，可以把葱姜花椒全部放里面）。\", \"11\": \"葱花蒜末里加生抽，醋，味精，花椒油，熟油辣椒，芝麻香油，做个蘸水，可以蘸排骨土豆白薯什么的，南瓜红薯玉米不用蘸，喜食清淡的可以不做蘸水，味道一样好。\", \"12\": \"千万不要在汤里放味精鸡精什么的，就要吃原汁原味的汤，回甜，营养丰富。\", \"13\": \"中午没吃饭，就吃这个了。\"}', '{\"排骨\": \"两匹\", \"玉米\": \"两根\", \"土豆\": \"一个\", \"南瓜\": \"半个\", \"白薯\": \"一块\", \"红薯\": \"一个\", \"香菇\": \"几个\", \"老姜\": \"两块\", \"小葱\": \"两颗\", \"花椒\": \"二十余颗\", \"盐\": \"适量\", \"蒜\": \"两颗\", \"生抽\": \"适量\", \"醋\": \"适量\", \"味精\": \"适量\", \"花椒油\": \"适量\", \"熟油辣椒\": \"适量\", \"芝麻香油\": \"适量\"}', 10, '2020-02-27 22:29:10', 0, '2020-03-12 19:36:05', '421315');
INSERT INTO `menu` VALUES (419, '日式肥牛饭', 65, '{\"1\": \"沸水中滴入几滴香油和适量盐，将切好的西兰花下入沸水中炒熟，盛出备用。\", \"2\": \"同样的做法，将胡萝卜切片炒熟。\", \"3\": \"洋葱切成细条状\", \"4\": \"肥牛下入沸水中焯一下，变色即可捞出，以免煮老了不好吃。\", \"5\": \"锅中倒入适量油，烧热后倒入洋葱，爆炒。\", \"6\": \"洋葱炒至微软，加入肥牛，翻炒均匀。\", \"7\": \"倒入半瓶盖饭汁，再加一点点盐，翻炒均匀。\", \"8\": \"加入适量清水，稍微煮1-2分钟左右，汤汁变得浓稠即可。\", \"9\": \"米饭装入大碗中，上面摆好西兰花和胡萝卜，再将炒好的肥牛和汤汁一起浇在米饭上即可。\"}', '{\"肥牛卷\": \"400g（做的是4人份的量）\", \"胡萝卜\": \"1根\", \"西兰花\": \"1棵\", \"洋葱\": \"1个\", \"米饭\": \"1碗\", \"三岛盖饭汁\": \"120g\", \"盐\": \"适量\", \"香油\": \"适量\"}', 11, '2020-02-27 22:29:10', 0, '2020-03-12 19:36:05', '496478');
INSERT INTO `menu` VALUES (420, '寿司卷', 69, '{\"1\": \"胡萝卜去皮切小条，煮稍软身。\", \"2\": \"午餐肉切小条。\", \"3\": \"鸡蛋打散，分2次煎成蛋皮。\", \"4\": \"切条。\", \"5\": \"牛油果对半切开，去皮去核切片。\", \"6\": \"全切好。\", \"7\": \"米饭预先煮好，放入寿司醋拌匀。\", \"8\": \"取一紫菜片放在寿司竹簾上，铺上米饭。\", \"9\": \"放入馅料。\", \"10\": \"卷起来，卷紧实些。\", \"11\": \"用锯齿刀切小块即可。\"}', '{\"香米\": \"1杯\", \"糯米\": \"1杯\", \"寿司醋\": \"4大匙\", \"鸡蛋\": \"3个\", \"梅林午餐肉\": \"适量\", \"胡萝卜\": \"1根\", \"牛油果\": \"2个\", \"寿司紫菜片\": \"6片\"}', 12, '2020-02-27 22:29:10', 0, '2020-03-12 19:36:05', '513024');
INSERT INTO `menu` VALUES (421, '猪肉炖粉条', 48, '{\"1\": \"食材：粉条(先用热开水烫软捞起，沥干水份)、五花肉用淀粉和酱油腌制、青菜、腐竹(凉水泡好)、炒香的花生米、姜蒜、葱花、干辣椒。\", \"2\": \"煮开水后，先放入粉条，调至小火慢煮。\", \"3\": \"炒锅放油，先把泡好腐竹炒香。\", \"4\": \"再放入青菜一起炒，加点盐和蚝油入味。\", \"5\": \"铲入砂锅的粉条面上，这时把火调至中大火。\", \"6\": \"接着爆香姜蒜。\", \"7\": \"将五花肉放入锅中翻炒。\", \"8\": \"加入酱油、豆瓣酱。\", \"9\": \"最后放入干辣椒炒香。\", \"10\": \"炒好的肉铲至砂锅内，撒入炒好的花生仁、葱花和少许鸡精，再用热油淋面，完工。\"}', '{\"猪肉\": \"200g\", \"粉条\": \"1把\", \"腐竹\": \"适量\", \"青菜\": \"100g\", \"花生米\": \"适量\", \"姜\": \"适量\", \"蒜\": \"适量\", \"葱花\": \"适量\", \"蚝油\": \"少许\", \"酱油\": \"适量\", \"盐\": \"适量\", \"豆瓣酱\": \"适量\", \"干辣椒\": \"4个\", \"鸡精\": \"少许\"}', 10, '2020-02-27 22:29:11', 0, '2020-03-12 19:36:05', '420600');
INSERT INTO `menu` VALUES (422, '紫菜包饭', 48, '{\"1\": \"所有食材切细条。\", \"2\": \"鸡蛋摊成饼切成大块。\", \"3\": \"蒸好的米饭。\", \"4\": \"在海苔的上面铺上一层米饭压实在摸上一层沙拉酱。\", \"5\": \"肉松。\", \"6\": \"沙拉酱上面在铺上一层肉松然后把所有食材摆好。\", \"7\": \"然后顺着一头卷起。\", \"8\": \"卷的越紧越好这样切成小块。\", \"9\": \"自己做得寿司料多多。\", \"10\": \"成品。\", \"11\": \"成品。\"}', '{\"胡萝卜\": \"适量\", \"黄瓜\": \"适量\", \"火腿肠\": \"适量\", \"鸡蛋\": \"2个\", \"沙拉酱\": \"适量\", \"肉松\": \"适量\", \"海苔\": \"适量\"}', 12, '2020-02-27 22:29:11', 0, '2020-03-12 19:36:05', '511452');
INSERT INTO `menu` VALUES (423, '家常版酸菜汆白肉', 70, '{\"1\": \"五花肉是我提前准备好的。如果是现做的话需要把五花肉先焯水后加入适量的生姜，料酒，花椒煮到筷子可以扎透，捞出放凉，切薄片。粉条提前泡软，酸菜清洗干净，我买的成品就是丝状的，不用再切了，其他的配料切片切丁都可以。\", \"2\": \"锅中放油，放入蒜片生姜和小米椒，再放入一个八角，炒出香味。\", \"3\": \"放入酸菜炒出香味。\", \"4\": \"加入高汤，我加的煮五花肉的水。\", \"5\": \"把酸菜煮一会，放入五花肉片，开小火炖15-20分钟。\", \"6\": \"加入适量的白花椒粉和盐，酸菜有点咸味，所以不要加太多的盐。\", \"7\": \"差不多好的时候加入泡好的粉条，在煮五分钟。\", \"8\": \"最后撒上点小芹菜或者香菜点缀一下，放一点鸡精，开吃吧！\"}', '{\"五花肉\": \"适量\", \"酸菜\": \"适量\", \"粉条\": \"适量\", \"生姜\": \"适量\", \"蒜\": \"适量\", \"小米椒\": \"适量\", \"盐\": \"适量\", \"白胡椒粉\": \"适量\", \"鸡精\": \"适量\"}', 10, '2020-02-27 22:29:12', 0, '2020-03-12 19:36:05', '419663');
INSERT INTO `menu` VALUES (424, '中式天妇罗', 16, '{\"1\": \"角瓜切丝，倒入调好的面糊里（适量清水加适量面粉调成糊状），调入适量盐和胡椒粉搅拌均匀。\", \"2\": \"入油锅炸。\", \"3\": \"炸至金黄色捞出沥下油即可。\"}', '{\"面粉\": \"10勺\", \"角瓜\": \"1块\", \"盐\": \"1勺\", \"胡椒粉\": \"1勺\"}', 11, '2020-02-27 22:29:13', 0, '2020-03-12 19:36:05', '469112');
INSERT INTO `menu` VALUES (425, '甜辣年糕', 49, '{}', '{\"年糕\": \"100g\", \"甜辣酱\": \"2匙\"}', 12, '2020-02-27 22:29:13', 0, '2020-03-12 19:36:05', '511208');
INSERT INTO `menu` VALUES (426, '日式炒面', 84, '{\"1\": \"准备材料。提前煎一个太阳蛋。小葱切葱花，五花肉切片，包菜切丝，洋葱切丝，鱼竹轮、韭菜黄切段。锅里炒热水，放入金龙鱼活性发酵空心面，稍微煮一下，控水捞起。\", \"2\": \"放入香油，拌匀。面条就不会粘粘，容易炒匀入味。\", \"3\": \"另起油锅烧热，放入五花肉片炒出油，放入小葱、洋葱炒出香味。\", \"4\": \"放入鱼竹轮翻炒至熟。\", \"5\": \"倒入包菜丝、韭黄，炒匀。\", \"6\": \"放面条，稍微拌炒均匀。\", \"7\": \"倒入日式炒面酱（或者牛排酱、烤肉酱） 、盐、黑胡椒碎，炒至酱汁被面条吸收。\", \"8\": \"炒匀以后，装起。撒上海苔丝、太阳蛋，依次喜好挤上蛋黄沙拉酱。就可以开吃啦。\"}', '{\"金龙鱼活性发酵空心面\": \"1把\", \"包菜\": \"100克\", \"洋葱\": \"1/2个\", \"猪肉\": \"100克\", \"日式鱼竹轮\": \"4个\", \"鸡蛋\": \"1个\", \"海苔丝\": \"适量\", \"小葱\": \"1根\", \"韭菜黄\": \"4根\", \"盐\": \"适量\", \"黑胡椒碎\": \"适量\", \"日式炒面酱\": \"50ml\", \"蛋黄沙拉酱\": \"适量\"}', 11, '2020-02-27 22:29:14', 0, '2020-03-12 19:36:05', '463409');
INSERT INTO `menu` VALUES (427, '日式拉面', 40, '{\"1\": \"买的日式拉面，这拉面爽滑而Q弹，不错。\", \"2\": \"生菜洗净。\", \"3\": \"拉面放入开水锅煮2分钟。(按说明煮就好。)\", \"4\": \"用纯净水过个冷河。\", \"5\": \"另起锅把水烧开，倒入浓缩酱油，再烧开。(咸度按自家口味调)\", \"6\": \"放入鱼饼和生菜煮开。(鱼饼预先切片。)\", \"7\": \"日式叉烧用不粘锅煎一下。\", \"8\": \"把面放入碗中。\", \"9\": \"放上生菜，鱼饼，日式叉烧，卤蛋，浇上汤就可以开吃了。\"}', '{\"日式拉面\": \"450g\", \"日式叉烧\": \"4-5片\", \"鱼饼\": \"8-10片\", \"绿叶生菜\": \"1棵\", \"卤蛋\": \"4个\", \"浓缩酱油\": \"适量\"}', 11, '2020-02-27 22:29:15', 0, '2020-03-12 19:36:05', '450858');
INSERT INTO `menu` VALUES (428, '泡菜虾仁寿司卷', 82, '{\"1\": \"虾仁从中间剖开。\", \"2\": \"加入一点料酒、白胡椒粉、糖、盐腌5分钟。\", \"3\": \"开水焯熟。\", \"4\": \"鸡蛋打散摊个蛋皮。火要小点，我的火有点大，摊的蛋皮不平整。\", \"5\": \"所有食材准备齐全。\", \"6\": \"米饭中加入一勺寿司醋。\", \"7\": \"适量的香油，\", \"8\": \"适量的熟芝麻搅拌均匀。\", \"9\": \"用饭铲将米饭铺在烤紫菜上，压实。\", \"10\": \"蛋皮边沿切整齐，铺在米饭上。其实蛋皮铺在紫菜和米饭中间粘合性更好。\", \"11\": \"放上黄瓜条，泡萝卜条，修下的蛋皮边，焯水后的虾仁。\", \"12\": \"放上适量泡白菜。\", \"13\": \"用寿司帘卷起，要卷紧实些。\", \"14\": \"用锋利的刀切成小块。\"}', '{\"米饭\": \"1碗\", \"虾仁\": \"5只\", \"鸡蛋\": \"1个\", \"黄瓜\": \"1/4\", \"泡菜\": \"适量\", \"泡萝卜\": \"1小根\", \"烤紫菜\": \"1张\", \"寿司醋\": \"1勺\", \"香油\": \"适量\", \"熟芝麻\": \"适量\"}', 12, '2020-02-27 22:29:16', 0, '2020-03-12 19:36:05', '511134');
INSERT INTO `menu` VALUES (429, '章鱼小丸子', 51, '{\"1\": \"准备好主要食材。\", \"2\": \"章鱼烧粉放水鸡蛋和食用油搅拌均匀至无颗粒。\", \"3\": \"模具锅加热，抹上食用油。\", \"4\": \"之后调成小火，倒入面糊。\", \"5\": \"之后放入八成熟的小八爪鱼。\", \"6\": \"之后慢慢用铁签转动，直至变为圆成，并且呈枣红色就可以出锅了。\", \"7\": \"淋上沙拉酱照烧酱，撒上海苔丝木鱼花就可以了。\"}', '{\"章鱼烧粉\": \"120克\", \"鸡蛋\": \"一个\", \"食用油\": \"10克\", \"清水\": \"160克\", \"木鱼花\": \"适量\", \"海苔丝\": \"适量\", \"照烧酱\": \"少于\"}', 11, '2020-02-27 22:29:18', 0, '2020-03-12 19:36:05', '446081');
INSERT INTO `menu` VALUES (430, '韩式辣酱炒年糕', 28, '{\"1\": \"韩式辣酱和芝士夹心年糕组合一包。\", \"2\": \"芝士年糕解冻。\", \"3\": \"一包韩式辣酱。\", \"4\": \"韩式辣酱放入煮锅。\", \"5\": \"加水调匀。\", \"6\": \"放入芝士年糕大火烧开转小火煮五分钟。\", \"7\": \"煮熟，煮制粘稠关火。\", \"8\": \"出锅了。\"}', '{\"芝士夹心年糕\": \"适量\", \"韩式辣酱\": \"适量\"}', 12, '2020-02-27 22:29:18', 0, '2020-03-12 19:36:05', '510586');
INSERT INTO `menu` VALUES (431, '三文鱼刺身', 69, '{\"1\": \"购买新鲜三文鱼。\", \"2\": \"卖家现场处理三文鱼。\", \"3\": \"三文鱼块。\", \"4\": \"切片，蘸刺身酱油和青芥辣食用。\"}', '{\"三文鱼\": \"适量\", \"青芥辣\": \"适量\", \"刺身酱油\": \"适量\"}', 11, '2020-02-27 22:29:19', 0, '2020-03-12 19:36:05', '444062');
INSERT INTO `menu` VALUES (432, '韩式拌海带丝', 35, '{\"1\": \"海带洗净切细丝。海带是自己泡发的。\", \"2\": \"放入开水中煮5分钟，捞出过凉水沥干。\", \"3\": \"洋葱切丝，大葱切圈。\", \"4\": \"把所有辅料放入碗中搅匀，根据自己的口味增减。\", \"5\": \"把海带丝、洋葱和大葱放入大碗中。\", \"6\": \"加入料汁拌匀。\"}', '{\"海带丝\": \"200克\", \"洋葱\": \"半个\", \"大葱\": \"1节\", \"韩式辣酱\": \"1大匙\", \"辣椒面\": \"适量\", \"生抽\": \"1大匙\", \"醋\": \"1大匙\", \"香油\": \"1小匙\", \"芝麻\": \"1小匙\", \"蒜泥\": \"1大匙\", \"白糖\": \"适量\"}', 12, '2020-02-27 22:29:20', 0, '2020-03-12 19:36:05', '510342');
INSERT INTO `menu` VALUES (433, '三文鱼刺身', 35, '{\"1\": \"主辅料： 三文鱼、辣根 。\", \"2\": \" 盘中装适量碎冰，再覆盖一层保鲜膜。 \", \"3\": \" 把冻三文鱼切片。 \", \"4\": \" 把切好的三文鱼摆盘。 \", \"5\": \" 兑制蘸料，碗中放适量辣根，加生抽、醋。 \", \"6\": \" 蘸料和三文鱼同时上桌，蘸食即可。\"}', '{\"三文鱼\": \"200克\", \"辣根\": \"适量\", \"生抽\": \"适量\", \"醋\": \"适量\"}', 11, '2020-02-27 22:29:20', 0, '2020-03-12 19:36:05', '440062');
INSERT INTO `menu` VALUES (434, '韩式虾仁泡菜汤', 71, '{\"1\": \"几样主料。\", \"2\": \"虾仁解冻后焯水2、3分钟。\", \"3\": \"豆腐切件，提前焯水。\", \"4\": \"金针菇洗净。\", \"5\": \"泡菜大小适中的话不必再切。\", \"6\": \"锅中加适量水，倒入适量的泡菜和泡菜汤。\", \"7\": \"开锅后放金针菇、豆腐。\", \"8\": \"快好时下入虾仁。\", \"9\": \"泡菜和泡菜汤已有一定盐分，喜欢吃淡的话，基本不用再加盐。\", \"10\": \"盛入碗中。\", \"11\": \"撒上青蒜末。\", \"12\": \"开胃，下饭。\", \"13\": \"配馒头片也不错。\"}', '{\"虾仁\": \"适量\", \"韩式泡菜\": \"适量\", \"豆腐\": \"适量\", \"金针菇\": \"适量\"}', 12, '2020-02-27 22:29:21', 0, '2020-03-12 19:36:05', '509654');
INSERT INTO `menu` VALUES (435, '牛果培根饭团', 69, '{\"1\": \"糯米提前泡几个小时，上锅蒸20多分钟，蒸碗的水量与米面齐平，中间可以翻动一二次。\", \"2\": \"米饭拌上黑芝麻。\", \"3\": \"干酪块切成小条。\", \"4\": \"蛋液加盐和料酒打散。\", \"5\": \"以少许油煎熟。\", \"6\": \"培根两面煎一下。\", \"7\": \"最好放点油，我没放，感觉不够酥脆。\", \"8\": \"牛油果切条。\", \"9\": \"米果是一种吃着脆脆的零食，放它完全是为了增加香酥的口感，代替外面饭团放的油炸物。\", \"10\": \"日式拌饭料最后洒上。\", \"11\": \"寿司帘铺保鲜膜，均匀地铺上一层米饭。\", \"12\": \"如没有寿司帘，菜板上铺层保鲜膜也OK。\", \"13\": \"放上所有馅料。\", \"14\": \"卷成筒状。\", \"15\": \"尽量卷得紧实些，吃时不易散。\", \"16\": \"去掉保鲜膜是为了拍照，包着膜吃起来更方便。\"}', '{\"糯米\": \"2人份\", \"黑芝麻\": \"适量\", \"鸡蛋\": \"1个\", \"干酪\": \"适量\", \"培根\": \"适量\", \"牛油果\": \"1个\", \"米果\": \"适量\", \"日式拌饭料\": \"适量\", \"食用油\": \"适量\", \"盐\": \"适量\", \"料酒\": \"适量\"}', 11, '2020-02-27 22:29:21', 0, '2020-03-12 19:36:05', '438782');
INSERT INTO `menu` VALUES (436, '鱼籽寿司', 12, '{\"1\": \"把醋糖盐放入碗中调成寿司醋，也可以直接用寿司醋。\", \"2\": \"把调好的醋倒入蒸好的米饭中，充分搅拌均匀。\", \"3\": \"拌好的米饭铺在海苔上。\", \"4\": \"在一端均匀的铺上鱼籽酱。\", \"5\": \"借助帘子卷起来。\", \"6\": \"放置一会儿让寿司定型，然后切段。\", \"7\": \"把切好的寿司平放，表面再铺上鱼子酱。\", \"8\": \"如果想要鱼子酱多一些，把米饭往下压一下，这样借助海苔围边，可以放多点鱼子酱上去。\"}', '{\"米饭\": \"1碗\", \"寿司海苔\": \"2张\", \"鱼子酱\": \"40g\", \"白米醋\": \"15g\", \"盐\": \"1g\", \"糖\": \"2g\"}', 11, '2020-02-27 22:29:22', 0, '2020-03-12 19:36:05', '438407');
INSERT INTO `menu` VALUES (437, '辣炒年糕', 97, '{\"1\": \"朴家炒年糕一袋。\", \"2\": \"年糕条，料包，彩椒，荷兰豆。\", \"3\": \"彩椒切斜丝，荷兰豆切斜片。\", \"4\": \"年糕焯软捞出备用。\", \"5\": \"锅内适量油烧热，姜蒜炒香。\", \"6\": \"彩椒，荷兰豆放入。\", \"7\": \"翻炒几下，倒入一勺开水。\", \"8\": \"年糕条倒入。\", \"9\": \"年糕料包倒入。\", \"10\": \"翻炒均均匀，关火盛盘。\", \"11\": \"成品。\", \"12\": \"成品。\"}', '{\"韩式年糕\": \"适量\", \"彩椒\": \"适量\", \"荷兰豆\": \"适量\", \"玉米油\": \"适量\", \"姜\": \"适量\", \"炒年糕料包\": \"一袋\", \"蒜\": \"适量\"}', 12, '2020-02-27 22:29:22', 0, '2020-03-12 19:36:05', '509249');
INSERT INTO `menu` VALUES (438, '低脂版玉米面赤豆铜锣烧', 42, '{\"1\": \"玉米面加水搅拌成颗粒状。\", \"2\": \"加面粉，泡打粉1g，酵母3g，白糖3g。(若爱吃甜的可多放糖)\", \"3\": \"加牛奶或水搅拌成粘稠的糊糊。放冰箱冷藏发酵一晚上。(或室温发酵一小时)\", \"4\": \"准备豆沙馅:用压力锅压赤豆40分钟后放蒸汽开盖，按个人口味加适量糖，启动开盖收汁功能八分钟，取出后放料理机打碎。比买来现成的豆沙馅糖分低卡路里低，但口感和味道肯定是没那么好。\", \"5\": \"冷藏放置一晚后搅拌一下，搅拌出气泡。\", \"6\": \"电饼铛预热，刷一层薄油，启动薄饼功能，用小勺子把糊糊滴在上面。\", \"7\": \"一面金黄后翻面烙另一面，熟了出锅。\", \"8\": \"均匀抹豆沙在一片玉米饼上，再用另一个玉米饼扣在上面，颜色浅的一面在里面哦。可以吃啦。没有做豆沙也可以直接吃玉米饼，低脂健康的粗粮，这么做口感也蛮好的，爱吃糖可以多放些。\"}', '{\"玉米面\": \"60g\", \"面粉\": \"30g\", \"赤豆\": \"100g\", \"白糖\": \"3g+10g\", \"酵母\": \"3g\", \"泡打粉\": \"1g\"}', 11, '2020-02-27 22:29:24', 0, '2020-03-12 19:36:05', '437398');
INSERT INTO `menu` VALUES (439, '泡菜炒饭', 65, '{\"1\": \"火腿肠切薄片，香葱洗净切葱花备用。\", \"2\": \"鸡蛋打入碗中，加入少许盐，鸡精打散。\", \"3\": \"隔夜的剩米饭备用。\", \"4\": \"泡菜切小丁备用。\", \"5\": \"锅中加入油，倒入鸡蛋液炒散。\", \"6\": \"放入火腿肠翻炒。\", \"7\": \"加入泡菜丁，倒入少许的泡菜汁。\", \"8\": \"放入米饭。\", \"9\": \"用锅铲将米饭压散炒匀。\", \"10\": \"最后加入葱花炒匀即可。\", \"11\": \"装盘。\", \"12\": \"成品。\"}', '{\"隔夜饭\": \"适量\", \"韩国泡菜\": \"适量\", \"鸡蛋\": \"2个\", \"火腿肠\": \"1个\", \"葱花\": \"适量\", \"盐\": \"适量\", \"鸡精\": \"适量\"}', 12, '2020-02-27 22:29:24', 0, '2020-03-12 19:36:05', '490485');
INSERT INTO `menu` VALUES (440, '泰式冬阴功', 9, '{}', '{\"虾\": \"20个\", \"墨鱼\": \"一条（冰鲜）\", \"番茄\": \"两个\", \"草菇\": \"6个\", \"金针菇\": \"一捆\", \"花甲\": \"适量\", \"白胡椒粉\": \"少许\", \"料酒\": \"两勺\", \"椰浆\": \"一罐\", \"鱼露\": \"适量\", \"小米椒\": \"适量\", \"冬阴功酱料包\": \"适量\", \"蒜头\": \"两瓣\", \"油\": \"少许\", \"青柠\": \"一个\", \"香茅\": \"适量\", \"青柠叶\": \"适量\"}', 13, '2020-02-27 22:29:25', 0, '2020-03-12 19:36:05', '426997');
INSERT INTO `menu` VALUES (441, '韩式南瓜粥', 75, '{\"1\": \"南瓜去皮去瓤切小片蒸熟。\", \"2\": \"在料理机中打成细腻的南瓜泥。。\", \"3\": \"糯米粉备用。\", \"4\": \"南瓜泥加水、冰糖烧煮。\", \"5\": \"糯米粉加少量水搅散调成芡汁。\", \"6\": \"把糯米粉水倒入南瓜泥中，边倒边搅拌，烧熟成糊状。\", \"7\": \"香甜又营养的南瓜粥。\"}', '{\"南瓜\": \"适量\", \"糯米粉\": \"适量\", \"冰糖\": \"适量\", \"水\": \"适量\"}', 12, '2020-02-27 22:29:26', 0, '2020-03-12 19:36:05', '505374');
INSERT INTO `menu` VALUES (442, '山药草莓大福', 99, '{\"1\": \"山药去皮，切块。\", \"2\": \" 南瓜去皮，切块。\", \"3\": \" 放入蒸锅，蒸约15分钟至完全熟透。\", \"4\": \"草莓不要去根，用清水冲洗一遍，然后淡盐水中浸泡5分钟，此时可让大多数的农药随著水溶解。\", \"5\": \" 山药南瓜熟透取出，倒入碗中捣泥。（我还加了点糖，一岁以下的宝贝可以不加糖。）\", \"6\": \"将草莓去叶子，再用厨房纸擦干水分，切去根蒂备用。\", \"7\": \"取适量南瓜山药泥，揉成圆球，压扁。\", \"8\": \"将草莓的尖朝上放进去，用虎口的位置将草莓用南瓜山药泥，慢慢地包起来。\", \"9\": \"之后放到盛满了椰蓉的小盘子中滚一圈就好了。\", \"10\": \"完成了，切开俩半放入碟中，可以拿去给宝宝享用了。\"}', '{\"山药\": \"100g\", \"南瓜\": \"30g\", \"草莓\": \"4颗\", \"椰蓉\": \"适量\"}', 11, '2020-02-27 22:29:26', 0, '2020-03-12 19:36:05', '434832');
INSERT INTO `menu` VALUES (443, '泰式冬阴功酸辣味 ', 92, '{\"1\": \"干料下锅 放入椰奶 放花蛤 大虾西兰花番茄小米辣。\", \"2\": \"放入冬阴功汤宝 放入半个青柠檬汁 就可以出锅了。摆好盘就可以了 要是有鱼露可以少加点。\", \"3\": \"美味出锅。\"}', '{\"虾\": \"适量\", \"花蛤\": \"适量\", \"西兰花\": \"适量\", \"番茄\": \"适量\", \"香茅\": \"适量\", \"姜\": \"适量\", \"柠檬叶\": \"适量\", \"椰奶\": \"适量\", \"小米辣\": \"适量\", \"冬阴功汤宝\": \"适量\", \"青柠檬\": \"适量\"}', 13, '2020-02-27 22:29:26', 0, '2020-03-12 19:36:05', '422375');
INSERT INTO `menu` VALUES (444, '韩式辣酱煎猪肉', 64, '{\"1\": \"猪头洗净切片，爱吃肥一点可选五花肉，加柠檬汁腌制。\", \"2\": \"放入盐，酱油，韩式辣酱。\", \"3\": \"放入蚝油，辣椒细粉，糖。\", \"4\": \"腌制一个小时以上。\", \"5\": \"生菜，洋葱，青椒洗净备用。\", \"6\": \"蒜米，葱末，剁椒，糖，酱油，蚝油炒制料汁备用。\", \"7\": \"Harthsun牛排煎锅预热，加入油放入腌好的猪肉。\", \"8\": \"两面反复煎制，全程小火就OK了，煎锅受热均匀。\", \"9\": \"煎至熟透即可。\", \"10\": \"导流嘴空出多余的油脂，健康饮食的同时也利于清洗锅体。\", \"11\": \"韩式辣酱煎猪肉完美呈现。\", \"12\": \"吃货宝贝已经按耐不住啦。\", \"13\": \"成品图。\"}', '{\"猪肉\": \"适量\", \"生菜\": \"适量\", \"洋葱\": \"适量\", \"青椒\": \"适量\", \"柠檬\": \"半个\", \"油\": \"适量\", \"盐\": \"适量\", \"韩式辣酱\": \"适量\", \"酱油\": \"适量\", \"糖\": \"适量\", \"辣椒细粉\": \"适量\", \"葱\": \"适量\", \"蒜\": \"适量\", \"剁椒\": \"适量\", \"蚝油\": \"适量\"}', 12, '2020-02-27 22:29:27', 0, '2020-03-12 19:36:05', '508259');
INSERT INTO `menu` VALUES (445, '日式简易寿司卷', 60, '{\"1\": \"取适量剩米饭盛入碗中（最好是温热的）。\", \"2\": \"取白醋3勺，白糖2勺，适量的盐调制出寿司醋，然后按照寿司醋和米饭1:6的比例将寿司饭调制均匀。\", \"3\": \"锅中放入适量的花生油，将鸡蛋摊成鸡蛋饼。\", \"4\": \"分别将火腿肠、鸡蛋饼、尖椒、寿司大根，切成如图所示的长条。\", \"5\": \"取一片海苔片铺在竹帘上，带上一次性手套，将调制好的寿司米饭均匀地铺在寿司海苔片上。\", \"6\": \"将切好的寿司大根、火腿肠、尖椒和鸡蛋饼条，铺在上面寿司上，如图所示。\", \"7\": \"然后用竹帘推动着寿司由内向外轻轻卷起，要注意，卷的时候要紧一点，这样最后卷出来的寿司才不会容易散掉。\", \"8\": \"卷好的寿司，撤掉竹帘，平放在菜板上。\", \"9\": \"刀身蘸取少许清水，然后用刀将寿司切好，进行摆盘。\"}', '{\"海苔片\": \"2个\", \"剩米饭\": \"1大碗\", \"日式寿司大根\": \"2根\", \"鸡蛋\": \"1个\", \"花生油\": \"适量\", \"火腿肠\": \"1根\", \"尖椒\": \"1个\", \"白砂糖\": \"适量\", \"盐\": \"适量\", \"清水\": \"适量\", \"醋\": \"适量\"}', 11, '2020-02-27 22:29:27', 0, '2020-03-12 19:36:05', '430832');
INSERT INTO `menu` VALUES (446, '韩式牛肉海带汤', 61, '{\"1\": \"牛肉300克，裙带叶50克，淘米水1000ml，葱白1段，大蒜半头。\", \"2\": \"盐渍鲜裙带叶50克，用清水泡一会，挨个大点的盆多加手，像洗衣服一样反复搓洗多换几次水洗干净后剪成小块备用。\", \"3\": \"牛肋尖牛肉里最嫩的部位，洗净切厚片，葱蒜本来应该切末，但有小孩子吃所以切成大块。\", \"4\": \"准备好所有食材，最小碗里是香油，用香油做汤很有特色呢。\", \"5\": \"锅烧热加1勺香油，把牛肉片放锅里炒，火不要太大不能炒糊，炒到牛肉里的水份没有了，加上淘米水(我淘米时第一次水倒了，第二次搓洗留用)，然后大火烧开小火慢炖20分钟。\", \"6\": \"炖了20分钟牛肉又软又烂这时候可以放裙带叶了，裙带叶特别薄烫一下就熟，跟据口味加点盐撒I葱蒜就可以关火了，也可以加鸡汁，鸡精，牛肉粉一类的东西，我多年不吃这些东西了没加，原汁也挺好喝的。\", \"7\": \"和朝鲜族姑娘学做牛肉海带汤，使我的生活中又多了一个美味。\"}', '{\"牛肋尖\": \"300克\", \"裙带叶\": \"50克\", \"淘米水\": \"1000ml\", \"葱白\": \"1段\", \"大蒜\": \"半头\", \"香油\": \"1勺\", \"盐\": \"少许\"}', 12, '2020-02-27 22:29:28', 0, '2020-03-12 19:36:05', '507800');
INSERT INTO `menu` VALUES (447, '雪媚娘', 88, '{\"1\": \"混合糯米粉60g、白砂糖15g、玉米淀粉18g，加入105克牛奶，搅拌均匀。\", \"2\": \"碗上盖保鲜膜，放入蒸锅蒸30分钟。\", \"3\": \"炒熟15g糯米粉，备用。\", \"4\": \"去除奥利奥饼干中间的夹心部分，将饼干放入保鲜袋中敲成碎渣，备用。\", \"5\": \"在加拿大这种35% whipping cream（国内淘宝上奶油也很多）随处可见。\", \"6\": \"在100g奶油中加入10g白糖，打发。\", \"7\": \"将奥利奥饼干碎渣混入打发后的奶油中，搅拌均匀。\", \"8\": \"糯米糊蒸好之后放入黄油，余温会让黄油迅速融化。搅拌的越充分，越不容易粘连。\", \"9\": \"将面团分成5份。\", \"10\": \"将内馅包入皮中，包后将多余的皮去除掉。\", \"11\": \"整理成形即可。\"}', '{\"糯米粉\": \"60克\", \"玉米淀粉\": \"18克\", \"白砂糖\": \"10克\", \"牛奶\": \"105克\", \"黄油\": \"8克\", \"35%奶油\": \"100克\", \"奥利奥饼干\": \"若干\", \"熟糯米粉\": \"15克\"}', 11, '2020-02-27 22:29:29', 0, '2020-03-12 19:36:05', '430099');
INSERT INTO `menu` VALUES (448, '咖喱鸡', 79, '{\"1\": \"材料。\", \"2\": \"切好的材料，鸡腿用－点咖哩粉，－点盐腌漬。\", \"3\": \"热鍋，下五湯匙油，小火，把咖哩粉，香芧，咖哩叶，亞参片，小红葱片－起下锅略为爆香。把鸡肉，馬鈴薯倒下，把所有材料炒均勻，然后下糖，盐，素菜精。\", \"4\": \"把兩飯碗水倒进，燒滚，小中火煮约二十分鐘即成。\", \"5\": \"成品，和热騰騰的白米飯一起吃，贊。\"}', '{\"鸡腿\": \"4个\", \"马铃薯\": \"3个\", \"鸡肉咖哩粉\": \"半碗\", \"香茅\": \"二根\", \"小红葱\": \"三粒\", \"咖喱叶\": \"二根\", \"亚参片\": \"1小片\", \"水\": \"2碗\", \"糖\": \"一茶匙\", \"盐\": \"适量\", \"素莱精\": \"适量\"}', 13, '2020-02-27 22:29:29', 0, '2020-03-12 19:36:05', '417068');
INSERT INTO `menu` VALUES (449, '肉松蛋黄寿司', 15, '{\"1\": \"剩大米中倒入寿司醋用筷子扒散拌匀。\", \"2\": \"准备好菜和咸鸭蛋。\", \"3\": \"在寿司帘上铺上一张海苔。\", \"4\": \"将米饭平铺在海苔上。\", \"5\": \"挤上沙拉酱。\", \"6\": \"抹平，再将鸭蛋黄放在大米的一头。\", \"7\": \"摆上黄瓜条,火腿肠和肉松。\", \"8\": \"借助寿司帘卷起来卷紧。\", \"9\": \"切块，食用前再挤上一些沙拉酱即可。\"}', '{\"剩饭\": \"适量\", \"黄瓜\": \"1根\", \"咸鸭蛋蛋黄\": \"1个\", \"海苔\": \"1片\", \"火腿肠\": \"2根\", \"沙拉酱\": \"20克\", \"肉松\": \"10克\", \"寿司醋\": \"5克\"}', 11, '2020-02-27 22:29:30', 0, '2020-03-12 19:36:05', '427527');
INSERT INTO `menu` VALUES (450, '无油咖喱烤鸡腿', 95, '{\"1\": \"准备材料。\", \"2\": \"小鸡腿洗干净，加入姜丝，现磨黑胡椒拌均匀。\", \"3\": \"加入盐，生抽拌均匀。\", \"4\": \"咖喱块放入微波炉，加热至融化取出。\", \"5\": \"咖喱加入鸡腿里。\", \"6\": \"用手抓均匀。\", \"7\": \"放入冰箱冷藏3小时，中间翻动几次。\", \"8\": \"面粉和生粉加适量水，调成一碗稠稠的面糊。\", \"9\": \"取一个飞天腿，在面糊里滚一圈，挂一层浆。\", \"10\": \"再放到面包糠里滚一圈。\", \"11\": \"把做好的鸡腿，放在盘里。\", \"12\": \"放入微波炉，先盖上一个盘子（忘记拍照了）。\", \"13\": \"高火5分钟。\", \"14\": \"鸡腿翻转一面，取下盖的盘子，再高火5分钟即可。\", \"15\": \"无油咖喱烤鸡腿。\", \"16\": \"无油咖喱烤鸡腿。\"}', '{\"鸡腿\": \"400g\", \"咖喱块\": \"20g\", \"生粉\": \"20g\", \"面粉\": \"20g\", \"面包糠\": \"250g\", \"盐\": \"3g\", \"生抽\": \"10g\", \"黑胡椒\": \"适量\", \"姜\": \"15g\"}', 13, '2020-02-27 22:29:30', 0, '2020-03-12 19:36:05', '406634');
INSERT INTO `menu` VALUES (451, '红酒烤五花肉', 55, '{\"1\": \"五花肉拿切成5cm左右宽的条状。把胡椒粒均匀的撒在肉上面，然后倒入1/4瓶红酒，把一条一条肉分开放入保鲜袋里，放进冰箱冷冻2小时。辣椒切丁，蒜切片，生菜洗净放好待用。\", \"2\": \"冻好的肉拿出来，切成小厚片，放入烤锅开始烤。\", \"3\": \"把辣白菜切成片，放入烤盘的一边，和肉一起烤。根据喜好，也可放入口蘑等一起烤。\", \"4\": \"烤好的肉，辣白菜装盘，拿生菜包起一块，再放进一片蒜，辣椒丁一起包起来吃。味道好极啦！\"}', '{\"五花肉\": \"2斤\", \"胡椒粒\": \"若干\", \"红酒\": \"1/4瓶\", \"酸辣白菜\": \"1/4棵\", \"烤肉酱\": \"1小碟\", \"大蒜\": \"1头\", \"尖椒\": \"5个\", \"生菜\": \"1头\"}', 12, '2020-02-27 22:29:31', 0, '2020-03-12 19:36:05', '506452');
INSERT INTO `menu` VALUES (452, '素食关东煮', 28, '{\"1\": \"食材准备。\", \"2\": \"干海带放入冷水中煮。\", \"3\": \"浸泡干香菇备用。\", \"4\": \"将香菇水倒入汤中，继续煮。\", \"5\": \"白萝卜削皮切片，苹果切片。\", \"6\": \"切好的白萝卜、苹果放入汤中煮。\", \"7\": \"倒入适量酱油，煮成汤底。\", \"8\": \"把娃娃菜和卷心菜用热水烫软以便卷起来，再依次用竹签串成串。\", \"9\": \"将胡萝卜、玉米、豆腐、油豆腐切片切块，用竹签串成串备用。\", \"10\": \"将备用食材按煮熟的难易程度依次倒入高汤，煮熟即可食用。\"}', '{\"白萝卜\": \"1个\", \"娃娃菜\": \"1棵\", \"胡萝卜\": \"1个\", \"苹果\": \"1个\", \"玉米\": \"1根\", \"卷心菜\": \"1个\", \"年糕\": \"若干\", \"豆腐\": \"1块\", \"干海带\": \"一节\", \"香菇\": \"若干\", \"油豆腐\": \"若干\", \"酱油\": \"适量\", \"盐\": \"适量\"}', 11, '2020-02-27 22:29:31', 0, '2020-03-12 19:36:05', '426961');
INSERT INTO `menu` VALUES (453, '泰式生腌螃蟹', 88, '{\"1\": \"螃蟹洗净。\", \"2\": \"去掉腮清理干净。\", \"3\": \"蟹腿拍一下。\", \"4\": \"再剁适当大小的块入容器中，放入蒜末。\", \"5\": \"加入小米辣。\", \"6\": \"加入香菜末。\", \"7\": \"鱼露，香油，生抽，糖，柠檬汁拌匀。\", \"8\": \"入冰箱冷藏一小时以上或过夜口味更好。\"}', '{\"螃蟹\": \"5只\", \"鱼露\": \"少许\", \"香油\": \"少许\", \"蒜末\": \"适量\", \"香菜\": \"适量\", \"小米辣\": \"一个\", \"生抽\": \"适量\", \"柠檬汁\": \"适量\", \"糖\": \"适量\"}', 13, '2020-02-27 22:29:31', 0, '2020-03-12 19:36:05', '416543');
INSERT INTO `menu` VALUES (454, '关东煮', 76, '{\"1\": \"超市买现成的关东煮丸子。\", \"2\": \"青菜 金针菇去根洗净备用。\", \"3\": \"适量清水加鸡汁火锅底料4块（后加了一块）。\", \"4\": \"加入姜片 葱段。\", \"5\": \"大火煮开。\", \"6\": \"加入各种丸子，蟹足棒等烧开后煮熟。\", \"7\": \"加入培根煮开。\", \"8\": \"加入金针菇煮开。\", \"9\": \"最后加入青菜断生后即可食用。\"}', '{\"牛肉丸\": \"少许\", \"鱼丸\": \"少许\", \"蟹足棒\": \"少许\", \"火腿肠\": \"少许\", \"青菜\": \"少许\", \"金针菇\": \"少许\", \"培根\": \"一袋\", \"鱼豆腐\": \"少许\", \"鸡汁火锅底料\": \"4块\", \"姜\": \"少许\", \"葱\": \"少许\", \"清水\": \"适量\"}', 11, '2020-02-27 22:29:32', 0, '2020-03-12 19:36:05', '425571');
INSERT INTO `menu` VALUES (455, '日式红豆饼', 74, '{\"1\": \"准备好面团的所有材料，除黄油外，按照先液体后粉类最后酵母和盐的顺序把面团的材料放进面包桶，启动imix程序揉面。\", \"2\": \"揉面10分钟后，揉至面团光滑，加入软化的黄油，揉至完全吸收。\", \"3\": \"一个程序后，面团能拉出薄膜就可以了。\", \"4\": \"揉好的面团放到大碗里，盖上保鲜膜，可以室温或者放烤箱30度发酵。\", \"5\": \"面团发酵到1.5-2倍大，手指沾面粉戳一个洞，不回缩、不塌陷，发酵完成。\", \"6\": \"发酵好的面团利用擀面杖排气，再分成6个同等大小的面团。\", \"7\": \"揉圆，盖上保鲜膜，静置15分钟。\", \"8\": \"豆沙平均分成6份，揉圆。\", \"9\": \"醒发好后的面团擀成圆饼，把一颗红豆馅放中间。\", \"10\": \"四边以对角的形式往中间提。\", \"11\": \"然后收口，把口捏紧，收口向下轻轻按压。\", \"12\": \"依次把所有面团全部包上，放在烤盘里。\", \"13\": \"整形好的红豆饼进行二发，可以室温，如果天气变冷了，就用烤箱30--35度发酵，发酵好以后，表面喷些水，撒上一点白芝麻。\", \"14\": \"在红豆饼上面铺一层油纸，油纸上面压一个烤盘，顺便轻轻按压，预热好的烤箱上下火190度，烤18分钟就可以吃啦，刚出炉，热乎乎，超级超级软。\"}', '{\"高筋面粉\": \"170g\", \"低筋面粉\": \"30g\", \"细砂糖\": \"18g\", \"奶粉\": \"15g\", \"盐\": \"2.5g\", \"黄油\": \"15g\", \"红豆沙\": \"270g\", \"水\": \"125g\", \"干酵母\": \"3g\"}', 11, '2020-02-27 22:29:33', 0, '2020-03-12 19:36:05', '423972');
INSERT INTO `menu` VALUES (456, '泰式滋味鸡', 75, '{\"1\": \"鸡剁成小块洗净。\", \"2\": \"加入盐，料酒，白胡椒粉，腌制30分钟。\", \"3\": \"蒜瓣备好，姜去皮切片，香葱切长段，红椒切丝。\", \"4\": \"泰式甜辣酱、老抽、生抽、白糖加清水调成酱汁。\", \"5\": \"起锅加入鸡块翻炒至变色。\", \"6\": \"加入葱姜蒜炒出香味。\", \"7\": \"加入酱汁，煮开后转小火将鸡肉炖熟。\", \"8\": \"炖好开盖，加入红椒丝。\", \"9\": \"翻炒至汤汁浓稠，关火出锅撒上白芝麻即可。\"}', '{\"鸡肉\": \"半只\", \"红尖椒\": \"1个\", \"盐\": \"1茶匙\", \"料酒\": \"1勺\", \"白胡椒粉\": \"1茶匙\", \"蒜瓣\": \"3瓣\", \"姜片\": \"3片\", \"香葱\": \"1根\", \"泰式甜辣酱\": \"2汤匙\", \"老抽\": \"1茶匙\", \"生抽\": \"1勺\", \"白糖\": \"1茶匙\", \"白芝麻\": \"1茶匙\"}', 13, '2020-02-27 22:29:34', 0, '2020-03-12 19:36:05', '399408');
INSERT INTO `menu` VALUES (457, '韩式冬至粥（동지팥죽)', 79, '{\"1\": \"提前泡红豆，泡4小时以上；\", \"2\": \"图方便买了小小圆子；\", \"3\": \"煮红豆：大火开锅后，小火煮30分钟；\", \"4\": \"一边煮红豆，一边洗净大米泡上；\", \"5\": \"红豆煮好后用料理机打成糊糊；\", \"6\": \"打成这程度；\", \"7\": \"放入泡好的大米，不停搅拌防止糊锅底；\", \"8\": \"熬至大米变胖没有硬心；\", \"9\": \"放入小圆子，再放些盐；\", \"10\": \"煮到小圆子飘起来就熬好了；\", \"11\": \"做得了；\", \"12\": \"不错不错 比传统做法简便了很多^_^\", \"13\": \"第二天 加热继续吃\"}', '{\"红豆\": \"适量\", \"大米\": \"适量\", \"玲珑小圆子\": \"适量\", \"盐\": \"适量\"}', 12, '2020-02-27 22:29:34', 0, '2020-03-12 19:36:05', '433329');
INSERT INTO `menu` VALUES (458, '韩国大酱汤', 22, '{\"1\": \"锅里放2/3量的水，放入三勺大酱（大酱提前放小碗里用少量水化开后再倒入锅里）\", \"2\": \"依次放入洋葱，蒜末，从难煮开的材料开始放，猪肉，土豆，豆腐，西葫芦，最后放小白菜。喜欢辣的也可以切两个小米辣丁放进去。\", \"3\": \"煮10分钟左右放入大喜大1勺，不要加盐，大酱本身的咸度住够了。蒜末也放进去。\", \"4\": \"放进砂锅，完活！\"}', '{\"猪肉\": \"5克\", \"豆腐\": \"1块\", \"土豆\": \"0.5个\", \"西葫芦\": \"1/3个\", \"小白菜\": \"一小把\", \"大酱\": \"3勺\", \"蒜\": \"2瓣\", \"牛肉粉\": \"1小勺（大喜大）\"}', 12, '2020-02-27 22:29:35', 0, '2020-03-12 19:36:05', '503882');
INSERT INTO `menu` VALUES (459, '咖喱菠萝炒饭', 86, '{\"1\": \"虾去皮，去虾线，沿背背剪开，用适量盐，料酒，一点点淀粉抓匀腌制片刻。\", \"2\": \"菠萝纵切，大的约2/3，小的1/3。\", \"3\": \"大半用刀划切出长方形，挖出果肉。\", \"4\": \"切成小丁，用盐水泡10分钟。攥出水份。\", \"5\": \"绰过水的豌豆，培根丁，红椒丁，菠萝丁。\", \"6\": \"锅内适量油烧热。\", \"7\": \"虾仁倒入，翻炒几下，虾仁变红盛出备用。\", \"8\": \"豌豆，红椒，培根放入煸炒。\", \"9\": \"放入两块好侍辣咖喱。\", \"10\": \"小火炒化。\", \"11\": \"米饭放入。\", \"12\": \"翻炒均匀。\", \"13\": \"菠萝和虾仁放入。\", \"14\": \"翻匀关火。\", \"15\": \"成品。\", \"16\": \"成品。\", \"17\": \"成品。\"}', '{\"米饭\": \"适量\", \"菠萝\": \"一个\", \"虾仁\": \"适量\", \"红椒\": \"适量\", \"培根\": \"三片\", \"豌豆\": \"适量\", \"咖喱\": \"2块\", \"盐\": \"适量\", \"料酒\": \"适量\", \"生粉\": \"适量\", \"花生油\": \"适量\"}', 13, '2020-02-27 22:29:35', 0, '2020-03-12 19:36:05', '396752');
INSERT INTO `menu` VALUES (460, '无需烤箱的面包 - 日式咖喱肉松多拿滋', 54, '{\"1\": \"准备所有食材。\", \"2\": \"将除黄油以外的所有食材放入搅拌盆中，我用的海氏HM740厨师机1档混合面团成团大约1分钟左右，调至2档继续搅拌约20分钟。\", \"3\": \"检测面团，可以拉出大片的面团的薄膜，可以清楚的看到不均匀的纹理，此时加入黄油。\", \"4\": \"继续揉约10分钟左右，检测面团轻松拉出大片薄膜，面团均匀，破洞的边缘光滑，柔韧性强，面团温度25℃左右。\", \"5\": \"将面团揉圆放入发酵盒中放入发酵箱，温度设定27℃，发酵约50分钟左右，手指蘸面粉从戳入面团中间，不立即回缩的状态即发酵完成。\", \"6\": \"在发面的时间我们来炒内馅，易小焙随心咸馅倒入平底锅内炒至温热，加入咖喱块稍加水和馅一起炒拌均匀。\", \"7\": \"待内馅温凉加入肉松拌匀，待用。\", \"8\": \"将发酵完成的面团分割，揉圆整形，盖上保鲜膜松弛约30分钟，用手检测请按压表面不回缩。\", \"9\": \"将面团收口向下，用手心压平，将面团翻面滚圆的收口面放上肉松咖喱馅。将面团四周像包包子一样包好，用虎口捏紧将收口折叠。\", \"10\": \"排入烤盘进行二发，温度设定32℃，湿度75%，发酵约40分钟，轻按压面团微微回弹。\", \"11\": \"二发完成的面包，裹一层全蛋液。\", \"12\": \"将晒干的吐司片，打成面包糠，将裹了蛋液的面包再裹一层面包糠。\", \"13\": \"锅中放入油，加热后，放入裹好面包糠的面包，炸至四周金黄。\", \"14\": \"用厨房用纸粘干油，就可以吃了，不用烤箱的面包，喜欢咖喱的会深爱，隔夜也是很好吃的，超美味的多拿滋。\"}', '{\"高筋面粉\": \"200g\", \"低筋面粉\": \"50g\", \"奶粉\": \"10g\", \"细砂糖\": \"20g\", \"盐\": \"3g\", \"全蛋\": \"55g\", \"水\": \"110g\", \"酵母\": \"3.5g\", \"黄油\": \"25g\", \"易小焙随心咸馅\": \"500g\", \"百梦多咖喱\": \"2块\", \"肉松\": \"适量\", \"全蛋液\": \"表面\", \"面包糠\": \"表面\", \"油\": \"表面\"}', 11, '2020-02-27 22:29:35', 0, '2020-03-12 19:36:05', '423666');
INSERT INTO `menu` VALUES (461, '飞鱼籽寿司', 80, '{\"1\": \"将糯米在蒸锅蒸熟\", \"2\": \"一个鸡蛋打散，煎薄片\", \"3\": \"三文鱼切片\", \"4\": \"糯米饭用调料拌好，做成长方形块，高3厘米，将海苔、鸡蛋海苔切成同样大小的块放在米饭块上，再放一块同样大小的米饭块\", \"5\": \"饭团上在放一片三文鱼片，摆盘。几个饭团按以上都做好了.\", \"6\": \"每个三文鱼上都放一勺飞鱼籽，撒上白芝麻粒，葱花碎撒盘上和寿司上。\", \"7\": \"用鳗鱼汁画一幅画，大功告成，非常完美漂亮好吃的寿司做好了。\"}', '{\"飞鱼籽\": \"4勺\", \"糯米饭\": \"100g\", \"三文鱼片\": \"4块\", \"海苔\": \"4g\", \"鸡蛋\": \"1个\", \"醋\": \"1ml\", \"香油\": \"1ml\", \"盐\": \"1勺\"}', 11, '2020-02-27 22:29:36', 0, '2020-03-12 19:36:05', '422416');
INSERT INTO `menu` VALUES (462, '自制韩式拌饭', 75, '{\"1\": \"准备好所需菜品，根据自己的口味随意选择。洗干净，胡萝卜可切成丝。\", \"2\": \"香菇、胡萝卜、金针菇、菠菜焯一下水，然后用平底锅放少量油，除了紫甘蓝，将其他菜品小炒一下，炒熟即可。\", \"3\": \"用小刷子在石锅底部和周围刷上一层香油，不用太多，刷一层就够。防止米饭糊锅。\", \"4\": \"在刷好油的石锅中先铺好炒好的豆芽菜，再放一小碗米饭，铺平。新蒸的或者隔夜的都行。我用的隔夜的。隔夜的米饭可以做之前热一下。米饭放多少根据石锅的大小和自己的食量来定。\", \"5\": \"在米饭上面铺放其他炒好的菜品。\", \"6\": \"铺好菜后将石锅盖好，整个放到火上加热。微火加热至出现“滋滋”声后关火。开盖淋上拌饭酱。\", \"7\": \"最后在酱上放一个煎鸡蛋。\", \"8\": \"题外话：把饭拌匀再吃，就着跟酱一起买的辣白菜和萝卜泡菜，棒棒哒！\"}', '{\"臻酱园的拌饭酱\": \"随自己口味适量\", \"米饭\": \"一碗\", \"豆芽菜\": \"适量\", \"胡萝卜\": \"适量\", \"香菇\": \"适量\", \"紫甘蓝\": \"适量\", \"油菜\": \"适量\", \"菠菜\": \"适量\", \"金针菇\": \"适量\", \"香油\": \"适量\", \"鸡蛋\": \"适量\"}', 12, '2020-02-27 22:29:37', 0, '2020-03-12 19:36:05', '505031');
INSERT INTO `menu` VALUES (463, '牛肉咖喱饭', 33, '{\"1\": \"牛肉切粒抹料。\", \"2\": \"蔬菜切丁。\", \"3\": \"蔬菜切丁。\", \"4\": \"主要材料咖喱。\", \"5\": \"先把牛肉炒至表面变色。\", \"6\": \"再放入切好的配料丁，炒5分钟。\", \"7\": \"放入咖喱。\", \"8\": \"加蚝油、生抽、盐适量，加入勾芡，再炒三分钟。\", \"9\": \"最后出锅。\"}', '{\"牛肉\": \"3两\", \"胡萝卜\": \"1个\", \"土豆\": \"1个\", \"咖喱\": \"两小块\", \"西红柿\": \"半个\"}', 13, '2020-02-27 22:29:37', 0, '2020-03-12 19:36:05', '395765');
INSERT INTO `menu` VALUES (464, '海苔饭卷', 23, '{\"1\": \"土豆去皮洗干净，豌豆去皮取豆，洋葱洗干净，备好米饭、海苔。\", \"2\": \"海苔、洋葱、土豆切小丁。\", \"3\": \"起油锅，洋葱入锅炒。\", \"4\": \"洋葱炒出香味，豌豆入锅炒。\", \"5\": \"土豆入锅炒。\", \"6\": \"加盐炒。\", \"7\": \"米饭入锅炒。\", \"8\": \"海苔碎入锅炒，炒均匀盛出。\", \"9\": \"取一张海苔，炒好的米饭放在海苔上。\", \"10\": \"从一侧卷起。\", \"11\": \"卷好的海苔卷切段。\", \"12\": \"装盘即可享用。\"}', '{\"米饭\": \"90g\", \"海苔\": \"40g\", \"橄榄油\": \"适量\", \"盐\": \"适量\", \"豌豆\": \"适量\", \"土豆\": \"适量\", \"洋葱\": \"适量\"}', 11, '2020-02-27 22:29:37', 0, '2020-03-12 19:36:05', '422309');
INSERT INTO `menu` VALUES (465, '韩式拌面', 51, '{\"1\": \"准备韩式辣酱，将韩式辣酱，白糖，辣椒面，蒜蓉，酱油加水，放锅里烧开，盛到碗里备用\", \"2\": \"另起一锅，煮面条\", \"3\": \"面条煮熟后，捞到酱汁里拌匀。完成！\"}', '{\"面条\": \"适量\", \"韩式辣酱\": \"2勺\", \"白糖\": \"适量\", \"酱油\": \"1勺\", \"蒜蓉\": \"1勺\", \"辣椒粉\": \"适量\"}', 12, '2020-02-27 22:29:38', 0, '2020-03-12 19:36:05', '504989');
INSERT INTO `menu` VALUES (466, '泰式胡萝卜沙拉', 18, '{}', '{\"胡萝卜\": \"半根\", \"卷心生菜\": \"3片\", \"鱼露\": \"适量\", \"小泰椒\": \"3个\", \"蒜\": \"4块\", \"虾米\": \"1勺\", \"柠檬\": \"半颗\", \"熟花生米\": \"1勺\", \"白糖\": \"半勺\"}', 13, '2020-02-27 22:29:39', 0, '2020-03-12 19:36:05', '394310');
INSERT INTO `menu` VALUES (467, '韩式泡萝卜', 3, '{\"1\": \"萝卜去皮切小方块。(切片，切小块都可以，随自己喜欢。)\", \"2\": \"放盐腌2-3小时出水。(放了3小匙的盐，相当于1大匙。)\", \"3\": \"把腌过的萝卜用水冲洗一下，这时候尝尝咸度，如果太咸就再冲水，直到适合自家咸度。\", \"4\": \"沥干水备用。\", \"5\": \"韭菜切小段。(我喜欢吃韭菜，所以多放点。)\", \"6\": \"梨去皮去核剁碎。(可以用搅拌机打烂，我懒得洗，就用刀剁碎了。)\", \"7\": \"姜蒜同样剁碎碎。\", \"8\": \"糯米粉放水拌匀。(糯与水比例：1：10)\", \"9\": \"煮成糊状，放凉。(煮的时候要不停的搅拌，防止糊底。)\", \"10\": \"把姜蒜，梨，鱼露和糖放入糯米糊里拌匀。\", \"11\": \"把韭菜和糊糊放入萝卜里拌匀，放入辣椒粉拌匀。(我用的是中等辣的辣椒粉。)\", \"12\": \"拌匀后，密封放冰箱保存。\", \"13\": \"1-2天就可以吃了。(最好不要超过5天，会发酸。)\", \"14\": \"非常爽脆开胃。\"}', '{\"白萝卜\": \"1根(约1200g）\", \"韭菜\": \"130g\", \"梨\": \"1个\", \"姜\": \"1小块\", \"蒜头\": \"1个\", \"盐\": \"1大匙\", \"糖\": \"1大匙\", \"韩国辣椒粉\": \"2大匙\", \"鱼露\": \"2茶匙\", \"糯米粉\": \"20g\", \"水\": \"200g\"}', 12, '2020-02-27 22:29:40', 0, '2020-03-12 19:36:05', '500220');
INSERT INTO `menu` VALUES (468, '正宗泰式冬阴功汤', 38, '{\"1\": \"南姜和香茅草洗净。\", \"2\": \"原料备齐，草菇对半切开，大虾开背去虾线，用少许料酒腌一下去腥。\", \"3\": \"南姜切片，香茅草拍下，切段，线椒切小块。\", \"4\": \"南姜、香茅草、线椒、柠檬叶放入水中。\", \"5\": \"开锅后小火煮3分钟。\", \"6\": \"放入冬阴功酱。\", \"7\": \"再放入椰子粉、草菇和大虾煮。\", \"8\": \"再加上鱼露和柠檬汁，成品。\"}', '{\"大虾\": \"4个\", \"草菇\": \"5个\", \"香茅草\": \"50g\", \"南姜\": \"适量\", \"冬阴功酱\": \"1大勺\", \"红线椒\": \"1个\", \"柠檬\": \"半个\", \"柠檬叶\": \"2片\", \"椰子粉\": \"30g\", \"鱼露\": \"适量\", \"料酒\": \"少许\"}', 13, '2020-02-27 22:29:40', 0, '2020-03-12 19:36:05', '391862');
INSERT INTO `menu` VALUES (469, '韩式拌饭', 88, '{\"1\": \"香菇切片，煮熟备用。\", \"2\": \"胡萝卜切丝，炒熟备用。\", \"3\": \"菠菜焯水，放入盐，芝麻，香油拌均匀，备用。\", \"4\": \"豆芽焯水，放入盐，芝麻，香油拌均匀，备用。\", \"5\": \"鸡蛋，煎一面，撒上白芝麻，备用。\", \"6\": \"取辣白菜，备用。\", \"7\": \"刚出锅的米饭，依次摆放食材，最后放入鸡蛋。\", \"8\": \"美味的韩式拌饭做好了，放入辣酱，开动吧。\"}', '{\"米饭\": \"适量\", \"鸡蛋\": \"一个\", \"辣酱\": \"适量\", \"白芝麻\": \"适量\", \"香菇\": \"适量\", \"菠菜\": \"适量\", \"豆芽\": \"适量\", \"胡萝卜\": \"适量\", \"海带\": \"适量\", \"辣白菜\": \"适量\", \"香油\": \"适量\", \"盐\": \"适量\"}', 12, '2020-02-27 22:29:41', 0, '2020-03-12 19:36:05', '499453');
INSERT INTO `menu` VALUES (470, '咖喱土豆鸡丁', 44, '{\"1\": \"鸡胸脯切丁，用适量盐、淀粉、料酒腌制15分钟\", \"2\": \"土豆、胡萝卜、洋葱切成丁\", \"3\": \"油热后放鸡肉，炒到鸡肉变白后盛出来\", \"4\": \"放入土豆和胡萝卜，就着炒过鸡肉的油翻炒\", \"5\": \"土豆胡萝卜炒到变软加水，要没过食材\", \"6\": \"盖上锅盖煮一会，放咖喱块\", \"7\": \"继续煮，用筷子可以插过土豆就放炒好的鸡肉，搅拌均匀\", \"8\": \"加入洋葱丁，加少量盐，因为咖喱是有咸味的，再翻炒均匀\", \"9\": \"咖喱和米饭最配啦(￣▽￣)\"}', '{\"鸡胸肉\": \"2块\", \"土豆\": \"1个\", \"胡萝卜\": \"半根\", \"洋葱\": \"半个\", \"咖喱\": \"1块\", \"盐\": \"适量\", \"淀粉\": \"适量\", \"料酒\": \"适量\"}', 13, '2020-02-27 22:29:42', 0, '2020-03-12 19:36:05', '381208');
INSERT INTO `menu` VALUES (471, '美味下饭菜【牛肉咖喱锅】', 61, '{\"1\": \"牛肉洗净，切块。\", \"2\": \"放入冷水锅中，加料酒和葱、姜。\", \"3\": \"汆烫去掉血沫，盛起。\", \"4\": \"用温水洗净控水；高压锅里加适量清水，炖二十分钟左右。\", \"5\": \"牛肉盛出来，汤留下备用。\", \"6\": \"土豆洗净去皮，切成滚刀块。\", \"7\": \"洋葱切成小块。\", \"8\": \"胡萝卜切成滚刀块。\", \"9\": \"炒锅烧热放油。\", \"10\": \"放入洋葱炒出香味。\", \"11\": \"倒入牛肉翻炒。\", \"12\": \"再下胡萝卜、土豆块翻炒片刻。\", \"13\": \"加入牛肉汤，汤不够就加清水，刚刚没过食材即可。\", \"14\": \"大火烧开后加入咖喱块，搅拌至融化。\", \"15\": \"椰浆粉加清水调开，加入锅中。\", \"16\": \"调中火慢炖。\", \"17\": \"等待土豆、胡萝卜和牛肉软烂后，加适量食盐，将汤汁收到浓稠，最好在不粘锅中进行，并随时搅拌防止粘底。\", \"18\": \"提前蒸好米饭，把煮好的咖喱牛肉直接浇在米饭上就可以吃啦。\"}', '{\"牛肉\": \"500g\", \"土豆\": \"2个\", \"胡萝卜\": \"2根\", \"洋葱\": \"半个\", \"咖喱块\": \"100g\", \"椰浆粉\": \"2汤匙\", \"葱\": \"两根\", \"姜\": \"4片\", \"料酒\": \"1汤匙\", \"盐\": \"适量\", \"色拉油\": \"适量\"}', 13, '2020-02-27 22:29:43', 0, '2020-03-12 19:36:05', '375980');
INSERT INTO `menu` VALUES (472, '正宗韩国荞麦冷面', 10, '{\"1\": \"取荞麦冷面200-300g ，开水煮2-3分钟，再用冷水洗3-4次，备用。\", \"2\": \"煮鸡蛋5-6分钟，放入冷水中，易扒皮，切两半，备用。\", \"3\": \"黄瓜切片再切丝，备用。\", \"4\": \"取调料加入白醋，白糖，盐，生抽，白芝麻，备用。\", \"5\": \"取辣白菜放入碗中，之前煮好的牛肉，鸡蛋和黄瓜丝。\", \"6\": \"最后，放入冰水，冰爽可口的韩国荞麦冷面做好了。\"}', '{\"韩国荞麦冷面\": \"200g\", \"黄瓜\": \"一个\", \"鸡蛋\": \"一个\", \"牛肉\": \"适量\", \"辣白菜\": \"适量\", \"白芝麻\": \"适量\", \"白醋\": \"适量\", \"生抽\": \"适量\", \"白糖\": \"适量\", \"盐\": \"适量\", \"冰水\": \"适量\"}', 12, '2020-02-27 22:29:44', 0, '2020-03-12 19:36:05', '498282');
INSERT INTO `menu` VALUES (473, '私房冬阴功汤', 6, '{\"1\": \"蘑菇摘洗干净，虾去壳去虾线，洗干净。尖椒去蒂去籽切块，西红柿一部分切成块，一部分切成丁。洋葱切丁，姜切片（为了吃姜所以切成了丝。）\", \"2\": \"锅热之后倒入少许油放入洋葱和姜，翻炒出香味。\", \"3\": \"加入西红柿丁翻炒至变软。\", \"4\": \"加入开水，放蘑菇煮到蘑菇变软\", \"5\": \"放入尖椒和虾，半袋咖喱粉，1勺糖\", \"6\": \"大火烧开，小火慢煮五分钟，根据个人口味加入适量的盐即可\", \"7\": \"撒上香菜\"}', '{\"虾\": \"适量\", \"西红柿\": \"适量\", \"尖椒\": \"适量\", \"蟹味菇\": \"适量\", \"洋葱\": \"适量\", \"姜\": \"适量\", \"香菜\": \"适量\", \"泰式咖喱粉\": \"适量\", \"盐\": \"适量\", \"糖\": \"适量\"}', 13, '2020-02-27 22:29:45', 0, '2020-03-12 19:36:05', '373669');
INSERT INTO `menu` VALUES (474, '咖喱锅', 76, '{\"1\": \"生菜，香菇，丸子洗净。豆腐，火腿肠切片，玉米宰块，洋葱切丝备用\", \"2\": \"锅里加油加热至冒烟\", \"3\": \"倒入豆腐\", \"4\": \"煎至两面金黄\", \"5\": \"煎好的豆腐\", \"6\": \"到出多余的油，倒入洋葱和生姜，炒香\", \"7\": \"倒入丸子\", \"8\": \"加入清水\", \"9\": \"放入咖喱块，煮至咖喱融化\", \"10\": \"倒入玉米煮至10分钟\", \"11\": \"加入金针菇\", \"12\": \"依次倒入豆腐，火腿肠煮至所有食材熟透（大致10至15分钟）自己掌控火候哦，这个时候比较容易糊锅\", \"13\": \"起锅前加入食盐\", \"14\": \"生菜放入碗里，倒入煮好的食材就可以了\", \"15\": \"成品图欣赏\", \"16\": \"成品图欣赏\", \"17\": \"成品图欣赏\"}', '{\"丸子\": \"185g\", \"金针菇\": \"240g\", \"豆腐\": \"160g\", \"火腿肠\": \"1根\", \"生菜\": \"1颗\", \"玉米\": \"半根\", \"咖喱\": \"2块\", \"盐\": \"6g\", \"水\": \"550g\"}', 14, '2020-02-27 22:29:45', 0, '2020-03-12 19:36:05', '329163');
INSERT INTO `menu` VALUES (475, '韩式泡菜', 92, '{\"1\": \"白菜两颗（重1836g），切开白菜底部，用手撕开。\", \"2\": \"用手撕开的大白菜叶子是整片的，刀切叶子会碎。个头大的白菜，可以分成4份。\", \"3\": \"在白菜的每一层叶片中撒上大颗粒盐80g，腌渍大约7、8小时（如果是用细盐，要减量并缩短腌制时间），靠近菜帮处多撒一点，全部做完用重物压住。每过半小时或一小时，上下颠倒一次，防止下面腌泡的时间过长，吸收盐份过多。\", \"4\": \"腌至白菜打软，对折菜帮不折。用水冲洗白菜上的盐粒，去去白菜的咸味。尝尝白菜的味道，咸了一会放虾酱和鱼露就要减少用量。\", \"5\": \"100克糯米粉加500克水加70克玉米糖浆水开煮5分钟左右，煮至糯米糊浓稠冒泡冒泡。没有玉米糖浆的可以换成糖或者是蜂蜜，但是量要减少。总之是根据自己的口味酌情加。\", \"6\": \"200g白萝卜擦丝，洋葱70g切丝，香葱20g切段，韭菜20g切段\", \"7\": \"去皮苹果半个（110g），去皮梨半个（115g）生姜50g，大蒜210g。将全部材料放入搅拌机中搅碎。\", \"8\": \"粗辣椒粉100g，细辣椒粉100g，自己家做的比较辣的辣椒粉30g，虾酱50g，鱼露70g。\", \"9\": \"在搅碎的果泥中（苹果梨大蒜生姜）放入白萝卜丝、洋葱丝、香葱段、韭菜段，接着加入粗辣椒粉、细辣椒粉、虾酱、鱼露搅拌均匀，加入糯米糊。这个酱料做完两颗白菜后，大约还剩一半。\", \"10\": \"将拌好的泡菜调料，抹在白菜叶的每一层，不用抹的太多。盖上保鲜膜，放在阴凉处发酵一天。\", \"11\": \"然后把泡菜装进保鲜盒内放入冰箱保存。大约3~7天后就可以吃了。\"}', '{\"大白菜\": \"1836g\", \"糯米粉\": \"100g\", \"水\": \"500g\", \"玉米糖浆\": \"70g\", \"粗辣椒粉\": \"100g\", \"细辣椒粉\": \"100g\", \"辣辣椒粉\": \"30g\", \"虾酱\": \"50g\", \"鱼露\": \"70g\", \"苹果\": \"半个\", \"梨\": \"半个\", \"大蒜\": \"210g\", \"生姜\": \"50g\", \"白萝卜\": \"适量\", \"洋葱\": \"适量\", \"香葱\": \"适量\", \"韭菜\": \"适量\"}', 12, '2020-02-27 22:29:46', 0, '2020-03-12 19:36:05', '497238');
INSERT INTO `menu` VALUES (476, '维尼熊咖喱海鲜饭', 93, '{\"1\": \"大虾5个去除虾线去壳剥成虾仁，切小段加入少许料酒和白胡椒粉、一点点盐腌制15分钟。\", \"2\": \"笔管2个去除内脏和头部不能食用部分，切成小块。红极参提前化冻切成小块。\", \"3\": \"1/3个胡萝卜切成小块。\", \"4\": \"1个土豆切成小块。\", \"5\": \"炒锅烧热加入适量植物油，下入姜片爆香。\", \"6\": \"下入胡萝卜和土豆煸炒几下，下入1汤匙生抽和1汤匙料酒煸炒均匀。\", \"7\": \"加入适量热水大火烧开后转中火煮一会。\", \"8\": \"待土豆刚刚熟放入腌制过的虾仁。\", \"9\": \"煮锅烧热水将笔管焯水，打卷后马上捞出\", \"10\": \"煮至虾仁变色后下入咖喱块，小火煮至咖喱融化，可以尝一尝根据自己口味少加一点点盐。\", \"11\": \"然后下入红极参和焯过水的笔管翻炒均匀，煮至汤汁粘稠关火。\", \"12\": \"事先煮一些玉米小碴子和大米的米饭。\", \"13\": \"先用保鲜膜团出维尼熊的头部、耳朵和身体、胳膊。\", \"14\": \"然后用海苔做出五官。\", \"15\": \"将煮好的咖喱海鲜盛入碗中，维尼熊的耳朵我用牙签固定了一下，将维尼熊放在上面即可。\", \"16\": \"成品图\"}', '{\"土豆\": \"1个\", \"胡萝卜\": \"1/3个\", \"红极参\": \"适量\", \"大虾\": \"5个\", \"笔管\": \"2个\", \"米饭\": \"1碗\", \"海苔\": \"少许\", \"咖喱\": \"1小块\", \"盐\": \"少许\", \"生抽\": \"1汤匙\", \"料酒\": \"1汤匙\", \"姜\": \"几片\", \"白胡椒粉\": \"少许\", \"植物油\": \"适量\"}', 13, '2020-02-27 22:29:46', 0, '2020-03-12 19:36:05', '374080');
INSERT INTO `menu` VALUES (477, '咖喱饭', 87, '{\"1\": \"胡萝卜切块\", \"2\": \"土豆切块\", \"3\": \"洋葱切碎\", \"4\": \"鸡肉加盐焯水\", \"5\": \"准备咖喱块\", \"6\": \"橄榄油炒香洋葱，放入其他食材，翻炒后不加任何调料加适量水炖。土豆软糯后关火，放入咖喱块，咖喱块融化后，开小火熬煮3分钟左右关火\", \"7\": \"放片生菜摆盘。如果食材多了，觉得酱料有点淡可以放适量盐\"}', '{\"咖喱酱\": \"6块\", \"生菜\": \"1片\", \"土豆\": \"2个\", \"胡萝卜\": \"2根\", \"鸡肉\": \"500g\", \"米饭\": \"150g\"}', 14, '2020-02-27 22:29:46', 0, '2020-03-12 19:36:05', '326068');
INSERT INTO `menu` VALUES (478, '泰式冬阴功汤', 55, '{\"1\": \"备好基础调料：冬阴功汤料、良姜、香茅、青柠檬、柠檬叶、朝天椒。本次用椰粉代替了椰浆。\", \"2\": \"鲜虾洗净，剃好虾线。\", \"3\": \"龙利鱼切块。番茄切块。口蘑洗净切片，焯水15分钟。鲜虾两面煎一下，提鲜且有光泽，更加美观。\", \"4\": \"鸡汤倒入锅中，放入冬阴功汤料、香茅、良姜、柠檬叶、朝天椒，煮沸出香味后再小火煮十五分钟，然后用小漏勺将香茅和良姜滤掉，仅剩汤汁。如嫌麻烦可省略过滤步骤。放入口蘑、番茄、龙利鱼。龙利鱼即将煮熟时放入鲜虾。大约六分钟后加小番茄、白糖、柠檬汁、鱼露、椰粉。酌情添加食盐。\", \"5\": \"装盘，放入两片青柠檬片装饰。\"}', '{\"鲜虾\": \"适量\", \"口蘑\": \"适量\", \"番茄\": \"适量\", \"小番茄\": \"适量\", \"香茅\": \"适量\", \"青柠檬\": \"适量\", \"柠檬叶\": \"适量\", \"龙利鱼\": \"适量\", \"朝天椒\": \"适量\", \"鱼露\": \"适量\", \"良姜\": \"适量\", \"椰浆\": \"适量\", \"鸡汤\": \"适量\", \"白糖\": \"适量\", \"冬阴功调料块\": \"适量\"}', 13, '2020-02-27 22:29:47', 0, '2020-03-12 19:36:05', '372244');
INSERT INTO `menu` VALUES (479, '韩式拌冷面', 74, '{\"1\": \"准备冷面。这是延吉带回来的。\", \"2\": \"取出冷面，浸泡一下。\", \"3\": \"鸡蛋中加入少许盐，少许清水，打散。平底锅预热，加少许玉米油，放入鸡蛋，轻轻晃匀。无需翻面。2分钟左右即熟，成为薄鸡蛋饼。\", \"4\": \"鸡蛋饼切细条，并切好其他蔬菜。\", \"5\": \"煮好冷面，过冰水后取出。\", \"6\": \"准备调料汁，这是附赠的，有糖醋汁、辣椒油、辣椒酱三个包。倒在碗里拌匀，即为调料汁。\", \"7\": \"冷面上摆好配菜，倒上调料汁。美美哒食用。\"}', '{\"冷面\": \"适量\", \"冷面调料汁\": \"适量\", \"西红柿\": \"适量\", \"胡萝卜\": \"适量\", \"黄瓜\": \"适量\", \"鸡蛋\": \"1个\", \"辣白菜\": \"适量\"}', 12, '2020-02-27 22:29:48', 0, '2020-03-12 19:36:05', '479043');
INSERT INTO `menu` VALUES (480, '#信任之美#印度味道之奶茶', 45, '{\"1\": \"绿色小豆蔻荚去壳放入牛奶中，加入2勺糖，肉桂，丁香，大火烧开\", \"2\": \"过滤掉所有调料和茶叶，分两杯即可。每杯里稍微加一点开水，也可以不加水，如果喜欢更浓的。\"}', '{\"红茶\": \"2茶匙\", \"牛奶\": \"一杯半\", \"热水\": \"适量\", \"糖\": \"2茶匙\", \"豆蔻荚\": \"2颗\", \"肉桂\": \"一根\", \"丁香粒\": \"2粒\"}', 14, '2020-02-27 22:29:48', 0, '2020-03-12 19:36:05', '298671');
INSERT INTO `menu` VALUES (481, '泰式海鲜酸辣河粉', 11, '{\"1\": \"虾处理干净，蟹柳切小段，鱿鱼切花刀\", \"2\": \"豆芽稥莱洗净，洋葱切细丝\", \"3\": \"锅中加入一碗水煮开，放入冬阴调料块，加入糖和少许盐煮约3分钟\", \"4\": \"放入准备好的蛤蜊、鱿鱼、虾，最后下蟹柳煮熟，熄火\", \"5\": \"锅中加水煮开，将豆芽汆汤一下\", \"6\": \"河粉在开水中汆汤，捞起控干水装入碗中\", \"7\": \"倒入煮好的海鲜酸辣汤\", \"8\": \"放上豆芽、洋葱、稥莱和葱花就完成了\"}', '{\"河粉\": \"200克\", \"虾\": \"3尾\", \"小鱿鱼\": \"1条\", \"蟹柳\": \"1根\", \"蛤蜊\": \"6颗\", \"香菜\": \"少许\", \"葱花\": \"少许\", \"豆芽\": \"适量\", \"洋葱\": \"少许\", \"泰式冬阴调料块\": \"1块\", \"糖\": \"1茶匙\", \"盐\": \"少许\"}', 13, '2020-02-27 22:29:48', 0, '2020-03-12 19:36:05', '372168');
INSERT INTO `menu` VALUES (482, '韩国泡菜', 85, '{\"1\": \"去除大白菜表层受伤脏掉的，在底部用菜刀划开约15公分深度切口。\", \"2\": \"用双手撕开对半。\", \"3\": \"用盐均匀的洒在叶白处。\", \"4\": \"底部浸泡在盐水里約2小時后。\", \"5\": \"全部浸泡在盐水里5~6小時, 白菜全軟（不可太咸，撕下一小块用水清洗，尝尝看有点咸味就可以了）。\", \"6\": \"用清水洗净，放在筛网上約1~2小時沥水备用。\", \"7\": \"韭菜、小香葱 切4cm段备用, 辣椒切片。\", \"8\": \"紅白萝卜切丝，备用。\", \"9\": \"把所有泡菜酱调料放入，果汁机里打成泡菜酱(冷饭和糯米糊的效果是一样的)。\", \"10\": \"泡菜酱里放入萝卜丝、葱、韭菜轻轻地搅拌。\", \"11\": \"把拌好的酱料，均匀的涂抹在白菜每片叶子上, 找来容器把塗好酱料的白菜平整的放在容器里，一层一层叠放，盖上盖子等它发哮就可以了。\", \"12\": \"把拌好的白菜层层放在容器内，用手压实，放在室温中保存，很快就会发酵。\"}', '{\"大白菜\": \"2颗\", \"盐\": \"2杯\", \"水\": \"6000cc (35℃~40℃溫水)\", \"萝卜\": \"1/2颗\", \"紅萝卜\": \"1颗\", \"细韭菜\": \"60克\", \"小香葱\": \"60克\", \"松子\": \"2大匙\", \"细辣椒粉\": \"一杯半\", \"粗辣椒粉\": \"一杯\", \"鱼露\": \"半杯\", \"虾酱\": \"三大匙\", \"砂糖\": \"1大匙\", \"糯米糊\": \"2杯\", \"蒜头\": \"15瓣\", \"姜\": \"一小塊\", \"洋葱\": \"半个\", \"水梨\": \"半个\"}', 12, '2020-02-27 22:29:49', 0, '2020-03-12 19:36:05', '479165');
INSERT INTO `menu` VALUES (483, '印度豌豆奶豆腐', 54, '{}', '{\"印度奶豆腐\": \"适量\", \"豌豆\": \"适量\", \"西红柿\": \"适量\", \"香菜\": \"适量\", \"盐\": \"适量\", \"黑胡椒粒\": \"适量\", \"香菜种子\": \"适量\", \"腰果\": \"适量\", \"丁香\": \"适量\", \"洋葱\": \"适量\", \"绿辣椒\": \"适量\", \"姜\": \"适量\", \"大蒜\": \"适量\", \"肉桂\": \"适量\", \"黄油\": \"适量\", \"孜然粒\": \"适量\", \"姜黄\": \"适量\", \"辣椒粉\": \"适量\", \"葛拉姆马萨拉\": \"适量\"}', 14, '2020-02-27 22:29:49', 0, '2020-03-12 19:36:05', '254190');
INSERT INTO `menu` VALUES (484, '泰国冬阴功汤', 59, '{\"1\": \"准备好今天要用的材料。\", \"2\": \"大虾和青口贝洗净备用（青口贝要用刷子多刷几遍）\", \"3\": \"要用的香料。\", \"4\": \"锅内放油烧热，倒入姜末和蒜末炒香，放入大虾。\", \"5\": \"大虾两面炒红倒入泰国冬阴功酱\", \"6\": \"炒匀放一边备用。\", \"7\": \"起锅放500g的水，水开后放入墨鱼丸，蟹肉棒，青口贝下去煮熟。最后倒入炒好的虾和酱料，再次沸腾后关火。\", \"8\": \"出锅，挤入柠檬汁，加入薄荷和柠檬片，清香扑鼻。\", \"9\": \"酸辣可口，十分入味～\"}', '{\"大虾\": \"200g\", \"青口贝\": \"8只\", \"冬阴功汤酱料\": \"1袋\", \"青柠\": \"1个\", \"薄荷草\": \"适量\", \"墨鱼丸\": \"5颗\", \"蟹肉\": \"5根\", \"盐\": \"适量\"}', 13, '2020-02-27 22:29:50', 0, '2020-03-12 19:36:05', '372033');
INSERT INTO `menu` VALUES (485, '韩国泡菜锅', 23, '{\"1\": \"豆腐切片，厚约5mm。\", \"2\": \"洋葱切丝。\", \"3\": \"豆芽洗净备用。\", \"4\": \"年糕一片片剥好备用。\", \"5\": \"大葱切片，青辣椒切片，干辣椒切片、蒜头切片备用。\", \"6\": \"泡菜取出备用。\", \"7\": \"鸡肉切片加入调味料，用手抓拌腌制，静置十分钟。\", \"8\": \"炒锅加热后加入少许色拉油，放入腌制好的鸡肉翻炒到半熟。\", \"9\": \"加入备好的泡菜续炒。\", \"10\": \"将炒好的泡菜，鸡肉放到汤锅里。\", \"11\": \"继续放入年糕，豆腐，豆芽，葱，青红辣椒蒜头，再加入500cc的清水。\", \"12\": \"盖上锅盖，开大火煮沸。\", \"13\": \"汤滚后加上一大匙的辣椒粉，转小火盖上锅盖继续煮。\", \"14\": \"煮约5到6分钟，加点鸡精提味就可上桌了。\"}', '{\"泡菜\": \"150g\", \"老豆腐\": \"1/4个\", \"年糕\": \"150g\", \"洋葱\": \"1/4个\", \"黄豆芽\": \"150g\", \"鸡胸肉\": \"1块\", \"水\": \"500cc\", \"大葱\": \"1根\", \"青辣椒\": \"2根\", \"干辣椒\": \"1根\", \"蒜头\": \"2瓣\", \"辣椒粉\": \"1大匙\", \"麻油\": \"1茶匙\", \"料酒\": \"1大匙\", \"酱油\": \"1茶匙\", \"鸡粉\": \"1茶匙\", \"土豆粉\": \"1大匙\"}', 12, '2020-02-27 22:29:50', 0, '2020-03-12 19:36:05', '477412');
INSERT INTO `menu` VALUES (486, '印度玛夏拉红茶', 51, '{}', '{\"阿萨姆红茶叶\": \"5g\", \"小豆蔻\": \"3粒\", \"桂皮\": \"1段\", \"老姜\": \"约5克\", \"丁香\": \"2粒\", \"八角\": \"1枚\", \"全脂牛奶\": \"250ml\", \"纯净水\": \"250ml\", \"白砂糖\": \"4茶匙（20g）\"}', 14, '2020-02-27 22:29:50', 0, '2020-03-12 19:36:05', '233049');
INSERT INTO `menu` VALUES (487, '泰式鸡酱带鱼卷', 98, '{\"1\": \"选择国产带鱼三条，肉看着比较厚一点的。\", \"2\": \"片鱼的时候贴近鱼中间的大梁骨，由鱼尾往头部方向片\", \"3\": \"把片子来的鱼肉切成三段，倒入盆中加适量盐和料酒腌制一下\", \"4\": \"鱼骨加盐和料酒腌制后用根串串的签一先穿头部，再穿腰部，后穿尾部，拍上淀粉待用\", \"5\": \"将腌好的鱼肉从有肉的面卷起来，用牙签固定住，拍上淀粉待用\", \"6\": \"炸的时候先炸鱼骨，呈金黄色即可，然后插在一半心里美萝卜上，放到盘子中间。鱼卷也炸成金黄色围到盘子周围\", \"7\": \"最后将泰国鸡酱均匀地涂抹在鱼卷上即可。\"}', '{\"带鱼\": \"500g\", \"泰式鸡酱\": \"5ml\"}', 13, '2020-02-27 22:29:51', 0, '2020-03-12 19:36:05', '371097');
INSERT INTO `menu` VALUES (488, '印度飞饼', 14, '{\"1\": \"把炼乳加水调匀，\", \"2\": \"把调好的汁水慢慢加进面粉里，搅拌边加边搅拌，\", \"3\": \"把全蛋液加进去，同样慢慢加，边搅拌边加，\", \"4\": \"把色拉油加进去，揉至成光滑的面团，\", \"5\": \"用电动打蛋器的和面的钩，低速挡搅拌，搅拌3-4min左右，\", \"6\": \"把软化的黄油切成小块，加进去，继续搅拌，3min左右，\", \"7\": \"直至变成光滑的面团，拉开能基本成扩展阶段即可，（制作面包的扩展阶段，用手轻轻拉开能成薄膜）用纱布盖起来，静置3小时左右，\", \"8\": \"分成等量份的小剂子，（我家的平底锅比较小，所以面团就分的小一些，）\", \"9\": \"把面团先按压开，\", \"10\": \"用手提起边缘，在空中画圆弧形，（动作幅度什么的自己掌握吧，我也是练习了好几次才成功的，）\", \"11\": \"放在案板上能透过面皮看到桌上的花纹，撒上全蛋液，把四周向中间折叠，折成一个方形，平底锅内放油，油要刚刚铺满锅底，然后翻进去煎炸至两面金黄香脆即可，拿出沥干油，切成小块，淋上适量炼乳即可，\", \"12\": \"还可以放上一些蔓越莓干、葡萄干、香蕉之类的，个人最喜欢的是香蕉味的，放熟透的香蕉，特别好吃，\"}', '{\"水\": \"75g\", \"白砂糖\": \"5g\", \"全蛋液\": \"30g+适量\", \"小苏打\": \"1/4小勺\", \"色拉油\": \"10g\", \"炼乳\": \"10g+适量\", \"盐\": \"1g\", \"高筋面粉\": \"150g\", \"黄油\": \"20g\"}', 14, '2020-02-27 22:29:52', 0, '2020-03-12 19:36:05', '208965');
INSERT INTO `menu` VALUES (489, '早渔人家|韩式烤鱼糕', 56, '{\"1\": \"准备食材，鱼糕切成小三角形，姜蒜切末，辣椒切小段；\", \"2\": \"鱼糕煎至两面金黄；\", \"3\": \"辣椒也煎起皮；\", \"4\": \"小火煎一个鸡蛋\", \"5\": \"姜蒜、豆瓣酱、生抽、糖和一小碗水淀粉调成酱汁；\", \"6\": \"煎鸡蛋切成小块；\", \"7\": \"鱼糕蘸上酱汁，再加一块鸡蛋、辣椒，一起裹在生菜里，就可以开吃咯。\"}', '{\"荆州鱼糕\": \"4片\", \"红辣椒\": \"1个\", \"鸡蛋\": \"1个\", \"生菜\": \"1颗\", \"豆瓣酱\": \"1勺\", \"姜\": \"2片\", \"蒜\": \"2瓣\", \"生抽\": \"1勺\", \"糖\": \"1勺\", \"盐\": \"适量\", \"淀粉\": \"1勺\"}', 12, '2020-02-27 22:29:52', 0, '2020-03-12 19:36:05', '475013');
INSERT INTO `menu` VALUES (490, '泰式鱼饼', 29, '{\"1\": \"鱼肉剁成泥\", \"2\": \"加入柠檬叶拌匀\", \"3\": \"加入生粉，咖哩酱拌匀\", \"4\": \"摔打成鱼饼再占些淀粉\", \"5\": \"入锅炸或煎金黄酥脆即可\", \"6\": \"成品图\", \"7\": \"成品图\"}', '{\"龙利鱼\": \"1个\", \"红咖哩酱\": \"1勺\", \"柠檬叶\": \"2个\", \"淀粉\": \"适量\"}', 13, '2020-02-27 22:29:52', 0, '2020-03-12 19:36:05', '370753');
INSERT INTO `menu` VALUES (491, '韩式拌饭', 60, '{\"1\": \"准备好自己喜欢的配菜，炒也可以水煮也可以，看自己喜欢\", \"2\": \"辣椒酱用雪碧卸开（不要放太多，容易变稀），再加入芝麻油，搅拌，可以减少辣味，增加香味。（经过多次测试，这个牌子的辣椒酱最适合）\", \"3\": \"把配菜和肥牛铺到米饭上，最上面放一个煎蛋\", \"4\": \"加入调好的辣椒酱，浓稠度大概这样，不要太稀也不要太稠。\", \"5\": \"搅拌均匀就可以开吃啦，简直不要太香啦。\"}', '{\"米饭\": \"适量\", \"香菇\": \"适量\", \"胡萝卜\": \"适量\", \"西葫芦\": \"适量\", \"豆芽\": \"适量\", \"肥牛卷\": \"适量\", \"煎蛋\": \"适量\", \"韩式辣椒酱\": \"适量\", \"芝麻油\": \"适量\", \"雪碧\": \"适量\"}', 12, '2020-02-27 22:29:53', 0, '2020-03-12 19:36:05', '474874');
INSERT INTO `menu` VALUES (492, '黄金咖喱猪扒饭', 83, '{\"1\": \"洋葱切丁\", \"2\": \"土豆，胡萝卜，牛肉切丁(2厘米左右)，牛肉焯水\", \"3\": \"土豆，洋葱，胡萝卜，牛肉下锅炒断生\", \"4\": \"加水大火煮开转小火(水淹没食材即可)\", \"5\": \"小火半小时后加入咖喱，不停的搅拌\", \"6\": \"咖喱完全融化后搞定\", \"7\": \"猪肉用送肉锤打散里面的筋膜\", \"8\": \"胡椒粉跟盐巴腌制二十分钟\", \"9\": \"腌好的猪扒粘上鸡蛋液裹上面包糠\", \"10\": \"160左右的油温炸两分钟\", \"11\": \"摆好猪扒，淋上咖喱，开吃\"}', '{\"猪肉\": \"500克\", \"牛肉\": \"100克\", \"胡萝卜\": \"1条\", \"土豆\": \"1个\", \"洋葱\": \"1棵\", \"咖喱\": \"50克\", \"盐\": \"5克\", \"面包糠\": \"100克\", \"胡椒粉\": \"5克\", \"鸡蛋\": \"1个\"}', 14, '2020-02-27 22:29:53', 0, '2020-03-12 19:36:05', '198598');
INSERT INTO `menu` VALUES (493, '咖喱什锦饭', 20, '{\"1\": \"土豆、胡萝卜洗净去皮切块，洋葱切丁。\", \"2\": \"锅里下油，油热热，下洋葱翻炒。洋葱煸出香味下胡萝卜，翻炒片刻下土豆一起翻炒。\", \"3\": \"紧接着加水，大火煮沸转中火10分钟左右。\", \"4\": \"注意不能把水烧干，10分钟后加2块咖喱块，不停的翻炒，不然容易粘锅。翻至均匀后出锅。\", \"5\": \"将咖喱土豆浇至米饭上，就完成啦。\"}', '{\"土豆\": \"1个\", \"胡萝卜\": \"1根\", \"洋葱\": \"半个\", \"大米\": \"300g\", \"咖喱块\": \"2个\"}', 13, '2020-02-27 22:29:53', 0, '2020-03-12 19:36:05', '369407');
INSERT INTO `menu` VALUES (494, '韩式五花肉拌饭', 23, '{\"1\": \"准备好各种食材。将大米淘洗干净，倒在甑子里，再放进臻米脱糖电饭煲的内胆里，加适量水，稍微盖过米。最后把内胆放入电饭煲内。\", \"2\": \"盖好盖子，把底部的米汤盒扣好，接通电源后选择“蒸饭”功能，5秒后自动启动，耗时22分钟。在做米饭的同时，处理其他食材。将豆芽和菠菜洗净，焯水后沥净，和黄瓜丝一起撒少许盐拌匀，腌制片刻。\", \"3\": \"把五花肉用少许盐抓匀，在平底锅中小火煎熟，出锅前倒入少许辣白菜汤汁，更入味好吃。将胡萝卜丝也在刚才的锅中稍微炒一下，盛出备用。这样胡萝卜素更容易被人体吸收。\", \"4\": \"米饭蒸好后会自动进入保温程序，这时开盖盛出即可。将米饭倒入拌饭的大碗或小锅中，在底部铺平。\", \"5\": \"把挤掉水分的豆芽、胡萝卜、菠菜、黄瓜丝，和五花肉、辣白菜依次摆在米饭上。将煎好的鸡蛋放在中间，加一勺韩式甜辣酱，撒少许熟芝麻，拌匀就可以吃了。\"}', '{\"大米\": \"2杯\", \"水\": \"适量\", \"豆芽\": \"1把\", \"菠菜\": \"几棵\", \"五花肉\": \"1两\", \"辣白菜\": \"适量\", \"胡萝卜\": \"少许\", \"黄瓜\": \"少许（可选）\", \"鸡蛋\": \"1个\", \"韩式甜辣酱\": \"1大勺\", \"植物油\": \"2勺\", \"熟芝麻\": \"少许\", \"盐\": \"少许\"}', 12, '2020-02-27 22:29:54', 0, '2020-03-12 19:36:05', '467825');
INSERT INTO `menu` VALUES (495, '正宗印度咖喱饭', 44, '{\"1\": \"火腿肠、黄瓜、洋葱、土豆切丁。\", \"2\": \"煮\", \"3\": \"起锅\"}', '{\"火腿肠\": \"200g\", \"黄瓜\": \"250g\", \"洋葱\": \"250g\", \"咖喱粉\": \"30g\"}', 14, '2020-02-27 22:29:55', 0, '2020-03-12 19:36:05', '198174');
INSERT INTO `menu` VALUES (496, '菠萝饭', 94, '{\"1\": \"洋葱 青椒 胡萝卜 菠萝切丁，鸡蛋打散搅打均匀\", \"2\": \"将一半菠萝丁用榨汁机榨成泥\", \"3\": \"热锅凉油，油热之后放入鸡蛋液炒8分熟盛出\", \"4\": \"锅中再倒入适量油，油热之后，放入洋葱 青椒 胡萝卜 菠萝进行翻炒\", \"5\": \"加入米饭翻炒\", \"6\": \"加入鸡蛋翻炒\", \"7\": \"放入盐和菠萝泥翻炒即可。放菠萝泥特别増味\", \"8\": \"装盘\"}', '{\"剩米饭\": \"适量\", \"鸡蛋\": \"2个\", \"青椒\": \"适量（或青豆）\", \"胡萝卜\": \"适量\", \"洋葱\": \"适量\", \"菠萝\": \"适量\", \"油\": \"适量\", \"盐\": \"适量\"}', 13, '2020-02-27 22:29:55', 0, '2020-03-12 19:36:05', '366895');
INSERT INTO `menu` VALUES (497, '韩式冷面', 92, '{\"1\": \"韩式冷面调料包。\", \"2\": \"把调料包剪开倒入容器中，加入醋。\", \"3\": \"加生抽，适量的水，香油，鱼露，糖，蒜姜末拌匀。\", \"4\": \"西瓜切片，黄瓜切丝备用。\", \"5\": \"韩式冷面备用。\", \"6\": \"锅做水冷面凉水下锅烧开，煮软一些，牙齿轻轻咬断即可捞出。\", \"7\": \"投凉控净水分。\", \"8\": \"捞入碗中浇上冷面汤。\", \"9\": \"加黄瓜丝，鸡蛋，辣白菜，芝麻，香菜即可。\"}', '{\"冷面\": \"1把\", \"黄瓜\": \"半个\", \"西瓜\": \"3片\", \"辣白菜\": \"适量\", \"清水\": \"适量\", \"香醋\": \"2勺\", \"糖\": \"2勺\", \"香油\": \"少许\", \"蒜末\": \"适量\", \"姜末\": \"3g\", \"鱼露\": \"少许\", \"芝麻\": \"适量\", \"香菜\": \"少许\", \"调料包\": \"1个\"}', 12, '2020-02-27 22:29:56', 0, '2020-03-12 19:36:05', '361207');
INSERT INTO `menu` VALUES (498, '鲜香浓厚的滋味----【玉米笋咖喱鸡】', 28, '{\"1\": \"鸡腿取肉切丁（骨头可煲汤），用盐和料酒、少量生粉腌制。\", \"2\": \"玉米笋切成2段，洋葱切小，胡萝卜切丁，青椒切丁。\", \"3\": \"锅内放油，放入鸡腿肉炒至变色，乘出。\", \"4\": \"用剩下的少量油，加入胡萝卜炒一下。\", \"5\": \"再加入洋葱炒香。\", \"6\": \"加入炒好的鸡肉和玉米笋，兑水，盖过材料。\", \"7\": \"准备好咖喱。\", \"8\": \"煮开后先加入一块咖喱溶化，一边搅动，以防粘锅。\", \"9\": \"加入青椒的同时再加入一块咖喱，一边搅。\", \"10\": \"煮至汤汁浓稠调味即可。\"}', '{\"咖喱\": \"2块\", \"玉米笋\": \"适量\", \"红萝卜\": \"半个\", \"青椒\": \"半个\", \"鸡腿肉\": \"适量\", \"盐\": \"适量\", \"生粉\": \"适量\", \"料酒\": \"适量\"}', 14, '2020-02-27 22:29:57', 0, '2020-03-12 19:36:05', '188700');
INSERT INTO `menu` VALUES (499, '三文鱼寿司', 54, '{\"1\": \"米饭与沙拉酱拌均匀\", \"2\": \"三文鱼提前准备好\", \"3\": \"米饭弄成椭圆形\", \"4\": \"摆入三文鱼，加入海苔\", \"5\": \"成品图\", \"6\": \"成品图\", \"7\": \"成品图\", \"8\": \"成品图。\"}', '{\"三文鱼\": \"适量\", \"米饭\": \"一碗\", \"海苔\": \"适量\", \"沙拉酱\": \"适量\"}', 12, '2020-02-27 22:29:57', 0, '2020-03-12 19:36:05', '451208');
INSERT INTO `menu` VALUES (500, '泰式北极磷虾炒饭', 85, '{\"1\": \"北极磷虾提前化冻\", \"2\": \"胡萝卜切丁、洋葱切碎\", \"3\": \"锅中加入少许玉米油，加入洋葱碎炒香\", \"4\": \"加入适量磷虾翻炒\", \"5\": \"加入米饭翻炒\", \"6\": \"加入胡萝卜丁翻炒\", \"7\": \"加入2勺泰式大虾膏翻炒\", \"8\": \"大虾膏与米饭翻炒均匀即可出锅\", \"9\": \"用秋葵点缀，香气四溢、色泽诱人的磷虾炒饭！\", \"10\": \"非常好吃！\"}', '{\"北极磷虾\": \"适量\", \"米饭\": \"适量\", \"玉米油\": \"少量\", \"胡萝卜\": \"1/2根\", \"洋葱\": \"1/4个\", \"泰式大虾膏\": \"2勺\"}', 13, '2020-02-27 22:29:58', 0, '2020-03-12 19:36:05', '362567');
INSERT INTO `menu` VALUES (501, '咖喱鸡', 38, '{\"1\": \"准备食材\", \"2\": \"鸡腿切块，土豆、胡萝卜切块，洋葱切丝。\", \"3\": \"锅烧热，到少许植物油，下洋葱丝煸炒。\", \"4\": \"炒至洋葱发黄，倒入鸡块翻炒加少许盐。\", \"5\": \"炒至洋葱发黄，倒入鸡块翻炒加少许盐。\", \"6\": \"鸡块炒至发黄即可下入土豆和胡萝卜块。\", \"7\": \"翻炒均匀\", \"8\": \"向锅内加适量清水，水量以没过材料为宜。\", \"9\": \"大火烧开，撇去浮沫。中火煮10分钟使土豆软烂。关火，将咖喱液块放入锅中。\", \"10\": \"让咖喱液块充分与食材融为一体。\", \"11\": \"盛饭，浇汁，开吃！\"}', '{\"鸡腿\": \"4根\", \"土豆\": \"3个\", \"胡萝卜\": \"1根\", \"洋葱\": \"1个\", \"咖喱液块\": \"1盒\", \"植物油\": \"少许\", \"盐\": \"少许\"}', 14, '2020-02-27 22:29:58', 0, '2020-03-12 19:36:05', '177051');
INSERT INTO `menu` VALUES (502, '黄瓜胡萝卜寿司', 34, '{\"1\": \"米饭与沙拉酱拌均匀备用\", \"2\": \"胡萝卜，黄瓜切片备用\", \"3\": \"肉松提前准备好备用\", \"4\": \"取一片紫菜摆好\", \"5\": \"摆好米饭抹均匀\", \"6\": \"均匀撒入肉松\", \"7\": \"依次摆好胡萝卜黄瓜\", \"8\": \"卷起来，固定10分钟\", \"9\": \"切断，完成。\", \"10\": \"成品图。\"}', '{\"紫菜\": \"适量\", \"胡萝卜\": \"适量\", \"米饭\": \"一碗\", \"肉松\": \"适量\", \"黄瓜\": \"适量\", \"沙拉酱\": \"适量\"}', 12, '2020-02-27 22:29:59', 0, '2020-03-12 19:36:05', '451207');
INSERT INTO `menu` VALUES (503, '泰式冬阴功汤', 62, '{\"1\": \"将香茅，冬阴功酱，草菇，花蛤，明虾备好\", \"2\": \"锅加水烧开，加入冬阴功酱，香茅和花蛤继续烧开\", \"3\": \"最后加入明虾，加入少许的糖，烧开马上起锅\", \"4\": \"直接上桌，可以加入香菜，我忘买了\"}', '{\"明虾\": \"适量\", \"草菇\": \"适量\", \"花蛤\": \"适量\", \"冬阴功酱\": \"3-4匙\", \"香茅\": \"少许\", \"糖\": \"适量\"}', 13, '2020-02-27 22:29:59', 0, '2020-03-12 19:36:05', '362239');
INSERT INTO `menu` VALUES (504, '印度咖喱饼', 93, '{\"1\": \"温水和面，醒发30分钟\", \"2\": \"醒好的面团分成4个小剂子\", \"3\": \"把一个剂子擀成薄片\", \"4\": \"抹上咖喱酱\", \"5\": \"撒上葱花\", \"6\": \"从下往上卷起\", \"7\": \"卷成这个样子\", \"8\": \"稍微擀平\", \"9\": \"全部做好\", \"10\": \"平底锅放油，下锅煎成两面金黄即可\", \"11\": \"烙好的饼，用手轻轻拍散就可以了\"}', '{\"面粉\": \"200g\", \"葱花\": \"适量\", \"印度咖喱\": \"适量\"}', 14, '2020-02-27 22:30:00', 0, '2020-03-12 19:36:05', '165301');
INSERT INTO `menu` VALUES (505, '咖喱鸡腿盖饭', 62, '{\"1\": \"鸡腿去骨切块\", \"2\": \"土豆胡萝卜洋葱切丁\", \"3\": \"热锅凉油，油热之后放入鸡肉翻炒至变色\", \"4\": \"加入土豆胡萝卜洋葱翻炒\", \"5\": \"加入水没过菜，放入咖喱粉混合均匀，大火烧开，小火慢炖至土豆熟透汤汁浓稠关火\", \"6\": \"将菜浇到煮好的米饭上即可\"}', '{\"鸡腿\": \"1个\", \"胡萝卜\": \"1个\", \"土豆\": \"1个\", \"洋葱\": \"半个\", \"咖喱粉\": \"半袋\", \"盐\": \"2匙\"}', 13, '2020-02-27 22:30:01', 0, '2020-03-12 19:36:05', '359519');
INSERT INTO `menu` VALUES (506, '印度咖喱羊肉', 75, '{\"1\": \"羊肉炖熟备用。 \", \"2\": \"锅烧热后加入花椒爆香。\", \"3\": \"加入熟羊肉翻炒。\", \"4\": \"炒至变色\", \"5\": \"加入咖喱粉翻炒。\", \"6\": \"加入盐翻炒。\", \"7\": \"加入生抽翻炒。\", \"8\": \"加入大葱段翻炒。\", \"9\": \"加入孜然。\", \"10\": \"翻炒入味加少许水\"}', '{\"熟羊肉\": \"500g\", \"油\": \"适量\", \"花椒\": \"适量\", \"盐\": \"适量\", \"咖喱粉\": \"适量\", \"生抽\": \"适量\", \"孜然\": \"适量\", \"大葱\": \"适量\"}', 14, '2020-02-27 22:30:01', 0, '2020-03-12 19:36:05', '164105');
INSERT INTO `menu` VALUES (507, '泰式酸辣凤爪', 9, '{\"1\": \"准备材料，木耳泡发，只用几朵就可以了，我这么多是还有拿一大半炒菜。葱姜蒜切好备用。\", \"2\": \"先把鸡脚洗干净，切去指甲，剖成两半。\", \"3\": \".锅里放水和鸡脚煮，水开后撇去浮沫，然后煮5分钟左右。鸡脚很容易熟不要煮太久，煮太短又会过硬难入味\", \"4\": \"把煮好的鸡脚捞出放着冰水里面冰镇，这样可以让鸡脚比较有嚼头。\", \"5\": \"把泡好的木耳切两半，放水里用中火煮，煮8分钟左右就可以了。\", \"6\": \"黄瓜拍一下切成块。\", \"7\": \"把鸡脚，黄瓜，木耳全部放入大碗里。\", \"8\": \"放入葱花，蒜粒，辣椒，放少许酱油，白糖，鱼露，挤进半个柠檬汁，一点点盐和鸡精，拌均就可以了\", \"9\": \"这道菜也是到餐厅里吃到的，一吃就爱上了它。现在也成了我们的一道家常菜，自己做出来味道也非常棒，每次都是一抢而空意犹未尽，柠檬的酸不刺激，很清爽加上辣真的是一道很好的下饭菜。\", \"10\": \"辣椒多少可以根据自己的喜好去放\"}', '{\"鸡脚\": \"6个\", \"干木耳\": \"8朵左右（我用的是小的）\", \"黄瓜\": \"1个\", \"酱油\": \"少许\", \"大蒜\": \"一瓣\", \"葱花\": \"3根\", \"小红尖椒\": \"3个\", \"盐\": \"少许\", \"柠檬\": \"半个\", \"白糖\": \"少量\", \"鱼露\": \"少许\", \"鸡粉\": \"一点点\"}', 13, '2020-02-27 22:30:02', 0, '2020-03-12 19:36:05', '356830');
INSERT INTO `menu` VALUES (508, '紫薯寿司', 50, '{\"1\": \"米饭与沙拉酱拌均匀备用\", \"2\": \"肉松提前准备好待用\", \"3\": \"倒入紫菜一张\", \"4\": \"加入米饭，均匀撒入肉松\", \"5\": \"撒入紫薯\", \"6\": \"依次卷起来，切成小段\", \"7\": \"完成，成品图\", \"8\": \"成品图\", \"9\": \"成品图。\"}', '{\"紫薯\": \"一个\", \"肉松\": \"适量\", \"米饭\": \"一碗\", \"紫菜\": \"适量\", \"沙拉酱\": \"适量\"}', 12, '2020-02-27 22:30:02', 0, '2020-03-12 19:36:05', '451205');
INSERT INTO `menu` VALUES (509, '咖喱牛肉饭', 83, '{\"1\": \"牛肉切块\", \"2\": \"准备好桂皮、香叶和八角\", \"3\": \"肉块凉水入锅，有浮沫飘起时撇去浮沫\", \"4\": \"放入煮肉料和一些香菜根\", \"5\": \"压力锅加压上汽后压13分钟。\", \"6\": \"土豆切丁，胡萝卜、洋葱切丁，蒜切末\", \"7\": \"土豆热油炸至表面金黄。\", \"8\": \"胡萝卜少量油炒变色，加入洋葱炒香\", \"9\": \"炖好的牛肉捞出倒入锅中\", \"10\": \"加入炸好的 土豆，放入咖喱块，这是一小盒全用了\", \"11\": \"倒入炖牛肉的汤，\", \"12\": \"大火煮开后小火炖10分钟。\", \"13\": \"蒸好的米饭装盘，浇上咖喱牛肉菜即可。\"}', '{\"牛肉\": \"600g\", \"土豆\": \"400g\", \"咖喱\": \"1盒\", \"米饭\": \"适量\", \"胡萝卜\": \"适量\", \"洋葱\": \"适量\", \"煮肉料\": \"适量\"}', 14, '2020-02-27 22:30:02', 0, '2020-03-12 19:36:05', '163864');
INSERT INTO `menu` VALUES (510, '韩式牛肉卷炒豆芽', 10, '{\"1\": \"所需食材。\", \"2\": \"牛肉卷内放入清酒、酱油、蒜末腌制一会。\", \"3\": \"大蒜切片。\", \"4\": \"大葱切末。\", \"5\": \"洋葱切丝。\", \"6\": \"胡萝卜切圆片制成花瓣状如对菜品有任何疑问，可关注公众号：朝族媳妇辣白菜 交流经验。\", \"7\": \"锅内多放一些油，下蒜瓣炸一会。\", \"8\": \"加入洋葱翻炒。\", \"9\": \"再加胡萝卜翻炒10秒。\", \"10\": \"放入腌制好的牛肉卷翻炒至稍微变色。\", \"11\": \"加入糖、耗油继续翻炒。\", \"12\": \"肉炒至基本全部变色后加入绿豆芽翻炒30秒。\", \"13\": \"盖盖闷1分钟，最后加入葱花即可。\", \"14\": \"盛出装盘。\"}', '{\"牛肉卷\": \"300g\", \"绿豆芽\": \"150g\", \"蒜\": \"5瓣\", \"洋葱\": \"半个\", \"胡萝卜\": \"1/4个\", \"大葱\": \"1根\", \"白砂糖\": \"1勺\", \"蚝油\": \"1勺\", \"清酒\": \"1勺\", \"酱油\": \"1.5勺\", \"蒜末\": \"1/2勺\"}', 12, '2020-02-27 22:30:03', 0, '2020-03-12 19:36:05', '450976');
INSERT INTO `menu` VALUES (511, '冬荫功汤', 13, '{\"1\": \"口蘑切块，香茅斜切段，虾洗净去头和皮，去虾线，留一只完整的虾做装饰。鱿鱼洗净切适当大小的块，小米辣斜切段，冬荫功酱和柠檬叶备用\", \"2\": \"锅做水下入柠檬叶和香茅烧开煮制2-3分钟\", \"3\": \"下入2勺冬荫功酱搅匀烧开\", \"4\": \"下入口蘑，加入鸡汤块和椰汁烧开煮制一分钟\", \"5\": \"加入盐调味，加入海鲜稍煮关火，加入小米辣，挤入柠檬汁拌匀即成\", \"6\": \"成品图，香菜最后装饰\"}', '{\"鱿鱼\": \"1只\", \"虾\": \"10只\", \"口蘑\": \"2个\", \"冬荫功酱\": \"20g\", \"柠檬汁\": \"适量\", \"鸡汤块\": \"半个\", \"盐\": \"适量\", \"小米辣\": \"一个\", \"椰奶\": \"适量（或牛奶）\", \"香茅\": \"一个\", \"柠檬叶\": \"3-4片\"}', 13, '2020-02-27 22:30:04', 0, '2020-03-12 19:36:05', '355383');
INSERT INTO `menu` VALUES (512, '清香苹果咖喱', 36, '{\"1\": \"瘦肉洗净血水后切成小片。\", \"2\": \"切好的肉片放入碗里，加入少量盐和少许胡椒粉搅匀后腌制一会。\", \"3\": \"炒锅倒油烧热，放入肉片翻炒熟。\", \"4\": \"放入一汤匙的咖喱粉翻炒翻炒。\", \"5\": \"倒入半碗水煮沸改小火焖一会。\", \"6\": \"苹果去皮切成小片，葡萄干洗净备用。\", \"7\": \"烧至略收汁时，尝尝味放入少许的盐和鸡精。\", \"8\": \"放入苹果片和葡萄干翻炒均匀即可关火食用。\"}', '{\"瘦肉\": \"100克\", \"苹果\": \"100克\", \"葡萄干\": \"20克\", \"咖喱粉\": \"一匙\", \"油\": \"适量\", \"盐\": \"适量\", \"胡椒粉\": \"适量\", \"鸡精\": \"适量\"}', 14, '2020-02-27 22:30:04', 0, '2020-03-12 19:36:05', '148948');
INSERT INTO `menu` VALUES (513, '印度酱香鸡（Butter Chicken）', 95, '{\"1\": \"500毫升可打稠制作掼奶油的奶油（whipping cream）、1/2杯牛奶、凝乳干酪（paneer）\", \"2\": \"1大勺胡芦巴籽（fenugreek seed）\", \"3\": \"印度的咖喱粉香料（garam masala或G-masala）少许\", \"4\": \"一只鸡或鸡胸肉切小块，用盐和酒先腌。\", \"5\": \"炒锅中火炒香姜蓉，放入腌过的鸡，炒至七八分熟。\", \"6\": \"砂锅中火炒香蒜蓉和姜蓉\", \"7\": \"放入3个小的罐头番茄酱（tomato paste），时不时搅拌。\", \"8\": \"放入盐、胡椒粉、胡芦巴籽、1小勺孜然籽、干辣椒末、可打稠制作掼奶油的奶油、牛奶，烧开，时不时搅拌。\", \"9\": \"放入炒过的鸡、凝乳干酪块和印度的咖喱粉香料，时不时搅拌。\", \"10\": \"烧20分钟左右，直至想要的浓稠度即可。\"}', '{\"鸡\": \"一只\", \"胡芦巴籽\": \"1大勺\", \"凝乳干酪\": \"适量\", \"牛奶\": \"1/2杯\", \"奶油\": \"500毫升\", \"咖喱粉\": \"少许\", \"盐\": \"适量\", \"酒\": \"适量\", \"姜\": \"适量\", \"蒜\": \"适量\", \"番茄酱\": \"适量\", \"胡椒粉\": \"适量\", \"干辣椒末\": \"适量\"}', 14, '2020-02-27 22:30:05', 0, '2020-03-12 19:36:05', '143669');
INSERT INTO `menu` VALUES (514, '韩式酱山药丝', 1, '{\"1\": \"所需食材。\", \"2\": \"山药切丝（小心时间，尽量不要让山药变色）。\", \"3\": \"煮开水以后放入盐和醋，放入山药丝，煮2分钟左右捞出备用。\", \"4\": \"斜切辣椒圈。\", \"5\": \"姜切片。\", \"6\": \"锅内倒入，水、酱油、糖煮开。\", \"7\": \"加入清酒、熟油拌均匀。\", \"8\": \"煮开后，放入山药丝、辣椒和姜片。\", \"9\": \"放入糖浆后再煮5分钟左右，倒入适量香油和芝麻就可以出锅了。\", \"10\": \"摆盘撒芝麻。\"}', '{\"山药\": \"1根\", \"绿辣椒\": \"1个\", \"姜\": \"少许\", \"盐\": \"2勺\", \"醋\": \"1勺\", \"水\": \"1.5杯\", \"酱油\": \"4勺\", \"清酒\": \"3勺\", \"糖\": \"2勺\", \"熟油\": \"1勺\", \"糖浆\": \"1勺\", \"香油\": \"适量\", \"芝麻\": \"适量\"}', 12, '2020-02-27 22:30:06', 0, '2020-03-12 19:36:05', '442943');
INSERT INTO `menu` VALUES (515, '印度巧克力奶茶', 80, '{\"1\": \"把水加入奶锅里，加热沸腾后，加入掰碎的肉桂略煮后再加入碾碎的小豆蔻略煮。\", \"2\": \"加入红茶，煮1分钟后再加入与水同量的牛奶，煮开，继续煮30秒-1分钟茶汤变浓。\", \"3\": \"把刚刚煮好的茶过滤后加入巧克力糖浆。\", \"4\": \"注意，如果没有巧克力糖浆的话可以用普通糖浆和融化的巧克力液替代，现在把奶茶拌匀就可以享用啦~~\"}', '{\"牛奶\": \"200ml\", \"巧克力糖浆\": \"适量\", \"红茶\": \"两小袋\", \"杏仁\": \"适量可选\", \"鲜奶油\": \"适量可选\", \"可可粉\": \"适量\", \"肉桂\": \"适量\", \"小豆蔻\": \"适量\", \"糖\": \"适量\"}', 14, '2020-02-27 22:30:06', 0, '2020-03-12 19:36:05', '137788');
INSERT INTO `menu` VALUES (516, '寿司卷김밥', 49, '{\"1\": \"大米加少量糯米闷饭后，加入少许香油拌匀\", \"2\": \"把紫菜铺在帘上，饭平铺在紫菜上，厚度适中。把蔬菜摆在一端，如图。然后卷起来即可。\", \"3\": \"卷好的寿司放凉后再切成小块。\"}', '{\"紫菜\": \"（专用）\", \"黄萝卜\": \"适量\", \"黄瓜\": \"适量\", \"胡萝卜\": \"适量\", \"火腿肠\": \"适量\", \"鸡蛋\": \"适量\", \"大米\": \"适量\", \"糯米\": \"适量\", \"油\": \"适量\", \"盐\": \"适量\", \"香油\": \"适量\", \"芝麻\": \"适量\"}', 12, '2020-02-27 22:30:08', 0, '2020-03-12 19:36:05', '442319');
INSERT INTO `menu` VALUES (517, '印度风味虾仁', 61, '{\"1\": \"在3/4茶匙绿辣椒和生姜的酱中加入辣椒粉，姜黄粉，柠檬汁和盐腌虾，约10分钟。【如果喜欢酥脆口感的食物的话，可以先将虾仁沸水煮后沥干。】\", \"2\": \"把油烧热，加入大蒜，葱或洋葱，葫芦巴和咖喱叶炒香，约7-8分钟。\", \"3\": \"加入煮熟的虾，香菜和1/4茶匙辣椒粉，小茴香粉，玛萨拉粉，翻炒3分钟。加入椰肉丝炒香3分钟，最后加入加入适量盐，翻炒几下就好。\"}', '{\"虾仁\": \"适量\", \"洋葱\": \"1-2个\", \"绿辣椒酱\": \"适量\", \"生姜\": \"1块\", \"大蒜\": \"3个\", \"红辣椒粉\": \"1 .5茶匙\", \"姜黄粉\": \"1/4茶匙\", \"香菜粉\": \"1茶匙\", \"小茴香粉\": \"1/2茶匙\", \"葫芦巴粉\": \"1/2茶匙\", \"玛萨拉粉\": \"适量\", \"柠檬汁或酸奶\": \"1汤匙\", \"椰肉\": \"2汤匙（磨碎）\", \"咖喱叶\": \"15-20片\", \"盐\": \"适量\", \"油\": \"适量\"}', 14, '2020-02-27 22:30:08', 0, '2020-03-12 19:36:05', '137483');
INSERT INTO `menu` VALUES (518, '四季餐桌|泰式香芒甜辣虾', 81, '{\"1\": \"鲜虾洗净，减去虾头、虾尾、虾脚\", \"2\": \"芒果切开后用刀划十字，切下果肉待用\", \"3\": \"生姜、小葱、红椒切小段\", \"4\": \"锅中放入适量油，油热后依次加入生姜、小葱、红椒段翻炒\", \"5\": \"爆香后放入鲜虾，翻炒\", \"6\": \"依次加入甜辣酱、特级金标生抽，翻炒\", \"7\": \"放入芒果块，加入少量水，翻炒\", \"8\": \"出锅装盘即可食用\"}', '{\"鲜虾\": \"250g\", \"芒果\": \"2个\", \"生姜\": \"1块\", \"红椒\": \"1个\", \"小葱\": \"3根\", \"生抽\": \"适量(特级金标)\", \"古道料酒\": \"适量\", \"泰式甜辣酱\": \"适量\"}', 13, '2020-02-27 22:30:08', 0, '2020-03-12 19:36:05', '349454');
INSERT INTO `menu` VALUES (519, '韩式五花肉', 69, '{\"1\": \"准备一小块五花肉。\", \"2\": \"切成薄片。\", \"3\": \"葱姜切片。\", \"4\": \"葱姜切成碎末。\", \"5\": \"韩式辣椒酱和白胡椒粉。\", \"6\": \"将切成片的五花肉内加入蒜末、姜末，一勺韩式辣椒酱、盐、白胡椒粉、料酒，然后翻拌。\", \"7\": \"当每块肉片上都沾上佐料即翻拌好。。然后腌制20分钟。\", \"8\": \"取平底不粘锅，放油，然后一片片码上肉片，正反面煎至焦黄即可。。肥肉煎至透明。\", \"9\": \"成品图。\"}', '{\"五花肉\": \"适量\", \"蒜\": \"适量\", \"姜\": \"适量\", \"韩式辣椒酱\": \"适量\", \"白胡椒粉\": \"适量\", \"盐\": \"适量\", \"料酒\": \"适量\"}', 12, '2020-02-27 22:30:09', 0, '2020-03-12 19:36:05', '441641');
INSERT INTO `menu` VALUES (520, '印度甜薄饼', 69, '{\"1\": \"在一个盆里，放入面粉，盐和水一起搅拌，直至形成软面团。将面团倒在撒有面粉的板上，揉一会。然后将面团分成小团，约高尔夫球大小，用湿布或保鲜膜盖上。\", \"2\": \"取一小面团，用擀面棍擀得非常薄，但不要擀破。均匀撒上细糖粉，折叠成小方块后再用擀面棍擀，直到非常薄。\", \"3\": \"取一平底煎锅置于中火上，刷薄油，把擀好的薄饼放入，每面煎30秒到1分钟，直到呈金黄色。趁热马上吃。\", \"4\": \"重复上述步骤，直到所有的面团球已擀完和煎完。\"}', '{\"面粉\": \"250g\", \"细白糖粉\": \"50g\", \"盐\": \"2g\", \"菜籽油\": \"15ml\"}', 14, '2020-02-27 22:30:09', 0, '2020-03-12 19:36:05', '137216');
INSERT INTO `menu` VALUES (521, '咖喱鸡块', 20, '{\"1\": \"鸡剁成小块，洗净，用吸油纸擦干水分，撒上3克盐腌制十分钟\", \"2\": \"土豆去皮洗净切丁，胡萝卜、洋葱、青椒洗净切丁\", \"3\": \"锅中倒入油，能够差不多淹没鸡块即可，油加热到筷子插入冒小泡即可\", \"4\": \"放入鸡块炸至变色，捞出\", \"5\": \"放入土豆胡萝卜洋葱青椒炸熟\", \"6\": \"将锅中油倒出，只留少许油，放入蒜末，煸出香味\", \"7\": \"放入鸡块和其它的蔬菜丁，放入一大勺生抽、2克盐、2大勺咖喱粉翻炒，再加入适量的水炖一会，炖至汤汁粘稠即可\"}', '{\"三黄鸡\": \"1只\", \"土豆\": \"1个\", \"胡萝卜\": \"1段\", \"青椒\": \"一个\", \"洋葱\": \"半个\", \"生抽\": \"1大勺\", \"盐\": \"5g\", \"咖喱粉\": \"2大勺\"}', 13, '2020-02-27 22:30:10', 0, '2020-03-12 19:36:05', '345077');
INSERT INTO `menu` VALUES (522, '印度风味烤鱼', 12, '{\"1\": \"将姜蒜泥，柠檬汁，盐，红辣椒粉，姜黄粉，孜然粉和香菜粉混合制作腌料，用这个腌料腌制鱼片20-30分钟\", \"2\": \"将油烧热，加一些咖喱叶，将腌好的鱼放入锅中，调至中火14-15分钟。\", \"3\": \"待颜色变得金黄，轻轻翻转过来，等10-12分钟至烤鱼另一面颜色也变得金黄。\", \"4\": \"盛出，趁热用洋葱圈和柠檬片做装饰即可。\"}', '{\"鱼片\": \"500g\", \"洋葱\": \"1个\", \"柠檬\": \"1个\", \"姜蒜泥\": \"1.5汤匙\", \"柠檬汁\": \"1个柠檬的量\", \"孜然粉\": \"适量\", \"香菜粉\": \"适量\", \"姜黄粉\": \"适量\", \"红辣椒粉\": \"1.5茶匙\", \"咖喱叶\": \"适量\", \"油\": \"适量\", \"盐\": \"适量\"}', 14, '2020-02-27 22:30:10', 0, '2020-03-12 19:36:05', '136623');
INSERT INTO `menu` VALUES (523, '韩式香蕉年糕', 55, '{\"1\": \"所需食材。\", \"2\": \"香蕉一根切小块。\", \"3\": \"将香蕉压成泥。\", \"4\": \"糯米粉分三次放入（第一次少放一些），和成面团。\", \"5\": \"香蕉一根切成薯条大小的块。\", \"6\": \"面团手捏成面皮包二小根香蕉。\", \"7\": \"锅内煮水。\", \"8\": \"水开后把包好的香蕉糕下锅煮5-7分钟左右即可。\", \"9\": \"捞出凉凉 蘸上黄豆粉就可以了。\"}', '{\"香蕉\": \"2根\", \"糯米粉\": \"150g\", \"豆粉\": \"适量\"}', 12, '2020-02-27 22:30:10', 0, '2020-03-12 19:36:05', '438857');
INSERT INTO `menu` VALUES (524, '虾仁草菇煲仔饭', 87, '{}', '{\"大虾\": \"2只\", \"草菇\": \"100g\", \"荷兰豆\": \"少许\", \"大米\": \"70g\", \"冬阴功酱\": \"1勺\", \"鱼露\": \"少许\", \"椰子粉\": \"1勺\"}', 13, '2020-02-27 22:30:12', 0, '2020-03-12 19:36:05', '340607');
INSERT INTO `menu` VALUES (525, '印度奶酪帕可拉', 34, '{\"1\": \"将米粉、玉米粉、姜蒜酱、红辣椒粉,小苏打和盐加入适量水做成糊状。\", \"2\": \"印度白奶酪块在热水中煮5分钟再沥干。\", \"3\": \"将油烧热,每个印度奶酪块蘸上准备好的面糊后放入锅中炸。直到颜色变成金黄色后放到吸水纸上\", \"4\": \"把炸好的奶酪放在盘子上摆好，配上一杯番茄酱或绿酸辣酱和一杯玛萨拉就完成了。\"}', '{\"印度奶酪\": \"约0.25kg\", \"鹰嘴豆面粉\": \"1杯\", \"米粉\": \"2汤匙\", \"玉米面粉\": \"1汤匙\", \"小苏打\": \"适量\", \"姜蒜酱\": \"1茶匙\", \"盐\": \"适量\", \"红辣椒粉\": \"0.5茶匙\"}', 14, '2020-02-27 22:30:12', 0, '2020-03-12 19:36:05', '136417');
INSERT INTO `menu` VALUES (526, '印度传统土豆小食', 82, '{\"1\": \"把土豆洗净，去皮，切成小块\", \"2\": \"将油烧热，油热时减小火，加芥末籽，待发出噼啪的声音时立即加入孜然，炒几秒钟。\", \"3\": \"加入土豆块和绿辣椒（这时可以在锅中加入适量咖喱叶或者洋葱），中火煎4-5分钟，注意要经常的翻动土豆块。把火调小，加入姜黄粉，盖上锅盖小火10分钟。 10分钟后，取下锅盖，加盐调味，拌匀。\", \"4\": \"盖上锅盖继续用小火，直到土豆变软。快要完成的时候，拿掉锅盖，中火，不停翻动。【注意不要盖上锅盖】直到上面有好看的棕色阴影，注意不要烧焦，这个过程大概要7-10分钟。\"}', '{\"马铃薯\": \"2个去皮切丁\", \"自行搭配\": \"适量\", \"姜黄粉\": \"0.5茶匙\", \"绿辣椒\": \"2个\", \"芥菜籽\": \"0.5汤匙\", \"小茴香种子\": \"0.5茶匙\", \"盐\": \"适量\", \"油\": \"适量\", \"咖喱叶\": \"适量（可选）\"}', 14, '2020-02-27 22:30:14', 0, '2020-03-12 19:36:05', '136239');
INSERT INTO `menu` VALUES (527, '意式鲜虾香肠芝士焗饭（超详细步骤）', 100, '{\"1\": \"如图备料，我这次做的是两人份。（意式香肠在国外超市很常见，如果是国内，可以买类似口味的外国风味的香肠或者肉丸代替，或者其他自己喜欢的肉类代替。）\", \"2\": \"意式肉肠放烤箱200度烘烤约10分钟左右\", \"3\": \"拿出来翻面继续200度烤10分钟左右\", \"4\": \"锅里加油放入切好的蒜头翻炒几下\", \"5\": \"然后加入洋葱一起翻炒\", \"6\": \"翻炒出香味后加入切好的番茄丁，一起翻炒，放一茶匙盐和两茶匙糖，继续翻炒，适量加小半碗水，盖锅盖焖煮，大火煮开后小火熬煮。\", \"7\": \"几分钟后加入适量番茄沙司，搅拌均匀。继续炖煮。\", \"8\": \"炖煮到番茄洋葱还有大蒜基本酥烂后，开大火收汁，期间不停的搅拌，方法类似于做果酱，只是不需要熬到果酱那么干粘。差不多汁收浓就可以出锅了。\", \"9\": \"锅重新倒入适量油，把洋葱碎和胡萝卜碎导入翻炒。\", \"10\": \"然后把刚刚烤完只有半熟的香肠放入其中，淋如一勺水，迅速盖上锅盖，焖煮1-2分钟。\", \"11\": \"开锅盖后把香肠捞出，切厚片。然后锅中导入米饭一起翻炒。翻炒均匀后加入黑胡椒粉，适量的盐和糖，以及小半碗的牛奶和2汤勺左右的水，一起炖煮，并且加入切好的香肠。\", \"12\": \"炖煮到一半的时候加入之前熬好的番茄酱（一半），和适量的芝士粉还有虾仁，翻炒均匀后继续炖煮。\", \"13\": \"炖煮到汁水少了很多，但是米饭依旧湿乎乎的状态就可以关火了。米饭千万不能太硬，否则烤过之后会很难吃。\", \"14\": \"准备两个可以放入烤箱的容器（最好是玻璃或者陶瓷质地的，由于我家里陶瓷质地的太大，所以治好选用这样的蛋糕模具，由于是蛋糕/面包模具，所以降温速度比较快，所以饭也冷的快。所以为了可以慢慢享受美味，建议大家还是选用陶瓷或者玻璃质地的）\", \"15\": \"把饭分装好，在饭的表面涂抹上之前用剩余的番茄酱。\", \"16\": \"然后在表面放上马苏里拉芝士，点缀上黑橄榄。\", \"17\": \"烤箱事先200度预热5分钟，然后放入饭，继续200度烘烤10分钟左右，等芝士融化，边上微微起焦黄色就可以出炉了。\", \"18\": \"出炉后撒上干欧芹碎\", \"19\": \"依据个人口味还可以另外撒上芝士粉和黑胡椒粉，配上一杯白葡萄酒也是不错的选择哦。\"}', '{\"米饭\": \"适量\", \"意式香肠\": \"2根\", \"胡萝卜\": \"2根\", \"马苏里拉芝士\": \"适量\", \"黑橄榄\": \"3-4粒\", \"黑胡椒粉\": \"2-3勺\", \"牛奶\": \"2汤匙\", \"糖\": \"适量\", \"虾仁\": \"适量\", \"番茄\": \"2个\", \"洋葱\": \"适量\", \"大蒜\": \"适量\", \"芝士粉\": \"2-3勺\", \"番茄沙司\": \"2-3勺\", \"盐\": \"适量\", \"干欧芹碎\": \"适量\"}', 15, '2020-02-27 22:30:14', 0, '2020-03-12 19:36:05', '196910');
INSERT INTO `menu` VALUES (528, '赋予蛤蜊另一种吃法：泰式椰香蛤蜊', 87, '{\"1\": \"将葱姜丝、小洋葱等食材切碎。中火加热，将黄油加入锅内。\", \"2\": \"用黄油将香茅的香味炒出来。\", \"3\": \"将洋葱、姜、蒜等配料放入锅内大火煸炒。\", \"4\": \"加入椰奶和韩式辣酱。\", \"5\": \"调完酱汁之后，将蛤蜊倒入锅内炒至成熟。\", \"6\": \"盖上锅盖之后，用中火焖制3-5分钟。\", \"7\": \"出锅前加入少许柠檬汁，这道垂涎欲滴的泰式椰香蛤蜊就可以上桌啦！\"}', '{\"蛤蜊\": \"适量\", \"平菇\": \"适量\", \"香茅草\": \"适量\", \"柠檬\": \"适量\", \"香菜\": \"适量\", \"大蒜\": \"适量\", \"生姜\": \"适量\", \"小洋葱\": \"适量\", \"黄油\": \"适量\", \"椰浆\": \"适量\", \"辣酱\": \"适量\"}', 13, '2020-02-27 22:30:15', 0, '2020-03-12 19:36:05', '332916');
INSERT INTO `menu` VALUES (529, '黑椒咖喱鸡腿', 37, '{\"1\": \"鸡腿剁成块状，洗净。\", \"2\": \"土豆去皮，洗净。\", \"3\": \"土豆切成滚刀块，用冷水浸泡备用。\", \"4\": \"洋葱洗净，切丁。\", \"5\": \"鸡腿块上撒适量盐、黑胡椒粉，抓拌均匀。\", \"6\": \"放入白朗姆酒，抓匀，腌制半小时。\", \"7\": \"煎锅烧热，小火，将鸡腿块带皮的一面向下放入锅中。\", \"8\": \"煎至一面金黄后，翻面再煎另一面。\", \"9\": \"煎至两面金黄，放入洋葱，翻炒出香味后放入土豆块，翻炒均匀。\", \"10\": \"倒入适量开水。开后撇去浮沫，加盖，小火炖30分钟左右至鸡肉熟透。\", \"11\": \"关火，放入咖喱膏，静置至咖喱膏融化。\", \"12\": \"再度开火，搅匀，中火煮至汤汁收浓后盛出即可。\"}', '{\"鸡腿\": \"280克\", \"洋葱\": \"25克\", \"土豆\": \"160克\", \"开水\": \"适量\", \"盐\": \"适量\", \"黑椒粉\": \"适量\", \"白朗姆酒\": \"1大勺\", \"咖喱膏\": \"20克\"}', 14, '2020-02-27 22:30:15', 0, '2020-03-12 19:36:05', '129141');
INSERT INTO `menu` VALUES (530, '焗烤海鲜饭', 70, '{\"1\": \"新鲜的仙贝开水快速焯烫20秒捞出滤水，\", \"2\": \"处理过的鲜虾快速焯水20秒捞出滤水\", \"3\": \"茄子切1厘米厚的片，表面打花刀，容易入味，不要切断。\", \"4\": \"茄子放煎锅中，少许油煎至水分蒸发，茄汁变软。茄子煎好备用\", \"5\": \"锅中放少许黄油，把洋葱末炒出香味，\", \"6\": \"下入米饭炒散\", \"7\": \"下入熟的豌豆和玉米粒翻炒均匀\", \"8\": \"加盐和胡椒粉\", \"9\": \"加适量的特级酱油翻炒均匀。\", \"10\": \"煎好的茄子茄子在鲍鱼汁中沾一下，没有用鲍鱼，让茄子有鲍鱼的味道\", \"11\": \"炒好的米饭铺在烤盘里，铺上一层沾了鲍鱼汁的茄子\", \"12\": \"把虾摆上，仙贝撒上面，\", \"13\": \"撒上一点青椒粒，最后撒上马苏里拉碎。\", \"14\": \"用锡纸包裹，烤箱180度预热后烤15分钟\", \"15\": \"焗烤完成的海鲜饭，非常的好吃的！\"}', '{\"鲜虾\": \"300g\", \"仙贝\": \"200g\", \"茄子\": \"1个\", \"米饭\": \"1碗\", \"马苏里拉\": \"150g\", \"胡椒粉\": \"适量\", \"盐\": \"适量\", \"鲍鱼汁\": \"适量\", \"特级酱油\": \"适量\", \"熟豌豆玉米粒\": \"适量\"}', 15, '2020-02-27 22:30:16', 0, '2020-03-12 19:36:05', '190611');
INSERT INTO `menu` VALUES (531, '泰国牛肉色拉', 59, '{\"1\": \"用黑胡椒腌制牛肉。\", \"2\": \"将腌制好的牛肉放入锅中煎至三分熟。\", \"3\": \"将所有食材切成小块。\", \"4\": \"将薄荷叶碾碎，加入金桔汁和蜂蜜搅拌，挤出汁，加入少许橄榄油搅拌均匀。\", \"5\": \"将调好的料汁与切好的食材搅拌均匀。\", \"6\": \"最后重要的一步，美美地摆盘，浇上料汁，over！\"}', '{\"牛肉\": \"适量\", \"小番茄\": \"适量\", \"金桔\": \"适量\", \"柠檬\": \"适量\", \"芒果\": \"适量\", \"薄荷\": \"适量\", \"芹菜\": \"适量\", \"大蒜\": \"适量\", \"豆芽\": \"适量\", \"红辣椒\": \"适量\", \"香菜\": \"适量\"}', 13, '2020-02-27 22:30:16', 0, '2020-03-12 19:36:05', '333805');
INSERT INTO `menu` VALUES (532, '印度咖喱牛肉', 6, '{\"1\": \"牛肉切块焯水，待肉稍一变色就捞出\", \"2\": \"焯好水的牛肉换一锅清水开始炖煮，大概需要20分钟\", \"3\": \"土豆切滚刀块，放入清水中泡一下，这样泡出土豆的淀粉，烧的时候不容易碎，口感好\", \"4\": \"洋葱切滚刀块，和咖喱、黄油一并待用\", \"5\": \"锅中放入黄油，炒化\", \"6\": \"黄油彻底化开之后，倒入土豆和洋葱炒出香味\", \"7\": \"加入咖喱块、番茄酱、糖、和盐来调味\", \"8\": \"炖煮5分钟\", \"9\": \"辅料烧差不多5成熟时，加入牛肉继续炖煮10分钟\", \"10\": \"出锅之前加入两勺酸奶，酸奶可以中和咖喱的辣味，让味道更香醇\", \"11\": \"美味的咖喱牛肉出锅啦，喜欢的亲还可以直接盖在饭上面就是咖喱牛肉盖饭了哦\"}', '{\"牛肉\": \"400g\", \"土豆\": \"三个（小）\", \"洋葱\": \"半个\", \"咖喱\": \"两块\", \"黄油\": \"一小块\", \"白糖\": \"两勺\", \"番茄酱\": \"两勺\", \"酸奶\": \"一盒\", \"盐\": \"适量\"}', 14, '2020-02-27 22:30:17', 0, '2020-03-12 19:36:05', '124381');
INSERT INTO `menu` VALUES (533, '虾仁培根焗饭', 20, '{\"1\": \"准备食材\", \"2\": \"虾仁用料酒、盐、生抽腌一下，其余都切丁\", \"3\": \"起油锅煸炒虾仁\", \"4\": \"等虾仁变色时放入其余食材翻炒\", \"5\": \"再放入米饭翻炒\", \"6\": \"放盐\", \"7\": \"放胡椒粉炒匀，使米饭全部散开\", \"8\": \"放入烘焙碗中，上面撒上马苏里拉芝士\", \"9\": \".烤箱预热，180度15分钟左右\"}', '{\"米饭\": \"一大碗\", \"虾仁\": \"100克\", \"培根\": \"2片\", \"玉米粒\": \"适量\", \"胡萝卜\": \"适量\", \"芦笋\": \"适量\", \"油\": \"适量\", \"盐\": \"适量\", \"胡椒粉\": \"适量\", \"料酒\": \"适量\", \"马苏里拉芝士\": \"适量\"}', 15, '2020-02-27 22:30:17', 0, '2020-03-12 19:36:05', '180448');
INSERT INTO `menu` VALUES (534, '咖喱面包蟹', 86, '{\"1\": \"冻熟的面包蟹自然解冻，用小刷子刷干净外壳。\", \"2\": \"椰浆\", \"3\": \"咖喱酱\", \"4\": \"土豆去皮，玉米切块，开水炒半熟备用。\", \"5\": \"将面包蟹去腮分解切块，保持蟹壳的完整（蟹壳不太好分离，很硬，第一次弄裂了，第二次完整）\", \"6\": \"蟹块裹面粉过油炸一下，加水加加咖喱煮开加椰浆和土豆玉米煮3、4分钟，装盘。（注意别粘锅，忘拍过油和煮制的照片了）\", \"7\": \"第二次做的蟹壳完整！\"}', '{\"面包蟹\": \"1只\", \"泰国咖喱酱\": \"10g\", \"椰浆\": \"200ml\", \"玉米\": \"1个\", \"土豆\": \"1个\"}', 13, '2020-02-27 22:30:17', 0, '2020-03-12 19:36:05', '335056');
INSERT INTO `menu` VALUES (535, '咖喱鸡肉饭', 61, '{\"1\": \"用盐和胡椒均匀涂抹鸡翅和鸡腿肉，放入冰箱至少2h或隔夜。\", \"2\": \"锅内中高火预热植物油，将鸡肉表面水分擦干后皮面向下入锅煎至略带焦糖色，盛出待用。此时预热烤箱150摄氏度。\", \"3\": \"用余油煎香洋葱丝至软嫩且半透明。\", \"4\": \"入咖喱粉（1cup rice should be seasoned with 1/4tsp curry powder），翻炒均匀至香气散出。\", \"5\": \"入米饭一同翻炒，直到油脂均匀裹覆米粒，大概3min。\", \"6\": \"按照米包装所示水量加入高汤，盐调味。\", \"7\": \"沸腾后上盖，转入烤箱中层，上下火，30-40min。\", \"8\": \"准备一锅沸水，入少许橄榄油和盐，焯水至翠绿色后立即用冰水冷却，随即放入冰箱待用。\", \"9\": \"这时候就可以开罐啤酒看看电视，呵呵，放松一下。\", \"10\": \"饭焖好出锅后拌入秋葵，以酸奶为sauce，配上柠檬片或一些你喜欢的香草，就一起开动吧！\"}', '{\"长梗米\": \"适量\", \"鸡翅中\": \"适量\", \"鸡腿肉\": \"适量\", \"洋葱\": \"适量\", \"高汤\": \"适量\", \"全脂酸奶\": \"适量\", \"咖喱粉\": \"（最好不要用咖喱块，会使饭粒粘\", \"盐\": \"适量\", \"现磨黑胡椒\": \"适量\", \"葵花籽油\": \"适量\"}', 14, '2020-02-27 22:30:18', 0, '2020-03-12 19:36:05', '107193');
INSERT INTO `menu` VALUES (536, '肉酱焗饭', 32, '{\"1\": \"准备所需材料。\", \"2\": \"猪肉剁成馅，胡萝卜、洋葱、芹菜切成细末、西红柿去皮切成小丁。\", \"3\": \"锅内倒入橄榄油烧热。\", \"4\": \"放入洋葱和芹菜炒出香味。\", \"5\": \"再加入番茄小丁、胡萝卜炒30秒。\", \"6\": \"倒入猪肉馅翻炒至熟。\", \"7\": \"加入高汤。\", \"8\": \"然后加入亨氏番茄酱。\", \"9\": \"最后加入迷迭香。\", \"10\": \"加入适量的盐。\", \"11\": \"再加入少许胡椒粉。\", \"12\": \"熬至变浓稠即可。\", \"13\": \"把肉酱铺在米饭上。\", \"14\": \"再铺上马苏里拉芝士。\", \"15\": \"烤箱预热180度，放中层烤至马苏里拉芝士溶化。\", \"16\": \"出炉后撒上芝士粉即可食用。\"}', '{\"大米饭\": \"1碗\", \"瘦猪肉\": \"150克\", \"洋葱\": \"1/4个\", \"胡萝卜\": \"1/4个\", \"芹菜\": \"2根\", \"番茄\": \"2个\", \"高汤\": \"1大碗\", \"迷迭香\": \"1/4小匙\", \"橄榄油\": \"4大匙\", \"亨氏番茄酱\": \"5大匙\", \"盐\": \"适量\", \"胡椒粉\": \"少许\"}', 15, '2020-02-27 22:30:19', 0, '2020-03-12 19:36:05', '179183');
INSERT INTO `menu` VALUES (537, '泰式咖喱虾', 98, '{\"1\": \"明虾拨壳后开边（不能断）挑背线，洋葱青椒切好备用\", \"2\": \"明虾用料酒，鸡粉，蛋清，少许糖腌制5分钟，然后加入生粉至有点粘稠即可\", \"3\": \"锅下油烧热改小火，将腌制好的虾下油炸1-2分钟左右\", \"4\": \"炸好的虾备用\", \"5\": \"另起锅下一匙油，然后下入洋葱，青椒，红椒翻炒几下，下入3分之二瓶的椰汁，一匙鱼露，3分之一瓶咖喱酱翻炒\", \"6\": \"最后下入炸好虾翻炒\", \"7\": \"翻炒2分钟出锅装盘\"}', '{\"明虾\": \"适量\", \"洋葱\": \"适量\", \"青椒\": \"适量\", \"红椒\": \"适量\", \"椰汁\": \"适量\", \"鱼露\": \"适量\", \"咖喱酱\": \"适量\", \"料酒\": \"适量\", \"鸡粉\": \"适量\", \"生粉\": \"适量\", \"蛋清\": \"适量\", \"糖\": \"适量\"}', 13, '2020-02-27 22:30:19', 0, '2020-03-12 19:36:05', '334899');
INSERT INTO `menu` VALUES (538, '西班牙风情炒饭', 36, '{\"1\": \"番茄去皮：现在番茄顶划十字刀\", \"2\": \"然后放入沸水中焯一会\", \"3\": \"捞出放凉一会就可以很轻松地把皮去掉了\", \"4\": \"去皮的番茄去蒂后切丁\", \"5\": \"青红尖椒洗净去籽切成丁，洋葱切丁，蒜切末\", \"6\": \"准备好米饭【现煮的或者隔夜的皆可，现煮的话米饭稍微煮干一点儿，不要煮太湿了哦~~~】\", \"7\": \"锅中热适量油，爆香蒜末\", \"8\": \"加入洋葱丁炒香\", \"9\": \"加入番茄炒软\", \"10\": \"然后依次加入糖、番茄酱、生抽、盐、黑胡椒粉炒匀\", \"11\": \"下入米饭和青红尖椒丁炒散炒匀即可出锅\"}', '{\"干饭\": \"250g\", \"番茄\": \"200g\", \"青尖椒\": \"1个\", \"红尖椒\": \"1个\", \"洋葱\": \"100g\", \"蒜仁\": \"2瓣\", \"盐\": \"1茶匙\", \"糖\": \"1茶匙\", \"黑胡椒粉\": \"1/4茶匙\", \"亨氏番茄酱\": \"2大匙\", \"生抽\": \"1茶匙\"}', 15, '2020-02-27 22:30:20', 0, '2020-03-12 19:36:05', '176416');
INSERT INTO `menu` VALUES (539, '印度蔬菜球vegetable madchurian', 51, '{\"1\": \"先把切碎的花椰菜，红萝卜和绿辣椒煮软，一般就是把菜放入开水里煮3分钟就好了，然后把菜放入面布或者纱布里把水过滤掉。\", \"2\": \"加盐，黑胡椒粉，酱油，葱花，面粉搅匀，这里面粉可以自己拿捏，只要菜可以抓成一个小球的粘度就可以了，不用过量。\", \"3\": \"这是捏好的菜球\", \"4\": \"把菜球在面粉上滚一圈，裹上面粉\", \"5\": \"把菜球放入热油里炸至外围有些焦黑\", \"6\": \"这是炸好的菜球，先搁置一边\", \"7\": \"热锅，加孜然籽，姜蒜蓉炒半分钟\", \"8\": \"加入洋葱炒至褐色\", \"9\": \"然后加入辣椒酱，黑胡椒粉，酱油，糖，葱花，少盐拌匀1分钟\", \"10\": \"这是葱花\", \"11\": \"再加入适量柠檬汁\", \"12\": \"加入菜球烧至汤汁变没\", \"13\": \"做好了，可以滴少量柠檬汁在上面。\"}', '{\"花椰菜\": \"半小个\", \"红萝卜\": \"半个\", \"绿辣椒\": \"1个\", \"葱花\": \"适量\", \"姜蒜蓉\": \"适量\", \"黑胡椒粉\": \"适量\", \"孜然籽\": \"1勺\", \"酱油\": \"适量\", \"辣椒酱\": \"适量\", \"糖\": \"1勺\", \"面粉\": \"适量\", \"盐\": \"适量\", \"柠檬汁\": \"适量\"}', 14, '2020-02-27 22:30:20', 0, '2020-03-12 19:36:05', '94412');
INSERT INTO `menu` VALUES (540, '盛夏菠萝炒饭', 65, '{\"1\": \"食材准备：米饭煮好盛出晾凉、备用；鸡胸脯肉切小粒后，放盐、油、料酒和淀粉适量腌制10分钟；胡萝卜、玉米和甜青椒切粒备用。\", \"2\": \"菠萝对半切，取出菠萝肉，用勺子挖出多余果肉，凹出造型。\", \"3\": \"起锅把水煮开，倒入蔬菜粒，进行焯水，2分钟后用漏勺沥干水盛出。\", \"4\": \"将锅烧热倒油，下鸡胸脯肉炒香，待肉色略金黄时倒入焯过水的蔬菜粒一起翻炒。\", \"5\": \"盛好的米饭这时候可以下锅一起拌匀和翻炒，点一些酱油加一小勺甜辣酱，放入菠萝果肉块，继续翻炒3分钟。\", \"6\": \"盛夏菠萝炒饭，可以盛出上桌享用啦！\"}', '{\"菠萝\": \"1个\", \"大米\": \"适量\", \"鸡胸肉\": \"适量\", \"玉米\": \"适量\", \"红萝卜\": \"适量\", \"青豆\": \"适量\", \"酱油\": \"适量\", \"料酒\": \"少许\", \"淀粉\": \"少许\", \"盐\": \"半茶匙\", \"甜辣酱\": \"1勺\", \"油\": \"少许\"}', 13, '2020-02-27 22:30:20', 0, '2020-03-12 19:36:05', '333455');
INSERT INTO `menu` VALUES (541, '印度黄油奶油鸡肉Butter chicken', 59, '{\"1\": \"先把切块的鸡肉加入少许淀粉，姜蒜蓉，盐，少辣椒粉，少芫荽粉，少油抓匀放20分钟\", \"2\": \"把鸡肉放入热油里炒2分钟至肉色变得有点小焦，因为是大火，其实鸡肉已经熟了但是不老。\", \"3\": \"这是炒好的鸡肉，放入盘中先放一边。\", \"4\": \"在锅里加入孜然籽，翻炒几秒\", \"5\": \"加洋葱炒至变成褐色\", \"6\": \"放姜蒜蓉翻炒几秒\", \"7\": \"倒1碗水入锅里，等水开后放入芫荽粉，辣椒粉，葫芦巴叶搅匀几秒\", \"8\": \"加入绿辣椒小火翻炒至水变少\", \"9\": \"把西红柿放入已经做好的香料锅里\", \"10\": \"用小火把西红柿煮软，只要变成糊糊就好了，一定要用小火，不然会变干变焦。\", \"11\": \"煮了大概10分钟后的西红柿糊糊，把糊糊放入搅拌机里\", \"12\": \"因为刚做好的西红柿糊很热，可以放几分钟后再把他们打成咖喱状的糊糊\", \"13\": \"在锅里加入黄油块，还有刚刚炒好的鸡肉里的少许鸡汤，如果没有鸡汤，可以加少许水\", \"14\": \"等黄油热化几秒\", \"15\": \"把咖喱糊加入黄油里\", \"16\": \"这里又出现印度红色色粉了，为了让菜色好看，印度人会放少许色粉\", \"17\": \"把糖和盐放入搅匀后的咖喱汁里，一定要用小火，因为咖喱汁很稠，小火才不会把它烧焦。家里没有番茄酱了，但是应该加入2勺番茄酱会更好吃。\", \"18\": \"这是奶油，等咖喱热几秒后可以加入半碗奶油\", \"19\": \"把奶油搅匀\", \"20\": \"加入鸡肉，轻轻的把鸡肉用咖喱裹住，小火不断的搅动，然后煮5分钟让鸡肉入味\", \"21\": \"最后放香菜叶和印度GRAM MASALA，拌匀，关火。\", \"22\": \"做好了\", \"23\": \"搭配印度饼\", \"24\": \"我家大爷示范印度手抓饼吃法\"}', '{\"鸡胸肉\": \"1块\", \"洋葱\": \"半个\", \"西红柿\": \"2个\", \"姜蒜蓉\": \"适量\", \"绿辣椒\": \"1个\", \"香菜叶\": \"少许\", \"红辣椒粉\": \"1勺\", \"芫荽粉\": \"2勺\", \"葫芦巴叶\": \"1勺\", \"孜然籽\": \"1勺\", \"印度红色色粉\": \"少许\", \"糖\": \"适量\", \"盐\": \"适量\", \"番茄酱\": \"2勺\", \"奶油\": \"半碗\", \"印度综合香料\": \"少许\", \"黄油\": \"2勺\"}', 14, '2020-02-27 22:30:21', 0, '2020-03-12 19:36:05', '94421');
INSERT INTO `menu` VALUES (542, '冬阴功汤', 92, '{\"1\": \"罗氏虾适量。黄金贝适量，将外壳刷洗干净后备用。西红柿2个，打上十字花刀，开水浸泡5分钟，5分钟后，撕去外皮，切去柿蒂，将西红柿切片、切丝后再切成小丁，入盘备用。\", \"2\": \"锅烧热，去适量的油，下入西红柿丁，大火煸炒，一边炒一边用勺背压碎西红柿粒，直到炒融，炒融后加入适量的水、南姜、柠檬叶、香茅草、干辣椒，拌均后盖上锅盖，大火烧开后转小火煮30分钟，30分钟后，捞出所有料渣。\", \"3\": \"锅中加入适量的冬阴功酱、虾酱、胡椒粉、白糖、盐，再下入罗氏虾、黄金贝和白玉菇，拌均后中火焖煮10分钟。10分钟后，打去浮沫，加入适量的椰子汁、鱼露和鸡精，推均后，挤入青柠檬汁，关火起锅。\", \"4\": \"成品图\"}', '{\"罗氏虾\": \"适量\", \"黄金贝\": \"适量\", \"西红柿\": \"适量\", \"南姜\": \"适量\", \"柠檬叶\": \"适量\", \"香茅草\": \"适量\", \"干辣椒\": \"适量\", \"冬阴功酱\": \"适量\", \"虾酱\": \"适量\", \"胡椒粉\": \"适量\", \"白糖\": \"适量\", \"盐\": \"适量\", \"椰子汁\": \"适量\", \"鱼露\": \"适量\", \"鸡精\": \"适量\", \"青柠檬汁\": \"适量\"}', 13, '2020-02-27 22:30:21', 0, '2020-03-12 19:36:05', '331760');
INSERT INTO `menu` VALUES (543, '【亨氏茄汁主义】心形土豆饼', 64, '{\"1\": \"土豆去皮切片，过清水洗去淀粉，沥干水分。\", \"2\": \"上蒸笼大火蒸约10分钟至熟透。\", \"3\": \"取出用勺背碾成土豆泥，加适量玉米淀粉，约为土豆泥的1/2，再加入少量盐和面粉，揉成面团。\", \"4\": \"面板上撒上面粉，把土豆泥团擀成约半厘米厚的片。\", \"5\": \"用模具切割出心形。给心形饼轻轻拍一层面粉，再把多余的面粉抖掉。\", \"6\": \"小锅里放油，油热至6、7成时放入土豆饼，炸至表面稍黄即可。\", \"7\": \"出锅沥油，趁热蘸食番茄酱食用。\"}', '{\"土豆\": \"一个\", \"玉米淀粉\": \"适量\", \"面粉\": \"适量\", \"番茄酱\": \"适量\", \"盐\": \"适量\", \"油\": \"适量\"}', 15, '2020-02-27 22:30:21', 0, '2020-03-12 19:36:05', '164715');
INSERT INTO `menu` VALUES (544, '印度椰茸咖喱鸡肉KONKANI CHILKEN', 100, '{\"1\": \"先把切好的鸡肉用少许淀粉，盐抓匀放30分钟\", \"2\": \"把少许椰茸用小火炒成褐色然后搁置一边待用\", \"3\": \"热油加少许肉桂\", \"4\": \"加入洋葱和姜蒜蓉中火炒\", \"5\": \"等洋葱变褐色加入姜黄粉，辣椒粉，绿辣椒和适量盐\", \"6\": \"1分钟后加入鸡肉，这个过程都不要加水，然后把鸡肉炒10分钟\", \"7\": \"10分钟后鸡肉上了色，入了味\", \"8\": \"等鸡肉入味后，加入1碗水把它煮开\", \"9\": \"待水开1分钟后加入西红柿汁，搅匀汤汁，用中小火煮\", \"10\": \"3分钟后加入印度MASALA\", \"11\": \"也加入椰茸\", \"12\": \"小火不断搅拌2分钟，汤汁就会收起，变成浓稠的咖喱,加适量盐\", \"13\": \"试吃味道OK后，加少许糖搅匀，关火，入盘，这里大家可以放少许香菜叶在上面\", \"14\": \"今天我用鸡肉搭配印度大米，和青瓜片\"}', '{\"鸡胸肉\": \"1块\", \"绿辣椒\": \"半个\", \"姜\": \"少许\", \"蒜蓉\": \"适量\", \"西红柿汁\": \"半碗\", \"洋葱\": \"半个\", \"香菜\": \"少许\", \"肉桂\": \"少许\", \"印度姜黄粉\": \"半匙羹\", \"印度辣椒粉\": \"1匙羹\", \"印度咖喱\": \"少许\", \"新鲜椰茸\": \"少许\"}', 14, '2020-02-27 22:30:22', 0, '2020-03-12 19:36:05', '92336');
INSERT INTO `menu` VALUES (545, '泰式柠檬鱼', 25, '{\"1\": \"准备好所有食材\", \"2\": \"自制泰式酸辣酱：先将芹菜、香菜和辣椒切成颗粒状，放进碗里之后挤入柠檬汁，和鱼露、糖、盐、香油、料酒一起搅拌均匀\", \"3\": \"鲈鱼用米酒腌制备用\", \"4\": \"生姜和葱白摆盘\", \"5\": \"鱼放入蒸盘里，浇上酸辣酱\", \"6\": \"高温蒸汽180摄氏度，15分钟\", \"7\": \"蒸完取出，将柠檬片和香菜撒在上面就可以了。\"}', '{\"鲈鱼\": \"1条\", \"柠檬\": \"1颗\", \"芹菜\": \"适量\", \"香菜\": \"适量\", \"辣椒\": \"适量\", \"鱼露\": \"2小匙\", \"盐\": \"1/4匙\", \"糖\": \"1/4匙\", \"香油\": \"1/4匙\", \"料酒\": \"适量\", \"姜片\": \"适量\", \"葱白\": \"适量\"}', 13, '2020-02-27 22:30:22', 0, '2020-03-12 19:36:05', '331630');
INSERT INTO `menu` VALUES (546, '鲜虾奶酪焗饭', 28, '{\"1\": \"新鲜的大虾剪掉须、脚，并挑去虾线。因为用的是整个的虾，剪掉虾枪后把头部里面的沙包挑出来，这样吃起来才放心\", \"2\": \"虾开水快速焯烫一下捞出\", \"3\": \"苏里拉切丝\", \"4\": \"胡萝卜和卤肉切丁备用\", \"5\": \"中放油，下入胡萝卜煸炒至变色\", \"6\": \"下入米饭炒散\", \"7\": \"入肉丁，加适量的盐和胡椒粉\", \"8\": \"翻炒均匀关火\", \"9\": \"撒上一层马苏里拉\", \"10\": \"放上虾，再撒上点马苏里拉\", \"11\": \"烤箱170度预热3分钟，烤10分钟即可。\", \"12\": \"烤好取出，再切点黄瓜片，即装饰又补充营养成分。享用吧！\"}', '{\"米饭\": \"1碗\", \"马苏里拉\": \"100g\", \"鲜虾\": \"适量\", \"卤肉\": \"适量\", \"胡萝卜\": \"适量\", \"盐\": \"适量\", \"胡椒粉\": \"适量\"}', 15, '2020-02-27 22:30:23', 0, '2020-03-12 19:36:05', '163717');
INSERT INTO `menu` VALUES (547, '印度黄豆拌土豆泥球moong dal vadas', 18, '{\"1\": \"先把黄豆泡一个晚上，第二天会变大一倍\", \"2\": \"把泡过水的黄豆（不加水）绞成泥\", \"3\": \"再准备土豆泥（可以把土豆煮软然后压成泥就是这样）\", \"4\": \"准备绿辣椒，香菜，姜蒜蓉\", \"5\": \"把土豆泥，黄豆泥，还有绿辣椒，香菜，姜蒜蓉全部放一起\", \"6\": \"再加入芫荽粉，辣椒粉，盐，糖，印度味精\", \"7\": \"把所有东西都搅匀，可以加少水，但是不多，泥泥有些些软就好了，然试尝味道碱度是否OK\", \"8\": \"捏成小球一个一个放入中火热油里面炸至两面金黄就好了\", \"9\": \"做好早餐了，因为我的土豆泥比黄豆泥多，所以炸后是黑色的。正确应该是黄豆泥要比土豆泥多，炸后就是金黄色的。\", \"10\": \"这里是黄豆泥比土豆泥多做出来的漂亮样子。大家可以试试哦。\"}', '{\"印度黄豆moong dal\": \"半碗\", \"土豆\": \"一小个\", \"绿辣椒\": \"1个\", \"香菜叶\": \"适量\", \"姜蒜蓉\": \"适量\", \"孜然籽\": \"半勺\", \"印度味精HING\": \"少许\", \"红辣椒粉\": \"半勺\", \"芫荽粉\": \"一勺\", \"盐\": \"适量\", \"糖\": \"少许\"}', 14, '2020-02-27 22:30:24', 0, '2020-03-12 19:36:05', '92103');
INSERT INTO `menu` VALUES (548, '西班牙浓情热巧克力', 91, '{\"1\": \"准备好原材料\", \"2\": \"将香草棒与八角与牛奶一起倒入奶锅中，用最小火慢慢加热至沸腾\", \"3\": \"在杯子中放入黑巧克力，将煮沸的牛奶倒入杯中搅拌至巧克力化开。之后加入可可粉搅拌均匀，再加入少许黑胡椒、白胡椒、肉桂粉、糖粉搅拌均匀即可饮用。\"}', '{\"全脂牛奶\": \"200ml\", \"黑巧克力\": \"20克\", \"可可粉\": \"30克\", \"黑胡椒\": \"0.5克\", \"白胡椒\": \"0.5克\", \"香草\": \"5厘米\", \"肉桂粉\": \"1克\", \"糖\": \"3克\", \"八角\": \"1颗\"}', 15, '2020-02-27 22:30:24', 0, '2020-03-12 19:36:05', '107906');
INSERT INTO `menu` VALUES (549, '西班牙海鲜饭-电饭煲版', 41, '{\"1\": \"用电饭煲煮意大利米，像我们平时煮饭一样下水就行，加点油\", \"2\": \"加适量盐\", \"3\": \"洗好的鸡翅吸干净水份用盐提前腌制半小时，在电饭锅按下煮饭键之后用平底锅把鸡翅煎到7分熟\", \"4\": \"饭煮到7成熟的时候，下藏红花粉，用勺子搅匀\", \"5\": \"搅均匀后铺上煎过的鸡翅。继续煮饭。\", \"6\": \"这时把洋葱炒到半透明后加上提前烫熟的青豆，炒好放一边备用\", \"7\": \"海鲜冲洗干净，备用\", \"8\": \"饭煮好后闷10分钟，然后连同鸡翅一起装在蛋糕模具上，铺上海鲜和洋葱，青豆，淋一点白葡萄酒，撒盐。大锅烧开水，驾着蛋糕模具大火蒸5-6分钟，直到海鲜熟透\", \"9\": \"蒸好的海鲜饭可以再适当撒一点黑椒粉，海虾本来带咸味，主要是给青口上点味道。\"}', '{\"意大利米\": \"适量\", \"大虾\": \"适量\", \"青口\": \"适量\", \"鸡肉\": \"适量\", \"洋葱\": \"适量\", \"青豆\": \"适量\", \"藏红花\": \"适量\", \"白酒\": \"适量\", \"盐\": \"适量\", \"柠檬汁\": \"适量\"}', 15, '2020-02-27 22:30:26', 0, '2020-03-12 19:36:05', '155006');
INSERT INTO `menu` VALUES (550, '【冬日暖呼呼】猪腰焗饭', 6, '{\"1\": \"猪腰一只，准备好。\", \"2\": \"2.用刀沿边切开，用剪刀辅助去除猪腰内部的筋膜(腰臊)。\", \"3\": \"3.除干净内部筋膜后，将猪腰切片。\", \"4\": \"4.将切好的猪腰稍微用水冲洗后，用姜，生抽，盐，植物油腌制。\", \"5\": \"5.香菜，香葱切细粒。\", \"6\": \"6.等饭快熟的时候，倒入腌制好的猪腰。\", \"7\": \"7.开吃之前倒入香菜，葱花焖一会。\", \"8\": \"8.拌均匀即可开吃。\", \"9\": \"成品图【猪腰焗饭】。\"}', '{\"猪腰\": \"一只\", \"姜\": \"适量\", \"香菜\": \"适量\", \"香葱\": \"适量\", \"生抽\": \"适量\", \"盐\": \"适量\", \"植物油\": \"适量\"}', 15, '2020-02-27 22:30:27', 0, '2020-03-12 19:36:05', '153238');
INSERT INTO `menu` VALUES (551, '西式培根蔬菜焗饭', 43, '{\"1\": \"奶油白酱的做法：原料：无盐黄油30克，面粉30克，牛奶350ml，淡奶油60ml，糖，盐，黑胡椒粉少许。 没有淡奶油的可以直接加500ml牛奶\", \"2\": \"黄油在不粘锅中融化，调小火。\", \"3\": \"加入面粉拌成面糊，\", \"4\": \"分2次加入牛奶，快速搅拌至融合。\", \"5\": \"加入淡奶油，调好浓稠度，\", \"6\": \"调入少许盐，白糖，黑胡椒粉，尝好味道就OK啦。超市有一种芝麻奶油白酱味道也特别好，就是做的过程中，加入炒熟打碎的白芝麻和少许柠檬汁，有喜欢的可以试试。\", \"7\": \"焗饭用的米饭，隔夜饭最好，米饭要蒸的硬一些，颗粒分明口感才好。\", \"8\": \"奶酪解冻备用。\", \"9\": \"培根解冻切碎丁。\", \"10\": \"胡萝卜擦成丝后切碎。\", \"11\": \"洋葱切碎。\", \"12\": \"香菇洗净切碎。泡发的干香菇口感更好。\", \"13\": \"炒锅入少许油，将培根，洋葱，香菇，胡萝卜依次放入锅中炒熟，\", \"14\": \"加入鸡蛋液划散，撒入适量盐炒匀。\", \"15\": \"倒入米饭翻炒均匀后关火，加入适量奶油白酱拌匀。\", \"16\": \"盛入饭盘中，表面撒满奶酪碎（奶酪量的多少根据自己的喜好）\", \"17\": \"烤箱预热后将饭放入烤箱中层180度5分钟左右，奶酪上色即可。\"}', '{\"米饭\": \"适量\", \"马苏里拉奶酪\": \"适量\", \"培根\": \"适量\", \"胡萝卜\": \"适量\", \"香菇\": \"适量\", \"洋葱\": \"适量\", \"鸡蛋\": \"适量\", \"盐\": \"适量\", \"奶油白酱\": \"适量\"}', 15, '2020-02-27 22:30:29', 0, '2020-03-12 19:36:05', '134100');
INSERT INTO `menu` VALUES (552, '香煎土豆饼配凤梨酱', 88, '{\"1\": \"土豆去皮，切块\", \"2\": \"放入保鲜袋，放入少量水，微波炉中高火两次6分钟\", \"3\": \"用工具压成土豆泥\", \"4\": \"加入西芹丁、鸡蛋、盐和黑胡椒调味\", \"5\": \"拌入洋葱丁\", \"6\": \"平底锅加热，放入油，将土豆丸子放入锅内，用铲子稍稍压扁\", \"7\": \"小火，每面煎至金黄色，翻面\", \"8\": \"酸奶酱原料放入料理机打成泥，食用时，用土豆饼蘸酸奶酱即可\"}', '{\"土豆\": \"300克\", \"洋葱丁\": \"50克\", \"面粉\": \"40克\", \"西芹杆\": \"100克\", \"鸡蛋\": \"1个\", \"马苏里拉芝士\": \"50克\", \"盐\": \"适量\", \"黑胡椒\": \"适量\", \"希腊酸奶\": \"100克\", \"凤梨\": \"150克\", \"迷你小洋葱\": \"1个\", \"薄荷\": \"7-8片\"}', 15, '2020-02-27 22:30:30', 0, '2020-03-12 19:36:05', '131823');
INSERT INTO `menu` VALUES (553, '土豆的极致吃法——葱香土豆饼干', 91, '{\"1\": \"备好材料。\", \"2\": \"土豆切成大片泡在水里去下淀粉，最后沥干水分。\", \"3\": \"土豆蒸熟或微波炉高温叮4分钟。\", \"4\": \"土豆片和牛奶放入搅拌机里搅拌成泥或用勺子捣成泥。\", \"5\": \"土豆泥加入葱花，蛋黄，葱花适量，盐半茶匙，黑胡椒1茶匙，红油半茶匙调味。\", \"6\": \"搅拌均匀。\", \"7\": \"装入裱花袋挤出心形。\", \"8\": \"烤箱190度先烤5分钟刷层蛋液再烤5分钟。或微波炉中火叮5分钟。\", \"9\": \"成品。\"}', '{\"土豆\": \"1个\", \"牛奶\": \"30g\", \"蛋黄\": \"1个\", \"葱花\": \"适量\", \"盐\": \"半茶匙\", \"黑胡椒\": \"1茶匙\", \"红油\": \"半茶匙\"}', 15, '2020-02-27 22:30:31', 0, '2020-03-12 19:36:05', '131508');
INSERT INTO `menu` VALUES (554, '浓郁奶香【蘑菇鸡肉焗饭】', 42, '{\"1\": \"准备材料。\", \"2\": \"番茄切小块，蘑菇切片，葱切段。\", \"3\": \"芝士切碎。\", \"4\": \"热锅下油，下葱段煸香。\", \"5\": \"下番茄翻炒，加少许水炒出汁，加一勺糖。\", \"6\": \"加入蘑菇一起炒至变软。\", \"7\": \"加入剩米饭。\", \"8\": \"撒适量黑胡椒。\", \"9\": \"加适量盐。\", \"10\": \"倒入鸡丁一起炒匀。\", \"11\": \"加入玉米粒。我的玉米粒是半熟的，如果是生的，则和蘑菇同时下。\", \"12\": \"炒好的饭盛一半出来，撒一部分芝士。\", \"13\": \"盛好剩下的饭，撒剩下的芝士。\", \"14\": \"入烤箱200度烤到芝士融化，变金黄色即可。\"}', '{\"米饭\": \"一碗\", \"鸡丁\": \"一碗\", \"番茄\": \"1个\", \"蘑菇\": \"2朵\", \"玉米粒\": \"适量\", \"切打芝士\": \"适量\", \"葱\": \"适量\", \"橄榄油\": \"适量\", \"盐\": \"适量\", \"黑胡椒粉\": \"适量\", \"白糖\": \"适量\"}', 15, '2020-02-27 22:30:33', 0, '2020-03-12 19:36:05', '129819');
INSERT INTO `menu` VALUES (555, '【土豆丝小饼】做一个有内涵的土豆饼&变身土豆饼小汉堡', 76, '{\"1\": \"土豆擦成丝备用。\", \"2\": \"火腿肠切丝，香葱切末。\", \"3\": \"盆里放入土豆丝，火腿丝，香葱末，鸡蛋。\", \"4\": \"加入面粉，淀粉搅拌均匀。\", \"5\": \"加入盐，五香粉，可以少加点白胡椒粉。\", \"6\": \"小锅里刷油，倒入土豆丝饼面糊。\", \"7\": \"小火慢煎，然后翻面继续煎至焦黄。\", \"8\": \"牛肉撒山美国现磨黑胡椒，一点点生抽，然后倒点油抓匀，腌制10分钟。\", \"9\": \"锅里倒入一点底油，放入开始煎牛肉。\", \"10\": \"然后翻面继续煎另一面，撒上现磨黑胡椒。\"}', '{\"土豆\": \"150\", \"火腿肠\": \"2根\", \"牛肉\": \"2小块\", \"鸡蛋\": \"2个\", \"面粉\": \"30克\", \"淀粉\": \"15克\", \"香葱\": \"20克\", \"黑胡椒\": \"适量\", \"盐\": \"适量\", \"五香粉\": \"适量\"}', 15, '2020-02-27 22:30:34', 0, '2020-03-12 19:36:05', '128416');
INSERT INTO `menu` VALUES (556, '香菇笋干包', 60, '{\"1\": \"备好材料(如上面所说的所有材料备好)。\", \"2\": \"粉丝用水清洗后备用。\", \"3\": \"面粉水和酵母还有白糖按上面的料调好。\", \"4\": \"然后搅拌(搅拌到成团状不黏为止，放到有温度的地方发酵)。\", \"5\": \"发酵好后，用擀面杖擀，就可以包了。\", \"6\": \"蒸15分钟之后，再等几分钟就可以吃了。\", \"7\": \"OK啦！\"}', '{\"香菇\": \"适量\", \"粉丝\": \"适量\", \"面粉\": \"500g\", \"酵母\": \"5g\", \"白糖\": \"10g\", \"水\": \"300g\", \"葱末\": \"适量\", \"盐\": \"适量\", \"油\": \"适量\"}', 16, '2020-02-27 22:30:35', 0, '2020-03-12 19:36:05', '515656');
INSERT INTO `menu` VALUES (557, '西班牙海鲜烩饭', 21, '{\"1\": \"鸡腿肉去骨后切成块，番茄、青椒、彩椒切大丁，黑橄榄切圈，虾去壳开背，鱿鱼切圈，3/4洋葱、蒜切末备用\", \"2\": \"炼虾汤：将虾壳与1/4个洋葱丝中火炒香后加入300ml水煨煮10分钟\", \"3\": \"以橄榄油炒香鸡腿肉后放入洋葱、蒜末拌炒\", \"4\": \"放入生米用中小火拌炒，在倒入60ml白葡萄酒\", \"5\": \"待酒精挥发完毕后撒入1/2g藏红花、1.5汤匙盐、1.5茶匙白胡椒粉调味\", \"6\": \"加入番茄、青椒、彩椒、黑橄榄翻炒均匀后倒入虾汤搅匀\", \"7\": \"将饭和汤盛入烤碗中后均匀的铺上海鲜（汤汁的量要漫过米饭2cm左右）\", \"8\": \"盖好锡纸后烤箱以180度烤45分钟至汤汁彻底被米饭吸收烤熟即可\"}', '{\"鸡腿\": \"1个\", \"洋葱\": \"1个\", \"生米\": \"180g\", \"番茄\": \"1个\", \"青椒\": \"1/2个\", \"彩椒\": \"1/2个\", \"大虾\": \"3只\", \"蛤蜊\": \"5个\", \"鱿鱼\": \"适量\", \"橄榄\": \"6个\", \"蒜\": \"2瓣\", \"柠檬\": \"1/2个\", \"白葡萄酒\": \"适量\", \"藏红花\": \"适量\", \"盐\": \"适量\", \"白胡椒粉\": \"适量\", \"橄榄油\": \"适量\", \"水\": \"适量\"}', 15, '2020-02-27 22:30:35', 0, '2020-03-12 19:36:05', '126992');
INSERT INTO `menu` VALUES (558, '韭菜鸡蛋素馅煎包', 61, '{\"1\": \"准备好食材。\", \"2\": \"3克酵母，用温水化开。\", \"3\": \"酵母倒入面粉里面，和成面团醒发。\", \"4\": \"虾皮洗净，鸡蛋炒熟，木耳煮5分钟。\", \"5\": \"韭菜，木耳切好。\", \"6\": \"所有食材放在一起，调料都要放进去。\", \"7\": \"拌匀.备用。\", \"8\": \"醒好的面团。\", \"9\": \"面团揉好，分成均匀的剂子，擀成包子皮。\", \"10\": \"包子全部包好，醒发20分钟。\", \"11\": \"锅内放花生油烧热，放入包子。\", \"12\": \"中火煎至底部金黄。\", \"13\": \"倒入100克清水。\", \"14\": \"盖上锅盖，中火烧7分钟，打开锅盖，出锅。\", \"15\": \"好吃。\", \"16\": \"鲜美。\"}', '{\"韭菜\": \"适量\", \"鸡蛋\": \"两个\", \"虾皮\": \"适量\", \"木耳\": \"适量\", \"面粉\": \"400克\", \"花生油\": \"适量\", \"香油\": \"适量\", \"酵母\": \"适量\", \"蚝油\": \"适量\", \"胡椒粉\": \"适量\", \"盐\": \"适量\", \"姜末\": \"适量\"}', 16, '2020-02-27 22:30:36', 0, '2020-03-12 19:36:05', '515560');
INSERT INTO `menu` VALUES (559, '家庭改良版电饭锅——西班牙海鲜饭', 52, '{\"1\": \"鲜虾剪须去虾线洗净、白贝洗净、章鱼洗净切粒、番茄去皮切末、洋葱洗净切粒、蒜切末、米洗净待用。\", \"2\": \"起锅下洋葱粒及蒜末爆香。\", \"3\": \"再倒入番茄末翻炒。\", \"4\": \"待炒番茄炒至糊烂后加入适量白胡椒粉、盐均匀翻炒后停火。\", \"5\": \"将洗净的珍珠米倒入电饭锅内胆内。\", \"6\": \"再将煮好的番茄洋葱倒入。\", \"7\": \"加适量清水搅拌均匀。（跟平时煮饭的水量差不多）\", \"8\": \"把电饭锅内胆放回电饭锅内，加盖按煮饭键。\", \"9\": \"待饭煮至7成熟后，将海鲜全部倒在饭面上，再加盖煮几分钟。\", \"10\": \"煮至白贝打开后、章鱼粒、鲜虾熟即可。\", \"11\": \"再将电饭锅里的海鲜及饭搅拌均匀即可。\", \"12\": \"成品图。\"}', '{\"珍珠米\": \"适量\", \"鲜虾\": \"适量\", \"白贝\": \"适量\", \"章鱼\": \"适量\", \"番茄\": \"适量\", \"洋葱\": \"适量\", \"蒜\": \"适量\", \"香菜\": \"适量\", \"盐\": \"适量\", \"油\": \"适量\", \"白胡椒粉\": \"适量\"}', 15, '2020-02-27 22:30:37', 0, '2020-03-12 19:36:05', '121529');
INSERT INTO `menu` VALUES (560, '水煎速冻包', 95, '{\"1\": \"食材，速冻小肉包，无需解冻。\", \"2\": \"起锅，平底锅不粘锅放油。\", \"3\": \"把包子摆入。\", \"4\": \"倒入适量清水。\", \"5\": \"加盖靠蒸汽焖熟。\", \"6\": \"等水干，底部有点小焦黄，洒入香菜碎，芝麻。\", \"7\": \"成品！妥妥的美味生煎包子！\"}', '{\"速冻肉包\": \"5个\", \"玉米油\": \"适量\", \"芝麻\": \"适量\", \"香菜碎\": \"适量\"}', 16, '2020-02-27 22:30:38', 0, '2020-03-12 19:36:05', '514572');
INSERT INTO `menu` VALUES (561, '外焦里嫩火烧馍', 1, '{\"1\": \"酵母放在面粉中搅拌成絮状。\", \"2\": \"面发起来一个小时。\", \"3\": \"揉成光滑的面团，有筋道。\", \"4\": \"擀成适当厚度的剂子。\", \"5\": \"刷上有酥。\", \"6\": \"卷起来，擀成圆形。\", \"7\": \"烙成两面金黄。\", \"8\": \"预热200度的烤箱，放入火烧馍，继续烤10分钟左右。\", \"9\": \"出炉。\", \"10\": \"外焦里嫩，分层明显，风味别具一格。\", \"11\": \"卖相不错吧！\"}', '{\"面粉\": \"适量\", \"纯净水\": \"(30度左右)适量\", \"酵母\": \"适量\", \"花生油\": \"适量\", \"盐\": \"适量\", \"五香粉\": \"适量\"}', 17, '2020-02-27 22:30:38', 0, '2020-03-12 19:36:05', '518247');
INSERT INTO `menu` VALUES (562, '可爱草莓包', 22, '{\"1\": \"酵母放入牛奶中化开。\", \"2\": \"把面粉，奶粉，白糖混合均匀，倒入牛奶酵母水，用筷子搅拌成絮状，再把絮状面粉揉成光滑的面团。\", \"3\": \"取一小块面团，加入少量大麦苗粉，揉搓成绿色面团备用。\", \"4\": \"往剩余的面团中加入红曲粉，揉搓成粉色面团，并分切成大小相同的小剂子备用。\", \"5\": \"取其中一个小剂子，压扁擀薄，往中间放入草莓馅，包成圆球，再整理成桃心型。\", \"6\": \"取绿色面团擀成薄片，用模具印出叶片，粘在草莓顶部，用筷子在叶片中间戳个凹槽，安上叶柄；接着用牙签在草莓身上戳满小点，草莓生胚就做好了。\", \"7\": \"把所有草莓生胚放进蒸笼，发酵到两倍大，冷水上锅，开大火蒸15—20分钟。\", \"8\": \"熄火后焖5分钟，就可以装盘开吃啰！\", \"9\": \"表皮松软可口，草莓馅果味浓郁，好吃！\"}', '{\"面粉\": \"150克\", \"白糖\": \"15克\", \"奶粉\": \"15克\", \"牛奶\": \"85毫升\", \"酵母\": \"1.5克\", \"红曲粉\": \"1.5克\", \"大麦苗粉\": \"少量\", \"草莓馅\": \"适量\"}', 16, '2020-02-27 22:30:39', 0, '2020-03-12 19:36:05', '513632');
INSERT INTO `menu` VALUES (563, '南瓜芝麻酥饼', 76, '{\"1\": \"把南瓜泥，酵母，白糖，面粉放到盆子里用筷子搅拌成絮状和成光滑的面团，放在温暖处发酵两倍大。\", \"2\": \"发酵好的面团。\", \"3\": \"揉面团排气，在切成小剂子。\", \"4\": \"擀成薄片，抹上芝麻酱。\", \"5\": \"折起来，然后卷成一个面团。\", \"6\": \"擀成薄饼。\", \"7\": \"放到加热的电饼铛，烙3分钟左右就可以吃啦。\", \"8\": \"香酥可口，还有南瓜的甜味。\"}', '{\"南瓜泥\": \"少量\", \"面粉\": \"两碗\", \"酵母\": \"4g\", \"糖\": \"一勺\", \"芝麻酱\": \"少许\"}', 17, '2020-02-27 22:30:39', 0, '2020-03-12 19:36:05', '518204');
INSERT INTO `menu` VALUES (564, '猪肉粉丝包', 56, '{\"1\": \"猪肉500克剁碎成肉末。\", \"2\": \"提前泡好的粉丝切碎，白萝卜切成丁，大蒜切碎。\", \"3\": \"将泡好的木耳，香菇切碎。\", \"4\": \"包子馅需要的几种菜，依个人口味，加点生姜末。\", \"5\": \"炒馅锅烧热加入肥肉炒油再加肉沫下锅，依次加入萝卜丁，粉丝，香菇黑木耳。\", \"6\": \"炒熟加入耗油，生抽老抽调味料鸡精，捞起放凉。\", \"7\": \"将醒发好的面擀成面皮，中间厚边缘薄。\", \"8\": \"将包好的包子放入蒸格，注意格子上抹少许油防止粘锅。\", \"9\": \"上锅蒸20分钟左右。\", \"10\": \"快起锅的时候为了防止回缩记得起锅前五分钟开盖放下汽五秒钟，然后再盖上。香喷喷的包子就蒸好了。\", \"11\": \"皮薄馅多，非常好吃。\"}', '{\"面粉\": \"1000\", \"猪肉\": \"500\", \"粉丝\": \"300\", \"白萝卜\": \"500\", \"大蒜\": \"250\", \"香菇\": \"100\", \"黑木耳\": \"100\"}', 16, '2020-02-27 22:30:40', 0, '2020-03-12 19:36:05', '513892');
INSERT INTO `menu` VALUES (565, '香菇粉丝包', 36, '{\"1\": \"准备好面粉和白糖，酵母粉用温水化开。再把香菇和粉丝用开水泡发。\", \"2\": \"把面粉揉成光滑的面团，另外把香菇和粉丝切碎炒熟。\", \"3\": \"发酵成这样两倍大就可以了。\", \"4\": \"重新揉面把它排气，再切成一个一个的小剂子，把馅料包进去做成包子生胚，然后再二次发酵20分钟。\", \"5\": \"然后用冷水上锅蒸，水开后再蒸10分钟就关火，然后再虚焖5分钟就可以出锅了。\", \"6\": \"这就是蒸好的成品包子咯。\"}', '{\"面粉\": \"1000g\", \"酵母粉\": \"7g\", \"香菇\": \"适量\", \"粉丝\": \"适量\", \"温水\": \"适量\", \"白糖\": \"适量50g\"}', 17, '2020-02-27 22:30:40', 0, '2020-03-12 19:36:05', '517715');
INSERT INTO `menu` VALUES (566, '羊肉白菜包子', 27, '{\"1\": \"取适量面粉。\", \"2\": \"温水化开酵母。\", \"3\": \"面粉搅拌絮状。\", \"4\": \"揉成光滑面团发酵。\", \"5\": \"接下来准备馅，羊肉白菜洗净。\", \"6\": \"白菜切丁，香菇切丁，肉切末。\", \"7\": \"切好的白菜，加适量盐杀水。\", \"8\": \"白菜出水后，和香菇丁肉馅搅拌均匀，加入盐生抽老抽调味。\", \"9\": \"面醒发两倍大。\", \"10\": \"分成小剂子。\", \"11\": \"擀成厚点的面皮。\", \"12\": \"包成包子，再次醒发30分钟。\", \"13\": \"上锅蒸20分钟，闷5分钟出锅。\", \"14\": \"成品。\"}', '{\"白菜\": \"1颗\", \"羊肉\": \"适量\", \"香菇\": \"5个\", \"面粉\": \"适量\", \"盐\": \"适量\", \"生抽\": \"适量\", \"老抽\": \"适量\", \"安琪酵母\": \"适量\"}', 16, '2020-02-27 22:30:41', 0, '2020-03-12 19:36:05', '513661');
INSERT INTO `menu` VALUES (567, '蒸包子', 56, '{\"1\": \"发酵粉兑温水适量，和面，做到3光，即面光手光盆光，那你就是个和面小能手！盖上保鲜膜，放在温暖的地方，让它自我膨胀。可以用手指戳出来笑脸就说明发好了。\", \"2\": \"豆角切丁，加入盐，花椒粉，油，腌制一会入味。\", \"3\": \"茄子去皮切丁，辣椒切小圈，调入盐，花椒粉，油腌制一会。\", \"4\": \"忘记拍中间包包子的图片了，就是把发好的面，揪成小团，擀成片，然后包入馅儿。\", \"5\": \"水烧至60°以上，关火，放入包子，二次发酵，看到包子大了一圈，就可以大火开蒸了。\", \"6\": \"大火蒸至锅周围冒白气，转小火，计时20分钟后，关火，捂2分钟，就可以拿出来了！\", \"7\": \"开咥！\"}', '{\"面粉\": \"适量\", \"豆角\": \"适量\", \"茄子\": \"1个\", \"杭椒\": \"适量\", \"稻米油\": \"适量\", \"花椒粉\": \"适量\", \"盐\": \"适量\"}', 17, '2020-02-27 22:30:42', 0, '2020-03-12 19:36:05', '518192');
INSERT INTO `menu` VALUES (568, '电蒸锅蒸包子', 36, '{\"1\": \"面粉500克。\", \"2\": \"酵母温水泡开。\", \"3\": \"倒入酵母。\", \"4\": \"揉成面团。\", \"5\": \"面团。\", \"6\": \"醒发。\", \"7\": \"好了。\", \"8\": \"葱姜切到肉里。\", \"9\": \"切肉和葱姜。\", \"10\": \"好。\", \"11\": \"一个鸡蛋搅拌。\", \"12\": \"香油。\", \"13\": \"生抽。\", \"14\": \"蚝油。\", \"15\": \"花生油。\", \"16\": \"老抽。\", \"17\": \"糖。\", \"18\": \"盐。\", \"19\": \"顺时针搅拌，放置。\", \"20\": \"豆角过水。\", \"21\": \"捞出。\", \"22\": \"切丁备用。\", \"23\": \"粉丝泡软。\", \"24\": \"切碎。\", \"25\": \"搅拌一起。\", \"26\": \"木耳。\", \"27\": \"面皮。\", \"28\": \"皮。\", \"29\": \"包。\", \"30\": \"蒸20分钟。\", \"31\": \"慢等5分出锅，吃吧。\"}', '{\"前肘\": \"一份\", \"面粉\": \"一份\", \"盐\": \"1勺\", \"生抽\": \"1勺\", \"老抽\": \"1勺\", \"糖\": \"1勺\", \"香油\": \"2勺\", \"花生油\": \"3勺\", \"蚝油\": \"1勺\", \"鸡蛋\": \"1个\", \"木耳\": \"10个\", \"葱\": \"1颗\", \"姜\": \"1块\", \"龙口粉丝\": \"1块\"}', 16, '2020-02-27 22:30:43', 0, '2020-03-12 19:36:05', '513836');
INSERT INTO `menu` VALUES (569, '不发面豆腐渣馒头', 79, '{\"1\": \"豆腐渣。\", \"2\": \"纯牛奶。\", \"3\": \"拌匀揉成面团。\", \"4\": \"醒十分钟在揉，反复揉两遍。\", \"5\": \"分成8个剂子。\", \"6\": \"像揉馒头一样揉成馒头型。\", \"7\": \"直接上蒸锅醒1个小时。\", \"8\": \"开火蒸半小时，在焖5分钟左右。\", \"9\": \"看看挺好的。\", \"10\": \"清香可口。\", \"11\": \"成品这种放法适合新手。\"}', '{\"豆腐渣\": \"适量\", \"面粉\": \"适量\", \"牛奶\": \"200克\", \"酵母\": \"3克\", \"白糖\": \"10克\", \"小苏打\": \"1克\"}', 17, '2020-02-27 22:30:43', 0, '2020-03-12 19:36:05', '518176');
INSERT INTO `menu` VALUES (570, '生煎包', 51, '{\"1\": \"韭菜，胡萝卜，黑木耳切碎，鸡蛋炒散加入盐，鸡精，十三香，生抽，香油，搅拌均匀。\", \"2\": \"300克面粉加一点盐，柔成光团，醒20分钟。\", \"3\": \"柔成长条。\", \"4\": \"切成小块剂子。\", \"5\": \"赶成饺子皮饼。\", \"6\": \"放入馅料，包成小包子。\", \"7\": \"全部包好。\", \"8\": \"锅里倒入油，修口朝下，煎至金黄色，到入半碗水。\", \"9\": \"烧至水干，撒上䓤花，黑芝麻。\", \"10\": \"成品图。\"}', '{\"面粉\": \"300克\", \"黑木耳\": \"适量\", \"韭菜\": \"适量\", \"胡萝卜\": \"适量\", \"鸡蛋\": \"2\"}', 16, '2020-02-27 22:30:44', 0, '2020-03-12 19:36:05', '513787');
INSERT INTO `menu` VALUES (571, '菠菜锅盔', 68, '{\"1\": \"菠菜洗净备用。\", \"2\": \"入开水锅中焯水，加一点油，颜色非常好看。\", \"3\": \"加一点点碱粉和水打成糊状。\", \"4\": \"取适量面粉，加入发酵粉，倒入菠菜糊揉成软一点的面团，放温暖处发酵。\", \"5\": \"发酵两倍大小即可。\", \"6\": \"加入适量碱粉揉匀，二次醒发十分钟。\", \"7\": \"取出擀成圆饼状。\", \"8\": \"电饼铛预热抹油放入。\", \"9\": \"一面定型后反过来烙另一面。\", \"10\": \"饼上扎孔排气，有助于熟透，多翻几次哦。\", \"11\": \"美美的菠菜锅盔出锅啦。\", \"12\": \"晾凉后切开装盘。\", \"13\": \"家常面食，请您品鉴。\", \"14\": \"成品图。\"}', '{\"菠菜\": \"适量\", \"面粉\": \"适量\", \"发酵粉\": \"适量\", \"碱粉\": \"适量\", \"水\": \"适量\"}', 17, '2020-02-27 22:30:44', 0, '2020-03-12 19:36:05', '518043');
INSERT INTO `menu` VALUES (572, '三合面馒头', 99, '{\"1\": \"原料：面粉、玉米面、小米面、白糖和酵母粉。\", \"2\": \"将面粉、玉米面和小米面混合，再倒入白糖，混合均匀。\", \"3\": \"清水与酵母粉混合后，慢慢倒入面粉中，再和成面团。\", \"4\": \"面团放入盆中，盖上盖子，放温暖处发酵。\", \"5\": \"发酵好的面团变大，扒开底部有拉丝，将面团移到面板上，撒些面粉，慢慢地揉光滑，成长条状，分成10个小面剤。\", \"6\": \"将小面团揉圆，再次醒发。\", \"7\": \"醒好的面团放入开水锅中，蒸18分钟即可。\", \"8\": \"黄橙橙的杂粮馒头出锅啦。\"}', '{\"面粉\": \"300克\", \"玉米面\": \"120克\", \"小米面\": \"50克\", \"白糖\": \"3克\", \"清水\": \"230克\", \"酵母粉\": \"3克\"}', 17, '2020-02-27 22:30:45', 0, '2020-03-12 19:36:05', '514204');
INSERT INTO `menu` VALUES (573, '香菇油菜包', 92, '{\"1\": \"香菇油菜包是一款经典的素菜包，鲜香营养。主料面粉300，放2克发酵粉，一点点糖。将面活的软一点，我放到了暖气下面，半个多小时就发好了。准备油菜、香菇、鸡蛋、葱姜，料酒、生抽、盐，花生油、香油、五香粉！\", \"2\": \"将油菜洗净，用开水烫一下，30秒捞出，凉了开始切碎，挤干水分。\", \"3\": \"我用的是鲜香菇，切碎用油煸炒了一下，也挤干水分，鸡蛋也炒碎。\", \"4\": \"将鸡蛋、油菜、香菇、葱姜混合，加入生抽，一点点料酒，盐、香油，五香粉，搅拌均匀！\", \"5\": \"馅调好后，把发好的面揉一下分成小剂子，一个一个包好。\", \"6\": \"包好后放到锅里，二次醒发十分钟！\", \"7\": \"大火烧开，等锅上汽后蒸10分钟，关火，别打开盖子，闷10分钟，孩子着急吃我闷了5分钟就开盖了，感觉面有点缩回去了，闷的时间长一点蒸的会更胖一些！\", \"8\": \"包子鲜而香，绝不逊色用肉包子！你也快来试试吧！\"}', '{\"面粉\": \"300g\", \"油菜\": \"500g\", \"香菇\": \"数颗\", \"鸡蛋\": \"3颗\", \"大葱\": \"半颗\", \"姜\": \"少许\"}', 16, '2020-02-27 22:30:45', 0, '2020-03-12 19:36:05', '513630');
INSERT INTO `menu` VALUES (574, '大米松饼', 89, '{\"1\": \"准备原料：金龙鱼小鲜米、面粉、白糖和鸡蛋。\", \"2\": \"大米倒出来称量80克，80克的大米可以做10多个松饼，家里人口少的，可以减量。\", \"3\": \"加入清水浸泡1个小时。\", \"4\": \"将泡好的小鲜米倒入破壁机，我用的是果汁功能，打成米浆。\", \"5\": \"这是打好的米浆，我用破壁机打了两遍。\", \"6\": \"米浆中加入白糖搅拌。\", \"7\": \"加入鸡蛋搅拌均匀，也可以将鸡蛋先搅匀再倒入米浆中。\", \"8\": \"加入面粉，搅拌均匀，没有干粉就可以了。\", \"9\": \"这时的米浆应该是粘稠的。\", \"10\": \"锅烧热后，刷上一层油防粘，舀入一勺米浆小火烙，底部定型后，翻面烙另一面直到成熟。\", \"11\": \"香香、软软又Q弹的大米松饼做好啦。\", \"12\": \"做好大米松饼，趁热吃特别好吃。\"}', '{\"鸡蛋\": \"1个\", \"金龙鱼小鲜米\": \"80克\", \"面粉\": \"150克\", \"白糖\": \"40克\", \"清水\": \"适量\"}', 17, '2020-02-27 22:30:47', 0, '2020-03-12 19:36:05', '518000');
INSERT INTO `menu` VALUES (575, '小熊维尼豆沙包', 86, '{\"1\": \"制作黄色面团：1.酵母用少量的温开水化开备用。2.把面粉、奶粉和白糖放进和面盆中搅拌均匀。3.把南瓜泥和酵母水倒进面粉中，用筷子搅拌成絮状。4.把絮状面粉揉成光滑的面团。\", \"2\": \"取一小块黄色面团，加入适量可可粉揉搓成褐色面团。\", \"3\": \"把面团盖好静置发酵到两倍大，拉开看见里面的组织呈蜂窝状，就拿出来揉搓排气，并切分成大小相同的小剂子备用。\", \"4\": \"取其中一个小剂子按扁擀薄，放入绿豆沙馅，包成椭圆球状做小熊的脑袋，取两块小面团搓成小圆球，粘在脑袋上做小熊的耳朵。\", \"5\": \"再用褐色面团做出小熊的嘴巴、眼睛、眉毛、鼻子以及嘴巴，粘在的脸上，小熊维尼豆沙包的生胚就完成了。\", \"6\": \"把做好的豆沙包放进蒸笼醒发15—20分钟后，冷水上锅，开大火蒸20分钟，熄火焖5分钟再开锅，就可以上桌开吃啰！\"}', '{\"面粉\": \"300克\", \"白糖\": \"30克\", \"奶粉\": \"30克\", \"南瓜泥\": \"150克\", \"酵母\": \"3克\", \"温水\": \"少量\", \"可可粉\": \"适量\", \"绿豆沙馅\": \"适量\"}', 16, '2020-02-27 22:30:47', 0, '2020-03-12 19:36:05', '508799');
INSERT INTO `menu` VALUES (576, '素馅饺子', 60, '{\"1\": \"今天家里没什么菜了就凑合包两种馅的素饺子，一个是豆腐青菜粉条，一个是西葫芦鸡蛋馅，都是切碎，鸡蛋炒好切碎，木耳泡好切碎，豆腐切小丁用油煎黄一点，然后调味，盐，鸡精，花椒粉，食用油，香油。\", \"2\": \"豆腐馅。\", \"3\": \"西葫芦鸡蛋馅。\", \"4\": \"我饺子包的不好，见笑了。其他那些是我三岁儿子包的。\", \"5\": \"饺子包好了后，锅中加水，烧开后下入饺子，水开后加入清水再烧开，需要烧开三次饺子都漂起来说明熟了，出锅了，非常好吃。\", \"6\": \"蘸料吃非常美味哦。\"}', '{\"面皮\": \"适量\", \"豆腐\": \"适量\", \"粉条\": \"适量\", \"木耳\": \"适量\", \"鸡蛋\": \"适量\", \"西葫芦\": \"适量\", \"油\": \"适量\", \"盐\": \"适量\", \"香油\": \"适量\", \"鸡精\": \"适量\", \"花椒粉\": \"适量\"}', 18, '2020-02-27 22:30:48', 0, '2020-03-12 19:36:05', '516219');
INSERT INTO `menu` VALUES (577, '菠菜锅盔', 44, '{\"1\": \"菠菜洗净备用。\", \"2\": \"入开水锅中焯水，加一点油，颜色非常好看。\", \"3\": \"加一点点碱粉和水打成糊状。\", \"4\": \"取适量面粉，加入发酵粉，倒入菠菜糊揉成软一点的面团，放温暖处发酵。\", \"5\": \"发酵两倍大小即可。\", \"6\": \"加入适量碱粉揉匀，二次醒发十分钟。\", \"7\": \"取出擀成圆饼状。\", \"8\": \"电饼铛预热抹油放入。\", \"9\": \"一面定型后反过来烙另一面。\", \"10\": \"饼上扎孔排气，有助于熟透，多翻几次哦。\", \"11\": \"美美的菠菜锅盔出锅啦。\", \"12\": \"晾凉后切开装盘。\", \"13\": \"家常面食，请您品鉴。\", \"14\": \"成品图。\"}', '{\"菠菜\": \"适量\", \"面粉\": \"适量\", \"发酵粉\": \"适量\", \"碱粉\": \"适量\", \"水\": \"适量\"}', 17, '2020-02-27 22:30:49', 0, '2020-03-12 19:36:05', '518043');
INSERT INTO `menu` VALUES (578, '韭菜包子', 19, '{\"1\": \"用酵母和面，醒发成都是小孔。\", \"2\": \"揉搓成长条。\", \"3\": \"分成大小合适的剂子。\", \"4\": \"擀成皮，中间厚两边薄点。\", \"5\": \"提前准备好的馅，特别说明下炒鸡蛋的时候多放点油，鸡蛋冷亮再放韭菜里，把所有材料放一起拌匀，最后放盐再次拌匀。\", \"6\": \"做成大包子，放太阳下醒发二十分钟，一定要醒面。\", \"7\": \"凉水下锅，开锅蒸二十五分钟关火，停三五分钟开锅。制作完成。\"}', '{\"韭菜\": \"适量\", \"面粉\": \"适量\", \"鸡蛋\": \"适量\", \"虾皮\": \"适量\", \"粉条\": \"适量\", \"葱\": \"适量\", \"姜\": \"适量\", \"盐\": \"适量\", \"鸡精\": \"适量\", \"味精\": \"适量\"}', 16, '2020-02-27 22:30:49', 0, '2020-03-12 19:36:05', '513535');
INSERT INTO `menu` VALUES (579, '三合面馒头', 25, '{\"1\": \"原料：面粉、玉米面、小米面、白糖和酵母粉。\", \"2\": \"将面粉、玉米面和小米面混合，再倒入白糖，混合均匀。\", \"3\": \"清水与酵母粉混合后，慢慢倒入面粉中，再和成面团。\", \"4\": \"面团放入盆中，盖上盖子，放温暖处发酵。\", \"5\": \"发酵好的面团变大，扒开底部有拉丝，将面团移到面板上，撒些面粉，慢慢地揉光滑，成长条状，分成10个小面剤。\", \"6\": \"将小面团揉圆，再次醒发。\", \"7\": \"醒好的面团放入开水锅中，蒸18分钟即可。\", \"8\": \"黄橙橙的杂粮馒头出锅啦。\"}', '{\"面粉\": \"300克\", \"玉米面\": \"120克\", \"小米面\": \"50克\", \"白糖\": \"3克\", \"清水\": \"230克\", \"酵母粉\": \"3克\"}', 17, '2020-02-27 22:30:50', 0, '2020-03-12 19:36:05', '514204');
INSERT INTO `menu` VALUES (580, '大白菜猪肉饺子', 30, '{\"1\": \"面粉加入温水揉成面团醒发一会。\", \"2\": \"大白菜切碎加入盐杀出水。\", \"3\": \"猪肉剁碎，把白菜挤干水加入猪肉馅中，加入配料搅拌。\", \"4\": \"擀成圆皮，加入馅料包好。\", \"5\": \"包好的饺子。\", \"6\": \"大胖饺子。\", \"7\": \"锅中烧开水加入饺子煮沸，点两次冷水再次煮沸即可。\", \"8\": \"成品。\", \"9\": \"喜欢辣椒加点蘸料。\"}', '{\"面粉\": \"适量\", \"猪肉\": \"适量\", \"大白菜\": \"适量\", \"酱油\": \"适量\", \"蚝油\": \"适量\", \"食用油\": \"适量\", \"生姜\": \"适量\", \"盐\": \"适量\"}', 18, '2020-02-27 22:30:51', 0, '2020-03-12 19:36:05', '515503');
INSERT INTO `menu` VALUES (581, '红豆豆包', 28, '{\"1\": \"这是发好的面。\", \"2\": \"揉面排气。\", \"3\": \"搓成长条型。\", \"4\": \"分成均等小面肧。\", \"5\": \"擀成面皮。\", \"6\": \"红小豆适量。\", \"7\": \"取一个面皮包入适量红小豆。\", \"8\": \"都包成包子的形状揉圆在醒发15分。\", \"9\": \"冷水上锅蒸开锅计时15分。\", \"10\": \"很甜很宣的小豆包。\", \"11\": \"成品。\", \"12\": \"成品。\"}', '{\"面粉\": \"适量\", \"酵母\": \"适量\", \"盐\": \"适量\", \"红小豆\": \"适量\"}', 16, '2020-02-27 22:30:51', 0, '2020-03-12 19:36:05', '513330');
INSERT INTO `menu` VALUES (582, '大米松饼', 2, '{\"1\": \"准备原料：金龙鱼小鲜米、面粉、白糖和鸡蛋。\", \"2\": \"大米倒出来称量80克，80克的大米可以做10多个松饼，家里人口少的，可以减量。\", \"3\": \"加入清水浸泡1个小时。\", \"4\": \"将泡好的小鲜米倒入破壁机，我用的是果汁功能，打成米浆。\", \"5\": \"这是打好的米浆，我用破壁机打了两遍。\", \"6\": \"米浆中加入白糖搅拌。\", \"7\": \"加入鸡蛋搅拌均匀，也可以将鸡蛋先搅匀再倒入米浆中。\", \"8\": \"加入面粉，搅拌均匀，没有干粉就可以了。\", \"9\": \"这时的米浆应该是粘稠的。\", \"10\": \"锅烧热后，刷上一层油防粘，舀入一勺米浆小火烙，底部定型后，翻面烙另一面直到成熟。\", \"11\": \"香香、软软又Q弹的大米松饼做好啦。\", \"12\": \"做好大米松饼，趁热吃特别好吃。\"}', '{\"鸡蛋\": \"1个\", \"金龙鱼小鲜米\": \"80克\", \"面粉\": \"150克\", \"白糖\": \"40克\", \"清水\": \"适量\"}', 17, '2020-02-27 22:30:51', 0, '2020-03-12 19:36:05', '518000');
INSERT INTO `menu` VALUES (583, '青菜香菇包子', 56, '{\"1\": \"青菜洗净在沸水中炒熟，捞出过凉水挤干。\", \"2\": \"切细。\", \"3\": \"香菇切细丁。\", \"4\": \"豆腐切细丁。\", \"5\": \"全部食材放到盆里，加盐、白糖、蚝油拌匀。\", \"6\": \"发酵好的面团。\", \"7\": \"取出放案板上排气，分成小剂子。\", \"8\": \"擀成中间厚四周薄的小圆片，放上馅料，包成包子。\", \"9\": \"放到抹了油的笼屉里二次饧发至包子胚明显变大。\", \"10\": \"冷水上锅，大火烧开上汽后转中火蒸12分钟，关火虚蒸3分钟开盖。\"}', '{\"青菜\": \"适量\", \"香菇\": \"适量\", \"豆腐\": \"适量\", \"蒜泥\": \"适量\", \"葱油\": \"适量\", \"白糖\": \"适量\", \"蚝油\": \"适量\"}', 16, '2020-02-27 22:30:52', 0, '2020-03-12 19:36:05', '494310');
INSERT INTO `menu` VALUES (584, '烫面蒸饺', 79, '{\"1\": \"胡萝卜用原汁机榨汁，汁用来活面了，渣用来拌馅，韭菜切碎，鸡蛋炒好，拌匀。\", \"2\": \"面是开水烫面，和好醒一会，擀成比饺子皮稍大一点的皮。\", \"3\": \"包了白面和胡萝卜汁面。\", \"4\": \"凉水上锅蒸20分钟。\", \"5\": \"摆盘，调个自己喜欢的蘸汁就可以吃了。\"}', '{\"韭菜\": \"半斤\", \"胡萝卜\": \"一根\", \"鸡蛋\": \"2个\", \"面粉\": \"一碗\", \"盐\": \"适量\", \"香油\": \"适量\", \"饺子调料\": \"适量\"}', 17, '2020-02-27 22:30:54', 0, '2020-03-12 19:36:05', '517993');
INSERT INTO `menu` VALUES (585, '洋葱肉包子电蒸锅', 84, '{\"1\": \"葱姜。\", \"2\": \"剁肉葱姜。\", \"3\": \"这样。\", \"4\": \"放入鸡蛋搅拌均匀。\", \"5\": \"均匀。\", \"6\": \"香油。\", \"7\": \"生抽。\", \"8\": \"蚝油。\", \"9\": \"花生油。\", \"10\": \"老抽。\", \"11\": \"糖。\", \"12\": \"盐。\", \"13\": \"搅拌均匀一个方向。\", \"14\": \"洋葱，注意会留泪哦。\", \"15\": \"放。\", \"16\": \"木耳泡发后切碎。\", \"17\": \"洋葱，木耳放进肉里搅拌。\", \"18\": \"第一步酵母发面，温水。\", \"19\": \"面粉发。\", \"20\": \"揉。\", \"21\": \"发。\", \"22\": \"发好。\", \"23\": \"揉面。\", \"24\": \"切。\", \"25\": \"皮。\", \"26\": \"包。\", \"27\": \"这样。\", \"28\": \"这样。\", \"29\": \"放进电蒸锅。\", \"30\": \"共蒸20分钟，关火焖5分。\", \"31\": \"吃起来。\"}', '{\"前肘猪肉\": \"一块\", \"葱\": \"一块\", \"姜\": \"一块\", \"洋葱\": \"2个\", \"木耳\": \"20个\", \"盐\": \"适量\", \"香油\": \"适量\", \"花生油\": \"适量\", \"蚝油\": \"适量\", \"老抽\": \"适量\", \"生抽\": \"适量\", \"鸡蛋\": \"1只\", \"糖\": \"少许\"}', 16, '2020-02-27 22:30:54', 0, '2020-03-12 19:36:05', '512309');
INSERT INTO `menu` VALUES (586, '西葫芦虾仁水饺', 57, '{\"1\": \"鸡蛋.虾仁.西葫芦。\", \"2\": \"木耳，用温水泡30分钟，上锅煮5分钟。\", \"3\": \"面粉，倒入适量清水，用筷子搅拌成絮状。\", \"4\": \"揉成光滑的面团，醒面10分钟。\", \"5\": \"锅内放花生油，倒入鸡蛋炒熟。\", \"6\": \"所有食材，全部切碎，放在一起。\", \"7\": \"放入耗油，香油，盐。\", \"8\": \"拌匀。\", \"9\": \"面团，撮成长条，分成大小均匀的剂子。\", \"10\": \"擀成饺子皮。\", \"11\": \"放入馅料。\", \"12\": \"饺子全部包好。\", \"13\": \"锅内加水烧开，放入饺子，大火烧开，中火煮5分钟。\", \"14\": \"出锅喽。\", \"15\": \"成品。\", \"16\": \"清爽美味 ，好吃不腻。\"}', '{\"西葫芦\": \"一块\", \"虾仁\": \"适量\", \"面粉\": \"300克\", \"鸡蛋\": \"两个\", \"木耳\": \"一把\", \"香油\": \"2勺\", \"花生油\": \"2勺\", \"蚝油\": \"1勺\", \"盐\": \"适量\", \"姜末\": \"适量\", \"葱末\": \"适量\"}', 18, '2020-02-27 22:30:55', 0, '2020-03-12 19:36:05', '512752');
INSERT INTO `menu` VALUES (587, '千层小蒸糕', 51, '{\"1\": \"准备好面粉。\", \"2\": \"准备好黄糖和蜂蜜，用热水泡着。\", \"3\": \"碗中放面粉，先倒入蜂蜜水，搅拌成面糊状，大概粉和糖水的比例是2：3。\", \"4\": \"然后上锅蒸大概七分钟左右。\", \"5\": \"接着又重新弄黄糖水面浆。\", \"6\": \"在刚刚蒸好的一层上，再浇上面浆，以此类推，蒸上四五层。\", \"7\": \"蒸好到最后一层后，关火后，过三四分钟再开盖，然后蒸糕表面的水蒸气有点多，可以倒掉，然后取出来，放凉，切成块状，即可。\"}', '{\"面粉\": \"适量\", \"黄糖\": \"少许\", \"蜂蜜\": \"少许\"}', 17, '2020-02-27 22:30:55', 0, '2020-03-12 19:36:05', '517979');
INSERT INTO `menu` VALUES (588, '粉条东坡肉包子', 14, '{\"1\": \"600g面粉+5g酵母+330g牛奶/水。\", \"2\": \"揉成光滑的面团，盖保鲜膜，饧面。\", \"3\": \"东坡肉三块。东坡肉做法见我上一篇菜谱。\", \"4\": \"东坡肉切成小块，不用太碎。放至冷却。\", \"5\": \"粉条用开水泡至无硬芯。\", \"6\": \"切段。放凉。\", \"7\": \"东坡肉、粉条，和煮东坡肉剩下的汤汁，混合均匀。若无汤汁，放适量酱油，得保证盐味儿够，反正都是熟的可以先尝一尝。\", \"8\": \"混合均匀后静置一会儿。\", \"9\": \"面团饧至两倍大，手指戳面团不回弹。揉几分钟，排气。\", \"10\": \"面团整理成大小均等的面剂子，用擀面杖擀成圆形，包入馅料。\", \"11\": \"包成包子的形状。饧发20分钟。\", \"12\": \"上锅蒸，水开后转小火蒸15分钟。关火焖三分钟。\", \"13\": \"成品。\"}', '{\"东坡肉\": \"三块\", \"粉条\": \"一把\", \"面粉\": \"600g\", \"酵母\": \"5g\", \"水\": \"330g\"}', 16, '2020-02-27 22:30:55', 0, '2020-03-12 19:36:05', '512000');
INSERT INTO `menu` VALUES (589, '芹菜肉末鸡蛋饺子', 82, '{\"1\": \"芹菜切断与肉末，加入一勺盐，生抽，香油，鸡蛋顺着一个方向搅拌均匀。\", \"2\": \"饺子皮取一片。\", \"3\": \"放入馅料。\", \"4\": \"依次包好，可以冷冻起来！\", \"5\": \"想吃的时候，煮来吃。\", \"6\": \"成品图！\"}', '{\"肉末\": \"400\", \"芹菜\": \"500克\", \"鸡蛋\": \"一个\", \"饺子皮\": \"适量\", \"玉米油\": \"5毫升\", \"生抽\": \"5毫升\", \"盐\": \"3克\"}', 18, '2020-02-27 22:30:56', 0, '2020-03-12 19:36:05', '513276');
INSERT INTO `menu` VALUES (590, '香酥油条', 73, '{\"1\": \"准备好面粉。\", \"2\": \"主料中所有的食材都放入一个大碗中。\", \"3\": \"搅拌成面絮。\", \"4\": \"面千万不要揉，用手颌成面团就行。\", \"5\": \"先用拳头多拽面，盖上保鲜膜，醒面半个小时。\", \"6\": \"重复揣面，这样的动作反复三次。\", \"7\": \"最后抹上食用油，裹上保鲜膜密封冷藏一个晚上（8个小时以上）。\", \"8\": \"第二天早上，拿出面团室温回温半个小时后，用手按扁（0.5cm厚）抻长，醒好的面团千万不要揉。\", \"9\": \"切成3cm宽的小条。\", \"10\": \"再对半切段。\", \"11\": \"两个为一组。\", \"12\": \"筷子粘少许水，在中间压一下，两边捏紧。\", \"13\": \"起锅烧油，油温要高（用一块小面条放入油锅试一下，五秒后能马上浮起即可）。\", \"14\": \"用筷子不停的翻动，油条才会蓬松酥脆。\", \"15\": \"起锅，沥干油。\", \"16\": \"蓬松酥脆，都是蜂窝状呢。\", \"17\": \"成品图。\", \"18\": \"成品图。\"}', '{\"中筋面粉\": \"500克\", \"鸡蛋\": \"一个（50克左右）\", \"无铝泡打粉\": \"8克\", \"食用小苏打\": \"3克\", \"盐\": \"8克\", \"油\": \"1000克（以实际消耗为准）\", \"温水\": \"260克\"}', 17, '2020-02-27 22:30:56', 0, '2020-03-12 19:36:05', '517958');
INSERT INTO `menu` VALUES (591, '木耳菜包子', 89, '{\"1\": \"先将黑木耳泡发，然后将黑木耳、青菜和豆干洗净剁碎。\", \"2\": \"放入盐，油，糖和老抽搅拌均匀备用。\", \"3\": \"酵母水倒入面粉中搅拌。\", \"4\": \"揉成光滑的面团进行发酵。\", \"5\": \"发酵至2倍。\", \"6\": \"排空气，将面团分成12小团。\", \"7\": \"取一面团压扁放上木耳菜馅。\", \"8\": \"然后收口。\", \"9\": \"包好后放入蒸隔上醒20分钟。\", \"10\": \"锅中放水，放入包子蒸15分钟。\", \"11\": \"15分钟关火再焖5分钟。\"}', '{\"面粉\": \"500g\", \"青菜\": \"500g\", \"豆干\": \"3块\", \"黑木耳\": \"20g（干）\", \"酵母\": \"8g\", \"水\": \"适量\", \"盐\": \"适量\", \"香油\": \"适量\", \"生抽\": \"适量\", \"糖\": \"适量\"}', 16, '2020-02-27 22:30:56', 0, '2020-03-12 19:36:05', '511996');
INSERT INTO `menu` VALUES (592, '芋头白菜肉末饺子', 80, '{\"1\": \"肉末，白菜切碎，打入一个鸡蛋混合。\", \"2\": \"萝卜去皮切碎，芋头切碎，葱花切碎备用。\", \"3\": \"萝卜，芋头倒入与肉末混合。\", \"4\": \"油锅烧热倒入玉米油，倒入葱花爆炒一分钟。\", \"5\": \"将葱花与肉末白菜，芋头混合，顺着一个方向搅拌均匀备用。\", \"6\": \"取面团，揉面的时候，适当的加入盐，这样做出来的饺子好吃，有弹性！\", \"7\": \"揉10分钟成光滑长条形。\", \"8\": \"分成一个个小剂子。\", \"9\": \"擀成饺子皮！\", \"10\": \"依次包好。\", \"11\": \"吃不完的，可以冷冻起来哦！\"}', '{\"芋头\": \"适量\", \"白菜\": \"适量\", \"肉末\": \"适量\", \"葱花\": \"适量\", \"鸡蛋\": \"一个\", \"香油\": \"6毫升\", \"盐\": \"3克\", \"生抽\": \"5毫升\"}', 18, '2020-02-27 22:30:57', 0, '2020-03-12 19:36:05', '513267');
INSERT INTO `menu` VALUES (593, '黄玫瑰馒头', 56, '{\"1\": \"南瓜削皮，上锅蒸熟。\", \"2\": \"蒸熟的南瓜趁热捣成泥，放凉倒入3克酵母。\", \"3\": \"放入适量白砂糖（根据个人喜好添加）。\", \"4\": \"倒入面粉搅拌成絮状。\", \"5\": \"揉成面团，醒发至两倍大。\", \"6\": \"面团取一段，搓成长条，切成小剂子。\", \"7\": \"取一个剂子搓成圆形，擀成长条，用刀子划三下。\", \"8\": \"面团编成麻花辫。\", \"9\": \"由下往上卷起来。\", \"10\": \"做好的黄玫瑰馒头，二次醒发。\", \"11\": \"上锅蒸15分钟即可。\", \"12\": \"成品图。\"}', '{\"南瓜\": \"1块\", \"面粉\": \"300克\", \"酵母\": \"3克\", \"白砂糖\": \"30克\"}', 17, '2020-02-27 22:30:57', 0, '2020-03-12 19:36:05', '517915');
INSERT INTO `menu` VALUES (594, '海带芽麻辣槐花汤饺', 23, '{\"1\": \"儿子想喝酸汤水饺，又包了一些。\", \"2\": \"蒜黄，小米椒，葱姜。\", \"3\": \"蒜黄，葱姜，小米椒，放入碗内，适量盐，生抽，陈醋，香油，麻椒粉，鸡精调味。\", \"4\": \"海带芽材料包。\", \"5\": \"锅内适量水烧开，黄豆芽，海带芽倒入煮两滚儿。\", \"6\": \"槐花杏鲍菇蛋饺放入。\", \"7\": \"煮三滚儿。\", \"8\": \"调好的料汁倒入。\", \"9\": \"搅匀关火。\", \"10\": \"辣椒油盾自己喜欢，成品。\", \"11\": \"成品。\"}', '{\"海带芽\": \"适量\", \"槐花蛋饺\": \"适量\", \"黄豆芽\": \"适量\", \"葱\": \"适量\", \"蒜黄\": \"适量\", \"小米椒\": \"适量\", \"盐\": \"适量\", \"生抽\": \"适量\", \"陈醋\": \"适量\", \"香油\": \"适量\", \"鸡精\": \"适量\", \"麻椒粉\": \"适量\", \"姜\": \"适量\"}', 18, '2020-02-27 22:30:58', 0, '2020-03-12 19:36:05', '513177');
INSERT INTO `menu` VALUES (595, '牛肉胡萝卜馅包子', 20, '{\"1\": \"适量的面粉加入糖，盐适量的温水拌成大面絮。\", \"2\": \"揉成光滑面团，自然发酵90分钟，室内有20度。\", \"3\": \"准备馅料食材：牛排，胡萝卜，黑木耳。\", \"4\": \"把食材都剁碎，加黑椒汁，蚝油，香油拌匀，馅就调好了。\", \"5\": \"面发好了，如图，很不错。\", \"6\": \"揉揉排气，搓长条。\", \"7\": \"分成大小均匀的面剂子。\", \"8\": \"按扁，擀成中间厚周边薄的面皮，放入适量的馅，包成自己喜欢的样子。\", \"9\": \"包好一个了，依次都包好。\", \"10\": \"放入蒸锅，二次醒发15-20分，冷水开蒸20分钟，再虚蒸5分钟即可。\", \"11\": \"叮当，时间到，美味包子眼前一亮。\", \"12\": \"夹出装盘。\", \"13\": \"成品图。\"}', '{\"面粉\": \"适量\", \"酵母\": \"适量\", \"牛排\": \"适量\", \"胡萝卜\": \"适量\", \"洋葱\": \"少许\", \"黑木耳\": \"少许\", \"黑椒汁\": \"适量\", \"蚝油\": \"少许\", \"香油\": \"少许\"}', 16, '2020-02-27 22:30:59', 0, '2020-03-12 19:36:05', '511637');
INSERT INTO `menu` VALUES (596, '韭菜肉末菠菜饺子', 27, '{\"1\": \"菠菜洗干净备用。\", \"2\": \"大火烧开，加入菠菜煮熟。\", \"3\": \"榨成菠菜汁。\", \"4\": \"韭菜切断，与肉末混合。\", \"5\": \"加入一个鸡蛋，盐，生抽，香油顺着一个方向搅拌均匀备用。\", \"6\": \"菠菜汁倒入面粉，酵母粉，揉10分钟，揉光滑面团。\", \"7\": \"取部分面团揉成椭圆形长条，切断。\", \"8\": \"擀成饺子皮，表面放入馅料。\", \"9\": \"依次包好，吃不完的，可以冷冻起来。\", \"10\": \"大火烧开。放入饺子，加入三次水，煮熟。\", \"11\": \"一份完美好吃的饺子做好了。\", \"12\": \"成品图，好吃美味！\"}', '{\"菠菜\": \"适量\", \"面粉\": \"适量\", \"肉末\": \"适量\", \"韭菜\": \"适量\", \"香油\": \"适量\", \"盐\": \"5克\", \"生抽\": \"8毫升\", \"鸡蛋\": \"一个\"}', 18, '2020-02-27 22:31:00', 0, '2020-03-12 19:36:05', '513160');
INSERT INTO `menu` VALUES (597, '香煎韭菜鸡蛋木耳虾皮小笼包', 30, '{\"1\": \"备好食材:韭菜，鸡蛋，黑木耳需提前泡，虾皮，饺子皮。\", \"2\": \"热锅放油，下鸡蛋炒熟。\", \"3\": \"用锅铲用得碎一点，铲出。\", \"4\": \"韭菜，木耳都切碎加入鸡蛋末，虾皮。\", \"5\": \"放入调料:胡椒粉，蚝油，香油拌匀。\", \"6\": \"取一张饺子皮，放适量馅，包好。\", \"7\": \"如图，包成小笼包即可。\", \"8\": \"依次都包好，锅中放油，摆入小笼包，中小火煎。\", \"9\": \"煎至小笼包底部定型，如图。\", \"10\": \"用生粉加水调个薄芡粉水。\", \"11\": \"把生粉水倒入锅中大火煎制，盖上盖子更易熟透。\", \"12\": \"3分钟左右水份差不多干了打开盖子再煎一会。\", \"13\": \"再2分钟左右水份干了底部会有冰花状，小火再煎1分钟左右即可。\", \"14\": \"夹一个小笼包看下，底部已金黄色，很酥脆，很香哦。\", \"15\": \"夹出装盘。\", \"16\": \"成品图。\", \"17\": \"成品图。\"}', '{\"韭菜\": \"适量\", \"鸡蛋\": \"1个\", \"黑木耳\": \"少许\", \"饺子皮\": \"适量\", \"虾皮\": \"少许\", \"油\": \"适量\", \"蚝油\": \"少许\", \"香油\": \"少许\", \"胡椒粉\": \"少许\", \"生粉\": \"少许\"}', 17, '2020-02-27 22:31:00', 0, '2020-03-12 19:36:05', '517910');
INSERT INTO `menu` VALUES (598, '酸汤水饺', 75, '{\"1\": \"葱切成葱花，蒜切碎，放入白芝麻，辣椒粉。\", \"2\": \"锅中倒油，烧开后放入花椒粒炸香然后浇到碗中搅拌均匀，然后加入醋，生抽，盐调味。\", \"3\": \"饺子煮到都浮起来差不多就好了，捞出来加入一点饺子汤，然后把调好的汁倒入饺子上，最后撒上香菜或韭菜就可以吃了。\"}', '{\"水饺\": \"适量\", \"葱\": \"适量\", \"姜\": \"适量\", \"蒜\": \"适量\", \"白芝麻\": \"适量\", \"辣椒粉\": \"适量\", \"香菜\": \"或韭菜\", \"盐\": \"适量\", \"醋\": \"适量\", \"生抽\": \"适量\"}', 18, '2020-02-27 22:31:02', 0, '2020-03-12 19:36:05', '513210');
INSERT INTO `menu` VALUES (599, '梅干菜猪肉包', 76, '{\"1\": \"肉馅中放入梅干菜、加盐、糖、生抽、鸡精拌匀待用。\", \"2\": \"面粉放入盘中、酵母用温水化开倒入，用筷子搅拌成絮状。\", \"3\": \"开始揉面、揉成光滑的面团，盖上保鲜膜发酵至两倍大。\", \"4\": \"发好的面团取岀，再撒些干面粉揉成光滑面团，然后揉搓成长条。\", \"5\": \"切成小剂子。\", \"6\": \"每个面团分别揉一揉、擀成圆形。\", \"7\": \"取一张包子皮放入肉馅包成包子，烤盘铺上油纸、放上包子。\", \"8\": \"放入烤箱、二次发酵40分钟。\", \"9\": \"烤箱调至蒸汽模式、100度蒸18分钟、焖2-3分钟后打开门。\", \"10\": \"蒸好的馒头出锅了。\", \"11\": \"成品。\"}', '{\"面粉\": \"适量\", \"肉末\": \"适量\", \"梅干菜\": \"适量\", \"酵母\": \"适量\", \"水\": \"适量\", \"姜\": \"适量\", \"盐\": \"适量\", \"糖\": \"适量\", \"干辣椒\": \"适量\"}', 16, '2020-02-27 22:31:02', 0, '2020-03-12 19:36:05', '391969');
INSERT INTO `menu` VALUES (600, '麻将烧饼', 47, '{\"1\": \"面粉酵母，水一起放入盆中。\", \"2\": \"和成软一点的面团醒发制1.5倍大，不要发过了。\", \"3\": \"麻将加适量盐和水调匀，不能调太稀。\", \"4\": \"醒发好的面团放揉面垫揉一揉排气。\", \"5\": \"擀成长方形薄片。\", \"6\": \"刷上麻将。\", \"7\": \"从一边卷起来。\", \"8\": \"切成小段。\", \"9\": \"取一个小面段两头捏紧按扁。\", \"10\": \"刷上酱油。\", \"11\": \"沾上一层芝麻。\", \"12\": \"放在烤盘上。\", \"13\": \"全部做好放在烤盘里醒十五分钟。\", \"14\": \"烤箱提前预热上下火180°烤20分钟。\", \"15\": \"出炉。\"}', '{\"面粉\": \"300g\", \"水\": \"180g\", \"酵母\": \"3g\", \"芝麻酱\": \"3勺\", \"盐\": \"适量\", \"酱油\": \"适量\", \"白芝麻\": \"适量\"}', 17, '2020-02-27 22:31:02', 0, '2020-03-12 19:36:05', '517656');
INSERT INTO `menu` VALUES (601, '笋丁酱肉包', 45, '{\"1\": \"煮牛肉剩了些酱渣，不舍得浪费，加了些水和1大勺桂馨斋的甜面酱调和一下。400g猪肉（3：7的肥瘦比）姜末8g。\", \"2\": \"炒锅炸10粒花椒，出香味后捞出。\", \"3\": \"下入猪肥肉丁煸出油脂。\", \"4\": \"加入姜末。\", \"5\": \"接着下入瘦肉丁煸炒。\", \"6\": \"变色后加入料酒。\", \"7\": \"倒入干黄酱煸炒。\", \"8\": \"加入适量温水大火煮开。\", \"9\": \"小火炖煮。中间要时不时的从底部翻动，防止糊锅。\", \"10\": \"煮至肉丁呈酱红色，汁水浓稠。\", \"11\": \"炖肉的空隙，将提前焯过水的160g冬笋切成小丁备用。\", \"12\": \"盛出多余的酱肉丁。锅中剩300g酱肉丁，加入冬笋丁。\", \"13\": \"调入适量的五香粉、盐，收收汁水盛出晾凉。\", \"14\": \"等酱肉完全晾凉后，加入35克的香葱碎。\", \"15\": \"500g面粉，加入3g酵母，285g温水和成柔软光滑的面团，放温暖处省发。\", \"16\": \"面团发酵至1.5倍大小，气孔充盈。\", \"17\": \"将面团揉光揉匀，搓长条、切剂子。\", \"18\": \"擀皮，放上适量的馅料。\", \"19\": \"开始包包子，馅料很诱人哦！\", \"20\": \"包好的酱肉笋丁包。\", \"21\": \"全部包完，盖上布子防止表皮风干，放在温暖处省发至包子变轻，或手触包子皮能缓缓弹起。\", \"22\": \"笼屉上抹点油防粘，放入醒发好的包子。\", \"23\": \"开水上锅，上气后调小火力，保持有蒸汽溢出，蒸10分钟就好。\"}', '{\"酱肉丁\": \"300g\", \"冬笋丁\": \"160g\", \"香葱\": \"35g\", \"盐\": \"适量\", \"五香粉\": \"适量\", \"面粉\": \"500g\", \"酵母\": \"3g\", \"温水\": \"285g\"}', 16, '2020-02-27 22:31:03', 0, '2020-03-12 19:36:05', '480038');
INSERT INTO `menu` VALUES (602, '槐花杏鲍菇蛋饺', 36, '{\"1\": \"适量面粉。\", \"2\": \"适量清水边倒边用筷子搅拌，搅成絮状。\", \"3\": \"和成软硬适中的面团。饧一个小时。\", \"4\": \"这边准备馅儿。杏鲍菇两根，切成片。\", \"5\": \"开水中焯两滚儿捞出控水。\", \"6\": \"槐花解冻，清水洗一下，捞出攥一下水份备用。\", \"7\": \"杏鲍菇切小丁。\", \"8\": \"锅内适量油烧热，姜葱炒香。\", \"9\": \"杏鲍菇和胡萝卜丁放入，一点点盐，老抽调味。\", \"10\": \"翻炒几下关火，盛出备用。\", \"11\": \"柴鸡蛋4个打散。\", \"12\": \"锅内宽油，凉油倒入蛋液，小火，用筷子不停打圈搅动。\", \"13\": \"搅成蛋碎备用。\", \"14\": \"葱姜。\", \"15\": \"槐花，杏鲍菇，蛋碎，葱姜放容器内。\", \"16\": \"适量香油，蚝油，花椒粉，鸡精，盐调味。\", \"17\": \"拌匀备用。\", \"18\": \"饧好的面团，搓成长条。\", \"19\": \"切成小剂。\", \"20\": \"按扁。\", \"21\": \"擀成饺子皮。\", \"22\": \"包上馅儿。\", \"23\": \"包成饺子。\", \"24\": \"包好的饺子。\", \"25\": \"锅内适量水烧开，饺子放入。\", \"26\": \"煮两三滚儿，捞出关火。\", \"27\": \"葱姜蒜，小米椒碎，适量生抽，陈醋，香油调成蘸料。\", \"28\": \"成品。\", \"29\": \"成品。\", \"30\": \"成品。\"}', '{\"槐花\": \"适量\", \"杏鲍菇\": \"适量\", \"鸡蛋\": \"4个\", \"胡萝卜\": \"适量\", \"葱姜\": \"适量\", \"盐\": \"适量\", \"蚝油\": \"适量\", \"香油\": \"适量\", \"花椒粉\": \"适量\", \"老抽\": \"适量\", \"鸡精\": \"适量\", \"面粉\": \"适量\", \"清水\": \"适量\", \"葱\": \"适量\", \"小米椒\": \"2个\", \"生抽\": \"适量\", \"陈醋\": \"适量\", \"姜\": \"适量\", \"蒜\": \"适量\"}', 18, '2020-02-27 22:31:03', 0, '2020-03-12 19:36:05', '513036');
INSERT INTO `menu` VALUES (603, '酱香饼', 24, '{\"1\": \"面粉中加水先用筷子搅拌成絮状。\", \"2\": \"用手揉匀，发酵20分钟左右。\", \"3\": \"这时候备酱料，碗中加入香辣酱，蚝油，辣椒油，甜面酱和冷开水搅拌均匀，甜面酱和辣椒油要少点，其他的酱可以一样。\", \"4\": \"面团擀成薄片，滑一个口子，围着中间卷起来。\", \"5\": \"卷成一个锥子样子。\", \"6\": \"再从上往下压成这样的。\", \"7\": \"再擀成薄片。\", \"8\": \"因为没有平底锅，就分成几块煎，锅中油不用太多。\", \"9\": \"其中一面煎脆了，就翻面煎另外一面，煎脆的那面就可以刷酱了。\", \"10\": \"另外一面煎脆了就可以撒上芝麻和香菜出锅了。\", \"11\": \"切成小块装盘享用了。\"}', '{\"面粉\": \"适量\", \"芝麻\": \"适量\", \"香菜\": \"适量\", \"蚝油\": \"适量\", \"香辣酱\": \"适量\", \"甜面酱\": \"适量\", \"辣椒油\": \"适量\"}', 17, '2020-02-27 22:31:03', 0, '2020-03-12 19:36:05', '517720');
INSERT INTO `menu` VALUES (604, '香菇肉末饺子', 16, '{\"1\": \"肉末，香菇肉，加入一勺盐，生抽，香油，鸡蛋一个顺着一个方向搅拌均匀。\", \"2\": \"取一片饺子皮。\", \"3\": \"依次包好备用。\", \"4\": \"吃不完可以冷冻起来哦！\", \"5\": \"大火烧开，一个一个放入饺子。\", \"6\": \"加三次水，即可出锅！\", \"7\": \"成品图！\", \"8\": \"好吃的饺子上桌了！\"}', '{\"饺子皮\": \"适量\", \"肉末\": \"适量\", \"干香菇\": \"适量\", \"鸡蛋\": \"一个\", \"盐\": \"3克\", \"生抽\": \"5毫升\", \"香油\": \"5毫升\"}', 18, '2020-02-27 22:31:04', 0, '2020-03-12 19:36:05', '513124');
INSERT INTO `menu` VALUES (605, '萝卜丝肉包', 70, '{\"1\": \"面粉加入酵母，加入适量水揉成团，盖保鲜膜发酵至2倍大。\", \"2\": \"白萝卜刨成丝。\", \"3\": \"胡萝卜切碎。\", \"4\": \"葱切碎。\", \"5\": \"萝卜丝绰水后捞出放入凉水中。\", \"6\": \"撰干水分。\", \"7\": \"肉糜准备好。\", \"8\": \"锅烧热后加入适量油，然后加入白萝卜丝和胡萝卜丝煸炒，再加入葱花，加入适量耗油和盐，糖调味。\", \"9\": \"炒好的萝卜馅加入肉糜中搅拌均匀，包子馅就做好了。\", \"10\": \"发酵好的面团分成若干份。\", \"11\": \"包入馅料，然后入锅中二发，之后蒸20分钟。\", \"12\": \"蒸好后闷3，4分钟再开锅盖，取出晾凉，包子一次可以多做些让冷冻，早上热一下就可以吃啦。\", \"13\": \"这是冷冻后取出热的包子。\"}', '{\"普通面粉\": \"适量\", \"酵母\": \"适量\", \"白萝卜\": \"适量\", \"胡萝卜\": \"适量\", \"肉糜\": \"适量\", \"葱花\": \"适量\"}', 16, '2020-02-27 22:31:04', 0, '2020-03-12 19:36:05', '510210');
INSERT INTO `menu` VALUES (606, '烫面饼子卷时蔬', 20, '{\"1\": \"面粉加入开水拌成絮状，加入熟油揉成软一点的面团。\", \"2\": \"醒至三十分钟左右。\", \"3\": \"案板抹油揉至光滑。\", \"4\": \"搓成条改到同等大小的剂子。\", \"5\": \"两个一组中间抹油压成饼。\", \"6\": \"擀成薄饼状。\", \"7\": \"平底锅抹油加热，放入饼子。\", \"8\": \"小火煎制两面上色即可。\", \"9\": \"熟后揭开即可。\", \"10\": \"依次做好备用。\", \"11\": \"卷入各种时蔬美味极了。\", \"12\": \"家常美味，请您品鉴。\"}', '{\"面粉\": \"适量\", \"开水\": \"适量\", \"油\": \"适量\", \"各种时蔬\": \"适量\"}', 17, '2020-02-27 22:31:05', 0, '2020-03-12 19:36:05', '517682');
INSERT INTO `menu` VALUES (607, '萝卜肉末饺子', 88, '{\"1\": \"肉末，加入香葱，盐，生抽，鸡蛋2个顺着一个方向搅拌均匀。\", \"2\": \"萝卜去皮切丝榨干水分。\", \"3\": \"放入与肉末顺着一个方向搅拌均匀。\", \"4\": \"面团准备好！揉面的时候需要加入适量盐，和一个鸡蛋，揉10分钟。\", \"5\": \"揉成长条备用。\", \"6\": \"切成小剂子备用。\", \"7\": \"擀成饺子皮。\", \"8\": \"依次包好！包饺子非常简单！\", \"9\": \"水开放入饺子，点三次水即可！\", \"10\": \"入盘，完美，开吃！\"}', '{\"萝卜丝\": \"1000克\", \"肉末\": \"适量\", \"鸡蛋\": \"2个\", \"香葱\": \"适量\", \"香油\": \"一勺\", \"生抽\": \"一勺\", \"盐\": \"2克\"}', 18, '2020-02-27 22:31:06', 0, '2020-03-12 19:36:05', '513085');
INSERT INTO `menu` VALUES (608, '仿真香菇包子', 54, '{\"1\": \"备好食材\", \"2\": \"可可加点水调成糊 枣泥搓圆球\", \"3\": \"发好的面切节\", \"4\": \"把小节搓个窝头状放入枣泥封口搓圆\", \"5\": \"依次做好在搓点蘑菇腿\", \"6\": \"放入蒸锅刷一层可可粉2次醒发\", \"7\": \"醒发20分钟大火烧开蒸15分钟即可 把腿塞进蘑菇中\", \"8\": \"出炉。\"}', '{\"面粉\": \"500克\", \"枣泥\": \"300克\", \"可可粉\": \"1勺\", \"酵母\": \"6克\", \"糖\": \"6克\"}', 16, '2020-02-27 22:31:06', 0, '2020-03-12 19:36:05', '505912');
INSERT INTO `menu` VALUES (609, '葱油花卷', 6, '{\"1\": \"温水加入酵母化开，老面撕成小块加入稍微搅拌一下。\", \"2\": \"加入面粉和成面团，放温暖处发酵或提前一晚和好待其慢慢发酵，第二天再用。\", \"3\": \"面团发成蜂窝状即可。如果发酵过了有酸味就再加入少量苏打或碱面中和一下。\", \"4\": \"撒面薄把面团倒案板上，苏打要和面薄掺一起，揉至无酸味盖盆醒发5分钟。擀成面片。\", \"5\": \"先抹油再撒盐，花椒面，葱花。\", \"6\": \"从一头卷起，切块。\", \"7\": \"两块叠一起，用筷子压一下。\", \"8\": \"拉长。\", \"9\": \"像后卷起并压实底部，醒发20分钟。\", \"10\": \"放入笼屉，锅里加入水开火上汽后蒸15分钟关火，再焖2分钟出锅。\"}', '{\"面粉\": \"500g\", \"酵母\": \"5g\", \"水\": \"250g\", \"老面\": \"适量（可不加）\", \"葱花\": \"适量\", \"油\": \"适量\", \"盐\": \"少许\", \"花椒面\": \"少许\"}', 17, '2020-02-27 22:31:06', 0, '2020-03-12 19:36:05', '516469');
INSERT INTO `menu` VALUES (610, '槐花蒸饺', 74, '{\"1\": \"冷冻槐花洗净。\", \"2\": \"入沸水焯一下捞出过凉水挤干。\", \"3\": \"韭菜切碎。\", \"4\": \"肉馅加入料酒、花椒粉、酱油、姜末和盐，放入1个鸡蛋的蛋清搅上劲。\", \"5\": \"加入韭菜、槐花。\", \"6\": \"加入油和适量盐拌匀成馅。\", \"7\": \"面粉加入大约180克沸水搅成面絮，再分次加点清水揉成软硬合适的面团醒一会。\", \"8\": \"分小剂子。\", \"9\": \"擀皮。\", \"10\": \"包成饺子。\", \"11\": \"凉水上锅，上汽后15分钟左右。趁热取出放凉。\"}', '{\"面粉\": \"300克\", \"槐花\": \"200克\", \"肉馅\": \"160克\", \"韭菜\": \"1小把\", \"姜\": \"1块\", \"料酒\": \"1小匙\", \"花椒粉\": \"1小匙\", \"酱油\": \"1大匙\", \"花生油\": \"1大匙\", \"香油\": \"1小匙\", \"盐\": \"适量\", \"蛋清\": \"1个\"}', 18, '2020-02-27 22:31:07', 0, '2020-03-12 19:36:05', '512927');
INSERT INTO `menu` VALUES (611, '水煎包', 57, '{\"1\": \"面粉500克、酵母粉3克、白糖10克\", \"2\": \"面粉、酵母粉、白糖混合均匀，逐次加入温水揉光滑的面团，盖上保鲜膜放温暖处醒发。\", \"3\": \"醒发面团的时候来调制馅料，猪肉馅400克，大葱2棵\", \"4\": \"猪肉馅中加入料酒、1茶匙老抽、2茶匙生抽、1茶匙蚝油、姜粉、食盐搅拌均匀\", \"5\": \"搅拌均匀的肉馅中分次加入高汤或水超一个方向搅打上劲\", \"6\": \"大葱切末\", \"7\": \"切好的葱末放入搅打好的肉馅中，加入食用油，香油（香油、食用油淋在大葱末）\", \"8\": \"用时拌匀即可（加入大葱后，馅料尽量不要提前拌，用时拌匀即好）\", \"9\": \"面团醒发至两倍大（用手沾些面粉，戳一下面团不回缩即好）\", \"10\": \"醒发好的面团放在案板上揉匀排气。\", \"11\": \"揉匀的面团搓长下剂子（剂子大小随意，本着肉馅剂子小一些，素馅大些）\", \"12\": \"下好的剂子团圆，擀成四边薄中间略厚的面皮，放入调制好的馅料，按自己方式包成包子。（第一次做不要贪心大馅，馅和皮的比例1：1比较合适）\", \"13\": \"所有依次包好后再次醒10分钟左右。（泽瑞妈包包子不好看，不给大家做示范了）\", \"14\": \"锅中放少许底油，码入醒发好的包子小火煎至底边稍有硬嘠，倒入面粉水(面粉水的比例10：1）\", \"15\": \"倒入面粉水后，快速盖上盖子，中小火焖煎。\", \"16\": \"煎至水份基本收干，打开盖子，撒上香葱末、白芝麻，小火煎1分钟左右即好。\", \"17\": \"看一看，金黄的底，喧软的皮。\", \"18\": \"成品。\"}', '{\"面粉\": \"500克\", \"温水\": \"300克\", \"白糖\": \"10克\", \"酵母粉\": \"3克\", \"猪肉馅\": \"400克\", \"大葱\": \"2棵\", \"生抽\": \"2茶匙\", \"姜粉\": \"1/3茶匙\", \"老抽\": \"1茶匙\", \"香油\": \"2茶匙\", \"料酒\": \"1茶匙\", \"食盐\": \"适量\", \"香葱末\": \"适量\", \"白芝麻\": \"适量\"}', 16, '2020-02-27 22:31:07', 0, '2020-03-12 19:36:05', '505458');
INSERT INTO `menu` VALUES (612, '翡翠蒸饺', 81, '{\"1\": \"猪肉白菜馅。\", \"2\": \"20g菠菜叶加水60g水搅打并过滤出汁水，100克面粉加57克菠菜汁，和成柔软面团，将面团揉搓光滑，擀成长方形，宽度已能包住白色面条为宜，厚度约1厘米左右。\", \"3\": \"将白色面条紧紧的包裹在绿色面片中，\", \"4\": \"捏实边沿，并揉搓成均匀粗细的长条。\", \"5\": \"切成均匀大小的剂子。\", \"6\": \"将面剂子用手团成整齐的圆柱形，再用手掌中间压扁。这样做绿色能够整齐的包裹在白色周围。\", \"7\": \"擀成薄圆片，\", \"8\": \"左手大拇指摁住馅料，右手捏个尖角，等距分成五份。\", \"9\": \"中间一定要捏牢。\", \"10\": \"从中心处开始用手推挤出褶皱，直到尖角的下面约一厘米处停止。\", \"11\": \"将最结尾处的褶皱提拉折回与下一片刚开始处捏合，依次类推。\", \"12\": \"一个翡翠蒸饺就做好了。\", \"13\": \"蒸屉上涂点油防粘。\", \"14\": \"水开上锅蒸6分钟即可。\", \"15\": \"蒸好的翡翠饺。\"}', '{\"菠菜汁\": \"57g\", \"面粉\": \"100g\", \"白色面团\": \"适量\", \"白菜猪肉馅\": \"适量\"}', 18, '2020-02-27 22:31:08', 0, '2020-03-12 19:36:05', '512434');
INSERT INTO `menu` VALUES (613, '笋丁酱肉包', 30, '{\"1\": \"处理好 的笋\", \"2\": \"挤掉水分，切碎丁\", \"3\": \"今天选用的是雪花猪肉\", \"4\": \"先凉水入锅，焯烫去除血沫\", \"5\": \"再加八角、小茴香，生姜等煮40分钟，煮熟\", \"6\": \"煮好的肉捞出控水\", \"7\": \"肉切丁，加生抽、蚝油，叉烧酱\", \"8\": \"拌均匀备用\", \"9\": \"锅里加一汤勺猪肉的汤，加入老抽酱油\", \"10\": \"煮开\", \"11\": \"粉条洗净放入老抽汁里煮软\", \"12\": \"晾凉后把粉条切碎\", \"13\": \"粉条和笋丁和酱好的肉丁放一起拌均匀\", \"14\": \"加入姜末拌均匀\", \"15\": \"700克面粉加入380克水，7克酵母粉，和成面团，发酵至两倍大\", \"16\": \"发酵好的面团分剂，擀成中间后的皮\", \"17\": \"包入馅料\", \"18\": \"开水上笼蒸17分钟\", \"19\": \"蒸好的包子，满满的馅料，很好吃的哦。\"}', '{\"雪花猪肉\": \"500g\", \"竹笋\": \"300g\", \"粉条\": \"100g\", \"面粉\": \"700g\", \"生抽\": \"15g\", \"蚝油\": \"15g\", \"老抽\": \"15g\", \"姜末\": \"适量\", \"盐\": \"适量\", \"叉烧酱\": \"20g\"}', 16, '2020-02-27 22:31:08', 0, '2020-03-12 19:36:05', '505809');
INSERT INTO `menu` VALUES (614, '青菜豆腐麦饼', 79, '{\"1\": \"面粉加水和成偏软的面团，盖湿布饧发2个小时。\", \"2\": \"青菜焯水后切细挤去水分。\", \"3\": \"香菇泡发切细。\", \"4\": \"葱切细。\", \"5\": \"全部食材放入深盆中，豆腐掰碎捏细。\", \"6\": \"加盐、蚝油、香油拌匀。\", \"7\": \"饧发好的面团取出分成小份揉圆（喜欢小麦饼，所以每个大概65g左右）。\", \"8\": \"擀成中间厚、四周薄的圆片。\", \"9\": \"放上蔬菜馅。\", \"10\": \"面皮从四周往上包好收口，收口朝下。\", \"11\": \"轻轻从中间往四周按扁，擀成薄饼，透过面皮都可以看见馅料了。\", \"12\": \"平底锅放油烧热，放入麦饼两面煎至金黄（煎时都成胖饼了）。\", \"13\": \"皮薄馅厚，青菜豆腐馅鲜香软嫩，好吃。\"}', '{\"青菜\": \"适量\", \"香菇\": \"适量\", \"豆腐\": \"适量\", \"葱\": \"适量\", \"油\": \"适量\", \"盐\": \"适量\", \"蚝油\": \"适量\"}', 17, '2020-02-27 22:31:08', 0, '2020-03-12 19:36:05', '516674');
INSERT INTO `menu` VALUES (615, '汤水饺', 28, '{\"1\": \"鸡蛋、芹菜、木耳、香菇、豆腐炒制成馅料备用。\", \"2\": \"包饺子备用。\", \"3\": \"热锅凉油，西红柿炒出红油。\", \"4\": \"加水煮开后，下水饺，锅开后点两次凉水。\", \"5\": \"放入香菇、木耳、黄花菜丝。\", \"6\": \"加盐、蚝油、醋调味。\", \"7\": \"盛出。\"}', '{\"饺子面团\": \"200克\", \"鸡蛋\": \"一个\", \"芹菜\": \"100克\", \"黑木耳\": \"50克\", \"香菇\": \"50克\", \"豆腐\": \"50克\", \"西红柿\": \"一个\", \"黄花菜\": \"50克\", \"调和油\": \"适量\", \"盐\": \"少许\", \"蚝油\": \"适量\", \"醋\": \"适量\"}', 18, '2020-02-27 22:31:09', 0, '2020-03-12 19:36:05', '452923');
INSERT INTO `menu` VALUES (616, '麻花馒头', 79, '{\"1\": \"面粉中放入发酵粉。\", \"2\": \"用清水搅拌成絮状。\", \"3\": \"揉成光滑面团，盖上盖帘放温暖的屋里发酵2小时。\", \"4\": \"发好的面。\", \"5\": \"放案板上揉光滑。\", \"6\": \"搓成长条。\", \"7\": \"切均匀的剂子。\", \"8\": \"搓成细长条。\", \"9\": \"从中间弯过来，一条粗，一条细，把粗条从中间切开，搓成圆条。\", \"10\": \"像编麻花辫一样编起来。\", \"11\": \"编好后，把最后捏起来。\", \"12\": \"从底向上卷起来。\", \"13\": \"卷到最后，把卷起来的面团压到最后的面上。\", \"14\": \"做好的麻花馒头。\", \"15\": \"放到盖帘上醒发20分钟，变轻了就可以蒸了。\", \"16\": \"凉水下锅蒸20分钟。\", \"17\": \"喧腾、好看又好吃的麻花馒头，给节日带来喜庆吉祥。\"}', '{\"面粉\": \"2斤\", \"酵母粉\": \"10g\"}', 17, '2020-02-27 22:31:10', 0, '2020-03-12 19:36:05', '517618');
INSERT INTO `menu` VALUES (617, '小白菜肉包', 33, '{\"1\": \"准备好的材料。\", \"2\": \"白菜粉丝焯水。\", \"3\": \"白菜粉丝切碎，豆干切丁。\", \"4\": \"放入五香粉，耗油姜葱沫盐。\", \"5\": \"搅拌均。\", \"6\": \"准备好啦。\", \"7\": \"面团发酵2倍大，揪成小剂子。\", \"8\": \"擀皮。\", \"9\": \"装馅。\", \"10\": \"包好的包子，发酵10分钟。\", \"11\": \"水开，大火15分钟即可。\"}', '{\"白菜\": \"适量\", \"粉条\": \"适量\", \"猪肉\": \"适量\", \"面团\": \"适量\", \"豆干\": \"适量\", \"葱\": \"适量\", \"姜\": \"适量\", \"蚝油\": \"适量\", \"五香粉\": \"适量\", \"盐\": \"适量\"}', 16, '2020-02-27 22:31:10', 0, '2020-03-12 19:36:05', '480688');
INSERT INTO `menu` VALUES (618, '全麦槐花黄瓜肉饺', 27, '{\"1\": \"全麦粉揉成团，醒面20分钟。\", \"2\": \"肉剁碎，黄瓜剁碎，所有材料放入盆中。\", \"3\": \"拌匀。\", \"4\": \"面切成小剂子，擀开，包好。\", \"5\": \"开水入锅点水三次。\", \"6\": \"捞出。\"}', '{\"全麦粉\": \"适量\", \"黄瓜\": \"适量\", \"槐花\": \"适量\", \"猪肉\": \"适量\", \"蚝油\": \"适量\", \"盐\": \"适量\", \"香油\": \"适量\", \"五香粉\": \"适量\", \"葱\": \"适量\", \"姜\": \"适量\"}', 18, '2020-02-27 22:31:11', 0, '2020-03-12 19:36:05', '462957');
INSERT INTO `menu` VALUES (619, '蒸千层饼', 99, '{\"1\": \"面粉中放入发酵粉。\", \"2\": \"用清水搅拌成面絮。\", \"3\": \"盖上盖帘放温暖的屋里发酵，呈蜂窝状就好了。\", \"4\": \"发面时做油酥，碗中放1勺面，倒1匙盐。\", \"5\": \"1匙花椒面。\", \"6\": \"花生油烧热后浇上去。\", \"7\": \"搅拌均匀，找一个崭新的小刷子清洗干净，用来抹油酥。\", \"8\": \"面发好后，放案板上揉光滑。\", \"9\": \"用两个馒头大小的剂子，擀成长方形大薄面皮。\", \"10\": \"均匀刷上油酥。\", \"11\": \"从右到左等量大小切口，左边最后留大点。\", \"12\": \"从右开始对折。\", \"13\": \"向左折叠。\", \"14\": \"再把两边面皮折叠到上面。\", \"15\": \"一直折到最后。\", \"16\": \"最后的样子。\", \"17\": \"把开口的一边捏起来。\", \"18\": \"用擀面杖擀薄。\", \"19\": \"做好了，放盖帘上醒发10分钟，变轻了就可以下锅了。\", \"20\": \"凉水下锅蒸20分钟。\", \"21\": \"层层叠叠，仿佛有翻书的感觉，油盐花椒面的香味让人回味无穷！\"}', '{\"面粉\": \"2斤\", \"发酵粉\": \"10g\", \"花椒面\": \"1匙\", \"盐\": \"1匙\", \"花生油\": \"1勺\"}', 17, '2020-02-27 22:31:11', 0, '2020-03-12 19:36:05', '517577');
INSERT INTO `menu` VALUES (620, '鲜肉包子', 75, '{\"1\": \"酵母粉用温水调均\", \"2\": \"加入到面粉中拌均，边加水边揉面\", \"3\": \"揉成光滑面团，蒙上保鲜膜发酵\", \"4\": \"发酵好的面团\", \"5\": \"揉均匀排气\", \"6\": \"擀成圆皮\", \"7\": \"准备好猪肉馅\", \"8\": \"萝卜擦丝，加入盐杀出水\", \"9\": \"把水挤干，再切碎些\", \"10\": \"葱姜末萝卜碎耗油十三香生抽盐一起加入到肉馅中\", \"11\": \"搅拌均匀\", \"12\": \"肉馅放入面皮中\", \"13\": \"按自己的手法包成自己喜欢的样子\", \"14\": \"放入蒸锅中大火蒸10分钟左右闷3分钟左右\", \"15\": \"成品。\"}', '{\"面粉\": \"700g\", \"酵母粉\": \"5g\", \"温水\": \"适量\", \"猪肉\": \"适量\", \"白萝卜\": \"适量\", \"蚝油\": \"适量\", \"食用油\": \"适量\", \"生抽\": \"适量\", \"盐\": \"适量\", \"十三香\": \"适量\", \"葱\": \"适量\"}', 16, '2020-02-27 22:31:11', 0, '2020-03-12 19:36:05', '505736');
INSERT INTO `menu` VALUES (621, '蒜苗猪肉蒸饺', 54, '{\"1\": \"面和成团醒二十分钟。\", \"2\": \"猪肉切碎，加姜末。\", \"3\": \"蒜苗切碎。\", \"4\": \"所有材料混合均匀。\", \"5\": \"面团分成若干小剂子，擀开，包好。\", \"6\": \"开水入锅蒸15分钟。\", \"7\": \"成品。\"}', '{\"猪肉\": \"适量\", \"面粉\": \"适量\", \"蒜苗\": \"适量\", \"蚝油\": \"适量\", \"料酒\": \"适量\", \"姜\": \"适量\", \"盐\": \"适量\", \"香油\": \"适量\", \"五香粉\": \"适量\"}', 18, '2020-02-27 22:31:12', 0, '2020-03-12 19:36:05', '462968');
INSERT INTO `menu` VALUES (622, '南瓜面青萝卜丝肉包', 100, '{\"1\": \"面粉和酵母混合，用南瓜泥和面\", \"2\": \"和成光滑的面团，盖保鲜膜放冰箱发酵一晚上\", \"3\": \"发酵好的面团\", \"4\": \"取出放案板上按揉排气，分成小剂子按扁\", \"5\": \"擀成中间厚四周薄的圆面片\", \"6\": \"肉末加盐、料酒生抽、蚝油拌匀，青萝卜丝用盐搓揉后挤去水分，小葱切细，全部食材拌匀成馅\", \"7\": \"取适量肉馅包成柳叶状包子\", \"8\": \"放到笼屉里饧发，待包子明显变大时冷水上锅蒸，上汽后15分钟，关火再虚蒸5分钟开盖\", \"9\": \"阳光下的肉包。\"}', '{\"青萝卜丝肉馅\": \"适量\", \"南瓜泥\": \"适量\", \"面粉\": \"500g\", \"酵母\": \"5g\"}', 16, '2020-02-27 22:31:13', 0, '2020-03-12 19:36:05', '505372');
INSERT INTO `menu` VALUES (623, '杂菜素蒸饺', 98, '{\"1\": \"炒锅里放多一点油，烧热后，关小火。\", \"2\": \"把粉条放到油锅里炸蓬松。\", \"3\": \"一定要关小火，不然粉条会炸糊，味道就苦了，粉条炸蓬松就可以了。\", \"4\": \"炸好的粉条放到一个锅里或盆里，用勺子碾碎。\", \"5\": \"这样就好了，不要太碎，影响口感。\", \"6\": \"这是烫好的马齿笕和苋菜挤干水分备用。\", \"7\": \"白菜切碎，挤干水分放到盆里。\", \"8\": \"把烫好的野菜挤干水分，切碎放到白菜盆里。\", \"9\": \"大葱切碎备用。\", \"10\": \"把切碎的大葱放到装有菜的盆里。\", \"11\": \"炒锅里到多一点油，放入花椒，把花椒炸香，捞出。\", \"12\": \"在盛有菜的盆里放少许五香粉，姜粉，再浇上炸好的热油，拌匀。\", \"13\": \"最后把碾碎的粉条放到菜馅里，再加上适量盐，少许鸡精，少许香油，拌匀备用。\", \"14\": \"这是拌好的菜馅，香香的。\", \"15\": \"面盆里放适量面粉。\", \"16\": \"加上适量的水，和盛软硬适中的面团，盖上保险膜，醒十五分钟。\", \"17\": \"把醒好的面团，分成比饺子剂大一点的剂子，擀成饺子皮。\", \"18\": \"包成大饺子。\", \"19\": \"蒸锅里水开，把饺子到放篦子上，开锅蒸15分钟即可。\", \"20\": \"蒸好的杂菜馅饺子，香的很呀！\"}', '{\"白菜\": \"半棵\", \"野菜\": \"适量\", \"面粉\": \"适量\", \"粉条\": \"适量\", \"大葱\": \"一棵\", \"五香粉\": \"少许\", \"姜粉\": \"少许\", \"盐\": \"适量\", \"食用油\": \"适量\", \"花椒\": \"少许\", \"香油\": \"少许\"}', 18, '2020-02-27 22:31:13', 0, '2020-03-12 19:36:05', '512069');
INSERT INTO `menu` VALUES (624, '紫菜酸汤食龙耳', 91, '{\"1\": \"大葱猪肉水饺提前包好备用。\", \"2\": \"紫菜，豆芽，鸡蛋和大葱备用。\", \"3\": \"鸡蛋入平底锅煎好备用。\", \"4\": \"汤锅煮水煮沸。\", \"5\": \"将饺子和黄豆芽煮熟。\", \"6\": \"碗中放入紫菜，葱末，蚝油，醋，酱油，盐，油泼辣子。\", \"7\": \"调制底汤。\", \"8\": \"饺子和豆芽煮熟盛入底汤中。\", \"9\": \"码入鸡蛋撒入香菜碎即可。\", \"10\": \"家常美味，请您品鉴。\", \"11\": \"成品图。\"}', '{\"饺子\": \"适量\", \"紫菜\": \"适量\", \"香菜\": \"适量\", \"鸡蛋\": \"适量\", \"大葱\": \"适量\", \"黄豆芽\": \"适量\", \"油泼辣子\": \"适量\", \"盐\": \"适量\", \"蚝油\": \"适量\", \"醋\": \"适量\", \"酱油\": \"适量\"}', 17, '2020-02-27 22:31:14', 0, '2020-03-12 19:36:05', '517555');
INSERT INTO `menu` VALUES (625, '百合花包子', 87, '{\"1\": \"面粉加水和发酵粉。和成光滑的面团后发酵两倍大\", \"2\": \"干的百合花用开水煮两分钟煮软备用\", \"3\": \"白萝卜干开水煮软。\", \"4\": \"芹菜焯水后备用\", \"5\": \"煮好的萝卜干切碎，姜切碎末\", \"6\": \"鸡蛋用油炒成小碎块状。豆腐也用水炒出水分，忘了拍炒豆腐的图片\", \"7\": \"里放油炒香姜末以后倒入萝卜干炒翻炒大约2分钟，然后加黄酒和老抽翻炒均匀\", \"8\": \"炒好的豆腐，蘑菇干鸡蛋放入锅里加盐，糖，五香粉调味。翻拌均匀。\", \"9\": \"煮好的百合花挤干水分放入\", \"10\": \"焯好的芹菜切小丁用香油拌一下，在和其他食材一起拌均匀\", \"11\": \"调好的百合花馅儿\", \"12\": \"发好的面分剂擀片，包入馅\", \"13\": \"包好包子醒发20分钟\", \"14\": \"包子的样子随自己的喜欢，想包什么样到什么样。我包的包子样子很难看，各位只看馅料好啦！\", \"15\": \"开水上笼蒸18分钟。\", \"16\": \"蒸好的包子满满的百合花，很好吃的哟！\"}', '{\"干百合花\": \"100g\", \"白萝卜干\": \"30g\", \"鸡蛋\": \"3个\", \"老豆腐\": \"200g\", \"芹菜\": \"150g\", \"面粉\": \"400g\", \"盐\": \"8g\", \"酱油\": \"10g\", \"五香粉\": \"2g\", \"香油\": \"10g\", \"食用油\": \"适量\", \"姜\": \"适量\"}', 16, '2020-02-27 22:31:14', 0, '2020-03-12 19:36:05', '504823');
INSERT INTO `menu` VALUES (626, '红糖发糕', 67, '{\"1\": \"红糖加入温水中融化。\", \"2\": \"面粉中加入两个鸡蛋4g酵母搅拌，再把红糖水倒进面粉里搅拌到没有颗粒，面糊要稀点，盖上保鲜膜发酵（我是昨天晚上拌好发酵了一晚）。\", \"3\": \"这是发酵了一晚的，用筷子搅拌一下排气。\", \"4\": \"磨具跟纸杯中刷油，把面糊倒入，把红枣，葡萄干用水泡一下晾干摆在面糊上，冷水上锅蒸（面包模具我蒸了40分钟，纸杯的我蒸了30分钟）关火焖5分钟。\", \"5\": \"端出就OK啦。\"}', '{\"面粉\": \"400g\", \"红糖\": \"120g\", \"水\": \"350ml\", \"酵母\": \"4g\", \"鸡蛋\": \"2个\", \"红枣\": \"8颗\", \"葡萄干\": \"1小把\", \"黑芝麻\": \"少许\"}', 17, '2020-02-27 22:31:15', 0, '2020-03-12 19:36:05', '517551');
INSERT INTO `menu` VALUES (627, '好吃的猪肉大葱包子！', 8, '{\"1\": \"温水里面加酵母，化开，因为天气冷，我用的温水！\", \"2\": \"加入面粉，揉成一个光滑的面团，保鲜膜包好，放在温暖的地方，发至2倍大\", \"3\": \"胡萝卜，香菇，大葱切碎，和肉馅一起搅拌\", \"4\": \"加入盐。鸡精，耗油，十三香，鸡蛋，搅拌均匀\", \"5\": \"馅准备好了，开始做包子皮了\", \"6\": \"把面团揉成一个长条条，切成大小一样的剂子，擀面杖擀的圆圆的\", \"7\": \"加入馅料，开始包了\", \"8\": \"都包好了，上锅蒸，蒸20分钟就可以了！\", \"9\": \"出锅了，看着都好吃！开动吧。\"}', '{\"五花肉\": \"适量\", \"大葱\": \"适量\", \"香菇\": \"适量\", \"胡萝卜\": \"适量\", \"蚝油\": \"适量\", \"盐\": \"适量\", \"十三香\": \"适量\", \"鸡精\": \"适量\", \"鸡蛋\": \"2个\"}', 16, '2020-02-27 22:31:16', 0, '2020-03-12 19:36:05', '504764');
INSERT INTO `menu` VALUES (628, '西红柿鸡蛋面', 70, '{\"1\": \"100g面粉+1个偏大的鸡蛋+少量盐揉成面团。\", \"2\": \"面团放保鲜袋里静置20分钟。\", \"3\": \"西红柿用绞馅机、破壁机等任意工具搅成泥。\", \"4\": \"先小火炒熟一个鸡蛋，盛出去备用。再小火炒香葱花，炒西红柿泥，加一勺白糖、番茄酱、和1.5小勺盐，加500g水煮开。\", \"5\": \"四根筷子用皮套绑在一起。面团整理成长条（按理说应该比我的细很多，我是有点着急吃了），放筷子上，用刀背切断、用力碾压，留下筷子缝隙的痕迹。\", \"6\": \"面做好了。\", \"7\": \"锅沸腾，下面，煮三分钟左右。\", \"8\": \"下鸡蛋，再煮一分钟。\", \"9\": \"出锅。\"}', '{\"西红柿\": \"1个\", \"鸡蛋\": \"1个\", \"葱花\": \"适量\", \"盐\": \"适量\", \"糖\": \"适量\", \"番茄酱\": \"适量\", \"面粉\": \"100g\"}', 17, '2020-02-27 22:31:16', 0, '2020-03-12 19:36:05', '517547');
INSERT INTO `menu` VALUES (629, '韭菜鸡蛋素饺子', 98, '{\"1\": \"准备食材，方便面没拍上。\", \"2\": \"没和面，买的现成的饺子皮。\", \"3\": \"韭菜洗净沥干水分备用。\", \"4\": \"韭菜切碎放入适量花生油，防止出水，鸡蛋打散放入韭菜碎，这样的鸡蛋更有味。\", \"5\": \"起锅多放油，烧热炒鸡蛋。\", \"6\": \"鸡蛋炒成小碎，晾凉备用。\", \"7\": \"鸡蛋凉后和韭菜搅拌均匀，放入盐调味，放一包干脆面碎。\", \"8\": \"开始包。\", \"9\": \"包成饺子。\", \"10\": \"烧开水下饺子。\", \"11\": \"煮开三滚就行，素饺子好熟。\", \"12\": \"成品。\"}', '{\"韭菜\": \"1把\", \"鸡蛋\": \"5个\", \"饺子皮\": \"适量\", \"盐\": \"适量\", \"花生油\": \"适量\", \"干脆面\": \"1包\"}', 18, '2020-02-27 22:31:17', 0, '2020-03-12 19:36:05', '510882');
INSERT INTO `menu` VALUES (630, '不加一滴水和油，香甜松软的小米奶香饼', 10, '{\"1\": \"准备好所有材料。\", \"2\": \"先倒出小米。\", \"3\": \"将小米淘洗干净，沥干水。\", \"4\": \"倒入破壁机加牛奶榨米浆。\", \"5\": \"把炸好的牛奶小米浆体用面粉筛过滤一下。\", \"6\": \"蔓越莓切细碎。\", \"7\": \"倒入牛奶小米浆里。\", \"8\": \"低筋面粉过筛到小米浆里。\", \"9\": \"用手动打蛋器搅拌均匀，不要过度搅拌防止起筋，提起打蛋器面糊是飘带状。\", \"10\": \"中火，锅里可以用硅胶刷一点油，也可以不刷，挖一勺面糊倒入锅里，煎制凝固后，翻面再继续煎1分钟左右。\", \"11\": \"煎好出锅，香甜松软。\", \"12\": \"成品图。\"}', '{\"低筋粉\": \"80g\", \"金龙鱼桃花小米\": \"50g\", \"蔓越莓\": \"10g\", \"白糖\": \"3g\"}', 17, '2020-02-27 22:31:18', 0, '2020-03-12 19:36:05', '517537');
INSERT INTO `menu` VALUES (631, '冬天包死面包子学会这一点，不用放发酵粉发面：蒸出包子超级好吃', 30, '{\"1\": \"所有材料都准备好，南瓜洗净对半切开弄掉里头南瓜子，用擦丝器成细丝备用。\", \"2\": \"擦好的南瓜丝放入盆中倒入适量的盐用筷搅拌均匀后用手攥干南瓜表面的水分放在菜板剁碎备用。\", \"3\": \"葱姜分别洗净用菜刀剁成葱姜末备用。\", \"4\": \"盆中倒入500g的面粉和200g的自来水一边往盛有面粉的盆中倒水，一边用手搅拌干面粉，直到搅拌成无干面粉的雪花。\", \"5\": \"揉成面团。\", \"6\": \"剁好的南瓜放入一个干净的容器，倒入适量的食用油用筷子搅拌均匀备用。\", \"7\": \"静止好的面团放入案板上，再用手揉1~2分钟后用刀切成快后取其中一块搓成长条备用。\", \"8\": \"搓好的长条用刀切成大小均匀的剂子用手掌心氨苄备用。\", \"9\": \"弄好的面剂子擀成中间厚边缘薄的饺子皮后像包饺子一样把包子封口南瓜馅子的包子的水饺就包好了。\", \"10\": \"锅内倒入适量水，放入蒸俾和纱布放入包好的包子（两个包子中间要留细缝），盖上锅盖后在锅盖表面压上一个重物大火煮开后转中小火蒸15～20分钟即可。\"}', '{\"南瓜\": \"几个\", \"中筋面粉\": \"500g\", \"水\": \"200g\", \"姜末\": \"适量\", \"食用油\": \"适量\", \"盐\": \"2匙\", \"葱\": \"适量\"}', 16, '2020-02-27 22:31:18', 0, '2020-03-12 19:36:05', '503311');
INSERT INTO `menu` VALUES (632, '白菜馅水饺', 16, '{\"1\": \"面粉可以是高筋粉或者饺子粉，也可以是普通面粉，包饺子都可以。面粉加入适量的水和成面团，揉匀盖上保鲜膜饧着。\", \"2\": \"准备饺子馅的食材，白菜、青椒、红椒在淡盐水中浸泡10分钟左右，然后反复洗净。白菜切碎，撒点盐腌制一会儿。\", \"3\": \"鸡蛋打散搅拌均匀，少许兑点水，让鸡蛋更嫩一些。炒锅加入适量的油，可以适量多加一些，拌馅时不另加油了，油开倒入蛋液，迅速用筷子搅拌，越碎越好，然后取出备用。清、红椒切碎，葱头去皮洗净切碎，葱头北方叫毛葱，包饺子特别提味，比大葱更鲜亮，如果没有毛葱，可换成大葱，多放一点味道更鲜美。腌制好的白菜攥掉水分。\", \"4\": \"取大盆，加入准备好的所有食材。\", \"5\": \"加入五香粉、胡椒粉、蘑菇粉、盐、蚝油搅拌均匀，饺子馅就做好了。\", \"6\": \"包饺子，将饧好的面团揉成长条，揪成均匀的小面剂子，擀成饼，包上馅，两手对捏就好了，这是饺子最简单的包法，人人都会哈。\", \"7\": \"包饺子，将饧好的面团揉成长条，揪成均匀的小面剂子，擀成饼，包上馅，两手对捏就好了，这是饺子最简单的包法，人人都会哈。\", \"8\": \"煮饺子：锅中加入适量的水，水开下入饺子，煮开浇点冷水，再煮开再次浇点冷水，第三次煮开关火，捞出，饺子煮的恰到好处。\", \"9\": \"煮饺子：锅中加入适量的水，水开下入饺子，煮开浇点冷水，再煮开再次浇点冷水，第三次煮开关火，捞出，饺子煮的恰到好处。\", \"10\": \"出锅装盘。素馅水饺，好吃不腻。\", \"11\": \"出锅装盘。素馅水饺，好吃不腻。\"}', '{\"面粉\": \"300g\", \"鸡蛋\": \"3个\", \"白菜\": \"适量\", \"油\": \"适量\", \"盐\": \"适量\", \"毛葱\": \"适量\", \"青椒\": \"半个\", \"辣椒\": \"1个\", \"五香粉\": \"适量\", \"胡椒粉\": \"适量\", \"蘑菇粉\": \"适量\", \"蚝油\": \"适量\"}', 18, '2020-02-27 22:31:18', 0, '2020-03-12 19:36:05', '511180');
INSERT INTO `menu` VALUES (633, '牛肉水饺', 43, '{\"1\": \"把两种肉馅一并放入盆内，加入蚝油、特级酱油、姜末拌匀，腌渍至少半小时。\", \"2\": \"把面粉、水放入厨师机，再加上少许食盐，低速搅打5-10分钟，然后加入一点点猪油，继续搅打均匀，收拢光滑后用保鲜膜盖好松弛。\", \"3\": \"起锅倒入一大勺的植物油，下姜片、葱段、花椒炸香，关火后捞出姜片等。\", \"4\": \"把炸好的熟油倒入肉馅盆内，顺时针搅打上劲。\", \"5\": \"洋葱剁碎，捏干水分后放入盆内，再加入少许食盐、鸡精。\", \"6\": \"再次顺时针搅打均匀，我们的馅料就准备好了。\", \"7\": \"取出松弛好的面团，搓成长条，切成小剂子，压扁，擀成饺子皮。\", \"8\": \"放上馅料，对折一挤，饺子就包好了，饺子的包法各个地方都略有不同，能包好捏紧即可。\", \"9\": \"准备一锅清水，下一勺盐跟1块八角，煮至完全沸腾，顺着锅边下入饺子。\", \"10\": \"大火煮三开，我们的饺子就熟透了，沥水捞出，准备开吃喽。\"}', '{\"牛肉馅\": \"250g\", \"猪肉馅\": \"100g\", \"洋葱\": \"1个\", \"中筋面粉\": \"300g\", \"水\": \"160g\", \"猪油\": \"5g\", \"植物油\": \"1大勺\", \"姜\": \"1块\", \"大葱\": \"1段\", \"花椒\": \"少许\", \"蚝油\": \"1勺\", \"生抽\": \"5勺\", \"盐\": \"少许\", \"鸡精\": \"少许\"}', 17, '2020-02-27 22:31:19', 0, '2020-03-12 19:36:05', '517531');
INSERT INTO `menu` VALUES (634, '牛肉萝卜饺', 38, '{\"1\": \"牛肉剁碎。\", \"2\": \"白萝卜刨丝，断生。\", \"3\": \"小蒜和姜备好。\", \"4\": \"切碎小葱姜放入肉中，放入配料调味。\", \"5\": \"萝卜丝剁碎。\", \"6\": \"分次加入肉中，拌匀。\", \"7\": \"和面擀面皮。\", \"8\": \"加入适量肉馅。\", \"9\": \"包成饺子。\", \"10\": \"煮熟配蒜泥。\", \"11\": \"开动啦。\"}', '{\"牛肉\": \"适量\", \"白萝卜\": \"适量\", \"小葱\": \"适量\", \"姜\": \"适量\", \"面粉\": \"适量\", \"生抽\": \"适量\", \"胡椒粉\": \"适量\", \"花椒油\": \"适量\", \"花生油\": \"适量\", \"香油\": \"适量\"}', 18, '2020-02-27 22:31:20', 0, '2020-03-12 19:36:05', '510753');
INSERT INTO `menu` VALUES (635, '鹅蛋木耳馅包子', 52, '{\"1\": \"面粉加入适量酵母和温水活成面团\", \"2\": \"包菜洗净切碎\", \"3\": \"木耳泡发好后切碎\", \"4\": \"豆腐皮切碎\", \"5\": \"锅中热油倒入打散的鹅蛋液\", \"6\": \"把鹅蛋炒成大块后关火，在锅中不用盛出\", \"7\": \"把切好的菜类倒入锅中\", \"8\": \"加入适量油\", \"9\": \"加少许鸡精\", \"10\": \"淋上适量生抽\", \"11\": \"加入适量蚝油\", \"12\": \"把馅料搅拌均匀\", \"13\": \"把醒发好的面团拿出来，切成大小等同的剂子\", \"14\": \"把记剂子赶成饼\", \"15\": \"包入馅料把包子包好\", \"16\": \"包好的包子放入锅中\", \"17\": \"把锅盖盖上醒发，大约一个小时后开锅蒸，中火蒸12分钟后，关火焖三分钟。\"}', '{\"面粉\": \"500g\", \"木耳\": \"50g\", \"豆腐皮\": \"50g\", \"包菜\": \"50g\", \"鹅蛋\": \"1个\", \"油\": \"10ml\", \"生抽\": \"5ml\", \"蚝油\": \"5ml\", \"鸡精\": \"5g\", \"酵母\": \"3g\"}', 16, '2020-02-27 22:31:20', 0, '2020-03-12 19:36:05', '502207');
INSERT INTO `menu` VALUES (636, '韭菜鸡蛋馅馅饼和饺子', 17, '{\"1\": \"面粉用稍微有点温度的水，觉成絮状。\", \"2\": \"揉成面团，不要太硬(饺子需要硬)，也不要太软(馅饼需要软)，醒面一个小时。\", \"3\": \"鸡蛋炒成碎，放凉备用。\", \"4\": \"韭菜切成碎。\", \"5\": \"鸡蛋放凉放入韭菜里，一定要凉，不然韭菜烫熟了。\", \"6\": \"鸡精，花椒面，食用油，酱油，耗油，盐，放入韭菜里。\", \"7\": \"搅拌均匀，最后放盐，一定最后放。\", \"8\": \"醒好的面团，切成大小差不多的小面团，擀成大饺子皮。\", \"9\": \"包成大饺子或者圆的馅饼，炒锅烙，有平底锅就更好了。\", \"10\": \"顺便包点饺子。\", \"11\": \"饺子馅饼都有了，来吃喽。\"}', '{\"面粉\": \"2小碗\", \"韭菜\": \"1捆\", \"鸡蛋\": \"3个\", \"食用油\": \"适量\", \"鸡精\": \"1勺\", \"酱油\": \"1勺\", \"蚝油\": \"1勺\", \"花椒面\": \"适量\", \"盐\": \"适量\"}', 17, '2020-02-27 22:31:20', 0, '2020-03-12 19:36:05', '517440');
INSERT INTO `menu` VALUES (637, '如意翡翠饺', 93, '{\"1\": \"菠菜洗净入开水锅中焯水。\", \"2\": \"加适量水打成菠菜糊。\", \"3\": \"和成软硬适中的面团醒至三十分钟。\", \"4\": \"韭菜洗净控水后，切碎备用。\", \"5\": \"鸡蛋打散入油锅炒散。\", \"6\": \"加入五香粉和姜粉，用熟油淋入拌匀。\", \"7\": \"面团醒后多揉几遍。\", \"8\": \"搓条改到同等大小的剂子擀皮。\", \"9\": \"馅中加盐调味，取适量放入饺子皮上，开始包饺子喽。\", \"10\": \"依次包好备用。\", \"11\": \"饺子入开水锅煮三开捞出。\", \"12\": \"如意翡翠饺完美呈现，搭配红油醋汁同食，简直太棒了！\", \"13\": \"小伙伴们一起来啊！\"}', '{\"面粉\": \"适量\", \"菠菜\": \"适量\", \"韭菜\": \"适量\", \"鸡蛋\": \"适量\", \"碱粉\": \"适量\", \"水\": \"适量\", \"油\": \"适量\", \"盐\": \"适量\", \"五香粉\": \"适量\", \"姜粉\": \"适量\"}', 18, '2020-02-27 22:31:21', 0, '2020-03-12 19:36:05', '510348');
INSERT INTO `menu` VALUES (638, '猪猪包', 83, '{\"1\": \"准备好食材；\", \"2\": \"将除甜菜根粉外的其它面团材料混合揉成一个光滑不粘手的面团；\", \"3\": \"盖上保鲜膜放进烤箱选择发酵功能发酵到2倍大；\", \"4\": \"面团发酵的时候来把低糖紫薯馅分成6等份一 一揉圆，每等份20g；\", \"5\": \"发酵好的面团排气后分割成50g一个的面团，包上紫薯馅收紧收口后揉圆，盖上保鲜膜松弛15分钟；\", \"6\": \"在剩下的面团中倒入甜菜根粉，揉均匀成一个红色的面团；\", \"7\": \"将红色面团擀开，用工具做出小猪的鼻子、耳朵、尾巴；\", \"8\": \"在面团表面相关部位抹少许水，粘上鼻子、耳朵、尾巴、黑芝麻，用牙签在鼻子部位戳2个洞；\", \"9\": \"做好的猪猪包下面垫一张油纸，放进事先装好水的雷诺瓦蒸锅里，盖上盖子二次发酵30分钟；\", \"10\": \"二次发酵好后开火，大火烧开后转中小火继续蒸12分钟左右，关火焖3-5分钟再开盖即可；\", \"11\": \"成品；\", \"12\": \"可爱的小尾巴；\", \"13\": \"软萌软萌的， 有没有戳到你的心呀！\"}', '{\"中筋面粉\": \"200g\", \"纯牛奶\": \"120g\", \"白糖\": \"10g\", \"干酵母粉\": \"3g\", \"玉米油\": \"5g\", \"甜菜根粉\": \"10g\", \"低糖紫薯馅\": \"120g\", \"黑芝麻\": \"少许\"}', 16, '2020-02-27 22:31:21', 0, '2020-03-12 19:36:05', '502995');
INSERT INTO `menu` VALUES (639, '黑芝麻双色卷', 91, '{\"1\": \"材料如下。\", \"2\": \"面粉5勺。\", \"3\": \"加入适量的发酵粉。\", \"4\": \"加入适量清水和成面团。\", \"5\": \"另准备适量面粉。\", \"6\": \"加入二勺黑芝麻粉。\", \"7\": \"加入适量发酵粉。\", \"8\": \"和成面团醒发。\", \"9\": \"醒发后揉一下排气。\", \"10\": \"擀成长方形。\", \"11\": \"黑芝麻面团擀成长方形。\", \"12\": \"随意搭配。\", \"13\": \"卷起来。\", \"14\": \"切成小块。\", \"15\": \"头尾切下来揉成黑芝麻小馒头。\", \"16\": \"大火蒸15分钟即可。\", \"17\": \"哈哈 很可爱哦。\"}', '{\"黑芝麻粉\": \"适量\", \"面粉\": \"适量\", \"发酵粉\": \"适量\", \"水\": \"适量\"}', 17, '2020-02-27 22:31:22', 0, '2020-03-12 19:36:05', '517274');
INSERT INTO `menu` VALUES (640, '刺五加叶鸡蛋馅煎饺', 14, '{\"1\": \"准备一杯开水，取合适大小的面盆，加入面粉，一分为二，一半烫面，一半常温水和面，然后揉在一起，盖上保鲜膜饧着。可以提前一天晚上和面，放入冰箱冷藏室饧着。\", \"2\": \"饺子馅是之前包饺子剩下的馅，不过，刚好用来煎饺子。早上取出冷藏的面团，或者饧好的面团，面团揉匀揉成长条，均匀的分成若干等份，团成小面剂子，擀成薄饼。\", \"3\": \"取一片面饼包入已经准备好的饺子馅，捏成饺子形状，饺子要大一点，皮薄馅大。\", \"4\": \"平底锅刷底油，放入包好的大饺子，小火煎。没有平底锅用炒锅也一样，懒人今天用的就是炒锅，煎出来一样好看好吃。记住一定要小火慢煎，别煎糊了不好看也不好吃。\", \"5\": \"翻面煎。\", \"6\": \"用铲子推一下饺子，能推动时倒入少许水（大约小半碗水）盖上锅盖焖1分钟，让内馅煎熟。\", \"7\": \"收干水分，煎饺就煎好了。\", \"8\": \"出锅装盘。\"}', '{\"面粉\": \"300g\", \"油\": \"适量\", \"刺五加叶鸡蛋馅\": \"适量\"}', 18, '2020-02-27 22:31:22', 0, '2020-03-12 19:36:05', '510321');
INSERT INTO `menu` VALUES (641, '牛肉芹菜馅包子', 49, '{\"1\": \"取牛肉馅，加水调制，向着一个方向搅拌。\", \"2\": \"取芹菜，切小碎丁，备用。\", \"3\": \"牛肉馅加入调料，继续搅拌，再加一个鸡蛋。\", \"4\": \"最后放入芹菜，均匀淋上香油和食用油。\", \"5\": \"搅拌均匀，备用。\", \"6\": \"和面，醒发好了，备用。\", \"7\": \"和面，和成长条形，备用。\", \"8\": \"拍扁，擀皮，包包子。\", \"9\": \"面比较软，封口处可以留孔，随个人的喜好。\", \"10\": \"放置10分钟，二次醒发，再放入冰箱冷冻，等吃的时候放入平锅做水煎包。\", \"11\": \"煎至底部金黄色，上面鼓起来，熟了。\"}', '{\"牛肉馅\": \"300g\", \"芹菜\": \"适量\", \"面粉\": \"适量\", \"酵母粉\": \"适量\", \"白糖\": \"适量\", \"盐\": \"适量\", \"生抽\": \"适量\", \"老抽\": \"适量\", \"蚝油\": \"适量\", \"料酒\": \"适量\", \"姜粉\": \"适量\", \"胡椒粉\": \"适量\", \"味精\": \"适量\", \"鸡蛋\": \"1个\"}', 16, '2020-02-27 22:31:23', 0, '2020-03-12 19:36:05', '501914');
INSERT INTO `menu` VALUES (642, '白菜猪肉煎饺', 40, '{\"1\": \"准备食材。\", \"2\": \"娃娃菜洗净，先切成细丝再剁成末。\", \"3\": \"将娃娃菜放入大盆中，放入五克盐腌制十分钟。\", \"4\": \"腌好的娃娃菜挤干水分备用。\", \"5\": \"将肉末、生姜末和葱花放入盆中。\", \"6\": \"加二汤匙料酒、一汤匙蚝油和二克五香粉搅拌均匀。\", \"7\": \"放入娃娃菜后用勺子拌均匀备用。\", \"8\": \"将馅料放入饺子皮中对折捏紧，二头留空。\", \"9\": \"锅加热，涮上一层食用油。\", \"10\": \"放入饺子，大火煎至底部变硬发黄。\", \"11\": \"锅中放入温水，水没过饺子一半即可，盖上盖子大火烧至水干后，小火再煎半分钟出锅即可。\", \"12\": \"美味多汁的白菜猪肉煎饺就出锅了，趁热吃，一口咬下去，汤汁直掉，非常的鲜美。\"}', '{\"娃娃菜\": \"1个\", \"肉末\": \"150克\", \"生姜\": \"2片\", \"葱\": \"2根\", \"饺子皮\": \"20张\", \"盐\": \"5克\", \"料酒\": \"2汤匙\", \"蚝油\": \"1汤匙\", \"五香粉\": \"2克\"}', 18, '2020-02-27 22:31:23', 0, '2020-03-12 19:36:05', '510236');
INSERT INTO `menu` VALUES (643, '马齿苋干儿酱肉包', 66, '{\"1\": \"马齿苋干洗干净放入锅中加水煮开然后泡两个小时\", \"2\": \"面粉放入容器中加白糖，酵母，加水\", \"3\": \"和成面团放温暖出醒发制两倍大\", \"4\": \"我用的是熟猪肉（用生猪肉更好），肉切丁\", \"5\": \"葱姜切末\", \"6\": \"锅中放油烧热下入肉丁和葱姜炒香\", \"7\": \"加料酒，生抽\", \"8\": \"加甜面酱\", \"9\": \"翻炒均匀\", \"10\": \"放马齿苋干儿\", \"11\": \"翻炒均匀关火，基本不用放盐了，加五香粉，香油拌匀即可\", \"12\": \"醒发好的面团揉一揉排气\", \"13\": \"搓成长条\", \"14\": \"分成小剂子\", \"15\": \"擀成中间厚边缘薄的皮，放馅料，包成包子\", \"16\": \"全部包好放蒸锅醒二十分钟，蒸十五分钟\", \"17\": \"出锅了。\"}', '{\"马齿苋干儿\": \"适量\", \"猪肉\": \"适量\", \"甜面酱\": \"适量\", \"葱花\": \"适量\", \"姜末\": \"适量\", \"生抽\": \"适量\", \"料酒\": \"适量\", \"面粉\": \"500g\", \"酵母\": \"5g\", \"白糖\": \"10g\", \"水\": \"300g\"}', 16, '2020-02-27 22:31:24', 0, '2020-03-12 19:36:05', '501842');
INSERT INTO `menu` VALUES (644, '梅菜干卤肉包子', 66, '{\"1\": \"面粉加入酵母，糖拌匀。\", \"2\": \"加入温水和成软硬适中的面团，密封发酵90分钟。\", \"3\": \"发酵好，取出揉揉排气。\", \"4\": \"分成均匀大小的剂子。\", \"5\": \"取一面剂擀圆，放入适量的梅菜干卤肉，包成包子。\", \"6\": \"依次包好其他的，放入蒸锅二次醒发15-20分钟，冷水起蒸20分钟焖5分钟即可完成。\", \"7\": \"时间到，打开盖子，包子好了(忽略包得丑，味道超赞)。\", \"8\": \"成品图。\", \"9\": \"成品图。\"}', '{\"面粉\": \"200克\", \"酵母\": \"2克\", \"梅干菜卤肉\": \"适量\", \"糖\": \"少许\"}', 17, '2020-02-27 22:31:24', 0, '2020-03-12 19:36:05', '517424');
INSERT INTO `menu` VALUES (645, '简单好吃三鲜水饺', 94, '{\"1\": \"所有食材准备好，韭菜摘干净放在盆中放入食用食用盐浸泡15～20分钟后清洗干净控干表面水分用刀切成韭菜末放入碗中备用。\", \"2\": \"取1把木耳放入碗中倒入适量水把干木耳浸泡开，锅内倒入适量油烧热后磕入3个鸡蛋，用炒菜铲子快速把鸡蛋滑熟放凉备用。\", \"3\": \"盆中倒入1碗面粉，然后一边倒入适量水，一边用手把面粉搅拌成雪花状后一边倒入适量水一边开始揉面，直到揉成面团得面团盖上保鲜膜静止30分钟。\", \"4\": \"浸泡开的木耳用清水洗干净，用刀切成细丝和炒熟放凉的鸡蛋一起放入切好的韭菜末。\", \"5\": \"弄好的韭菜馅，倒入适量食用油搅拌均匀后倒入1匙蚝油和1匙食用盐再次搅拌均匀。\", \"6\": \"醒发好的面团从盆中取出，揉一下排除多余面团里的气用刀把面团对半切开后搓成长条用刀切成均匀大小的的面剂子，用手掌心氨苄备用。\", \"7\": \"氨苄的饺子皮擀面杖擀成中间厚边缘薄的饺子皮包上调好的馅子用两只手的大拇指和食指往中间一挤，三鲜水饺就包好了。\", \"8\": \"锅内倒入适量水放入少量盐煮开后倒入包好的水饺，用筷子沿着锅边慢慢的把水饺底部冲一下大火煮开后倒入少量水转小火煮至水饺飘起，饺子皮鼓起关火盛出即可。\"}', '{\"韭菜\": \"1把\", \"鸡蛋\": \"3个\", \"干木耳\": \"1吧\", \"面粉\": \"1碗\", \"水\": \"适量\", \"蚝油\": \"1匙\", \"盐\": \"1匙\", \"干面粉\": \"适量\", \"食用油\": \"适量\"}', 18, '2020-02-27 22:31:25', 0, '2020-03-12 19:36:05', '510100');
INSERT INTO `menu` VALUES (646, '南方家常韭菜猪肉饺子', 40, '{\"1\": \"三线肉去皮，洗净，沥干水分。切成大概长7厘米、宽1厘米的小长条状与适量姜丝（加入量根据自己喜好）一起放入搅拌机搅拌成肉泥装，放入容器中备用。\", \"2\": \"韭菜洗净，沥干，切成小粒，备用。\", \"3\": \"打入一颗鸡蛋，目的是让馅肉质更嫩滑，味道更鲜美（根据自己喜好，也可以打两颗）\", \"4\": \"打一个鸡蛋放入碗中，再加入少许花椒粉、五香粉和适量盐，并把蛋液调匀。\", \"5\": \"将蛋液调匀\", \"6\": \"蛋液调入肉泥\", \"7\": \"加入韭菜\", \"8\": \"把调好的蛋液、切好的韭菜一起放入肉泥中，搅拌均匀，饺子肉馅完成。\", \"9\": \"包饺子啦\", \"10\": \"本人习惯一次性包六七十个饺子，放入冰箱冷藏备存，方便食用。\"}', '{\"精三线肉\": \"1.5斤\", \"韭菜\": \"3两\", \"鸡蛋\": \"1颗\", \"盐\": \"适量\", \"花椒粉\": \"1g\", \"五香粉\": \"2g\", \"生姜\": \"适量\"}', 17, '2020-02-27 22:31:26', 0, '2020-03-12 19:36:05', '517404');
INSERT INTO `menu` VALUES (647, '南瓜糖三角', 25, '{\"1\": \"准备食材\", \"2\": \"南瓜削皮，切块，上锅蒸15分钟\", \"3\": \"蒸熟的南瓜趁热捣成泥，放凉加入2克酵母\", \"4\": \"放上面粉搅拌成絮状\", \"5\": \"揉成面团，醒15分钟\", \"6\": \"取一段面条，搓成长条备用\", \"7\": \"切成小段\", \"8\": \"面团揉圆，用擀面杖擀成圆形面皮\", \"9\": \"放一勺姜红糖\", \"10\": \"取三等分，捏成三角形\", \"11\": \"再用筷子沿边缘处夹下，好定型\", \"12\": \"依次做好的，发15分钟\", \"13\": \"上锅蒸15分钟即可\", \"14\": \"成品图。\"}', '{\"南瓜\": \"半个\", \"面粉\": \"500克\", \"酵母\": \"2克\", \"姜红糖\": \"适量\"}', 16, '2020-02-27 22:31:26', 0, '2020-03-12 19:36:05', '501719');
INSERT INTO `menu` VALUES (648, '巴沙鱼猪肉四鲜馅儿饺子', 72, '{\"1\": \"除了鱼，其他都没剁，都是用切的。葱姜切碎，白菜切碎，韭菜切碎。猪肉是选好肉，店家绞的。如果不能现场绞肉，建议别直接买肉馅儿。巴沙鱼化冻，剁成茸。\", \"2\": \"鱼茸和肉馅混合。\", \"3\": \"加入葱姜碎，加适量白胡椒粉和生抽，搅拌上劲儿。\", \"4\": \"加入白菜小丁和韭菜末。\", \"5\": \"加一点儿食用油和盐(八喜的勺3勺)。\", \"6\": \"面包机和面完成，面水大概2:1，后来又揉进一点儿水，所以只记录了面团的分量。\", \"7\": \"搓条儿切剂子擀皮儿包饺子，一条龙操作。\", \"8\": \"馅儿1100g+面团700g，出60个饺子。\"}', '{\"巴沙鱼\": \"250g\", \"猪肉馅\": \"250g\", \"白菜\": \"400g\", \"韭菜\": \"200g\", \"面团\": \"700g\", \"白胡椒粉\": \"适量\", \"葱\": \"适量\", \"盐\": \"适量\", \"生抽\": \"适量\", \"姜\": \"适量\"}', 18, '2020-02-27 22:31:26', 0, '2020-03-12 19:36:05', '509856');
INSERT INTO `menu` VALUES (649, '苹果丁烙饼', 25, '{\"1\": \"面粉适量，玉米面少量备好。\", \"2\": \"苹果去皮切成小丁备用。\", \"3\": \"枸杞备好。\", \"4\": \"白糖备好。\", \"5\": \"面粉加入适量温水调成糊状，加入少量糖，枸杞搅拌均匀。\", \"6\": \"电饼档预热，加入少许食油用，用油刷抹开。\", \"7\": \"用勺子舀入面糊。\", \"8\": \"烙至两面金黄色即可，依次烙好。\", \"9\": \"成品图。\", \"10\": \"成品图。\"}', '{\"苹果丁\": \"适量\", \"枸杞\": \"适量\", \"面粉\": \"适量\", \"玉米粉\": \"适量\", \"稻米油\": \"适量\", \"白糖\": \"适量\"}', 17, '2020-02-27 22:31:27', 0, '2020-03-12 19:36:05', '517383');
INSERT INTO `menu` VALUES (650, '粘豆包', 58, '{\"1\": \"玉米面，用凉水粉一下。\", \"2\": \"加入黄米面，同样用凉水粉一下。\", \"3\": \"细细揉搓至没有干面。\", \"4\": \"用适量开水烫面。\", \"5\": \"加入面引子，揉成光滑的面团，放再温暖的地方发酵。\", \"6\": \"红小豆和红芸豆，浸泡两个小时。\", \"7\": \"转入高压锅，加适量水，炖煮40分钟。\", \"8\": \"煮好的豆子，小豆、芸豆开花了。\", \"9\": \"加适量白糖，搅拌成馅儿。\", \"10\": \"发开的面，涨成两倍大，再揉均匀。\", \"11\": \"蒸锅加足水，用烫好的菜叶铺锅。包豆包。\", \"12\": \"边包边下锅，中间要留足空隙。凉水下锅，包好后开火，水开后，再蒸20分钟。\", \"13\": \"20分钟蒸好后，关火，等3-5分钟打开锅盖。热气升腾，看不清楚。空隙还是留小了。\", \"14\": \"盛出装盘，金黄金黄的，很漂亮。\", \"15\": \"筷子加开，看看里面，粘香甜软，美味。\", \"16\": \"顺带包了一锅酸菜馅儿的。真正的可甜可盐，东北的朋友们过年共同的记忆。\"}', '{\"黄米面\": \"500克\", \"玉米面\": \"300克\", \"红小豆\": \"200克\", \"红芸豆\": \"100克\", \"白糖\": \"适量\", \"面引子\": \"适量\"}', 16, '2020-02-27 22:31:27', 0, '2020-03-12 19:36:05', '501091');
INSERT INTO `menu` VALUES (651, '红糖发糕', 26, '{\"1\": \"红糖用开水化开。\", \"2\": \"水变温，下入发酵粉搅拌均匀。\", \"3\": \"加入一个鸡蛋。搅拌均匀。\", \"4\": \"葡萄干准备。\", \"5\": \"加入面粉搅拌。\", \"6\": \"搅拌均匀。\", \"7\": \"蛋糕模具刷油。\", \"8\": \"倒入后发酵一个小时。\", \"9\": \"发酵好后。加入葡萄干。\", \"10\": \"上锅蒸，大火35分钟，焖五分钟。\", \"11\": \"成品。\"}', '{\"面粉\": \"适量\", \"红糖\": \"适量\", \"鸡蛋\": \"适量\", \"发酵粉\": \"适量\", \"葡萄干\": \"适量\"}', 17, '2020-02-27 22:31:28', 0, '2020-03-12 19:36:05', '517384');
INSERT INTO `menu` VALUES (652, '家庭版三鲜包', 9, '{\"1\": \"将面粉、干酵母粉、泡打粉、白糖放盛器内混合均匀，加水250毫升搅拌成块，用手揉搓成团，放案板反复揉搓，直至面团光滑，备用。\", \"2\": \"鹌鹑蛋煮熟剥壳，红萝卜木耳用开水烫一下，捞起来拌料，放入盐，鸡精，十三香，香油，香葱，拌匀\", \"3\": \"　(1)将醒发完成的生胚置于已沸腾的蒸锅上；(2)大火蒸猛气蒸熟（一般12分钟）。\", \"4\": \"蒸好关火闷3分钟的，避免热胀冷缩，包子塌陷\", \"5\": \"干净好吃的包子做好了.\"}', '{\"面粉\": \"500g\", \"酵母\": \"7g\", \"水\": \"250-270g\", \"泡打粉\": \"3g\", \"白糖\": \"5g\"}', 16, '2020-02-27 22:31:28', 0, '2020-03-12 19:36:05', '500801');
INSERT INTO `menu` VALUES (653, '团圆美满元宝饺子', 66, '{\"1\": \"和好面团，准备好饺子馅。\", \"2\": \"取一个面皮擀圆。\", \"3\": \"放入饺子馅，两面对折捏紧。\", \"4\": \"两个角捏在一起，形成元宝状，翻一下边缘，使形状更漂亮。\", \"5\": \"成品。\"}', '{\"面粉\": \"适量\", \"清水\": \"适量\", \"南瓜泥\": \"适量\", \"菠菜汁\": \"适量\", \"饺子馅\": \"适量\"}', 18, '2020-02-27 22:31:29', 0, '2020-03-12 19:36:05', '509148');
INSERT INTO `menu` VALUES (654, '莲藕猪肉水饺', 60, '{\"1\": \"猪肉八分瘦两分肥，切成小块。\", \"2\": \"用刀慢慢剁细。\", \"3\": \"加入姜碎，继续剁。\", \"4\": \"剁好的肉馅放入盆里，加入鸡蛋，少许盐，少许生抽。\", \"5\": \"用筷子搅打均匀上劲。\", \"6\": \"莲藕去皮，改刀切小块。\", \"7\": \"用搅碎机，搅成碎末状。\", \"8\": \"把莲藕碎加入肉馅里。\", \"9\": \"继续搅拌均匀，充分融合莲藕碎和肉馅。\", \"10\": \"一张饺子皮，放上适量馅料。\", \"11\": \"对折捏紧即可，包完所有饺子。\", \"12\": \"用辣椒油醋和少许盐和鸡精兑个蘸水料。\", \"13\": \"水开后放入饺子。\", \"14\": \"饺子放入后暂时不要翻动，以免破皮。\", \"15\": \"待饺子再次煮开，开始浮动时，轻轻晃动锅子。\", \"16\": \"点两次水后，再次煮到饺子全部浮起来即可关火捞出。\", \"17\": \"捞出，淋上蘸水。\"}', '{\"莲藕\": \"一节\", \"猪肉馅\": \"适量\", \"姜\": \"少许\", \"盐\": \"少许\", \"鸡蛋\": \"一个\", \"生抽\": \"1勺\", \"十三香\": \"一撮\", \"辣椒油\": \"少许\", \"醋\": \"少许\", \"鸡精\": \"少许\"}', 18, '2020-02-27 22:31:30', 0, '2020-03-12 19:36:05', '509886');
INSERT INTO `menu` VALUES (655, '萝卜缨豆腐粉条素包', 40, '{\"1\": \"适量面粉。\", \"2\": \"发酵粉适量温水化开备用。\", \"3\": \"酵母水倒入面粉中，用筷子搅成絮状。\", \"4\": \"和成光滑的面团。盖上盖放温水盆中加速发酵。\", \"5\": \"焯好水冻在冰糖冷冻室的萝卜缨适量解冻，洗净攥一下水份。\", \"6\": \"切成小粒。\", \"7\": \"粉条焯软。\", \"8\": \"捞出控水。\", \"9\": \"粉条切碎备用。\", \"10\": \"胡萝卜适量切片。\", \"11\": \"开水焯至断生。\", \"12\": \"切成小丁。\", \"13\": \"豆腐也切小丁。\", \"14\": \"锅内适量油烧至冒烟，豆腐小丁倒入。\", \"15\": \"大火炸制。\", \"16\": \"炸至金黄捞出。\", \"17\": \"锅内留底油烧热，油宽裕些。\", \"18\": \"葱姜炒出香味儿。\", \"19\": \"粉条放入。\", \"20\": \"一点盐，生抽，老抽调一下味儿。\", \"21\": \"翻炒均匀关火备用。\", \"22\": \"萝卜缨，豆腐丁，胡萝卜丁，炒过的粉条放盆内。\", \"23\": \"适量盐，花椒粉，蚝油，香油调味。\", \"24\": \"搅拌均匀备用。\", \"25\": \"发好的面团。\", \"26\": \"蜂窝组织非常棒。\", \"27\": \"适量面团排气揉圆。\", \"28\": \"揪成大小均匀的剂子。\", \"29\": \"擀成包子皮。\", \"30\": \"包入适量馅儿料。\", \"31\": \"包成包子。\", \"32\": \"天冷，饧二十五分钟。冷水上屉，中小火蒸二十五分钟。\", \"33\": \"焖三四分钟，打开盖儿。\", \"34\": \"成品。\", \"35\": \"成品。\", \"36\": \"成品。\", \"37\": \"成品。\"}', '{\"面粉\": \"适量\", \"发酵粉\": \"适量\", \"萝卜缨\": \"适量\", \"豆腐\": \"适量\", \"胡萝卜\": \"适量\", \"粉条\": \"适量\", \"花生油\": \"适量\", \"盐\": \"适量\", \"生抽\": \"适量\", \"老抽\": \"适量\", \"蚝油\": \"适量\", \"花椒粉\": \"适量\", \"香油\": \"适量\"}', 16, '2020-02-27 22:31:30', 0, '2020-03-12 19:36:05', '500060');
INSERT INTO `menu` VALUES (656, '茴香素包子', 26, '{\"1\": \"因为冬天所以用40度左右的温水倒入适量的酵母粉和几克白糖拌匀和面，贴上保鲜膜放在暖气上或阳光下发酵。\", \"2\": \"茴香摘好洗干净控干水分切成碎粒。\", \"3\": \"坐锅点火倒油小火慢慢地将虾皮炒至金黄。\", \"4\": \"放的就是这个魔厨高汤。葱姜切碎。\", \"5\": \"茴香切碎后放入少许盐腌至半小时，期间翻动两次，最后用屉布将茴香中的水分挤出。\", \"6\": \"挤干的茴香中放入虾皮，少许的油，盐，鸡精，蚝油，魔厨高汤，香油，葱姜，拌均即可。\", \"7\": \"先在一点点面粉中加入3克左右的碱面拌匀，将这些碱面揉进发面中。（这样做面皮就不会塌陷了)\", \"8\": \"面和好后醒面20分钟，然后将面做成小剂子。\", \"9\": \"开始包了，馅大一点好吃。\", \"10\": \"包好后盖上保鲜膜再次醒面20分钟，锅开后放入包子，蒸上20分钟即可关火，5分钟后在打开盖子。\", \"11\": \"包子蒸好了。\", \"12\": \"成品。\"}', '{\"茴香\": \"1500g\", \"虾皮\": \"100g\", \"面粉\": \"1500g\", \"酵母粉\": \"10g\", \"葱\": \"适量\", \"油\": \"少许\", \"香油\": \"少许个\", \"蚝油\": \"少许\", \"盐\": \"少许\", \"鸡精\": \"少许\", \"魔厨高汤\": \"少许\", \"姜\": \"适量\"}', 16, '2020-02-27 22:31:31', 0, '2020-03-12 19:36:05', '500250');
INSERT INTO `menu` VALUES (657, '“破五”吃饺子', 42, '{\"1\": \"高筋面粉适量。\", \"2\": \"菠菜汁适量。\", \"3\": \"边倒入菠菜汁边用筷子搅拌成穗状。\", \"4\": \"将面反复揉至均匀成光滑的面团饧20分后再揉匀。\", \"5\": \"搓成条状，一会揪剂子还需搓细一些。\", \"6\": \"揪出均匀大小的剂子。\", \"7\": \"将剂子逐个按扁。\", \"8\": \"擀成饺子皮。\", \"9\": \"羊肉酸菜馅。\", \"10\": \"加入各种调料搅拌均匀。\", \"11\": \"饺子皮中放入馅料包成饺子坯。\", \"12\": \"又准备了双色剂子。\", \"13\": \"擀双色饺子皮。\", \"14\": \"包成两种颜色的饺子。\", \"15\": \"饺子包的还可以。\", \"16\": \"锅中水烧开，然后依次下入饺子。\", \"17\": \"旺火煮开，再调至适当火侯。\", \"18\": \"将煮好的饺子捞出装盘，碧绿碧绿的颜色。\", \"19\": \"羊肉酸菜馅饺子汁水饱满，汁鲜味美。\", \"20\": \"双颜色饺子皮薄馅大。\"}', '{\"高筋面粉\": \"适量\", \"菠菜汁\": \"适量\", \"羊肉馅\": \"适量\", \"料酒\": \"适量\", \"蚝油\": \"适量\", \"海鲜酱油\": \"适量\", \"五香粉\": \"适量\", \"姜粉\": \"适量\", \"白胡椒粉\": \"适量\", \"盐\": \"适量\", \"鸡精\": \"适量\", \"葱花\": \"适量\", \"香油\": \"适量\", \"食用油\": \"适量\"}', 18, '2020-02-27 22:31:31', 0, '2020-03-12 19:36:05', '509678');
INSERT INTO `menu` VALUES (658, '豆腐包子', 72, '{\"1\": \"辣椒切碎\", \"2\": \"豆腐切丁\", \"3\": \"放在锅里炒一下，豆腐可以煎，然后加入生抽，盐，虾米，胡椒粉\", \"4\": \"水烧开，放入锅里蒸，20分钟\", \"5\": \"出锅\", \"6\": \"拍了一下照片还不错吧，喜欢可以试试呀。\"}', '{\"豆腐\": \"适量\", \"辣椒\": \"适量\", \"虾米\": \"适量\", \"生抽\": \"适量\", \"胡椒粉\": \"适量\", \"盐\": \"适量\"}', 16, '2020-02-27 22:31:32', 0, '2020-03-12 19:36:05', '499745');
INSERT INTO `menu` VALUES (659, '翡翠三鲜饺子', 100, '{\"1\": \"菠菜洗净切成小段，加入适量清水，放入料理机打成汁。用菠菜汁和面。再用清水和面。分开和两个面团，揉光滑。\", \"2\": \"芹菜洗净切小段，放入绞菜器中，搅碎。\", \"3\": \"大虾去皮去虾线，切碎。\", \"4\": \"猪肉馅中放入适量生抽，一勺蚝油，一勺料酒，适量盐，适量香油，一个鸡蛋。把切好的虾碎放入其中。再加入适量植物油。\", \"5\": \"朝一个方向划圈儿搅拌均匀。\", \"6\": \"加入芹菜朝一个方向划圈儿搅拌均匀。\", \"7\": \"两种颜色的面团揉光滑，分别揪成大小差不多的小剂子。\", \"8\": \"两种颜色的剂子各取一个，放在一起。\", \"9\": \"擀成皮。\", \"10\": \"放入三鲜馅，做成饺子。\", \"11\": \"锅中放入适量清水烧开，下入饺子烧开。\", \"12\": \"中间点三次冷水，待饺子完全漂着鼓起即可关火出锅。\", \"13\": \"成品图。\"}', '{\"菠菜\": \"一棵\", \"芹菜\": \"适量\", \"猪肉\": \"适量\", \"大虾\": \"适量\", \"面粉\": \"适量\", \"蚝油\": \"适量\", \"生抽\": \"适量\", \"料酒\": \"适量\", \"鸡蛋\": \"一个\", \"盐\": \"适量\", \"香油\": \"适量\"}', 18, '2020-02-27 22:31:33', 0, '2020-03-12 19:36:05', '509510');
INSERT INTO `menu` VALUES (660, '五福饺子', 39, '{\"1\": \"准备好南瓜泥、紫薯泥、火龙果汁、青菜汁，加面粉和成五个彩色面团，盖湿布饧发个1小时。\", \"2\": \"廋肉剁成末。\", \"3\": \"加盐、料酒、生抽、蚝油、五香粉往同一个方向拌匀。\", \"4\": \"白萝卜擦成丝，加盐搓软。\", \"5\": \"萝卜丝挤去水分剁细。\", \"6\": \"香葱切细。\", \"7\": \"香葱先用油拌一下锁住水分，和白萝卜、肉末放在一起。\", \"8\": \"拌匀成馅。\", \"9\": \"面团搓成长条形，切成小剂子。\", \"10\": \"擀成饺子皮，取适量萝卜肉馅包成柳叶饺。\", \"11\": \"做好各种颜色的饺子。\", \"12\": \"平底锅中放油，摆入饺子，底部稍煎黄。\", \"13\": \"添加饺子一半高度的水，盖盖烧煮。\", \"14\": \"水分烧干，五福饺子吃起。\"}', '{\"面粉\": \"适量\", \"南瓜泥\": \"适量\", \"紫薯泥\": \"适量\", \"青菜汁\": \"适量\", \"火龙果汁\": \"适量\", \"猪肉\": \"适量\", \"白萝卜\": \"适量\", \"葱\": \"适量\", \"姜\": \"适量\", \"盐\": \"适量\", \"料酒\": \"适量\", \"生抽\": \"适量\", \"蚝油\": \"适量\", \"五香粉\": \"适量\"}', 18, '2020-02-27 22:31:35', 0, '2020-03-12 19:36:05', '509149');
INSERT INTO `menu` VALUES (661, '浇汁饺子', 20, '{\"1\": \"速冻素饺适量。\", \"2\": \"钩中水开，饺子下入，用锅铲轻轻推离锅底，防粘锅底。\", \"3\": \"煮三滚儿，点三次水。（素冻的，多煮一下）。\", \"4\": \"饺子全部浮起。\", \"5\": \"捞出备用。\", \"6\": \"姜蒜小粒，青红椒。\", \"7\": \"锅内适量油烧热，姜蒜，青红椒炒香。\", \"8\": \"倒入半勺开水，适量盐，生抽调味。\", \"9\": \"浇到饺子上拌匀即可。\", \"10\": \"成品。\", \"11\": \"成品。\"}', '{\"速冻饺子\": \"适量\", \"花生油\": \"适量\", \"姜\": \"适量\", \"青红椒\": \"适量\", \"盐\": \"适量\", \"生抽\": \"适量\", \"蒜\": \"适量\"}', 18, '2020-02-27 22:31:37', 0, '2020-03-12 19:36:05', '507169');
INSERT INTO `menu` VALUES (662, '荠菜蘑菇鲜肉饺', 41, '{\"1\": \"夹心肉剁成肉末，加盐、料酒、生抽、蚝油拌匀放置10分钟。\", \"2\": \"荠菜捡去杂物，清洗干净泥沙。\", \"3\": \"在沸水中焯水，捞出过凉水。\", \"4\": \"挤去水分切细。\", \"5\": \"蘑菇切细末。\", \"6\": \"猪肉、荠菜、蘑菇放在深盆中，加适量米糠油（因荠菜偏干，所以需要放一些蘑菇、油增加馅料的香润）。\", \"7\": \"包成饺子。\", \"8\": \"锅中放水烧开，加少许盐，放入饺子烧煮，烧开后点两次冷水，烧至饺子全部浮起就熟了。\", \"9\": \"捞出饺子，倒点陈醋蘸汁更美味。\"}', '{\"荠菜\": \"适量\", \"蘑菇\": \"适量\", \"猪夹心肉\": \"适量\", \"葱\": \"适量\", \"油\": \"适量\", \"盐\": \"适量\", \"料酒\": \"适量\", \"生抽\": \"适量\", \"蚝油\": \"适量\"}', 18, '2020-02-27 22:31:38', 0, '2020-03-12 19:36:05', '503763');
INSERT INTO `menu` VALUES (663, '韭菜猪肉饺子', 53, '{\"1\": \"取适量面粉，温水和成面团，醒至三十分钟。\", \"2\": \"韭菜洗净控水。\", \"3\": \"猪头绞成肉泥。\", \"4\": \"韭菜切碎备用。\", \"5\": \"放入猪肉馅，加姜粉和五香粉，用热油淋入，加盐搅拌均匀。\", \"6\": \"醒好的面搓条，改到同等大小的剂子擀开。\", \"7\": \"皮上放入韭菜猪肉馅，开始包饺子喽。\", \"8\": \"依次包好备用。\", \"9\": \"饺子入开水锅煮沸三次盛出。\", \"10\": \"搭配红油醋汁简直完美到爆。\", \"11\": \"家常美味，请您品鉴。\", \"12\": \"成品图。\"}', '{\"面粉\": \"适量\", \"温水\": \"适量\", \"猪后腿肉\": \"适量\", \"韭菜\": \"适量\", \"油\": \"适量\", \"盐\": \"适量\", \"五香粉\": \"适量\", \"姜粉\": \"适量\"}', 18, '2020-02-27 22:31:39', 0, '2020-03-12 19:36:05', '507252');
INSERT INTO `menu` VALUES (664, '这饺子不用和面，不用切菜，一试就成', 71, '{\"1\": \"备齐做蛋饺的主食材。\", \"2\": \"整颗的鸡蛋需要打散，加适量的水，注意水不要加得太多，会影响鸡蛋的凝固。\", \"3\": \"大葱需要切碎了和肉馅混合一起，如同饺子馅一样的做法，放入配料里边的提及的佐料。\", \"4\": \"先将锅开通电源低温加热，温度不要过高，低温鸡蛋液凝固的比较慢，这样比较好操作。在模具里刷点油放一勺鸡蛋液\", \"5\": \"快速的在鸡蛋液上，放入适量的肉馅，保持低温。如果鸡蛋液凝固得快，先把温度档关掉。\", \"6\": \"用筷子或者镊子合成饺子，按压一下边缘利于粘合在一起，饺子就做好。\", \"7\": \"还是保持小火煎到饺子的双面都金黄了，肉馅也熟了。简单的几个步骤看看就会了吧。\", \"8\": \"这饺子只需三步，一做就成味道鲜美外皮还酥脆。\"}', '{\"鸡蛋\": \"4个\", \"植物油\": \"适量\", \"盐\": \"适量\", \"料酒\": \"适量\", \"生抽\": \"适量\", \"肉馅\": \"适量\"}', 18, '2020-02-27 22:31:41', 0, '2020-03-12 19:36:05', '506345');
INSERT INTO `menu` VALUES (665, '九层葱花饼', 65, '{\"1\": \"先来做面团，面粉中放入少许热水搅拌，再加入凉水和成面团，醒发一个小时。\", \"2\": \"再来做葱油，小葱切末加入食用油少许，盐少许，拌匀。\", \"3\": \"面团醒好后，擀成长椭圆形的面饼，将做好的葱油均匀涂满面饼。\", \"4\": \"面饼两边三分之一处上下各切一道口，像这样。\", \"5\": \"然后按顺序折叠，像这样。\", \"6\": \"再像这样\", \"7\": \"最后像这样\", \"8\": \"将面饼用手轻轻压薄。\"}', '{\"面粉\": \"适量\", \"小葱\": \"适量\", \"食用油\": \"适量\", \"盐\": \"适量\"}', 19, '2020-02-27 22:31:41', 0, '2020-03-12 19:36:05', '510434');
INSERT INTO `menu` VALUES (666, '香辣千层饼', 67, '{\"1\": \"准备好面粉，加入5克酵母粉，3克左右的盐搅拌均匀。\", \"2\": \"揉成面团盖上醒1个小时差不多。\", \"3\": \"醒好的面团会变的很大而且有很多蜂窝网。\", \"4\": \"把面团取出来揉一下排气，然后搓成长条。\", \"5\": \"再切成大小均匀的剂子，留最后一个稍微大的剂子，然后揉圆。\", \"6\": \"先把最大的那个面团擀成薄点的面皮，最好擀圆点，然后再擀好的面皮上刷上食用油，撒上一层盐，五香粉，辣椒粉，量不要太大，撒均匀就好。\", \"7\": \"然后依次把其他的面团都擀好叠放在一起每层都要刷油，撒盐，五香粉，辣椒粉，最后一个饼不放任何调料，就把白饼盖在上面。\", \"8\": \"最后一个饼就这样盖上。\", \"9\": \"然后在最上面的饼的四周抹点清水用最下面的面皮把这样都包裹起来，最后放到蒸锅上，盖上锅盖，不开火继续醒20分钟，然后开大火蒸25分钟就可以了。\", \"10\": \"把蒸好的饼放入平底锅中，平底锅要刷油，用小火慢慢烙，烙到两面金黄色（我家锅有点小）。\", \"11\": \"然后把饼切成三角状。\", \"12\": \"看起来不错吧，层次很多，很软，非常好吃。\"}', '{\"面粉\": \"500g\", \"酵母粉\": \"5克\", \"细辣椒粉\": \"适量\", \"盐\": \"适量\", \"油\": \"适量\", \"五香粉\": \"适量\"}', 19, '2020-02-27 22:31:42', 0, '2020-03-12 19:36:05', '515617');
INSERT INTO `menu` VALUES (667, '白菜馅饼', 62, '{\"1\": \"面粉加入一点食盐增加面粉的劲性，80度左右的温开水边倒水边搅拌，搅拌成这样的絮状。\", \"2\": \"加入一点食用油揉成光滑的面团。\", \"3\": \"把面团揉成长条切成大小均匀的剂子。\", \"4\": \"揉成面团放盘子里，表面再刷油，盖上保鲜膜醒十分钟。\", \"5\": \"白菜洗净，把白菜根部用刀拍扁切丝再切丁，放入盆里加入盐搅拌均匀静置10分钟。\", \"6\": \"把白菜挤出水分，放入盆中，胡萝卜擦成丝再切碎，木耳切碎，粉条切碎，鸡蛋炒成蛋花，葱花，姜末（感觉放入豆腐不是很好吃，自己喜欢吃就加入豆腐，豆腐切块再平底锅中油煎发黄盛出），加入调料，耗油，鸡精，盐，味极鲜，五香粉，香油，食用油。\", \"7\": \"馅调好了。\", \"8\": \"取出醒好的面团，揉一下擀成薄饼。\", \"9\": \"像包包子一样包好，把最后收口的面向周围拉伸尽量拉薄一点，不然出来的饼底会很厚。\", \"10\": \"这就是做好的饼坯。\", \"11\": \"锅中刷油，把饼坯放入，用有稍微按平按薄一点，然后再上面再刷一层油锁住水分。\", \"12\": \"煎两分钟左右翻面煎，直到两面都成金黄色，肉馅就要多煎一会，一份饼大约煎五六分钟就可以了。\", \"13\": \"自己做的皮薄馅多，而且想吃什么菜就做什么馅，简单实惠。\"}', '{\"白菜\": \"适量\", \"红萝卜\": \"适量\", \"豆腐\": \"适量\", \"鸡蛋\": \"适量\", \"木耳\": \"适量\", \"粉条\": \"适量\", \"面粉\": \"适量\", \"食用油\": \"适量\", \"盐\": \"适量\", \"耗油\": \"适量\", \"五香粉\": \"适量\", \"葱花\": \"适量\"}', 19, '2020-02-27 22:31:43', 0, '2020-03-12 19:36:05', '515002');
INSERT INTO `menu` VALUES (668, '红薯红豆桂花粥', 94, '{\"1\": \"泡了一夜的红豆。\", \"2\": \"红薯去皮。\", \"3\": \"切滚刀块。\", \"4\": \"电饭煲适量清水。\", \"5\": \"红豆放电饭煲中。\", \"6\": \"红薯也放入。\", \"7\": \"选择“粥”功能，按煮饭键开始。\", \"8\": \"约四十分钟，程序结束。\", \"9\": \"成品。\", \"10\": \"成品。\", \"11\": \"成品。\"}', '{\"红豆\": \"适量\", \"红薯\": \"适量\", \"桂花\": \"适量\", \"白糖\": \"适量\"}', 20, '2020-02-27 22:31:44', 0, '2020-03-12 19:36:05', '517289');
INSERT INTO `menu` VALUES (669, '发面千层饼', 20, '{\"1\": \"准备两碗面粉。\", \"2\": \"加入5克酵母粉。\", \"3\": \"加入两勺白糖。\", \"4\": \"边加水边用筷子顺时针搅拌。\", \"5\": \"边加水边搅拌，搅拌至棉絮状。\", \"6\": \"用手和面，和成光滑的面团。\", \"7\": \"套上干净的塑料袋或者盖上保鲜膜，醒发一个小时。\", \"8\": \"呈现蜂窝状抓出。\", \"9\": \"蜂窝状气泡醒发最佳。\", \"10\": \"用手完全把面抓出至案板，面盆完全清理干净。\", \"11\": \"揉面排气。\", \"12\": \"揉面排气。\", \"13\": \"揉面排气。\", \"14\": \"用刀切成三小块。\", \"15\": \"切成三小块，依次揉面排气，至光滑的表面，放一边。\", \"16\": \"依次揉面排气。\", \"17\": \"用擀面杖擀成饼。\", \"18\": \"擀成长方形的大长圆薄饼。\", \"19\": \"已擀好的薄饼。\", \"20\": \"均匀的抹一层油。\", \"21\": \"均匀的抹一层油。\", \"22\": \"在光滑的表面均匀的抹一层油。\", \"23\": \"根据个人口味，均匀的撒一勺盐，大约5克。\", \"24\": \"用手把盐抹匀。\", \"25\": \"根据个人口味，撒一勺五香粉。\", \"26\": \"均匀的涂抹五香粉。\", \"27\": \"对称的切十刀。\", \"28\": \"对称的切十刀，一面五刀。\", \"29\": \"叠成长方形的面饼。\", \"30\": \"先左后右叠面饼。\", \"31\": \"先左后右，或者先右后左。\", \"32\": \"已经叠好，面叶封好口，捏实。\", \"33\": \"最后的面叶朝下。\", \"34\": \"面叶朝下。\", \"35\": \"用擀面杖擀成圆饼。\", \"36\": \"擀成光滑的圆饼。\", \"37\": \"放到笼屉里。\", \"38\": \"依次擀好的三个面饼，分开放到笼屉里。\", \"39\": \"盖上锅盖，水开蒸30分钟即可。\", \"40\": \"已蒸好的面饼。\", \"41\": \"热气腾腾的面饼，看着就很诱人，很有食欲。\", \"42\": \"用刀切十字，切成四块。\", \"43\": \"层次分明，软香可口。\", \"44\": \"软香可口。\", \"45\": \"层次分明，软香可口，配上菜肴，更是美味佳肴。\"}', '{\"面粉\": \"500g\", \"酵母粉\": \"5g\", \"白糖\": \"2勺\", \"食用油\": \"适量\", \"盐\": \"5g\", \"五香粉\": \"1勺\"}', 19, '2020-02-27 22:31:44', 0, '2020-03-12 19:36:05', '514950');
INSERT INTO `menu` VALUES (670, '软面牛肉萝卜馅饼', 27, '{\"1\": \"先来和面团，面团食材一起放入容器中和成特别软的面团醒十分钟然就和揉面一样揉一揉，这面团软就往中间折叠，感觉光滑了就可以了，盖上盖子醒发制两倍大。\", \"2\": \"肉馅加入所有调料。\", \"3\": \"用筷子顺一个方向搅拌均匀。\", \"4\": \"放入提前弄好的白萝卜馅（白萝卜去皮擦丝加一勺盐腌制两分钟攥出多余的水分）。\", \"5\": \"用筷子拌匀。\", \"6\": \"醒好的算面团不用揉面，手抹油直接取一块大小适中的面团稍微按扁。\", \"7\": \"放适量馅料。\", \"8\": \"包起来按扁。\", \"9\": \"收口朝下放入预热好的电饼档，电饼档里多刷油。\", \"10\": \"煎制两面金黄色大约三四分钟就熟了。\", \"11\": \"出锅了，用这种软面团做出的馅饼太好吃了。不信你可以试试。\"}', '{\"面粉\": \"500g\", \"酵母\": \"5g\", \"白糖\": \"10g\", \"小苏打\": \"2g\", \"牛肉馅\": \"适量\", \"白萝卜\": \"1个\", \"姜末\": \"适量\", \"盐\": \"适量\", \"生抽\": \"适量\", \"老抽\": \"适量\", \"蚝油\": \"适量\", \"香油\": \"适量\", \"食用油\": \"适量\", \"胡椒粉\": \"适量\", \"花椒粉\": \"适量\", \"葱\": \"适量\"}', 19, '2020-02-27 22:31:46', 0, '2020-03-12 19:36:05', '514953');
INSERT INTO `menu` VALUES (671, '绿豆粥', 63, '{\"1\": \"绿豆头天晚上泡好。\", \"2\": \"将绿豆和大米放到电饭煲里，按煲粥键。\", \"3\": \"做好的粥。\", \"4\": \"装碗，其实一份早餐很简单。\"}', '{\"绿豆\": \"适量\", \"大米\": \"适量\", \"水\": \"适量\"}', 20, '2020-02-27 22:31:46', 0, '2020-03-12 19:36:05', '516714');
INSERT INTO `menu` VALUES (672, '香煎虾饼', 38, '{\"1\": \"将新鲜活虾去头、剥壳、挑虾线，这里我用的是买来已经处理好的闽正南美白虾仁，省去处理步骤更方便。\", \"2\": \"依据喜好将虾仁剁碎成心仪大小的虾蓉备用，口感上喜欢Q爽一些的话，可以不用剁得太细。\", \"3\": \"将胡萝卜、香芹切细丁，焯水后沥干水分备用。\", \"4\": \"虾蓉、胡萝卜丁、香芹丁、甜玉米粒一同放入碗中，加生粉、鸡蛋清、盐、胡椒粉搅拌均匀。\", \"5\": \"取锅中小火热油，用勺子将混合虾蓉舀入锅中摊成块状小饼。\", \"6\": \"煎3分钟左右，翻面再煎3分钟，直至煎熟即可出锅装盘。\"}', '{\"南美白虾仁\": \"250克\", \"甜玉米粒\": \"30克\", \"胡萝卜\": \"30克\", \"香芹\": \"适量\", \"鸡蛋清\": \"1粒量\", \"生粉\": \"适量\", \"白胡椒粉\": \"适量\", \"盐\": \"适量\"}', 19, '2020-02-27 22:31:47', 0, '2020-03-12 19:36:05', '513545');
INSERT INTO `menu` VALUES (673, '瑶柱虾滑粥', 48, '{\"1\": \"虾滑解冻切小块。\", \"2\": \"瑶柱和米一起下锅煲好加入虾滑。\", \"3\": \"最后加入芹菜。\", \"4\": \"完成。\"}', '{\"虾滑\": \"适量\", \"瑶柱\": \"适量\", \"大米\": \"适量\", \"芹菜\": \"适量\"}', 20, '2020-02-27 22:31:47', 0, '2020-03-12 19:36:05', '516398');
INSERT INTO `menu` VALUES (674, '家常春饼', 84, '{\"1\": \"和面，加水顺时针搅拌，成棉絮状。\", \"2\": \"搅拌成棉絮状。\", \"3\": \"用手和面，把棉絮状揉成团。\", \"4\": \"揉到盆子里没有面。\", \"5\": \"上案板继续揉。\", \"6\": \"揉成光滑的表面即可。\", \"7\": \"揪成小块面。\", \"8\": \"继续一个一个团圆。\", \"9\": \"团成这样就可以了。\", \"10\": \"擀面杖刷油，面上刷油，擀成小饼。\", \"11\": \"擀成小饼。\", \"12\": \"擀成小饼。\", \"13\": \"全部擀成小饼，然后摞起来擀成大饼。\", \"14\": \"每个小饼背面刷油，摞到一起不容易粘在一起。\", \"15\": \"放入蒸锅。\", \"16\": \"盖上盖水开蒸20分钟即可。\", \"17\": \"蒸20分钟，闷三分钟。\", \"18\": \"把春饼趁热乎儿，揭成单张的小薄饼。\", \"19\": \"揭成单张小薄饼。\", \"20\": \"揭成单张小薄饼。\", \"21\": \"揭成单张小薄饼。\", \"22\": \"掀成单张薄饼。\", \"23\": \"春饼卷菜，一口吃下去。美味无穷。\", \"24\": \"好吃有嚼劲。\"}', '{\"面粉\": \"500ml\", \"油\": \"20g\", \"水\": \"50g\"}', 19, '2020-02-27 22:31:48', 0, '2020-03-12 19:36:05', '514647');
INSERT INTO `menu` VALUES (675, '南瓜小米粥', 67, '{\"1\": \"小米提前泡20分钟，南瓜去皮切片，红枣，冰糖备用。\", \"2\": \"小米洗净放入冷水，放入南瓜大火煮开。\", \"3\": \"煮开后放入冰糖，转小火煮10分钟。\", \"4\": \"放入红枣小火煮5分钟，焖5分钟。\", \"5\": \"盛出即可。\", \"6\": \"成品图。\"}', '{\"南瓜\": \"1块\", \"小米\": \"1小碗\", \"冰糖\": \"少许\", \"红枣\": \"少许\"}', 20, '2020-02-27 22:31:48', 0, '2020-03-12 19:36:05', '516376');
INSERT INTO `menu` VALUES (676, '黄金饼', 20, '{\"1\": \"大黄米蒸熟。加入糖和黄油。\", \"2\": \"温水加酵母混合倒入面粉，和成团。\", \"3\": \"发酵。\", \"4\": \"排气。\", \"5\": \"擀成片，放入大黄米。\", \"6\": \"包成团。\", \"7\": \"擀成片。沫水。\", \"8\": \"盘子加面包糠。\", \"9\": \"放入面包糠里，另一面沫水。\", \"10\": \"在粘上面包糠。\", \"11\": \"锅中放油，放入饼。\", \"12\": \"两面煎黄。\", \"13\": \"切块。\"}', '{\"面粉\": \"适量\", \"大黄米\": \"适量\", \"面包粉\": \"适量\", \"酵母\": \"适量\", \"糖\": \"适量\", \"黄油\": \"适量\"}', 19, '2020-02-27 22:31:49', 0, '2020-03-12 19:36:05', '514633');
INSERT INTO `menu` VALUES (677, '南瓜绿豆粥', 57, '{\"1\": \"南瓜削皮切块，绿豆提前泡两个小时。\", \"2\": \"准备适量大米小米糯米红枣，喜欢稀的少放点米。\", \"3\": \"淘洗干净，放到锅里加入适量水，大火烧开，小火煮40分钟。\", \"4\": \"熬至浓稠，绿豆开花即可。\", \"5\": \"成品。\"}', '{\"南瓜\": \"适量\", \"绿豆\": \"适量\", \"大米\": \"适量\", \"小米\": \"适量\", \"糯米\": \"适量\", \"红枣\": \"6个\"}', 20, '2020-02-27 22:31:50', 0, '2020-03-12 19:36:05', '515446');
INSERT INTO `menu` VALUES (678, '南瓜米粥', 96, '{\"1\": \"南瓜去皮洗净切成小块。\", \"2\": \"3勺大米。\", \"3\": \"3勺小米1勺糯米。\", \"4\": \"洗净放入锅里加入适量水，大火煮开，小火煮40分钟。\", \"5\": \"出锅即可。\"}', '{\"南瓜\": \"1块\", \"大米\": \"3匙\", \"小米\": \"3匙\", \"糯米\": \"1匙\", \"红枣\": \"5个\", \"水\": \"适量\"}', 20, '2020-02-27 22:31:51', 0, '2020-03-12 19:36:05', '515117');
INSERT INTO `menu` VALUES (679, '山药糯米饼', 4, '{\"1\": \"准备食材。\", \"2\": \"山药上锅蒸十分钟。\", \"3\": \"蒸好的山药去皮放入大盆中。\", \"4\": \"趁热用叉子压成泥备用。用叉子好用又好清洗，不过一定要记好趁热压成泥啊。\", \"5\": \"盆中趁热放入十五克白糖和一百克糯米粉，用筷子搅拌均匀。\", \"6\": \"加少许开水搅拌成絮状并揉成团。\", \"7\": \"将糯米团分成八等分，搓圆并压扁，表面粘上少许白芝麻。\", \"8\": \"热锅热油放入山药饼，大火煎至表面金黄香脆。\", \"9\": \"反个面继续煎至金黄即可。\", \"10\": \"将煎好的山药饼放厨房用纸上吸去多余的油脂。\", \"11\": \"美味的山药糯米饼就做好了。\"}', '{\"山药\": \"200克\", \"糯米粉\": \"100克\", \"白糖\": \"15克\", \"白芝麻\": \"少许\"}', 19, '2020-02-27 22:31:51', 0, '2020-03-12 19:36:05', '513376');
INSERT INTO `menu` VALUES (680, '咖喱油饼', 27, '{\"1\": \"和面。\", \"2\": \"分成小剂子。\", \"3\": \"擀开，撒盐和咖喱粉，刷油。\", \"4\": \"卷起来，再次擀开。\", \"5\": \"锅中放油烧热，放入饼坯，中火煎至两面金黄。\", \"6\": \"好了。\", \"7\": \"金黄色，很漂亮。\"}', '{\"面粉\": \"适量\", \"咖喱粉\": \"适量\", \"盐\": \"适量\"}', 19, '2020-02-27 22:31:53', 0, '2020-03-12 19:36:05', '513344');
INSERT INTO `menu` VALUES (681, '虾仁蔬菜粥', 5, '{\"1\": \"准备好食材。\", \"2\": \"淘米加水稀饭模式煮粥。\", \"3\": \"虾仁和胡萝卜切丁，青菜我选用的小白菜叶子切丝。\", \"4\": \"粥煮熟后加入虾仁胡萝卜丁胡椒粉煮5分钟，最后放入青菜煮1-2分钟，放盐调味。\"}', '{\"大米\": \"一碗\", \"虾仁\": \"5-7个\", \"胡萝卜\": \"1块\", \"青菜\": \"一颗\", \"盐\": \"适量\", \"胡椒粉\": \"适量\"}', 20, '2020-02-27 22:31:53', 0, '2020-03-12 19:36:05', '515248');
INSERT INTO `menu` VALUES (682, '咖喱葱油饼', 8, '{\"1\": \"和面。\", \"2\": \"分成小剂子。\", \"3\": \"擀开，撒盐和咖喱粉，刷油，最后撒葱花。\", \"4\": \"卷起来，再次擀开。\", \"5\": \"锅中放油烧热放入饼，中火煎至两面金黄即可。\"}', '{\"面粉\": \"适量\", \"咖喱粉\": \"适量\", \"盐\": \"适量\", \"葱花\": \"适量\"}', 19, '2020-02-27 22:31:54', 0, '2020-03-12 19:36:05', '513342');
INSERT INTO `menu` VALUES (683, '小米燕麦粥', 7, '{\"1\": \"准备小米，燕麦，红枣。\", \"2\": \"洗净倒入电饭煲，红枣去核切碎。\", \"3\": \"煮好的。\", \"4\": \"很养胃的，来一碗。\"}', '{\"小米\": \"适量\", \"燕麦\": \"适量\", \"红枣\": \"适量\", \"冰糖\": \"适量\"}', 20, '2020-02-27 22:31:55', 0, '2020-03-12 19:36:05', '515012');
INSERT INTO `menu` VALUES (684, '葱油饼', 53, '{\"1\": \"和面。\", \"2\": \"分成小剂子。\", \"3\": \"擀开，撒盐五香粉，刷油，最后撒葱花，卷起来，再次擀开。\", \"4\": \"锅中放油烧热放入饼，中火煎至两面金黄。\", \"5\": \"成品。\"}', '{\"面粉\": \"适量\", \"葱花\": \"适量\", \"盐\": \"适量\", \"五香粉\": \"适量\"}', 19, '2020-02-27 22:31:55', 0, '2020-03-12 19:36:05', '513338');
INSERT INTO `menu` VALUES (685, '牛肉粥', 34, '{\"1\": \"洗净的大米中放两片姜，加适量清水，用高压锅稀饭档煮熟备用。\", \"2\": \"牛肉用饺肉机低速档稍稍打一下下，成粗粒即可。加适量的盐，黑胡椒粉，姜丝，玉米淀粉抓匀腌制。\", \"3\": \"芹菜切末备用。\", \"4\": \"稀饭开锅后，直接再点煮饭档，别盖盖子，让高压锅继续加热，沸腾后撒入牛肉末。8\", \"5\": \"加入适量的白胡椒粉和芝麻香油增香。\", \"6\": \"加适量食盐和鸡精调味。\", \"7\": \"撒入芹菜末，拌匀即可断电出锅。牛肉末很快熟的，不用久煮。\", \"8\": \"粥香迷人，牛肉嫩滑，胡椒暖胃，寒冷的日子来一碗，从身暖到心。\"}', '{\"大米\": \"适量\", \"牛肉末\": \"适量\", \"食盐\": \"适量\", \"黑胡椒粉\": \"适量\", \"生姜\": \"适量\", \"玉米淀粉\": \"适量\", \"白胡椒粉\": \"适量\", \"芝麻油\": \"适量\", \"鸡精\": \"适量\", \"芹菜\": \"适量\"}', 20, '2020-02-27 22:31:56', 0, '2020-03-12 19:36:05', '514933');
INSERT INTO `menu` VALUES (686, '手抓饼', 55, '{\"1\": \"发好的面揉面排气搓成长条型下成小面饥。\", \"2\": \"都按扁。\", \"3\": \"葱花多一些。\", \"4\": \"加入适量面粉和适量盐锅中烧热油。\", \"5\": \"油开倒入面粉中搅拌均匀。\", \"6\": \"把面肧擀成这样的牛舌状越长越薄是最好。\", \"7\": \"抹上适量油酥涂抹开来然后顺着一头卷起。\", \"8\": \"卷成一个长条形。\", \"9\": \"然后从一头顺势把面肧盘起来，就像图中这样。\", \"10\": \"然后按扁压圆擀成面饼再醒面10分。\", \"11\": \"电饼铛提前预热放入饼肧。\", \"12\": \"两面受热均匀。\", \"13\": \"然后用筷子就像图中这样2头夹起往中间聚拢。\", \"14\": \"面肧鼓起来活油酥的时候我加了盐是为了烙出来的饼更有滋味。\", \"15\": \"酥的掉渣太香了。\", \"16\": \"颜色也好漂亮。\", \"17\": \"层次也很丰富。\", \"18\": \"香葱的味道浓郁。\", \"19\": \"烙好一张饼大约4分左右时间。\", \"20\": \"成品。\", \"21\": \"成品。\"}', '{\"面粉\": \"300克\", \"酵母\": \"3克\", \"盐\": \"3克\", \"葱花\": \"适量\", \"油\": \"适量\"}', 19, '2020-02-27 22:31:57', 0, '2020-03-12 19:36:05', '513306');
INSERT INTO `menu` VALUES (687, '南瓜青菜粥', 99, '{\"1\": \"大米。\", \"2\": \"淘洗干净后加入适量的水，盖上盖启动煮饭键。\", \"3\": \"准备南瓜、青菜、鲜菇、干贝、蒜。\", \"4\": \"将南瓜、青菜，鲜菇切成丁。\", \"5\": \"待米粥煮开后倒入干贝。\", \"6\": \"放两颗蒜。\", \"7\": \"倒入南瓜丁，盖盖继续熬制。\", \"8\": \"当粥还剩下十分钟的时间，放入鲜菇、油、盐、胡椒粉，继续熬煮。\", \"9\": \"当还剩两分钟时开盖放入青菜，拌匀即可。\", \"10\": \"成品。清淡鲜美的素菜粥完成。\"}', '{\"大米\": \"适量\", \"小南瓜\": \"半个\", \"青菜\": \"100g\", \"鲜菇\": \"3棵\", \"干贝\": \"50g\", \"蒜\": \"2瓣\", \"油\": \"适量\", \"盐\": \"适量\", \"胡椒粉\": \"适量\"}', 20, '2020-02-27 22:31:57', 0, '2020-03-12 19:36:05', '514904');
INSERT INTO `menu` VALUES (688, '手抓饼', 6, '{\"1\": \"和面。\", \"2\": \"分成小剂子。\", \"3\": \"擀开，撒盐和五香粉，抹油，用刀切成条。\", \"4\": \"一边抻一边卷起来，再次擀开。\", \"5\": \"平底锅中放油烧热，中火放入饼坯煎至两面金黄。\", \"6\": \"快熟时反复用铲子或筷子动饼，更容易散开。\", \"7\": \"稍微抖开。\"}', '{\"面粉\": \"适量\", \"盐\": \"适量\", \"五香粉\": \"适量\", \"油\": \"适量\"}', 19, '2020-02-27 22:31:58', 0, '2020-03-12 19:36:05', '513319');
INSERT INTO `menu` VALUES (689, '香蕉糯米饼', 7, '{\"1\": \"准备食材。\", \"2\": \"香蕉去皮切成小块放入大盘中。\", \"3\": \"用叉子把香蕉片按压成香蕉泥。\", \"4\": \"放入二十克白糖和一百五十克糯米粉搅拌均匀。\", \"5\": \"将糯米粉揉成团，揉的时候四个手指收，然后用掌根按压，这样三分钟就能揉成团。\", \"6\": \"将揉好的糯米团十等分，揉圆并按扁成小饼备用。喜欢加馅的可以加上豆沙馅。\", \"7\": \"锅中倒入150毫升油，大火加热至油温七成热，判断油温几成热可以根据锅中的气泡来看，六成热的时候开始冒泡。\", \"8\": \"锅中放入糯米饼坯，中小火炸至表面金黄并鼓起。\", \"9\": \"将炸好的香蕉糯米饼放厨房用纸上吸除多余的油脂。\", \"10\": \"美味的香蕉糯米饼就做好了。\"}', '{\"香蕉\": \"2根\", \"糯米粉\": \"150克\", \"白糖\": \"20克\"}', 19, '2020-02-27 22:31:59', 0, '2020-03-12 19:36:05', '513269');
INSERT INTO `menu` VALUES (690, '绿豆西米', 85, '{\"1\": \"洗干净煮。\", \"2\": \"煮开了加入西米。\", \"3\": \"我煮了差不多一个多小时，西米看不见白心了就可以加入冰糖。\", \"4\": \"可以啦！\"}', '{\"绿豆\": \"1碗\", \"西米\": \"半碗\", \"冰糖\": \"适量\"}', 20, '2020-02-27 22:31:59', 0, '2020-03-12 19:36:05', '514420');
INSERT INTO `menu` VALUES (691, '红豆沙饼', 27, '{\"1\": \"经过二次发酵后的发酵面团，面团内部分部均匀的小气泡。\", \"2\": \"揉排出空气。\", \"3\": \"擀园饼，放上红豆沙馅。（红豆沙：红小豆提前浸泡24小时，上蒸锅蒸熟，压碎，用蜂蜜调味，放微波箱转几分钟，打出多余水分）\", \"4\": \"包成园饼。\", \"5\": \"放烤盘，喷上清水，撒上白芝麻仁。烤箱110度上下火先预热5分钟，入烤箱烤30分钟。\", \"6\": \"豆沙饼涨大裂开了。\"}', '{\"发酵面团\": \"300克\", \"红豆沙\": \"适量\", \"白芝麻仁\": \"适量\"}', 19, '2020-02-27 22:32:00', 0, '2020-03-12 19:36:05', '455671');
INSERT INTO `menu` VALUES (692, '南瓜红薯玉米粥', 46, '{\"1\": \"主要原料。\", \"2\": \"南瓜和红薯去皮切块备用。\", \"3\": \"锅里放水，放南瓜和红薯煮制。\", \"4\": \"玉米面加适量水调匀。\", \"5\": \"将南瓜和红薯煮熟，倒入玉米糊。不停搅动，避免糊锅。\", \"6\": \"将玉米粥煮熟即可。\", \"7\": \"南瓜红薯玉米粥。\"}', '{\"玉米面\": \"适量\", \"小贝南瓜\": \"适量\", \"红薯\": \"适量\"}', 20, '2020-02-27 22:32:00', 0, '2020-03-12 19:36:05', '514620');
INSERT INTO `menu` VALUES (693, '鲜肉梅干菜馅饼', 9, '{\"1\": \"面粉加温水合成柔软的面团松弛一会。\", \"2\": \"梅干菜洗几遍.然后用水泡发.可以尝看看梅干菜咸不咸.如果咸的话.后门拌馅可以少放一点盐。\", \"3\": \"泡发好的梅干菜挤去水份切碎，切些葱末备用。\", \"4\": \"切好的梅干菜和葱末一起放入肉馅中，加入食盐、蚝油、白糖、白胡椒粉生抽、香油等调料。\", \"5\": \"搅拌均匀成馅料备用。\", \"6\": \"松弛好的面团再次揉匀。分成小剂子。\", \"7\": \"取一份小剂子擀成四边稍薄中间厚的面皮，放上适量梅干菜馅料。\", \"8\": \"像包包子一样包紧不要漏馅，然后封口朝下，用手稍稍压扁。\", \"9\": \"然后用擀面杖轻轻的擀开。\", \"10\": \"擀开后稍微刷些清水，撒上些白芝麻按紧。\", \"11\": \"做好的饼坯放入煎锅或电饼铛小火煎烙。\", \"12\": \"一面上色后.翻另一面！煎至有焦黄饼花熟透即可。\"}', '{\"面粉\": \"500克\", \"梅干菜\": \"200克\", \"猪肉馅\": \"400克\", \"蚝油\": \"适量\", \"生抽\": \"适量\", \"胡椒粉\": \"少许\", \"食盐\": \"适量\", \"葱\": \"适量\", \"香油\": \"适量\"}', 19, '2020-02-27 22:32:01', 0, '2020-03-12 19:36:05', '488634');
INSERT INTO `menu` VALUES (694, '豌豆瘦肉粥', 100, '{\"1\": \"大米糯米清洗备用。\", \"2\": \"砂锅水煮沸倒入大米。\", \"3\": \"准备豌豆胡萝卜鲜肉。\", \"4\": \"等大米煮开花了倒入瘦肉豌豆胡萝卜丁。\", \"5\": \"煮沸加入盐食用油调味即可。\"}', '{\"大米\": \"适量\", \"糯米\": \"适量\", \"豌豆\": \"适量\", \"胡萝卜\": \"适量\", \"瘦肉\": \"适量\", \"盐\": \"适量\", \"食用油\": \"适量\"}', 20, '2020-02-27 22:32:02', 0, '2020-03-12 19:36:05', '514585');
INSERT INTO `menu` VALUES (695, '牛奶杂粮粥', 25, '{\"1\": \"把红豆、黑米、花生、莲子和燕麦米提前一晚用清水浸泡。\", \"2\": \"浸泡好后冲洗干净，放入九阳养生壶内。\", \"3\": \"倒入适量的纯净水。\", \"4\": \"全程炖煮约45分钟。\", \"5\": \"剩余5分钟加入纯牛奶。\", \"6\": \"接着加入冰糖煮沸即可。\", \"7\": \"非常美味又营养的牛奶杂粮粥。\", \"8\": \"简单易做，快来试试吧！\"}', '{\"燕麦米\": \"30g\", \"红豆\": \"45g\", \"黑米\": \"25g\", \"莲子\": \"20g\", \"花生\": \"25g\", \"冰糖\": \"10g\", \"牛奶\": \"250ml\", \"纯净水\": \"适量\"}', 20, '2020-02-27 22:32:03', 0, '2020-03-12 19:36:05', '514232');
INSERT INTO `menu` VALUES (696, '薄饼早餐卷', 100, '{\"1\": \"黑胡椒料加点水调开。\", \"2\": \"鸡腿洗净去骨切成条，加入黑胡椒腌料抓匀腌制。晚上腌上早晨用就可以了。\", \"3\": \"面粉加温水揉成面团。醒一会。\", \"4\": \"将面团揉匀，切成剂子，按扁一个刷点油，摞起来。\", \"5\": \"擀成薄饼。\", \"6\": \"不粘锅热后放入饼。\", \"7\": \"小火烙熟。\", \"8\": \"趁热分开。\", \"9\": \"将鸡肉炒熟。\", \"10\": \"取一张饼，放上生菜鸡肉卷起来即可。\", \"11\": \"成品图。\", \"12\": \"成品图。\", \"13\": \"成品图。\", \"14\": \"成品图。\"}', '{\"面粉\": \"1碗\", \"鸡腿\": \"1条\", \"生菜\": \"适量\", \"油\": \"适量\", \"黑胡椒腌料\": \"适量\"}', 19, '2020-02-27 22:32:03', 0, '2020-03-12 19:36:05', '512551');
INSERT INTO `menu` VALUES (697, '发面千层饼', 86, '{\"1\": \"发好的面揉面排气。\", \"2\": \"然后擀成一个大饼。\", \"3\": \"碗中适量面粉和盐然后烧开的油浇到面粉里搅拌均活成油酥备用匀。\", \"4\": \"然后把油酥抹在饼上。\", \"5\": \"切成如图这样的形状。\", \"6\": \"就像图中这样的手法对折。\", \"7\": \"依次对折卷起来。\", \"8\": \"然后就做成这样大的饼肧依次像第6步的手法反复3次这样目的是让做出的饼层次更丰富。\", \"9\": \"擀好的饼肧在上面沾一点水撒上熟芝麻按压一下。\", \"10\": \"然后盖上保鲜膜在醒发20分冷水上锅蒸15分开锅立刻取出。\", \"11\": \"放在面板上凉凉蒸出来的饼就已经很宣软好吃。\", \"12\": \"然后电饼铛提前预热放入蒸好的饼再2面烙至目的是让做出的饼更起酥。\", \"13\": \"2面烙至金黄色4分钟就可以出锅喽。\", \"14\": \"看看这层次够丰富吧。\", \"15\": \"酥的掉渣层层宣软。\", \"16\": \"第一次尝试还可以吗？\", \"17\": \"成品。\"}', '{\"面粉\": \"适量\", \"酵母\": \"适量\", \"盐\": \"适量\", \"熟芝麻\": \"适量\", \"油\": \"适量\"}', 19, '2020-02-27 22:32:04', 0, '2020-03-12 19:36:05', '512459');
INSERT INTO `menu` VALUES (698, '八宝粥', 52, '{\"1\": \"八宝米侵泡一个晚上。\", \"2\": \"放到高压锅里，40分钟。\", \"3\": \"煮好的八宝粥。\", \"4\": \"撒枸杞。\", \"5\": \"装碗。\"}', '{\"八宝米\": \"适量\", \"冰糖\": \"适量\", \"水\": \"适量\"}', 20, '2020-02-27 22:32:04', 0, '2020-03-12 19:36:05', '514282');
INSERT INTO `menu` VALUES (699, '发面葱花大饼', 16, '{\"1\": \"用划刀到温水和酵母，搅拌没有干面的稠点面湖，醒发一个小时。\", \"2\": \"放葱花，少量的盐，花椒粉少量，用刮刀拌匀。\", \"3\": \"小火加热饼锅，然后停火，到入面湖，醒15分钟左右。\", \"4\": \"就看样大约有七分熟了。\", \"5\": \"小火全城小火，盖上盖子，焖几分，用铲刀轻轻翘起来。\", \"6\": \"借助平盘翻面。\", \"7\": \"在盖上盖子小火煎几分钟。\", \"8\": \"出锅。\", \"9\": \"分成8份，4人的量。\", \"10\": \"非常轩软。\", \"11\": \"成品简单全城手不碰到面。\"}', '{\"面粉\": \"适量\", \"酵母\": \"2克\", \"温水\": \"适量\", \"葱花\": \"适量\", \"盐\": \"适量\", \"花椒粉\": \"适量\"}', 19, '2020-02-27 22:32:05', 0, '2020-03-12 19:36:05', '512300');
INSERT INTO `menu` VALUES (700, '蛋花瘦肉粥', 74, '{\"1\": \"将五花肉和生姜绞成肉糜备用，切好香葱，打好鸡蛋搅拌均匀。（鸡蛋忘记放图拍了）\", \"2\": \"将米淘洗干净后加入适量的水，加盐、芝麻油3滴，然后将电饭煲调到煲粥模式。煮了半小时后，加入肉糜。\", \"3\": \"粥煮好后，加入拌匀的鸡蛋，搅拌成蛋花状，加入适量鸡精，撒上葱花即可。\", \"4\": \"煮粥加水量，大家根据自己的喜好自行调节。我喜欢喝清点的粥，加水就多了点。不过，味道还是很不错。大家可以试试哦。\"}', '{\"珍珠米\": \"适量\", \"鸡蛋\": \"2个\", \"五花肉\": \"适量\", \"生姜\": \"适量\", \"芝麻油\": \"适量\", \"盐\": \"适量\", \"香葱\": \"适量\", \"鸡精\": \"适量\"}', 20, '2020-02-27 22:32:05', 0, '2020-03-12 19:36:05', '514159');
INSERT INTO `menu` VALUES (701, '玉米面茼蒿鸡蛋饼', 45, '{\"1\": \"茼蒿，鸡蛋。\", \"2\": \"茼蒿.切碎。\", \"3\": \"面粉.玉米面是1：1\", \"4\": \"放入盐，13香。\", \"5\": \"搅拌均匀。\", \"6\": \"锅内放油。\", \"7\": \"倒入面糊，摸平。\", \"8\": \"两面烙之金黄就好了。\", \"9\": \"成品。\", \"10\": \"营养健康。\"}', '{\"玉米面\": \"适量\", \"鸡蛋\": \"两个\", \"茼蒿\": \"适量\", \"面粉\": \"适量\", \"十三香\": \"少许\", \"盐\": \"少许\"}', 19, '2020-02-27 22:32:06', 0, '2020-03-12 19:36:05', '512175');
INSERT INTO `menu` VALUES (702, '蔬菜粥', 88, '{\"1\": \"备好食材。\", \"2\": \"冬寒菜洗好。\", \"3\": \"锅里水开下煮中途搅拌一下防粘。\", \"4\": \"煮十五分钟米开花。\", \"5\": \"在加入冬寒菜煮二分钟。\", \"6\": \"营养清香。\"}', '{\"优糖米\": \"100g\", \"冬寒菜\": \"50g\"}', 20, '2020-02-27 22:32:07', 0, '2020-03-12 19:36:05', '513761');
INSERT INTO `menu` VALUES (703, '青萝卜猪肉馅饼', 51, '{\"1\": \"青萝卜去皮后擦成细丝。\", \"2\": \"在锅中煮一分钟。\", \"3\": \"煮好的青萝卜丝，晾凉后挤干水分。\", \"4\": \"在萝卜丝那淋入一层油，翻拌均匀。\", \"5\": \"猪肉剁成肉泥。\", \"6\": \"把猪肉和青萝卜丝混合均匀，淋入适量生成。\", \"7\": \"加入适量蚝油和盐。\", \"8\": \"加入适量芝麻油。\", \"9\": \"肉馅搅拌均匀。\", \"10\": \"面粉中加适量水活成软硬适中的面团，放置20分钟。\", \"11\": \"面团取出后切成大小等同的剂子。\", \"12\": \"面团用擀面杖压扁。\", \"13\": \"每一个面剂子包入馅后，整理成馅饼。\", \"14\": \"电饼铛内加适量油，把馅饼放入煎。\", \"15\": \"馅饼煎至两面金黄即可。\"}', '{\"猪肉\": \"400g\", \"青萝卜\": \"300g\", \"面粉\": \"400g\", \"蚝油\": \"10ml\", \"生抽\": \"10ml\", \"芝麻油\": \"15ml\", \"盐\": \"5g\", \"油\": \"10ml\"}', 19, '2020-02-27 22:32:07', 0, '2020-03-12 19:36:05', '505924');
INSERT INTO `menu` VALUES (704, '五谷杂粮', 74, '{\"1\": \"材料泡几个小时。\", \"2\": \"材料洗干净。\", \"3\": \"加入适量水煮。\", \"4\": \"继续煮。\", \"5\": \"40-50分钟左右就可以了！泡过的比较好煮一点。\"}', '{\"黑豆\": \"1把\", \"红豆\": \"1把\", \"莲子\": \"数颗\", \"糯米\": \"少许\", \"赤小豆\": \"少许\", \"红枣\": \"5-6颗\"}', 20, '2020-02-27 22:32:08', 0, '2020-03-12 19:36:05', '513375');
INSERT INTO `menu` VALUES (705, '枸杞红糖小米粥', 51, '{\"1\": \"红糖备用。\", \"2\": \"小米洗净备用。\", \"3\": \"锅中入水放入小米。\", \"4\": \"小火煮沸转小火慢煮。\", \"5\": \"慢煮二十分钟。\", \"6\": \"小米浓稠后加入枸杞。\", \"7\": \"放入红糖煮至入味。\", \"8\": \"家常粥品，请您品鉴。\", \"9\": \"成品图。\", \"10\": \"成品图。\"}', '{\"枸杞\": \"适量\", \"红糖\": \"适量\", \"小米\": \"适量\", \"水\": \"适量\"}', 20, '2020-02-27 22:32:09', 0, '2020-03-12 19:36:05', '513455');
INSERT INTO `menu` VALUES (706, '韭菜鸡蛋饼', 14, '{\"1\": \"面粉放2克盐，加温水用筷子搅拌成絮状，用手揉成光滑的面团腥10分钟。\", \"2\": \"鸡蛋加点盐打散。\", \"3\": \"锅内热油、五成热倒入鸡蛋液，用筷子划散。\", \"4\": \"韭菜切碎放入碗中、加一勺油拌匀。\", \"5\": \"炒好的鸡蛋、粉丝切碎放入。\", \"6\": \"加盐、蚝油拌匀。\", \"7\": \"醒好的面分成小剂子，用擀面杖擀成饼皮。\", \"8\": \"取一张饼皮、然后包上韭菜鸡蛋馅。\", \"9\": \"像包包子一样。\", \"10\": \"收口。\", \"11\": \"按扁。\", \"12\": \"平底锅烧热刷油，放入做好的饼胚。\", \"13\": \"加盖小火煎至金黄。\", \"14\": \"反面把另一面也加盖煎至金黄、同时小饼鼓起，熟了出锅。\", \"15\": \"成品。\"}', '{\"韭菜\": \"适量\", \"鸡蛋\": \"适量\", \"粉丝\": \"适量\", \"面粉\": \"适量\", \"盐\": \"适量\", \"玉米油\": \"适量\", \"温水\": \"适量\"}', 19, '2020-02-27 22:32:09', 0, '2020-03-12 19:36:05', '469345');
INSERT INTO `menu` VALUES (707, '碎米蛋花粥', 3, '{\"1\": \"大米提前浸泡一夜。\", \"2\": \"用擀面杖擀碎。\", \"3\": \"加入锅中加入适量水。\", \"4\": \"鸡蛋打散备用。\", \"5\": \"多煮一会打入鸡蛋。\", \"6\": \"煮开即可。\"}', '{\"大米\": \"适量\", \"鸡蛋\": \"1个\"}', 20, '2020-02-27 22:32:10', 0, '2020-03-12 19:36:05', '512933');
INSERT INTO `menu` VALUES (708, '南瓜千层饼', 8, '{\"1\": \"南瓜蒸熟打散。\", \"2\": \"放入5勺面粉。\", \"3\": \"2勺小麦胚芽。\", \"4\": \"1勺小米粉。\", \"5\": \"三克发酵粉和适量糖。\", \"6\": \"和成面团醒发。\", \"7\": \"醒发好的面团擀成薄片。\", \"8\": \"分别码上油，盐和少许面粉。\", \"9\": \"切成长边形状即可，短边切错了。\", \"10\": \"对折。\", \"11\": \"折成面饼后再擀薄一些。\", \"12\": \"面上撒水铺上芝麻，再擀结实。\", \"13\": \"大火蒸20分钟后再放不粘锅里煎至金黄色。\", \"14\": \"随心意切块。\", \"15\": \"开动啦。\"}', '{\"南瓜\": \"适量\", \"芝麻\": \"适量\", \"面粉\": \"适量\", \"小米粉\": \"适量\", \"小麦胚芽\": \"适量\", \"发酵粉\": \"适量\", \"盐\": \"适量\", \"油\": \"适量\", \"糖\": \"适量\"}', 19, '2020-02-27 22:32:10', 0, '2020-03-12 19:36:05', '512056');
INSERT INTO `menu` VALUES (709, '山药红枣粥', 64, '{\"1\": \"铁棍山药去皮洗净切滚刀块，红枣洗净。\", \"2\": \"大米淘洗，和山药红枣一起放到电饭煲中，加适量水按煮粥键。\", \"3\": \"秋天燥热，山药红枣粥正好。\"}', '{\"铁棍山药\": \"适量\", \"红枣\": \"适量\", \"大米\": \"适量\", \"水\": \"适量\"}', 20, '2020-02-27 22:32:12', 0, '2020-03-12 19:36:05', '477124');
INSERT INTO `menu` VALUES (710, '黑芝麻发面糖饼', 68, '{\"1\": \"将适量面粉酵母盐用温水和面搅成大的面絮，活好的面粉放在温暖地方醒发2倍大醒发好的面团揉面排气。\", \"2\": \"搓成长条型下成小面饥。\", \"3\": \"按扁压圆。\", \"4\": \"黑芝麻糖面粉。\", \"5\": \"倒入适量熟油搅拌均匀糖馅就准备好了。\", \"6\": \"去2适量糖馅包入面皮离。\", \"7\": \"包成包子的形状按扁压圆再醒面15分。\", \"8\": \"当小饼起鼓 都彭起来就差不多好了。\", \"9\": \"电饼铛提前预热放入饼肧。\", \"10\": \"好香啊！黑芝麻的香味已经溢满厨房。\", \"11\": \"叮咚，出炉哦。\", \"12\": \"这是饼的反面所以颜色很漂亮。\", \"13\": \"成品。\"}', '{\"面粉\": \"适量\", \"酵母\": \"适量\", \"盐\": \"适量\", \"黑芝麻\": \"适量\", \"水\": \"适量\"}', 19, '2020-02-27 22:32:12', 0, '2020-03-12 19:36:05', '511863');
INSERT INTO `menu` VALUES (711, '红枣莲子八宝粥', 19, '{\"1\": \"花生、莲子、红豆、薏米提前一晚浸泡，红枣洗净，板栗去壳。\", \"2\": \"大米淘洗后和所有食材加水放到高压锅中，盖盖烧煮，上汽后15分钟。\", \"3\": \"食材丰富，营养均衡。\"}', '{\"红枣\": \"适量\", \"莲子\": \"适量\", \"花生\": \"适量\", \"板栗\": \"适量\", \"红豆\": \"适量\", \"薏米\": \"适量\", \"大米\": \"适量\", \"水\": \"适量\"}', 20, '2020-02-27 22:32:13', 0, '2020-03-12 19:36:05', '490625');
INSERT INTO `menu` VALUES (712, '烫面蒸春饼', 99, '{\"1\": \"首先我们来准备做这款春饼需要用到的食材：简单到极致的面粉加热水。普通家用的面粉都可以，冷水和面的话春饼口感比较硬，所以需要用烫面。烫面的热水80度左右即可，不好掌握温度的话刚烧开的开水晾三五分钟左右就能用了。面粉的数量根据家里人数和每个人的饭量自由掌握，将适量面粉放入料理盆中，一边倒热水一边用筷子匀速搅拌，将面粉和水搅拌成面絮。\", \"2\": \"将面絮晾凉至不烫手的状态，再用手掌将面絮反复揉成光滑的面团，这样手上不会沾上太多面糊，清洗时候比较简单一点。面团揉好后盖上锅盖或则保鲜膜，将面盆放置到温暖的地方饧面。虽然不是发面，但饧面的过程让春饼的口感更细滑，所以这一步尽量不省略。\", \"3\": \"取出饧好的面团放到案板上操作，用手掌将面团反复揉匀至表面光滑，再搓成长条状，用刀切或者直接用手分成35克左右的小面剂子，将面剂子切口面朝上，用手掌按扁，上面刷一层食用油，每两个面剂子为一组，把刷过食用油的那一面对着叠加到一起。有点儿铜锣烧的感觉，刷油是为了让春饼软嫩不粘连。\", \"4\": \"用擀面杖将叠放的双层面剂子轻轻擀成圆形的薄饼，我们不是开餐厅，自己在家吃，不必苛刻追求擀出来的春饼有多圆多薄多对称，尽量擀得圆一点、薄一点就行了，当然技艺高超的除外。\", \"5\": \"春饼擀好之后就可以准备蒸锅了，锅内加入适量清水大火烧开，防止烧干锅一定要加入足够多的清水。按上面的步骤再按扁一组面剂子，用擀面杖擀成圆形的薄饼。掌握不好技巧的话一次不用蒸太多，反正春饼够纤薄，蒸三五分钟就熟了。\", \"6\": \"水烧开后打开锅盖，将擀好的春饼上屉蒸5分钟。我是用蒸屉放了屉布再放春饼的，没有蒸屉和屉布的直接拿个干净的盘子放入春饼开始蒸也是一样的，反正春饼表皮有食用油隔离不会粘连。\", \"7\": \"5分钟后将蒸熟的春饼取出，取得时候要防止蒸汽烫手。由于春饼是双层的比较薄，中间有一层食用油隔离，趁热将春饼撕开分层，看看是不是薄的透明，而且非常柔韧有弹性，这是烙春饼达不到的境界哦。如果吃不完放入保鲜袋中，隔夜再吃口感也不会发硬。\", \"8\": \"将春饼分层后对折再对折，折成三角形摆入准备好的盘中，吃的时候取一张纤薄的春饼，放上自己喜欢的菜，隔着春饼皮就能看到缤纷的菜色。怎么样？蒸的春饼也够简单吧？聪明如你，一看就会，有空也在家试试吧！自己做的就是最家常的温暖味道！\"}', '{\"面粉\": \"300g\", \"热水\": \"180ml\", \"食用油\": \"10ml\"}', 19, '2020-02-27 22:32:13', 0, '2020-03-12 19:36:05', '512023');
INSERT INTO `menu` VALUES (713, '红枣糯米粥', 82, '{\"1\": \"糯米淘洗干净。\", \"2\": \"一次性加足水。\", \"3\": \"大火煮开。\", \"4\": \"转小火焖煮。\", \"5\": \"中途记得搅拌。\", \"6\": \"米汤粘稠。\", \"7\": \"放入红枣小火焖煮。\", \"8\": \"煮至红枣熟即可。\", \"9\": \"成品。\", \"10\": \"成品。\"}', '{\"糯米\": \"50g\", \"大枣\": \"10+\"}', 20, '2020-02-27 22:32:14', 0, '2020-03-12 19:36:05', '513406');
INSERT INTO `menu` VALUES (714, '葱花饼', 65, '{\"1\": \"准备好普通饺子粉。\", \"2\": \"用大概70°的水和面。\", \"3\": \"成团后表面抹一层油。\", \"4\": \"保鲜膜封好，醒面半小时。\", \"5\": \"擀成薄薄的一层。\", \"6\": \"将切碎的葱花均匀的撒在上面，以及根据自己口味撒盐。\", \"7\": \"如图示，左右对称各切两刀，不连断。\", \"8\": \"一块一块的叠加。\", \"9\": \"反复叠加。\", \"10\": \"最后一步将边角包好，以免葱花漏出。\", \"11\": \"用手轻轻按，按到薄厚均匀即可。\", \"12\": \"锅中倒少许油，小火煎制两面金黄。\", \"13\": \"出锅！切块方便食用！\"}', '{\"面粉\": \"100g\", \"香葱\": \"50g\", \"油\": \"5ml\", \"盐\": \"2g\", \"温水\": \"50ml\"}', 19, '2020-02-27 22:32:14', 0, '2020-03-12 19:36:05', '511879');
INSERT INTO `menu` VALUES (715, '番薯发面饼', 74, '{\"1\": \"这是做花卷剩下的面那就用它做点发面饼。\", \"2\": \"搓成长条切成小面饥。\", \"3\": \"按扁压圆擀好面皮。\", \"4\": \"红薯切成薄片上锅蒸15分。\", \"5\": \"捣好的红薯泥。\", \"6\": \"然后取适量红薯泥放入面皮中包成包子的形状按扁。\", \"7\": \"压圆擀成面饼在醒发10分。\", \"8\": \"电饼铛提前预热放入面饼。\", \"9\": \"一面烙至大约2分左右小饼都鼓起来就好了。\", \"10\": \"面饼2面都烙至金黄色。\", \"11\": \"香甜小饼出锅喽。\", \"12\": \"甜甜的红薯包裹在其中特别香甜。\", \"13\": \"成品。\"}', '{\"面粉\": \"300克\", \"酵母\": \"3克\", \"盐\": \"3克\", \"地瓜\": \"2个\", \"水\": \"适量\", \"白糖\": \"适量\"}', 19, '2020-02-27 22:32:15', 0, '2020-03-12 19:36:05', '511762');
INSERT INTO `menu` VALUES (716, '山药桂圆燕麦粥', 50, '{\"1\": \"快煮燕麦。\", \"2\": \"桂圆肉适量，洗一下备用。\", \"3\": \"山药去皮切小段。\", \"4\": \"锅内适量清水，山药放入。\", \"5\": \"煮开。\", \"6\": \"适量燕麦，洗一下倒入锅中。\", \"7\": \"煮十五分钟左右。\", \"8\": \"放入桂圆肉。\", \"9\": \"煮片刻关火。桂圆肉煮太长时间容易无味儿。\", \"10\": \"成品。\", \"11\": \"成品。\"}', '{\"山药\": \"适量\", \"桂圆肉干\": \"适量\", \"燕麦\": \"适量\", \"纯净水\": \"适量\"}', 20, '2020-02-27 22:32:15', 0, '2020-03-12 19:36:05', '510892');
INSERT INTO `menu` VALUES (717, ' 牛奶南瓜芝麻小饼', 1, '{\"1\": \"南瓜去皮去瓤。\", \"2\": \"将南瓜切成片。\", \"3\": \"南瓜放入微波炉盒，入微波炉打熟。\", \"4\": \"将南瓜压成泥。\", \"5\": \"南瓜泥中加小麦粉、糯米粉。\", \"6\": \"加适量牛奶，和成面团静置5分钟。\", \"7\": \"静置好的面团做成大小适中的面剂，将面剂团圆 。\", \"8\": \"再按稍微扁些，沾上白芝麻，两面都沾上白芝麻成牛奶南瓜小饼坯。\", \"9\": \"不粘锅加少许油，小饼坯入锅煎。\", \"10\": \"煎熟即可盛出享用。\"}', '{\"牛奶\": \"200ml\", \"南瓜\": \"100g\", \"小麦粉\": \"150g\", \"糯米粉\": \"适量\"}', 19, '2020-02-27 22:32:17', 0, '2020-03-12 19:36:05', '511770');
INSERT INTO `menu` VALUES (718, '南瓜枸杞小米粥', 1, '{\"1\": \"小米清洗。\", \"2\": \"一次性加足水开锅后转小火焖煮。\", \"3\": \"倒入南瓜块。\", \"4\": \"中途记得要搅拌（要加冰糖就加）\", \"5\": \"南瓜熟后可压碎煮。\", \"6\": \"加入枸杞。\", \"7\": \"关火焖煮几分钟即可。\", \"8\": \"出锅。\", \"9\": \"成品。\", \"10\": \"成品。\", \"11\": \"成品。\", \"12\": \"成品。\"}', '{\"小米\": \"50g\", \"南瓜\": \"小块\", \"枸杞\": \"10+\", \"冰糖\": \"适量\"}', 20, '2020-02-27 22:32:17', 0, '2020-03-12 19:36:05', '513343');
INSERT INTO `menu` VALUES (719, '自配方蒸春饼', 22, '{\"1\": \"将面粉与木薯粉混合均匀。\", \"2\": \"面粉约1/2用开水和面。\", \"3\": \"另1/2面粉用凉水和面。\", \"4\": \"然后和成软硬适度的面团，揉至光滑盖盖饧15至20分钟。\", \"5\": \"将面团取出在面案上揉至均匀。\", \"6\": \"揪出大小一致的面剂子。\", \"7\": \"将面剂压扁。\", \"8\": \"擀成大小均匀的面皮。\", \"9\": \"将面皮刷一层均匀的薄油，再放另一张面皮。\", \"10\": \"以此类推，大约12张左右。\", \"11\": \"然后采取用擀面杖先横向压面的方式。\", \"12\": \"再纵压面。\", \"13\": \"平盘先刷一层薄油，再放入擀好的若干饼，锅中水烧开菸12至13分钟(这摞12张左右)。\", \"14\": \"蒸饼期间将菜事先洗净切段备用。\", \"15\": \"豆芽炒韭菜。\", \"16\": \"出锅装盘。\", \"17\": \"这些面蒸了3锅饼(每锅12张左右，共30多张薄饼)将蒸好的春饼逐张掀开，又软又薄又筋道(不用加盐，凉了也不硬)。\", \"18\": \"开始蔬菜卷饼了，饼很薄露出了蔬菜颜色。\"}', '{\"中筋面粉\": \"300g\", \"开水\": \"适量\", \"凉水\": \"适量\", \"橄榄油\": \"(刷饼用)适量\", \"木薯粉\": \"100g\"}', 19, '2020-02-27 22:32:18', 0, '2020-03-12 19:36:05', '511477');
INSERT INTO `menu` VALUES (720, '南瓜红枣小米粥', 70, '{\"1\": \"清洗小米。\", \"2\": \"一次性加足水开锅后转小火焖煮。\", \"3\": \"倒入南瓜块。\", \"4\": \"中途记得要搅拌。\", \"5\": \"南瓜熟后可压碎煮。\", \"6\": \"加入红枣小火焖煮。\", \"7\": \"出锅。\", \"8\": \"出锅。\", \"9\": \"成品。\", \"10\": \"成品。\", \"11\": \"成品。\"}', '{\"小米\": \"50g\", \"南瓜\": \"小块\", \"红枣\": \"6个\"}', 20, '2020-02-27 22:32:18', 0, '2020-03-12 19:36:05', '513347');
INSERT INTO `menu` VALUES (721, '烙春饼', 100, '{\"1\": \"原料很简单，普通面粉就可以。\", \"2\": \"将温水分次倒入面粉中，先用筷子搅拌均匀，再用手慢慢和成软的面团。\", \"3\": \"面团和好后，好盖上保鲜膜醒15分钟。\", \"4\": \"醒好的面团揉均匀，分成25克一个的小面团，揉圆按扁。\", \"5\": \"用擀面杖擀成薄薄的圆面处。\", \"6\": \"平底锅小火，将饼放入锅中。\", \"7\": \"底部成熟后翻面继续烙，饼很薄很快就可以成熟。\", \"8\": \"薄薄的春饼烙好啦，口感筋道。\"}', '{\"面粉\": \"250克\", \"温水\": \"135克\"}', 19, '2020-02-27 22:32:19', 0, '2020-03-12 19:36:05', '511426');
INSERT INTO `menu` VALUES (722, '玉米粥', 95, '{\"1\": \"玉米碴洗净。\", \"2\": \"锅中加入适量清水倒入玉米碴一同大火烧开小火慢煮20多分煮。\", \"3\": \"米汤粘稠时就可以了。\", \"4\": \"黄色的玉米粥就煮好喽。\", \"5\": \"成品。\"}', '{\"玉米碴\": \"适量\", \"水\": \"适量\"}', 20, '2020-02-27 22:32:19', 0, '2020-03-12 19:36:05', '513161');
INSERT INTO `menu` VALUES (723, '蒸春饼', 80, '{\"1\": \"面粉加水(水里加盐）搅成絮状。\", \"2\": \"在揉成光滑的面团。醒15分钟。\", \"3\": \"面团醒好后搓成长条，分成50克左右的小剂子，按扁。\", \"4\": \"擀长薄饼，越薄越好，饼的大小根据自己的蒸屉大小决定。\", \"5\": \"蒸锅放水烧开，蒸屉上铺屉布，放入薄饼，盖上锅盖中火蒸2分钟。\", \"6\": \"2分钟后薄饼蒸的鼓起气泡，在蒸薄饼的2分钟里擀下一张薄饼，依次蒸一张擀一张。\", \"7\": \"第一张薄饼蒸好后接着放第二张，盖上锅盖接着蒸，依次类推，把薄饼蒸完。放饼的时候我们可以把擀好的饼对叠成2层放蒸屉上。\", \"8\": \"再揭开成一层，防止薄饼胚在拿起的过程中变形。\", \"9\": \"依次放最后一张薄饼后，盖上锅盖蒸5分钟，关火后焖2分钟。\", \"10\": \"出锅了，用筷子轻轻一拨就揭下一层饼，一点都不粘。\", \"11\": \"依次把饼揭完。\", \"12\": \"薄薄的春饼就做好了。\", \"13\": \"裹上自己喜欢的菜就可以了。\"}', '{\"面粉\": \"300g\", \"水\": \"150g\", \"盐\": \"1g\"}', 19, '2020-02-27 22:32:20', 0, '2020-03-12 19:36:05', '511369');
INSERT INTO `menu` VALUES (724, '春饼', 70, '{\"1\": \"适量面粉，加入一点盐。\", \"2\": \"适量温水，慢慢倒入，用筷子搅成絮状。\", \"3\": \"和成软硬适中的面团。饧二十分钟？\", \"4\": \"饧好的面包，揉光滑。\", \"5\": \"擀成厚约0.5厘米圆片。\", \"6\": \"用饺子皮模具切出厚圆饺子皮。没有模具的可下成小剂子，擀成厚点的饺子皮。\", \"7\": \"一张一张刷上油。盖上盖，再饧一小会。\", \"8\": \"饧好的厚饺子皮5到8张张一摞，用手按扁。\", \"9\": \"用擀面杖擀薄擀圆。\", \"10\": \"锅内适量水烧开，擀好的春饼上屉蒸8到10分钟。\", \"11\": \"蒸好的春饼立即打开盖，防止水蒸汽滴落。\", \"12\": \"一层一层揭开，薄而透明，又软又有筋性的春饼即成。\", \"13\": \"我又试了电饼干版的。\", \"14\": \"勤翻面，烙好一张揭开一张。防止烙出焦痕，这样有点干。相比效烙的，我更喜欢蒸的。擀好往蒸锅里一放，蒸10分钟左右，一层一层揭开就好。\", \"15\": \"成品。\", \"16\": \"成品。\"}', '{\"面粉\": \"适量\", \"盐\": \"一点点\", \"温水\": \"适量\", \"玉米油\": \"适量\"}', 19, '2020-02-27 22:32:21', 0, '2020-03-12 19:36:05', '511358');
INSERT INTO `menu` VALUES (725, '菠菜肉末粥', 8, '{\"1\": \"肉末准备一些备用！\", \"2\": \"倒入米饭到锅里。\", \"3\": \"煮开加入肉末和菠菜。\", \"4\": \"加入少许盐。\", \"5\": \"拌均匀出锅。\", \"6\": \"盛入碗中，午餐一道家常粥！\"}', '{\"菠菜\": \"适量\", \"大米饭\": \"一碗\", \"肉末\": \"适量\", \"盐\": \"少许\"}', 20, '2020-02-27 22:32:22', 0, '2020-03-12 19:36:05', '513139');
INSERT INTO `menu` VALUES (726, '美味核桃饼', 5, '{\"1\": \"一斤面粉放入小袋的发酵粉半袋，发面一小时后备用，核桃敲开取出核桃，洗一下后，用电饼铛烤一下，更酥脆，放到案板上用擀面杖擀碎，刀切一下，放到盆里面，放两勺花椒面，一勺盐，适量的熟油，搅拌均匀备用。\", \"2\": \"把面就成一个个的小剂子擀成鞋垫状，上面抹上核桃料，卷起来。\", \"3\": \"把核桃饼中间划一刀，再抹上核桃陷，抻长一点卷起来，形成一个小饼。\", \"4\": \"把小饼中间用手按个小洞，别弄破，再抹些核桃料，用擀面杖擀平！\", \"5\": \"将小饼擀平后放入电饼铛，上下加热即可。\", \"6\": \"出锅啦，越嚼越香，又有营养！\"}', '{\"面粉\": \"一斤\", \"核桃\": \"20颗\", \"花椒粉\": \"2匙\", \"精盐\": \"1匙\"}', 19, '2020-02-27 22:32:22', 0, '2020-03-12 19:36:05', '511076');
INSERT INTO `menu` VALUES (727, '椰菜豆渣饼', 7, '{\"1\": \"准备好材料。\", \"2\": \"红椰菜稍切碎一点，葱切末。\", \"3\": \"面粉豆渣混合，加入红椰菜，打入一个鸡蛋，再加点盐和椒盐粉。\", \"4\": \"拌均匀后加入葱末拌匀。\", \"5\": \"揉成团。\", \"6\": \"热油锅，把豆渣面团做成小饼放锅里煎。\", \"7\": \"两面煎至金黄就好。\", \"8\": \"很不错，试试吧。\"}', '{\"面粉\": \"120克\", \"豆渣\": \"小半碗\", \"红椰菜\": \"100克\", \"小葱\": \"几根\", \"鸡蛋\": \"1个\", \"椒盐粉\": \"少 量\", \"盐\": \"适量\", \"油\": \"适量\"}', 19, '2020-02-27 22:32:23', 0, '2020-03-12 19:36:05', '511072');
INSERT INTO `menu` VALUES (728, '白菜鸡蛋饼', 100, '{\"1\": \"大白菜尖，菜叶部分切细细丝。\", \"2\": \"香肠切丁。\", \"3\": \"白菜火腿，放入盆中打入两个鸡蛋。\", \"4\": \"加胡椒粉和盐。\", \"5\": \"喜欢吃葱的放香葱，我不吃葱，放了一点菠菜碎，为了调颜色。\", \"6\": \"用筷子拌匀。\", \"7\": \"加面粉。\", \"8\": \"搅拌成均匀的糊。\", \"9\": \"电饼档提前预热好刷油，用勺子舀入面糊。\", \"10\": \"摊成饼，整理成圆形。\", \"11\": \"煎制两面金黄色即可。\", \"12\": \"切块吃起来更方便。\", \"13\": \"成品图。\"}', '{\"大白菜\": \"100g\", \"鸡蛋\": \"2个\", \"香肠\": \"1根\", \"面粉\": \"100g\", \"盐\": \"适量\", \"胡椒粉\": \"适量\", \"葱花\": \"适量\"}', 19, '2020-02-27 22:32:24', 0, '2020-03-12 19:36:05', '511000');
INSERT INTO `menu` VALUES (729, '咸蛋粥', 64, '{\"1\": \"生菜切丝，切点姜丝，咸蛋白。\", \"2\": \"预约煲好的二米粥(小米，大米)。\", \"3\": \"把粥放入锅中煮开。\", \"4\": \"加入姜丝，放少许油。\", \"5\": \"加入生菜丝。\", \"6\": \"轻轻拌匀煮1分钟。\", \"7\": \"把咸蛋白均匀的淋在粥表面，待凝固即可关火。\"}', '{\"二米粥\": \"适量\", \"咸蛋白\": \"适量\", \"油\": \"少许\", \"生菜\": \"少许\", \"姜丝\": \"少许\"}', 20, '2020-02-27 22:32:24', 0, '2020-03-12 19:36:05', '513052');
INSERT INTO `menu` VALUES (730, '火龙果鸡蛋卷饼', 57, '{\"1\": \"火龙果榨成汁，打入一个鸡蛋，加少许油和盐、适量面粉。\", \"2\": \"搅拌成面粉糊。\", \"3\": \"装到裱花袋中。\", \"4\": \"不粘锅烧热，挤入火龙果面粉糊，小火煎熟。\", \"5\": \"香菇、胡萝卜、豆干、绿萝卜切丝。\", \"6\": \"锅中放油，下入蔬菜炒熟，加盐、料酒、生抽调味。\", \"7\": \"盛出。\", \"8\": \"取一张火龙果鸡蛋饼，放上炒好的蔬菜。\", \"9\": \"卷起。\"}', '{\"火龙果\": \"适量\", \"鸡蛋\": \"适量\", \"面粉\": \"适量\", \"豆干\": \"适量\", \"胡萝卜\": \"适量\", \"香菇\": \"适量\", \"绿萝卜\": \"适量\", \"油\": \"适量\", \"盐\": \"适量\", \"料酒\": \"适量\", \"生抽\": \"适量\"}', 19, '2020-02-27 22:32:25', 0, '2020-03-12 19:36:05', '510213');
INSERT INTO `menu` VALUES (731, '花生红枣核桃粥', 11, '{\"1\": \"准备好所有食材。\", \"2\": \"大米洗净，核桃仁洗净，红枣去核洗净一起放入破壁机。\", \"3\": \"加水不要没过热饮水位线。\", \"4\": \"启动米糊功能。\", \"5\": \"熟了自动停止保温。\", \"6\": \"放入枸杞盖上盖子保温五分钟，这样不会不会破坏枸杞的营养成分。\", \"7\": \"特别香甜可口。\"}', '{\"花生\": \"适量\", \"红枣\": \"适量\", \"核桃仁\": \"适量\", \"枸杞\": \"适量\", \"水\": \"适量\"}', 20, '2020-02-27 22:32:25', 0, '2020-03-12 19:36:05', '513035');
INSERT INTO `menu` VALUES (732, '胡萝卜鸡蛋卷饼', 94, '{\"1\": \"胡萝卜擦丝。\", \"2\": \"打入两个鸡蛋。\", \"3\": \"放盐，胡椒粉。\", \"4\": \"放适量面粉。\", \"5\": \"多次少量加水，调成浓稠的糊。\", \"6\": \"电饼铛提前预热刷油，倒入调好的面糊，摊平。\", \"7\": \"煎制两面金黄熟透。\", \"8\": \"出锅。\", \"9\": \"卷成卷切开，吃起来方便。\"}', '{\"胡萝卜\": \"1个\", \"鸡蛋\": \"2个\", \"面粉\": \"适量\", \"盐\": \"适量\", \"胡椒粉\": \"适量\"}', 19, '2020-02-27 22:32:26', 0, '2020-03-12 19:36:05', '510441');
INSERT INTO `menu` VALUES (733, '南瓜燕麦粥', 59, '{\"1\": \"大米淘洗后加水放入高压锅中。\", \"2\": \"南瓜去皮去瓤切小块，燕麦片准备好。\", \"3\": \"燕麦片和南瓜放入高压锅中同煮至粥粘稠。\", \"4\": \"口感滑润，南瓜香气浓郁。\"}', '{\"南瓜\": \"适量\", \"燕麦片\": \"适量\", \"大米\": \"适量\", \"水\": \"适量\"}', 20, '2020-02-27 22:32:27', 0, '2020-03-12 19:36:05', '492353');
INSERT INTO `menu` VALUES (734, '青菜粥', 86, '{\"1\": \"剩米饭。\", \"2\": \"放入电饭锅里。\", \"3\": \"打米饭铲散后一次性加足水。\", \"4\": \"放入电饭煲。\", \"5\": \"按煮粥健。\", \"6\": \"青菜叶切碎待用。\", \"7\": \"半小时后粥煮好了。\", \"8\": \"倒入菜叶碎。\", \"9\": \"加入盐和胡椒粉拌匀后焖煮。\", \"10\": \"菜叶断生后倒点小麻油拌均即可。\", \"11\": \"出锅。\", \"12\": \"成品。\", \"13\": \"成品。\"}', '{\"剩米饭\": \"小碗\", \"青菜叶\": \"3片\", \"盐\": \"适量\", \"胡椒粉\": \"适量\", \"麻油\": \"适量\"}', 20, '2020-02-27 22:32:28', 0, '2020-03-12 19:36:05', '512914');
INSERT INTO `menu` VALUES (735, '黑米红豆薏仁粥', 63, '{\"1\": \"把各种食材清水洗净，浸泡8-9个小时。\", \"2\": \"把泡好的食材全部放入电饭锅。\", \"3\": \"一碗米，两碗水就差不多了，我又加了鹿角胶粉两茶勺，水的话，各家的锅不同看着放吧。\", \"4\": \"煮2个小时就可以了。\", \"5\": \"还可以前一天晚上把所有材料洗干净放电饭锅，用电饭锅的预约功能，第二天早上就有粥喝了。比如:前一天晚上9点放锅里，预约第二天早上5点煮粥，7-8点就可以吃了。\"}', '{\"赤小豆\": \"适量\", \"薏米仁\": \"适量\", \"黑米\": \"适量\", \"枸杞\": \"适量\", \"鹿角胶\": \"适量\"}', 20, '2020-02-27 22:32:30', 0, '2020-03-12 19:36:05', '512512');
INSERT INTO `menu` VALUES (736, '山药黑米麦仁粥', 38, '{\"1\": \"山药去皮切块。\", \"2\": \"黑米，麦仁，红枣洗净，和山药一起放入电饭煲。\", \"3\": \"选择煮粥功能。\", \"4\": \"盛出。\"}', '{\"麦仁\": \"适量\", \"黑米\": \"适量\", \"山药\": \"适量\", \"红枣\": \"适量\"}', 20, '2020-02-27 22:32:32', 0, '2020-03-12 19:36:05', '447944');
INSERT INTO `menu` VALUES (737, '黄瓜北极虾粥', 40, '{\"1\": \"黄瓜，香菇切丁。\", \"2\": \"北极虾洗净。\", \"3\": \"大米粥煮至浓稠，放入香菇，玉米，虾，少许料酒和盐再煮5分钟关火。\", \"4\": \"放入黄瓜丁拌匀。\", \"5\": \"盛出放入少许香油。\"}', '{\"北极虾\": \"适量\", \"黄瓜\": \"1/3个\", \"大米\": \"适量\", \"玉米\": \"适量\", \"盐\": \"适量\", \"料酒\": \"适量\", \"香油\": \"适量\"}', 20, '2020-02-27 22:32:33', 0, '2020-03-12 19:36:05', '461559');
INSERT INTO `menu` VALUES (738, '红薯莲子粥', 54, '{\"1\": \"红薯去皮切块，莲子泡发，大米一杯。\", \"2\": \"大米淘洗，和莲子红薯放入高压锅中加水，盖盖烧煮上汽后10分钟。\", \"3\": \"营养粥喝起。\"}', '{\"红薯\": \"适量\", \"莲子\": \"适量\", \"大米\": \"适量\", \"水\": \"适量\"}', 20, '2020-02-27 22:32:34', 0, '2020-03-12 19:36:05', '495121');
INSERT INTO `menu` VALUES (739, '苹果红枣小米粥', 54, '{\"1\": \"小米和莲子洗干净加入适量水煮。\", \"2\": \"准备好其他的食材。\", \"3\": \"煮20分钟后加入红枣。\", \"4\": \"15分钟后放入苹果红枣枸杞。\", \"5\": \"继续煮5分钟。\", \"6\": \"倒入牛奶，不喜欢牛奶的可以不用。\", \"7\": \"美颜粥。\"}', '{\"苹果\": \"半个\", \"小米\": \"适量\", \"红枣\": \"适量\", \"牛奶\": \"1碗\", \"枸杞\": \"少许\", \"莲子\": \"少许\"}', 20, '2020-02-27 22:32:36', 0, '2020-03-12 19:36:05', '512359');
INSERT INTO `menu` VALUES (740, '胡萝卜荸荠大米粥', 31, '{\"1\": \"食材：五常大米、胡萝卜（已洗净去皮切开）、荸荠（已洗净去皮）。\", \"2\": \"将荸荠放在案板上对半切开，待用。\", \"3\": \"将五常大米和胡萝卜下入电饭锅里。\", \"4\": \"随后，加适量的清水。\", \"5\": \"盖上盖，插上电源按好1小时煮粥功能进行工作。\", \"6\": \"接着，离煮粥还差20分钟时开盖，合入荸荠。\", \"7\": \"搅动一下。\", \"8\": \"继续盖上盖再煮。\", \"9\": \"然后，工作完毕跳的保温时。\", \"10\": \"最后，开盖、加适量的盐。\", \"11\": \"加适量的味精。\", \"12\": \"调味拌匀即成。\"}', '{\"五常大米\": \"165克\", \"胡萝卜\": \"165克\", \"荸荠\": \"9个\", \"盐\": \"适量\", \"味精\": \"适量\"}', 20, '2020-02-27 22:32:37', 0, '2020-03-12 19:36:05', '512335');
INSERT INTO `menu` VALUES (753, '巧克力', 11, '{\"1\": \"新鲜的大虾剪掉须、脚，并挑去虾线。因为用的是整个的虾，剪掉虾枪后把头部里面的沙包挑出来，这样吃起来才放心\", \"2\": \"虾开水快速焯烫一下捞出\", \"3\": \"苏里拉切丝\", \"4\": \"胡萝卜和卤肉切丁备用\", \"5\": \"中放油，下入胡萝卜煸炒至变色\", \"6\": \"下入米饭炒散\", \"7\": \"入肉丁，加适量的盐和胡椒粉\", \"8\": \"翻炒均匀关火\", \"9\": \"撒上一层马苏里拉\", \"10\": \"放上虾，再撒上点马苏里拉\", \"11\": \"烤箱170度预热3分钟，烤10分钟即可。\", \"12\": \"烤好取出，再切点黄瓜片，即装饰又补充营养成分。享用吧！\"}', '111', 1, '2020-03-08 23:34:36', NULL, '2020-03-08 23:34:36', '111');
